insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京大学',' ','北京',1,0,1,1,1,' ','暂空',' ','http://www.pku.edu.cn','北京大学创于1898年，初名京师大学堂，是第一所国立综合性大学，也是当时中国的最高教育行政机关。辛亥革命后，于1912年改为现名。作为新文化运动的中心和“五四”运动的策源地，作为中国最早传播马克思主义和民主科学思想的发祥地，作为中国共产党最早的活动基地，北京大学为民族的振兴和解放、国家的建设和发展、社会的文明和进步做出了不可代替的贡献，在中国走向现代化的进程中起到了重要的先锋作用。爱国、进步、民主、科学的传统精神和勤奋、严谨、求实、创新的学风在这里生生不息、代代相传。1917年，著名教育和民主主',13487034);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京大学医学部',' ','北京',1,3,1,0,0,' ','暂空',' ','http://www.bjmu.edu.cn','北京大学医学教育开始于1912年，建校后先后称为国立北京医科专门学校、北京大学医学院、北京医学院、北京医科大学。2000年与北京大学合并成为新的北京大学后，改称北京大学医学部。北京大学医学部在长达近一个世纪的办学实践中，逐渐形成了“勤奋、严谨、求实、创新”的学风和“坚定正确的政治方向，严谨求实的治学态度，热忱献身的服务精神”的校风。北京大学医学部师资雄厚，专业技术人员近万人，其中高级职称人员近六千人，两院院士11名，长江学者74名，国家有突出贡献专家16位。在校生人数一万余人，其中研究生和本科生',1543974);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民大学',' ','北京',1,11,1,1,1,' ','暂空',' ','http://www.ruc.edu.cn','中国人民大学是一所以人文社会科学为主，兼有部分理工学科的综合性研究型全国重点大学。学校的前身是1937年诞生于抗日战争烽火中的陕北公学，以及后来的华北联合大学、北方大学和华北大学。1950年10月3日，以华北大学为基础的中国人民大学正式成立，成为新中国创办的第一所新型大学。著名教育家吴玉章、成仿吾、袁宝华、黄达、李文海先后担任校长。现任校长为纪宝成教授，党委书记为程天权教授。自成立之日起，中国人民大学广大师生发扬“始终奋进在时代前列”的优良传统，与党和国家同呼吸、共命运，勤俭办学，艰苦奋斗，积极',8392487);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('清华大学',' ','北京',1,1,1,1,1,' ','暂空',' ','http://www.tsinghua.edu.cn','清华大学(TsinghuaUniversity)是中国著名高等学府，坐落于北京西北郊风景秀丽的清华园。是中国高层次人才培养和科学技术研究的重要基地之一。清华大学的前身是清华学堂，成立于1911年，当初是清政府建立的留美预备学校。1912年更名为清华学校，为尝试人才的本地培养，1925年设立大学部，同年开办研究院(国学门)，1928年更名为“国立清华大学”，并于1929年秋开办研究院，各系设研究所。1937年抗日战争爆发后，南迁长沙，与北京大学、南开大学联合办学，组建国立长沙临时大学，1938年迁',12538099);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京交通大学',' ','北京',1,1,1,1,0,' ','暂空',' ','http://www.bjtu.edu.cn','北京交通大学是教育部直属，教育部、中国铁路总公司、北京市人民政府共建的全国重点大学，是国家“211工程”、“985工程优势学科创新平台”项目建设高校和具有研究生院的全国首批博士、硕士学位授予高校。我校牵头的“2011计划”“轨道交通安全协同创新中心”是国家首批14个认定的协同创新中心之一。学校位于首都北京“学府胜地”海淀区，毗邻中国“硅谷”中关村，有东西两个校区，总面积1000余亩，建筑面积84万平方米，教学、科研设施完善，校园环境优美。北京交通大学作为交通大学的三个源头之一，历史渊源追溯到18',5691939);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京工业大学',' ','北京',1,1,0,1,0,' ','暂空',' ','http://www.bjut.edu.cn','北京工业大学校本部位于北京市朝阳区平乐园100号，东临东四环南路，南抵左安东路，西邻西大望路，北望平乐园小区；另有三地分校区。校园总建设用地面积约63.97公顷。北京工业大学（BeijingUniversityOfTechnology）创建于1960年，是一所以工为主，理、工、经、管、文、法、艺术相结合的多科性市属重点大学。1981年成为国家教育部批准的第一批硕士学位授予单位，1985年成为博士学位授予单位。1996年12月学校通过国家“211工程”预审，正式跨入国家二十一世纪重点建设的百所大学',5102289);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京航空航天大学',' ','北京',1,1,0,1,1,' ','暂空',' ','http://www.buaa.edu.cn','北京航空航天大学（简称“北航”，原名北京航空学院，1988年更名）创办于1952年10月，由当时清华大学、北洋大学等8所著名大学的航空院系合并而成。学校地处北京中关村高科技核心区，毗邻国家奥林匹克体育中心，是新中国第一所重点航空高等院校。早在20世纪50年代初，北航就被国家首批确定为全国15所重点大学之一；80年代中期，被列入国家\"七五\"重点建设的15所高校行列，是我国首批具有博士、硕士学位授予权并成立研究生院的高校之一；90年代，被确定为国家“八五”重点建设的14所高校之一，并成为国家首批获准',6429807);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京航空航天大学软件学院',' ','北京',1,1,0,0,0,' ','暂空',' ','http://soft.buaa.edu.cn/BuaaPublic/html/index.htm','2013年软件工程（软件质量管理与测试）硕士调剂招生简章2013年硕士研究生招录过程中，软件质量管理与测试人才的培养受到学校和国家工信部的高度重视，研究生院、软件学院、工信部软件质量管理与测试教育培训中心共同筹集资源，增加计算机互联网专业的培养计划，接受调剂申请。欢迎各地优秀考生报考。调剂条件：理工科相关专业，参加2013年普研统考，达到我院规定调剂线可直接参加复试。报名方式：登录软件学院报名系统选择\"统考（调剂）\"，预报专业选择软件质量管理与测试专业。复试：侧重专业综合知识、学习能力、个人发展',39760);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京理工大学',' ','北京',1,1,0,1,1,' ','暂空',' ','http://www.bit.edu.cn','北京理工大学创办于1940年，前身是诞生于延安的“自然科学院”，是中国共产党创办的第一所理工科大学，是新中国成立以来国家历批次重点建设的高校，首批设立研究生院，首批进入国家“211工程”和“985工程”建设行列，现隶属于工业和信息化部。七十年栉风沐雨，学校的发展始终得到党和国家的高度重视和大力支持。李富春、徐特立、李强等老一辈无产阶级革命家先后担任学校的主要领导。1942年秋，毛泽东同志亲自为自然科学院题写了校名。1949年，学校迁入北京。1952年，学校更名为北京工业学院，成为新中国第一所国防',8288797);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京科技大学',' ','北京',1,1,1,1,0,' ','暂空',' ','http://www.ustb.edu.cn/','北京科技大学于1952年由天津大学（原北洋大学）、清华大学等6所国内著名大学的矿冶系科组建而成，现已发展成为以工为主，工、理、管、文、经、法等多学科协调发展的教育部直属全国重点大学，是全国首批正式成立研究生院的高等学校之一。1997年5月，学校首批进入国家“211工程”建设高校行列。2006年，学校成为首批“985工程”优势学科创新平台建设项目试点高校。2014年，学校牵头的，以北京科技大学、东北大学为核心高校的“钢铁共性技术协同创新中心”成功入选国家“2011计划”。建校六十多年来，学校逐步形',5237571);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北方工业大学',' ','北京',1,1,0,0,0,' ','暂空',' ','http://www.ncut.edu.cn/','学校概况北方有凤，西山最奇；钟灵毓秀，北方工大。北方工业大学是一所以工为主、文理兼融，具有学士、硕士、博士培养层次和高水平棒垒球运动员招收资格的多科性高等学府，具有硕士研究生免试推荐资格，是教育部“卓越工程师教育培养”院校。学校由中央与北京市共建，以北京市管理为主，前身是创立于1946年的“国立北平高级工业职业学校”。随后几经变迁，于1985年经国务院批准，由北京冶金机电学院易名为北方工业大学。学科专业学校设有计算机学院、电子信息工程学院、电气与控制工程学院、机械与材料工程学院、建筑与艺术学院、',2203147);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京化工大学',' ','北京',1,1,1,1,0,' ','暂空',' ','http://www.buct.edu.cn/','北京化工大学创办于1958年，原名北京化工学院，是新中国为“培养尖端科学技术所需求的高级化工人才”而创建的一所高水平大学。作为教育部直属的全国重点大学，国家“211工程”和“‘985’优势学科创新平台”重点建设院校，肩负着高层次创新人才培养和基础性、前瞻性科学研究以及原创性高新技术开发的使命。北京化工大学经过半个多世纪的建设，已经发展成为理科基础坚实，工科实力雄厚，管理学、经济学、法学、文学、教育学、哲学、医学等学科富有特色的多科性重点大学，形成了从本科生教育到硕士研究生、博士研究生、博士后流动',3629662);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京工商大学',' ','北京',1,6,0,0,0,' ','暂空',' ','http://www.btbu.edu.cn','北京工商大学是北京市重点建设的多科性大学，1999年6月由原北京轻工业学院（1958年成立）与北京商学院（1950年成立）合并组建而成。学校现有41个本科专业，5个第二学士学位专业，31个硕士学位点和1个共建博士学位点，覆盖理学、工学、文学、法学、经济学、管理学、哲学、历史学等8个学科门类，其中9个部级重点学科，2个北京市重点学科，4个北京市重点建设学科，2个国家级检验中心，1个北京市级重点实验室。学校现有高级职称教师400多人，硕士学位以上教师占专任教师总数61%。现已形成以学术造诣深、在国内',3114085);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京服装学院',' ','北京',1,1,0,0,0,' ','暂空',' ','http://www.bift.edu.cn/','北京服装学院1959年2月建校，原名北京纺织工学院。1961年7月，更名为北京化学纤维工学院。1987年2月，改扩建为北京服装学院，成为我国第一所以服装命名的公办全日制普通高等学校。现设有7个院系(服装艺术与工程学院、材料科学与工程学院、艺术设计学院、商学院、信息工程学院、外语系、造型艺术系)、3个教学部门(基础教学部、思想政治理论课教学部、计算机信息中心)，以及研究生部、国际学院、继续教育学院。有在校生8600余人，其中，本科生5900余人，博士、硕士研究生850余人，留学生300余人，成人教',1518746);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京邮电大学',' ','北京',1,1,1,1,0,' ','暂空',' ','http://www.bupt.edu.cn/','北京邮电大学是教育部直属、工业和信息化部共建、首批进行“211工程”建设的全国重点大学，是“985优势学科创新平台”项目重点建设高校，是一所以信息科技为特色、工学门类为主体、工管文理协调发展的多科性大学，是我国信息科技人才的重要培养基地。自1955年建校以来，经过半个多世纪的建设与发展，学校全日制教育已经形成了信息背景浓郁、专业特色鲜明、学科优势突出的办学格局。学校现设有信息与通信工程学院、电子工程学院、计算机学院、自动化学院、软件学院、数字媒体与设计艺术学院、理学院、经济管理学院、人文学院、公',6286627);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京印刷学院',' ','北京',1,1,0,0,0,' ','暂空',' ','http://zhaosheng.bigc.edu.cn/','北京印刷学院隶属于北京市，是由原国家新闻出版总署和北京市共建的全日制普通高等院校。学校的前身是1958年文化部建立的文化学院印刷工艺系。1961年文化学院撤销，印刷系并入中央工艺美术学院（现清华大学美术学院）。1978年，经国务院批准，在印刷工艺系基础上组建成立北京印刷学院。经过57年的发展建设，学校已经成为学科特色鲜明、师资力量雄厚、科学研究创新、办学格局开阔的传媒类大学。学校坚持特色发展，以特色学科建设提升核心竞争力。学校初步形成了传媒科技、传媒文化、传媒管理、传媒艺术四大特色学科专业群，建',1270958);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京石油化工学院',' ','北京',1,1,0,0,0,' ','暂空',' ','http://zhaosheng.bipt.edu.cn','北京石油化工学院创建于1978年，是一所中央与北京市共建，以北京市管理为主的普通高等学校。学校经过三十多年的发展和建设，已成为“立足北京，依托行业，服务首都，面向全国”，适应国家支柱产业和首都经济与社会发展需要，以工为主，理、工、经、管、文相结合，多学科相互渗透、协调发展、具有鲜明工程实践特色的高等学校。学校现有化学工程学院、机械工程学院、信息工程学院、经济管理学院、人文社科学院、材料科学与工程系、数理系、外语系、体育教学部和工程教育中心等10个教学院（系），设置有化学工程与工艺、高分子材料与工',1675407);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京电子科技学院',' ','北京',1,1,0,0,0,' ','暂空',' ','http://www.besti.edu.cn','北京电子科技学院是一所为全国各级党政机关培养信息安全和办公自动化专门人才的高等院校。学院隶属于中共中央办公厅。学院的前身是创建于1947年8月的河北平山的中央工委青年训练班，由中央工委直接管理。创建以来，她先后经历了张家口军委工程学校、北京电子专科学校和北京电子科技学院等发展时期。建校以来，学院始终得到党中央和中央办公厅领导的亲切关怀和高度重视。毛泽东、邓小平、江泽民同志先后题词勉励，江泽民同志还为学院题写了校名。胡锦涛总书记等中央领导同志对学院的建设作出过许多重要指示。温家宝、曾庆红、王刚、栗',2288112);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国农业大学',' ','北京',1,2,1,1,1,' ','暂空',' ','http://www.cau.edu.cn/','学校简介中国农业大学是国家“211工程”和“985工程”重点建设的教育部直属高校。现任党委书记姜沛民，校长柯炳生。中国农业大学是我国现代农业高等教育的起源地，其历史起自于1905年成立的京师大学堂农科大学。1949年9月，由北京大学农学院、清华大学农学院和华北大学农学院合并为北京农业大学。1952年10月，北京农业大学农业机械系与华北农业机械专科学校、中央农业部机耕学校合并成立北京机械化农业学院，同年11月，平原农学院并入北京机械化农业学院。1953年7月更名为北京农业机械化学院，1985年更名',5074199);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京农学院',' ','北京',1,2,0,0,0,' ','暂空',' ','http://www.bua.edu.cn/','北京农学院是一所特色鲜明、多科融合的北京市属都市型高等农业院校，始建于1956年,几经变迁、数易校名，1965年正式升格为大学，1978年经国务院批准定名为北京农学院。学校占地面积2159亩（校本部1136亩，科技园区1023亩），是北京市“花园式”单位，先后被授予“北京市文明校园”和“首都文明单位”等荣誉称号。学校现有15个二级学院（教学部）：生物技术学院、植物科学技术学院、动物科学技术学院、经济管理学院、园林学院、食品科学与工程学院、计算机与信息工程学院、人文社会科学学院、城乡发展学院（继续',1154270);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京林业大学',' ','北京',1,2,1,1,0,' ','暂空',' ','http://www.bfsu.edu.cn/','北京林业大学于1952年建校，办学历史可追溯至1902年的京师大学堂农业科林学目。1952年全国高校院系调整，北京农业大学森林系与河北农学院森林系合并独立设置北京林学院。1956年，北京农业大学造园系和清华大学建筑系部分并入我校，成立城市及居民区绿化专业。学校于1955年开始招收研究生，1956年开始招收留学生。1960年被中央列为全国重点高等院校。1969年，学校被迫迁往云南，辗转十年，于1979年返京复校。1982年国务院批准我校为首批具有博士、硕士学位授予权的高校。1985年更名为北京林业',3895050);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京协和医学院',' ','北京',1,3,0,0,0,' ','暂空',' ','http://www.pumc.edu.cn','北京协和医学院由美国洛克菲勒基金会于1917年创办，它最早开启了我国八年制临床医学教育和高等护理学教育的先河，在我国医学教育领域具有特殊的地位和独特的社会影响力。协和医学院作为卫生部唯一直属高校，与中国医学科学院（简称医科院）实行院校合一的管理体制。协和医学院“高进、优教、严出”的精英培养模式，不仅造就了张孝骞、林巧稚、黄家驷、吴阶平、聂毓婵等中国医学界的泰斗，还为我国医学事业培养了一大批享誉国内外的著名临床医学家、医学科学家、医学教育家、护理学家和医政管理家，在国内外享有很高的声誉。根据教育部',1086270);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都医科大学',' ','北京',1,3,0,0,0,' ','暂空',' ','http://zhshjy.ccmu.edu.cn/col/col4281/index.html','首都医科大学建校于1960年，是北京市重点高等院校，已故著名泌尿外科专家、两院院士、全国人大常委会副委员长吴阶平教授为首任校长。现任党委书记李明教授，校长吕兆丰教授。学校校本部设有10所学院和1所研究院，包括基础医学院、公共卫生学院、化学生物学与药学院、生物医学工程学院、中医药学院、卫生管理与教育学院、护理学院、燕京医学院、全科医学与继续教育学院、国际学院;北京脑重大疾病研究院。学校现有19所临床医学院暨附属医院，包括首都医科大学宣武医院(第一临床医学院)、附属北京友谊医院(第二临床医学院)、附',2820834);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京中医药大学',' ','北京',1,3,1,1,0,' ','暂空',' ','http://www.bucm.edu.cn','北京中医药大学创建于1956年，是中华人民共和国最早成立的高等中医院校之一，是唯一一所进入国家“211工程”建设的高等中医药院校。2000年7月31日原北京中医药大学与原北京针灸骨伤学院合并，组成新的北京中医药大学，并成为直属国家教育部管理的重点大学。学校现设有基础医学院、中药学院、针灸学院、管理学院、护理学院、国际学院、远程教育学院、继续教育学院、临床医学院、文法系、台港澳中医学部等教学机构。学校致力于构建面向新世纪的以高层次教育为主体的人才培养体系，目前设有中医学、中药学、制药工程、针灸推拿',3297885);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京师范大学',' ','北京',1,4,1,1,1,' ','暂空',' ','http://www.bnu.edu.cn','北京师范大学是一所以教师教育、教育科学和文理基础科学为主要特色的著名学府。2002年9月8日，在人民大会堂举行建校100周年庆祝大会。江泽民等党和国家领导同志出席庆祝大会。江泽民同志在会上充分肯定了百年北师大的办学成就，并强调实施科教兴国战略，大力推进教育创新。面向新世纪，北京师范大学确立了建设综合性、有特色、研究型世界知名高水平大学的奋斗目标。100年以前，清末戊戌变法因“需材孔亟”，开启民智，改变中国屡受列强欺凌的状况，在当时中国最高学府和最高教育行政机关的京师大学堂设立了师范馆，成为北京师',6887739);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都师范大学',' ','北京',1,4,0,0,0,' ','暂空',' ','http://www.cnu.edu.cn/','首都师范大学创建于1954年，是一所包括文、理、工、管、法、教育、外语、艺术等专业的综合性师范大学，是教育部、北京市“省部共建大学”，是北京市重点投入建设，进入“211工程”的北京市属重点大学。建校60年来，共培养各类高级专门人才十余万名，是为北京市基础教育输送合格师资和培养其他现代化建设所需人才的重要基地。学校现有博士学位授权一级学科17个，博士点97个，博士后流动站16个，硕士学位授权一级学科26个，硕士点147个。国家重点学科4个，国家重点培育学科1个，北京市一级重点学科8个，北京市二级重',3075733);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都体育学院',' ','北京',1,7,0,0,0,' ','暂空',' ','http://www.cupes.edu.cn','首都体育学院(原北京体育师范学院)是北京市市属院校。座落于北京著名的蓟门烟树景区，毗邻中关村科技园区。学院创建于1956年，原名“北京体育学校”，1960年改建为“北京体育师范学院”，2000年更名为“首都体育学院”。建院49年来，学院的各项事业都取得了令人瞩目的成就。目前，学院在办学规模、办学层次、专业设置、师资队伍、教学质量、科研成果、场馆设施等综合办学实力方面跻身于全国同类院校的前列。2004年，在由中国科学评价研究中心、科技部中国科技信息研究所和中国青年报社联合研发的“中国高校综合竞争力',284166);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京外国语大学',' ','北京',1,5,1,1,0,' ','暂空',' ','http://www.bfsu.edu.cn/','北京外国语大学坐落在北京市海淀区西三环北路，在三环路两侧分设东、西两个校区，是教育部直属、“985”优势学科创新平台和首批国家“211工程”建设的全国重点大学，是目前我国高等院校中历史悠久、教授语种最多、办学层次齐全的外国语大学。北京外国语大学前身是1941年成立于延安的中国抗日军政大学三分校俄文大队，后发展为延安外国语学校，建校始隶属于党中央领导。新中国成立后，学校归外交部领导，1954年更名为北京外国语学院，1959年与北京俄语学院合并组建新的北京外国语学院。1980年后直属国家教育部领导，',4743725);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京外国语大学国际教育学院',' ','北京',0,12,1,0,0,' ','暂空',' ','http://www.beiwaichuguo.com','北京外国语大学北京外国语大学是教育部直属、国家首批“211工程”建设的全国重点大学，是中国唯一一所开设61个语种学历学位教育、唯一一所培养了400多位驻外大使的高等学府，是中国乃至世界所公认的语种最全，教学理念最先进的高等学府。北京外国语大学国际教育学院北京外国语大学国际教育学院暨北外国际教育集团(以下简称为“北外国际”)，直属于北京外国语大学。它随着中国国际化教育发展的需求应运而生，依托北京外国语大学深厚的语言和文化底蕴，以大学视角和国际视野，引领国际教育理念和实践的新突破。北外国际致力于搭建',20850);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京第二外国语学院',' ','北京',1,5,0,0,0,' ','暂空',' ','http://www.bisu.edu.cn/','北京第二外国语学院是在敬爱的周恩来总理的亲切关怀下于1964年创立的，是一所以外国语言文学为主体学科、以旅游管理为特色学科，文学、管理学、经济学、法学等多学科门类协调发展的知名特色大学，是中国外语、翻译、旅游、经贸教学与研究的重要基地。学校拥有外国语言文学、工商管理、中国语言文学、应用经济学4个一级学科硕士学位授权点，英语语言文学、日语语言文学、俄语语言文学、德语语言文学、法语语言文学、阿拉伯语语言文学、亚非语言文学（韩国语）、外国语言学及应用语言学、比较文学与世界文学、美学、语言学及应用语言学',1608660);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京语言大学',' ','北京',1,5,1,0,0,' ','暂空',' ','http://www.blcu.edu.cn','北京语言大学是中国教育部直属高等学校，是在周恩来总理的亲自关怀下建立的，创办于1962年。1964年6月定名为北京语言学院，1974年毛泽东主席为学校题写校名，1996年6月更名为北京语言文化大学，2002年校名简化为北京语言大学。北京语言大学是中国唯一一所以对来华留学生进行汉语、中华文化教育为主要任务的国际型大学，素有“小联合国”之称；学校对中国学生进行外语、中文、信息科学、金融等专业教育，同时承担着培养汉语师资、出国留学预备人员出国前的外语培训工作等任务。经过五十多年的发展，北语已经发展成为',2341638);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国传媒大学',' ','北京',1,8,1,1,0,' ','暂空',' ','http://www.cuc.edu.cn','中国传媒大学是教育部直属的国家“211工程”重点大学。学校致力于广播、电视、电影、网络、出版、报刊及新媒体高层次人才培养和科学研究，是一所文、工、艺、管、理、经、法、教多学科协调发展，以信息传播为特色的综合性大学。学校位于中国北京东郊古运河畔，校园占地708亩，校舍总面积46万平方米。校训是：立德、敬业、博学、竞先。学校被誉为中国广播影视传播人才的摇篮。50多年来，学校为广播影视系统及信息传播业培养输送毕业生6万多人，培训在职人员35万多人次。校友遍布全国各地及世界70多个国家和地区，形成了一支',5900311);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中央财经大学',' ','北京',1,6,1,1,0,' ','暂空',' ','http://www.cufe.edu.cn','中央财经大学是教育部直属的国家“211工程”重点建设大学，是新中国成立后国家举办的第一所财经院校，在50多年的办学过程中始终得到了党和国家三代领导集体及教育部、财政部领导的特别关怀。1949年11月6日学校成立时的校名为中央税务学校，1951年改称中央财政学院。1952年全国院校调整时，将北京大学、燕京大学、清华大学、辅仁大学的财经系科并入我校后更名为中央财经学院，直属高教部领导，后改由财政部、人民银行总行领导（“文革”后改为财政部一家领导），校名改为中央财政金融学院。1996年经教育部批准更名',7197018);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('对外经济贸易大学',' ','北京',1,6,1,1,0,' ','暂空',' ','http://www.uibe.edu.cn','对外经济贸易大学，座落于首都北京朝阳区。校园草木葱郁，环境幽雅。功能齐全的教学楼，藏书丰富、实现自动化管理的图书馆，拥有卫星地面站和现代化语言实验室的教育技术中心，设备先进齐全的网络与计算中心，可供正式比赛使用的运动场和游泳场，舒适方便的学生公寓，神采飞扬的学生，温文尔雅的教师……一切，都透露出这所高等学府的青春活力、现代气息。对外经济贸易大学创建于1951年。2000年以前为原对外贸易经济合作部所属。2000年6月，原中国金融学院与原对外经济贸易大学合并成立新的对外经济贸易大学，转为教育部直属',4996519);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京物资学院',' ','北京',1,6,0,0,0,' ','暂空',' ','http://www.bwu.edu.cn/','北京物资学院是一所北京市属的主要为流通领域培养高素质专门人才的普通高等院校。前身是1963年北京经济学院设立的物资管理系。1980年，为适应我国改革开放和社会主义经济发展对物资管理人才的需要，经国务院批准，成立了北京物资学院。学校先后隶属于国家物资局、物资部、国内贸易部，1998年，国家对高等教育管理体制进行调整，学校划归北京市管理。经过几十年的艰苦创业、开拓创新，学校目前已基本发展成为一所涵盖经济学、管理学、理学、工学、文学、法学等学科门类，以物流学科为特色，以经济学科为基础，以管理学科为主干',1867202);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都经济贸易大学',' ','北京',1,6,0,0,0,' ','暂空',' ','http://www.cueb.edu.cn/index.htm','首都经济贸易大学创建于1956年，是由原北京经济学院和原北京财贸学院于1995年3月合并、组建的北京市属重点大学。50多年来，学校坚持育人为本的办学宗旨，以培养\"崇德尚能，经世济民\"之才为己任，为国家和社会培养了一大批优秀人才。经过几代首经贸人自强不息地开拓和建设，学校已经发展成为拥有经济学、管理学、法学、文学、理学和工学等六大学科，以经济学、管理学为重要特色和突出优势，各学科相互支撑、协调发展的现代化、多科性财经类大学。学校本部位于丰台区花乡，地处中关村高科技园区丰台园内，以全日制本科生和研究',3920555);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都经济贸易大学华侨学院',' ','北京',1,6,0,0,0,' ','暂空',' ','oldname": "首都经济贸易大学华侨学院','首都经济贸易大学创建于1956年，是经教育部批准、由原北京经济学院和原北京财贸学院于1995年合并组建的北京市属重点大学，是一所已有50多年建校历史，经济学、管理学为重要特色和突出优势，兼有法学、文学、理学、工学等六大学科相互支撑、协调发展的现代化、多科性财经类大学。学校设有经济学院、劳动经济学院、工商管理学院、统计学院、金融学院、财政税务学院、会计学院、城市经济与公共管理学院、信息学院、人文学院、安全与环境工程学院、法学院、马克思主义学院、华侨学院、外语系及体育教学部等教学单位，还设有研究生部',32361);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('外交学院',' ','北京',1,5,0,0,0,' ','暂空',' ','http://www.cfau.edu.cn','外交学院是外交部直属的唯一一所培养外交外事、国际问题研究和涉外经济、法律复合型人才的高等学校。经国务院学位委员会批准有权授予学士、硕士和博士三级学位。外交学院创建于1955年,是由周恩来总理亲自倡议,经党中央批准成立的。周恩来总理向我国外交人员提出的“站稳立场、掌握政策、熟悉业务、严守纪律”的“十六字方针”是我院的校训。1961??1969年陈毅副总理兼任我院院长。现任院长为国际展览局主席、全国政协副秘书长吴建民大使，党委书记为秦亚青教授。我院认真贯彻江泽民同志为我院所作“立足祖国,放眼世界,面',1567319);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民公安大学',' ','北京',1,11,0,0,0,' ','暂空',' ','http://www.cppsu.edu.cn/','中国人民公安大学是公安部直属的普通高等院校暨公安部高级警官学院，创办于1948年7月，历经华北公安干部学校、中央公安干部学校、中央人民公安学院、中央政法干部学校等时期。1984年1月，改建为全日制普通高等本科院校。1998年2月，与原中国人民警官大学合并，组成新的中国人民公安大学。半个多世纪以来，先后为全国公安政法机关培养、输送了17万余名各级领导、业务骨干和专门人才，被誉为“共和国警官的摇篮”。学校的建设与发展始终得到党和国家历代领导人的高度重视。毛泽东主席曾多次接见学校学员，邓小平同志亲笔为',3430985);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('国际关系学院',' ','北京',1,11,0,0,0,' ','暂空',' ','http://www.uir.cn/','国际关系学院地处北京西北郊风景区，西望颐和园，东临圆明园，毗邻北大、清华、中央党校等著名学府和中关村科技园区，精英荟萃和人杰地灵的条件使其地理位置和教育科研环境十分优越。国际关系学院有50多年成长发展的光荣历史，以培养新中国涉外工作专业人才著称。学院曾首创侧重外国当代语言学习的教学方法，率先完成从单纯外语专业向复合型涉外专业的转型，长期坚持宽口径、厚基础、新视野、强素质的办学传统，为国家政法机关、外事部门、新闻媒体、大中型企业及教育科研等单位培养输送了大批人才。国际关系学院现由教育部管理，以外语',1438151);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京体育大学',' ','北京',1,7,0,1,0,' ','暂空',' ','http://www.bsu.edu.cn','北京体育大学是国家体育总局直属、全国惟一的一所体育大学。是全国重点院校和“211工程”院校之一。是中国体育的最高学府。学校原名中央体育学院，筹建于1952年7月，1953年8月31日中央人民政府批准成立，1953年11月3日学校在北京先农坛举行开学典礼,1954年迁入现址，1956年3月14日，学校更名为北京体育学院，1993年12月11日，学校改名为北京体育大学,校庆日为11月1日。学校位于北京市海淀区中关村北大街，地处中关村高科技园区，毗邻圆明园遗址公园，学校现占地面积1000余亩，建筑面积',2012714);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中央音乐学院',' ','北京',1,8,1,1,0,' ','暂空',' ','http://www.ccom.edu.cn/','中央音乐学院建于1950年，其前身是二十年代至四十年代的各具特色的几所高等音乐院、系。分别是建于1927年的燕京大学音乐系，建于1939年的华北大学文艺学院音乐系，建于1940年的南京国立音乐院，建于1946年的国立北平艺术专科学校音乐系和香港、上海的中华音乐院，建于1948年的东北鲁迅文艺学院音乐系等。其主体是南京国立音乐院。1958年学院由天津迁至北京，坐落在北京西城区复兴门原清醇王府旧址（光绪皇帝出生地）。原隶属文化部，2000年归属教育部。1960年被定为国家重点高等学校，1999年被列',410569);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国音乐学院',' ','北京',1,8,0,0,0,' ','暂空',' ','http://www.ccmusic.edu.cn/ccmusic/mainweb/','中国音乐学院成立于1964年，是根据周恩来总理的提议而建立的。她是我国唯一一所以中国民族音乐教育和研究为主要特色，培养从事民族音乐理论研究、创作、表演和教育，推动民族音乐文化继承和发展的高级专门人才的高等音乐学府。学院现设音乐学系、作曲系、声乐歌剧系、中国民族器乐系、音乐教育系、钢琴系、艺术管理系、指挥系、管弦系、社会科学部等10个教学单位，有音乐学、作曲与作曲技术理论、音乐表演等3个本科专业；拥有音乐学博士、硕士学位授予权，艺术硕士（MFA）授予权；具有高校教师在职攻读硕士学位和同等学力申请硕',150640);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中央美术学院',' ','北京',1,8,1,0,0,' ','暂空',' ','http://www.cafa.edu.cn/','中央美术学院是教育部直属的唯一一所高等美术学校，于1950年4月由国立北平艺术专科学校与华北大学三部美术系合并成立。毛泽东为学院题名。徐悲鸿、江丰、吴作人、古元、靳尚谊先后担任院长。现任党委书记为杨力同志，现任院长为著名画家、美术史论家潘公凯教授，现任院学术委员会主任为著名油画家靳尚谊教授。北平艺术专科学校的历史可以上溯到1918年，由著名教育家蔡元培先生积极倡导下成立的国立北京美术学校，这是中国历史上第一所国立美术教育学府，也是中国现代美术教育的开端。华北大学美术系的前身是1938年创建于延安',401741);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中央戏剧学院',' ','北京',1,8,1,0,0,' ','暂空',' ','http://www.chntheatre.edu.cn','中央戏剧学院是教育部直属院校，是一所以戏剧影视艺术教育为主的艺术类大学,是中国戏剧艺术教育的最高学府之一。她是我国戏剧、影视艺术教学与科研的中心，是从事戏剧影视艺术训练和实践的重要基地。中央戏剧学院1950年4月正式成立，毛泽东主席亲笔题写校名。她的前身是延安鲁迅艺术学院、华北大学文艺学院、南京国立戏剧专科学校。著名戏剧家欧阳予倩、著名表演艺术家金山、著名导演艺术家徐晓钟先后担任院长，曹禺、张庚、光未然、沙可夫、李伯钊等著名戏剧家曾担任过学院院领导。党的三代领导人毛泽东、周恩来、朱德、彭德怀、陈',383461);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国戏曲学院',' ','北京',1,8,0,0,0,' ','暂空',' ','http://www.nacta.edu.cn','中国戏曲学院是中国戏曲教育的最高学府，是中国戏曲高等教育体系的建立者，在国内戏曲教育领域学科最完备、培养体系最健全、优秀师资最集中、输送人才最多、社会影响最大，作为中国戏曲高等教育的引领者和示范者，支撑了建国以来中国戏曲事业对高层次人才的基本需求，对中国戏曲事业的传承与发展有举足轻重的作用。2008年，中国戏曲学院当选为联合国教科文组织国际剧协戏剧院校亚太局董事会第一副主席单位。1、历史沿革学院原隶属文化部。1950年1月28日，文化部戏曲改进局戏曲实验学校成立，1955年1月正式定名为中国戏曲',147149);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京电影学院',' ','北京',1,8,0,0,0,' ','暂空',' ','http://www.bfa.edu.cn','北京电影学院是中国唯一一所培养电影专业人才的高等学校，近55年来，逐步形成了以专业制作为主体，教学、创作、科研并重，多学科，多层次的电影教育体系。北京电影学院以其优良的教学传统，以及师资力量雄厚，系科专业齐全，教学设备完善，教学秩序规范等独特优势，成为中国培养电影艺术创作、管理及理论研究人才的重要基地。多年来，北京电影学院以其骄人的业绩，辉煌的历史，在中国和世界上享有盛誉，吸引了一大批决心献身于电影事业的海内外学子。北京电影学院还有一支具有很高学术水准的教师队伍，形成了一个面向21世纪的、以培养',964029);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京舞蹈学院',' ','北京',1,8,0,0,0,' ','暂空',' ','http://www.bda.edu.cn','北京舞蹈学院，是新中国建立的第一所学制式专业舞蹈院校。学院前身“北京舞蹈学校”，始建于1954年。文化部于1978年正式批准学校为北京舞蹈学院，并直属文化部。1999年，获得舞蹈学硕士学位授予权，2005年获得艺术硕士学位授予权。2000年，北京舞蹈学院由直属文化部转为“中央和北京市共建，以北京市管理为主”。同年被北京市评为“文明校园”，同时获文化部、人事部颁发的“全国文化工作先进集体”荣誉称号。学院现有专任教师214人，其中高级职称75人。全日制在校大学生总数1804人，其中本科生1175人，',185953);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中央民族大学',' ','北京',1,9,0,1,1,' ','暂空',' ','http://www.muc.edu.cn/','中央民族大学（MinzuUniversityofChina）坐落于北京学府林立的海淀区，南邻国家图书馆，北依中关村科技园，校园环境典雅，古朴幽美，人文氛围浓郁，具有鲜明的民族特色。中央民族大学的前身是1941年9月中国共产党在延安创办的民族学院。1951年6月，中央民族学院在北京正式成立，乌兰夫任首任院长。1993年11月，更名为中央民族大学。现任校党委书记鄂义太教授、校长陈理教授。党和国家历届领导集体始终关心和重视中央民族大学的建设和发展。建校初至1966年，毛泽东、刘少奇、周恩来、朱德等党和',4242308);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国政法大学',' ','北京',1,11,1,1,0,' ','暂空',' ','http://www.cupl.edu.cn','中国政法大学是一所以法学为主，兼有哲学、经济学、教育学、管理学等多学科的教育部直属全国重点大学。中国政法大学的前身是1952年成立的由北京大学、燕京大学、辅仁大学、清华大学的法学、政治学、社会学等学科合并组建的北京政法学院，著名法学家、政治学家钱端升先生为首任院长。1983年，在中央“要抓紧筹办中国政法大学，把它建成我国政法教育中心”的指示下，学院更名为中国政法大学，邓小平同志为大学题写了校名。经过五十多年的建设与发展，学校现已拥有2个全国人文社科重点研究基地、1个博士后流动站及18个博士点、4',4892661);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华北电力大学',' ','北京',1,1,1,1,0,' ','暂空',' ','http://www.ncepu.edu.cn','华北电力大学是教育部直属的国家“211工程”重点建设高校，是教育部与由国家电网公司等七家特大型电力企业集团组成的校理事会共同建设的全国重点大学。学校创建于1958年，原名北京电力学院，1969年迁至河北，后更名为华北电力学院。1978年经国务院批准为全国重点大学。1995年5月，经原国家教委批准，华北电力学院和北京动力经济学院合并组建华北电力大学。学校校本部设在北京，分设保定校区，两地实行一体化管理。学校专业设置比较齐全，教学、科研条件比较完备，博士、硕士与本科教育层次兼备，并与高等成人教育协调',5326816);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南开大学',' ','天津',1,0,1,1,1,' ','暂空',' ','http://www.nankai.edu.cn/','南开大学是国家教育部直属重点综合性大学，是敬爱的周恩来总理的母校。南开大学创建于1919年，创办人是近代著名爱国教育家严修和张伯苓。抗日战争时期，南开大学与北京大学、清华大学在昆明组成举世闻名的西南联合大学，被誉为“学府北辰”。南开大学秉承“允公允能、日新月异”的校训，弘扬“爱国、敬业、创新、乐群”的光荣传统，培养了以周恩来、陈省身、吴大猷、曹禺等为代表的一大批杰出人才，为民族振兴和国家富强作出了重要贡献。新世纪新阶段，南开大学获得教育部和天津市重点共建支持，跻身21世纪国家重点建设大学行列。南',8309229);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津大学',' ','天津',1,1,1,1,1,' ','暂空',' ','http://www.tju.edu.cn/','天津大学是教育部直属国家重点大学，其前身为北洋大学，始建于1895年10月2日，是中国第一所现代大学，素以“实事求是”的校训、“严谨治学”的校风和“爱国奉献”的传统享誉海内外。1951年经国家院系调整定名为天津大学，是1959年中共中央首批确定的16所国家重点大学之一，是“211工程”、“985工程”首批重点建设的大学。天津大学的发展始终得到了党和国家主要领导人的热情关怀和有力支持，毛泽东、周恩来、邓小平、江泽民、胡锦涛、习近平等曾先后亲临学校视察。学校占地面积182万平方米，建筑面积142.5',10140872);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津科技大学',' ','天津',1,1,0,0,0,' ','暂空',' ','http://www.tust.edu.cn','天津科技大学位于渤海之滨、海河之畔，是中央和地方共建、天津市重点建设的以工为主，工、理、文、经、管、法、艺等学科协调发展的多科性大学。学校在五十余年的办学历史过程中，突出“坚持拓展轻工特色，精心培育行业中坚，矢志服务国计民生”的办学特色，立足轻工、服务社会，立足天津、面向全国，输送各类人才10万余名，他们在各自的工作岗位上，敬业奉献，拼搏进取，展现了科大学子内敛、踏实的精神品质。学校毕业生以基础扎实、素质全面、富于开拓精神和实践能力而受到社会各界青睐。历史沿革天津科技大学创建于1958年，时名为',5892634);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津工业大学',' ','天津',1,1,0,0,0,' ','暂空',' ','http://www.tjpu.edu.cn/','天津工业大学简介天津工业大学是教育部与天津市共建、天津市重点建设的全日制普通高等学校，在教育部首轮本科教学评估中获得优秀。学校历史悠久，最早的系始建于1912年，现已发展成为一所以工为主，工、理、文、管、经、法、艺协调发展的多科性工业大学。学校总占地面积约184万平方米，总建筑面积约93万平方米。学校下设1个学部、16个学院，具有培养学士、硕士和博士的完整教育体系，现有全日制在校生28000余名，其中研究生2800余名、本科生24000余名。学校现有2个一级学科博士点、16个二级学科博士点、20',5072897);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国民航大学',' ','天津',1,1,0,0,0,' ','暂空',' ','http://www.cauc.edu.cn/zh/','1．历史沿革中国民航大学是中国民用航空局直属的一所以培养高级工程技术和管理人才为主的普通高等学校。学校的前身是1951年9月成立的军委民航局第二民用航空学校。1958年12月更名为中国民用航空高级航空学校，1963年6月列入普通高校序列，更名为中国民用航空机械专科学校。1981年8月10日，更名为中国民用航空学院。2006年5月30日，更名为中国民航大学。历经60多年的建设与发展，学校已成为目前我国唯一一所民航学科专业门类齐全、将航空宇航科学技术与交通运输工程两大学科群交叉融合的高等学府，被誉为',5583939);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津理工大学',' ','天津',1,1,0,0,0,' ','暂空',' ','http://www.tjut.edu.cn/','天津理工大学是一所以工为主，工理结合，工、理、管、文、艺等学科协调发展的多科性大学。学校始建于1979年，前身为天津理工学院。1996年，与原天津大学分校、天津大学机电分校、天津大学冶金分校三所本科院校合并，组建成新的天津理工学院。经教育部批准，2004年更名为天津理工大学。学校坐落于天津市西青区宾水西道391号，总占地面积156.7万平方米、建筑面积67.3万平方米。本科以上全日制在校生26202人。现有专业学院17个，还设有继续教育学院、工程训练中心、体育教学部、信息技术研究所等机构。现有本',5884443);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津农学院',' ','天津',1,2,0,0,0,' ','暂空',' ','http://www.tjau.edu.cn/','天津农学院始建于1976年。2002年经天津市政府批准，天津市城乡经济学校并入天津农学院。1982年获学士学位授予权，2006年获硕士学位授予权。1996年首批通过教育部本科教学合格评价，2007年在教育部进行的本科教学工作水平评估中获得“优秀”。学校以农科为主体和优势，农学、工学、管理学、理学、经济学、文学协调发展。经过多年的办学实践，形成了紧贴区域经济社会发展，服务于都市型现代农业的鲜明办学特色。学校现有东、西两个校区，校园占地面积1049亩，校舍建筑面积30万平方米。建有现代化的教学楼、实',1874587);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津医科大学',' ','天津',1,3,0,1,0,' ','暂空',' ','http://www.tijmu.edu.cn/','天津医科大学的前身天津医学院创建于1951年，是新中国成立后国家政务院批准新建的第一所高等医学院校，著名内分泌学家、医学教育家朱宪彝教授为首任校长。1993年，天津市委、市政府决定天津医学院与天津第二医学院合并组建天津医科大学，同年12月得到国家教委批准。1994年6月，天津医科大学正式组建成立。学校现任党委书记为张连云教授，校长为著名生物化学与分子生物学家、中国科学院院士尚永丰教授。学校为国家“211工程”重点建设院校。学校发展层次类型定位为研究教学型，以医学为核心，以生命科学为主要依托的多科',3673983);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津中医药大学',' ','天津',1,3,0,0,0,' ','暂空',' ','http://www.tjutcm.edu.cn','天津中医药大学始建于1958年。1992年经国家教委批准，加挂中国传统医药国际学院校牌，是教育部高等学校中医学教学指导委员会和世界中医药学会联合会教育指导委员会挂靠单位。学校建有中医学、中药学2个博士后科研流动站，中医学、中药学2个一级学科博士学位授权学科，18个博士学位授权学科和25个硕士学位授权学科，18个本科专业。学校现有教职工3200余人，有中国工程院院士3人，现有本科以上在校生9000余人，其中留学生1100余人。学校目前拥有针灸推拿学和中医内科学2个国家级重点学科，7个省部级重点学科',2332087);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津师范大学',' ','天津',1,4,0,0,0,' ','暂空',' ','http://www.tjnu.edu.cn/','天津师范大学是天津市属重点院校，始建于1958年，原名天津师范学院，1982年更名为天津师范大学。1999年4月，原天津师范大学、天津师范高等专科学校、天津教育学院合并组建新天津师范大学。学校占地面积3519.88亩（2346596平方米）、建筑面积854207平方米。2005年9月，坐落于西青区的天津师范大学新校区正式启用，办学环境得到根本性改善，为学校发展奠定了坚实的基础。学校现有24个学院，61个本科专业，涉及文学、理学、教育学、历史学、法学、经济学、管理学、工学、艺术学等9个学科门类。学',4772698);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津职业技术师范大学',' ','天津',1,4,0,0,0,' ','暂空',' ','http://www.tute.edu.cn','天津职业技术师范大学是我国最早建立的以培养职业教育师资为主要任务的普通高等师范院校，成立于1979年，隶属于原国家劳动部；2000年，转制为中央与地方共建，以天津市管理为主；2010年，更名为天津职业技术师范大学；2012年，国家教育部与天津市人民政府共建天津职业技术师范大学，学校成为天津市地方高校中唯一一所部市共建高校。办学特色闻名全国。学校始终秉承“动手动脑、全面发展”的办学理念，坚持为全国培养高素质职教师资的办学定位不动摇，在全国首创“实行‘双证书’制，培养‘一体化’职教师资”的人才培养新',1782452);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津外国语大学',' ','天津',1,5,0,0,0,' ','暂空',' ','http://www.tjfsu.edu.cn/','天津外国语大学是一所以外国语言文学学科为优势学科，文学、经济学、管理学、法学、教育学、艺术学、工学等多学科协调发展、特色鲜明的高等学校，是全国最早独立设置的八所外语院校之一。1921年，学校前身之一的天津工商大学在马场道建校，之后先后更名为津沽大学、河北大学等。1964年，在周恩来总理的亲切关怀下，学校主体前身——秦皇岛外语专科学校（后更名为天津外国语专科学校）成立。1974年，由原天津外国语专科学校、原天津外国语学校、原天津师范学院日语专业、原河北大学外语和中文专业的学术力量和教师等重组为天津',3410559);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津商业大学',' ','天津',1,6,0,0,0,' ','暂空',' ','http://www.tjcu.edu.cn','天津商业大学创建于一九八〇年，原隶属国家商业部，现由天津市领导和管理，是一所以商学为主，管、经、工、法、文、理多学科相互支撑、协调发展的高等学校。学校坐落在美丽的子牙河畔，占地1379亩，为师生教学、科研、学习及生活创造了良好的条件。学校设有13个学院、4个教学部、2个省(部)级重点实验室(基地)、4个省(部)级重点学科和多个研究所(中心)。学校现有45个本科专业，25个硕士点，在校生1.3万余人。“十五”期间，国家财政部与天津市政府共同支持重点学科建设，教学科研仪器设备总值已超过亿元，具有一批',5530854);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津财经大学',' ','天津',1,6,0,0,0,' ','暂空',' ','http://www.tjufe.edu.cn','天津财经大学是新中国最早建立的财经大学之一，是一所以应用经济和工商管理学科为主干，兼有文学、法学、理学、工学、教育七大学科门类交叉渗透、协调发展的多科性大学。学校始建于1958年，是以南开大学经济类专业为基础，并从华北、东北地区高校汇聚一批经济学领域享有盛誉的名师组建起来的。原名为河北财经学院，1969年10月1日划归天津市领导，更名为天津财经学院。2004年5月17日，经国家教育部批准，更名为天津财经大学。沧桑砺洗，春华秋实。经过近50年的建设和发展，特别是沐浴着改革开放的春风，学校的面貌发生',5555635);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津体育学院',' ','天津',1,7,0,0,0,' ','暂空',' ','http://www.tjus.edu.cn','津体育学院建立于1958年8月。当时，国家体委、教育部根据华北地区体育教育事业发展需要以及体育师资、竞技体育和体育管理人才匮乏的状况，决定在当时的河北省省会天津市组建天津体育学院，由当时的河北省副省长、天津市市委书记、市长李耕涛兼任第一任院长。建校初期，学校拥有田径、球类、游泳、跳水、射击等教学训练场馆以及实验室、图书馆等教学设施。我国运动生理学奠基人陈家琦、著名体育管理学与体育场地建筑学专家马喻、体育理论专家牛兴华、运动心理学专家柴文秀、运动生物力学专家刘世藩以及在竞技体育领域取得突出成就的体',779884);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津音乐学院',' ','天津',1,8,0,0,0,' ','暂空',' ','http://www.tjcm.edu.cn/','天津音乐学院于一九五八年十月四日建院。发展和繁荣音乐文化事业，为两个文明建设做贡献是学院的办学宗旨。培养适应社会需要的德艺双馨的音乐理论、音乐创作、音乐表演专门人才和音乐师资是办学的中心任务。建院四十年来，已为国家培养和输送了四千多音乐人才。毕业生中许多人已成为著名作曲家、歌唱家、演奏家、优秀音乐教师和管理骨干，其中有多人次在国际音乐比赛中获奖，有大量的已毕业或在校的学生在国内各种比赛中获奖。他们为音乐事业的繁荣与发展作出了可喜的贡献。学院现占地139.58亩（92,957.78平方米）。教学行',202375);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津美术学院',' ','天津',1,8,0,0,0,' ','暂空',' ','http://www.tjarts.edu.cn','天津美术学院座落在海河之滨、三叉河口，位于天津市中心。其前身为北洋女师范学堂，1906年6月由中国近代著名教育家傅增湘先生创办，是我国最早的公立高等学府之一。辛亥革命与\"五四\"运动中，涌现出邓颖超、郭隆真、刘清扬、许广平等一大批先进分子，成立了\"觉悟社\"，并同周恩来、马骏等男校学生一起，领导了当时天津的反帝、反封建斗争，谱写了中国近代史上的光辉一页。该校初期是师范教育的学校，1926年左右，专门设立了美术科，同时增设了西画、国画、图案三个专用教室。1929年美术科发展为图画副系，学校成为当时国内',328705);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北大学',' ','河北',1,0,0,0,0,' ','暂空',' ','http://www.hbu.edu.cn','学校1921年始建于天津，前身是由法国耶稣会士创办的天津工商大学，先后易名为天津工商学院、津沽大学、天津师范学院、天津师范大学，1960年定名为河北大学。1970年迁至国家历史文化名城——河北省保定市。基础设施学校占地面积162.86万平方米，总建筑面积95万平方米，固定资产总值14.6亿元，其中教学科研仪器设备总值1.93亿元；学校建有省内高校设备技术一流的校园网络中心、新闻中心以及环境一流的体育馆、音乐馆、大学生活动中心和艺术教育中心；拥有河北省唯一一家高校出版社——河北大学出版社；学校图书',4104696);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北工程大学',' ','河北',1,1,0,0,0,' ','暂空',' ','http://zhaosheng.hebeu.edu.cn','河北工程大学是一所以实施现代高等工程教育为鲜明特色的河北省省属高校。经过几年来的励精图治、科学发展，学校已成为一所综合实力较强、办学水平较高、办学特色鲜明的多科性大学。学校校园占地面积2340亩，各类建筑面积87.7万平方米。现有教职工2718人，其中：教授224人、副教授422人；有博士学历学位人员135人、硕士学历学位人员696名。现有全日制在校生近30000人，其中硕士研究生710人。学校建有学士、硕士两级人才培养体系。设置13个学院和2个教学部以及58个本科专业。拥有21个硕士学位授权点',3931363);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北地质大学',' ','河北',1,6,0,0,0,' ','暂空',' ','http://www.sjzue.edu.cn/','河北地质大学（原石家庄经济学院）创办于1953年，是新中国最早设置经济管理类专业的地质部直属地质院校。2000年，学校管理体制由国土资源部管理转隶为中央与地方共建，以河北省管理为主。2013年国土资源部、河北省人民政府签署“省部共建石家庄经济学院协议”，学校成为省部共建大学。2016年3月1日，国家教育部批复河北省《教育部关于同意石家庄经济学院更名为河北地质大学的函》（教发函【2016】52号文件）；3月15日，河北省教育厅发文《河北省教育厅关于同意石家庄经济学院更名为河北地质大学的通知》（冀教',3052563);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华北电力大学保定校区',' ','河北',1,1,1,1,0,' ','暂空',' ','http://202.206.208.40/zs/','华北电力大学是教育部直属的国家“211工程”重点建设大学、国家“985工程优势学科平台”重点建设大学。学校校部设在北京，分设保定校区。学校现有教职工3千余人，全日制在校本科生2万余人，研究生1万人。学校占地1600余亩，建筑面积100余万平方米。学校1958年创建于北京，原名北京电力学院。1969年由北京迁至河北，先后更名为河北电力学院、华北电力学院。1995年华北电力学院与北京动力经济学院(含华北电力学院北京研究生部)合并组建为华北电力大学。2003年，在国家电力体制改革中，学校由国家电力公司',3277766);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北工业大学',' ','天津',1,1,0,1,0,' ','暂空',' ','http://www.hebut.edu.cn/','河北工业大学是一所以工科为主，工、理、管、经、文、法多学科协调发展的河北省属骨干大学，是进入国家“211工程”重点建设的高校。学校坐落在沿海开放城市——天津市，分为北辰校区和丁字沽校区，并在河北省廊坊市设有分校。校园占地面积3000亩，建筑面积80余万平方米，具有先进的教学、科研条件，学习生活设施齐全，环境优美，是青年学子求知成材的理想学府。学校具有百余年的办学历史，其前身是始建于1903年的“北洋工艺学堂”，曾先后定名“直隶高等工业学堂”、“直隶公立工业专门学校”、“河北省立工业学院”、“河北',4647143);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华北理工大学',' ','河北',1,0,0,0,0,' ','暂空',' ','http://www.ncst.edu.cn/','华北理工大学，坐落在沿海现代化城市、环渤海经济区工业重镇—唐山市;是一所以工、医为主，理、经、管、文、法、艺多学科协调发展，具有留学生教育、研究生教育、本科教育、继续教育等全方位教育层次的省属重点骨干大学。学校为河北省人民政府与国家安全生产监督管理总局、河北省人民政府与国家国防科技工业局共建高校。百年传承，底蕴深厚。华北理工大学的前身河北联合大学是2010年5月经教育部批准，由原河北理工大学和华北煤炭医学院强强联合组建而成的。河北理工大学前身为创办于1895年的北洋西学学堂矿务学学门，1958年',3687598);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北科技大学',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.hebust.edu.cn','河北科技大学是河北省重点建设的多科性骨干大学，坐落在太行山东麓的河北省省会石家庄市，1996年由原河北轻化工学院、河北机电学院、河北省纺织职工大学合并组建而成，2002年河北纺织工业学校并入。2013年，成为河北省人民政府与国家国防科技工业局共建高校。学校占地2617亩，建筑面积89.6万平方米。固定资产总值14.9亿元，图书馆馆藏文献192万册，数字化信息资源总量达39T。学校现有教职工2318人，其中教学科研人员1314人，具有副高级以上职称的专任教师749人，博士生导师10人，硕士生导师4',3927970);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北建筑工程学院',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.hebiace.edu.cn','河北建筑工程学院是河北省省属全日制建筑类高等本科院校，是教育部批准的具有高等学历教育招生资格的普通高等院校，国务院首被教育部批准为硕士学位授予单位。批批准具有学士学位授予权的院校。2013年学校创建于1950年，坐落于拥有“中国北方最美蓝天”美誉的塞外名城张家口市。学校占地960亩，建筑面积32万平方米，固定资产总值1亿多元。学校现有教职工近700人，其中专任教师491人，具有高级职称的282人，其中教授101人，具有博士、硕士学位的414人，有国家执业注册师资格的70多人。现有全日制本、专科在',1612853);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北水利电力学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hbgz.edu.cn/','河北工程技术高等专科学校坐落于沿海开放城市沧州市，是河北省教育厅直属的一所综合性工科高等专科学校。学校的前身是1952年河北省水利厅在天津建立的河北水利土木学校，是当时华北地区最早建立的一所水利学校。1955年更名为\"河北省天津水利学校\"。1958年，迁往保定市，并升格为高等学校，校名为\"河北水利电力学院\"，1960年改称为\"河北水利学院\"。1966年，迁往石家庄平山县岗南水库，校名改称为\"河北水利工读专科学校\"。1970年经河北省批准，学校迁往沧州市，定名为\"河北水利专科学校\"。1992年经原',279755);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北农业大学',' ','河北',1,2,0,0,0,' ','暂空',' ','http://www.hebau.edu.cn/','河北农业大学位于古城保定，是河北省重点骨干大学，教育部本科教学工作水平评估优秀学校。学校风景秀丽，环境幽雅，建筑楼群鳞次栉比，是陶冶情操、求学、授业的理想之地。历史悠久，历来受到国家的高度重视。学校创建于1902年(清光绪28年)，是我国成立最早的高等农业院校之一，河北省建立最早的高等院校，先后经历了直隶农务学堂、直隶高等农业学堂、河北省立农学院、河北农业大学等历史时期。上世纪50年代，全国高等院校进行院系调整，学校的森林系整建制调整到北京，参与组建了北京林学院(现北京林业大学)，畜牧兽医系整建',2037528);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北医科大学',' ','河北',1,3,0,0,0,' ','暂空',' ','http://www.hebmu.edu.cn','北医科大学位于河北省省会石家庄市，是1995年5月由原河北医学院、河北中医学院、石家庄医学高等专科学校合并组建的。其中原河北医学院建校最早，前身是1894年清政府批准建立的北洋医学堂。原河北中医学院、石家庄医学高等专科学校也有五十多年的办学历史。河北医科大学已成为集西医、中医、药学等学科专业为一体的综合性医科大学。现设有16个学院（含独立学院），19个本、专科专业，其中临床医学专业、临床医学专业（中西医结合方向）招收七年制本硕连读学生。建有基础医学、临床医学、中西医结合、生物学4个博士后科研流动',2918629);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北北方学院',' ','河北',1,0,0,0,0,' ','暂空',' ','http://www.hebeinu.edu.cn','河北北方学院坐落在素有北京“北大门”和“后花园”之称的塞外名城张家口市。集长城草原之灵蕴，得张垣文化之精髓，办学历史悠久，文化积淀深厚，是河北省西北部唯一具有硕士学位授予权的综合性本科院校。学校是经国家教育部批准，于2003年9月由张家口医学院、张家口师范专科学校和张家口农业高等专科学校三所省属高校合并组建而成。张家口医学院的前身是1945年建立的晋察冀白求恩卫生学校，1982年12月经教育部批准升格为本科院校，定名为张家口医学院;张家口师范专科学校始建于1956年;张家口农业高等专科学校始建于',1483362);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('承德医学院',' ','河北',1,3,0,0,0,' ','暂空',' ','http://www.cdmc.edu.cn','承德医学院(ChengdeMedicalUniversity)是一所隶属于河北省政府的普通高等医学本科学校，坐落在我国著名旅游胜地——承德市。学校创建于1945年，前身是冀东军区卫生学校。后为中国医科大学第四分校、热河医学院、承德医学专科学校等。1982年经教育部批准升格为本科院校，定名为承德医学院。1996年学校首批通过教育部组织的本科院校合格评估;2003年被国务院学位委员会批准为硕士学位授权单位;2007年在教育部本科教学水平评估中获得优秀。校园占地面积74.9万平方米，建筑面积16.68',1166149);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北师范大学',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.hebtu.edu.cn','河北师范大学是河北省人民政府与教育部共建的省属重点大学，具有一百余年的发展历史和光荣的办学传统。学校起源于1902年创建于北京的顺天府学堂和1906年创建于天津的北洋女师范学堂。1996年6月，原河北师范大学、河北师范学院与创建于1952年的河北教育学院、创建于1984年的河北职业技术师范学院合并，组建成新的河北师范大学。校友中有老一代革命家邓颖超、刘清扬、郭隆真、杨秀峰、康世恩、荣高棠等，有学界名人梁漱溟、张申府、汤用彤等，有中科院院士严陆光、郝柏林、李树深等，也有许绍发、蔡振华等一批体育界精',2762178);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('保定学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.bdu.edu.cn        ','保定学院是一所具有百余年办学历史的学校，创建于1904年，始称“保定初级师范学堂”，创始人是近代著名教育家严修。1909年改称“直隶第二初级师范学堂”,1928年随省易名为“河北省立第二师范学校”。新中国成立后定名“保定师范学校”。1978年经国务院批准改建保定师范专科学校。2007年3月16日，教育部正式下发文件，同意保定师范专科学校升格为本科层次的普通高校，并更名为保定学院。学校规划用地1194亩，现已征用874亩，另预留320亩。新校区由清华大学建筑学院设计，规划建筑面积31.45万平方米',1105147);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北民族师范学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.hbun.net    ','学校概况：河北民族师范学院坐落在闻名中外的世界历史文化名城--承德市，是一所具有百余年办学历史的学校，是河北省唯一一所民族本科高校，是河北省人民政府与国家民委共建地方高校，作为委省共建的重点项目，学校与中央民族大学签署协议，成为中央民族大学对口支援院校。是河北省首批转型试点高校之一，是教育部学校规划建设发展中心首批全国五所“产教融合创新实验项目”基地学校;是国家发改委、教育部“产教融合工程建设”项目基地校;2016年入选数据中国“百校工程”产教融合创新项目和中美应用技术教育“双百计划”首批试点院',344398);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('唐山师范学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.tstc.edu.cn/','唐山师范学院是一所省属全日制普通本科师范院校。学校创办于1956年，2000年经教育部批准升格为本科院校，2007年顺利通过教育部本科教学工作水平评估，2014年入选全国毕业生就业典型经验高校。学校位于河北省唐山市高新技术开发区。唐山北靠燕山，南临渤海，西接京津，是河北省经济首强城市，也是中国未来发展前景最好的城市之一，区位优势突出，环境优美，底蕴深厚。学校办学环境优越。学校占地1037亩，建筑面积32万多平方米;教学科研仪器设备总值近1亿多元;图书馆藏书230余万册，数据库20个;多媒体教室、',640756);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('廊坊师范学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.lfsfxy.edu.cn','廊坊师范学院地处被誉为“京津走廊上一颗璀璨明珠”的河北省廊坊市。1946年建校，2000年经教育部批准，升格为省属普通本科院校。升本以来，学校为社会输送了4万余名各级各类专业人才。学校分两个校区办学，占地2000亩，教育教学设施与设备条件优越，馆藏文献140万册，电子图书107万册。设有14个二级学院和继续教育学院、图书馆、社科部、期刊部等教辅单位，14个党政部门;含有普通本、专科教育、高职教育、成人教育等多种教育模式;现有全日制在校生近20000名，教职员工1394名，其中专任教师902人，具',1564701);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('衡水学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.hsnc.edu.cn/','衡水学院是经国家教育部批准建立的一所省属多科性全日制普通本科高等学校。学院座落在京九沿线第一个省级“综合开发实验市”—河北省衡水市。京九与石德铁路，京开与太青高速公路，京九与济石太银通讯光缆在这里聚汇相交，衡水成为北起北京、南达九龙，东接京沪，西连京广的重要交通通讯枢纽和新兴的人流、物流、信息流的大型集散地。距市区十公里处的衡水湖是华北地区第一个国家级湿地自然保护区。学院前身为始建于1923年的直隶第六师范。1978年经国务院批准建立衡水师范专科学校，2004年经教育部批准升格为衡水学院。在建校',1144400);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄学院',' ','河北',1,0,0,0,0,' ','暂空',' ','http://www.sjzc.edu.cn','石家庄学院是经教育部批准建立的国有全日制普通本科院校。学校地处河北省石家庄高新技术产业开发区，由南北两个校区组成，交通便捷，环境优美，具有良好的地理区位优势和经济文化条件。学校始建于1958年的石家庄专区师范学院，1959年更名为石家庄师范专科学校。1996年3月经河北省人民政府批准，石家庄师范专科学校、石家庄地区教育学院与石家庄市教育学院合并，校名定为“石家庄师范专科学校”。2004年5月经教育部批准，石家庄师范专科学校升格为石家庄学院。学校占地1221亩，建筑面积35.4万平方米。建有13个',1346941);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('邯郸学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.hdc.edu.cn','邯郸学院位于太行山下、滏漳河畔的太极圣地、成语之乡、文化历史名城——河北省邯郸市，是教育部批准的全日制公立普通本科院校。办学历史可以追溯到1905年成立的邯郸县初级师范学堂，其后几经分合变迁，薪火相传，1982年升格为邯郸师范专科学校，2000年与邯郸市第一教育学院、第二教育学院、幼儿师范学校合并，2004年升格为本科院校。学校现有教职工940人，专任教师606人，其中教授、副教授占38.1%，博士硕士72.9%。全日制本专科在校生13643人，成人学历教育在籍学生4280人。建有14个二级学院',1175471);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('邢台学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.xttc.edu.cn/','邢台学院前身直隶第四初级师范学堂，始建于1910年(宣统二年)，是当时直隶省最早成立的四所师范之一。1912年民国建立后，改称直隶第四初级师范学校，曾用名河北省第四师范学校、邢台师范学校等。1984年升格为邢台师范专科学校;1996年3月，与原邢台教育学院、邢台经济管理干部学校三校合并，成立邢台师范高等专科学校;2002年，经教育部批准升格为本科院校。目前是河北省唯一一所一脉单传、在原校址上不断建设延续传承发展而来的高校。学校在辛亥革命、国民革命、土地革命、抗日战争、解放战争和新中国建设等不同时',1301823);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沧州师范学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.caztc.edu.cn','沧州师范学院始建于1958年。2010年经教育部批准升格为全日制普通本科院校。校园一期占地600亩，建筑面积19.69万平方米；拥有各类实验室142个，教学设备仪器总值3300多万元；现代化图书馆1.83万平方米，藏书61万册，电子图书10万册。专任教师411名，教授、副教授211名，占专任教师的51.3%，具有博士、硕士学位的教师138名，占专任教师的34%。在“质量立校，特色兴校，人才强校，学科领校，开放活校”办学理念的指引下，学校紧紧围绕“一切为了学生，一切为了提高教育教学质量，一切为了学',568838);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄铁道大学',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.stdu.edu.cn/','石家庄铁道大学前身是中国人民解放军铁道兵工程学院，创建于1950年，系当时全军重点院校;1979年被列为全国重点高等院校;1984年转属铁道部，更名为石家庄铁道学院;2000年划转河北省，实行中央与地方共建，为河北省重点骨干大学;2010年3月更名为石家庄铁道大学;2013年12月成为国家国防科工局、河北省人民政府共建高校;2015年7月被教育部、国家铁路局、河北省政府批准为共建高校。学科建设学校设有19个学院(系、部)，25个研究所。现有49个本科专业，2个一级博士学位授权学科，2个博士后流动',3243623);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西大学',' ','山西',1,0,0,0,0,' ','暂空',' ','http://bkzs.sxu.edu.cn/','山西大学坐落在具有2500年历史的文化古都——山西省省会太原市，学校前身为创建于1902年的山西大学堂，其悠远的文脉可以上溯至明代三立书院及清代晋阳书院和令德书院。早期的山西大学堂中西合璧、文理并重，办学思路开阔，育人理念先进，是我国近代高等教育重要的发祥地之一，也是三晋大地百年文化科教的重镇。历经112年办学历程，山西大学始终根植于华夏文明的沃土，以“教授高深学术，养成硕学闳材，应国家之需要”为己任，秉承“中西会通、求真至善、登崇俊良、自强报国”的光荣传统，弘扬“勤奋、严谨、信实、创新”的优良',3012913);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原科技大学',' ','山西',1,1,0,0,0,' ','暂空',' ','http://www.tyust.edu.cn/','学校简介太原科技大学是新中国第一所重型机械本科院校，是一所具有鲜明办学特色和光荣办学传统的多科性大学，是我国重大技术装备领域重要的人才培养和科学研究基地。1952年，太原科技大学的前身山西省机械制造工业学校正式创建，近代中国杰出的机械工程专家，时任太原重型机器厂总工程师的支秉渊先生担任首任校长。1953年，学校划归中央第一机械工业部。1955年，长春汽校、汉口技术锻冲专业师生整建制并入。1960年，学校升格并更名为太原重型机械学院，成为新中国设立的第一所重型机械本科院校。1965年，大连工学院、',2973904);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中北大学',' ','山西',1,1,0,0,0,' ','暂空',' ','http://zsjy.nuc.edu.cn','中北大学是一所由山西省人民政府与国家国防科技工业局共建、山西省人民政府管理的多科性教学研究型大学。学校的前身是1941年八路军总司令部在太行抗日根据地创办的我党我军第一所兵工学校——太行工业学校，历经太原机械学院、华北工学院，2004年6月经教育部批准更名为中北大学。中北大学位于中国历史文化名城——山西省太原市，并在朔州市设有校区。学校总占地2501亩，建筑面积近101万平方米，固定资产总值19.3亿元，教学科研仪器设备总值6.1亿元，可利用中外文图书资源291万册，拥有完善的教学和公共服务设施',4434233);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原理工大学',' ','山西',1,1,0,1,0,' ','暂空',' ','http://www2015.tyut.edu.cn/cn/index.html.cn/cn/index.html','太原理工大学的前身是创立于1902年的国立山西大学堂西学专斋，是我国最早成立的三所国立大学之一；历经山西大学校工科、山西大学工学院；1953年独立建校，定名太原工学院，直属高教部；1962年归属山西省；1984年更名为太原工业大学。1997年太原工业大学与始建于1958年直属煤炭部的山西矿业学院合并，组建为太原理工大学。同年，学校被确定为国家“211工程”重点建设大学。学校现有迎西、虎峪、柏林、明向等四个校区，占地面积3201亩，公共校舍建筑面积121万平方米；现面向全国31个省、市、自治区招生',5489359);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西农业大学',' ','山西',1,2,0,0,0,' ','暂空',' ','http://www.sxau.edu.cn','山西农业大学是我国著名的高等农业学府，是山西省唯一的农业院校，建国前山西省两所本科高校之一，首批获得博士、硕士学位授权的高校之一，教育部本科教学评估优秀高校，省政府与农业部共建高校，山西省综合改革试点高校。学校有优良的办学传统。学校始建于1907年，初名私立铭贤学堂，创始人为孔祥熙，后发展为私立铭贤农工专科学校、私立铭贤学院；1951年改私立为公办，成立山西农学院；1979年更名为山西农业大学，成为改革开放初99所全国重点大学之一。在109年的办学实践中，学校立足山西，面向全国，服务“三农”，不',1947909);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西医科大学',' ','山西',1,3,0,0,0,' ','暂空',' ','http://www.sxmu.edu.cn','山西医科大学的前身是山西医学传习所，创建于1919年。学校多次易名，数次迁址。1932年1月，改为私立山西川至医学专科学校。1940年3月，更名为山西大学医学专修科。1946年8月，升格为国立山西大学医学院。新中国成立后，学校步入了一个崭新的发展时期。1953年9月，独立建校，更名为山西医学院。1955年全国高校院系调整时，山西、江苏、山东、浙江四所医学院的卫生系合并组建了山西医学院卫生系，成为全国保留有卫生系的六所高等医学院校之一，并由全国著名卫生学专家、国家一级教授、原江苏医学院院长邵象伊担',3179116);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西医科大学汾阳学院',' ','山西',1,3,0,0,0,' ','暂空',' ','http://www.sxmufyc.edu.cn:8080/','山西医科大学汾阳学院，是山西医科大学二级学院，前身系山西省汾阳高级护理学校，由美国基督教华北公理会于1923年创办，1985年恢复重建，1996年改为现名，学校坐落于素有“秦晋旱码头”之称的千年古城汾阳市，青银高速公路、307国道、太中银铁路倚门而过，交通便利。学校占地面积500余亩，建筑面积13.5万平方米。校园花木扶疏，绿树成荫，环境优雅，风景如画。我校是一所多专业、多学制、多层次的高等医学院校。现有临床医学、护理学、口腔医学、医学检验技术、医学影像学、眼视光学、康复治疗学、医学实验技术8个',992167);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长治医学院',' ','山西',1,3,0,0,0,' ','暂空',' ','http://www.czmc.com/','长治医学院位于上党古城长治市内，隶属山西省教育厅主管，是一所全日制普通高等学校。学院创建于1946年，是在刘伯承、邓小平、薄一波等老一辈革命家的亲切关怀下建立发展起来的。60年来，学院为社会培养输送了大批高质量的医卫人才，为我国的解放事业和社会主义现代化建设事业做出了积极的贡献。2004年学院顺利通过了国家教育部对我院进行的本科教学工作水平评估。学院现有医学、工学、管理学等学科门类，初步形成了多学科、多专业协调发展的办学格局。并设有临床医学、妇产科学（方向）、护理学、医学影像学、生物医学工程、医',1179171);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西师范大学',' ','山西',1,4,0,0,0,' ','暂空',' ','http://www.sxnu.edu.cn/','山西师范大学创建于1958年，坐落于华夏文明的重要发祥地古尧都临汾市，是培养山西省基础教育师资的重要基地。建校近60年来，学校不断上层次、上水平，现已发展成为一所培养体系完备、办学特色鲜明的省属重点师范大学。学校现有全日制本科生15100余人，研究生3200余人;设有22个学院，60个本科专业，17个一级硕士学位授权学科，2个一级博士学位授权学科，2个博士后流动站，1个教育部重点实验室，1个教育部创新团队，1个山西省服务产业创新学科群，9个省级重点学科，2个省级协同创新中心，4个山西省高校人文社',1909198);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原师范学院',' ','山西',1,4,0,0,0,' ','暂空',' ','http://www.tynu.edu.cn/','太原师范学院是一所以本科师范教育为主、非师范教育和硕士研究生教育协调发展的全日制高等师范院校，1999年3月经国家教育部批准，由山西大学师范学院、太原师范专科学校和山西省教育学院合并组建而成。山西大学师范学院创立于引领时代风气的改革开放年代，1988年开办本科高等师范教育;太原师范专科学校成立于新中国建立初期，1958年在山西大学堂原址办学;山西省教育学院诞生于烽火硝烟之中，1929年在国民师范旧址创建。在各自长期的办学历程中，它们弦歌不辍、薪火传承，誉满三晋、声名远播，为新中国的建设、为山西基',1315623);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西大同大学',' ','山西',1,0,0,0,0,' ','暂空',' ','http://zsjyc.sxdtdx.edu.cn','山西大同大学是2006年经中华人民共和国教育部批准，由原雁北师范学院、大同医学专科学校、大同职业技术学院、山西工业职业技术学院四校合并成立的多科性大学。新成立的山西大同大学坐落于历史文化名城、煤海之乡大同，占地面积2400亩，建筑面积近70万平方米，新校区四大主体建筑：新教学主楼、科技实验楼、新图书馆、体育馆已基本竣工。学校教学仪器设备总值9636万元，图书馆藏书180余万册。下设政法、文史、外国语、艺术、工学、医学、农学等16个学院，开设本科专业35个，全日制本专科在校生2.2万人。学校非常重',2194971);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('晋中学院',' ','山西',1,0,0,0,0,' ','暂空',' ','http://www.sxjztc.edu.cn','晋中学院的前身为始建于1958年的晋中师范专科学校，2004年5月经国家教育部批准，在原晋中师范高等专科学校、晋中教育学院、晋中职工大学三校合并的基础上建立山西省省属全日制多科性本科院校。学校坐落于三晋腹地、晋商故里—山西省晋中市，是入驻山西省高校新校园区的九所院校之一。晋中学院占地面积82.47万平方米，建筑面积为51.66万平方米，教学仪器设备总价值近亿元，图书馆藏书97.5万册，纸质期刊1322种，电子图书16.5万种。学院校园网覆盖全校所有教学、科研、教辅、办公、生活场所，联网计算机20',962578);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长治学院',' ','山西',1,0,0,0,0,' ','暂空',' ','http://www.czc.edu.cn','长治学院(ChangZhiUniversity)创办于1958年，是一所综合性全日制普通高等院校，位于中国重要能源、原材料工业基地、国家优秀旅游城市、国家园林城市、国家卫生城市、国家文明城市和全国十大魅力城市上党古城─山西省长治市，占地面积400余亩，分南北两校区，校舍建筑面积30.7万平方米。校园建筑布局有序，绿树成荫，呈现出清新、恬静、文明、和谐的园林化环境。学校设有中文、法律与经济学、历史文化与旅游管理、外语、数学、生物科学与技术、化学、计算机、电子信息与物理、教育、音乐舞蹈、美术、体育、',748618);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('运城学院',' ','山西',1,0,0,0,0,' ','暂空',' ','http://www.ycu.edu.cn/','运城学院是国家教育部批准在原运城高等专科学校基础上独立设置的一所地方性综合普通本科院校，隶属山西省人民政府领导，山西省教育厅管理。学校位于山西南部运城市，这里地处我国中西结合部，素有晋秦豫\"黄河金三角\"之美誉，是中华文明的发祥地之一。学院文理渗透，理工互补，本科技术应用教育与本科师范教育兼容，普通高等教育与成人教育并举，有着鲜明的技术应用教育办学特色和区域文化特征。学院坚持\"以人为本，注重教学，突出应用，面向地方，服务社会\"的办学指导思想，坚持立足山西，服务晋秦豫\"黄河金三角\"中部结合区域，面向',997530);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('忻州师范学院',' ','山西',1,4,0,0,0,' ','暂空',' ','http://www.xztc.edu.cn','忻州师范学院成立于1958年,是经国家教育部批准组建的全日制本科普通高等学校，学士学位授予单位，座落在佛教圣地五台山脚下的温泉之乡??山西省忻州市，有北同蒲铁路、京原铁路、大运高速公路经过，交通十分便利，经高速公路35分钟即可抵达省会太原。近年来，在上级和地方的大力支持下，在全院师生的共同努力下，在政府应投资一直没有到位的情况下，近几年积极自筹了2亿多元投入基本建设，逐步改善了办学条件，各项事业健康快速地跨越式发展，学生规模从合并前原忻州师专等四校的普通专科学生和成人教育学生总共不到2500人，',603074);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西财经大学',' ','山西',1,6,0,0,0,' ','暂空',' ','http://www.sxufe.edu.cn','山西财经大学是一所以经济学、管理学为特色和优势学科，经济学、管理学、法学为主干学科，经、管、法、文、理、工、教多学科相互支撑的多科性财经大学。学校始建于1951年，是新中国成立后建校最早的高等财经类院校之一。现面向全国30个省、市、自治区招生。现有各类在校学生20000余人，其中普通全日制本科生11000余人，研究生1400余人。学校地处具有2500多年历史的文化名城—山西省省会太原市，环境优美，交通便利。分坞城、迎泽和平阳三个校区办学，校园占地面积近1100亩，建筑面积近60万平方米。学校拥有',3963677);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古大学',' ','内蒙古',1,0,0,1,0,' ','暂空',' ','http://www.imu.edu.cn/','【历史沿革】内蒙古大学位于内蒙古自治区首府、历史文化名城呼和浩特市，距北京400余公里，是新中国成立后党和国家在少数民族地区创办的第一所综合大学。学校于1957年高起点创办，时任国务院副总理、自治区主席乌兰夫任首任校长(1956—1968)，原高教部综合大学司副司长于北辰任副校长，中科院学部委员、著名生物学家、北京大学一级教授、耶鲁大学博士李继侗先生来校执教并任学术副校长。现任校长为博士生导师陈国庆教授。党和政府从北京大学等国内十几所著名高校，选派一批知名学科带头人和学术骨干组成了建校初期高起点',2827744);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古科技大学',' ','内蒙古',1,0,0,0,0,' ','暂空',' ','http://www.imust.cn','历史沿革建国初期为了发展钢铁工业，1954年国家在包头兴建钢铁基地—包头钢铁公司。这颗“草原明珠”的诞生，急需大批工程技术人才。为适应形势的发展，1956年2月，重工业部决定在包头成立“重工业部包头钢铁工业学校”和“重工业部包头建筑工程学校”。1957年4月，冶金工业部决定包头钢铁工业学校与包头建筑工程学校合并，成立“冶金工业部包头工业学校”。1958年9月，冶金工业部决定，包头工业学校与包头钢铁公司大学筹备组合并成立了“包头工学院”。1959年6月，包头工学院更名为“包头钢铁工业学院”。196',2901520);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古工业大学',' ','内蒙古',1,1,0,0,0,' ','暂空',' ','http://www.imut.edu.cn/','内蒙古工业大学坐落在内蒙古自治区呼和浩特市，前身是始建于1951年的绥远省高级工业学校。1958年在清华大学支援下成立内蒙古工学院，曾隶属机械工业部、农业机械部，1983年划归内蒙古自治区，1993年更名为内蒙古工业大学。学校目前在新城校区、金川校区、准格尔校区办学，占地面积3178亩。经过60余年的发展，学校已建设成为一所以工为主，工、理、文、经、管、法、教育、艺术相结合，具有博士、硕士、本科完整人才培养体系的特色鲜明的多科性大学。现设有24个院(部、中心)，拥有54个研究院(所);有博士后流',2188235);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古农业大学',' ','内蒙古',1,2,0,0,0,' ','暂空',' ','http://www.imau.edu.cn','内蒙古农业大学是自治区重点大学，是国家首批32个大学生文化素质教育基地之一。2001年学校成为国家西部大开发“一省一校”重点支持建设院校，2012年成为自治区人民政府和国家林业局合作共建高校，2013年进入国家“中西部高等教育振兴计划”支持院校行列。学校位于中国历史文化名城——呼和浩特市，是“全国绿化模范单位”，被誉为塞外“花园式高等学府”。内蒙古农业大学是内蒙古自治区成立最早的本科高等院校，由原内蒙古农牧学院和内蒙古林学院于1999年合并组建而成。追根溯源，1952年，原河北农学院、平原农学院',1518067);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古师范大学',' ','内蒙古',1,4,0,0,0,' ','暂空',' ','http://www.imnu.edu.cn','内蒙古师范大学创建于1952年，是新中国成立后党和国家在边疆少数民族地区最早建立的高等学校，自治区重点大学，是具有鲜明教师教育和民族教育特色的综合性师范大学。经过几代人的艰苦创业、实践探索，现已成为自治区培养基础教育、民族教育师资和蒙汉兼通少数民族复合型人才的重要基地，是自治区中学教师培养中心、中小学教师继续教育中心、基础教育与民族教育改革发展研究中心,被社会誉为“民族教育的摇篮”。学校拥有盛乐校区和赛罕校区两个校区，占地总面积3800余亩（约253公顷），校舍建筑面积80余万平方米，固定资产总',2034062);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古民族大学',' ','内蒙古',1,0,0,0,0,' ','暂空',' ','http://www.imun.edu.cn','内蒙古民族大学始建于1958年，2000年由原内蒙古民族师范学院、内蒙古蒙医学院、哲里木畜牧学院合并组建而成，是内蒙古自治区重点建设高校，也是内蒙古自治区人民政府和国家民委共建高校。学校位于内蒙古自治区东部，坐落在被誉为“科尔沁草原明珠”的通辽市，拥有霍林河、西拉木伦和滨河园区3个校区，占地面积236.6万平方米，校舍面积41.8万平方米，固定资产总值8.8亿元，教学科研仪器设备总值2亿元，馆藏纸质图书158.4万册。学校设有27个教学单位、7个教辅单位、1个附属单位，开设本科专业72个，涵盖了',1288719);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('赤峰学院',' ','内蒙古',1,0,0,0,0,' ','暂空',' ','http://www.cfxy.cn/','赤峰学院是2003年经国家教育部批准，由具有40多年办学历史、在社会上享有良好声誉的赤峰民族师范高等专科学校与赤峰教育学院、内蒙古广播电视大学赤峰分校、赤峰卫生学校、内蒙古幼儿师范学校合并组建的一所多科性本科普通高等学校。2008年赤峰艺术旅游学校并入赤峰学院。学校位于闻名遐迩的“红山文化”发祥地——内蒙古赤峰市。学校占地面积780亩，规划占地面积1800亩，建筑面积23.38万平方米，固定资产总值5.3亿元。拥有各类专业实验室、专业实训室、计算机中心、数字化校园网、体育馆、多功能厅、博物馆、大',718762);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁大学',' ','辽宁',1,0,0,1,0,' ','暂空',' ','http://www.lnu.edu.cn/','经过半个多世纪的不懈努力，辽宁大学已经成为一所具备文、史、哲、经、法、理、工、管、艺等多学科的辽宁省惟一的综合性大学。学校现有三个校区，即沈阳崇山校区、沈阳蒲河校区和辽阳武圣校区，教学区占地面积2016亩，建筑面积61.8万平方米，是国家“211工程”重点建设院校之一。辽宁大学初创于1948年11月东北人民政府在沈阳建立的商业专门学校，建校之初即招收四年制本科生。1958年9月15日，东北财经学院、沈阳师范学院和沈阳俄文专科学校合并，组建成辽宁大学，朱德同志亲笔题写了校名。学校设有25个学院，即',4901257);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连理工大学',' ','辽宁',1,1,1,1,1,' ','暂空',' ','http://www.dlut.edu.cn/','大连理工大学1949年4月建校，时为大连大学工学院；1950年7月大连大学建制撤销，大连大学工学院独立为大连工学院；1960年被确定为教育部直属全国重点大学；1986年设研究生院；1988年更名为大连理工大学；1996年~2000年实施\"九五\"\"211工程\"建设，教育部、辽宁省、大连市共建大连理工大学；2001年~2003年，教育部、辽宁省、大连市重点共建大连理工大学，实施\"985工程\"一期建设；2003年~2005年实施\"十五\"\"211工程\"建设；2003年12月被中央确定为中管干部学校；20',8820473);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连理工大学盘锦校区',' ','辽宁',0,12,1,0,0,' ','暂空',' ','oldname": "大连理工大学盘锦校区','基于服务国家发展战略、满足人民群众分享优质高等教育资源新期待、实现大连理工大学发展战略目标的需要，2012年，大连理工大学抓住国家实施科教兴国和人才强国战略、建设创新型国家、建设高水平研究型大学以及环渤海经济区开发开放等一系列重大发展机遇，与辽宁省盘锦市人民政府签订战略合作协议，并经教育部批准，建立大连理工大学盘锦校区。大连理工大学盘锦校区位于盘锦市辽河入海口西侧、辽东湾新区金帛湾水城中心地带，这一区域集中了行政管理、文化教育、商贸金融、休闲娱乐、生态住区、港口服务等功能。校区依城面海，舒心怡悦',101121);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳工业大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://zsnet.sut.edu.cn/','沈阳工业大学坐落在中国东北地区现代化的中心城市沈阳市，是一所以工为主，涵盖工、理、经、管、文、法、哲、艺术等八大学科门类的多科性教学研究型大学。学校始建于1949年，1985年由沈阳机电学院更名为沈阳工业大学，原为国家机械工业部所属院校，1998年起由中央和地方共建，以辽宁省管理为主。学校由位于辽宁省沈阳市的中央校区、兴顺校区、国家大学科技园和位于辽宁省辽阳市的辽阳校区等四个校区组成。中央校区（主校区）位于沈阳市装备制造业聚集的国家级经济技术开发区。校园内碧波荡漾，草木葱茏，精致典雅，如诗如画，',3592269);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳航空航天大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.sau.edu.cn/','沈阳航空航天大学是一所以航空宇航为特色，以工为主，工、理、文、经、管等学科协调发展的多科性高等院校，是教育部、中航工业集团公司与辽宁省三方共建高校，是国防科工局与辽宁省共建高校，是空军依托培养后备军官的全国18所地方院校之一，是辽宁省装备制造业紧缺人才(航空航天)培养基地，已经基本建设成为“国防科技人才培养基地”、“辽宁老工业振兴人才培养基地”和“空军后备军官培养基地”。学校始建于1952年，是原航空工业部所属的6所本科航空院校之一，前身为“沈阳航空工业学校”，后历经“东北第一工业学校”、“沈阳',3733066);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳理工大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.sylu.edu.cn/sylusite/','沈阳理工大学始建于1948年，前身是东北军工专门学校。1960年组建成立了沈阳工业学院，隶属国家兵器工业部，时任国家副主席的董必武同志为学校题写了校名。1999年学校划归辽宁省管理，实行省部共建。2004年经国家教育部批准学校更名为沈阳理工大学。沈阳理工大学由浑南主校区、抚顺校区、科技园、武器装备实习实验基地等部分组成，占地面积1897亩（不含抚顺校区），校舍建筑面积42.7万平方米（不含抚顺校区），其中主校区位于辽宁省沈阳市浑南高新区风景秀丽的浑河南岸，校园内建筑气势恢宏，庄重典雅，环境清新宜',3872367);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北大学',' ','辽宁',1,1,1,1,1,' ','暂空',' ','http://www.neu.edu.cn','东北大学始建于1923年。东北大学是教育部直属的国家重点大学，坐落在东北最大的中心城市沈阳。学校占地总面积203万平方米,建筑面积100万平方米。东北大学始建于1923年4月，是一所具有爱国主义光荣传统的大学。1928年8月至1937年1月，著名爱国将领张学良将军兼任校长。“九?一八”事变后，东北大学被迫先后迁徙北平、开封、西安、四川三台等地。在此期间，广大师生积极参加爱国抗日运动，是“一二?九”运动的先锋队和主力军。1949年3月，在东北大学工学院、理学院(部分)的基础上成立了沈阳工学院。19',7866100);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁科技大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://zsjy.ustl.edu.cn/index.htm','辽宁科技大学是一所面向全国招生，以工学为主，涵盖工学、理学、经济学、管理学、文学、法学、艺术学等七大学科门类的多科性大学。地处祖国钢都、国家卫生城、中国优秀旅游城市—辽宁省鞍山市。校园东临国家森林公园千山，西倚国家4A级旅游胜地玉佛苑，环境优美，风格现代，底蕴深厚，交通便利，是潜心读书和研究的一片沃土。学校始建于1948年，1958年成立本科学院—鞍山钢铁学院，隶属于原冶金工业部，是我国较早组建的冶金高校之一。1998年随着高等教育体制改革，学校转为辽宁省与教育部共建共管，以省管为主。2006年',2639439);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁工程技术大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.lntu.edu.cn/','辽宁工程技术大学是一所具有近60年悠久历史的国家重点大学。早在建国前夕，为恢复生产、发展经济、支援解放全中国，经东北人民政府批准，分别成立了鹤岗工科高级职业学校、鸡西煤矿工业学校、抚顺工科高级职业学校、阜新工科高级职业学校以及东北煤矿工人速成学校。1958年分别成立了阜新煤矿学院、抚顺煤矿学院、辽宁煤矿师范学院和鸡西矿业学院。六十年代初期院校调整，四校合并，校址选于辽宁省阜新市，定名为阜新煤矿学院，成为东北地区唯一一所煤炭高等院校。1978年学校更名为阜新矿业学院。1996年4月，经国家教委批准',2265955);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁石油化工大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.lnpu.edu.cn','辽宁石油化工大学1950年始建于大连，是新中国第一所石油工业学校，1953年迁至抚顺办学，1958年升格为抚顺石油学院，2000年2月由中央部委划转为辽宁省人民政府领导，实行“中央与地方共建，以地方为主”的管理体制，2002年2月经教育部批准更名为辽宁石油化工大学。建校55年来已培养5万多名毕业生，其中绝大部分已经成为石油石化等各行业的各级领导干部和技术骨干。学校坐落在辽宁省抚顺市风景秀丽的浑河岸畔，依山傍水，环境优美。校园占地2013亩，建筑面积56.4万平方米，藏书153万册（含电子图书65',2621849);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳化工大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.syuct.edu.cn/','沈阳化工学院位于历史文化名城沈阳市的西南，地处国家级沈阳经济技术开发区的腹地，紧邻装备制造业核心区和化学工业区。学校为国家首批学士学位授予权单位，1993年被国务院学位委员会批准为硕士学位授予权单位。学校是辽宁省科技创新的重要基地之一，沈阳市重点项目研发基地，辽宁石油化工技术紧缺人才培养培训基地。学校始建于1952年，之初隶属东北人民政府；1956年，隶属化学工业部；1958年，隶属辽宁省人民政府，原中国科学院院长郭沫若为学校题写了校名；1960年，更名为辽宁科学技术大学，隶属中国科学院辽宁分院',2005963);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连交通大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.djtu.edu.cn/','大连交通大学创建于1956年，时为大连机车车辆制造学校；1958年升格为大连铁道学院，隶属原铁道部管理；2000年2月划转为辽宁省政府管理，实施“中央与地方共建，以地方管理为主”的管理体制；2004年5月经教育部批准更名为大连交通大学。大连交通大学是一所以轨道交通和复合型软件人才培养为特色的高等学校。学校总占地面积85.16万平方米，由沙河口校区和旅顺口校区组成。校舍建筑总面积55.5万平方米，教学仪器设备总值3.1亿元，图书馆建筑总面积4.3万平方米，馆藏文献资源总量153.45万册，中外文电',5643237);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连海事大学',' ','辽宁',1,1,0,1,0,' ','暂空',' ','http://www.dlmu.edu.cn/','大连海事大学(原大连海运学院)是交通运输部所属的全国重点大学，是中国著名的高等航海学府，是被国际海事组织认定的世界上少数几所“享有国际盛誉”的海事院校之一。大连海事大学历史悠久，其前身可追溯到1909年晚清邮传部上海高等实业学堂(南洋公学)船政科。1911年，以船政科为基础创办邮传部上海高等商船学堂。1912年，改名为吴淞商船学校。1929年，经停办后正式复校，定名为交通部吴淞商船专科学校。1950年，中央人民政府交通部决定，交通部吴淞商船专科学校与交通大学航业管理系正式合并，成立上海航务学院。',6449976);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连工业大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.dlpu.edu.cn/','大连工业大学创建于1958年，前身是沈阳轻工业学院，1970年迁至大连，改称为大连轻工业学院。学校原隶属国家轻工业部，1998年转制为中央与地方共建、辽宁省为主的管理体制。2007年经教育部批准更名为大连工业大学。学校是我国最早建立的4所轻工业学院之一，也是国家在东北地区唯一布点，以轻纺为特色的多科性大学。经过50年的建设，学校办学规模逐渐扩大，办学实力逐步增强，实现了规模、结构、质量、效益的协调发展，已经成为我国培养现代消费产业高级工程技术人才、艺术设计人才及经营管理人才的重要基地。学校共有',3024753);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳建筑大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://zsjy.sjzu.edu.cn/','沈阳建筑大学是以建筑、土木、机械等学科为特色，以工为主，工、管、理、文、农、法、艺术等学科门类协调发展的省部共建高等学校，原隶属于国家建设部，2000年在国家办学管理体制调整中划归辽宁省管理。2010年，辽宁省政府与国家住房和城乡建设部在北京签署了共建沈阳建筑大学协议，为学校创建有特色、高水平建筑大学奠定了坚实的基础，为进一步密切学校与行业及行业主管部门的联系搭建了新的平台。学校位于沈阳浑南新区的主校区占地面积1500亩，建筑面积48万平方米。主校区规划设计体现了以人为本、与自然和谐共生的理念，',2941580);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁工业大学',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.lnut.edu.cn/','辽宁工业大学始建于1951年，坐落于依山傍海、交通发达的辽宁西部中心城市---锦州。校园占地面积1000余亩，建筑面积36万余平方米，各类在校学生18000余人，是一所以工为主，理、工、经、管、文协调发展的省属全日制多科性大学。2013年学校入选国家“中西部高校基础能力建设工程”重点建设高校。学校现有21个教学院、部、中心，7个一级学科硕士学位授权点，35个二级学科硕士学位授权点，9个工程硕士授权领域，52个本科专业。形成了以本科教育为主，又有研究生教育、留学生教育、继续教育等多层次的办学格局。',4015428);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳农业大学',' ','辽宁',1,2,0,0,0,' ','暂空',' ','http://zs.syau.edu.cn/','沈阳农业大学是一所地方与中央共建的全国重点大学。现任党委书记迟维意，校长刘广林。学校坐落在沈阳市东郊的天柱山南麓，东与世界文化遗产清福陵毗邻，西与沈阳城区相连，占地面积一万余亩。校园山水、田地、林园兼备，风景秀丽，环境幽雅，农业教育、科研条件优越。沈阳农业大学的办学历史可追溯到我国农业教育的始兴时期——1906年清政府设立的省立奉天农业学堂，以后经历了奉天农业大学、东北大学农学院和沈阳农学院几个历史时期，1952年全国高等院校调整，与复旦大学农学院合并组建成了新的沈阳农学院。1979年10月经国',1033461);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连海洋大学',' ','辽宁',1,2,0,0,0,' ','暂空',' ','http://www.dlou.edu.cn/','大连海洋大学是我国北方地区唯一的一所以海洋和水产学科为特色，农、工、理、管、文、法、经、艺等学科协调发展的多科性高等院校。学校创建于1952年，前身为东北水产技术学校，1958年升格为大连水产专科学校，1978年升格为大连水产学院。2000年由农业部划转辽宁省管理。2008年以“优秀”成绩通过教育部本科教学工作水平评估。2010年经教育部批准更名为大连海洋大学。学校坐落于美丽的海滨城市大连。有黄海校区、渤海校区和瓦房店校区3个校区，占地面积80万平方米，教学、科研使用海域面积67万平方米，总建筑',3935438);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国医科大学',' ','辽宁',1,3,0,0,0,' ','暂空',' ','http://202.118.40.32/bkzs/','中国医科大学是中国共产党最早创建的院校，是唯一以学校名义走完红军两万五千里长征全程并在长征中继续办学的院校，是我国最早进行西医学学院式教育的医学高校之一。其前身为中国工农红军军医学校和中国工农红军卫生学校，1931年11月创建于江西瑞金。1940年9月在延安，经毛泽东同志提议，中共中央批准，学校更名为中国医科大学。1948年11月，在沈阳合并了原国立沈阳医学院（前身为满州医科大学，1911年由日本国南满铁道株式会社建立）和原私立辽宁医学院（前身为盛京医科大学，1883年由英国苏格兰教会建立）。中',2751672);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('锦州医科大学',' ','辽宁',1,3,0,0,0,' ','暂空',' ','http://www.lnmu.edu.cn/','锦州医科大学诞生于解放战争时期，学校前身为1946年在吉林省洮南市成立的辽吉军区卫生学校。1949年迁址辽宁省锦州市，1958年成立锦州医学院，2006年更名为辽宁医学院。2007年以优秀的成绩通过国家教育部本科教学工作水平评估。2016年3月，经国家教育部批准，正式更名为锦州医科大学。经过70年的发展已成为一所以医学为主，多门类、多层次、多种办学形式的省属普通高等院校。学校占地面积1494亩。有普通高等教育、成人教育、高等职业技术教育和留学生教育4种办学形式;有研究生、本科生、专科生3个办学层',1245603);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连医科大学',' ','辽宁',1,3,0,0,0,' ','暂空',' ','http://www.dmu.edu.cn/','大连医科大学创建于1947年，原校名为关东医学院。1950年更名为大连医学院，1969年举校南迁贵州省遵义市，1978年学校在大连医学院原址复办，1994年更名为大连医科大学。学校现已发展成为以医学为主，文学、理学、管理学、法学等多学科发展的医科大学。大连医科大学位于旅顺南路海滨观光带的中段。学校占地面积150.97万平方米，建筑面积38万平方米，教学科研仪器设备总值1.08亿元，图书82.49万册，电子图书62.44万册。学校现有26个党政管理机构，15个教学教辅机构，6个直属机构，2所直属附',2744910);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁中医药大学',' ','辽宁',1,3,0,0,0,' ','暂空',' ','http://www.lnutcm.edu.cn/','辽宁中医药大学成立于1958年，主校区位于沈阳，分校区位于大连和本溪，是辽宁省唯一一所培养中医、中药、针灸推拿、中西医临床医学、高级护理和医学相关类人才的高等院校。学校先后获得全国创先争优先进基层党组织,全国教育纪检监察先进集体,全国文明单位，辽宁省五一奖状、先进集体等荣誉称号。经过50多年的发展，辽宁中医药大学在国际交流型中医药人才、实验创新型人才、传统中医药人才培养方面形成了自己的办学特色，2013年组建成立辽宁省中医药科学院。学校设置医、理、工、管、文、法等6个学科门类，20个本科专业，现',956167);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳药科大学',' ','辽宁',1,3,0,0,0,' ','暂空',' ','http://zsjyc.syphu.edu.cn/zhaosheng/','沈阳药科大学是一所具有光荣革命传统的学校，1931年诞生于江西瑞金，是我国历史最悠久的综合性药科大学。学校占地面积141.8万平方米，建筑面积57万平方米。学校目前已发展成为多学科、多层次、多形式教育的高等药学学府，现设有药学院、制药工程学院、中药学院、生命科学与生物制药学院、工商管理学院、医疗器械学院、社科与文体学院、继续教育学院和亦弘商学院等9个学院。学校是国家批准有权授予博士学位、硕士学位和招收港、澳、台地区学员及外国留学生、国内高中保送生的院校。有药学、中药学2个博士后流动站，一级学科博',1259879);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳医学院',' ','辽宁',1,3,0,0,0,' ','暂空',' ','http://www.symc.edu.cn','沈阳医学院简介沈阳医学院始建于1949年，是教育部批准的全日制普通高等医学院校。建校65年来，学校办学条件不断改善，办学实力不断增强，办学水平不断提高，现已形成集研究生、本科和高职专科为一体的多层次办学体系，先后培养了4万余名德才兼备的医疗卫生人才，为医疗卫生事业发展和经济建设做出了重要贡献。2007年在教育部本科教学工作水平评估中获得“优秀”；2013年经国务院学位委员会批准，获得硕士学位授予权，顺利通过临床医学专业认证。学校占地面积44.4万平方米，建筑面积23.6万平方米。固定资产13.8',1380543);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁师范大学',' ','辽宁',1,4,0,0,0,' ','暂空',' ','http://www.lnnu.edu.cn/','辽宁师范大学是一所省属重点大学，坐落在中国北方美丽的海滨城市——大连，是辽宁省基础教育师资培养与继续教育基地、教育科学研究咨询基地、高校师资培训基地、适应地区经济建设与社会发展需要的科研基地，现已成为辽宁省最大的、起示范带头作用的教师教育中心。学校始建于1951年8月，初建时为旅大师范专科学校，是新中国诞生后首批建立的高等师范院校之一。1953年，东北地区7所师专调整归并为3所时，我校更名为大连师范专科学校。1958年7月，扩建为大连师范学院。1960年更名为辽宁师范学院。1983年12月，更名',2491872);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳师范大学',' ','辽宁',1,4,0,0,0,' ','暂空',' ','http://zs.synu.edu.cn/Index.aspx','沈阳师范大学隶属于辽宁省人民政府，是一所涵盖哲学、经济学、法学、教育学、文学、理学、工学、管理学、艺术学等九大门类的多科性大学。学校始建于1951年，其前身为东北教育学院。1953年，更名为沈阳师范学院，是当时东北地区创办最早的两所本科师范院校之一。1965年更名为辽宁第一师范学院。1978年恢复沈阳师范学院校名。2002年省政府决定并经教育部批准,沈阳师范学院与辽宁教育学院合并组建沈阳师范大学。学校现占地面积1879亩，建筑面积76.36万平方米，藏书180余万册。学校共有全日制本、专科生25',2315697);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('渤海大学',' ','辽宁',1,0,0,0,0,' ','暂空',' ','http://www.bhu.edu.cn','渤海大学是一所省属综合性大学，坐落于渤海之滨的美丽城市—锦州。学校南临秀美的女儿河，与巍巍南山隔水相望。校园建筑欧式风格浓郁，湖光山色，灵动清雅。学校创建于1950年2月。半个多世纪以来，为国家培养各类人才10万余名，他们在教育、科技、商贸、管理等各个领域为社会主义现代化建设做出了重要贡献，许多毕业生创造了不凡的业绩，成为国内外颇具影响的专家学者、企业家及高层管理人才。学校占地74.8万平方米，建筑面积42万余平方米，固定资产近10亿元，图书馆藏书132万余册。学校设有化学化工、信息工程、新闻、',3239529);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鞍山师范学院',' ','辽宁',1,4,0,0,0,' ','暂空',' ','http://www.asnc.edu.cn/','鞍山师范学院是一所本科层次的全日制普通高等师范学校，位于辽宁省鞍山市，东倚风景秀丽、文化底蕴独特的千山风景区，西北望气势宏伟的四十里钢城。学校始建于1958年，1962年7月院校调整后停办。1978年恢复建立鞍山师范专科学校，1993年12月11日经国家教委批准升格为鞍山师范学院。2004年学校通过了国家教育部本科教学合格评估，至今已走过了半个世纪的风雨历程。建校50余年来，鞍山师范学院秉承“博雅兼上知行合一”的校训，在一代代鞍师人的精心铸造下，逐渐形成了“奉献严谨文明创新”的优良校风，在发挥人',1118602);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('朝阳师范高等专科学校',' ','辽宁',0,4,0,0,0,' ','暂空',' ','http://www.cysz.com.cn/','朝阳师专新校区坐落在风景秀丽的朝阳市龙山，其前身是1960年由辽宁省人民政府批准建立的朝阳市师范专科学校，1962年至1977年调整停办，1977年由辽宁省教育局批准成立辽宁省第一师范学院朝阳师专班，1981年由国务院批准正式建立朝阳师范专科学校，1990年朝阳市教育学院并入，1993年由国家教委更名为朝阳师范高等专科学校，2001年朝阳第一师范学校、朝阳职工大学并入。2008年异地新建于目前校址。自1981年重建以来，朝阳师专一直承担着朝阳市及周边地区的基础教育师资培养任务，共培养全日制大专毕',104204);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北财经大学',' ','辽宁',1,6,1,0,0,' ','暂空',' ','http://www.dufe.edu.cn/','东北财经大学地处国家级风景区——大连市星海公园西侧。整个校园由主、辅两个校区构成，总占地923.05亩。截至2013年9月，学校有全日制在校生19202人，其中全日制本科生12633人，硕士、博士研究生6003人，教职工1930人。早在20世纪90年代中期，东北财经大学就因校园恬静、优美被辽宁省人民政府和大连市人民政府分别命名为“绿化先进单位”、“优秀花园式单位”。进入21世纪，学校又因校园“绿化”被大连市人民政府授予“绿色大学”称号，在大连市获此殊荣的四所高校中排名第一，2012年获得“大连市',5552502);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国刑事警察学院',' ','辽宁',1,11,0,0,0,' ','暂空',' ','http://www.npuc.edu.cn/','中国刑事警察学院座落在历史名城沈阳，占地面积28万平方米，建筑面积19万平方米，是公安部直属的公安高等院校。学院始建于1948年5月，前身是东北公安干部学校，后改为中央人民警察干部学校、公安部第一人民警察干部学校、公安部人民警察干部学校，1981年11月经国务院批准，扩建为中国刑事警察学院，并由时任全国人大常委会委员长的彭真同志题写了校名。半个多世纪以来，在中央和公安部领导的关怀下，学院艰苦创业，与时俱进，逐步创建了符合我国国情，适应公安工作需要、具有鲜明特色的学科专业体系，创建了教学、科研、办',1653603);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳体育学院',' ','辽宁',1,7,0,0,0,' ','暂空',' ','http://zsjyc.syty.edu.cn/','1953年，中央体委与东北人民政府根据中央人民政府在各大区兴建体育学院的发展计划，成立了由东北文委、东北教育局、东北体委联合组成的东北体育学院筹备委员会，共同筹建沈阳体育学院前身东北体育学院。筹备委员会主任和东北体育学院第一任院长由时任东北人民政府教育部部长、东北体委主任、著名教育家车向忱同志兼任。1954年9月15日，学院第一批新生入学，东北体育学院正式成立，隶属中央体委。1956年2月24日，根据高教部的通知，学院改称为现名。2001年6月学院划转地方，由中央和地方共建。建院以来，学院为国家',533672);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳音乐学院',' ','辽宁',1,8,0,0,0,' ','暂空',' ','http://www.sycm.com.cn','沈阳音乐学院是中国东北地区唯一一所历史悠久、具有光荣传统的高等音乐、舞蹈艺术院校。学院缘起于1938年由毛泽东、周恩来、林伯渠、徐特立、成仿吾、艾思奇、周扬等老一辈无产阶级革命家在延安发起、创立的鲁迅艺术学院，1940年更名为鲁迅艺术文学院。抗日战争胜利后，学院由延安迁至东北，1948年底定址沈阳，改称为东北鲁迅文艺学院。1953年在学院音乐部的基础上，成立了东北音乐专科学校，1958年正式更名为沈阳音乐学院，面向全国招生。建校以来，学院不断发展壮大，现已初步形成了以主校区为中心，南校区、桃仙校',325451);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鲁迅美术学院',' ','辽宁',1,8,0,0,0,' ','暂空',' ','http://www.lumei.edu.cn','鲁迅美术学院前身是1938年建于延安的鲁迅艺术学院，由毛泽东、周恩来等老一代领导人亲自倡导创建。毛泽东同志为学院书写校名和“紧张、严肃、刻苦、虚心”的校训。1945年，延安鲁艺迁校至东北。1958年发展为鲁迅美术学院。1998年，江泽民同志为学院题词：“弘扬鲁艺传统，培育艺术人才，繁荣社会主义文化事业”。悠久的办学历史积淀了学院深厚的文化内涵和领先的办学实力。经过70年的发展，鲁迅美术学院现已成长为师资力量雄厚，专业齐全，办学水平和整体实力居全国同类院校前列、有鲜明的办学特色、在国内外有重要影响',278849);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('抚顺师范高等专科学校',' ','辽宁',0,4,0,0,0,' ','暂空',' ','http://www.fstc.cn/','抚顺师范高等专科学校始建于1978年，原名为抚顺师范专科学校，是国家教育部批准的市属地方高等专科学校。1993年更名为抚顺师范高等专科学校。1998年7月，抚顺市师范学校、抚顺市艺术幼儿师范学校并入我校，使学校师资力量、办学规模进一步扩大。学校位于抚顺市顺城区历史悠久的高尔山公园东部，依山傍水，环境幽雅，是一所四季常青的花园式学校。多年来，学校坚持社会主义办学方向，大力推进素质教育，形成了“厚德、树人、务实、创新”的校风和“励志、勤奋、求真、成才”的学风，探索出“基础适度、强化技能、提高素质、一',81037);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('锦州师范高等专科学校',' ','辽宁',0,4,0,0,0,' ','暂空',' ','http://www.jzsz.com.cn/','锦州师范高等专科学校（以下简称锦州师专）位于渤海之滨、交通发达的辽西中心城市——锦州。是经国务院批准、教育部注册备案的一所全日制公办普通高等专科学校。锦州师专前身是成立于1914年的奉天省立第四师范学校，到现在已有95年的办学历史。1978年分为锦州师范专科学校和锦州市第一师范学校，1996年，两校又合并为锦州师范高等专科学校，2001年，辽宁广播电视大学锦州分校、锦州市职工大学等校先后并入，优化和集中了四所院校的人、财、地、物等教育资源。学校占地面积26万平方米，建筑面积14万平方米；固定资产',122232);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('营口职业技术学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://www.ykdx.net/','营口职业技术学院是经辽宁省人民政府批准，隶属于辽宁省教育厅和营口市人民政府共同管理的一所公办全日制普通高校，是高等职业教育、特殊师范教育和普通师范教育协调发展的一所高校，是营口市唯一一所市属普通高校。她的前身是营口师范高等专科学校、营口高等职业专科学校、辽宁广播电视大学营口分校、辽宁工运学院营口分院、辽宁省特殊教育师范学校及营口市幼儿师范学校，于2000年2月合并组建。学院地处中国十大港口城市之一、渤海辽东湾美丽富饶的海滨城市-----营口市市中心，总占地面积54万平方米，总建筑面积15.4万平',81857);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('铁岭师范高等专科学校',' ','辽宁',0,4,0,0,0,' ','暂空',' ','http://www.tlsz.com.cn/','铁岭师范高等专科学校地处素有“小品艺术之乡”、“二人转之乡”、“红楼文化之乡”、“体育冠军之乡”、“煤电能源之城”美誉的辽宁省铁岭市，是经教育部批准成立的一所全日制普通高等专科学校。学校由原铁岭师范、铁岭师专和铁岭职工大学合并而成。从1949年铁岭师范建校至今，已走过60年的光辉发展历程，为社会输送了四万多名合格人才；现有专业37个，在校生5631人；现任党委书记、校长徐兆东教授。校区西倚风景秀丽的龙首山，北临碧波荡漾的柴河水，环境优美，景色宜人。目前，校区总占地面积19.2万平方米，建筑面积7',96129);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林大学',' ','吉林',1,0,1,1,1,' ','暂空',' ','http://www.jlu.edu.cn/newjlu/','吉林大学坐落在吉林省省会长春市，是教育部直属的一所全国重点综合性大学，1995年首批通过国家教委“211工程”审批，2001年被列入“985工程”国家重点建设的大学之一。吉林大学于2000年6月12日由原吉林大学、吉林工业大学、白求恩医科大学、长春科技大学、长春邮电学院合并组建而成。2004年8月29日，原中国人民解放军军需大学并入吉林大学。合并前的六所学校，都有着光荣的历史。原吉林大学的前身是始建于1946年的东北行政学院，1950年更名为东北人民大学，1952年经院系调整成为我党亲手创建的第',9731274);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('延边大学',' ','吉林',1,0,0,1,0,' ','暂空',' ','http://www.ybu.edu.cn','延边大学成立于1949年4月，是一所具有鲜明民族特色的地方综合性大学。1957年9月前曾隶属于国务院高教部，之后划归吉林省所属。建校初期，学校设有文学部、医学部和农业专科，后来经过院系调整，将原有的学部和农业专科发展成为师范学院、医学院和农学院。1958年8月，为了适应国民经济和社会发展的需要，医学院、农学院、工学院从延边大学中分离出来，成为各自独立的延边大学、延边医学院、延边农学院、延边工学院。1959年3月，延边工学院又并入延边大学。1983年1月成立了延边师范高等专科学校。1988年6月成',3216148);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春理工大学',' ','吉林',1,1,0,0,0,' ','暂空',' ','http://www.cust.edu.cn/','长春理工大学是一所以光电技术为特色，光、机、电、算、材相结合为优势，工、理、文、经、管、法、艺多学科协调发展的教学研究型大学，吉林省、国家国防科技工业局、长春市共建院校，享有“中国光学英才摇篮”的美誉。学校原名长春光学精密机械学院，1958年由中国科学院创办。著名科学家、两院院士王大珩为学校创始人、第一任院长。建校以来，学校先后隶属于中国科学院、国防科委、第五机械工业部、国家机械工业委员会、机械电子工业部、中国兵器工业总公司;1999年被划转为以吉林省管理为主，并与国防科工委共建;2002年更名',2736057);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北电力大学',' ','吉林',1,1,0,0,0,' ','暂空',' ','http://www.nedu.edu.cn','东北电力大学是吉林省重点大学，坐落在依山傍水、景色宜人的著名旅游城市--吉林省吉林市，是一所以工科为主，理、工、文、管、法、经、教育等学科门类协调发展的多科性大学。在长期办学过程中，学校坚持以质量求生存，以改革促发展，形成了“勤奋、严谨、求实、创新”的良好校风和突出“一实两创”（实践能力、创新意识、创业精神）的人才培养特色，为国家电力工业和吉林省的经济建设、社会发展做出了重要贡献。',4234877);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春工业大学',' ','吉林',1,1,0,0,0,' ','暂空',' ','http://www.ccut.edu.cn','长春工业大学是一所以工为主，工、管、文、理、经、法、教育、艺术等多学科相互支撑、协调发展的省属重点大学，是吉林省高层次人才培养、应用技术研发、高新技术产品研制、高水平社会服务的重要基地。1992年被吉林省政府确定为首批三所省属重点高校之一，在2004年全国本科教学工作水平评估中获得优秀等级。学校现有全日制在校学生19807名，其中本科生15435名，博士、硕士研究生2169名。教职员工1803名；设有19个学院，2个教研部，1个工程训练中心；拥有2个省重中之重立项建设学科，8个省优势特色重点学科',2610049);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林化工学院',' ','吉林',1,1,0,0,0,' ','暂空',' ','http://www.jlict.edu.cn/','吉林化工学院（原隶属化工部）成立于1958年，是吉林省唯一一所化工类大学，经过几代化院人的探索实践，积淀了深厚的文化底蕴，形成了“校企合作，育人成才”鲜明的办学特色。在化工、机电等传统优势学科领域一直居于省内领先地位，为国家、地方经济建设和社会发展做出了重要的贡献。学校占地面积86万平方米，设有14个教学院部，专业设置涵盖工、理、经、管、文、医6个学科门类，实现硕士、本科和高职多层次办学，全日制在校学生1.5万余人。学校注重内涵发展，师资力量雄厚，专任教师中具有高级专业技术职称人员300余人，具',1124920);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林农业大学',' ','吉林',1,2,0,0,0,' ','暂空',' ','http://www.jlau.edu.cn/index_2013.asp','吉林农业大学创建于1948年，是吉林省属重点大学，是我国首批具有学士、硕士学位授予权的高等院校之一，1993年，成为吉林省省属院校中最早获得博士学位授权单位的高校，2011年，成为吉林省人民政府与农业部合作共建大学，并被教育部授予“全国毕业生就业典型经验高校”称号。学校办学条件优越，师资力量雄厚，校园风光别致，是治学育人的佳园。学校办学历史悠久，文化底蕴深厚，是吉林省农业、农村发展不可替代的、重要的人才培养摇篮和科技创新基地。经过67年的奋斗与努力，现已发展成以涉农优势学科为骨干，多学科协调发展',1921418);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春中医药大学',' ','吉林',1,3,0,0,0,' ','暂空',' ','http://www.ccucm.edu.cn/','长春中医药大学前身为1958年成立的长春中医学院，2006年更名长春中医药大学。历经五十余年的辛勤耕耘、孜孜以求和创新发展，学校形成了“启古纳今，厚德精术”的校训，形成了“坚持以中医药学科为主体，走学研产结合之路，主动服务地方经济建设和社会发展”的办学特色，形成了博士、硕士、学士授权的办学层次，形成了本科教育、研究生教育、高职教育、继续教育、留学生教育等培养高等中医药人才的教育体系，已经成为吉林省唯一一所以中医药学科为主，医、理、工、管、文等多学科协调发展、具有鲜明办学特色和广泛社会声誉的省属重',1429601);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北师范大学',' ','吉林',1,4,1,1,0,' ','暂空',' ','http://www.nenu.edu.cn/','著名的高等学府东北师范大学是教育部直属高校，国家“211工程”重点建设大学。学校坐落在美丽的北国春城长春市。本部校区地处人民大街中段，净月校区设在风景如画的净月潭旅游开发区。学校占地面积150万平方米，其中本部校区占地80万平方米，净月校区占地70万平方米。学校原名东北大学，建校于1946年，是中国共产党在东北地区创建的第一所综合性大学，1949年定址于长春。1950年，根据国家教育事业发展的需要，易名为东北师范大学。东北行政委员会副主席，张学良将军的胞弟张学思为学校首任校长，著名教育家张如心、',4158382);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北华大学',' ','吉林',1,0,0,0,0,' ','暂空',' ','http://www.beihua.edu.cn','北华大学是吉林省省属规模最大的重点综合性大学，1999年经教育部批准，由原吉林师范学院、吉林医学院、吉林林学院、吉林电气化高等专科学校合并组建而成。学校坐落在风景迷人的雾凇之都、中国魅力城市——吉林市。学校现有东、南、西、北四个校区，占地面积126.37万平方米，建筑面积83.03万平方米。教学科研仪器设备总值1.91亿元，馆藏图书263万册。有各类全日制在校生21742人，其中本科生18868人，研究生802人，留学生408人。拥有27个学院［政法学院、教育科学学院、体育学院、文学院、外语学院',3102823);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('通化师范学院',' ','吉林',1,4,0,0,0,' ','暂空',' ','http://www.thnu.edu.cn/','通化师范学院坐落于长白山脚下、鸭绿江畔、中朝边境的吉林省通化市。学院风景秀丽、群山环抱，形成了以长白山的自然、粗犷为主基调，以长白山园林景树、植物为主要景点的长白山园林式校园。通化师范学院的前身通化师范专科学校始建于1958年，1978年经国家教育部批准升格为本科院校。经过近五十年的发展，现已成为以教师教育为主，多学科协调发展的省属师范院校。学院学科门类比较齐全，现有15个系35个专业，其中本科专业30个，专科（高职）专业5个，涵盖法学、教育学、文学、历史学、理学、工学、医学、管理学8个学科门类',634865);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林师范大学',' ','吉林',1,4,0,0,0,' ','暂空',' ','http://www.jlnu.edu.cn','吉林师范大学是吉林省重点大学，坐落在吉林省四平市。学校建于1958年。现任党委书记卓越教授，现任校长张伯军教授。吉林师范大学是吉林省高中骨干教师培养中心。建校50年来，共为国家培养各类人才6万余人。毕业生以能力强、素质好、潜力大而受到社会的广泛赞誉。吉林师范大学是吉林省高校师资培训中心。肩负着全省所有普通高校、成人高校、职业技术学校的教师培训任务，多年来，通过各种形式培训各类教师2.2万人。吉林师范大学是吉林省教师教育研究中心。自1989年国家设立教学成果奖以来，学校连续五届获国家教学成果奖共5',1560183);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林工程技术师范学院',' ','吉林',1,4,0,0,0,' ','暂空',' ','http://www.jltiet.net/','吉林工程技术师范学院坐落在风光秀美的历史名城吉林省省会长春市，是国务院批准成立的全国第一所专门为职业教育培养培训专业课师资的全日制本科师范院校，也是目前东北三省和内蒙古地区唯一一所独立设置的全日制本科职业师范院校，被誉为职业教育师资的摇篮。学校创建于1979年，原名吉林技工师范学院，隶属原国家劳动总局；1983年，划归吉林省管理并更名为吉林职业师范学院；2002年2月，经教育部批准更名为吉林工程技术师范学院。学校是教育部确定的全国重点建设职业教育师资培训基地，是吉林省人民政府确定的全省职教师资保',659704);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('白城师范学院',' ','吉林',1,4,0,0,0,' ','暂空',' ','http://www.bcsfxy.com/','白城师范学院坐落在素有鹤乡之称的白城市，是吉林省西部唯一的一所本科院校。始建于1958年。2000年8月，吉林省政府决定将白城林业学校并入白城师范高等专科学校；2002年3月经教育部批准升格为本科院校。1999年与四平师范学院联合招收本科生，开始本科教育；2002年学院独立招收本科生；2007年与东北师范大学联合培养硕士研究生，开始硕士研究生教育。学院现已发展成为以师范教育为主，涵盖法学、教育学、文学、历史学、理学、工学、农学、管理学等多学科协调发展的教学型省属普通本科院校。在半个世纪的办学过程',714028);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林财经大学',' ','吉林',1,6,0,0,0,' ','暂空',' ','http://www.jlufe.edu.cn','吉林财经大学是吉林省重点大学，是吉林省人民政府与国家税务总局共同建设的学校。学校起步于1946年7月东北银行总行举办的银行干部训练班，经历了东北银行干部学校、东北银行专门学校、长春银行学校、吉林财贸学院、吉林省财贸学校等几个历史时期，1978年5月恢复吉林财贸学院，1992年5月更名为长春税务学院，2010年3月更名为吉林财经大学。学校曾是新中国创建的第一所金融高等学校、国家较早成立的普通本科财经院校之一、国家第一所税务本科大学。2005年，学校整体搬迁至被誉为\"亚洲第一大人工林海\"的净月潭畔，',3275813);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林体育学院',' ','吉林',1,7,0,0,0,' ','暂空',' ','http://www.jlsu.edu.cn/','吉林体育学院始建于1958年,学院是集“教育、竞技、科研、企业”四位一体的新型体育院校，经几代人上下求索，逐日昌盛，为国家育世界冠军百余人。近年来，学院党委带领全院师生员工，锐意改革，取得了跨越式发展，在办学理念、基础设施建设、办学规模、制度创新、教学改革等领域都开创了全新的局面，走出了一条在国际上有影响、在国内有特色的办学新路，为吉林省乃至国家的体育事业做出了突出贡献。凝炼办学理念改善办学条件办学理念是大学的灵魂，是大学坚持自身的独立性、不为眼前利益所驱动的保证。吉林体院建院以来，经几代人上下',332530);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林艺术学院',' ','吉林',1,8,0,0,0,' ','暂空',' ','http://www.jlart.edu.cn/','吉林艺术学院是东北地区惟一的综合性高等艺术学府。她诞生于硝烟战乱之时，成长于民族复兴之际，在历史的行进中，深深植根于东北这片广袤大地之上，浸染着白山黑水的精魂，铸就了博大开放、生生不息的传统办学精神。1946年，学院的前身东北大学鲁迅文艺学院音乐系初创之时，艺术的种子便已开始萌生。1958年，伴随着吉林省艺术教育事业的蓬勃发展，音乐系从她的母体中分离，从祖国四方汇聚一批艺术名家，组建成独立建制的吉林艺术专科学校。此后，学院先后合并了吉林省文化干部学校、吉林省艺术学校、吉林省歌舞剧院附设的音乐舞蹈',281880);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江大学',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.hlju.edu.cn/','志存高远，爱国奉献，艰苦奋斗，自强不息。黑龙江大学是教育部与黑龙江省人民政府共建的有特色、高水平、现代化地方综合性大学，坐落于北国冰城哈尔滨，其前身是1941年成立的中国人民抗日军政大学第三分校俄文队。办学70多年以来，黑龙江大学在国家全球战略构架中，推进与服务中俄战略协作伙伴关系提升，强化俄语学科专业的优势地位和引领作用，实现人才培养方向、科学研究领域、对外交流合作和社会服务等多方位与俄罗斯对接，形成全国高校独树一帜的对俄办学特色。作为黑龙江省学科门类最全、专业覆盖最广、后发优势最强的综合性大',3749830);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨工业大学',' ','黑龙江',1,1,0,1,1,' ','暂空',' ','http://www.hit.edu.cn','哈尔滨工业大学隶属于工业和信息化部，是由工信部、教育部、黑龙江省共建的国家重点大学，是首批进入国家“211工程”和“985工程”建设的若干所大学之一。1920年，中东铁路管理局为培养工程技术人员创办了哈尔滨中俄工业学校——即哈尔滨工业大学的前身，学校成为中国近代培养工业技术人才的摇篮。新中国成立后，哈尔滨工业大学成为全国学习前苏联高等教育办学模式的两所大学之一，此后学校一直得到国家的重点建设。2000年，同根同源的哈尔滨工业大学、哈尔滨建筑大学合并组建新的哈尔滨工业大学。如今，学校已经发展成为一',7583838);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨工业大学（威海）',' ','山东',1,1,0,1,1,' ','暂空',' ','http://www.hitwh.edu.cn/','哈尔滨工业大学创建于1920年，隶属于工业和信息化部，是国家首批“985工程”和“211工程”高校之一。哈工大坚持立足航天、服务国防，面向国际学术前沿和国家重大需求，历经90余年的奋斗，学校已发展成为一所特色鲜明、实力雄厚，居于国内一流水平，在国际上有较大影响的多学科、开放式、研究型的国家重点大学。哈工大威海校区是哈工大“一校三区”办学格局中的重要组成部分。1985年，经原航天工业部批准，哈尔滨工业大学威海分校成立，后成为威海校区，即哈尔滨工业大学(威海)。经过30年的建设发展，威海校区形成了“',1515498);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨理工大学',' ','黑龙江',1,1,0,0,0,' ','暂空',' ','http://zs.hrbust.edu.cn/','哈尔滨理工大学由原机械工业部所属的哈尔滨科学技术大学、哈尔滨电工学院和哈尔滨工业高等专科学校于1995年合并组建而成，1998年划转黑龙江省属，实行中央与地方共建、以地方为主的管理体制，2008年，在黑龙江省高等教育强省建设规划中被确定为重点建设的十所高水平大学之一。三校均始建于二十世纪五十年代，历经60多年的发展，学校现已成为一所以工为主、理工结合，经、管、哲、文、法、教育、艺术等学科协调发展、具有较强办学实力和特色鲜明的教学研究型大学。2003年，被教育部授予“本科教学工作水平评估优秀学校”',4537975);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('燕山大学',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.ysu.edu.cn','燕山大学是河北省人民政府、教育部、工业和信息化部、国家国防科技工业局四方共建的全国重点大学，河北省重点支持的国家一流大学和世界一流学科建设高校，北京高科大学联盟成员。学校源于哈尔滨工业大学，始建于1920年。1958年哈尔滨工业大学重型机械系及相关专业成建制迁至工业重镇齐齐哈尔市富拉尔基区，组建了哈尔滨工业大学重型机械学院。1960年独立办学，定名为东北重型机械学院，成为原机械工业部直属高校。1978年被确定为全国重点高等院校。1985年至1997年学校整体南迁秦皇岛市。1997年经原国家教委批',3191274);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨工程大学',' ','黑龙江',1,1,0,1,0,' ','暂空',' ','http://www.hrbeu.edu.cn/','哈尔滨工程大学坐落于美丽的松花江畔——北国冰城哈尔滨市。学校前身是创建于1953年的中国人民解放军军事工程学院（“哈军工”）。1970年，在“哈军工”原址，以海军工程系全建制及其他各系（部）部分干部教师为基础，组建哈尔滨船舶工程学院；1978年，被国务院确定为全国重点大学；1982年，成为首批具有博士、硕士学位授予权的单位；1994年，更名为哈尔滨工程大学；1996年，进入首批“211工程”建设高校行列；2002年，被教育部批准设立研究生院；2007年，由国防科工委、教育部、黑龙江省政府、海军四',4068887);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北石油大学',' ','黑龙江',1,1,0,0,0,' ','暂空',' ','http://www.dqpi.edu.cn/default.html','东北石油大学是一所全国重点大学，是伴随大庆油田的发现而诞生的一所以工学为主，工、理、管、文、经、法、教育、艺术多学科协调发展的普通高等学校。学校从1961年开始招收本科生，1981年获得硕士学位授予权，1993年获得博士学位授予权。学校有61种本科专业，8个国家级特色专业建设点，19个省级重点(特色)专业;1个国家重点一级学科，3个国家重点二级学科，1个省级重点学科群，6个省级重点一级学科(含26个二级学科);4个博士后科研流动站，1个博士后科研工作站，3个博士学位授权一级学科，18个博士学位点',2709480);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('佳木斯大学',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.jmsu.org/','华夏东极人才的摇篮——佳木斯大学，坐落于中国最早迎接太阳升起的地方、最佳生态环境魅力城市黑龙江省佳木斯市。学校始建于1947年，经过68年的发展建设，现已成为黑龙江省属重点综合性大学，也是黑龙江省重点建设的十所高水平大学之一。学校占地2175亩(145万平方米)，建筑面积57万平方米，设有26个学院和66个研究所(室)。学校学科专业设置涵盖文学、理学、工学、医学、经济学、教育学、法学、历史学、管理学、农学、艺术学等十一大学科门类，现有2个博士学位授权一级学科，10个硕士学位授权一级学科，7个硕士',2132796);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江八一农垦大学',' ','黑龙江',1,2,0,0,0,' ','暂空',' ','http://www.hlau.cn','黑龙江八一农垦大学是黑龙江省属全日制普通高校，由王震将军于1958年主持创建并亲任校长。经过五十余年的发展，八一农大秉承“艰苦奋斗、无私奉献、务实求真、负重致远”的传统和“困知勉行、积厚成器”的校训，致力于为现代化大农业发展提供科技和人才支撑，形成了“服务现代化大农业”的鲜明特色，受到社会各界的广泛赞誉。学校拥有一支实力较强、结构合理的师资队伍。现有教职工1441人，其中专任教师864人，教授166人、副教授259人，具有博士学位教师257人、具有硕士学位教师538人。学校自主培养了龙江学者特聘',1321843);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北农业大学',' ','黑龙江',1,2,0,1,0,' ','暂空',' ','http://www.neau.edu.cn','东北农业大学是一所“以农科为优势，以生命科学和食品科学为特色，农、工、理、经、管等多学科协调发展”的国家“211工程”重点建设大学，是黑龙江省人民政府与农业部省部共建大学、国家“中西部高校基础能力建设工程”项目入选高校、教育部本科教学工作水平评估优秀院校。学校1948年创建于哈尔滨，始称东北农学院，是中国共产党在解放区创办的第一所普通高等农业院校，知名教育家、哈尔滨市第一任市长刘达为学校第一任院长。建校以来，先后隶属于东北行政委员会农业部、国家高等教育部、国家农业部、黑龙江省农业委员会、黑龙江省',2378878);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北林业大学',' ','黑龙江',1,2,1,1,0,' ','暂空',' ','http://www.nefu.edu.cn/','东北林业大学创建于1952年，是在浙江大学农学院森林系和东北农学院森林系基础上建立的，是教育部直属、教育部与国家林业局共建、教育部与黑龙江省人民政府共建的国家“211工程”和“优势学科创新平台”项目重点建设高校。学校地处我国最大国有林区的中心——哈尔滨市，校园占地136公顷，并拥有帽儿山实验林场(帽儿山国家森林公园)和凉水实验林场(凉水国家级自然保护区)等教学、科研、实习基地，总面积达3.3万公顷。学校是一所以林科为优势，以林业工程为特色，农、理、工、经、管、文、法、医、艺相结合的多科性大学。现',2983984);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨医科大学',' ','黑龙江',1,3,0,0,0,' ','暂空',' ','http://sta.hrbmu.edu.cn/zsxc/index.htm','哈尔滨医科大学是由原哈尔滨医科大学（我国医学先驱伍连德博士于1926年创建的哈尔滨医学专门学校,1938年改名为哈尔滨医科大学）和原兴山（现鹤岗市）中国医科大学第一、二分校（其前身为中国共产党于1931年在江西瑞金建立的中国工农红军卫生学校）组建而成。伍连德博士铜像学校历经80余年的发展建设，传承了伍连德博士“赤诚爱国、自强创业”的精神，发扬了中国工农红军卫校“政治坚定、技术优良”的光荣传统，秉承“木直中绳、博学载医”的校训，综合实力不断增强。具有相当的发展规模和鲜明的办学特色，已经跻身于全国高',3208419);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江中医药大学',' ','黑龙江',1,3,0,0,0,' ','暂空',' ','http://zsjy.hljucm.net/zs/default.asp','黑龙江中医药大学始建于1954年，经半个多世纪的建设与发展，学校已成为具有较高教学、科研、医疗水平，在国内外有一定影响的高等中医药院校，现为黑龙江省重点建设的高水平大学。学校于2004年获得全国首批教育部本科教学工作水平评估优秀结论，2007年全国首家通过教育部本科中医学专业认证单位，2008年被确定为国家中医临床研究基地建设单位，2009年晋升全国精神文明建设工作先进单位。学校地处风光秀丽的北国名城哈尔滨市，校园环境优雅，绿树成荫，建筑中西合璧，风格独特，教学主楼和礼堂为典雅的欧式建筑，属国家',1535650);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('牡丹江医学院',' ','黑龙江',1,3,0,0,0,' ','暂空',' ','http://www.mdjmu.cn/','牡丹江医学院创建于1958年，著名史学家郭沫若先生题写了校名。是黑龙江省东南部地区唯一一所省属全日制普通本科医药院校。1997年一次性通过原国家教委合格评估。2006年在国家本科教学水平评估中专家组给予了优秀的评价。2009年经国务院学位委员会批准为新增硕士学位立项建设单位。2013年通过硕士学位立项建设单位整体验收。学院现有教职工2600人，具有副高级以上职称600多人。专任教师580多人，具有博士学历88人(其中留学归国博士7人)，硕士学历550人。有龙江学者特聘教授1人，省级教学名师1人，',823446);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨师范大学',' ','黑龙江',1,4,0,0,0,' ','暂空',' ','http://zsb.hrbnu.edu.cn/','哈尔滨师范大学是一所具有悠久办学历史的省属重点大学，是黑龙江省教育、艺术、人文社会科学和自然科学的重要人才培养基地和科学研究基地，是黑龙江省重点建设的省属高水平大学之一，是教育部本科教学工作水平评估优秀学校。建校63年来，学校秉承“敦品励学，弘毅致远”的校训精神，努力践行“学高为师，身正为范”的从师准则，发扬“敢为人先、争创一流、艰苦奋斗、求真务实、与时俱进、开拓创新”的优良传统，培养和输送了34万余名各级各类优秀人才，为国家和地方经济社会科学文化发展做出了重要贡献。学校办学基础条件良好，师资力',2551752);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('齐齐哈尔大学',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.qqhru.edu.cn/','齐齐哈尔大学始建于1952年，是黑龙江省西部地区唯一一所省属综合性普通高等学校。她坐落在嫩江之滨闻名遐尔的丹顶鹤故乡--黑龙江省齐齐哈尔市劳动湖畔，占地面积126万平方米，建筑面积75万平方米，享有“环湖大学”的美誉。被省委、省政府授予“花园式学校”称号，2007年荣获教育部“高等学校本科教学工作水平评估优秀学校”称号，2008年荣获全省“十佳和谐校园”称号，2009年荣获“全国精神文明建设工作先进单位”称号，2011年荣获“全国文明单位”和黑龙江省“五一劳动奖状”称号，是黑龙江省重点建设高校。',2008174);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('牡丹江师范学院',' ','黑龙江',1,4,0,0,0,' ','暂空',' ','http://xywz.mdjnu.cn/','牡丹江师范学院始建于1958年，历经东北农学院嫩江分院、北安师范专科学校、宁安师范专科学校、牡丹江师范学院四个发展阶段，是国务院批准的首批具有学士学位授予权的高师本科院校，是黑龙江省所属普通高等学校。经过半个多世纪的发展，学校现已成为黑龙江省东南部地区规模最大、综合实力最强的省属本科院校，是黑龙江省东南部地区教师教育研究中心、基础教育研究中心和继续教育培训中心，是中国政府奖学金院校。学校现具有硕士、学士两级学位授权体系。学校坐落在国家级优秀旅游城市——风景秀丽的牡丹江市。2001年10月，学校顺',880830);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨学院',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://zsxx.hrbu.edu.cn/','哈尔滨学院是一所经国家教育部批准的地方普通本科院校，地处享有“东方巴黎”美誉的冰城──哈尔滨。学校占地面积65万平方米，综合办学条件优良，校区错落分布、疏朗有致，格调清新、宁静幽美，被黑龙江省建设厅命名为“花园式单位”，是读书治学的理想园地。多年来，学校坚持“稳定规模、优化结构、提高质量、突出特色、强化服务”的办学方针，实施错位发展战略，坚持质量立校，人才强校，特色兴校，坚持以人才培养为根本任务，以教学为中心工作，面向社会发展需求，培养了一大批高素质应用型人才。学校现有工学院、教育科学学院、经济',1373645);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大庆师范学院',' ','黑龙江',1,4,0,0,0,' ','暂空',' ','http://www.dqsy.net','大庆师范学院坐落于有“绿色油化之都、天然百湖之城、北国温泉之乡”美誉的中国著名石油工业城市——大庆市。学校的前身是1965年创办的大庆师范学校。1980年经教育部批准升格为大庆师范专科学校。1993年改建为大庆高等专科学校。2004年5月，国家教育部正式批准以大庆高等专科学校为基础建立大庆师范学院。2013年12月，顺利通过教育部本科教学合格评估。现有主校区和方晓校区两个校区，占地面积62.75万平方米，建筑面积23.65万平方米，图书馆藏书110万册，电子图书86916GB，固定资产4.5亿元',667689);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('绥化学院',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.shxy.edu.cn','绥化学院是黑龙江省属公立综合性普通本科高等学校，座落在中国寒地黑土绿色物产之乡绥化市，与省会哈尔滨相距70余公里。学校办学历史攸久。1953年，经黑龙江省教育厅批准，绥化县创办了绥化师范学校;1971年，绥化地区在原绥化师范学校的基础上组建绥化地区师范学校;1978年，经国务院批准，成立绥化师范专科学校，是“文革”后国家首批设立的高等师范专科学校之一;2004年，经国家教育部批准成立绥化学院。学校学科门类多样。现设有13个学院(系、部)，开设31个本科专业57个专业方向，涵盖教育学、文学、理学、',686964);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('齐齐哈尔高等师范专科学校',' ','黑龙江',0,4,0,0,0,' ','暂空',' ','http://www.qqhrtc.com/','齐齐哈尔高等师范专科学校其前身始建于1906年（清光绪三十二年）省城（齐齐哈尔）高等小学（前汉义学）创办的师范速成班。1908年（光绪三十四年）在此基础上，成立两级师范学堂。1914年（民国三年）改名为“黑龙江省立第一师范学校”，是全国建校最早的几所中等师范学校之一，2003年经国家教育部批准由原齐齐哈尔市师范学校、幼儿师范学校、教育学院学历教育部分和克山师专部分资源合并，组建了齐齐哈尔高等师范专科学校。在近一个世纪的发展历程中，学校几经变迁，始终坚持勤俭办学，严格治校，育人为本，形成了艰苦创业',77296);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨商业大学',' ','黑龙江',1,6,0,0,0,' ','暂空',' ','http://www.hrbcu.edu.cn/','哈尔滨商业大学始建于1952年，为中国第一所多科性商业大学，是中央与地方共建大学、黑龙江省重点建设的十所大学之一，学校定位为教学研究型大学。2006年在教育部普通高等学校本科教学工作水平评估中获得“优秀”。2012年入选国家“中西部高校基础能力建设工程”建设院校，2013年成为中俄经济类大学联盟成员，2014年被授予第四届“全国文明单位”称号。学校占地总面积113.17万平方米，校舍总面积60.03万平方米。现有全日制在校生25515人，其中研究生1712人。学校师资力量雄厚，有教职员工1936',3068032);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨体育学院',' ','黑龙江',1,7,0,0,0,' ','暂空',' ','http://www.hrbipe.edu.cn/Index.Asp','哈尔滨体育学院坐落在风光秀丽的北方名城哈尔滨市，历经50年的发展建设，现已成为了冰雪特色鲜明，学科布局合理，专业协调发展，教学、科研、训练成绩显著的普通高等体育学府，是2006年教育部本科教学工作水平评估获得优秀院校。哈尔滨体育学院总占地面积278.10万平方米，建筑面积16.65万平方米，由哈市主校区与帽儿山滑雪场组成。现有教职工475人，其中教授33人，副教授76人。各类在籍学生5200余人。目前，哈体院拥有体育教育训练学、体育人文社会学、运动人体科学3个硕士点；体育教育、运动训练、民族传统',176872);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨金融学院',' ','黑龙江',1,6,0,0,0,' ','暂空',' ','http://www.hrbfu.edu.cn/','哈尔滨金融学院坐落于美丽的冰城哈尔滨，始建于1950年，历经东北银行黑龙江省分行干部培训班、中国人民银行齐齐哈尔干部学校、黑龙江银行学校、哈尔滨金融专科学校、哈尔滨金融高等专科学校等几个历史时期。她是经国务院批准组建的我国第一所金融类高等专科学校。学校原隶属于中国人民银行总行，2000年全国高等学校管理体制改革后，改为中央和地方共建，以地方管理为主。2010年4月，经教育部批准，升格为哈尔滨金融学院。经过半个多世纪的探索与发展，学院现占地面积64.29万平方米，校舍建筑面积20.23万平方米。图',1355407);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('复旦大学',' ','上海',1,0,1,1,1,' ','暂空',' ','http://www.fudan.edu.cn','复旦大学创建于1905年，原名复旦公学。“复旦”二字由创始人、中国近代知名教育家马相伯先生选定，选自《尚书大传?虞夏传》中“日月光华，旦复旦兮”的名句，意在自强不息，寄托当时中国知识分子自主办学、教育强国的希望。复旦大学经历近百年的沿革。1917年复旦公学改名为私立复旦大学，下设文、理、商三科以及预科和中学部。1937年抗日战争爆发后，学校内迁重庆北碚，并于1941年改为“国立”。1946年学校迁回上海江湾原址。到1949年学校已设立文、理、法、商、农五院20多个系（科）。鲁迅、郭沫若、邹韬奋、',10229201);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('同济大学',' ','上海',1,1,1,1,1,' ','暂空',' ','http://www.tongji.edu.cn','同济大学是教育部直属重点大学。她创建于1907年，早期为德国医生在上海创办的德文医学堂，取名\"同济\"意蕴合作共济。1912年增设工学堂，1923年被批准改名为大学，1927年正式定为国立同济大学。抗战期间曾内迁经浙、赣、滇入川，1946年回迁上海并发展为以拥有理、工、医、文、法五大学院著称海内外的综合性大学。1952年院系调整后，同济大学成为国内土木建筑领域最大、专业最全的工科大学。1978年经中央批准恢复对德交流，在中科院学部委员李国豪校长领导下实行“两个转变”，即由土木为主的理工科大学向理工',8887670);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海交通大学医学院',' ','上海',1,3,1,0,0,' ','暂空',' ','http://www.shsmu.edu.cn','上海第二医科大学(原名上海第二医学院)由圣约翰大学医学院(1896-1952)、震旦大学医学院(1911-1952)、同德医学院(1918-1952)于1952年全国高等学校院系调整时合并而成。1997年学校通过了“211工程”正式立项，是国家重点建设的“211工程”大学之一。2002年又通过了“211工程”“九五”项目验收和“十五”建设项目论证。学校现有教职医护员工12400人，其中专业技术人员10168名，具有高级职称的在职人员1340名。目前在校生5700多名，其中博士生和硕士生千余名，占',1234122);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海交通大学',' ','上海',1,0,1,1,1,' ','暂空',' ','http://www.sjtu.edu.cn','上海交通大学是我国历史最悠久的高等学府之一，是教育部直属、教育部与上海市共建的全国重点大学，是国家“七五”、“八五”重点建设和“211工程”、“985工程”的首批建设高校。经过百余年的不懈努力，上海交通大学已经成为一所“综合性、研究型、国际化”的国内一流、国际知名大学，并正在向世界一流大学稳步迈进。19世纪末，甲午战败，民族危难。盛宣怀先生和一批有识之士秉持“自强首在储才，储才必先兴学”的信念，在上海创办了交通大学的前身——南洋公学。建校伊始，学校即坚持“求实学，务实业”的宗旨，以培养“一等人才',7679167);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华东理工大学',' ','上海',1,1,1,1,0,' ','暂空',' ','http://www.ecust.edu.cn/','华东理工大学地处于人文荟萃、海纳百川的大都市上海，原名华东化工学院，其办学历史可以追溯到100多年前的南洋公学和震旦学院，是1952年全国院系调整时由交通大学(上海)、震旦大学(上海)、大同大学(上海)、东吴大学(苏州)、江南大学(无锡)等校的化工系合并组建而成的全国第一所以化工特色闻名的院校。1956年被定为全国首批招收研究生的学校之一。1960年起被中共中央确定为直属教育部的全国重点高校。1993年经国家教委批准，更名为华东理工大学。1996年6月学校进入国家“211工程”重点建设行列。19',5956323);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海理工大学',' ','上海',1,1,0,0,0,' ','暂空',' ','http://www.usst.edu.cn/','上海理工大学是一所以工学为主，理学、管理学、经济学、文学等多学科交融发展的上海市属重点大学，已有百余年办学历史。学校办学文脉源于1906年创办的沪江大学和1907年创办的德文医工学堂，百年薪火相传，独树一帜的优秀办学传统孕育了一大批爱国青年和志士仁人，滋养了一大批学术精英、工程专家和社会翘楚，为国家和社会培养了十余万优秀专业人才，享有中国“制造业黄埔军校”的美誉。学校动力工程及工程热物理、光学工程、管理科学与工程三大学科长期居于国内领先地位，学科领域的优势与特色为全国公认。伴随工程教育的时代发展',5419252);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海海事大学',' ','上海',1,1,0,0,0,' ','暂空',' ','http://www.shmtu.edu.cn/','中国高等航海教育发轫于上海，1909年晚清邮传部上海高等实业学堂（南洋公学）船政科开创了我国高等航海教育的先河。1912年成立吴淞商船学校，1933年更名为吴淞商船专科学校。1959年交通部在沪组建上海海运学院。2004年经教育部批准更名为上海海事大学。为更好地服务上海国际航运中心建设和国家航运事业发展，根据上海市高校布局结构调整规划，2008年上海海事大学主体搬迁临港新城。2009年学校举行百年校庆系列活动。上海海事大学是一所以航运、物流、海洋为特色，具有工学、管理学、经济学、法学、文学和理学',4405982);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东华大学',' ','上海',1,1,1,1,0,' ','暂空',' ','http://www.dhu.edu.cn/','致力于建设“国内一流、国际有影响，有特色的高水平大学”的东华大学以“海纳百川、追求卓越”之勇气，坚持走“观念兴校、学术兴校、管理兴校”发展之路，在“211工程”国家重点建设的高等院校中独树一帜。地理历史：学校地处中国上海，三个校区分别位于松江区和长宁区，占地面积近2000亩，校园环境宜人，系“上海市花园单位”。学校创建于1951年，前身是华东纺织工学院。1985年，更名为中国纺织大学；1999年，更名为东华大学。1960年，被国家教育部确定为全国重点大学，是中国首批具有博士、硕士、学士三级学位授',4767490);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海电力学院',' ','上海',1,1,0,0,0,' ','暂空',' ','http://www.shiep.edu.cn','热忱欢迎广大考生报考上海电力学院！“教育部卓越工程师教育培养计划”首批试点高校光明使者——电力工程师的摇篮能源电力特色鲜明、多学科协调发展的高水平理工类高校上海电力学院地处人文荟萃、海纳百川的国际大都市——上海，是中央与上海市共建、以上海市管理为主的全日制普通高等院校。学校创建于1951年，缘电而生，倚电而立，随电而进。建校65年来，上电培养了三十余万名能源电力人才，见证与支持了我国电力工业的发展和飞跃。近年来，学校在能源电力特色的学科建设、应用型人才培养方面获得了社会赞誉，2016年获得“上海',2935807);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海应用技术大学',' ','上海',1,1,0,0,0,' ','暂空',' ','http://www.sit.edu.cn','上海应用技术大学是一所有60余年办学历史的以工为主，理、管、经、文、法、农、艺多学科协调发展，特色鲜明的全日制普通本科高等学校。在多年的办学实践中，学校坚持以“应用技术”为本，强化内涵建设，走出了一条应用技术型本科院校的特色发展之路。学校先后取得学士学位授予权、硕士学位授予权以及工程硕士培养单位资格。学校已连续八次获得上海市“文明单位”称号。2016年3月，经教育部批准学校更名为“上海应用技术大学”。学校以“应用技术”命名的本科高校，传承行业办学的优良传统，适应现代国际大都市先进制造业、现代生产',2552126);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海健康医学院',' ','上海',1,3,0,0,0,' ','暂空',' ','http://www.sumhs.edu.cn/','上海健康医学院是一所新建的市属本科医学院校，由原上海医药高等专科学校、上海医疗器械高等专科学校和上海健康职业技术学院组建而成。近60年的办学历史，学校形成了特色鲜明的医工结合、医技人才培养见长的办学优势，赢得了业界“健康促进的使者，白衣天使的摇篮”及我国“医疗器械工程师的摇篮”等美誉，为我国医学技术、临床医学、医疗器械等相关领域输送了大批高素质、高技能的应用型人才，为中国的医疗卫生健康事业做出了重要贡献。学校定位于医学及医学相关类应用型本科医学院校，培养特色鲜明、实用性强、服务于临床医学和人类健',134415);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海海洋大学',' ','上海',1,2,0,0,0,' ','暂空',' ','http://www.shfu.edu.cn','上海海洋大学是上海市人民政府与国家海洋局、农业部共建的多科性应用研究型高等学府，前身是张謇、黄炎培创建于1912年的江苏省立水产学校，1952年升格为中国第一所本科水产高校——上海水产学院，1985年更名为上海水产大学，2008年更为现名。1914年9月1日定校训：勤朴忠实。学校目前有上海市浦东新区沪城环路校区、杨浦区军工路校区、杨浦区民星路校区3个校区。其中，主校区沪城环路校区占地约1600余亩，规划建设面积58.6万平方米。学校教育体系完备，优势学科明显，研究成果显著。拥有研究生教育、普通本',3073139);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海中医药大学',' ','上海',1,3,0,0,0,' ','暂空',' ','http://ygzs.shutcm.edu.cn/','创立于1956年的上海中医药大学，是新中国诞生后国家首批建立的四所中医药高等院校之一，也是上海市属高校中唯一的一所医科类重点特色院校。1985年上海市中医药研究院成立，目前与上海中医药大学实行两块牌子、一套管理机构，学校校级领导兼任研究院院级领导的管理体制。学校位于浦东新区张江高科技园区科研教育区内，占地500余亩，教学设施齐全、环境优美，在上海建设“创新型城市”战略布局中，已成为“张江药谷”的重要组成部分。建校50多年来，为国家培养和输送了各级各类中医药专门人才，校友遍布60多个国家和地区。学',8099486);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华东师范大学',' ','上海',1,4,1,1,1,' ','暂空',' ','http://www.ecnu.edu.cn','学校创建于1951年10月16日，是以大夏大学、光华大学为基础，同时调进复旦大学和同济大学等院校的部分系科，在大夏大学原址上创办的新中国第一所社会主义师范大学。1959年华东师范大学就被确定为全国首批16所重点院校之一，1978年再次被确认为全国重点大学，是1986年国务院批准首批建立研究生院的33所高等院校之一。1996年通过“211工程”预审并被列入国家重点建设大学行列。2006年教育部和上海市决定重点共建华东师范大学，学校在建设世界知名的高水平研究型大学的道路上迈出了坚实步伐。学校地处上海',6746555);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海师范大学',' ','上海',1,0,0,0,0,' ','暂空',' ','http://www.shnu.edu.cn','上海师范大学创建于1954年，是上海市重点建设的高校，是一所以文科见长并具教师教育特色的文、理、工、艺学科协调发展的综合性大学。在对接国家“双一流”建设中，学校的中文、数学、教育学等11个学科进入高峰高原学科建设，入选学科数位居上海市属高校第二。在最新QS世界大学学科排名中，学校9个学科达到QS世界一流学科入选标准，入选学科数位居上海所有高校第八，市属高校第二。学校已进入上海市教育综合改革部市共同支持的高校行列。上海师范大学现有徐汇和奉贤两个主校区，共有17个学院，86个本科专业，覆盖哲学、经济',4608666);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海外国语大学',' ','上海',1,5,1,1,0,' ','暂空',' ','http://www.shisu.edu.cn/','上海外国语大学是国家教育部直属并与上海市共建、进入“211工程”的全国重点大学。学校创建于新中国成立不久后的1949年12月，时名上海俄文学校。1956年，学校更名为上海外国语学院。1963年，学校被确定为全国重点高校，直属教育部领导。1994年，学校升格为上海外国语大学，并成为国家教委和上海市共建的首批学校。1996年，学校被列为国家教委“211工程”建设高校。60年书香翰墨、桃李芬芳，学校已在社会上取得良好的声誉，在国际上获得了一定的影响力。学校现有虹口、松江两个校区。虹口校区位于上海市中心',3706466);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海财经大学',' ','上海',1,6,1,1,0,' ','暂空',' ','http://www.shufe.edu.cn','上海财经大学已有91年历史，现为教育部直属、国家“211工程”重点建设高校，并进入国家建设高水平大学项目行列。1997年，江泽民同志亲笔题词：“面向新世纪把上海财经大学建设成为具有一流水平的社会主义大学”。上海财经大学源于1917年南京高等师范学校创办的商科，著名社会活动家、爱国民主人士杨杏佛任商科主任。南京高等师范学校扩展为国立东南大学时，为适应商学人才培养的需要，商科迁址上海，于1921年成立上海商科大学，这是中国教育史上最早的商科大学，著名教育家郭秉文任校长，著名经济学家马寅初任教务主任。',7427443);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海海关学院',' ','上海',1,6,0,0,0,' ','暂空',' ','http://shanghai_edu.customs.gov.cn','上海海关学院是经教育部批准设立的具有高等学历教育招生资格的全日制普通高等学校，直属于中华人民共和国海关总署。中国海关高等教育已有近百年历史，1908年清政府在北京建立税务学堂（后更名为税务专门学校，并迁址上海），该学堂是世界上最早从事海关高等专业教育的学校之一。新中国成立后，1953年上海海关学校设立；1980年经国务院批准，升格为上海海关专科学校；1996年更名为上海海关高等专科学校。2007年3月，教育部批准在上海海关高等专科学校的基础上设立上海海关学院。学院现为全国唯一一所系统设置海关类课',2887164);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海旅游高等专科学校',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.sitsh.edu.cn/','上海旅游高等专科学校创建于1979年，时名为“上海旅行游览专科学校”，隶属上海市外办。1980年，教育部批准更名为“上海旅游专科学校”。1983年，划归上海市旅游局、高教局双重领导。1986年，划归国家旅游局领导。1992年，更名为“上海旅游高等专科学校”。2000年，划归上海市教委领导。2003年，划归上海师范大学管理，与原上海师范大学城市与旅游学院合并组成上海师范大学旅游学院。上海旅游高等专科学校与上海师范大学旅游学院是同时采用两个名称的合一教育机构。上海旅游高等专科学校是独立法人单位，20',449303);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华东政法大学',' ','上海',1,11,0,0,0,' ','暂空',' ','http://www.ecupl.edu.cn/','华东政法大学是新中国创办的第一批高等政法院校。1952年6月，经华东军政委员会批准，圣约翰大学、复旦大学、南京大学、东吴大学、厦门大学、沪江大学、安徽大学、上海学院、震旦大学等9所院校的法律系、政治系和社会系合并，在圣约翰大学旧址成立华东政法学院。1958年，学校并入上海社会科学院；1963年再次筹建，次年招生；1966年停止招生，1972年被撤销；1979年3月，经国务院批准，第二次复校。2007年3月，经教育部批准，学校更名为华东政法大学。学校是上海市管理的高校。现任党委书记曹文泽教授，校长',3150566);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海体育学院',' ','上海',1,7,0,0,0,' ','暂空',' ','http://www.sus.edu.cn','上海体育学院是新中国成立后创建最早的体育高等学府，建于1952年，原名华东体育学院，由原南京大学、华东师范大学和南京金陵女子大学等院校的体育系科合并而成。学院原直属国家体育总局，2001年6月划转地方，由国家体育总局和上海市人民政府共建共管。学院具有浓厚的历史积淀与人文底蕴，先后汇聚著名体育教育家吴蕴瑞，中国第一位体育女博士、运动解剖学学科创始人张汇兰、武术名家蔡龙云、“南拳王”周士彬、“北拳王”张立德等大批知名学者，传承“身心一统、德技相长、文理兼修、服务社会”的办学思想，形成“身心一统、兼蓄',809479);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海音乐学院',' ','上海',1,8,0,0,0,' ','暂空',' ','http://www.shcmusic.edu.cn','上海音乐学院是享誉国内外的著名音乐学府。它的前身是伟大的民主革命家、杰出的教育家、思想家蔡元培先生和音乐教育家萧友梅博士于1927年11月27日共同创办的国立音乐院。自1949年至1984年，著名作曲家、音乐理论家、音乐教育家贺绿汀长期担任院长（后为名誉院长），为学校的建设及发展作出了卓越的贡献。1984年后桑桐、江明?教授先后任院长，现任院长为杨立青教授。一批国内外享有盛誉的知名教授及近年来崭露头角的中青年学术骨干执教于各系各专业，七十多年来，学院培养的人才遍布全国及世界各地，被誉为“音乐家的',675727);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海戏剧学院',' ','上海',1,8,0,0,0,' ','暂空',' ','http://www.sta.edu.cn/','上海戏剧学院是中国培养戏剧专门人才的高等艺术院校，前身是上海市市立实验戏剧学校，1945年12月1日由著名教育家顾毓琇与著名戏剧家李健吾、顾仲彝、黄佐临等创立，熊佛西先生为首任院长。1949年10月，上海市立实验戏剧学校改名为上海市立戏剧专科学校。1952年全国高等院校实行院系调整，山东大学艺术系戏剧科、上海行知艺术学校戏剧组并入后正式建院，更名为中央戏剧学院华东分院。1956年正式命名为上海戏剧学院，系文化部直属高等艺术院校。2000年4划转为由上海市与文化部共建，2002年6月，原上海师范大',605787);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海大学',' ','上海',1,0,0,1,0,' ','暂空',' ','http://www.shu.edu.cn','上海大学是上海市属、国家“211工程”重点建设的综合性大学。现任党委书记是于信汇教授，校长是罗宏杰教授。1922年10月，上海大学创建，校长为于右任，教务长为瞿秋白。这是一所被誉为“武有黄埔、文有上大”的革命学校，1927年“四•一二”反革命事变后，被国民党当局强行关闭。1994年5月，上海工业大学(成立于1960年)、上海科学技术大学(成立于1958年)、原上海大学(成立于1983年)和上海科技高等专科学校(成立于1959年)合并，组建为新的上海大学。上海大学的广大师生立志继承与发扬二十世纪二',8401094);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京大学',' ','江苏',1,0,1,1,1,' ','暂空',' ','http://www.nju.edu.cn','南京大学坐落于钟灵毓秀、虎踞龙蟠的金陵古都，是一所历史悠久、声誉卓著的百年名校。其前身是创建于1902年的三江师范学堂，此后历经两江师范学堂、南京高等师范学校、国立东南大学、第四中山大学、国立中央大学、国立南京大学等历史时期，于1950年更名为南京大学。1952年，在全国高校院系调整中，南京大学调整出工学、农学、师范等部分院系后与创办于1888年的金陵大学文、理学院等合并，仍名南京大学。校址从四牌楼迁至鼓楼金大原址。在一个多世纪的办学历程中，南京大学及其前身与时代同呼吸、与民族共命运，谋国家之强',8892172);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州大学',' ','江苏',1,0,0,1,0,' ','暂空',' ','http://zsb.suda.edu.cn','苏州大学坐落于素有“人间天堂”之称的古城苏州，是国家“211工程”重点建设高校、“2011计划”首批认定高校，是江苏省属重点综合性大学。苏州大学之前身是SoochowUniversity(东吴大学堂，1900年创办)，开西式教育之先河，融中西文化之菁华，是中国最早以现代大学学科体系举办的大学。在中国高等教育史上，东吴大学最先开展法学(英美法)专业教育、最早开展研究生教育并授予硕士学位，也是第一家创办学报的大学。1952年中国大陆院系调整，由东吴大学之文理学院、苏南文化教育学院、江南大学之数理系合',10187012);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东南大学',' ','江苏',1,0,1,1,1,' ','暂空',' ','http://www.seu.edu.cn/','东南大学是中央直管、教育部直属的全国重点大学，是国家“985工程”和“211工程”重点建设的大学之一。是国务院授权首批可授予博士、硕士、学士学位，审定教授、副教授任职资格及自批增列博士生导师的高校。学校座落在历史文化名城南京，主校区位于四牌楼2号，是六朝宫苑的遗址，也曾是明朝国子监所在地，千百年来书声不断，学泽绵延。东南大学是我国最早建立的高等学府之一，素有“学府圣地”和“东南学府第一流”之美誉。东南大学前身是创建于1902年的三江师范学堂。1921年经近代著名教育家郭秉文先生竭力倡导，以南京高',8856442);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京航空航天大学',' ','江苏',1,1,0,1,0,' ','暂空',' ','http://www.nuaa.edu.cn/nuaanew/','南京航空航天大学座落在钟灵毓秀、虎踞龙蟠的历史文化名城南京。自1952年创建以来，已经成为一所具有航空、航天、民航特色，以工为主，工、管、理、经、文、法、哲等多学科协调发展，国家“211工程”建设的全国重点大学，是全国56所设立研究生院的大学之一，学校现隶属于国防科学技术工业委员会。温家宝总理与我南航学子胡铃心亲切交流南京航空航天大学现有明故宫和将军路两个校区，占地面积近2600亩，建筑面积70余万平方米，图书馆馆藏文献296万余册。现设有航空宇航学院、能源与动力学院、自动化学院、信息科学与技术',5452853);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京理工大学',' ','江苏',1,1,0,1,0,' ','暂空',' ','http://zsb.njust.edu.cn','南京理工大学是隶属于中华人民共和国工业和信息化部的全国重点大学，坐落在钟灵毓秀、虎踞龙蟠的古都南京。学校由创建于1953年的新中国军工科技最高学府---中国人民解放军军事工程学院(简称“哈军工”)分建而成，经历了炮兵工程学院、华东工程学院、华东工学院等发展阶段，1993年更名为南京理工大学。1995年，学校成为国家首批“211工程”重点建设高校;2000年，获教育部批准成立研究生院;2011年，获批建设“985工程优势学科创新平台”。学校坚持“以人为本，厚德博学”的办学理念，秉持“进德修业，志道',5851028);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏科技大学',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.just.edu.cn/','江苏科技大学是一所省部共建、以工为主、特色鲜明的普通高等学校，具有博士学位授予权，是江苏省重点建设高校。学校始终坚持为船舶工业、国防工业、蚕业服务，形成了船舶、国防、蚕业三大特色，逐步发展成为一所工、管、农、文、理、经、教、法等诸学科协调发展的多科性大学。学校坐落于风景秀丽的江南历史文化名城——江苏省镇江市，办学历史源自1933年诞生于黄浦江畔的上海大公职业学校，1993年更名华东船舶工业学院，2004年更名为江苏科技大学。2006年12月，学校以优秀成绩通过了教育部本科教学水平评估。学校现有镇',3175916);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国矿业大学',' ','江苏',1,1,1,1,0,' ','暂空',' ','http://www.cumt.edu.cn','中国矿业大学是教育部直属的全国重点高校、国家“211工程”和“985优势学科创新平台项目”建设高校，同时也是教育部与江苏省人民政府、国家安全生产监督管理总局共建高校。作为一所具有一百多年办学历史、特色鲜明的多科性研究型高水平大学，对我国煤炭能源行业和地方经济社会发展发挥着不可替代的引领和支撑作用。1960年和1978年，学校先后两次被确定为全国重点高校，为全国首批具有博士和硕士授予权的高校之一，学校设有研究生院。学校坐落于素有“五省通衢”之称的国家历史文化名城——江苏省徐州市，校园占地面积441',4405080);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国矿业大学(北京)',' ','北京',1,1,1,1,0,' ','暂空',' ','http://www.cumtb.edu.cn','中国矿业大学是教育部直属的全国重点大学，由地处北京和徐州的两个校区组成。中国矿业大学（北京）是在原北京矿业学院基础上发展起来的一所研究型大学。1978年，经国务院批准，中国矿业学院北京研究生部成立，招收和培养研究生；1997年7月，经原国家教委和北京市批准，成立了中国矿业大学（北京），1998年开始招收本科生。学校位于北京市高校云集的海淀区学院路，东眺国家奥林匹克公园，西望颐和园、圆明园与香山，学校占地面积24万平方米，总建筑面积32万平方米，图书馆藏书45万余册，电子图书18.9万册。中国矿业',1106431);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京工业大学',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.njtech.edu.cn','南京工业大学具有百年办学历史，是一所以工为主，理、管、经、文、法、哲、医协调发展的多科性大学，于2001年由原南京化工大学与原南京建筑工程学院合并组建而成，是江苏省重点建设高校,江苏省高等教育综合体制改革试点高校。原南京化工大学的前身可追溯到创建于1902年的三江师范学堂；原南京建筑工程学院的前身可追溯到创建于1915年的同济医工学堂机师科，均具有悠久的办学历史和雄厚的办学实力。学校拥有学院（部）26个，各类学生近3万人，教职工2800余人，高级职称近900人。其中中国工程院院士3人，国务院学位',3695672);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常州大学',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.cczu.edu.cn/','江苏工业学院坐落在美丽富饶、人杰地灵的江南名城——常州，是一所中央与地方共建、以江苏省管理为主的普通高等院校。经过30多年的建设，江苏工业学院已发展成为一所拥有工学、理学、文学、经济学、管理学、法学共六大学科门类，具有一定规模，在国际、国内有一定知名度的本科教育和研究生教育的高等学府。学校有白云和武进两个校区，占地面积1600亩。现有44个本科专业，面向全国27个省、市、自治区招生。学校现有全日制在校生16000多人，其中研究生1000多人。有教职工1300多人，专任教师近900人(其中，高级职',1903952);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京邮电大学',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.njupt.edu.cn','南京邮电大学是一所具有光荣革命传统的高校，其前身是1942年诞生于山东抗日根据地的战邮干训班，是我党、我军早期系统培养通信人才的学校之一，江苏省重点建设高校。1958年经国务院批准，学校正式改建为本科院校，长期为工业与信息化部（原邮电部）直属重点高校。2000年2月起，实行中央与地方（现为工业和信息化部与江苏省）共建，以江苏省管理为主。2005年4月，经教育部批准，南京邮电学院正式更名为南京邮电大学。几十年来，学校坚持与时俱进，形成了“勤奋、求实、进取、创新”的校风，综合办学实力和整体办学水平不',4538713);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河海大学',' ','江苏',1,1,1,1,0,' ','暂空',' ','http://www.hhu.edu.cn','河海大学是一所因水利而熠熠生辉享誉海内外的著名学府。学校有着悠久的办学历史、深厚的文化积淀和独特的精神传承。1915年著名实业家张謇先生创办的河海工程专门学校，是我国历史上第一所专门培养水利科技人才的高等学府。1952年全国院系调整，融合吸纳了南大、同济、交大、浙大等名校水利相关专家，成立华东水利学院，其雄厚的办学实力和骄人的办学成果名噪一时。1985年学校复名“河海大学”，邓小平同志亲笔题写了校名。河海大学校本部位于南京市鼓楼区，并在常州市新北区、南京市江宁区设有校区，总占地面积2300余亩。',5864266);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江南大学',' ','江苏',1,0,1,1,0,' ','暂空',' ','http://www.jiangnan.edu.cn/','江南大学坐落于太湖之滨的江南名城——江苏省无锡市，是教育部直属、国家“211工程”重点建设高校和“985优势学科创新平台”建设高校。在部省市的支持下，学校现已建成占地面积3200亩、建筑面积107万平方米，以“生态校园，曲水流觞”为设计理念，设施先进、功能齐全、环境优美的校园。江南大学享有“轻工高等教育明珠”的美誉。学校源于1902年创建的三江师范学堂，历经国立中央大学、南京大学等发展时期；1952年由原中央大学、复旦大学、武汉大学、浙江大学、江南大学的有关系科组建南京工学院（现东南大学）食品工',7619589);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京林业大学',' ','江苏',1,2,0,0,0,' ','暂空',' ','http://www.njfu.edu.cn/','南京林业大学坐落在风景秀丽的紫金山麓、碧波荡漾的玄武湖畔，是一所中央与地方共建并以地方管理为主的多科性大学。学校前身为中央大学（创建于1902年）森林系和金陵大学（创建于1888年）森林系，1952年合并组建的南京林学院，是当时全国仅有的三所高等林业院校之一。1955年华中农学院林学系（武汉大学、南昌大学和湖北农学院森林系合并组成）并入，1972年更名为南京林产工业学院，1983年恢复南京林学院名称，1985年更名为南京林业大学。南京林业大学作为一所以林科为特色，以资源、生态和环境类学科为优势的',2525066);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏大学',' ','江苏',1,0,0,0,0,' ','暂空',' ','http://www.ujs.edu.cn/site1/','学校概况江苏大学是2001年8月经教育部批准，由原江苏理工大学和镇江医学院、镇江师范专科学校合并组建的以工科为特色的教学研究型综合性全国重点大学，是全国首批具有博士学位授予权的高校，综合实力位居全国高校百强之列。学校坐落在风景秀丽的江南历史文化名城江苏省镇江市，校园占地3045亩，各类建筑面积140余万平方米。学校与南京大学、东南大学等学校同宗同源，办学历史可追溯到1902年刘坤一、张之洞等在南京创办的三江师范学堂。目前，学校设有24个学院，全日制在校生35000余人，其中各类研究生近7000人',3951452);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京信息工程大学',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.nuist.edu.cn/newindex/index.htm','南京信息工程大学前身是有“中国气象人才摇篮”美誉的南京气象学院，始建于1960年，1978年被确定为全国重点大学，2004年更名为南京信息工程大学，是中国气象局和江苏省人民政府共建的全国重点高校，是江苏省重点建设大学，教育部本科教学工作水平评估优秀学校，具有完整的学士、硕士、博士教育培养体系，并设有博士后科研流动站。学校现有全日制在校本科生3万余人；专任教师千余人，其中近70%具有高级职称或博士学位。现有大气科学学院、应用气象学院、遥感学院、大气物理学院、水文气象学院、海洋科学学院、信息与控制学',3144324);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京城市职业学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.ncc.com.cn/','南京城市职业学院坐落在钟灵毓秀、人文荟萃的古都南京，是经江苏省人民政府批准，国家教育部备案，独立建制的普通高等职业院校。南京城市职业学院是在地方优质高等教育资源的基础上组建的一所新型普通高校。伴随着全国高等教育的大发展，学院在业已打造的优秀办学品牌和积累的丰富的高职院校办学经验基础上，进一步适应经济社会发展对高素质职业技术人才的需求，积极探索高等职业教育的办学模式和人才培养模式，努力为地方社会经济发展培养生产、建设、管理、服务一线需要的职业性、应用性人才，不断深化教育教学改革，全面推进综合素质教',19830);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南通大学',' ','江苏',1,0,0,0,0,' ','暂空',' ','http://www.ntu.edu.cn/','学校概况网址：www.ntu.edu.cn联系电话：0513-85012170南通大学坐落于素有“江海明珠”、“历史文化名城”美誉的沿海开放城市——江苏省南通市，是江苏省人民政府和交通运输部共建的综合性大学。学校的办学历史最早可追溯到近代著名实业家、教育家张謇先生于1912年创办的私立南通医学专门学校和南通纺织专门学校。2004年，南通医学院、南通工学院、南通师范学院三校合并组建南通大学。经过一个世纪的办学历程，南通大学现已建设成为一所规模结构合理、学科门类齐全、教学质量优秀、办学效益明显的地方',3535824);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('盐城工学院',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.ycit.cn/','在美丽的黄海之滨，在享有“东方湿地之都，仙鹤神鹿世界”美誉的沿海新型工业中心城市——盐城。矗立着一所省属本科院校——盐城工学院。在50多年的发展历程中，我校大力弘扬新四军“铁军精神”，坚持走具有盐工特色的办学育人之路，凝炼了“笃学格致、厚德重行”的校训，形成了以工为主，经、管、文、理、农多学科协调发展的办学格局。学校现有17个二级学院、5个教学部和7个校属研究机构，开设44个本科专业，有全日制本科生近18000人，有专任教师995人，具有高级职称教师445人，具有博士学位、硕士学位的教师752人',1032883);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京农业大学',' ','江苏',1,2,1,1,0,' ','暂空',' ','http://lib.njau.edu.cn/','南京农业大学坐落于钟灵毓秀、虎踞龙蟠的古都南京，是一所以农业和生命科学为优势和特色，农、理、经、管、工、文、法学多学科协调发展的教育部直属全国重点大学，是国家“211工程”重点建设大学和“985优势学科创新平台”高校之一。现任党委书记左惟教授，校长周光宏教授。南京农业大学前身可溯源至1902年三江师范学堂农业博物科和1914年金陵大学农学本科。1952年，全国高校院系调整，由金陵大学农学院和中央大学农学院以及浙江大学农学院部分系科合并成立南京农学院。1963年被确定为全国两所重点农业高校之一。1',3550349);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京医科大学',' ','江苏',1,3,0,0,0,' ','暂空',' ','http://zs.njmu.edu.cn','南京医科大学是江苏省属重点高校。学校创建于1934年，时名江苏省立医政学院，是国内最早开展研究生教育的高等学府之一。1957年，更名为南京医学院。1962年，被列为全国首批六年制医药院校。1981年，被批准为全国首批博士、硕士学位授予单位。1993年，更名为南京医科大学。学校现有硕士学位授权点55个，博士学位授权点35个，博士后科研流动站3个，学位授权点已覆盖医学、理学、工学、管理学、法学、教育学和文学等7个学科门类，拥有3个国家重点学科、1个国家重点培育学科、4个江苏省一级学科重点学科、12个',1881613);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('徐州医科大学',' ','江苏',1,3,0,0,0,' ','暂空',' ','http://www.xzmc.edu.cn/','徐州医学院坐落在有“五省通衢”之称的历史文化名城——江苏省徐州市，隶属于江苏省教育厅。学校建于1958年，迄今已有50余年办学历程，目前，已发展成为苏北地区乃至整个淮海经济区医学教育、医疗服务和医学科研的中心，是该地区办学历史悠久、办学特色鲜明的一所高等医学院校，也是江苏省仅存独立建制设置的两所医学类院校之一。学校占地面积1000余亩，教育设施完备，临床教学资源充足，校园环境优美。学校学科建设定位明确，专业结构规划合理，师资力量雄厚，教学梯队合理，有一批杰出的教授和中青年业务骨干，教学科研成果显',1270521);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京中医药大学',' ','江苏',1,3,0,0,0,' ','暂空',' ','http://www.njutcm.edu.cn/','南京中医药大学始建于1954年，历经江苏省中医进修学校、江苏省中医学校、江苏新医学院、南京中医学院等历史时期，是全国建校最早的高等中医药院校之一，是江苏省重点建设高校，也是江苏省人民政府与国家中医药管理局共建高校。半个世纪以来，南京中医药大学为新中国高等中医教育培养输送了第一批师资、主持编写了第一套教材和教学大纲，培养并诞生了新中国中医药界最早的学部委员(院士)，为新中国现代中医高等教育模式的确立和推广做出了重要贡献，被誉为“中国高等中医教育的摇篮”。学校坐落于钟灵毓秀、虎踞龙蟠的古都南京，拥有',1847666);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国药科大学',' ','江苏',1,3,1,1,0,' ','暂空',' ','http://www.cpu.edu.cn','中国药科大学始建于1936年，是教育部直属的全国重点大学，是国家“211工程”重点建设的高校。中国药科大学是我国第一所独立设置的高等药学院校。经过近70年的励精图治和锐意进取，学校已发展成为以药学为特色，理、工、经、管、文等多学科协调发展，专科、本科、硕士、博士等多层次教育的多科性大学。学校主校区坐落在六朝古都南京，由玄武门校区、燕子矶校区和镇江校区三部分组成。玄武门校区重点是研究生教育，坐落在风景如画的玄武湖畔，校园内花木扶疏，绿草如茵；燕子矶校区重点是本科生教育，位于北临万里长江的燕子矶脚下',3508661);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京师范大学',' ','江苏',1,4,0,1,0,' ','暂空',' ','http://www.njnu.edu.cn/','南京师范大学坐落在六朝古都南京，是国家“211工程”重点建设的江苏省属重点大学、江苏省与教育部共建高校。它的主源可追溯到1902年创办的三江师范学堂，该学堂是中国高等师范教育的发祥地之一。后历经两江优级师范学堂、南京高等师范学校、东南大学、第四中山大学、江苏大学、中央大学、南京大学等时期；其另一源头为1888年创办的汇文书院，后发展为私立金陵大学，1951年与私立金陵女子文理学院（曾称私立金陵女子大学）合并，成立公立金陵大学。1952年全国高校院系调整，在原南京大学、金陵大学等有关院系的基础上组',4405663);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮阴师范学院',' ','江苏',1,4,0,0,0,' ','暂空',' ','http://www.hytc.edu.cn/','招生代码1341淮阴师范学院是周恩来总理故乡的一所省属本科院校，坐落在风光秀美、人文荟萃的国家历史文化名城——江苏省淮安市。学校设有17个二级学院和1个民办二级学院（文通学院），63个普高本科专业，涵盖理学、工学、经济学、法学、教育学、文学、历史学、管理学、艺术学等九个学科门类。面向19个省区招生，全日制在校本科生2万余人，成教生1万余人。与东北师范大学、宁夏大学、江苏师范大学等省内外高校联合培养在校研究生110余名。同美国、英国、法国、澳大利亚、韩国、日本和港澳台地区的近20多所境外高校建立了',897594);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('盐城师范学院',' ','江苏',1,4,0,0,0,' ','暂空',' ','http://www.yctc.edu.cn/','盐城师范学院是江苏省属公办本科院校，坐落在江苏沿海开放城市盐城市市区。学校创建于1958年，前身是盐城师范专科学校和盐城教育学院，2002年国家级重点中专盐城商业学校并入。学校现有通榆和新长两个校区，校园占地面积近1500亩。学校现有在校生36000余人，其中全日制本科在校生21000余人，成人学历教育学员15000人。设有文学院、经济法政学院、马克思主义学院、社会学院、外国语学院、音乐学院、美术学院、教育科学学院、数学科学学院、物理科学与电子技术学院、化学化工学院、生命科学与技术学院、药学院、',1041908);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京财经大学',' ','江苏',1,6,0,0,0,' ','暂空',' ','http://xsc.njue.edu.cn/zsxx','南京财经大学是一所以经济管理类学科为主，经济学、管理学、法学、工学、文学、理学等多学科支撑配套、协调发展的江苏省属重点建设大学。2000年，学校由原南京经济学院、江苏财经高等专科学校、江苏经济管理干部学院三校合并组建而成。2003年4月，经教育部批准，学校由南京经济学院正式更名为南京财经大学。2006年，学校在教育部本科教学工作水平评估中获得优秀。历经多年改革建设，依托新的发展机制，南京财经大学的知名度迅速扩大，社会影响力逐步提升，办学实力明显增强，整体面貌焕然一新，实现了超常规、跨越式发展。学',5257017);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏警官学院',' ','江苏',1,11,0,0,0,' ','暂空',' ','http://www.jspi.cn','江苏警官学院是江苏唯一一所公安政法类本科院校，是全国首批建立的省属公安本科院校。学院本部位于南京市安德门，另有龙潭、浦口两个校区。校园占地共1000多亩，校舍建筑面积16.4万平方米(正在规划建设的浦口主校区建筑面积19万平方米)。现有痕迹检验、法化学、刑事侦查等8个综合性实验室，教学科研仪器设备总值3200多万元。建有射击馆、警体训练馆、警用驾驶训练场、技战术训练模拟街区、标准运动场等训练场馆。图书馆馆藏纸质图书60多万册，电子图书60多万册，中外文期刊1300种。开发建设公安特色数据库8个，',1164046);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京体育学院',' ','江苏',1,7,0,0,0,' ','暂空',' ','http://zs.nipes.cn','南京体育学院成立于1956年8月，学院地处十朝都会，沐依扬子紫金，坐落于南京东郊中山陵旅游风景区内的民国中央体育场旧址，承继了中国近代体育的文脉。校园绿树掩映，建筑错落有致，优美的自然景观与厚重的人文景观交相辉映，环境优雅，底蕴丰厚。学院占地640余亩，校舍建筑面积13.7万平方米，拥有室外运动场面积5.99万平方米；图书资料18.5万册，教学训练科研仪器设备总值7300万元。学院现设3个本科系（体育系、运动系、运动人体科学系），1个专科二级学院（职业技术学院），3个部（研究生部[筹]、继续教育',300963);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京艺术学院',' ','江苏',1,8,0,0,0,' ','暂空',' ','http://www.njarti.edu.cn/','坐落于秦淮河畔、石头城下的南京艺术学院是江苏省唯一的综合性高等艺术学府。上溯到1912年，中国美术教育的奠基人刘海粟先生创办了上海美术院，1930年更名为上海美术专科学校，由蔡元培先生任上海美专董事会主席，并为校歌作词、题写校训、学训。1922年，颜文?先生在苏州创办了苏州美术专科学校。这两所中国最早的私立美术学校在1952年全国高等学校院系调整中与山东大学艺术系美术、音乐两科合并成为华东艺术专科学校，址于江苏无锡社桥。合并工作于12月8日完成，从此，这一天成为南京艺术学院的校庆日。1958年华',816466);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州科技大学',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://web.usts.edu.cn/','苏州科技学院地处古城苏州的西部——国家级高新技术开发区，由原苏州城建环保学院(建设部属)与苏州铁道师范学院(铁道部属)于2001年9月合并组建而成，是一所中央与地方共建、以江苏省管理为主的普通高等院校。学校以工为主，涵盖工学、理学、文学、历史学、哲学、法学、管理学、教育学、艺术学、经济学等10个学科门类。学校现有江枫、石湖、天平等3个校区，校园占地面积2260亩。现有全日制在校本科生14803人，硕士研究生940人。学科专业学校现设有建筑与城市规划学院等14个教学单位。设有建筑学等58个本科专业',2774002);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常熟理工学院',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.cslg.cn/','常熟理工学院坐落于人文荟萃、山明水秀的国家级历史文化名城、国际花园城市—常熟。其前身是创办于1958年的苏州师范专科学校和1984年建校的常熟职业大学，1989年经原国家教委和江苏省人民政府批准两校合并为常熟高等专科学校。2001年江苏省人口学校并入。2004年5月12日，经教育部批准在常熟高等专科学校基础上建立常熟理工学院。学校是一所以理工为主、应用技术和教师教育为特色的省属本科院校。学校现有6个学院和5个系（人文学院、外国语学院、艺术学院、生物与食品工程学院、计算机科学与工程学院、物理与电子',844978);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江大学',' ','浙江',1,0,1,1,1,' ','暂空',' ','http://www.zju.edu.cn/','浙江大学是教育部直属、省部共建的普通高等学校，是首批进入国家“211工程”和“985工程”建设的若干所重点大学之一。浙江大学前身求是书院成立于1897年，为中国人自己最早创办的新式高等学府之一。1952年，在全国高等院校调整时，曾被分为多所单科性学校，部分系科并入兄弟高校。1998年，同根同源的浙江大学、杭州大学、浙江农业大学、浙江医科大学合并组建新的浙江大学。经过一百多年的建设与发展，学校已成为一所基础坚实、实力雄厚，特色鲜明，居于国内一流水平，在国际上有较大影响的研究型、综合型大学。现任校长',11573374);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('杭州电子科技大学',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.hdu.edu.cn/','杭州电子科技大学是一所电子信息特色突出，经管学科优势明显，工、理、经、管、文、法、教等多学科相互渗透的教学研究型大学。学校前身为创建于1956年的杭州航空工业财经学校，属于军工事业单位，是我国较早成立的一所以信息科技(IT)学科为主导的高等院校。后经国务院批准，1980年设立杭州电子工业学院，2004年更名为杭州电子科技大学，学校先后隶属于第四机械工业部、电子工业部和信息产业部等中央部委。2000年开始实行省部共建、省管为主的管理体制，并被浙江省列为重点建设的教学研究型大学。2007年12月25',3501696);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江工业大学',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.zjut.edu.cn/','浙江工业大学是一所综合性的浙江省属重点大学，创建于1953年，其前身可以追溯到1910年创立的浙江中等工业学堂，先后经历了杭州化工学校（1953年6月～1958年6月），浙江化工专科学校（1958年6月～1960年8月）、浙江化工学院（1960年2月～1980年10月）、浙江工学院（1978年2月～1992年10月）和浙江工业大学（1991年12月至今）等发展阶段，几易校址，数历分合。浙江省经济管理干部学院、杭州船舶工业学校、浙江建材工业学校分别于1994年、1997年和2001年并入浙江工业大',4541470);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江理工大学',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.zstu.edu.cn/','浙江理工大学是一所特色鲜明，优势突出，理、工、文、艺、经、管、法、教育等多学科协调发展的浙江省属重点建设大学。学校坐落在历史文化名城杭州市，占地面积2100亩，拥有教学科研仪器设备值5.15亿元，图书馆藏书（含电子图书）近300万册、中外期刊4000余种，是浙江省高校数字图书馆下沙高教园区分中心。学校现有全日制在校学生26000余人，其中研究生2700余人。百年学府底蕴深厚学校前身蚕学馆创办于1897年，是我国最早创办的新学教育机构之一，1964年由国务院命名为浙江丝绸工学院，1999年更名为浙',4378692);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江海洋大学',' ','浙江',1,2,0,0,0,' ','暂空',' ','http://www.zjou.net.cn/','浙江海洋学院创建于1958年，1998年由原浙江水产学院和舟山师范专科学校合并组建，此后舟山卫生学校、浙江水产学校、浙江省海洋水产研究所、舟山石化学校、舟山商业学校等学校（单位）相继并（进）入。半个世纪以来，学校数易校址、历经变迁，积淀形成了“海纳百川、自强不息”的校训精神，现已发展成为一所具有硕士学位授权资格，以海洋为特色，理学、农学、工学、文学、经济学、管理学等多学科协调发展的省属本科高校。学校总占地面积2500余亩，其中位于浙江舟山群岛新区的主校区——新城校区占地1000余亩，校区枕山环海',1577111);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江农林大学',' ','浙江',1,2,0,0,0,' ','暂空',' ','http://www.zjfc.edu.cn','浙江林学院创建于1958年，是浙江省属全日制本科院校。经过50年的建设与发展，现已成为一所涵盖理学、工学、文学、管理学、农学、经济学、法学、医学等八大学科门类的多科性大学，以本科教育为主，具有硕士和学士学位授予权。具有招收留学生和港澳台学生资格。学校坐落于杭州西郊全国优秀旅游城市--临安市，交通便利，办学条件优良。校园占地面积2500余亩，校舍建筑面积近60万平方米。校园与植物园两园合一，是一座集教学、科研、生物多样性保护等功能于一体的现代化生态校园。学校设有农业与食品科学学院、林业与生物技术学',1957137);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江中医药大学',' ','浙江',1,3,0,0,0,' ','暂空',' ','http://www.zjtcm.net/default.html','浙江中医药大学是一所以中医中药为主，医理工管文多学科协调发展的省属高校。座落于历史文化名城浙江省杭州市钱塘江南岸，与国家级风景名胜“六和塔”隔江相望。学校前身是创办于1953年6月的浙江省中医进修学校。1959年6月，正式成立浙江中医学院，校址位于杭州市庆春街原浙江大学旧址。2000年3月，整体迁至现址。2006年2月，更名为浙江中医药大学。学校是全国首批招收和培养中医药研究生、获得港澳台地区招生权、免试招收香港学生的高等中医药院校，是首批国家教育信息化试点单位、国家级大学生创新创业训练计划实施',1356776);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江师范大学',' ','浙江',1,4,0,0,0,' ','暂空',' ','http://www.zjnu.edu.cn','学校规模■校园占地面积3300余亩，建筑面积100余万平方米；■全日制本专科在校生25700余人，各类研究生4970余人，留学生近400人；■现有19个学院，65个专业。师资队伍■在职教职员工2750余人，专任教师1540余人，正高职称教师330余人，副高职称教师750余人，具有博士学位教师670余人；■中科院院士1名，共享中科院院士5名、工程院院士1名，国家“千人计划”专家1人，国家外专千人计划专家1人，“长江学者”特聘教授3人，国家“杰出青年科学基金”获得者2人，国家突出贡献专家3人，享受国',2636729);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('杭州师范大学',' ','浙江',1,4,0,0,0,' ','暂空',' ','http://www.hznu.edu.cn','杭州师范大学于1978年经国务院批准建立，其前身杭州师范学校可以追溯到建于1908年的浙江两级师范学堂(1913年更名为浙江省立第一师范学校)。2000年以来，杭州教育学院、杭州医学高等专科学校等相继并入，组建新的杭州师范学院。2007年3月，学校更名为杭州师范大学。2001年，学校通过教育部组织的本科教育工作合格评估，2003年以优良成绩通过浙江省教育厅的教学工作水平评估，2007年通过了教育部组织的本科教学工作水平评估并获得优秀，是教师教育与艺术教育具有特色、人文社会科学学科具有一定优势、理',2911964);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖州师范学院',' ','浙江',1,4,0,0,0,' ','暂空',' ','http://www.hutc.zj.cn','湖州师范学院是一所“省市共建、以市为主”的全日制普通本科高等院校。学校办学历史可追溯至1916年的钱塘道第三县立师范讲习所，1958年开始高等教育，1994年开始招收本科生。1999年3月，经国家教育部批准，原湖州师范专科学校、湖州师范学校和湖州教师进修学院合并成立湖州师范学院。2000年5月，原湖州卫生学校并入湖州师范学院。2005年11月，学校以“良好”成绩通过了教育部本科教学工作水平评估，实现了跨越式发展。学校以“明体达用”为校训，是浙江省文明单位。学校区位:湖州师范学院位于历史悠久、山水',924175);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('绍兴文理学院',' ','浙江',1,4,0,0,0,' ','暂空',' ','http://www.usx.edu.cn','绍兴文理学院是一所普通全日制公办院校，硕士学位授予单位。其前身可以追溯到1909年创立的山会初级师范学堂，鲁迅先生曾出任学堂监督（校长）。学校坐落于全国首批历史文化名城绍兴市区。拥有本部（河东、河西、南山）、兰亭、上虞、镜湖四个校区，校园占地面积1980亩，校舍总建筑面积64万平方米。开设62个本科专业，涵盖经济学、法学、教育学、文学、理学、工学、农学、医学、管理学、艺术学等十大学科门类。设有人文学院等18个二级学院和1个独立学院（绍兴文理学院元培学院）。现有全日制在校生2.3万余人，教职工18',1338515);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('台州学院',' ','浙江',1,0,0,0,0,' ','暂空',' ','http://www.tzc.edu.cn','台州学院是一所经教育部批准，由浙江省和台州市共管共建的综合性普通高校，办学历史悠久，其前身是1978年经国务院批准建立的台州师专，其办学源头可上溯至1907年三台中学堂简易师范科。学校在发展历程中海纳百川，先后汇聚了台州教师进修学院、临海师范、台州卫校、温岭师范等多所学校。学校现在临海和椒江两地办学，校园总占地面积1500亩，总建筑面积39万平方米，现有馆藏纸质图书175万册，数字资源总量41984G，教学科研仪器设备总值16312万元，建有标准田径运动场、游泳池等先进设施。学校下设14个二级学',1384881);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('温州大学',' ','浙江',1,0,0,0,0,' ','暂空',' ','http://www.wzu.edu.cn/','温州大学是一所地方综合性大学，办学历史可追溯到温州近代著名爱国爱乡人士黄溯初先生1933年创办的温州师范学校，文化底蕴深厚，有着优良的办学传统。学校位于浙江省南部美丽的沿海城市温州。学校占地总面积2352亩，分茶山校区和学院路校区。茶山主校区位于温州高教园内，南眺罗山群峰，北蕴三垟湿地，山水灵秀与翰墨书香相得益彰，是莘莘学子修身治学的绝佳所在。学校校舍面积114.6万平方米，教学科研仪器设备总值4.51亿元，馆藏纸质图书243.1万册，电子图书约162.2万册，各类中外文电子期刊和资料数据库59',2106983);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('丽水学院',' ','浙江',1,4,0,0,0,' ','暂空',' ','http://www.lsu.edu.cn','丽水学院是一所公办全日制普通本科高校，地处素有“秀山丽水，养生福地”的国家级生态示范区——丽水市。其办学历史可追溯到1907年的处州师范学堂，以后几易其名，历尽沧桑。2004年5月在丽水师范专科学校和丽水职业技术学院合并的基础上经教育部批准升格更名为丽水学院。2010年5月顺利通过教育部新建本科院校教学工作合格评估。学校是浙江省文明单位、全国精神文明建设工作先进单位、全国民族体育先进集体、全国绿化模范单位。校园占地面积980亩，校舍建筑面积32万平方米。图书馆馆藏纸质图书145万册，期刊2000',2184182);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江工商大学',' ','浙江',1,6,0,0,0,' ','暂空',' ','http://www.zjgsu.edu.cn','发展沿革1911年，创建杭州中等商业学堂。章乃器、骆耕漠等是我校的著名校友。1980年，国务院批准成立杭州商学院，获学士学位授予权。1990年，学校获硕士学位授予权。2003年，学校获博士学位授予权和工商管理硕士学位（MBA）授予权。2004年，教育部批准更名为浙江工商大学。学院设置工商管理学院、经济学院、金融学院、统计与数学学院、财务与会计学院、旅游学院、法学院、食品生物与环境工程学院、信息与电子工程学院、计算机与信息工程学院、人文学院、公共管理学院、外国语学院、日本语言文化学院、艺术设计学院',3391404);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('嘉兴学院',' ','浙江',1,0,0,0,0,' ','暂空',' ','oldname": "嘉兴学院','嘉兴学院是2000年3月经国家教育部批准，由原浙江经济高等专科学校和嘉兴高等专科学校合并组建的普通本科高校，实行“省市共建共管，以省为主”的管理体制。此后平湖师范学校、嘉兴卫生学校、浙江会计学校和嘉兴市粮食干部学校等四校相继并入。原浙江经济高等专科学校创校于1914年。学校办学所在地嘉兴是国家历史文化名城，是江南文化发源地，也是中国共产党的诞生地，地处东南沿海、长三角中心腹地，东接上海、北邻苏州、西连杭州、南濒杭州湾，交通便捷、经济繁荣、社会和谐。世界著名物理学家、香港科技大学原校长朱经武教授受',2068900);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国美术学院',' ','浙江',1,8,0,0,0,' ','暂空',' ','http://www.caa.edu.cn','中国美术学院的前身是国立艺术院。1928年，时任大学院院长的蔡元培先生择址杭州西子湖畔，创立了第一所综合性的国立高等艺术学府——国立艺术院，设国画、西画、雕塑、图案四个系及预科和研究部，开始了“美育代宗教”的实践，揭开了中国高等美术教育的篇章。在中国美术学院八十年的发展历史中，数迁其址，几易其名，历经风雨。从国立艺术院、国立杭州艺术专科学校、国立艺术专科学校、中央美术学院华东分院、浙江美术学院到如今的中国美术学院，从杭州到江西、湖南、云南、重庆的漫漫西迁之路，从孤山钟声的悠远、南山湖畔的绵长、到',336367);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国计量大学',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.cjlu.edu.cn/','中国计量学院是我国质量监督检验检疫行业唯一的本科院校，是一所具有鲜明的计量标准质量检验检疫特色的浙江省重点建设大学。中国工程院院士庄松林教授任名誉校长，国家杰出青年基金获得者、浙江省特级专家林建忠教授任校长。学校坐落于历史文化名城、东方休闲之都——浙江省省会杭州。学校占地面积1580亩，校园环境优雅，办学条件优越，拥有教学科研仪器设备总值3.3亿元，纸质图书200万册。学校现有49个本科专业，学科涵盖工学、理学、管理学、文学、法学、经济学、农学、医药学、哲学、艺术学十大学科门类。设有19个学院（',4812086);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽大学',' ','安徽',1,0,0,1,0,' ','暂空',' ','http://www.ahu.edu.cn/','安徽大学(AnhuiUniversity)是国家“211工程”重点建设高校，是安徽省人民政府与教育部共建高校，安徽省属重点综合性大学。作为安徽现代高等教育开端，学校1928年创建于当时省会安庆市。抗战期间，学校被迫西迁，并一度流散，1946年复校，为国立安徽大学。1949年12月迁至芜湖，几经调整，学校于1956年迁建合肥市，1958年全面恢复招生。伴随着我国改革开放和现代化建设的伟大进程,学校进入历史上发展最快最好的时期,尤其是进入国家“211工程”高校建设行列之后，更是实现了跨越式的发展。学',4676242);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国科学技术大学',' ','安徽',1,1,0,1,1,' ','暂空',' ','http://www.ustc.edu.cn/','中国科学技术大学是中国科学院所属的一所以前沿科学和高新技术为主、兼有特色管理和人文学科的综合性全国重点大学。1958年9月创建于北京，首任校长由郭沫若兼任。它的创办被称为“我国教育史和科学史上的一项重大事件”。建校后，中国科学院实施“全院办校，所系结合”的办学方针，学校紧紧围绕国家急需的新兴科技领域设置系科专业，创造性地把理科与工科即前沿科学与高新技术相结合，注重基础课教学，高起点，宽口径培养新兴、边缘、交叉学科的尖端科技人才，汇集了严济慈、华罗庚、钱学森、赵忠尧、郭永怀、赵九章、贝时璋等一批国',3365850);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥工业大学',' ','安徽',1,1,1,1,0,' ','暂空',' ','http://www.hfut.edu.cn/ch/','合肥工业大学是一所教育部直属的全国重点大学、国家\"211工程\"重点建设高校和\"985工程\"优势学科创新平台建设高校，是教育部、工业和信息化部与安徽省共建高校。学校创建于1945年，1960年被中共中央批准为全国重点大学。刘少奇、朱德、邓小平等老一辈无产阶级革命家先后来校视察指导工作。1979年，邓小平同志亲笔题写了\"合肥工业大学\"校名；1997年，原合肥工业大学与安徽工学院合并组成新的合肥工业大学；1998年，重新划转教育部管理。建校以来，学校以民族振兴和社会进步为己任，深怀\"工业报国\"之志，秉',6418564);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽工业大学',' ','安徽',1,1,0,0,0,' ','暂空',' ','http://www.ahut.edu.cn/','安徽工业大学是一所以工为主，工、经、管、文、理、法、艺等七大学科门类协调发展、具有鲜明行业特色的多科性大学，是安徽省重点建设大学，是“中西部高校基础能力建设工程”项目实施高校。学校面向全国25个省（市、自治区）招生，具有免试推荐优秀本科生攻读硕士研究生资格，具有学士、硕士、博士三级学位授予权。学校创建于1958年，1977年组建马鞍山钢铁学院，1985年更名为华东冶金学院，1998年划转到安徽省，实行省部共建，2000年与安徽商业高等专科学校合并，更名为安徽工业大学。学校现有佳山、秀山两个校区，',2635641);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽理工大学',' ','安徽',1,1,0,0,0,' ','暂空',' ','http://www.aust.edu.cn/','安徽理工大学位于我国重要的能源城安徽省淮南市，是一所以工科为主，工、理、管、医、文、经、法、艺等学科协调发展的省属重点大学。学校创办于1945年，是安徽省第一所工科院校。学校是我国首批具有硕士学位、学士学位授予权的高校。现具有博士学位、硕士学位、工程硕士专业学位、高校教师在职攻读硕士学位、研究生毕业同等学力申请硕士学位和学士学位授予权。学校设有地球与环境学院、能源与安全学院、土木建筑学院、机械工程学院、电气与信息工程学院、材料科学与工程学院、化学工程学院、计算机科学与工程学院、理学院、外国语学院',3855128);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽工程大学',' ','安徽',1,1,0,0,0,' ','暂空',' ','http://www.ahpu.edu.cn/','安徽工程科技学院是一所省属多科性高等工科院校和安徽省重点建设院校，校址坐落在国家级开放城市芜湖市。学校办学历史可追溯到1935年由西班牙天主教耶稣会创设的安徽私立内思高级工校，解放后更名为芜湖电机制造学校（简称芜湖电校）,隶属于国家第一机械工业部。1972年学校更名为芜湖机械学校，1977年作为合肥工业大学教学点，开始招收全日制本科生。1978年12月经国务院批准升格为全日制普通本科高等学校，定名为安徽机电学院，2001年12月，更名为安徽工程科技学院。1996年通过原国家教委本科教学合格评价。',2384130);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽农业大学',' ','安徽',1,2,0,0,0,' ','暂空',' ','http://www.ahau.edu.cn/','安徽农业大学坐落于安徽省省会合肥，是一所办学历史悠久、学科门类齐全、社会影响广泛的省属重点高校。2011年成为安徽省人民政府和农业部共建高校。2012年成为国家首批建设“新农村发展研究院”的十所高校之一。2013年入选安徽省首批“有特色高水平”大学建设高校。2014年成为安徽省人民政府和国家林业局共建高校。学院设置：学校现设有农学院，植物保护学院，园艺学院，林学与园林学院，动物科技学院，茶与食品科技学院，理学院，生命科学学院，资源与环境学院，工学院，轻纺工程与艺术学院，信息与计算机学院，经济管理',2126248);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽医科大学',' ','安徽',1,3,0,0,0,' ','暂空',' ','http://www.ahmu.edu.cn/','安徽医科大学为安徽省省属重点大学。前身是1926年5月创办于上海的私立东南医学院。1949年底响应中共中央华东局“面向农村，走向内地”的号召，内迁安徽省，成为安徽省第一所高等医科院校。1952年改名为安徽医学院。1996年6月经国家教委批准，更名为安徽医科大学。学校位于安徽省会合肥市，校园环境优美，交通便利。学校的办学定位是建设高水平医科大学。学校将现代著名教育家蔡元培先生为学校前身东南医学院师生的题词“好学力行，造就良医”确立为校训，弘扬“爱国爱民，献身人类健康”的光荣传统，秉承“兴国、奉献、',2229005);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('蚌埠医学院',' ','安徽',1,3,0,0,0,' ','暂空',' ','http://www.bbmc.edu.cn/','蚌埠医学院创建于1958年7月，由原上海第二医学院分迁和安徽医学院援建而成，是安徽省省属普通高等医学本科院校和国家首批具有学士和硕士学位授予权的单位，是院士工作站和博士后岗位设置单位，是安徽省高等教育自学考试护理学专业主考单位。学校占地面积近1000亩，新校位于蚌埠市风景秀丽的龙子湖畔，是一所功能齐全、环境优美，集智能化、生态化为一体的省级园林式单位和绿化模范单位。学校秉承“笃学、精业、修德、厚生”的校训，遵循“以医为主、以本为主、以人为本、以质为本”的办学理念，孕育铸就了“艰苦创业、严谨治学、',1323799);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('皖南医学院',' ','安徽',1,3,0,0,0,' ','暂空',' ','http://www.wnmc.edu.cn/','皖南医学院是安徽省省属普通高等医学本科院校和国家首批具有学士和硕士学位授予权的单位，是教育部本科教学工作水平评估优秀学校，坐落在素有“长江巨埠、皖之中坚”之称的历史名城——芜湖市。学校现有滨江、赭麓、银湖三个校区，占地700余亩，其中滨江校区徽派建筑风格与现代气息相得益彰，四周绿水环绕，环境优美，设施齐全，条件完备，是我校办学的主校区，现有各类在校生2万7千余人。2015年面向全国12个省招收全日制本专科学生3800人。学校现有临床医学院、麻醉学院、医学影像与检验学院、法医学院、口腔医学院、护理',1352661);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽师范大学',' ','安徽',1,4,0,0,0,' ','暂空',' ','http://www.ahnu.edu.cn/','安徽师范大学是安徽建校最早的高等学府。学校坐落在具有“徽风皖韵、千湖之城”美誉的国家级开放城市——芜湖，濒临浩瀚长江，傍依灵秀赭山，是一所集江南自然之神秀、汇安徽人文之灵杰的省属重点大学。在十九世纪末清代敬敷书院和二十世纪初创办的安徽大学堂基础上，学校的前身——省立安徽大学于1928年创建于当时的安徽省府安庆市。1946年，省立安徽大学更名为国立安徽大学，时有“于京沪一带，仅次于上海同济大学”之誉。1949年，国立安徽大学成建制迁至芜湖，并与当时的安徽学院合并成立新的安徽大学。1950至1952',2118256);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阜阳师范学院',' ','安徽',1,4,0,0,0,' ','暂空',' ','http://www.fync.edu.cn/','阜阳师范学院坐落在皖西北中心城市阜阳市。这里钟灵毓秀、人文荟萃，历史文化源远流长，我国著名的文化精英管子、老子、庄子，曹操、曹丕、曹植，欧阳修、苏轼、曾巩等赋予了这片土地厚重的历史底蕴。学校毗邻古颍州西湖、京九铁路交通枢纽、皖北地区4C级机场，环境怡人，交通便捷，区位优越。学校创办于1956年，1977年开始招收本科生，1978年经国务院批准建立阜阳师范学院，获首批学士学位授予权;2001年通过教育部本科教学工作合格评估;2008年教育部本科教学工作水平评估获得“优秀”;2013年正式获批成为硕',1112005);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安庆师范大学',' ','安徽',1,4,0,0,0,' ','暂空',' ','http://www.aqtc.edu.cn/','安庆师范学院座落在国家历史文化名城安庆市，系安徽省省属本科师范院校。学校历史悠久，是安徽省高等教育的发源地。1897年，清代著名省学敬敷书院在此办学，1902年改为安徽大学堂。1928年省立安徽大学在此创办，1946年改为国立安徽大学。1981年获学士学位授予权。2001年，安徽省人民政府批准以安庆师范学院为基础筹建安庆大学。2002年学校顺利通过教育部本科教学工作合格评估。2004年开始招收硕士研究生。2006年国务院学位委员会批准为硕士学位授予单位。2006年教育部批准为国家大学生文化素质教',1274257);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮北师范大学',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.hbcnc.edu.cn/','淮北师范大学位于国家能源基地、国家园林城市安徽省淮北市，是一所多科性普通高等学校。1974年建校，时为安徽师范大学淮北分校。1978年，经国务院批准，定名为淮北煤炭师范学院，隶属原煤炭工业部，面向全国招生。1998年，实行“中央与地方共建，以安徽省管理为主”的管理体制。2010年，经教育部批准，更名为淮北师范大学。1981年获学士学位授权单位，2000年以优异成绩通过教育部本科教学工作合格评估，2003年获硕士学位授权单位，2007年在教育部本科教学工作水平评估中获得“优秀”等次，2009年新增',1907601);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黄山学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.hsu.edu.cn/','黄山学院坐落在风景秀丽、文风馥郁的中国优秀旅游城市——安徽省黄山市，是一所综合性的省属普通本科院校。其前身是始创于1978年的徽州师范专科学校。1997年12月，更名为黄山高等专科学校。2002年2月，经国家教育部批准，黄山高等专科学校升格为黄山学院。学校分为南、北两个校区，现有校园面积1800多亩，校园生态丰富、环境优美、人文底蕴深厚。校舍建筑面积35余万平方米，教学设备总值4800多万元，图书馆藏书92万册。学校拥有一批设施先进的综合实验室、分项实验室，并拥有网络中心、计算机中心、语言实验中',1082078);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('皖西学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.wxc.edu.cn/','安徽省首批地方应用型高水平大学立项建设单位——皖西学院皖西学院是经教育部批准设立的省属综合性公办本科院校，学校办学历史可以追溯到原全国人大副委员长朱蕴山先生1918年创办的“安徽省立第三甲种农业学校”。学校坐落在荣膺有“国家级园林城市”、“国家级生态示范区”、“中国水环境治理优秀城市”、“中国人居环境范例奖”、“中国特色魅力城市200强”等称号的安徽省历史文化名城-六安市。六安市是国家级交通枢纽城市，东距省会合肥仅60公里，距新桥国际机场30公里，多条高速铁路和公路穿境而过，目前到北京、合肥、南',934851);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('滁州学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.chzu.edu.cn','滁州学院是安徽省省属公办全日制普通本科院校，坐落在距南京50公里的历史文化名城——滁州市，与著名的琅琊山风景区和“天下第一名亭”醉翁亭相毗邻，区位优越、交通便捷、人文荟萃。学校创建于1950年，历经滁州师范学校、滁县师范学校、安徽师范大学滁县教学点（分校）办学阶段，1980年5月更名为滁州师范专科学校，2004年5月经教育部批准升格为滁州学院。学校现有会峰、琅琊两个校区，校园占地面积100.24万平方米，建筑面积38.23万平方米。教学仪器设备总值2.08亿元，建有配套的各类实验室和8个校内实习',816230);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽财经大学',' ','安徽',1,6,0,0,0,' ','暂空',' ','http://www.aufe.edu.cn/','安徽财经大学是一所以经、管、法学为主，跨文学、理学、工学、史学、艺术学八大学科门类，面向全国招生、就业的多科性高等财经院校，是我国首批具有学士学位授予权、第二批具有硕士学位授予权的高校，是安徽省重点建设的大学。学校于1959年5月始建于安徽合肥，时名为安徽财贸学院。1961年迁址素有“珍珠城”美誉的淮河流域中心城市蚌埠，校址设在交通路。1983年，校本部迁至龙湖西校区，先后隶属于中华全国供销合作总社、商业部、国内贸易部。2000年2月，国务院决定，安徽财贸学院实行中央与地方共建，以安徽省政府管理',4588330);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宿州学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.ahszu.edu.cn/','一、学校概况宿州学院是以工学为主，管理学、理学、艺术学、文学、经济学、教育学等多学科协调发展的应用型本科高校，是安徽省首批示范应用型本科高校建设单位并顺利通过验收、国家级大学生创新创业训练计划实施高校、安徽省普通高校首批大学生创新创业示范高校、第八届、第九届安徽省文明单位、安徽省高等学校优秀教学管理集体、安徽省普通高校毕业生就业工作标兵单位、第一届安徽省教育系统文明单位立项建设学校。学校坐落在“扼汴控淮，当南北冲要”的宿州市，一向被视为皖北地区的文化渊薮，是皖北地区和周边地区重要的人才培养基地。',805790);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('巢湖学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.chtc.edu.cn/','巢湖学院为安徽省属全日制普通高等本科院校，学院位于皖江开发带“合（合肥）巢（巢湖）芜（芜湖）工业走廊”的中心——巢湖市，座落在巢湖市东郊的半汤温泉疗养度假区。在浩瀚八百里的巢湖之滨，风景秀丽的汤山脚下，这所被安徽省人民政府命名的“文明单位”、“花园式学校”，依山傍水、碧树掩映、花香四溢、景色宜人，校园内楼房林立、道路纵横、景点散落、环境幽雅，是理想的教书育人园地。巢湖学院前身为1977年底创办的巢湖师范专科学校，2002年4月经国家教育部批准，升格更名为“巢湖学院”，隶属于安徽省教育厅。自创办以',751331);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮南师范学院',' ','安徽',1,4,0,0,0,' ','暂空',' ','http://www.hnnu.edu.cn','淮南师范学院简介淮南师范学院隶属于安徽省人民政府，是一所涵盖教育学、文学、理学、工学、经济学、管理学、艺术学、法学、农学等九大学科门类的多科性本科高校。2007年6月，接受了教育部组织的本科教学工作水平评估，获得了“良好”等次。2009年3月，学校被安徽省政府确立为硕士研究生培养省级立项建设单位，2010起与安徽大学联合培养硕士研究生。学校位于安徽省淮南市，与省会合肥及新桥国际机场高速仅1小时车程，京福高铁、商杭高铁（规划中）穿城而过。学校占地面积1216亩，建筑面积48万平方米，绿化覆盖率达4',966507);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('铜陵学院',' ','安徽',1,6,0,0,0,' ','暂空',' ','http://www.tlc.edu.cn/','铜陵学院是一所以经济学、管理学为主，工学、文学、理学、法学等学科协调发展的多科性省属普通本科院校，坐落在我国青铜文化发祥地之一，素有“中国古铜都”之称的区域交通枢纽、生态山水名城——安徽省铜陵市。学校现有两个校区，校园占地面积1310亩（约87.36万平方米）。现有教职工747人，其中专任教师600余人，教授、副教授近200人，拥有博士、硕士学位人员400多人。国务院特殊津贴专家1人，全国模范教师、优秀教师3人，省级教学名师2人。全日制本科在校生14478人。学院现有馆藏纸质图书100万册，中外',1251030);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门大学',' ','福建',1,0,1,1,1,' ','暂空',' ','http://zs.xmu.edu.cn','厦门大学是由著名爱国华侨领袖陈嘉庚先生于1921年创办的，是中国近代教育史上第一所华侨创办的大学，也是我国唯一地处经济特区的国家“211工程”和“985工程”重点建设的高水平大学。88年来，学校秉承“自强不息，止于至善”的校训，积累了丰富的办学经验，形成了鲜明的办学特色，成为一所学科门类齐全、师资力量雄厚、居国内一流、在国际上有广泛影响的综合性大学。建校迄今，已先后为国家培养了15万多名本科生和研究生，在厦大学习、工作过的两院院士达60多人。学校设有研究生院和22个学院（含60个系）和10个研究',19170316);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华侨大学',' ','福建',1,12,0,0,0,' ','暂空',' ','http://www.hqu.edu.cn','学校简介华侨大学是中国著名高等学府，创办于1960年，校区分别坐落于中国著名侨乡福建省厦门市和泉州市，学校直属国务院侨务办公室领导。华侨大学是国家重点建设的综合性大学。在50多年的办学历程中，周恩来、李先念、习近平、胡锦涛、俞正声、邓颖超、贾庆林、李长春等党和国家领导人先后为学校建设与发展做出重要指示。华侨大学坚持“面向海外、面向港澳台”的办学方针，秉承“为侨服务，传播中华文化”的办学宗旨，贯彻“会通中外，并育德才”的办学理念。学校实行校长负责制，国家领导人廖承志、叶飞亲自担任首任、次任校长。现',4092112);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州大学',' ','福建',1,1,0,1,0,' ','暂空',' ','http://www.fzu.edu.cn/','福州大学是国家“211工程”重点建设大学，创建于1958年，现已发展成为一所以工为主、理工结合，理、工、经、管、文、法、艺等多学科协调发展的福建省属重点大学。2008年，福州大学盛逢50周年校庆。学校确立了走区域特色创业型强校之路的办学理念，正朝着建设我国东南强校的奋斗目标大步迈进，努力为国家和海峡西岸经济区建设作出更大的贡献！●学院设置学校现有19个以本科生培养为主的学院：数学与计算机科学学院、物理与信息学院、化学化工学院、土木工程学院、机械工程及自动化学院、材料科学与工程学院、生物科学与工程',4578540);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建工程学院',' ','福建',1,1,0,0,0,' ','暂空',' ','http://https://www.fjut.edu.cn/main.htm','福建工程学院是福建省人民政府举办的全日制普通本科高校。办学历史溯源于1896年清末著名闽绅林纾、末代帝师陈宝琛创办的“苍霞精舍”，解放前为享有盛誉的“福建高工”。福建工程学院是福建省人民政府举办的全日制普通本科高校。办学历史溯源于1896年清末著名闽绅林纾、末代帝师陈宝琛创办的“苍霞精舍”，解放前为享有盛誉的“福建高工”。办学以来，已培养了9万多名高级专门技术人才，校友遍及海内外，许多人成为建筑、机械、电子电气行业的业务骨干和管理骨干。学校以工为主，逐步形成具有“大机电、大土木”背景的学科专业布',2820709);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建农林大学',' ','福建',1,2,0,0,0,' ','暂空',' ','http://www.fafu.edu.cn','福建农林大学是农业部、国家林业局与福建省政府共建大学，是福建省重点建设的三所高水平大学之一,在培养高质量人才、开展高水平科学研究、提供高效益的社会服务等方面取得了丰硕的办学成果。【学校是全国最早创办的农林院校之一】学校创办于1936年，现已发展成为一所以农林学科为优势和特色，理、工、经、管、文、法等多学科协调发展的省属重点大学。设有23个以全日制本科生和研究生培养为主的学院，全日制在校生2.8万人，其中博、硕士研究生4800人。校园占地4800余亩，另有科教基地1178亩，教学林场4.59万亩。',2437362);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('集美大学',' ','福建',1,0,0,0,0,' ','暂空',' ','http://www.jmu.edu.cn','集美大学是福建省重点建设的八所高校之一。集美大学是一所以教学为主、以本科教育为主、以培养具有创新精神和实践能力的应用型高级专门人才为主，服务于地方经济建设与社会发展的省属多科性大学。学校面向全国招生。学校现有19个学院，19个硕士学位授予点，2个硕士专业学位点，49个本科专业，6个专科专业，专业覆盖工学、农学、经济学、管理学、教育学、理学、文学和法学等八个学科门类，形成了研究生教育、本科教育、高等职业教育、海外教育和成人教育等培养层次比较齐全的办学体系，并成功开展了中外合作办学。学校现有专任教师',5478510);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建医科大学',' ','福建',1,3,0,0,0,' ','暂空',' ','http://www.fjmu.edu.cn/','福建医科大学创建于1937年，其前身是福建省立医学专科学校。1939年改名为福建省立医学院。1949年改称福建医学院。1969年，与福建中医学院、华侨大学医疗系合并，成立福建医科大学。1982年更名为福建医学院。1996年4月改为现名。经过70多年的建设发展，学校已形成学士—硕士—博士人才培养体系和“勤奋、严谨、求实、创新”优良校风，成为一所集教学、科研、医疗、预防和社会服务为一体的省属医科大学。2003年，被福建省人民政府确定为重点建设高校。同年以良好的成绩通过了教育部本科教学工作水平评估。学',2234822);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建中医药大学',' ','福建',1,3,1,0,0,' ','暂空',' ','http://www.fjtcm.edu.cn/','福建中医学院创建于1958年，是我国创办较早的高等中医药院校，是福建省重点建设高校。办学体系完善。学校设有9个系、部，4个二级学院，2个研究院，有中医骨伤科学、中西医结合临床2个博士学位授权点，18个硕士学位授权点；有中医学、七年制中医学、针灸推拿学、中西医临床医学、临床医学、医学影像学、药学、中药学、药物制剂、制药工程、市场营销、护理学、公共事业管理、应用心理学、食品科学与工程等25个本科专业及方向。现有全日制在校生8000多人，研究生900多人。师资力量雄厚。全校现有教职医护员工2400多人',1187418);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建师范大学',' ','福建',1,4,0,0,0,' ','暂空',' ','http://www.fjnu.edu.cn/','福建师范大学是福建省人民政府与教育部共建高校，福建省重点建设的高水平大学，学校肇端于1907年清朝帝师陈宝琛先生创办的“福建优级师范学堂”，是我国建校最早的师范大学之一。经过109年的积淀、传承与创新，学校已发展成一所学科门类齐全、师资力量雄厚、办学成果丰硕、对外交流活跃的综合性大学。荣获“全国文明单位”等荣誉称号。根据中国管理科学研究院中国大学评价课题组发布的《2016中国大学评价》中的“中国大学综合实力TOP200”显示，学校位居全国高校第90位。学校坐落于素有“海滨邹鲁”之誉的福建省省会、',3296594);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('闽江学院',' ','福建',1,1,0,0,0,' ','暂空',' ','http://www.mju.edu.cn','☆福建省属公办本科大学☆半个多世纪的悠久办学历史☆占地面积2500多亩☆建筑面积48.36万平方米☆在校生规模约20000人☆毕业生就业率每年达90％以上☆研究生学历以上的教师占教师总数74.1%以上☆2011年计划面向全国31个省市招生6674人闽江学院是经国家教育部正式批准的福建省省属全日制多科性公办本科大学,拥有半个多世纪的悠久办学历史。学院座落于福建省省会福州市，占地2500多亩，校舍总建筑面积达48.36万平方米，在校生约20000人，藏书193.4万册（含电子图书48万册），教学仪器',2321742);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武夷学院',' ','福建',1,0,0,0,0,' ','暂空',' ','http://www.wuyiu.edu.cn','国家教育部批准设立的公办全日制普通本科院校，800多年前朱熹亲手创建的武夷精舍（书院）的传承者。校训：致知力行，继往开来。学校位于海峡西岸经济区绿色腹地，坐落在“世界自然与文化遗产地”——武夷山，武夷山市为国际旅游度假城市，铁路、高速公路、航空四通八达，并且还有两条高速公路、两条快速铁路正在建设中。校园面积3000亩，依山傍水，湖光山色，四季如画。80余位院士和知名专家聘为客座、讲座教授，多国文教专家在校任教。一年四季，国内外专家学者云集，为扩大学生视野、培养优质人才提供了广阔的舞台。现有国务院',1260799);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁德师范学院',' ','福建',1,4,0,0,0,' ','暂空',' ','http://www.ndsy.cn/','宁德师专的前身是建于1958年的福安师专，1962年困难时期停办。1975年秋，宁德师范学校附设大专班。1978年12月28日，经国务院批准正式成立宁德师范专科学校。1990年8月，闽东职业大学并入宁德师专。1994年3月，学校正式更名为宁德师范高等专科学校。学校获得国家教委授予的“为基础教育培养合格师资、方向明确、成绩显著”表彰和奖励，两度被中宣部、国家教委、团中央评为“社会实践先进单位”，连续五届被评为省级“文明单位”,被中央绿化委员会评为“全国绿化先进单位”。建校以来，共培养了19000多',417999);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州师范学院',' ','福建',1,4,0,0,0,' ','暂空',' ','http://www.qztc.edu.cn','泉州师范学院是经国家教育部正式批准设置的省属本科院校。学校坐落在全国首批历史文化名城、著名侨乡、国际花园城市——泉州市。现有东海、崇福和诗山三个校区。东海校区位于晋、洛二水交汇处，携江临海，极目古港沧澜；崇福校区位于国家风景名胜区清源山南麓，倚山俯城，纵览泉南风光；诗山校区南倚高盖名山，北眺鹏峰胜景。是一所立足泉州、面向福建、辐射全国，集普通教育、特殊教育为一身，融职前培养和职后培训为一体，师范教育与非师教育并举的新型现代化大学。学校占地1268亩，全日制在校生12000多人，全校教职工1120',1714589);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌大学',' ','江西',1,0,0,1,0,' ','暂空',' ','http://zjc.ncu.edu.cn/','南昌大学是一所“文理工医渗透、学研产用结合”的综合性大学，是江西省人民政府和教育部共建的国家“211工程”重点建设大学，是国家“中西部高校综合实力提升工程”和“中西部高校基础能力建设工程”入选高校。学校办学始于1921年创办的江西公立医学专门学校和1940年创建的国立中正大学。1993年，江西大学与江西工业大学合并组建南昌大学。2005年，南昌大学与江西医学院合并组建新南昌大学。经过多年的改革、建设与发展，学校已初步形成较为完整的人才培养、科技创新、社会服务和文化传承创新体系，成为助推区域和国家',7276534);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华东交通大学',' ','江西',1,1,0,0,0,' ','暂空',' ','http://www.ecjtu.edu.cn','华东交通大学是一所以工为主、以交通运输为特色的教学研究型大学。1971年，全国教育工作会议提出，将上海交通大学内燃机车等3个专业以及同济大学铁道工程专业调入上海铁道学院，并更名为华东交通大学。同年9月22日，国务院、中央军委决定将学校迁往江西。校址选在南昌双港。1978年，中央同意原上海铁道学院不再搬迁，华东交通大学继续在南昌建校，由原铁道部和江西省双重领导，以原铁道部为主。2000年，学校转制为“中央与地方共建、以地方管理为主”，为我省重点加强建设的高等院校。是博士学位授予单位。目前，学校占地',4445751);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东华理工大学',' ','江西',1,1,0,0,0,' ','暂空',' ','http://zjc.ecit.edu.cn/zs/app/index.php','东华理工大学（原华东地质学院）是国防科工委与江西省人民政府共建的一所具有地学和核科学特色，以理、工、管为主，文、法、教兼备的多科性普通高等本科院校。学校于1956年创建，先后隶属于第二机械工业部、核工业部、中国核工业总公司，现由国防科工委与江西省人民政府共建，是海军国防生培养基地。建校半个世纪以来，学校励精图治，艰苦奋斗，成为我国核工业开路先锋--核燃料循环工程专门高级人才培养的摇篮，为国家培养和输送了65000余名各级各类专门人才，为我国核军工事业、核大国地位的确立和国民经济建设作出了应有的贡',3446672);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌航空大学',' ','江西',1,1,0,0,0,' ','暂空',' ','http://zjw.nchu.edu.cn/','南昌航空大学是一所以工为主，工理文管经法教艺等学科协调发展的多科性大学。学校创建于1952年，是全国首批具有学士学位授予权单位。1985年开始培养硕士研究生，1990年获硕士学位授予权。先后隶属于航空工业部、航空航天工业部、中国航空工业总公司，1999年开始实行中央与地方共建、以地方政府管理为主的管理体制，是江西省人民政府与国家国防科技工业局共建的具有鲜明航空、国防特色的高等学校。学校位于有着“物华天宝，人杰地灵”美誉的英雄城——南昌。校园环境优美，拥有前湖校区和上海路校区2个校区，占地面积30',4256771);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西理工大学',' ','江西',1,1,0,0,0,' ','暂空',' ','http://www.jxust.cn/','江西理工大学创办于1958年，原名江西冶金学院，1988年更名为南方冶金学院，2004年更名为江西理工大学。学校曾先后隶属于冶金工业部、中国有色金属工业总公司，2013年成为江西省人民政府、工业和信息化部、教育部共建高校。学校是一所以理工为主，工学、理学、经济学、管理学、法学、文学、教育学、艺术学等八大学科协调发展，博、硕士研究生教育与本科教育并举，面向全国招生和就业并有权接收华侨及港澳台学生的教学研究型大学;是宝钢教育奖评审高校;是我国有色金属工业和钢铁工业重要的人才培养和科研基地，被誉为“有',4228163);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('景德镇陶瓷大学',' ','江西',1,1,0,0,0,' ','暂空',' ','http://www.jci.edu.cn/index.asp','景德镇陶瓷大学是我国惟一一所以陶瓷命名的多科性本科高校，是全国29所独立设置的本科艺术院校之一和首批94所有资格招收享受中国政府奖学金攻读硕士、学士学位留学生的高校之一，现已发展成为全国乃至世界陶瓷文化与陶瓷艺术交流、陶瓷人才培养和科技创新的重要基地。历史沿革：学校前身是1910年创办的中国陶业学堂;1947年设置专科建制的江西省立陶业专科学校，成为当时江西省五所专科高校之一;1958年设置普通本科建制的景德镇陶瓷学院，隶属于国家原轻工业部。1998年学校管理体制转变为中央和江西省共建，以江西省',1380799);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西农业大学',' ','江西',1,2,0,0,0,' ','暂空',' ','http://www.jxau.edu.cn/','江西农业大学是一所以农为优势，以生物技术为特色，农、理、工、经、管、文、法、教、艺多学科协调发展的多科性大学，具有博士学位授予权，是我国首批具有学士学位、硕士学位授予权单位之一，是农业部与江西省人民政府共建高校。学校位于南昌经济技术开发区，教学用地3,950亩，校园环境优美，景色宜人。学校本科教育溯源于1940年10月创办的国立中正大学，1949年更名为南昌大学，1952年组建江西农学院。1958年创办江西共产主义劳动大学总校,1968年更名为江西共产主义劳动大学。1969年江西农学院和江西共产',2445373);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('赣南医学院',' ','江西',1,3,0,0,0,' ','暂空',' ','http://www.gmu.cn/','赣南医学院坐落在江西南部、京九铁路线上的历史文化名城---赣州市内。学校创办于1941年，1958年更名为赣南医学专科学校，1988年升格为赣南医学院并开始招收本科生，1993年获学士学位授予权，2013年获硕士学位授予权。1997年顺利通过教育部普通高等学校本科教学工作合格评估，2007年以良好的成绩通过本科教学工作水平评估。目前是江西省唯一独立设置的普通高等本科医学院，中国科学院院士韩济生教授担任名誉院长。学校有黄金、章贡两个校区，占地总面积1200亩，校舍建筑面积30余万平方米；教学医疗科',1508218);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西师范大学',' ','江西',1,4,0,0,0,' ','暂空',' ','http://zs.jxnu.edu.cn/','江西师范大学是教育部、江西省人民政府共建高校和中西部高校基础能力建设工程高校。学校融文学、历史学、哲学、经济学、管理学、法学、理学、工学、教育学、艺术学等十大学科门类于一体，位于具有深厚历史文化底蕴、素有“物华天宝、人杰地灵”美誉的江西省省会南昌，对江西的政治、经济、文化和社会发展有较大影响，被江西省人民政府确定为优先发展的省属重点(师范)大学。现有瑶湖、青山湖和共青城三个校区，占地面积4500余亩，建筑面积140余万平方米，馆藏纸质文献370余万册，电子图书100余万册。瑶湖校区不设围墙，四周',3310513);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上饶师范学院',' ','江西',1,4,1,0,0,' ','暂空',' ','http://www.sru.jx.cn','上饶师范学院前身是创办于1958年8月的上饶师范专科学校，1959年更名为赣东北大学，文革停办。1977年经国务院批准恢复上饶师范专科学校。2000年3月，经教育部批准、省政府同意，升格为上饶师范学院。复校20多年来，学院为国家输送了各类毕业生26000余人。上饶师范学院地处历史文化名城?上饶市志敏大道南侧，座落于闻名中外的上饶集中营旧址南麓，紧邻浙赣铁路、横南铁路和320国道、梨温高速公路，是具有深厚文化底蕴和革命传统的赣东北地区唯一一所全日制本科院校。校园占地面积650669平方米（976亩',890695);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宜春学院',' ','江西',1,0,0,0,0,' ','暂空',' ','http://www.ycu.jx.cn/','宜春学院是江西省学科门类最全的综合性本科大学（公办）。学校创办于1958年，办学历经48年。宜春学院位于正迅速崛起的赣西中心城市??宜春市（全国第一个生态城市）。新校园依山傍水，自然风光迷人，南与浙赣线毗邻，西接320国道，交通便利，地理位置优越。我校高安校区办学历史悠久，文化底蕴丰厚，位于著名物理学家和教育家吴有训的故里?高安市，距省会南昌仅45分钟车程。宜春学院现有经济学、法学、教育学、文学、历史学、理学、工学、农学、医学、管理学等十大学科门类，设有11个专业学院，113个本、专科专业。学校',1320275);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('赣南师范大学',' ','江西',1,4,0,0,0,' ','暂空',' ','http://zs.gnnu.cn/','赣南师范学院位于江西南部、京九线上享有“南国宋城、客家摇篮、红色故都、生态家园、稀土王国、世界钨都、脐橙之乡”等美誉的赣州市，是一所校园环境优美、文化底蕴深厚、学科门类较全、办学特色鲜明、综合实力较强、发展前景良好的省属本科师范院校。教育部评估专家组评价学校“在教学质量和办学实力等方面处于同类地方高等师范院校的前列。”学校创办于1958年6月，时为赣南师范专科学校。1959年改为赣南师范学院，招收了本科生。1964年恢复为赣南师范专科学校。此后校名多次更改。1984年恢复为赣南师范学院。2003',1358133);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('井冈山大学',' ','江西',1,0,0,0,0,' ','暂空',' ','http://www.jgsu.edu.cn/','井冈山大学是一所综合性普通本科院校，是江西省人民政府和教育部共同重点支持建设高校，也是同济大学对口支援和南京军区委托培养军队干部的高校。井冈山大学创办于1958年，办学55年来，学校发展得到了党和国家领导人、江西省委省政府、教育部以及各级党政部门、社会各界的亲切关怀与大力支持。学校位于中国革命摇篮井冈山所在地——江西省吉安市中心城区。校园占地面积2554亩，校舍建筑面积57万平方米。教学科研设备总值1.66亿多元，图书馆藏书172万余册，有各类数据库35个，电子图书57万余册。学校现有全日制在校',1944414);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西财经大学',' ','江西',1,6,0,0,0,' ','暂空',' ','http://zsjy.jxufe.edu.cn/zs/','江西财经大学是一所财政部、教育部、江西省人民政府共建，以经济、管理类学科为主，法、工、文、理、艺术等学科协调发展的高等财经学府。2008年2月6日，温家宝总理与江西财大师生共度除夕，发表了重要讲话，并称赞说：“你们学校是所很好的学校”，激励着学校永葆奋斗精神，办好人民满意的教育。1、办学历史：江西财经大学的前身为1923年秋创办的江西省立商业学校,1958年成立江西财经学院，文革期间几经更名并遭停办，1978年复校，1980年成为财政部部属院校，1996年更名为江西财经大学，2000年学校管理体',5180718);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东大学',' ','山东',1,0,1,1,1,' ','暂空',' ','http://www.sdu.edu.cn/','山东大学是一所历史悠久、学科齐全、学术实力雄厚、办学特色鲜明，在国内外具有重要影响的教育部直属重点综合性大学，是国家“211工程”和“985工程”重点建设的高水平大学之一。山东大学是中国近代高等教育的起源性大学。其医学学科起源于1864年，为近代中国高等教育历史之最。其主体是1901年创办的山东大学堂，是继京师大学堂之后中国创办的第二所国立大学，也是中国第一所按章程办学的大学。从诞生起，学校先后历经了山东大学堂、国立青岛大学、国立山东大学、山东大学以及由原山东大学、山东医科大学、山东工业大学三校',11445533);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国海洋大学',' ','山东',1,0,1,1,1,' ','暂空',' ','http://www2.ouc.edu.cn/zsb','中国海洋大学是一所以海洋和水产学科为特色，包括理学、工学、农学、医(药)学、经济学、管理学、文学、法学、教育学、历史学、艺术学等学科门类较为齐全的教育部直属重点综合性大学，是国家“985工程”和“211工程”重点建设高校之一，是国务院学位委员会首批批准的具有博士、硕士、学士学位授予权的单位。校训是海纳百川，取则行远。中国海洋大学创建于1924年，历经私立青岛大学、国立青岛大学、国立山东大学、山东大学等办学时期，于1959年发展成为山东海洋学院，1960年10月被中共中央确定为全国13所重点综合大',8562525);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东科技大学',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.sdust.edu.cn/','山东科技大学建校于1951年，现已发展成为一所工科优势突出，行业特色鲜明，工学、理学、管理学、文学、法学、经济学、艺术学等多学科相互渗透、协调发展的省属重点大学。学校是山东省重点建设的应用基础型人才培养特色名校。学校在青岛、泰安、济南三地办学，总占地面积243万平方米(3640余亩)，建筑面积142万平方米，固定资产总值19.33亿元、教学仪器设备总值5.27亿元。学校设有19个学院、9个教学系(部)、4个科研机构和1个独立学院。有9个博士后科研流动站;8个博士学位授权一级学科，43个博士学位授',6793091);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国石油大学（华东）',' ','山东',1,1,1,1,0,' ','暂空',' ','http://wzqb.upc.edu.cn/s/41/t/103/main.jspy','中国石油大学是教育部直属全国重点大学，是国家“211工程”重点建设和开展“优势学科创新平台”建设并建有研究生院的高校之一。中国石油大学（华东）是教育部和五大能源企业集团公司、教育部和山东省人民政府共建的高校，是石油石化高层次人才培养的重要基地，被誉为“石油科技人才的摇篮”，现已成为一所以工为主、石油石化特色鲜明、多学科协调发展的大学。追溯学校历史，1953年新中国成立之初，国民经济建设急需石油资源，石油工业发展急需专业人才。在这种形势下，以清华大学石油工程系为基础，汇聚北京大学、天津大学、大连工',4906809);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛科技大学',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.qust.edu.cn','青岛科技大学悠久的办学历史青岛科技大学始建于1950年，历经青岛橡胶工业学校、山东化工学院、青岛化工学院，1998年由化学工业部划转到山东省，实行中央与地方共建、以地方管理为主的体制，2001年山东省青岛工艺美术学校并入，2002年更名为青岛科技大学。现已发展成为一所以工为主，理、工、文、经、管、医、法、艺等学科协调发展，以材料学、化学工程、应用化学、机械工程、自动化、信息与计算机为特色学科的多科性大学。先进的办学条件学校现有崂山校区、四方校区和高密校区三个校区，占地总面积2762亩；固定资产1',5713667);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('济南大学',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.ujn.edu.cn/','济南大学是山东省政府和教育部共建的综合性大学，是山东省重点建设大学和首批应用型人才培养特色名校，具有学士、硕士、博士学位授予权和硕士研究生免试入学推荐权。学校始建于1948年，建校66年来，学校铭记“弘毅、博学、求真、至善”的校训，传承“勤奋、严谨、团结、创新”的校风，发扬“艰苦奋斗、凝聚向上、与时俱进、追求卓越”的精神，为国家建设和经济社会发展培养了大批优秀人才，18万余名校友遍布海内外。学校现设26个学院、2个教学中心。拥有3个博士学位授权一级学科、26个博士学位授权学科（专业），20个硕士',8289391);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛理工大学',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.qtech.edu.cn','青岛理工大学是一所以工为主，理工结合，土木建筑、机械制造、环境能源学科特色鲜明，理、工、经、管、文、法、艺多学科协调发展，科学教育与人文教育相结合的多科性大学。学校前身是1952年12月创建的“山东省青岛建筑工程学校”。1953年6月由山东省划归中央人民政府重工业部领导。1960年6月升格为“山东冶金学院”，开始招收本科生。此后，学校隶属关系几经更迭，办学层次几起几落。1978年更名为“山东冶金工业学院”，恢复本科招生，隶属冶金工业部。1985年9月更名为“青岛建筑工程学院”。1993年被国务院',8174450);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东建筑大学',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.sdjzu.edu.cn','山东建筑大学始建于1956年，是山东省人民政府与住房和城乡建设部共建高校。学校秉承“厚德博学、筑基建业”的校训，弘扬“勤奋、严谨、团结、创新”的校风，强化内涵建设，凝练办学特色，已发展成为一所以工科为主，以土木建筑为特色，多学科交叉渗透、协调发展的多科性大学。是山东省应用型人才培养特色名校、服务国家特殊需求“绿色建筑技术及其理论”博士人才培养高校、山东省高校协同创新中心首批立项建设单位。学校位于济南市临港开发区，占地面积2000余亩，校舍面积64余万平方米。目前，学校设有17个学院(部)和2个研',5989233);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东理工大学',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.sdut.edu.cn','山东理工大学是山东省属重点大学，创建于1956年，是国务院批准的首批具有学士学位授予权的高校之一。学校坐落在历史悠久的齐文化发祥地——山东省淄博市。山东理工大学以提供优质教育资源，培养社会需要之优秀人才为根本使命。学校坚持教学中心地位，在全国率先实行了按高考文理科类招生为特征的招生制度改革和以张扬学生个性、凸显学生自主选择为特征的完全学分制改革，构建了“知识、能力、素质”三位一体的人才培养体系，致力于培养基础厚、能力强、素质高、具有创新精神的应用型高级专门人才。学生在这里可以修读多学科的优质课程',6654577);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东农业大学',' ','山东',1,2,0,0,0,' ','暂空',' ','http://zhaosheng.sdau.edu.cn','山东农业大学坐落在雄伟壮丽的泰山脚下，前身是1906年创办于济南的山东高等农业学堂。后几经变迁，1952年经全国院系调整，成立山东农学院。1958年由济南迁至泰安，1983年更名为山东农业大学。1999年7月，原山东农业大学、山东水利专科学校合并，同时山东省林业学校并入，组建新的山东农业大学。目前，学校已经发展成为一所以农业科学为优势，生命科学为特色，融农、理、工、管、经、文、法、艺术学等于一体的多科性大学。学校是农业部和山东省人民政府共建高校，国家林业局和山东省人民政府共建高校，是教育部、农业',4325194);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛农业大学',' ','山东',1,2,0,0,0,' ','暂空',' ','http://www.qau.edu.cn/','一、历史沿革与现状青岛农业大学的前身是莱阳农学院，始建于1951年，2001年经山东省政府批准创建青岛校区，2007年3月经教育部批准更名为“青岛农业大学”，办学地址由莱阳市变更为青岛市城阳区。建校以来，学校已为社会培养全日制毕业生80000余名，中共中央政治局委员孙政才、玉米育种专家李登海是校友中的杰出代表。学校在长期的办学历程中，形成了“矢志三农、勤奋求实，自强不息、追求卓越，培养高素质应用型人才”的鲜明办学特色。学校在1997年原国家教委组织的本科教学工作合格评估和2007年教育部组织的本',4353786);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潍坊医学院',' ','山东',1,3,0,0,0,' ','暂空',' ','http://www.wfmc.edu.cn','潍坊医学院是山东省省属全日制普通高等医学院校，坐落于山东半岛中部闻名遐迩的世界风筝都—潍坊市。学校历史溯源于19世纪末20世纪初潍县乐道院设立的乐道院医护学校和潍县美国长老会医院。1951年，山东省人民政府卫生厅根据华东军政委员会卫生部的命令，指示昌潍区中心卫生院在潍坊筹建山东省昌潍医士学校。1958年，改建为昌潍医学院，1987年，更名为现名。学校1958年开始本科教育，1986年获硕士学位授予权，1998年获留学生招生资格，2008年，学校以优秀成绩通过教育部本科教学工作水平评估，人才培养质',2304034);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泰山医学院',' ','山东',1,3,0,0,0,' ','暂空',' ','http://www.tsmc.edu.cn','泰山医学院的前身是山东医学院楼德分院，自1974年起独立办学，1980年由楼德迁至泰安，1981年经国务院和山东省人民政府批准，定名为“泰山医学院”。1998年经国务院学位委员会批准，成为硕士学位授予权单位。2004年获得同等学力人员申请硕士学位授予权。多年来，学校以服务经济社会发展为己任，继承传统，艰苦创业，教学、医疗、科研、管理等各个方面均取得了显著进步。“十五”期间，学校紧紧抓住高等教育结构调整、快速发展的战略机遇，果断决策，乘势而上。2002年征地近3000亩建设新校区，该项目被列为山东',2214851);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('滨州医学院',' ','山东',1,3,0,0,0,' ','暂空',' ','http://www.bzmc.edu.cn/','滨州医学院是山东省省属普通高等医学院校，山东省应用型人才培养特色名校。60余年来，几代滨医人艰苦奋斗，求真务实，开拓创新，学校各项事业获得长足进步，已发展成为一所医学学科齐全，办学条件完善，办学特色鲜明，教育质量和办学水平不断提高的省属本科医学院校。历史沿革滨州医学院是山东省省属普通高等医学院校，前身是始建于1946年的原国立山东大学医学院，1956年3月独立建制为青岛医学院，1970年10月整体搬迁至山东省惠民地区行署驻地北镇办学，1974年11月设立青岛医学院北镇分院(为省属本科高等院校)，',2182728);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东中医药大学',' ','山东',1,3,0,0,0,' ','暂空',' ','http://www.sdutcm.edu.cn ','山东中医药大学创建于1958年，1978年被确定为全国重点建设的中医院校，1981年成为山东省重点高校，是教育部本科教学工作水平评估优秀学校、山东省首批五所应用基础型人才培养特色名校之一，山东省首批高等学校协同创新中心。学校在省属高校中拥有国家级重点学科最多，最早获得硕士、博士学位授权，最早设立博士后科研流动站，最早成为国家“973”项目首席承担单位。学校秉承“厚德怀仁，博学笃行”的校训，发挥中医药优势，狠抓内涵建设，逐步形成“以文化人、厚重基础、注重传承、勇于创新”办学特色，为国家和社会培养了',2752381);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东医学高等专科学校',' ','山东',0,3,0,0,0,' ','暂空',' ','http://www.sdmc.edu.cn','山东医学高等专科学校是一所省属全日制普通高等学校，设临沂、济南两个校区，校本部设在临沂。学校现任党委书记伊廷夫，研究员；校长王兴武，教授。学校本部座落在风景秀丽的历史文化名城临沂市，济南校区座落在风景秀丽的泉城济南南郊万灵山北麓，交通便利，办学环境优雅。学校占地1928亩，建筑总面积32.06万平方米；固定资产总值4.26亿元，其中教学科研仪器设备总值6360.93万元；图书馆藏书76.7万册；学校具有完善的教学设施设备，建有教学楼、实验楼、图书馆、标准化学生公寓、标准化学生食堂等基础设施，拥有',729000);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('济宁医学院',' ','山东',1,3,0,0,0,' ','暂空',' ','http://www.jnmc.edu.cn/','济宁医学院创建于1952年，是山东省省属普通本科高等医科院校。学校现有济宁和日照两个校区，总占地面积124万余平方米，建筑面积67万余平方米。学校固定资产总值17.3亿元，教学科研仪器设备总值1.44亿元，馆藏图书总量为301万册。2008年，学校以优秀成绩顺利通过教育部本科教学工作水平评估；2015年，以有效期6年的优异成绩通过教育部临床医学专业认证。学校设有临床医学院等16个院（系、部），开设临床医学、精神医学、口腔医学、预防医学、法医学等40余个专业和专业方向，有临床医学一级学科专业硕士学',1890725);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('菏泽医学专科学校',' ','山东',0,3,0,0,0,' ','暂空',' ','http://www.hzmc.edu.cn','办学历史悠久菏泽医学专科学校是一所省属、公办、全日制普通高等医学专科学校，始建于1950年。1978年1月经国务院批准，定名为菏泽医学专科学校，历史悠久，积淀深厚。目前全日制在校生13524人。校园环境优美学校坐落于驰名中外的中国牡丹之都——菏泽市。校区与历史名园---古今园、百花园毗邻，国色天香的牡丹花卉与源远流长的牡丹文化为学校发展奠定了丰厚的底蕴。校园景色秀丽，环境优美，交通便利。教学条件完善学校占地70.81万平方米，校舍建筑面积33.6万平方米。教学仪器设备总值9817.36万元，图书',559246);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东师范大学',' ','山东',1,4,0,0,0,' ','暂空',' ','http://www.sdnu.edu.cn/','山东师范大学概况(数据更新时间：2015年12月)山东师范大学坐落在历史文化名城济南。建校65年来，学校植根齐鲁文化沃土，汲取泉城人文灵韵，秉承“尊贤尚功、奋发有为”的校园精神和“弘德明志、博学笃行”的校训，自觉传承创新齐鲁文化，努力彰显教师教育特色，目前已发展成为一所学科专业齐全、学位体系完备、师资人才充沛、社会声誉优良的综合性高等师范院校。办学历史沿革学校办学历史可追溯到1902年山东大学堂内设的师范馆。1950年10月，在原华东大学教育学院和山东省行政干校的基础上组建山东师范学院，系新中国',4312599);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('曲阜师范大学',' ','山东',1,4,0,0,0,' ','暂空',' ','http://www.qfnu.edu.cn','曲阜师范大学1955年创建于济南，始称山东师范专科学校。1956年5月，被教育部批准升格为曲阜师范学院，同年9月迁址曲阜，开启了兴办本科教育的历程。1970年9月至1974年4月，与山东大学文科合并成为新的山东大学。1974年4月恢复曲阜师范学院建制。1981年，被山东省人民政府确立为重点建设的六所高校之一;同年，被批准为全国首批招收研究生的高校。1982年，取得硕士学位授予权。1985年11月，学校更名为曲阜师范大学。2002年，建设日照校区。2003年，取得博士学位授予权。2004年，获得教',3987887);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('聊城大学',' ','山东',1,0,0,0,0,' ','暂空',' ','http://zsb.lcu.edu.cn','聊城大学是山东省属综合性大学，坐落在有着“江北水城”美称的国家级历史文化名城——山东省聊城市。学校始建于1974年，原名山东师范学院聊城分院，1981年经国务院批准改为聊城师范学院，2002年经教育部批准更名为聊城大学。现任校长马春林。学校占地近3000亩，校舍面积75万余平方米，教学科研仪器设备总值2.9亿元，馆藏纸质图书270万册，拥有电子图书资料28808GB。建有现代化的教学楼、实验楼、图书馆、网络中心、体育场馆等设施。校园环境优雅，湖光山色，四季常青，享有\"学在聊大\"的美誉，是读书治学',4675469);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('德州学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.dzu.edu.cn/','德州学院是教育部批准建立的一所全日制综合性普通本科院校。建校40多年来，学校植根齐鲁文化与燕赵文化之沃土，汲取黄河文化与运河文化之灵韵，秉承艰苦创业与励志育人之精神，不断谱写教书育人、创新发展的新篇章。历史沿革学校始建于1971年，历经德州师范专科学校、德州高等专科学校、德州学院三个时期。1971年5月，德州师范专科学校在“文革”中诞生，经历了启程的步履维艰，在规范和超越之中不断发展壮大。1997年3月，德州师范专科学校、德州教育学院、德州市直业余大学三校合并办学，组建德州高等专科学校，开启了综',2223755);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('滨州学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.bzu.edu.cn','滨州学院是经国家教育部批准成立的一所综合性普通本科院校，坐落在“兵圣”孙子的故里，美丽富饶的黄河三角洲腹地——滨州市。学校前身是创建于1958年的北镇师范专科学校，1983年，更名为滨州师范专科学校。2000年，滨州教育学院、滨州广播电视大学并入滨州师范专科学校。2001年9月30日，滨州市委、市政府决定筹建滨州学院。2004年5月17日，教育部批准滨州师范专科学校改建为本科院校——滨州学院。学校占地2436亩，校园布局合理，设施齐全，河湖环绕，环境优雅。校舍建筑面积近52万平方米，资产总值11',1883912);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鲁东大学',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.ldu.edu.cn','鲁东大学坐落在“中国最佳魅力城市”——烟台，是一所以文理工为主体、多学科协调发展的省重点建设高校。2012年，成为首批服务国家特殊需求博士人才培养项目单位、山东省应用型人才培养特色名校。原文化部部长、著名作家王蒙评价鲁东大学“人杰校灵”。【文化传承】学校于1930年始建于具有悠久历史文化的千年古县莱阳，历经山东省立第二乡村师范学校、胶东公学、莱阳师范学校、莱阳师范专科学校、烟台师范专科学校、烟台教师进修学校、烟台师范学院等历史阶段。2001年，原直属山东省交通厅、建校于1978年的山东省交通学校',5141153);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('临沂大学',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.lyu.edu.cn','临沂大学是国家设立、山东省政府直属管理的综合性大学，“山东省特色名校工程”重点建设大学。学校坐落在风景秀美、人文荟萃的历史文化名城、商贸物流之都、滨水生态之城、红色旅游城市、全国文明城市山东省临沂市。前身是1941年由中共滨海区委和抗大一分校共同创建的滨海建国学院。在70多年的办学历程中，学校始终秉承“实”的校风和“明义、锐思、弘毅、致远”的校训，不断解放思想，开拓创新，实现了超常规跨越式发展，培养毕业生25万人。学校现设有19个二级学院、9个科研院所、2个分校和2个附属中学，本科专业77个，涵',4459717);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泰山学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.tsu.edu.cn','泰山学院是经教育部批准设立的综合性普通本科院校，高等教育办学历史已达57年。学校所在的泰安市是中外闻名的国家历史文化名城，位于名扬海内外的山水圣人旅游区的中心，京福、京沪高速公路在此交汇，104国道纵贯南北，京沪铁路和京沪高速铁路在此经过，区位与交通优势得天独厚。校园坐落在世界首例自然与文化双重遗产、世界地质公园——泰山南麓，毗邻省级旅游度假区——天平湖，建有以党和国家主要领导人的姓名命名并设在高校的第一座图书馆——万里图书馆。校园建筑错落有致，是读书治学的理想之地。学校占地1445亩，建筑面积',2023382);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('济宁学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.jnxy.edu.cn/','济宁学院是2007年3月经教育部批准设立的一所省属全日制普通本科高等学校，办学溯源于1951年，前身为济宁师范专科学校。2008年11月，创建于1905年的百年名校曲阜师范学校整建制并入。校园坐落在我国古代伟大的思想家、教育家、至圣先师孔子故里，儒家文化的发源地，世界历史文化名城曲阜。学校秉承“博学笃志、择善敦行”的校训，以“至诚至朴、求是求新”的校风、“诲人不倦、敦品励学”的教风和“学而不厌、切问近思”的学风，传播文明，探求新知，培育英才，先后为国家和社会培养了10万多名优秀人才，为地方经济社',1208331);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('菏泽学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.hezeu.edu.cn/','菏泽学院位于山东省菏泽市，是2004年教育部批准设立的综合性本科院校。菏泽学院建校于1949年，是在冀鲁豫区菏泽师范学校的基础上发展起来的，1958年改建为菏泽师范专科学校，至今已有60年的办学历史。学校分为北校区、东校区、西校区三个教学生活区，占地面积1414亩，建筑面积近49万平方米。各校区花木繁茂，碧草如茵，环境优雅，景色宜人，是读书治学的理想园地。学校建有近代物理、分子生物学、网络技术中心、分析测试中心、运动生理等46个实验室，教学科研仪器设备值6042万元。建有2.67万平方米的图书馆',1433755);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东体育学院',' ','山东',1,7,0,0,0,' ','暂空',' ','http://www.sdpei.edu.cn','山东体育学院是山东省唯一一所普通高等体育院校，是国家公办、全日制、以体育学科为主，理、工、文、经相结合的多学科大学，是全省体育教育、运动训练和体育科研的中心，担负着发展全省体育教育，搞好全省竞技体育训练与竞赛和体育科技攻关服务的重任。目前，我院拥有东校区(济南市文化东路65号)、西校区(济南市文化西路2号、10号)、济南新校区和日照校区(日照市山东东路)4个校区，总占地面积1870亩，教学行政用房总面积19.14万平方米，多媒体教室、计算机教室、校园宽带网等基本设施齐全。拥有室内体育馆34个，室',397834);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东艺术学院',' ','山东',1,8,0,0,0,' ','暂空',' ','http://www.sdca.edu.cn','山东艺术学院始建于1958年，是全国31所独立设置本科艺术院校之一，是山东省开办艺术教育最早的综合性高等艺术院校，是山东省人才培养特色名校立项建设单位。经过五十多年的建设和发展，已形成文化底蕴深厚，学科专业齐全，办学层次高，办学条件优越的艺术学府，在山东艺术教育与人才培养中发挥着重要作用。学校现有文东和长清两个校区，占地1313亩。现有全日制在校本专科生8000余人，另有研究生630余人。学校现在形成了含中专、普通本专科、研究生、艺术硕士(MFA)、成人教育在内的多层次办学格局，涉及艺术学、文学',507285);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州大学',' ','河南',1,0,0,1,0,' ','暂空',' ','http://www.zzu.edu.cn','郑州大学(简称郑大，英文简称：ZZU)是国家“211工程”重点建设高校、国家“中西部高校提升综合实力计划”入选高校、河南省人民政府与教育部共建高校。学校总占地面积5700余亩，校本部四个校区分别是：主校区(郑州市科学大道100号)、南校区(郑州市大学北路75号)、北校区(郑州市文化路97号)和东校区(郑州市大学北路40号)。学校面向全国招生，现有全日制普通本科生5.6万人、各类在校研究生1.5万人，以及来自60余个国家和地区的留学生1600余人。郑州大学医科教育源于1928年的国立第五中山大学(',9843392);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南理工大学',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.hpu.edu.cn/','●中央与地方共建、以地方管理为主的河南省属重点高校●中央与地方共建、以地方管理为主的河南省属重点高校●国家“中西部高校基础能力建设工程”重点建设高校●河南省人民政府与国家安全生产监督管理总局共建高校●国家教育部高等学校本科教学工作水平评估优秀学校河南理工大学始建于1909年，历经焦作路矿学堂、福中矿务大学、私立焦作工学院、西北工学院、国立焦作工学院、焦作矿业学院和焦作工学院等重要历史时期，2004年更名河南理工大学,是我国第一所矿业高等学府和河南省建立最早的高等学校。学校现已发展成为一所具有博士',6258067);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州轻工业学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.zzuli.edu.cn','郑州轻工业学院（ZhengzhouUniversityofLightIndustry）位于河南省会郑州市，是河南省重点建设高校。建校以来，学校秉承“为之则易、不为则难”的校训，抓住国家促进高等教育发展的历史机遇，不断开拓创新、砥砺奋进，经过三十多年的发展，已经成为一所以工为主，工、理、文、艺、经、管、法、教、农等多学科协调发展的高等学府。历史沿革学校从1977年建校至上世纪末，为中央部属本科院校；1998年转为中央和河南省共建，以河南省管理为主；2011年，河南省人民政府和国家烟草专卖局签约共建',4163469);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南工业大学',' ','河南',1,1,0,0,0,' ','暂空',' ','http://zs.haut.edu.cn/','河南工业大学(HenanUniversityofTechnology)位于河南省会郑州市，是河南省重点建设高校。建校以来，学校秉承“明德、求是、拓新、笃行”的校训，大力弘扬“崇尚科学、勇于探索、报国兴学、自强不息”的工大精神，坚持“育人为本、质量立校、特色发展”，在人才培养、科学研究、社会服务和文化传承创新等方面作出了重要贡献。1.历史沿革从1956年建校至上世纪末，为中央部属院校;1959年开展本科教育，1981年开始研究生教育;1998年划归河南省管理;2010年，河南省人民政府和国家粮食局',4240388);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南科技大学',' ','河南',1,0,0,0,0,' ','暂空',' ','http://www.haust.edu.cn','河南科技大学(简称河科大，英文简称：HAUST)是河南省人民政府与国家国防科工局共建高校、国家中西部高校基础能力建设工程支持高校、国家教育部本科教学工作水平评估优秀高校，是河南省重点建设的三所综合性大学之一。学校总占地面积4600多亩，现有开元、西苑、景华和周山4个校区，其中新校区开元校区占地面积3660余亩。学校面向全国招生，现有全日制普通本科生、研究生及留学生39100多人，非全日制成人教育生22900多人。学校坐落于“千年帝都，牡丹花城”、中原经济区副中心城市——洛阳。学校前身是创建于19',6542112);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中原工学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.zzti.edu.cn/','中原工学院原名郑州纺织工学院，是面向全国招生的普通本科院校。学校始建于1955年，原隶属国家纺织工业部，2000年划转河南省管理。学校占地1,610亩，分为南区、北区和西区，其中主校区南区毗邻郑州航空港经济综合实验区，占地1,390亩。现有教职工1,703人，其中专任教师990人(高级职称402人，博士275人);有全日制本科生19,878人、硕士研究生534人。建校以来，学校为全国输送各类毕业生8万余人。经过六十多年的建设，学校已发展成为以工为主，纺织服装及相关学科特色鲜明，工、管、文、理、经',3289100);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南农业大学',' ','河南',1,2,0,0,0,' ','暂空',' ','http://www.henau.edu.cn/','筚路蓝缕，栉风沐雨，河南农业大学走过了百余年的办学历程。学校源自1902年创办的河南大学堂，先后经历了河南高等学堂、河南高等学校、河南公立农业专门学校、国立第五中山大学农科、河南大学农学院等办学阶段。1952年全国院系调整时重新独立建制，更名为河南农学院。1984年12月更名为河南农业大学。2009年9月成为农业部与地方省政府共建的第一所省属农业高校。2012年11月成为国家林业局与省政府共建高校。2013年5月学校牵头的河南粮食作物协同创新中心入选国家首批“2011计划”。学校下设20个学院，',3073291);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南科技学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.hist.edu.cn ','河南科技学院是一所省属普通本科院校。学校始建于1939年，前身为中国共产党早期创建的延安自然科学院大学部生物系，历经北方大学农学院、华北大学农学院、华北大学农学院长治分院、北京农业大学长治分校、平原农学院、百泉农业专科学校、河南职业技术师范学院等时期。2004年5月，经教育部批准，更名为河南科技学院。学校地处中原名城新乡市，占地面积2478亩，校舍面积62万平方米。图书馆各类文献资源总量达278万册，中外文期刊1500余种。学校是国家“2011”计划协同创新单位，建有河南省协同创新中心。有25个',2507079);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南牧业经济学院',' ','河南',1,0,0,0,0,' ','暂空',' ','http://www.hnuahe.edu.cn','河南牧业经济学院是在原郑州牧业工程高等专科学校和原河南商业高等专科学校的基础上合并组建的一所省属公办全日制普通本科院校，是河南省本科转型发展试点学校。学校坐落于九州之中、中华腹地的河南省省会郑州，毗邻中华民族的母亲河——黄河。学校占地面积2112亩，现有全日制在校生三万余人。设有动物科技学院、动物医学院、食品工程学院、工商管理学院、会计学院等20个教学院(系)、5个教学部、1个示范性软件职业技术学院、1个继续教育学院和1个国际教育学院。学科专业设置涵盖农学、工学、管理学、经济学、理学、文学、法学',360719);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南中医药大学',' ','河南',1,3,0,0,0,' ','暂空',' ','http://www.hactcm.edu.cn','第二章学校概况第五条学校全称：河南中医药大学，国标代码10471。河南中医药大学创建于1958年，是教育部批准的具有高等学历教育招生资格并有留学生、华侨、港澳台学生招生资格的普通本科公办高等学校，是全国建校较早的高等中医药院校之一，其前身是开办于1955年的河南省中医进修学校。学校位于省会郑州，是河南省人民政府和国家中医药管理局共建高校、国家中西部高校基础能力建设工程建设规划学校、“国家中西部高等教育振兴计划”高校、本科教学水平评估优秀高校、博士学位授权单位、教育部中国政府奖学金生培养高校、省级',2253450);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新乡医学院',' ','河南',1,3,0,0,0,' ','暂空',' ','http://www.xxmu.edu.cn/','新乡医学院位于河南省新乡市，是目前河南省唯一独立建制的省属西医本科院校。学校渊源可追溯到1896年英国人劳海德在古城卫辉开办的西医诊所，医学教育肇始于1922年惠民医院护士学校，招收四年制护理专业学生并由中华护理学会(1923年名为“中华护士会”)颁发文凭。1949年冀鲁豫卫生学校及哈励逊医院迁入，于1950年成立平原省医科学校。学校经历华北第二医士学校、河南省汲县医士学校、新乡专区医学院等发展阶段，1962年更名为豫北医学专科学校;1982年升格本科，定名新乡医学院;1998年获得硕士学位授予',2375877);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南大学',' ','河南',1,0,0,0,0,' ','暂空',' ','http://zs.henu.edu.cn','河南大学坐落在历史文化名城、八朝古都开封。这里曾是河南贡院的所在地，1903、1904年最后两场全国会试在这里举行，上千年的科举制度在这里划上句号。1912年，以林伯襄为代表的一批河南仁人先贤，在欧风美雨和辛亥革命胜利的曙光中创办了河南留学欧美预备学校，成为当时中国的三大留学培训基地之一。后历经中州大学、国立第五中山大学、省立河南大学等阶段，1942年改为国立河南大学，成为拥有文、理、工、农、医、法等6大学院的综合性大学，是当时学术实力雄厚、享誉国内外的国立大学之一。新中国成立后，经院系调整，河',6522835);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南师范大学',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.henannu.edu.cn/','河南师范大学是一所建校历史较长的省属重点大学。学校北依巍巍太行，南滨滚滚黄河，位于京广、太荷铁路交汇处的豫北名城新乡市，坐落在广袤的牧野大地、美丽的卫水之滨。河南师范大学的前身是创建于1923年的中州大学(原国立河南大学前身)理科，1953年与平原师范学院合并，改称河南师范学院，后更名为新乡师范学院，1985年始称河南师范大学。2007年，被教育部确定为本科教学工作水平评估优秀学校。2012年，入选国家中西部高等教育振兴计划支持高校。2015年，成为河南省第三所省政府与教育部共建高校，为学校再上',3519396);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('信阳师范学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://210.43.24.25/zsjy/','信阳师范学院所在地——信阳市，位于河南省南部，东邻安徽，南接湖北，是江淮河汉间的战略要地，鄂豫皖区域性中心城市。山水秀丽，气候宜人，素有“北国江南，江南北国”之美誉，是中国著名的宜居之城、旅游之城、创业之城。学校坐落在信阳市贤山北麓、浉水之滨，西临“长淮明珠”南湾湖，南望“云中公园”鸡公山，校园四季有花，四季有绿，郁郁葱葱，高低错落，青山绿水，诗情画意，被誉为“河南省最美的校园”、“中国最美的大学之一”。信阳师范学院创建于1975年，原名为开封师范学院(今河南大学)信阳分院。1978年经国务院批',2383157);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('周口师范学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.zknu.edu.cn','周口师范学院是河南省属普通本科高校，地处三皇故都、老子故里、陈楚旧地、历史文化名城周口市。周口历史悠久，文化灿烂，人杰地灵，素有“华夏先驱，九州圣迹”之誉。颍河、沙河、贾鲁河在此交汇，不尽东流;漯阜铁路连线京九和陇海线，横穿周口;宁洛高速、大广高速等数条高速在此交汇，是连南贯北、承东启西的重要交通枢纽。校园内花木交荫，绿围翠绕，鸟语花香，四季如春，风光旖旎，景色宜人。吮千年文化滋养，依厚重历史沉淀。始建于1973年的周口师范学院，栉风沐雨，由小到大，由弱变强，筚路蓝缕，艰苦创业，砥砺前行、激扬奋',1184285);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安阳师范学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.aynu.edu.cn/','安阳师范学院位于世界文化遗产——殷墟所在地、国家历史文化名城、中国八大古都之一的安阳市，是河南省属普通本科高校。学校前身是始建于1908年的彰德府安阳师范传习所，1949年被命名为平原省立安阳师范学校，1958年更名为河南省安阳师范专科学校，1960年改名为安阳第一师范学校，1978年恢复为安阳师范专科学校，1992年更名为安阳师范高等专科学校。2000年安阳师范高等专科学校、安阳教育学院、安阳市第二师范学校合并组建安阳师范学院。2011年被国务院学位办批准成为硕士专业学位研究生培养试点单位。学',1868019);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('许昌学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.xcu.edu.cn','许昌学院是一所省属全日制普通本科院校。学校坐落于汉魏故都——河南省许昌市。许昌地处中原腹地，既是一座历史悠久的文化名城，又是一座蓬勃发展的现代新城。许昌三国文化底蕴深厚，钧瓷艺术独具魅力，地理位置优越，经济发展迅速，城市环境优美，先后获得国家文明城、国家卫生城、国家森林城、国家园林城、国家优秀旅游城等称号。许昌学院前身是创建于1942年8月的河南省第五行政区联立师范学校。1946年7月，河南省立许昌师范学校成立，五区联师并入许昌师范学校。新中国成立后学校健康发展，1959年经国务院批准为师范专科',1325070);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南阳师范学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.nynu.edu.cn','南阳师范学院是河南省省属全日制普通本科高等院校，办学历史可追溯到1907年，有着深厚的文化底蕴和优良的办学传统，已形成了师范教育和非师范教育并存、特色鲜明、优势互补、协调发展的办学格局。2007年以优异成绩通过教育部本科教学工作水平评估。2011年获批为硕士专业学位研究生培养单位，2012年开始招收研究生，在办学层次上实现重大跨越。学校位于历史文化名城南阳市。南阳历史悠久，文化灿烂，是楚汉文化和三国文化的集中地，也是科圣张衡和医圣张仲景的故里。校区坐落于南阳卧龙岗上，紧邻驰名中外的诸葛武侯祠景区',1625860);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('洛阳师范学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.lynu.edu.cn','洛阳师范学院是一所省属普通高等师范本科院校，位于千年帝都、牡丹花城——洛阳，文脉传承，底蕴丰厚。学校北依洛河，碧水东流，杨柳堆烟，流传着洛神宓妃的美丽传说;西望关林和世界文化遗产龙门石窟，泱泱万佛，蜚誉寰中，绽放着世界文化遗产的璀璨光芒。洛阳师范学院的前身是始建于1916年的河洛师范学校，历经河南省立河洛师范学校、河南省立第四师范学校、河南省立洛阳师范学校、洛阳师范专科学校、洛阳师范高等专科学校诸阶段，2000年升格为本科院校，定为现名。2007年，学校以优异成绩通过教育部本科教学工作水平评估，',2467400);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('商丘师范学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.sqnc.edu.cn/','商丘师范学院坐落于国家级历史文化名城、三商之源、华商之都——河南省商丘市，北依风景优美的黄河故道国家森林公园;南望历史上人文荟萃的归德古城和应天书院;地处京九铁路、陇海铁路，310、105国道、济广和连霍高速公路交汇的黄金交通枢纽之地。商丘师范学院的历史可以追溯到1905年创办的“归德府中学堂”，几经分合，历经商丘师范专科学校、商丘师范高等专科学校等阶段，2000年经教育部批准升格为商丘师范学院。学校环境优美，校园内有春明湖、夏梦湖、秋韵湖、冬泰湖和文秀山，亭台水榭、荷花垂柳、碧波荡漾、湖光山色',2204614);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南财经政法大学',' ','河南',1,6,0,0,0,' ','暂空',' ','http://zs.huel.edu.cn','河南财经政法大学位于河南省郑州市，地处中华腹地，九州通衢，北邻黄河，西依嵩山。学校由原河南财经学院和原河南省政法管理干部学院于2010年3月合并组建而成，是省属公办全日制普通高等学校，是河南省重点支持建设的骨干高校之一，是河南省博士学位授予权立项建设单位。基础设施日益完善学校现有文北校区、文南校区和郑东校区3个校区，占地面积2000多亩，总建筑面积107万余平方米。学校建有各类教学实验室，图书馆藏书260多万册，教学、科研、体育场等基础设施齐全。校园四季环境幽雅、花木繁茂、碧草如茵、景色宜人，是',5373329);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州航空工业管理学院',' ','河南',1,6,0,0,0,' ','暂空',' ','http://www.zzia.edu.cn/','郑州航空工业管理学院是一所全日制普通本科院校，其前身是1949年成立的平原省财经学校，后几经更名和迁址，1964年在全国16所航空学校专业调整合并时，更名为郑州航空工业学校。1978年升格为郑州航空工业管理专科学校，1984年升格为郑州航空工业管理学院(本科)，1989年获得学士单位授予权。1999年，学校隶属关系发生转变，由中国航空工业总公司主管转变为中央与地方共建，日常管理以河南省为主的办学体制。2009年，顺利通过硕士单位立项建设评审，2013年，国务院学位委员会第三十次会议审批通过我校新',3795779);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉大学',' ','湖北',1,0,1,1,1,' ','暂空',' ','http://www.whu.edu.cn/','江城多山，珞珈独秀；山上有黉，武汉大学。武汉大学是国家教育部直属重点综合性大学，是国家“985工程”和“211工程”重点建设高校。武汉大学溯源于1893年清末湖广总督张之洞奏请清政府创办的自强学堂，历经传承演变，1928年定名为国立武汉大学，是近代中国第一批国立大学。1946年，学校已形成文、法、理、工、农、医6大学院并驾齐驱的办学格局。新中国成立后，武汉大学受到党和政府的高度重视。1958年，毛泽东主席亲临武大视察。1993年，武汉大学百年校庆之际，江泽民等党和国家领导人题词祝贺。改革开放以来',16327241);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华中科技大学',' ','湖北',1,1,1,1,1,' ','暂空',' ','http://www.hust.edu.cn/','华中科技大学是国家教育部直属的全国重点大学，由原华中理工大学、同济医科大学、武汉城市建设学院于2000年5月26日合并成立，是首批列入国家“211工程”重点建设和国家“985工程”建设高校之一。学校校园占地7000余亩，园内树木葱茏，碧草如茵，环境优雅，景色秀丽，绿化覆盖率72%，被誉为“森林式大学”。学校教学支撑体系完备，各项公共服务设施齐全。学校学科齐全、结构合理，基本构建起研究型大学的学科体系。拥有哲学、经济学、法学、教育学、文学、历史学、理学、工学、农学、医学、管理学、艺术学等12大学科',9395139);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉科技大学',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.wust.edu.cn/default.html','武汉科技大学是一所湖北省人民政府、国家教育部和六家国家特大型企业共建的地方高水平大学，是国家“中西部高校基础能力建设工程”入选高校。学校办学历史溯源于1898年清末湖广总督张之洞奏请清朝政府批准成立的工艺学堂，是湖北省办学历史最为悠久的高校之一。学校校园总面积170.93万平方米，校舍建筑面积104.15万平方米。学校依湖览江、风景优美，教学设施齐备，办学条件完善，是湖北省“生态园林式学校”、“全国绿化模范单位”。学校学科门类齐全、特色鲜明，构建了以工为主、理工结合，工、理、管、医、文、经、法、',4164256);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长江大学',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.yangtzeu.edu.cn/','长江大学是2003年4月经国家教育部批准，由原江汉石油学院、湖北农学院、荆州师范学院、湖北省卫生职工医学院合并组建而成，是湖北省属高校中规模最大、学科门类较全的综合性大学，为湖北省重点建设的骨干高校，是国家“中西部高校基础能力建设工程”入选高校，也是湖北省人民政府与中国石油天然气集团公司、中国石油化工集团公司、中国海洋石油总公司共建和湖北省人民政府与国家农业部共建的高校。学校本部位于长江中游的历史文化名城—-荆州市，并建有武汉校区。荆州有着悠久的历史和灿烂的文化，是楚文化的发祥地和三国文化的集中',5128652);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉工程大学',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.wit.edu.cn/','历史沿革武汉工程大学创建于1972年6月，原名湖北化工石油学院，隶属湖北省。1980年3月，经教育部批准，更名为武汉化工学院，改由原化工部主管。1998年7月，随着高校管理体制的调整，学校划转到湖北省管理，实行中央与地方共建，以湖北省管理为主。同年，学校通过教育部本科教学合格评价，并获得硕士学位授予权。2006年2月，经教育部同意、湖北省人民政府批准，正式更名为武汉工程大学。2006年以优秀的成绩通过教育部对我校本科教学工作水平的评估。2012年学校入选中西部高校基础能力建设工程(小211工程)',4396740);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国地质大学',' ','湖北',1,1,1,1,0,' ','暂空',' ','http://www.cug.edu.cn','中国地质大学是教育部直属全国重点大学,是国家批准设立研究生院的大学，是国家“211工程”、教育部“优势学科创新平台”项目建设的大学;中国地质大学(武汉)位于武汉东湖之畔，南望山麓。学校以地球科学为主要特色，拥有地质学、地质资源与地质工程两个国家一级重点学科，学科涵盖理学、工学、文学、管理学、经济学、法学、艺术学、教育学、哲学等门类。学校是全国文明单位、湖北省最佳文明单位，拥有国家4A级旅游景区——逸夫博物馆。历史沿革中国地质大学创建于1952年，前身是北京大学、清华大学、天津大学、唐山铁道学院、',4096651);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉纺织大学',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.wtu.edu.cn/','武汉纺织大学简介武汉纺织大学(原武汉纺织工学院)是一所以工为主，理、工、文(艺)、经、管、法、教育等多学科协调发展，纺织服装特色鲜明、优势突出的普通高等学校。学校坐落在九省通衢的湖北省武汉市，位于中国光谷腹地，占地面积2006亩。现有全日制在校生12879人，其中本科生12218人，研究生661人。2006年，学校顺利通过教育部本科教学工作水平评估，被评为优秀。学校拥有2个硕士学位授权一级学科，16个硕士学位授权点。设有46个本科专业，现有学科覆盖了工学、理学、哲学、文学、管理学、经济学、法学和',2406836);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉理工大学',' ','湖北',1,1,1,1,0,' ','暂空',' ','http://www.whut.edu.cn/2015web/index.htm','武汉理工大学是由原武汉工业大学、武汉交通科技大学、武汉汽车工业大学于2000年5月27日合并组建而成，是教育部直属的全国重点大学，是首批列入国家“211工程”重点建设的高校。学校进入英国《泰晤士高等教育》2014年亚洲大学前100名排行榜，名列第49位；进入《泰晤士高等教育》2014-2015年度世界大学前400名排行榜，位居351-400位。学校现有三个校区，占地面积4000余亩，校舍总建筑面积逾156万平方米，四座现代化图书馆藏书400余万册。学校现有教职工5260人，其中专任教师3009人',7955150);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北工业大学',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.hbut.edu.cn/','湖北工业大学是一所以工学为主，覆盖工、文、理、艺、经济、管、法和教育等八大学科门类的多科性大学。学校创建于1952年，1984年由原湖北轻工业学院和原湖北农业机械专科学校合并组建成湖北工学院，2004年更名为湖北工业大学。学校是湖北省重点建设高校，被省委省政府定位为“在湖北省高教体系中起龙头示范作用的、水平较高的骨干大学”。1986年取得硕士学位授予权；1998年通过教育部本科教学工作合格评价；2006年获得教育部本科教学工作评估“优秀”；2010年获得“全国毕业生就业典型经验高校”称号；201',4390650);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华中农业大学',' ','湖北',1,2,1,1,0,' ','暂空',' ','http://www.hzau.edu.cn/2014/ch/','办学历史悠久学校是教育部直属、国家“211工程”建设的全国重点大学，前身是1898年湖广总督张之洞创办的湖北农务学堂，迄今已有116年办学历史。学校于1979年经国务院批准为全国重点高校，1981年成为首批具有博士学位和硕士学位授予权的学校。校园环境优美学校位于武汉市武昌南湖狮子山，占地面积7425亩。校园三面环湖，坐拥青山，风景秀丽，环境幽雅，教学科研基地集中连片，建筑楼群错落有致，是学习科学、追求真理的理想园地。学科优势明显学校生命科学特色鲜明，农、理、工、文、法、经、管、艺等多学科协调发展',3164278);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北中医药大学',' ','湖北',1,3,0,0,0,' ','暂空',' ','http://www.hbtcm.edu.cn','湖北中医学院创建于1958年，其前身是成立于1954年的湖北省中医进修学校，是湖北省唯一一所高等中医药本科院校，国家教育部本科教学工作水平合格评估优秀学校。2003年，原湖北中医学院与原湖北药检高等专科学校合并，成立新的湖北中医学院。湖北药检高等专科学校成立于1958年12月，它的前身是成立于1939年的湖北省立护士职业学校，是全国仅有的一所以药学、医学检验为主干学科的高等专科学校，在药学及医学检验领域具有一定特色与优势。在半个世纪的高等中医药教育实践中，湖北中医学院立足湖北，面向全国，放眼世界',2648322);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华中师范大学',' ','湖北',1,4,1,1,0,' ','暂空',' ','http://www.ccnu.edu.cn/','华中师范大学位于九省通衢的湖北省武汉市，坐落在武昌南湖之滨的桂子山上，占地面积2000多亩，是中国教育部直属重点综合性师范大学，国家“211工程”重点建设的大学，国家教师教育“985”优势学科创新平台建设高校，是国家培养中、高等学校师资和其他高级专门人才的重要基地。学校具有悠久的历史，她是在1903年创办的文华书院大学部(始于1871年创办的文华书院，1924年改名为华中大学)、1912年创办的中华大学、1949年创办的中原大学教育学院的基础上，1951年组建公立华中大学，1952年改制为华中高',4709674);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北大学',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://zsxx.hubu.edu.cn/','黄鹤江城，百湖灵秀;沙湖黉门，弦歌铿锵。【校园概貌】湖北大学是湖北省人民政府与教育部共建的省属重点综合性大学。学校地处国家中部地区中心城市、湖北省省会——武汉市内，现有武昌主校区、汉口校区、阳逻校区等3个校区，总占地2000余亩。学校是湖北省“最佳文明单位”。现任党委书记尚钢、校长熊健民。【历史沿革】1931年，学校前身湖北省立教育学院在武昌宝集庵诞生，此后迭经国立湖北师范学院、湖北省教育学院、湖北省教师进修学院、湖北师范专科学校、武汉师范专科学校和武汉师范学院等时期。1984年8月更名改制为湖',2832279);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北师范大学',' ','湖北',1,4,0,0,0,' ','暂空',' ','http://www.zsxx.hbnu.edu.cn/','校园环境优美学校位于黄石市中心城区，规划占地2002亩，校舍总面积60.45万平方米。学校襟江带湖，含山蓄水，绿茵广场、曲径小道、楼台亭榭等自然人文景观错落有致，相映成辉，风光宜人，曾被评为“湖北省园林式单位”和“湖北省最佳文明单位”，是读书治学和陶冶情操的理想场所。学科优势突出湖北师范学院是一所以人文学科、社会学科、理学、工学为重点，以教师教育为特色，以服务基础教育为主体的省属重点本科高等师范院校，具有硕士学位授予权，是全国本科教学工作水平评估优秀学校。学校设有20个教学院，6个一级学科硕士点',1537336);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黄冈师范学院',' ','湖北',1,4,0,0,0,' ','暂空',' ','http://www.hgnc.net/','冈师范学院是湖北省属全日制普通高等师范院校，是一所以本科教育为主、专科教育为辅，以师范教育为主、非师范教育为辅的多科性地方师范院校。学校实行省市共建、以省为主的办学体制。我校前身是创建于1905年的“黄州府师范学堂”，1937年更名为“湖北省立黄冈乡村师范学校”，1950年3月更名为“湖北省立黄冈师范学校”；1960年秋更名为“湖北省黄冈师范专科学校”，1977年秋更名为“华中师范学院黄冈分院”，1978年4月经国务院批准更名为“黄冈师范专科学校”。1988年被评为首届“全国先进师专”；1995',1193975);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北民族学院',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.hbmy.edu.cn/templet/default/index.html/','学校概况：湖北民族学院是1998年经教育部批准，由原湖北民族学院和恩施医学高等专科学校合并组建而成，是一所以本科教育为主的省属普通本科院校，是湖北省重点建设高校，是湖北省政府和国家民委共建学校。学校地处神奇美丽的恩施土家族苗族自治州。这里东连荆楚，南接潇湘，西邻渝黔，北靠陕渝，享有“鄂西林海”、“华中药库”、“世界硒都”的美誉。中国首条贯通东西的客运大通道——沪汉蓉高速铁路以及G50沪渝高速公路（上海至重庆）横贯境内。校园依山傍水，花木繁茂，碧草如茵，环境幽雅，与龙洞河美景交相辉映，相得益彰。校',2047094);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郧阳师范高等专科学校',' ','湖北',0,4,0,0,0,' ','暂空',' ','http://zhuanti.10yan.com/html/wmcjwsz/yysfgdzkxx.html','郧阳师范高等专科学校(简称郧阳师专)是一所国家举办的省属普通高校，学校位于湖北省丹江口市，其前身是创立于1954年的郧阳师范学校，1975年开办大专班，1977年为华中师范学院郧阳分院，1978年经国务院批准成立郧阳师范专科学校，1993年经原国家教委批准更名为郧阳师范高等专科学校。学校占地面积430亩，建筑面积18.18万平方米。学校现有教职工518人，其中正、副教授95人，具有博士、硕士学历的58人。学校面向全国10多个省、市招生，现有在校普通本、专科生6018人。学校还开展了国家公务员、计',148963);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中南财经政法大学',' ','湖北',1,6,1,1,0,' ','暂空',' ','http://www1.zuel.edu.cn/department/xgb/zskd.htm','中南财经政法大学是中华人民共和国教育部直属的一所以经济学、法学、管理学为主干，兼有哲学、文学、史学、理学、工学、艺术学等九大学科门类的普通高等学校，是国家“211工程”和“985工程”优势学科创新平台项目重点建设高校之一。学校现有两个校区，南湖校区位于风景秀丽的南湖湖畔，首义校区位于历史悠久的黄鹤楼下。历史底蕴深厚，发展稳中求快。学校前身是1948年以邓小平为第一书记的中共中央中原局创建,并由第二书记陈毅担任筹备委员会主任的中原大学。1953年,在全国高等院校调整中，以中原大学财经学院、政法学院',7076010);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉体育学院',' ','湖北',1,7,0,0,0,' ','暂空',' ','http://zs.whsu.edu.cn/','武汉体育学院原名中南体育学院，1953年创办于江西省南昌市，1955年迁至现址，1956年改为现名。武汉体育学院是新中国创办的首批高等体育学府之一，原为国家体育总局（国家体育运动委员会）直属体育院校，2001年学校管理体制改革，现为国家体育总局与湖北省共建院校。我校校园座落在风景秀丽的武汉市东湖之畔，占地面积835亩，建筑面积330000平方米，拥有较先进和齐全的教学、训练、科研及生活设施，并开通了校园网。为适应发展需要，我校现正在筹建第二校区，规划面积1000亩。我校现有在编教职工822人，其',794676);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北美术学院',' ','湖北',1,8,0,0,0,' ','暂空',' ','http://www.hifa.edu.cn/','湖北美术学院是中国华中地区最高美术学府，她坐落在长江之滨的中心城市武汉，现址武昌中山路374号（原昙华林）。湖北美术学院的前身是私立武昌艺术专科学校由曾参加过辛亥革命的蒋兰圃先生、唐义精先生及徐子珩先生等数位热衷于艺术教育事业的有志之士于1920年所创建。几十年来，我院师生创作了大量优秀的美术作品，参加国内外美术展览和设计展示活动多次获得国家级金、银奖章和其它重要奖项。有些作品被中外美术馆和博物馆收藏，还出版了大量的专著和画集等。经过几代人的努力，湖北美术学院形成了自己兼收并蓄的教学特点和锐意创',260178);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中南民族大学',' ','湖北',1,9,0,0,0,' ','暂空',' ','http://www.scuec.edu.cn/','中南民族大学是一所直属国家民族事务委员会的综合性普通高等院校，坐落于白云黄鹤的故乡——武汉南湖之滨。学校前身为中南民族学院，创建于1951年，2002年3月更名为中南民族大学。建校以来，学校始终坚持社会主义办学方向，贯彻落实党的教育方针和民族政策，始终坚持党的民族工作规律与高等教育规律相结合，民族高等教育的特殊性与普通高等教育的普遍性相结合，遵循高等教育的普遍规律，尊重民族高等教育的特殊性，努力探索办好民族院校的新路子，学校的各项事业获得了快速发展。学校占地1554亩，校舍面积100万余平米，馆',3732941);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北汽车工业学院',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.huat.edu.cn','湖北汽车工业学院坐落在风景秀丽的汽车城——湖北省十堰市，位于汉水之滨、旅游胜地武当山北麓，由原中科院学部委员孟少农任首任校长。学校经过近40年的发展，已成为一所以工为主，工、管、经、文、理、法等多学科协调发展的全日制普通本科院校。现有教职工823人，各类在校生10000余人。学校有26个本科专业、21个高职(专科)专业；拥有车辆工程、机械制造及其自动化2个省级重点学科；车辆工程、机械设计制造及其自动化和材料成型及控制工程3个专业被教育部、财政部批准为“特色专业建设点”；设有机械设计制造及其自动化',1180187);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湘潭大学',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.xtu.edu.cn/index.html','湘潭大学座落在毛泽东同志的故乡、中国历史文化名城——湖南省湘潭市，是湖南省唯一的教育部共建大学、湖南省唯一一所没有经过大规模合并的老牌综合性全国重点大学，国家“中西部高校基础能力建设工程”首期入选高校，湖南省与国家国防科工局共建高校，是全国首批具有硕士学位授予权的单位，教育部本科教学工作水平评估“优秀”学校，湖南省首批招收留学生和国防生的高校，历年来全国一本招生高校。毛泽东同志一生心忧天下，从不过问家乡的具体事项，但唯一关心的是湘潭大学的建设和发展。1958年9月10日，毛泽东同志亲笔为学校题写',5073934);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉首大学',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.jsu.edu.cn/','吉首大学创办于1958年9月，是位于湘鄂渝黔边区大湘西区域内一所具有地方性、民族性特色的省属综合性大学，是地处国家西部开发“10+3”范围民族地区的高校，是湖南省唯一进入国家西部大开发范围的高校，是国家《武陵山片区区域发展与扶贫攻坚规划》确定的重点建设高校，是国家“中西部高校基础能力建设工程”高校，是国家“服务国家特殊需求博士人才培养项目”高校。学校办在湘西自治州和张家界市两地，本部设在湘西自治州首府——吉首市。学校所在区域内拥有世界自然遗产——张家界武陵源自然风景区、中国历史文化名城——凤凰。',2488241);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南大学',' ','湖南',1,0,1,1,1,' ','暂空',' ','http://www.hnu.cn','湖南大学坐落在中国历史文化名城长沙，校区位于湘江之滨、岳麓山下，享有“千年学府，百年名校”之誉。她不仅是国家教育部直属的全国重点综合性大学，也是国家“211工程”、“985工程”重点建设的高水平大学。现任党委书记蒋昌忠教授，校长段献忠教授。学校办学起源于宋太祖开宝九年(公元976年)创建的岳麓书院，历经宋、元、明、清等朝代的变迁，始终保持着文化教育的连续性。1903年改制为湖南高等学堂，1926年定名湖南大学，1937年成为国民政府教育部十余所国立大学之一。中华人民共和国成立后，著名哲学家、教育',8642304);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中南大学',' ','湖南',1,0,1,1,1,' ','暂空',' ','http://www.csu.edu.cn/','中南大学坐落在中国历史文化名城──湖南省长沙市，占地面积5886亩，建筑面积276万平方米，跨湘江两岸，依巍巍岳麓，临滔滔湘水，环境幽雅，景色宜人，是求知治学的理想园地。中南大学是教育部直属全国重点大学、国家“211工程”首批重点建设高校、国家“985工程”部省重点共建的高水平大学和国家“2011计划”首批牵头高校。现任校党委书记高文兵教授、校长张尧学院士。中南大学由原湖南医科大学、长沙铁道学院与中南工业大学于2000年4月合并组建而成。原中南工业大学的前身为创建于1952年的中南矿冶学院，原长',11603673);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南科技大学',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.hnust.edu.cn','湖南科技大学是一所办学历史悠久、学科门类齐全、办学特色鲜明的教学研究型综合性大学，是教育部本科教学工作水平评估“优秀”高校、教育部批准的“卓越工程师教育培养计划”高校、全国毕业生就业典型经验50强高校。学校始建于1949年8月成立的湘北建设学院，2003年11月经国家教育部和湖南省人民政府批准，由原国家煤炭工业部在南方的唯一本科院校湘潭工学院与湖南省第二大本科师范院校湘潭师范学院合并组建而成，实行中央与湖南省共建。现占地面积3107亩，建筑面积100余万平方米,全校文献总量565.71万册，其中',4331324);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙理工大学',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.csust.edu.cn','长沙理工大学是一所以工为主，工、理、管、经、文、法、艺等多学科协调发展，以本科教育为主体，具有博士后科研流动站、博士学位授予权和硕士生推免权的多科性大学。学校是全国先进基层党组织、湖南省文明标兵单位、湖南省依法治校示范学校。经过近60年的建设和发展，学校积淀了以“博学、力行、守正、拓新”为校训和以“铺路石精神”为核心的长理文化。自建校以来，为交通、电力、水利、轻工等行业和区域经济社会发展培养了近20万高级专门人才。学校本科招生第一批次覆盖全国，毕业生就业率连续多年保持在95%以上。学校是国家“中',5103634);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南农业大学',' ','湖南',1,2,0,0,0,' ','暂空',' ','http://zs.hunau.edu.cn/','湖南农业大学创办于1903年10月8日，初名修业学堂，历经湖南省私立修业高级农业职业学校、湖南省立修业农林专科学校、湖南农学院等阶段。1951年3月，湖南省立修业农林专科学校与湖南大学农业学院合并组建为湖南农学院，同年11月毛泽东主席亲笔题写校名。1994年2月更名为湖南农业大学。学校位于长沙市芙蓉区，占地面积2.27平方公里，北领东湖秀色，南润浏阳河风光，空气清新，环境幽雅。百年沧桑，几度易名，务本崇实，矢志不渝。学校现已发展成为以农科为特色，农、工、文、理、经、管、法、医、教、艺术多学科综合',2522052);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中南林业科技大学',' ','湖南',1,2,0,0,0,' ','暂空',' ','http://www.csfujob.com ','中南林业科技大学经过55年的建设和发展，现已成为一所涵盖理、工、农、文、经、法、管、教、艺等九大学科门类，具有博士后科研流动站、博士学位授予权和硕士生推免权、富有特色的多科性大学。学校现设20院1系1部。拥有5个博士后科研流动站，5个一级学科博士点，28个二级学科博士点，13个一级学科硕士点，72个二级学科硕士点，4个硕士专业学位授权类别，14个硕士专业学位授权领域。学校拥有2个国家特色重点学科、3个国家重点学科、4个国家林业局重点学科、2个湖南省优势特色重点学科、11个湖南省重点学科。学校拥有',3214945);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南中医药大学',' ','湖南',1,3,0,0,0,' ','暂空',' ','http://www.hnctcm.edu.cn','学校创办于1934年，时名湖南国医专科学校，1960年改办为普通高等本科院校湖南中医学院，1990年原湖南科技大学成建制并入湖南中医学院，2002年湖南省中医药研究院与湖南中医学院合并，2006年经教育部批准更名为湖南中医药大学。2012年学校进入一本招生序列。目前学校与湖南省中医药研究院实行校院合一的管理体制。学校现有含浦、东塘2个校区，总占地面积1393亩，校舍建筑面积47万平方米。现有3所直属附属医院，10所非直属附属医院，2个校办合资企业。学校下设16个学院、1个部、16个研究中心（所）',2201601);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南师范大学',' ','湖南',1,4,0,1,0,' ','暂空',' ','http://www.hunnu.edu.cn/','学校简介湖南师范大学创建于1938年，位于历史文化名城长沙，是国家“211工程”重点建设的大学、教育部普通高等学校本科教学工作水平评估优秀高校。学校现有5个校区，占地168公顷，建筑面积100余万平方米。主校区西偎麓山，东濒湘江，风光秀丽，是全国绿化“400佳”单位之一。学校设有24个学院，3个教学部，本科和研究生专业覆盖哲、经、法、教、文、史、理、工、农、医、管、艺等12大学科门类。学校拥有发育生物学和中国近现代史等6个“十一五”国家重点学科、伦理学等9个“211工程”“三期”重点建设学科、生',3899246);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南理工学院',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.hnist.cn','湖南理工学院是一所省属普通本科高校，是经国务院学位委员会批准的硕士学位授予单位。学校座落在国家历史文化名城、长江沿岸开放城市岳阳市南湖风景区，自然环境优美，人文底蕴深厚。学校历史可追溯到1910年美国人创办的湖滨大学。新中国成立后，学校历经变迁。经教育部批准，1999年3月，由当时的岳阳大学、岳阳师范高等专科学校和岳阳教育学院合并为岳阳师范学院。2003年2月更名为湖南理工学院。2006年12月，学校高标准通过教育部本科教学工作水平评估。2008年12月31日，学校顺利通过专家评审，成为湖南省立',2338510);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湘南学院',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.xnu.edu.cn','湘南学院是经教育部批准，于2003年由原郴州师专、郴州医专、郴州教育学院、郴州师范合并组建的综合性全日制普通公办本科院校，其办学历史可追溯到1912年创建的湖南省第三女子师范学校。学校位于“天下第十八福地”、湖南“南大门”——郴州市，是“国家语言文字规范化示范校”、“湖南省文明高校”、“湖南省园林式单位”、“湖南省地方本科院校转型发展试点单位”、“湖南省教育信息化建设优秀单位”、“湖南省教育信息化创新应用示范校”、“湖南省创新型院校”、“湖南省大学生就业创业示范建设校”、湖南省普通高校就业工作“',1690858);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('衡阳师范学院',' ','湖南',1,4,0,0,0,' ','暂空',' ','http://www.hynu.edu.cn','衡阳师范学院是经教育部批准、湖南省直属的普通全日制公办本科院校。学校坐落于王船山（王夫之）故里、蔡伦家乡所在的历史文化名城、中国优秀旅游城市——衡阳市，地理位置优越，北望南岳衡山，南接奔腾湘江，京广铁路、武广高铁、湘桂高铁、京珠高速、衡枣高速、衡邵高速、衡炎高速等环绕四周，距高铁衡阳东站2公里，距南岳机场20公里。学校前身为1904年创办的湖南官立南路师范学堂，百余年来“南学津梁”弦歌不断，培养了近30万莘莘学子，恽代英、张秋人、蒋先云、黄静源、黄克诚、江华、陶铸、张经武、张平化、张际春、曾希圣',1341271);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('邵阳学院',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.hnsyu.net','邵阳学院创建于1958年，是经教育部批准的具有硕士学位研究生招生资格的省属多科性本科院校。学校位于湘西南历史文化名城——湖南省邵阳市。这片人文底蕴丰厚的沃土培育了“第一个睁眼看世界”的思想家魏源，“再造共和”的民主革命家蔡锷，杰出的马克思主义史学家吕振羽和德艺双馨的人民音乐家贺绿汀。宝庆竹刻、邵阳布袋戏、滩头年画和花瑶挑花等非物质文化遗产在湖湘文化中熠熠生辉。学校校园环境优美，景色秀丽，林木葱郁，鸟语花香，是湖南省“花园式单位”。学校现有李子园、七里坪、江北三个校区，校园占地面积1796亩；校舍',1565970);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('怀化学院',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.hhtc.edu.cn','怀化学院创办于1958年，前身为怀化师范高等专科学校，2002年经国家教育部批准升格为全日制普通本科院校。2000年以来，学校先后被评为“湖南省文明单位”、“湖南省文明高校”、“全国精神文明建设工作先进单位”、“国家节约型公共机构示范单位”。学校坐落于古有“滇黔门户、全楚咽喉”之称，现有湘黔、枝柳、渝怀铁路及209、320国道、沪昆、包茂高速交汇的交通枢纽城市——怀化市。学校占地1100余亩，建筑面积42万平方米，分东、西两个校区办学。学校拥有馆藏图书123万册，电子图书232万册，中文期刊18',1259017);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南文理学院',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.huas.cn/','湖南文理学院是一所省属全日制普通本科院校。1999年3月经教育部批准由常德师范高等专科学校和常德高等专科学校合并组建常德师范学院，2003年2月更名为湖南文理学院。学院升本以来，抓住高等教育快速发展的历史机遇，以教育部本科教学工作水平评估指标体系为指南，全国贯彻“以评促改，以评促建，以评促管，评建结合，重在建设”方针，加快建设步伐，学院办学规模逐步扩大，办学结构和条件明显改善，办学质量和效益显著提高。学院现占地面积1557亩，教学行政用房面积22.43万平方米，馆藏图书115.8万册，电子图书1',1571704);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南科技学院',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.huse.cn','五次荣获全国普通高等学校优秀教学成果奖全国普通高等学校教学管理工作先进单位全国毕业生就业典型经验50强高校全国五四红旗团委全国高等院校后勤工作先进集体湖南省普通高校毕业生就业工作优秀单位湖南省文明单位湖南省文明高校湖南省高等学校党建和思想政治工作先进单位湖南省思想政治工作先进单位湖南省高等学校思想政治教育工作先进单位湖南省高校学生思想政治教育先进单位湖南科技学院是一所经教育部批准，由湖南省教育厅主管的公办全日制普通本科院校。学院创建于1941年，前身为湖南省立七师，2002年由原零陵师专升格为本',1657814);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南人文科技学院',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.huhst.edu.cn/','湖南人文科技学院创建于1978年，是经教育部批准的省属综合性公办普通本科大学，学校具有招收硕士研究生、本专科学生和留学生资格。学校位于湖南湘中新城娄底，这里风景秀丽，气候宜人，物产丰富，交通便利，是全国最适宜人口居住的园林式城市之一。校园占地面积693680平方米，校舍建筑面积381744平方米，教学科研仪器设备总值10338.08万元，有馆藏图书112.71万册，电子图书188万多种，中外电子期刊40705种，中外纸质期刊1483种，电子期刊数据库6个，特色专题数据库23个。学校设政治与法律系',1069148);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南商学院',' ','湖南',1,6,0,0,0,' ','暂空',' ','http://admi.hnuc.edu.cn','湖南商学院是一所以经济学、管理学为主，涵盖经、管、法、文、理、工、艺等学科，具有硕士、学士学位授予权的省属全日制普通高等院校。是教育部本科教学工作水平评估优秀高校。学校位于历史文化名城长沙市，分南北两个校区。南校区位于岳麓区岳麓大道569号，北校区位于望城区雷锋大道，校园占地面积共1340.61亩，建筑面积47万余平方米。现有总值1亿余元的教学仪器设备，200余万册的图书馆藏书。设有17个教学院（部）、42个科研机构以及教育部首批批准成立的独立学院——北津学院。有普通全日制在校学生2万余人。学校',2540056);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南华大学',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.usc.edu.cn/','南华大学是由工业和信息化部、国家国防科技工业局、中国核工业集团公司、中国核工业建设集团公司与湖南省人民政府共建，具有58年办学历史的综合大学。学校由原隶属中国核工业部的中南工学院、核工业第六研究所与原隶属湖南省的衡阳医学院合并组建而成。学校座落在历史文化名城湖南省衡阳市市区，占地面积近3000亩，交通便捷，环境优美，是读书治学的理想园地。学校有直属学院26个，4所直属型附属医院，13所协作型附属医院，20个研究生协作培养单位。学校设有76个本科专业，18个一级学科硕士学位授权点，8种硕士专业学位',6941394);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中山大学',' ','广东',1,0,1,1,1,' ','暂空',' ','http://www.sysu.edu.cn','中山大学位于改革开放前沿的广东省。座落在珠江之畔、南海之滨。现有四个校区，总面积达6.17平方公里，广州南校区占地1.17平方公里，北校区占地0.39平方公里，广州东校区占地1.13平方公里，珠海校区占地3.48平方公里。各校区树木葱笼，绿草如茵，景色秀丽，各具风格，是陶冶情操、读书治学的胜境。中山大学是一所具有优良办学传统的名牌大学。1924年，由世纪伟人孙中山先生亲手创办，并亲笔题写校训：“博学、审问、慎思、明辨、笃行”。原名国立广东大学，1926年，为纪念中山先生，改名为国立中山大学。上世',13515003);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('暨南大学',' ','广东',1,0,0,1,0,' ','暂空',' ','http://www.jnu.edu.cn','暨南大学是中国第一所由国家创办的华侨学府，是目前全国境外生最多的大学，是国家“211工程”重点综合性大学，直属国务院侨务办公室领导。“暨南”二字出自《尚书·禹贡》篇：“东渐于海，西被于流沙，朔南暨，声教讫于四海。”意即面向南洋，将中华文化远播到五洲四海。学校的前身是1906年清政府创立于南京的暨南学堂。后迁至上海，1927年更名为国立暨南大学。抗日战争期间，迁址福建建阳。1946年迁回上海。1949年8月合并于复旦、交通等大学。1958年在广州重建。素有“华侨最高学府”之称的暨南大学，恪守“忠信',6641968);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('汕头大学',' ','广东',1,12,0,0,0,' ','暂空',' ','http://www.stu.edu.cn/','汕头大学是1981年经国务院批准成立的广东省属“211工程”重点建设综合性大学，是教育部、广东省、李嘉诚基金会三方共建的高等院校，也是全球唯一一所由私人基金会——李嘉诚基金会持续资助的公立大学。学校座落在海滨城市汕头的西北区，学校占地面积1920.18亩，建筑面积44.84万平方米。汕头大学得到著名爱国人士及国际企业家李嘉诚先生的鼎力相助，李嘉诚基金会对汕大(包括长江商学院)承诺捐款逾60亿港元。不仅如此，李嘉诚先生还为汕头大学的建设和发展付出了大量的心血，充分显示出李嘉诚“造福桑梓”的高尚情怀',1863615);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华南理工大学',' ','广东',1,1,1,1,1,' ','暂空',' ','http://www.scut.edu.cn/admission/','华南理工大学是直属教育部的全国重点大学，坐落在南方名城广州，占地面积294多万平方米。校园分为两个校区，五山校区位于广州市天河区石牌高校区，校园内湖光山色、绿树繁花，民族式建筑与现代化楼群错落有致，文化底蕴深厚，是教育部命名的“文明校园”，大学城校区位于广州市番禺区广州大学城内，是一个环境优美、设施先进、管理完善、制度创新的现代化校园。两个校区交相辉映，是莘莘学子求学的理想之地。华南理工大学原名华南工学院，组建于1952年全国高等学校院系调整时期，是以中山大学工学院、华南联合大学理工学院、岭南大',9765778);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华南农业大学',' ','广东',1,0,0,0,0,' ','暂空',' ','http://www.scau.edu.cn/','华南农业大学是全国重点大学，是广东省和农业部共建的“211工程”大学;2015年6月，学校入选广东省高水平大学重点建设高校。校园坐落在素有“花城”美誉的广州市，占地8269亩，其中天河五山本部占地4406亩，增城教学科研基地占地3863亩。五山本部校舍总建筑面积约137万平方米;初步形成“五湖四海一片林的紫荆校园”环境，自然景色与人文景观交相辉映，是读书求学的好地方。学校悠久的办学历史可追溯至始创于1909年的广东全省农事试验场暨附设农业讲习所。1952年，在全国高校院系调整时，由中山大学农学院',3282627);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东海洋大学',' ','广东',1,2,0,0,0,' ','暂空',' ','http://zsjy.gdou.edu.cn/zhaosheng/index.php','广东海洋大学是广东省人民政府和国家海洋局共建的省属重点建设大学，是一所以海洋和水产为特色、多学科协调发展的综合性大学，是教育部本科教学水平评估优秀院校，是具有“学士、硕士、博士”完整学位授权体系的大学，是入选广东省高水平大学建设范围的高校。学校的前身是创建于1935年的广东省立高级水产职业学校。她是广东现代海洋水产教育的发端，几经迁徙之后升格发展成农业部直属的湛江水产学院。1997年1月，由具有62年办学历史的湛江水产学院和39年办学历史的湛江农业专科学校实质性合并组建湛江海洋大学。2001年1',3608880);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东医科大学',' ','广东',1,3,0,0,0,' ','暂空',' ','http://www.gdmc.edu.cn','广东医科大学的前身是中山医学院湛江分院，创建于1958年，1964年升格为五年制医学本科院校并更名为湛江医学院，1992年易名为广东医学院，2016年更名为广东医科大学。1960年被评为全国文教战线先进单位，1978年有2项科研成果获全国科学大会奖励，1986年获硕士学位授予权单位，是广东省属重点建设大学，1991年获广东省办学水平评估一等奖，2005年在教育部本科教学工作水平评估中获“优秀”，2008年设立博士后科研工作站，2009年被广东省列为国家限额指标内的拟新增博士学位授予单位立项建设单',2258095);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州中医药大学',' ','广东',1,3,0,0,0,' ','暂空',' ','http://www.gzucm.edu.cn/','广州中医药大学成立于1956年，是全国首批4所中医药高等学府之一，也是国内首批具有硕士、博士学位授予权的高校之一；拥有中医学、中西医结合、中药学三个一级学科博士学位授予权，博士后科研流动站3个，博士学位授权学科专业15个、硕士学位授权学科专业18个，博士学位授权点、硕士学位授权点覆盖了中医学、中药学以及中西医结合三个一级学科项下的所有二级学科；学校还有中医药高校唯一的科学技术哲学专业硕士点，以及药剂学和社会医学与卫生事业管理等硕士点；拥有一支学术造诣高、技术力量强、年龄结构合理的研究生导师队伍，',2141381);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东药科大学',' ','广东',1,3,0,0,0,' ','暂空',' ','http://www.gdpu.edu.cn/','广东药学院是我国独立建制的3所药科大学之一，广东省热门高校，首批进驻广州大学城的10所优质名校之一，广东省培养药学、预防医学和临床医学等高级技术人才和管理干部的重要基地，广东省国家执业药师培训中心，也是广东省医药卫生系统继续教育的重要基地和广东省3家药学法定CMA计量认证单位之一，具有硕士学位授予权,以及接收港台澳台学生、外国留学生和面向全国招生资格。学校本部坐落在风光旖旎的广州大学城，有广州赤岗校区、宝岗校区和中山校区，全校园占地面积3200多亩。学校具有48年的办学历史。她的前身是建立于19',1453078);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华南师范大学',' ','广东',1,0,1,1,0,' ','暂空',' ','http://zsb.scnu.edu.cn/','华南师范大学始建于1933年，前身是广东省立勷勤大学师范学院。1996年进入国家“211工程”重点建设大学行列，2015年成为广东省人民政府和教育部共建高校，同年进入广东省高水平大学整体建设高校行列。学校现有广州石牌、广州大学城和佛山南海3个校区，占地面积3025亩，校舍面积155万平方米，图书374万册。学校现有教育技术学、发展与教育心理学、光学、体育人文社会学(重点培育)4个国家重点学科，9个国家“211工程”重点建设学科，22个广东省重点学科(含5个攀峰重点学科，14个优势重点学科和3个特',3922891);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('韶关学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://www.sgu.edu.cn','韶关学院是广东省人民政府主管的省市共建地方本科院校。在48年的办学历程中，学校为社会培养输送了各类人才7.5万余人。学校的前身是创办于1958年的韶关师范专科学校。1970年改名为韶关地区师范学校，1978年复办韶关师范专科学校。1989年韶关师范专科学校与市属韶关大学合并为新的韶关大学。2000年3月经国家教育部批准，韶关大学与韶关教育学院合并成立本科层次的韶关学院。2004年批准为学士学位授予单位。学校现设有英东生物工程学院、信息工程学院、经济管理学院、文学院、化学与环境工程学院、外语学院、',1807624);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('惠州学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://www.hzu.edu.cn','惠州学院位于全国文明城市、广东省历史文化名城--惠州，南临南海大亚湾，毗邻广州、东莞、深圳、香港，是广东省省属公办综合性本科大学。学院办学历史悠久，人文荟萃，校园青山环抱，浓荫匝地，现代建筑错落有致。占地总面积160.65万平方米，校舍建筑面积25.62万平方米，在建面积5.63万平方米，绿化面积12.66万平方米，绿化覆盖率达到70%，连年被惠州市授予“花园式单位”的称号。学院还先后获得全国模范职工之家、全国三八红旗集体、东西部学校结对帮扶工作先进单位、真维斯大学生助学基金项目实施先进单位、广',1391379);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('韩山师范学院',' ','广东',1,4,0,0,0,' ','暂空',' ','http://www.hstc.edu.cn','韩山师范学院是一所办学历史悠久、文化积淀深厚和办学特色鲜明的广东省属本科师范院校，位于国家历史文化名城潮州市。潮州是广东的东大门，地处汕头、深圳、厦门三大经济特区的中心位置，是珠江三角地区和海峡西岸经济区的辐射交汇点、交通连接点和文化交融点，交通便利，气候宜人，民风淳朴，社会和谐，享有“国家历史文化名城”、“中国优秀旅游城市”、“中国瓷都”、“中国园林城市”等美誉。学院以“韩山”为名，与唐代大文学家韩愈有关。韩愈曾任潮州刺史，兴学育才，利农除弊，千百年来一直为潮州人民所崇敬，以至江山改姓，潮州由',1035847);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('岭南师范学院',' ','广东',1,4,0,0,0,' ','暂空',' ','http://zsjy.lingnan.edu.cn/zs/','岭南师范学院(原湛江师范学院)是一所具有百年师范教育历史的广东省属普通本科院校，坐落在中国大陆最南端的美丽港城湛江市。学校1904年始设师范科教育，1978年更名为雷州师范专科学校，1991年升格更名为湛江师范学院，2014年更名为岭南师范学院。在百余年的师范教育历程中，学校肩负兴学育人的办学使命，凝练了“勤教力学、立己树人”的办学理念和“崇德、博雅、弘志、信勇”的校训精神，坚持“立足粤西、服务广东、走向全国、面向世界”的办学定位，培养了大批优秀教师、教育工作者和社会应用人才，为粤西地区乃至广东',1593468);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('肇庆学院',' ','广东',1,4,0,0,0,' ','暂空',' ','http://www.zqu.edu.cn/index.htm','肇庆学院为公办全日制综合性本科大学，创办于1970年。学校地处珠三角核心区广东省肇庆市端州区，坐落于国家级重点风景名胜星湖景区内，校园总占地面积1214.3亩，环境优美，先后获评为“全国绿化模范单位”、“全国造林绿化400佳单位”、“广东省文明单位”、2012年成功入选50所“全国毕业生就业典型经验高校”、广东省高校治安综合治理先进学校、肇庆市花园式单位等称号。学校有经济学、法学、教育学、文学、历史学、理学、工学、农学、管理学、艺术学等十大学科门类，62个本科专业，面向广东、广西、湖南、甘肃、湖',1229127);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('嘉应学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://www.jyu.edu.cn','嘉应学院是一所省属普通本科院校，有着94年的办学历史。学院位于中国历史文化名城，客家人的聚居地，著名的文化之乡、华侨之乡、足球之乡-广东省梅州市。梅州在清朝时称嘉应州，嘉应学院由此得名。嘉应学院校本部校园占地面积1041亩，校舍建筑面积37万平方米。学院教学科研仪器设备总值达7440.94万元，拥有22个设备先进的实验室，图书馆藏书125.7万册，中外期刊1500多种。学院现有专任教师665人，其中教授47人、副教授170人、讲师257人，具有博士学位的教师21人，具有硕士学位的教师250人，正',1466854);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州体育学院',' ','广东',1,7,0,0,0,' ','暂空',' ','http://www.gipe.edu.cn','广州体育学院地处广州市繁华的天河区商贸圈中心,南倚体育中心，东邻广州新八景“天河飘绢”，西傍东风公园，北靠天河火车东站，校园绿树成荫，花香流溢，是全国体院中享有独特地缘优势的多学科体育学院。1958年办学至今，为国家培养了一万多名各类体育人才，毕业生遍布全国各省、市、自治区及港澳台、美欧等地。改革开放以来，学院锐意改革，各项事业取得了令人瞩目的发展，形成了一所融教育学、经济学、文学、管理学等四个学科门类于一体，师范教育与非师范教育并举的高等体育院校。目前设有体育教育系、体育新闻与传播系、休闲体育',571421);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州美术学院',' ','广东',1,8,0,0,0,' ','暂空',' ','http://www.gzarts.edu.cn/2012/','广州美术学院是广东省所属的一所美术与设计系科设置齐全的高等美术学府，始建于1953年秋，其前身是中南美术专科学校，由华南文艺学院、中南文艺学院和广西艺专的美术专业调整合并而成，原址在湖北武昌，1958年迁至广州，同年8月更名为广州美术学院，并开始招收本科生。1969年与广州音乐专科学校、广东舞蹈学校合并为广东人民艺术学院。1978年2月恢复广州美术学院原有建制，并面向全国招收研究生。1982年具备硕士学位授予权，是全国首批取得硕士学位授予权的单位之一。1986年招收继续教育学生。1987年开始招',755486);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('星海音乐学院',' ','广东',1,8,0,0,0,' ','暂空',' ','oldname": "星海音乐学院','星海音乐学院地处岭南文化中心－－广州，是我国华南地区唯一的一所高等音乐专业学府。其办学历史可追溯到1932年由中国现代音乐教育先驱马思聪、陈洪先生创办的专业音乐学校－－“广州音乐院”。学院名称历经变化，1957年10月重建并更名为“广州音乐学校”，1958年更名为“广州音乐专科学校”。1977年开始招收本科生，1981年6月经国务院批准，“广州音乐专科学校”升格为“广州音乐学院”，同年，取得学士学位授予权资格。1985年12月，为纪念人民音乐家冼星海，“广州音乐学院”更名为星海音乐学院。1992',261034);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东技术师范学院',' ','广东',1,4,0,0,0,' ','暂空',' ','http://www.gdin.edu.cn/','广东技术师范学院是一所具有硕士学位授予权的省属全日制普通本科院校，隶属于广东省教育厅。学院前身为广东民族学院，创办于1957年，1977年开始招收本科生，1998年经广东省人民政府同意并报国家教育部批准，改名为广东职业技术师范学院，2002年更名为广东技术师范学院。2002年、2005年原广东机械学校、原广东省经济管理干部学院和广东省财贸管理干部学院先后并入我院。2006年经国务院学位委员会批准为硕士学位授予单位。学院具备留学生录取资格。学院的办学定位是，面向职教，服务职教，引领职教。主要任务是',1256400);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南大学',' ','海南',1,0,0,1,0,' ','暂空',' ','http://www.hainu.edu.cn','海南大学是于2007年8月由原华南热带农业大学与原海南大学合并组建而成的海南省属综合性重点大学，是海南省人民政府与教育部、财政部共建高校。2008年12月，经国家批准成为“211工程”重点建设高校。原华南热带农业大学创建于1958年，与创建于1954年的中国热带农业科学院紧密结合，并称“热作两院”，被誉为我国热带农业科教领域的“双子星”。“两院人”经过艰苦卓绝的努力，使我国成为世界上唯一在北纬18°—24°范围内大面积种植橡胶成功的国家，并由原来的植胶空白国，奇迹般地崛起为世界第五大产胶国。学校',8222717);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('深圳大学',' ','广东',1,0,0,0,0,' ','暂空',' ','http://zs.szu.edu.cn/index/','1980年，中国创办深圳经济特区；1983年，深圳经济特区创办深圳大学。学校坐落南山半岛，面临后海，遥对香港。占地1.44平方公里。深圳市委、市政府寄予学校的历史重托是，以改革为动力，建设一所为经济特区提供骨干人才和高端人才培养、高端智力服务、高端科技成果的特区大学，一所走上国际、能够与世界各国高等院校平等交流互相观照的窗口大学，一所努力创新办学体制、积极探索现代大学制度的实验大学。教育部高度重视特区大学建设。两院院士张维教授首任校长。清华大学为主，援建建筑、电子类学科；北京大学为主，援建中文、',6553798);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西大学',' ','广西',1,0,0,1,0,' ','暂空',' ','http://zs.gxu.edu.cn','广西大学是一所历史悠久的地方综合性大学，创办于1928年，首任校长是我国著名教育家、科学家马君武博士。1997年，广西大学与广西农学院合并组建新广西大学。现设哲学、经济学、法学、文学、理学、工学、农学、管理学、教育学等9大门类，是国家教育部和广西壮族自治区共建高校，国家“211工程”重点建设学校。2005年，在国家教育部本科教学工作水平评估中获得优秀。广西大学占地面积307.19公顷，校舍总建筑面积120.26万平方米，教学科研仪器设备总值达到4.12亿元。学校现有27个学院、1个直属教学部，1',3965339);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林电子科技大学',' ','广西',1,1,0,0,0,' ','暂空',' ','http://www.guet.edu.cn/extGuetWeb/','桂林电子科技大学座落在世界著名的风景游览城市和中国历史文化名城——广西壮族自治区桂林市，是工业和信息化部与广西壮族自治区共建高校、国家“中西部高校基础能力建设工程”入选高校、广西壮族自治区重点建设高校。中国绕月探测工程总设计师、国家最高科学技术奖获得者、中国科学院孙家栋院士为学校名誉校长。学校始建于1960年，1980年经国务院批准成立桂林电子工业学院，2006年更名为桂林电子科技大学。学校先后隶属于第四机械工业部、电子工业部、机械电子工业部、中国电子工业总公司、信息产业部。2000年管理体制转',2889091);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林理工大学',' ','广西',1,1,0,0,0,' ','暂空',' ','http://www.glut.edu.cn/','1956年，正值年轻的共和国大规模开展工业化建设的火红年代，为满足祖国建设需要，国家重工业部决定在广西组建桂林地质学校。同年11月，桂林理工大学这颗植根于八桂沃土的庄严生命宣告诞生。功崇惟志，天道酬勤。半个多世纪以来，学校坚持社会主义办学方向，以人才培养、科学研究为己任，共为国家培养了近10万名各类高级专业技术和管理人才。所培养学生以专业基础扎实，工作实践能力强而倍受社会称赞。学校占地面积1700多亩，现设52个本科专业，35个硕士学位授权点，有在校生16800多人。是一所以工学为主，理学、管理',3499054);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西医科大学',' ','广西',1,3,0,0,0,' ','暂空',' ','http://www.gxmu.edu.cn/','广西医科大学位于广西壮族自治区首府南宁市风景秀丽的南湖之滨，占地面积857.47亩，创建于1934年，是全国建校较早的医学院校之一。学校现有19个二级学院，4所直属附属医院、7所非直属附属医院，还有48所教学医院和预防医学实习基地。开设有临床医学（含肿瘤医学、麻醉医学、医学影像学）、预防医学、口腔医学、药学、护理学、信息管理与信息系统（医学）、医学检验等本科专业和药学、护理学、“3+2”形式五年制高职（专科）。经国家教育部批准，2000年开始面向全国招收七年制临床医学专业学生。学校设有55个教研',1920812);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('右江民族医学院',' ','广西',1,3,0,0,0,' ','暂空',' ','http://ymcnzs.university-hr.cn/','右江民族医学院是一所广西壮族自治区区（省）属全日制普通高等医科院校，座落在广西西部的重镇—百色市。这里是1929年邓小平同志领导著名的百色起义、创建中国工农红军第七军和右江革命根据地的地方，是大西南出海通道的枢纽和中国重要的铝工业基地。美丽、清澈的澄碧河和滚滚东流的右江在这里相会，穿城而过，有西南大动脉之称的南昆铁路和繁忙的南百高速公路贯穿右江河谷走廊，连接西东。学院座落于城市的西区，绿树成荫，环境幽雅，是一个学习深造的好地方。右江民族医学院创建于1958年，1978年升格为本科医学院校，198',724723);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林医学院',' ','广西',1,3,0,0,0,' ','暂空',' ','http://www.glmc.edu.cn/','桂林医学院是广西壮族自治区直属普通本科院校，创建于1935年，拥有硕士学位授予权。2006年11月26日至12月1日，我校接受了教育部本科教学工作水平评估。根据专家组的考察评估意见和教育部普通高等学校本科教学工作水平评估专家委员会意见，教育部确定对我校的评估结论为“优秀”。目前校园占地总面积847993.26㎡（1272亩），包括乐群、东城和临桂三个校区，其中临桂新校区正在筹建中。学校设有基础医学院、临床医学院、南溪山临床医学院、第二临床医学院、第三临床医学院、药学院、生物技术学院、护理学院、继',1199723);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西师范大学',' ','广西',1,4,0,0,0,' ','暂空',' ','http://www.gxnu.edu.cn','广西师范大学是广西壮族自治区重点大学，坐落在世界著名山水旅游名城、历史文化名城桂林市。学校有王城、育才、雁山3个校区，校园面积3114.9亩。现有在职教职员工2180多人，全日制本科生14600余人，博士生47人，硕士研究生4100多人（含教育硕士），留学生370多人，函授、进修生等约7600人。建校76年来，学校为国家尤其是广西培养了16万名教师和其他专业人才。办学历史悠久学校创办于1932年，原名广西省立师范专科学校，曾六次更名，八次迁址，四度调整。1936年与广西大学合并；1941年，重建',2317856);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西师范学院',' ','广西',1,4,0,0,0,' ','暂空',' ','http://www.gxtc.edu.cn/','广西师范学院成立于1953年，位于广西壮族自治区首府南宁市，是国家中西部高校基础能力建设工程(二期)项目学校，教育部数据中国“百校工程”项目试点学校，教育部直属综合性重点大学——西南大学对口支援建设学校，广西新增博士学位授予单位立项建设单位。现有明秀、长岗、五合三个校区，占地面积1688亩;另有1898亩武鸣校区规划建设用地，校舍建筑面积57.04万平方米，馆藏纸质图书178.32万册，电子图书157.6万种，合作举办1所独立学院。在职教职工1328人，全日制普通本科生13743人，专科生246',1537092);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西民族师范学院',' ','广西',1,9,0,0,0,' ','暂空',' ','http://www.gxnun.edu.cn','广西民族师范学院坐落在中国通往东南亚门户城市、桂西南区域交通枢纽城市、具有悠久历史和光荣革命传统的广西壮族自治区崇左市，是桂西南地区唯一一所公办全日制普通本科院校，被誉为“边陲明珠”、“南疆国门大学”。办学历史悠久学校的前身是广西省立龙州师范学校，自1939年创办以来，历经广西省龙州师范学校、南宁专区龙州师范学校、南宁地区第二师范学校等阶段。1978年12月，经国务院批准成立全日制普通高等学校，校名定为“南宁师范专科学校”，是广西最早成立的三所师专之一。1994年国家教委将学校更名为“南宁师范高',405454);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河池学院',' ','广西',1,0,0,0,0,' ','暂空',' ','http://www.hcnu.edu.cn/','河池学院是经国家教育部批准成立的全日制普通本科院校，实行广西壮族自治区与河池市共建、以自治区为主的办学体制，是自治区全额拨款事业单位。学院坐落在山青水秀、交通便捷的历史文化名城、中国优秀旅游城市——广西宜州市。在长期的办学过程中，学院本着“忠诚执著、刚毅厚重、朴实无华、至善和谐”的理念，开拓创新，不断改善办学条件，深化教育教学改革，提高人才培养质量，为经济社会发展培养了大批的优秀人才，受到社会的广泛赞誉。学院始终坚持以人为本，努力为学生的成长成才创造良好的育人环境，近年来先后被授予“全国暑期大学',693033);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('玉林师范学院',' ','广西',1,4,0,0,0,' ','暂空',' ','http://www.ylnu.net/index.asp','玉林师范学院坚持立足玉林、面向广西、服务基层的办学宗旨，逐步形成了自身的办学传统和鲜明的办学特色，为民族地区教育文化事业和经济的发展提供了强有力的人才和智力支持。玉林师范学院坐落在广西壮族自治区玉林市，是桂东南地区唯一的一所本科院校。桂东南面积2.3万多平方公里，占广西总面积的1/10；人口1,050多万，占广西总人口的1/5强，是广西最大的侨乡和最大的客家人聚居区。玉林师范学院是在我国高等教育大众化背景下广西第一所新建本科院校。学校的办学历史最早可以追溯到1945年创建的广西省立鬱林师范学校，',914769);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西艺术学院',' ','广西',1,8,0,0,0,' ','暂空',' ','http://zsb.gxai.edu.cn/','广西艺术学院位于广西壮族自治区首府南宁市，是全国7所综合性普通本科高等艺术院校之一，2012年成为中华人民共和国文化部与广西壮族自治区人民政府共建的高校。学校成立于1938年，前身是我国著名音乐家满谦子和现代杰出画家、美术教育家徐悲鸿以及著名作曲家、音乐教育家吴伯超于1938年在桂林倡议建立的“广西省会国民基础学校艺术师资训练班”(简称“艺师班”)。1960年更名为广西艺术学院，由专科学校升格为本科院校。在70多年的办学历史中，一批国内外知名的艺术家和艺术教育家，如满谦子、徐悲鸿、吴伯超、阳太阳',337121);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西民族大学',' ','广西',1,9,0,0,0,' ','暂空',' ','http://www.gxun.edu.cn/','学校创办于1952年3月，原为中央民族学院（今中央民族大学）广西分院，1953年改称广西省民族学院，1958年更名为广西民族学院，2006年2月4日教育部批准更名为广西民族大学。是国家民委和广西壮族自治区人民政府共建，广西壮族自治区“十一五”重点建设的综合性民族高等学校。在党和政府的正确领导下，经过56年的发展和积淀，广西民族大学已经发展成为学科门类较为齐全、师资力量较为雄厚、科研水平较高、学科建设与教育质量较好的综合性民族大学。学校在发展过程中，得到党和国家领导人的亲切关怀。1958年在南宁人',2469903);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('百色学院',' ','广西',1,0,0,0,0,' ','暂空',' ','http://www.bsuc.cn','百色学院是经教育部批准成立的一所全日制普通本科院校，坐落在广西百色市城区中心。现有在职教职工487人，专任教师295人。其中，正高职称17人，副高97人，博士、硕士及研究生学历94人，曾到国外研修教师8人。聘有知名客座教授15人；长期聘请3名外籍教师来院任教。学院教书育人成绩突出，学术气氛浓厚，学术成果颇丰。近年来，有9位教师荣获“曾宪梓教育基金奖”，有12位教师被评为全国优秀教师、全国“三育人”先进个人、全国教育先进工作者，有11位教师被评为自治区优秀教师等。学院现设有中文系、政治与法律系、外',703027);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川大学',' ','四川',1,0,1,1,1,' ','暂空',' ','http://www.scu.edu.cn','岷峨挺秀，锦水含章。巍巍学府，德渥群芳。四川大学是教育部直属全国重点大学，是布局在中国西部、“985工程”和“211工程”重点建设的高水平研究型综合大学。学校现任党委书记杨泉明教授、校长谢和平院士。四川大学地处中国历史文化名城——“天府之国”的成都，有望江、华西和江安三个校区，占地面积7050亩，校舍建筑面积254万平方米。校园环境幽雅、花木繁茂、碧草如茵、景色宜人，是读书治学的理想园地。四川大学由原四川大学、原成都科技大学、原华西医科大学三所全国重点大学经过两次合并而成。原四川大学起始于189',15356329);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆大学',' ','重庆',1,0,1,1,1,' ','暂空',' ','http://www.cqu.edu.cn/','重庆大学是教育部直属的全国重点大学，是国家“211工程”和“985工程”重点建设的高水平研究型综合性大学。学校地处国家西南工商业重镇、长江上游经济中心——重庆市，校园坐落在人文荟萃的文化名区——沙坪坝区。重庆大学创办于1929年，早在20世纪40年代就成为拥有文、理、工、商、法、医等6个学院的国立综合性大学。马寅初、李四光、何鲁、冯简、柯召、吴宓、吴冠中等大批著名学者曾在学校执教。经过1952年全国院系调整，重庆大学成为国家教育部直属的、以工科为主的多科性大学，1960年被确定为全国重点大学。改',10195923);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南交通大学',' ','四川',1,1,1,1,0,' ','暂空',' ','http://www.swjtu.edu.cn/','西南交通大学创建于1896年，是国家首批“211工程”、“特色985工程”重点建设、首批进入“2011计划”并设有研究生院的教育部直属全国重点大学，坐落在国家历史文化名城、现代化国际化大都市——成都，前身为山海关北洋铁路官学堂(ImperialChineseRailwayCollege)，是我国近代建校最早的国立大学之一，是我国土木工程、交通工程、矿冶工程高等教育的发祥地，是“交通大学”(ChiaoTungUniversity)两大最早源头之一，以“唐山交通大学”之名享誉海内外，素有“东方康奈尔',11037152);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('电子科技大学',' ','四川',1,1,1,1,1,' ','暂空',' ','http://www.uestc.edu.cn/','电子科技大学是教育部直属、国家“985工程”“211工程”重点建设大学，坐落于有“天府之国”之称的西部经济、文化、交通中心——四川省成都市。学校原名成都电讯工程学院，是1956年在周恩来总理的亲自部署下，由交通大学(现上海交通大学、西安交通大学)、南京工学院(现东南大学)、华南工学院(现华南理工大学)的电讯工程有关专业合并创建而成，1960年被中共中央列为全国重点高等学校，1961年被中共中央确定为七所国防工业院校之一，1988年更名为电子科技大学，1997年被确定为国家首批“211工程”建设的',6720430);
