insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南石油大学',' ','四川',1,1,0,0,0,' ','暂空',' ','http://www.swpu.edu.cn/','西南石油大学是新中国创建的第二所石油本科院校，是一所中央与地方共建、以四川省人民政府管理为主的高等学校。20世纪50年代中后期，我国石油工业相当落后，远远不能满足国家建设的需要，引起党和国家高度关注，毛泽东主席亲临四川隆昌气矿视察，中共中央总书记邓小平亲自主持四川油气田勘探开发。1958年3月，位于南充东观、广安武胜、遂宁大英的三口探井喷出高产油流，震动全国，石油工业部部长余秋里将军坐镇南充，打响了川中石油大会战。为了加快开发四川石油天然气资源，也为西南协作区发展石油天然气工业培养技术干部，国家',4654862);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都理工大学',' ','四川',1,1,0,0,0,' ','暂空',' ','http://www.cdut.edu.cn/default.html','学校概况1956年3月15日，国务院批准建立成都地质勘探学院。同年3月27日，高等教育部和地质部联合发文，以重庆大学地质系、西北大学和南京大学地质系的工科部分为基础同时抽调北京地质学院、长春地质勘探学院部分干部教师组建成都地质勘探学院，建校当年即开始招收本科生。学校建校后陆续部分或成建制的迁入了原北京地质学院石油系和二系部分、三系整体。1960年，学校开始招收研究生。1983年，学校成为国家恢复学位制度后首批招收博士生的高校。成都地质勘探学院1958年更名为成都地质学院，1993年更名为成都理工',7914196);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆邮电大学',' ','重庆',1,1,0,0,0,' ','暂空',' ','http://www.cqupt.edu.cn/','重庆邮电大学是国家布点设立并重点建设的几所邮电高校之一，是工业和信息化部与重庆市共建的一所以信息科学技术为特色和优势，在邮电通信行业、信息产业领域，在西部乃至全国具有重要地位和影响的教学研究型大学。学校发轫于1950年，在抗战时期交通部邮政总局原址上开办邮政人员培训班;在此基础上，先后举办西南邮电分校、重庆邮电学校和重庆电信学校;1959年由国务院命名为重庆邮电学院,并开始本科教育;1965年成为当时四川省招收研究生的10所院校之一;“文革”期间，于1970年改建为电信总局529厂，1973年改',4744930);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆交通大学',' ','重庆',1,1,0,0,0,' ','暂空',' ','http://www.cqjtu.edu.cn/','重庆交通大学创办于1951年，是一所具有“学士—硕士—博士”多层次人才培养体系，专业覆盖工、管、理、经、文、法等学科门类的多科性大学。学校由南岸校区、双福校区及大坪分部（重庆西南水运工程科学研究所）组成。南岸校区位于重庆市南岸区学府大道；双福校区位于重庆西部新城江津双福新区福星大道；大坪分部（重庆西南水运工程科学研究所）位于重庆市渝中区大黄路。重庆交通大学占地面积近3200亩，校舍建筑面积72万多平方米；现有教学科研仪器设备14000余台（套），总值2.28亿元；图书馆建筑面积3.3万平方米，现',5247370);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南科技大学',' ','四川',1,1,0,0,0,' ','暂空',' ','http://www.swust.edu.cn/','西南科技大学坐落于大禹之乡、李白故里、人杰地灵的中国科技城——四川省绵阳市。建校以来，学校几经搬迁，走过了创业、发展、壮大的不平凡历程。学校扎根西部，始终坚持“育人为本、德育为先、科学理性、开放包容、彰显卓越”的办学理念和“质量立校、人才强校、特色建校、和谐兴校”的办学思路，形成了“艰苦奋斗，拼搏创新”的西南科技大学精神与“厚德、博学、笃行、创新”之校训。跨入新世纪，学校在西部大开发和绵阳科技城建设的历史机遇中焕发出新的生机。学校发展成为国家重点建设的西部14所高校之一，全国现代远程教育试点学校',4699104);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都信息工程大学',' ','四川',1,1,0,0,0,' ','暂空',' ','http://www.cuit.edu.cn/','成都信息工程大学是四川省和中国气象局共建、四川省重点发展的省属普通本科院校，是以信息学科和大气学科为重点，以学科交叉为特色，工学、理学、管理学为主要学科门类，工学、理学、管理学、经济学、文学、法学、艺术学等多学科协调融合发展的多科性大学，是四川信息产业、中国气象事业、国家统计事业、国防建设人才培养和科学研究的重要基地。学校创建于1951年，前身为中国人民解放军西南空军气象干部训练大队，1956年改制为成都气象学校，1978年升格为成都气象学院;2000年由直属中国气象局划转为四川省人民政府管理，',3458361);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川理工学院',' ','四川',1,1,0,0,0,' ','暂空',' ','http://www.suse.edu.cn','四川理工学院是一所全日制公办省属本科院校，前身是1965年建立的原华东化工学院(现华东理工大学)西南分院。学校座落于享有“恐龙之乡、千年盐都、南国灯城”美誉的国家历史文化名城——四川省自贡市，是国家“中西部高校基础能力建设工程”重点建设高校，是四川省人民政府授予的“四川省高新技术产业示范科研单位”。学校占地面积2000余亩，有汇东、营盘、黄岭三个校区，国有资产总值10.8亿元，其中各类教学科研仪器设备总值2.42亿元，图书馆藏书丰富，馆藏纸质文献总量约233.6万册，电子图书355万种。学校设施',3227979);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西华大学',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.xhu.edu.cn','西华大学是2003年4月16日经教育部批准，由原四川工业学院和原成都师范高等专科学校合并组建的省属重点综合性大学。2008年9月25日四川经济管理(干部)学院并入西华大学。四川工业学院的前身四川农业机械学院建于1960年，是国家为实现农业机械化在当时全国7个大区分别布点所建立的综合性农业机械学院之一，1978年被四川省政府列为省属重点大学，1983年更名为四川工业学院。学校现有校本部、彭州校区、人南校区、安德校区。校园面积近3000亩。校本部坐落于中国历史文化名城成都，毗邻国家高新技术开发区西区',4804874);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国民用航空飞行学院',' ','四川',1,1,0,0,0,' ','暂空',' ','http://www.cafuc.edu.cn','或许是古蜀先民梦寐以求的飞天夙愿与现代文明偶然的“链接”，在古蜀国三星堆文化的都四川广汉，中国民用航空飞行学院如岿立的长梯，为翱翔蓝天的寻梦人铺筑了一条通天大道。作为亚洲最大、世界知名的“蓝天大学”――中国民用航空飞行学院,除了为新中国培养输送了占中国民航直属航空公司总数90%以上的飞行人才和大量的其他高级工程技术人才这样辉煌的业绩外，学院还拥有近20亿的固定资产和1100多公顷的校园面积；有由夏延、西门诺尔、TB、贝尔等8种机型共107架初、中、高三级先进飞机组成的教练机队；有亚太地区最大的飞',2525990);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川农业大学',' ','四川',1,2,0,1,0,' ','暂空',' ','http://www.sicau.edu.cn','四川农业大学是一所以生物科技为特色，农业科技为优势，农、理、工、经、管、医、文、教、法、艺多学科协调发展的国家“211工程”重点建设大学，也是教育部本科教学工作水平评估优秀高校。学校源自1906年创办的四川通省农业学堂，1927年和1935年两次并入四川大学，1956年由四川大学农学院整体迁往原西康省（1955年撤销）省会雅安成立四川农学院，1978年恢复招收研究生，1985年更名为四川农业大学，1998年批准成为国家“211工程”重点建设大学。学校现有3个校区，即雅安校本部、成都校区和都江堰校',4202253);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西昌学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.xcc.sc.cn/','学校概况西昌学院是经教育部批准建立的省属公办综合性全日制普通本科院校，前身为1939年北洋大学工学院内迁至西昌创建的国立西康技艺专科学校，距今已有70余年的办学历史。学校面向全国二十一个省、自治区、直辖市招生，有全日制在校生16400余人。学校地处国家级优秀旅游城市——四川省西昌市。西昌是全国知名的旅游胜地，这里山川秀丽，风景如画，四季如春，是“一座春天栖息的城市”，被称为“月亮城”。西昌还是卫星升起的地方，被誉为“航天城”。西昌交通十分便利，四通八达，航班、铁路、公路可通至全国各地。成都至西昌',1332847);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆医科大学',' ','重庆',1,3,0,0,0,' ','暂空',' ','http://www.cqmu.edu.cn/','重庆医科大学创建于1956年，由上海第一医学院(现复旦大学上海医学院)分迁来渝组建而成，原名重庆医学院，1985年更名为重庆医科大学。学校是国务院学位委员会批准的首批具有博士和硕士学位授予权的单位，重庆市人民政府、国家卫生计生委和教育部共建高校。学校由位于渝中区的袁家岗校区和位于重庆大学城的缙云校区组成，校园总面积2650余亩。其中，缙云校区占地2100余亩，校园环境优美，风景如画。学校具有招收博士后研究人员，港澳台学生和外国留学生资格。现有全日制在校学生近28000人，其中研究生5100余人，',5303784);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南医科大学',' ','四川',1,3,0,0,0,' ','暂空',' ','http://www.lzmc.edu.cn','泸州医学院是四川省省属高等院校，坐落在川、渝、滇、黔结合部的国家历史文化名城、国家卫生城市——泸州市。学院始建于1951年，其前身是西南区川南医士学校，1959年升格为泸州医学专科学校，1978年升格为本科院校并更名为泸州医学院。1982年获得学士学位授予权，1993年获得硕士学位授予权，1998年顺利通过教育部本科教学工作合格评价，2003年获准成为临床医学专业学位硕士研究生培养单位，2006年顺利通过教育部本科教学工作水平评估并被评为优秀等级。办学规模学院现有全日制在校学生12,000余人，',2254476);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都中医药大学',' ','四川',1,3,0,0,0,' ','暂空',' ','http://www.cdutcm.edu.cn/','成都中医药大学原名成都中医学院，创建于1956年，是在周恩来等党和国家第一代领导集体的亲切关怀下，经国务院批准建立的我国最早的四所中医药高等院校之一，1995年经国家教委批准更名为成都中医药大学，2006年原四川省卫生管理干部学院、四川生殖卫生学院并入成都中医药大学。历经50多年的建设，成都中医药大学已经成为一所以中医药学科为主体，兼有理、工、管、文、农、教等多学科相关专业交叉渗透、协调发展的四川省属重点高等学校，四川省人民政府与国家中医药管理局共建高校，教育部本科教学工作水平评估优秀学校。学校',3602473);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('川北医学院',' ','四川',1,3,0,0,0,' ','暂空',' ','http://www.nsmc.edu.cn/','川北医学院坐落在四川省历史文化名城、三国文化发祥地——南充市。60余年来，学校始终坚持“艰苦奋斗、自强不息”的办学精神，秉承“敬业、博爱、求是、创新”的校训，形成了“以人为本，以质量为生命”的办学思想，确立了“知识、能力、素质”三位一体的人才培养模式，现已发展成为一所以医学教育为主体，医、理、文、管、工多学科协调发展，有全日制在校研究生、本专科学生、留学生等各类在校生16000余人的省属高等医学院校。办学历史学校创办于1951年，前身为西南军政委员会卫生部下属川北医士学校，由四川阆中德启护士学校',1626772);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南大学',' ','重庆',1,0,1,1,0,' ','暂空',' ','http://www.swu.edu.cn/','西南大学是教育部直属重点综合大学，国家“211工程”和“985工程优势学科创新平台”建设学校，位于重庆市北碚区。学校起源于1906年4月建立的川东师范学堂，办学历史已逾百年。国学大师吴宓、中国土壤学科开拓者侯光炯院士等生前曾执教于此。经过百年办学发展，学校积淀了深厚的人文底蕴，铸就了“特立西南、学行天下”的大学精神，形成了“化西南自然人文，育实践创新人才，富社会国计民生，融世界发展潮流”的办学特色。学校坐落于国家级风景名胜区缙云山下，风景秀丽的嘉陵江畔，依山傍水，风景如画。学校占地总面积9629',10099602);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南大学计算机与信息科学学院',' ','重庆',1,0,0,0,0,' ','暂空',' ','http://computer.swu.edu.cn','西南大学计算机与信息科学学院是以工为主，融理、工、管为一体的综合性学院。经过多年的不懈努力，已发展成为西南地区计算机学科领域内教学、科研综合实力强，影响力大的学院，在重庆市国民经济建设中发挥着重要的作用。在全国6所招收免费师范生的学校中，西南大学计算机与信息科学学院有计算机科学与技术和教育技术两个专业招收师范生。学院现有在校全日制本科生2500余人，在校全日制研究生260人。在职教职工211人，教学科研人员147人，其中正高13人，副高53人，博士29人，硕士97人，师资规模及招生规模目前在西南',59233);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川师范大学',' ','四川',1,4,0,0,0,' ','暂空',' ','http://www.sicnu.edu.cn','四川师范大学是四川省人民政府举办的全日制综合性省属重点大学，是四川省举办师范类本科专业最早、师范类院校中办学历史最为悠久的大学。学校位于四川省省会——成都市，现有狮子山、成龙、广汉科教园、东校区四个校区，校园面积3300余亩。现有全日制本专科学生38000余人，博士与硕士研究生4000余人。现有各类教学、科研人员3000余人，其中，具有高级专业技术职务的教师近1000人，具有博士和硕士学位的教师1600余人。四川师范大学创建于1946年，其诞生与东北大学有直接的历史渊源。抗战初期，东北大学内迁到',5044148);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆师范大学',' ','重庆',1,4,0,0,0,' ','暂空',' ','http://www.cqnu.edu.cn','重庆师范大学创办于1954年，办学历史可追溯到1906年创立的官立川东师范学堂。学校是一所以文理为主，多学科协调发展的多科性高等学府，坐落在历史文化名城重庆，校园面积2804亩,包括大学城校区、沙坪坝校区和北碚校区。学校是全国绿化模范单位、重庆市依法治校示范校、重庆市文明单位、重庆市德育工作先进集体、重庆市园林式单位、重庆市十佳市容整洁单位、重庆市森林校园。拥有“国务院侨务办公室华文教育基地”、“教育部全国重点建设职教师资培训基地”、“国家汉办汉语国际推广师资培训基地（重庆）”等三个国家级基地，',3829262);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西华师范大学',' ','四川',1,4,0,0,0,' ','暂空',' ','http://www.cwnu.edu.cn/','西华师范大学是四川省重点建设的师范大学，坐落在中国优秀旅游城市、四川省历史文化名城、川东北经济文化中心——南充市。建校62年来，学校植根三国文化之沃土，汲取嘉陵江水之灵韵，与祖国共奋进，与时代同发展，谱写了以教书育人、创新发展为主旋律的华美篇章。历史沿革西华师范大学创建于1946年5月，先后历经私立川北农工学院、私立川北大学、四川师范学院、南充师范专科学校、南充师范学院、四川师范学院等几个阶段，2003年4月16日，经四川省人民政府和教育部批准，学校更名为西华师范大学。经过几代人的不懈努力，学校',2543455);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('绵阳师范学院',' ','四川',1,4,0,0,0,' ','暂空',' ','http://www.mnu.cn','绵阳师范学院是四川省属全日制普通本科院校，座落于中国科技城——四川省绵阳市。学校于2002年经国家教育部批准，由原绵阳师范高等专科学校和绵阳教育学院合并组建成立。2006年成为学士学位授予权单位；2007年以良好成绩通过国家教育部本科教学工作水平评估；2011年，获准成为“服务国家特殊需求项目”试点单位，招收环境工程专业学位硕士研究生。学校校园占地面积近2000亩，建筑面积53万平方米，建有体育馆、网球场、游泳池等各类现代化服务设施。图书馆藏书148万余册，各类电子数据库100个。固定资产总值8',1178698);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内江师范学院',' ','四川',1,4,0,0,0,' ','暂空',' ','http://www.njtc.edu.cn','内江师范学院是经教育部批准建立的一所省属本科院校。学校所在地—内江市，风景如画，人文荟萃，是驰名中外的“大千故里，书画之乡”。学校位于内江市东桐路，地处沱江之滨，校园依山傍水，绿树掩映，碧草如茵，是莘莘学子求学的理想场所。建校后，学校秉承内江师范教育近百年来艰苦创业，重视师德师风和师范技能的传统，借鉴半个世纪举办高等教育的办学经验，发扬团结奋进，负重自强的办学精神，学校已成为一所初具规模，以教师教育为主，文学、理学、教育学、管理学、历史学、法学、工学等多学科协调发展的地方性本科师范院校。学校占地',968777);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宜宾学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.yibinu.cn/web/index.aspx','宜宾学院是2001年5月经国家教育部批准由原宜宾师范高等专科学校和四川教育学院宜宾分院合并组建的一所省属全日制综合性普通本科院校。学校位于“万里长江第一城”、“国家历史文化名城”、“全国白酒之都”宜宾市。全校占地1050亩，校舍面积38万平方米，教学仪器设备总值上亿元，藏书274万册(含电子图书145万册)，拥有固定资产近6亿元。学校设有文学与新闻传媒、数学、物理与电子工程、外国语、政府管理、生命科学与食品工程、化学与化工、法学、教育科学、体育、计算机与信息工程、美术与艺术设计、音乐与表演艺术、',1408845);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆文理学院',' ','重庆',1,0,0,0,0,' ','暂空',' ','http://www.cqwu.net/','重庆文理学院是重庆市人民政府主办的全日制普通本科高等学校。其前身重庆师范高等专科学校和渝州教育学院分别创办于1976年和1972年；2001年5月两校合并组建为渝西学院；2005年4月，学校更名为重庆文理学院。学校坐落在距重庆市主城区56公里的永川区，有红河、星湖两个校区，校园占地面积1719亩，校舍建筑面积71万平方米，馆藏图书175万册，教学仪器设备总值1.7亿元；现代教育技术中心和电子阅览室设施齐备，校园网设备先进，教学、科研及生活设施完善；现有教职员工1200余人，其中中国工程院院士1名',2235093);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆三峡学院',' ','重庆',1,0,0,0,0,' ','暂空',' ','http://www.sanxiau.edu.cn/','重庆三峡学院简介(重庆报考代码：5009)硕士研究生招生培养单位教育部应用技术大学联盟成员单位东南大学对口支援单位重庆三峡学院是三峡库区唯一的一所多科性本科院校(教育部代码10643)，学校创办于1956年。2013年，学校增列为硕士学位授予单位。学校坐落于千年古城、移民之都——重庆市万州区，占地面积1980余亩，建有沙龙、百安两个校区，藏有各类图书资料296万余册(种)。经过近60年的发展，重庆三峡学院已形成了以本科教育为主体，研究生教育、成人教育和留学生教育协调发展的办学格局。现有全日制在校',1952416);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川文理学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.sasu.cn/index.asp','四川文理学院创建于1976年，时称达县师范学院，1978年经国务院批准正式纳入国家全日制普通高校行列，定名为达县师范专科学校，1993年更名为达县师范高等专科学校，2006年经教育部批准，正式升格为四川文理学院，是目前川东地区唯一的全日制本科院校。学院位于四川省东部重镇达州市，襄渝、达成、达万铁路在此交汇，210、318国道和达渝、达成高速公路贯通全境，河市机场可直航成都、西安、武汉、广州等城市，交通十分便利。学院占地总面积近800亩，其中老校区校园依山傍水、风景秀丽，新校区位于风光旖旎的莲花湖',1318215);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阿坝师范高等专科学校',' ','四川',0,4,0,0,0,' ','暂空',' ','http://www.abtc.edu.cn/','阿坝师范高等专科学校创办于1978年，是经国务院批准建立的高等院校，也是目前四川省内唯一一所省属公办全日制师范高等专科学校。学校是教育部中央教科所艺术教育中心副理事长单位、教育部中央教科所艺术教育中心艺术教育实验基地、教育部中国教师发展基金会常务理事单位。学校是中共中央、国务院和中央军委表彰的“全国抗震救灾英雄集体”，多次获得省级民族团结先进集体、高校平安校园建设先进单位、卫生先进单位、文明单位，高校后勤服务先进集体等称号。学校原坐落于汶川县城美丽的岷江河畔。2008年汶川特大地震使学校30多年',245469);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长江师范学院',' ','重庆',1,4,0,0,0,' ','暂空',' ','http://www.yznu.cn/','学校是一所重庆市属普通本科院校，2006年9月，由涪陵师范学院更名为长江师范学院，有80多年的办学历史，在社会各界享有良好办学声誉，在重庆市高校布局结构中具有重要地位，为地方经济建设和社会发展做出了突出贡献。在新形势下，学校一如既往地担当起服务地方经济建设与社会发展的责任与使命，积极进取，努力开创统筹城乡发展的地方高校建设新局面。学校所在地涪陵区，位于长江与乌江交汇处，素有“乌江门户”、“千里乌江第一城”之称，是重庆市经济强区。涪陵是先秦时期的巴国国都，文化积淀深厚，多元而独特的巴枳文化、易理文',1486282);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('乐山师范学院',' ','四川',1,4,0,0,0,' ','暂空',' ','http://www.lstc.edu.cn/','乐山师范学院始建于1978年，2000年3月经教育部批准，由原乐山师范高等专科学校、乐山教育学院合并升格为四川省省属普通本科院校。2006年通过教育部本科教学工作水平评估并获得良好等级。2012年纳入教育部“东部高校对口支援西部高校计划”，由武汉大学对口支援建设。学校地处世界自然与文化双遗产所在地、中国最佳旅游目的地、世界文化名人郭沫若的故乡——被誉为“森林在城市中，城市在山水中”的乐山市，东望乐山大佛，南临大渡河，西眺峨眉山，北接城市绿心，1938-1946年，国立武汉大学曾在此办学八年。学校',1164112);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南财经大学',' ','四川',1,6,1,1,0,' ','暂空',' ','http://www.swufe.edu.cn','西南财经大学是教育部直属的国家重点大学，也是国家“211工程”重点建设大学。学校坐落在享有“天府之国”美誉的成都平原，由光华、柳林两个校区组成，总占地面积2300余亩。校园绿树成荫，环境优雅，是著名的“园林式院校”和“全国绿化模范单位”。学校原名四川财经学院，是中国办学历史悠久的综合性财经大学之一。1952-1953年经两次全国高等院校院系调整，由西南地区17所财经院校、综合大学的财经系科合并组成，荟萃了西南地区的财经教育专家和教授，我国著名经济学家彭迪先、陈豹隐、李孝同、梅远谋、刘洪康、杨佑之',8243459);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南政法大学',' ','重庆',1,11,0,0,0,' ','暂空',' ','http://www.swupl.edu.cn/','西南政法大学位于中国长江上游地区的中央直辖市重庆，是新中国最早建立的高等政法学府，改革开放后全国首批重点大学，全国首批卓越法律人才教育培养基地，教育部与重庆市人民政府共建高校。一、历史沿革西南政法大学的前身为1950年创建的由刘伯承元帅担任校长的西南人民革命大学。1953年，以西南人民革命大学政法系为基础，合并当时的重庆大学法学院、四川大学政法学院、重庆财经学院法律系、贵州大学法律系、云南大学法律系，正式成立西南政法学院，郭沫若先生题写院名，首任院长是抗日民族英雄、原东北抗日联军第二路军总指挥周',5125868);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都体育学院',' ','四川',1,7,0,0,0,' ','暂空',' ','http://www.cdsu.edu.cn','成都体育学院是西南地区唯一的高等体育学府。原隶属国家体育总局，现由国家体育总局与四川省人民政府共建，以四川省管理为主。学院前身系建于1942年的四川省立体育专科学校，1950年更名为成都体育专科学校。1953年在“大区布局”的历史背景下，成都体育专科学校全建制转为全国六大体育学院之一，时名西南体育学院，1956年更为现名。经过近70年的积淀更新,学院现已成为在西南地区享有盛誉,在全国广有影响的体育院校。学院坚持党的教育方针，秉承“厚德博学，睿智健体”的校训，坚持以质量求生存，坚持以特色谋发展，坚',734677);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川音乐学院',' ','四川',1,8,0,0,0,' ','暂空',' ','http://www.sccm.cn','四川音乐学院的前身是创建于1939年秋的“四川省立艺术专科学校”，后更名为“西南音乐专科学校”。1959年6月经国家教育部批准为现名。目前，学院已发展成为我国一所专业门类齐全、师资力量雄厚、教学设施完善、教学成果显著的多学科、多层次的高等艺术院校。现任党委书记柴永柏，院长敖昌群。学院新、老校区现共有21个专业教学系（院）,17个专业。涵盖艺术类15个专业及2个交叉型专业（工业设计、公共事业管理），共计115个专业方向。学院拥有西南地区音乐艺术文献最丰富的图书馆和声像馆。学院有国务院学位委员会授予',596284);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川美术学院',' ','重庆',1,8,0,0,0,' ','暂空',' ','http://www.scfai.edu.cn','四川美术学院座落在美丽的山城---重庆市西郊的长江之畔。学院自1940年建校以来，以完善的、独特的美术教育体系、活跃的学术氛围、卓越的艺术成就，驰誉国内外，成为“长江上游的一颗璀璨的艺术明珠。”1938年李有行、沈福文、雷圭元、庞薰琴等热血青年，在成都创办了“中华工艺社”，表达振兴中华之意。1940年成立由李有行任校长的四川省艺术专科学校。当时成都还有另一所南虹艺术专科学校也是人才集聚的地方，王朝闻、张大千、徐悲鸿、黄宾虹、史岩、叶正昌、程丽娜等都曾先后在那里执教、讲学，后与四川省艺术专科学校合',343978);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南民族大学',' ','四川',1,9,0,0,0,' ','暂空',' ','http://www.swun.edu.cn/','西南民族大学是一所包括文、史、哲、经、管、法、教、理、工、农、医、艺等12个学科门类的综合性民族高等学校，坐落于天府之国，芙蓉之都的锦绣成都，与闻名中外的武侯祠毗邻。学校前身为西南民族学院，创建于1950年7月，于1951年6月1日正式成立，2003年4月更名为“西南民族大学”，是在周恩来、邓小平、王维舟等老一辈无产阶级革命家亲自关怀下，新中国最早建立的民族院校之一，由国家民族事务委员会主管。学校占地面积近3000亩，分为武侯校区（老校区）、航空港校区（新校区）、太平园校区和阿坝州红原县青藏高原',3847174);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州大学',' ','贵州',1,0,0,1,0,' ','暂空',' ','http://www.gzu.edu.cn','贵州大学创建于1902年，历经贵州大学堂、省立贵州大学、国立贵州农工学院、国立贵州大学等时期，1950年10月定名为贵州大学。1997年8月，与贵州农学院等院校合并。2004年8月，与贵州工业大学合并；同年12月，成为教育部与贵州省人民政府共建高校；2005年9月，进入国家“211工程”大学建设行列；2012年，被列为国家“中西部高校综合实力提升工程”14所高校之一。1951年11月，毛泽东同志亲笔题写“贵州大学”校名。1985至1988年，胡锦涛同志在贵州任省委书记时，十分关心贵州大学的建设和',4030025);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('遵义医学院',' ','贵州',1,3,0,0,0,' ','暂空',' ','http://www.zmc.edu.cn/','一、历史沿革遵义医学院坐落在历史文化名城——遵义市，是贵州省属重点建设高校。学校创建于1947年，是中国共产党创办的第一所本科医学院校。曾用大连医学院、关东医学院、大连大学医学院等名称。20世纪五六十年代，学校为卫生部管理高校。1969年为支援“三线”建设举校南迁，更名为遵义医学院。经过69年的建设和发展，学校已成为一所以医学为主，涵盖文、理、工、管理等多个学科协同发展，办学特色鲜明、办学实力雄厚的省属重点建设高校。学校由校本部和珠海校区组成，占地面积3300余亩，有全日制在校生18000余人(',1772311);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵阳中医学院',' ','贵州',1,3,0,0,0,' ','暂空',' ','http://www.gyctcm.edu.cn','贵阳中医学院创建于1965年，位于贵州省省会贵阳市，是贵州省唯一的高等中医药学府。改革开放至今，学院抓住机遇，在狠抓学科建设的基础上，一方面扩大办学规模，一方面调整专业结构，“规模”、“结构”、“质量”、“效益”协调发展，取得了令人瞩目的成绩。2006年，经贵州省人民政府批准我院被列为“省级重点支持建设高校”。2007年11月教育部本科教学工作水平评估专家组进校考察评估成绩为优秀。学院现有南、北两个校区。北校区位于风景秀丽的南明河畔、甲秀楼前；南校区为素有贵阳市“森林之肺”美誉的长坡岭林场所环抱',874643);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州师范大学',' ','贵州',1,4,0,0,0,' ','暂空',' ','http://www.gznu.edu.cn','黉门曜日月桃李竞芳菲欢迎莘莘学子报考贵州师范大学亲爱的同学们，贵州师范大学真诚地欢迎你们!贵州师范大学有着悠久的办学历史和优良的办学传统。1941年创建时，名为“国立贵阳师范学院”，是当时全国八所国立师范学院之一。1985年更名为“贵州师范大学”。现在是贵州省省属重点大学、教育部与贵州省人民政府共建的高等学校，办学实力雄厚。贵州师范大学现有宝山、花溪、白云三个校区，占地面积3000余亩。校区环境优美，设施齐备，交通便利。学校现有20个学院、1所独立学院(求是学院)，有全日制在校本科学生3.3万余',2239148);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('遵义师范学院',' ','贵州',1,4,0,0,0,' ','暂空',' ','http://www.zync.edu.cn/index.html','遵义师范学院位于闻名中外、有“红色圣地·醉美遵义”美誉的国务院首批革命历史文化名城--遵义市，是一所拥有百年师范传统的学府。2007年，学校以良好的成绩顺利通过国家教育部本科教学工作水平评估;2011年5月学校被教育部确定为“红色经典艺术教育示范基地”;2011年12月被授予“全国文明单位”称号;2013年学校获得教育部人文社科重点研究基地—中国共产党革命精神与文化资源研究中心;为纪念遵义会议召开80周年创作的音乐舞蹈史诗《遵义会议放光辉》获教育部第八届全国高校校园文化建设优秀成果评选二等奖;连',907511);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('铜仁学院',' ','贵州',1,4,0,0,0,' ','暂空',' ','http://www.gztrc.edu.cn','铜仁学院位于贵州省东北部著名旅游胜地——铜仁市。2010年获得贵州省高等学校学士学位授予权单位资格。2013年顺利通过教育部本科教学工作评估;2014年加入了教育部指导的全国应用大学(学院)联盟。学校先后被评为全国文明单位、全国民族团结进步模范单位、全国大中专学生“三下乡”社会实践活动先进单位等荣誉称号。学校现有38个本科专业，涵盖理、工、文、艺、经、教育等十大学科门类，有全日制在校学生8517人，校园占地1000余亩，教学仪器设备总值9800余万元，纸质图书资料74万册。有专兼职教师526名，',564606);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兴义民族师范学院',' ','贵州',1,4,0,0,0,' ','暂空',' ','http://www.xynun.edu.cn/','兴义民族师范学院具有悠久的办学历史，缘起于1813年的笔山书院，肇基于1978年的兴义师范专科学校，1982年改名为黔西南民族师专，1993年又改名为黔西南民族师范高等专科学校。2009年3月，经教育部批准正式升格为兴义民族师范学院，系省州共建的一所地方性全日制本科院校。学校占地面积1173.29亩，校舍建筑总面积26.16万平方米，现有21个管理部门、14个二级学院，34个本科专业及方向,专业涵盖了文学、理学、工学、法学、教育学、管理学、历史学、艺术学等八大学科门类，生源遍布全国18个省（区）',305500);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安顺学院',' ','贵州',1,0,0,0,0,' ','暂空',' ','http://www.asu.edu.cn','安顺学院是2006年经教育部批准组建、国家举办的一所全日制普通本科学院，其前身是安顺师范高等专科学校。学院主校区位于举世闻名的黄果树瀑布以东40公里处的甲级开放城市、国家优秀旅游城市——安顺市的娄湖湖畔。我院校园占地450余亩。校舍面积12万余平方米，有400米跑道标准运动场1个、200米跑道运动场1个、篮球场15个。我院是世行贷款项目单位，教学仪器设备总值2770余万元，藏书58万册（含20万册电子图书），期刊种类600多种。学院教学用房充足，建有物理实验大楼、化学实验大楼、综合教学大楼、电教',452734);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州工程应用技术学院',' ','贵州',1,0,0,0,0,' ','暂空',' ','http://www.gues.edu.cn/','毕节学院是2005年3月经国家教育部批准，由原毕节师范高等专科学校、毕节教育学院合并组建的一所全日制普通本科院校，其前身系1938年成立的贵州省省立毕节师范学校，地处时任中共贵州省委书记胡锦涛同志倡导，经国务院批准建立的毕节“开发扶贫、生态建设”试验区。学院现任党委书记陈永祥，院长张学立。学校有主校区和阳山校区，主校区分为东、西两区，校园占地面积807亩，校舍建筑面积17.9万㎡，教学仪器设备总值2437.24万元，有馆藏图书51万册及清华同方学术期刊全文数据库等电子图书资源，建有校园网、多媒体',687943);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('凯里学院',' ','贵州',1,4,0,0,0,' ','暂空',' ','http://www.kluniv.edu.cn/ ','凯里学院是经教育部批准建立的一所全日制公办普通本科学院。她座落在\"森林之洲\"、\"歌舞之洲\"、全球\"回归自然、返璞归真\"十大圣地之一的黔东南自治州州府凯里市。校园占地面积759454.77平方米，校舍建筑面积17余万平方米。教学仪器设备总价值3000余万元，建有千兆校园网和六个省级评估合格的基础课教学实验室，图书馆藏书达61.64万册。学校现有教职工576人，其中专任教师472人。有正高职称38人、副高职称130人。博士6人、硕士167人，另有在读博士16人，在读硕士56人。省管专家4人，外籍文教',747381);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黔南民族师范学院',' ','贵州',1,4,0,0,0,' ','暂空',' ','http://www.sgmtu.edu.cn','黔南民族师范学院是2000年3月，经教育部批准成立的全国第一所本科层次的民族师范院校，也是贵州省第一所升本的地方高校，其办学历史可追溯到1952年。2007年学校接受教育部本科教学工作水平评估获“良好”等次；2011年获批成为教育硕士专业学位研究生培养单位；2012年经教育部批准，成为“中小学教师国家级培训计划”示范性集中培训项目院校；2013年获建省级院士工作站；2014年加入全国应用技术大学（学院）联盟，并成为教育部20所转型发展案例院校之一。学校位于贵州省黔南布依族苗族自治州首府，中国优秀',520992);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南大学',' ','云南',1,0,0,1,0,' ','暂空',' ','http://www.ynu.edu.cn/','云南大学坐落在春城昆明，其前身东陆大学始建于1922年12月，1923年4月开始招生。独特的区位优势、八十余年的办学历史，成就了今日的云南大学--一所本科专业涵盖文、史、哲、经、管、法、理、工、教育九大学科门类的全国综合性重点大学，也是云南省唯一的国家\"211工程\"重点建设大学、国家西部开发重点建设院校和省部共建院校。学校现有全日制在校本科学生12800余人，研究生8500余人，各类成人教育学生19000人。设有17个学院、7个研究院、2个公共教学单位、1个留学生院，其中软件学院是教育部批准的全',5270471);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明理工大学',' ','云南',1,1,0,0,0,' ','暂空',' ','http://www.kmust.edu.cn','昆明理工大学办学历史悠久，是一所以工为主，理工结合，经济、管理、文学（含艺术）、法律、教育、哲学、医学等多学科协调发展的多科性大学。是云南省规模最大、办学层次和类别较为齐全的重点大学，属中国著名大学之一。2005年被教育部评估为“本科教学工作水平优秀学校”。学校现设有21个学院和3个教学部，有74个本科专业；在全国部分省市设有60个函授站，有61个夜、函大本专科专业。学校面向全国招生，学生毕业面向全国就业。学校所有本科专业及硕士、博士研究生专业都具有招收外国留学生学历教育的资格。学校现有6个博士',7580241);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南农业大学',' ','云南',1,2,0,0,0,' ','暂空',' ','http://www.ynau.edu.cn/','云南农业大学位于春城昆明，北依龙泉山、东傍盘龙江，毗邻著名风景名胜昆明黑龙潭公园，是云南省省属重点大学。历史沿革学校创办于1938年，前身是国立云南大学农学院，建址昆明市呈贡县，1958年独立建成昆明农林学院，迁址昆明北郊黑龙潭，1962年滇南大学、滇西大学并入昆明农林学院，1969年搬迁至大理州宾川县，1970年迁至原曲靖地区寻甸县，1971年与云南农业劳动大学合并成立云南农业大学，1980年搬回昆明北郊黑龙潭。1983年成为硕士学位授予单位，1993年列为云南省属重点大学，2003年成为博士',1802591);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南林业大学',' ','云南',1,2,0,0,0,' ','暂空',' ','http://www.swfu.edu.cn','西南林业大学起始于1939年的云南大学森林系，历经昆明农林学院、云南林业学院、云南林学院、西南林学院，于1978年独立建院，原属林业部直属院校，经2000年全国高等教育布局结构调整，管理体制由国家林业局直属高校调整为“省部共建、以省为主管理”，目前是我国西部地区唯一一所独立设置的高等林业本科院校。校址位于云南省昆明市，校园荣获“全国绿化模范单位”、“全国绿化先进集体”、“全省绿化优秀高校”称号。学校以林学学科为主干，以生物环境类学科为特色，农、理、工、文、法、管等学科交叉融合，协调发展，主要实施',1845589);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南中医学院',' ','云南',1,3,0,0,0,' ','暂空',' ','http://www.ynutcm.edu.cn/','云南中医学院坐落于风景秀丽、四季如春的昆明，创建于1960年，是全国第二批成立的高等中医药本科院校，为云南省唯一一所中医药本科院校。学校设有11个二级学院及2个教学部。有呈贡、白塔两个校区，占地面积871.36亩，呈贡校区是全国高等中医药院校中环境最优美的校园之一，其建筑为云南省“最具艺术特色建筑”。学校始终坚持以中医药学科为主体，发掘和建设民族医药、云南地道医药资源特色学科，支持学科间的融合与渗透，积极发展相关学科。现有涵盖医学、理学、工学、管理学4个学科门类的20个本科专业。有国家级特色专业',1047240);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南师范大学',' ','云南',1,4,0,0,0,' ','暂空',' ','http://www.ynnu.edu.cn/','国立西南联合大学在昆唯一遗脉云南省人民政府和教育部共建高校中西部高校基础能力建设工程100所重点建设高校教育部本科教学水平评估优秀高校云南省省属重点师范大学全国毕业生就业典型经验高校位于春城昆明的云南师范大学是一所具有光荣革命传统和厚重文化底蕴的省属重点师范大学。1938年，在抗日战争烽火中，北大、清华、南开南迁昆明，组建了国立西南联合大学，下设文学院、理学院、工学院及法商学院，同年增设师范学院。西南联大在昆8年，在极度简陋和艰苦的环境中传薪播火、弦歌不辍。华罗庚、朱自清、冯友兰、闻一多等一大批',2354198);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昭通学院',' ','云南',1,4,0,0,0,' ','暂空',' ','http://www.ztu.edu.cn/index','昭通师范高等专科学校创建于1978年，是一所集普通高等全日制专科、成人高等教育、广播电视大学三位一体的公办学校。学校位于云、贵、川三省结合部素有“锁钥南滇，咽喉西蜀”之称的云南省昭通市昭阳区，校园占地面积760亩，现有北校区、南校区和新校区一期工程在建校区，校舍建筑面积113636平米，校园环境优美。学校现有教职工526人，专任教师280人，其中正副教授94人，讲师141人，硕士、博士、博士后124人，常年聘请外籍教师执教。经过30年的建设和发展，学校设有中文系（语文教育、文秘）、外语系（英语教',136636);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('曲靖师范学院',' ','云南',1,4,0,0,0,' ','暂空',' ','http://www.qjnu.edu.cn/','曲靖师范学院是2000年3月经国家教育部批准建立的公办本科院校，于2000年10月27日正式挂牌成立。学院占地面积为1822亩，校舍建筑面积为21.1万平方米，固定资产总值为27836万元，其中教学仪器设备资产总值2303万元，生均教学仪器设备0.38万元/人，图书55万册，并建有校园信息网络。学院现有在职教职员工565人，其中专任教师385人。教师中有教授18人，副教授105人，高级讲师6人，讲师134人；具有博士学位的11人，具有硕士学位的63人。学院现有全日制在校生6267人，成人教育在籍',601006);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('普洱学院',' ','云南',1,4,0,0,0,' ','暂空',' ','http://www.peuni.cn/','思茅师范高等专科学校坐落在昆曼国际大通道上的枢纽城市、滇西南重镇普洱市市区。学校于1978年12月经国务院批准成立，是云南省最早成立的师范专科学校之一，管理体制为“省地共建共管，以省为主”。2004年11月，通过全国高职高专院校人才培养工作水平评估，结论为“良好”。普洱市位于祖国西南边疆云南省的西南部，4.5万平方公里的土地山川秀丽，风物神奇。这里自古就是一个多民族聚居区，世世代代居住着汉、佤、拉祜、哈尼、彝、傣、布朗、瑶、苗、傈僳等14种世居民族。其东南边界与越南、老挝接壤，西南边界与缅甸交界',147488);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('保山学院',' ','云南',1,0,0,0,0,' ','暂空',' ','http://www.bsnc.cn/','保山师范高等专科学校是1978年经国务院批准成立的云南省首批四所师范专科学校之一，位于云南省保山市隆阳区。保山古称永昌，是历史上开发较早的滇西重镇。保山文化昌明，人杰地灵，气候宜人，环境优美，资源丰富。有国家级自然保护区“高黎贡山”，有国家级腾冲“火山热海公园”，有中国十大魅力名镇之首——“和顺”。保山地处大理、德宏、丽江、临沧、怒江、迪庆7州市中心，距大理、丽江较近，境内交通便捷，高速公路已开通，拥有保山、腾冲两个机场，区位优势得天独厚，是历代中缅陆路贸易的重要集散地，随着对外开放的不断扩大，',218576);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('红河学院',' ','云南',1,0,0,0,0,' ','暂空',' ','http://www.uoh.edu.cn','红河学院是2003年4月经国家教育部批准，由原蒙自师范高等专科学校和云南省电大红河分校合并组建的云南省省属公办综合性普通本科院校。现任校党委书记陈永明，校长彭兵。学校位于云南省东南部的红河哈尼族彝族自治州州府蒙自市。蒙自市有着悠久的历史和丰厚的文化底蕴，西南联大文学院和法学院曾在此办学。蒙自离越南老街省较近，所以红河学院成了我国西南离边境线最近的大学。北回归线横穿蒙自市境内，年均气温18.6℃，校园内四季常青，鸟语花香，环境秀丽，景色宜人，被誉为园林式的高等学校，是读书治学的理想之地。红河学院倡',734009);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南财经大学',' ','云南',1,6,0,0,0,' ','暂空',' ','http://www.ynufe.edu.cn/','云南财经大学的前身是始建于1951年的云南省财政干部学校，1981年经教育部批准成为云南财贸学院并开始全日制本科教育，1995年被云南省政府确定为省属重点大学，2006年2月经教育部批准更名为云南财经大学，2007年学校获教育部本科教学工作水平评估优秀等级，2009年被列为博士学位授予立项建设单位，2013年2月通过整体验收。学校历经60余年的建设，已发展成为以经济学、管理学为主，法学、哲学、文学、理学、工学、艺术等学科协同发展的多科性省属重点大学，形成了以本科培养为主，兼有研究生、留学生培养和',4005032);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南艺术学院',' ','云南',1,8,0,0,0,' ','暂空',' ','http://www.ynart.edu.cn/index.shtml','云南艺术学院始建于1959年，是我国西南地区一所特色鲜明、艺术门类齐全的综合性高等艺术院校。建院至今，云南艺术学院汇聚了一大批著名艺术家。留法归来的油画家、雕塑家廖新学教授，小提琴家李丹教授，声乐家方于教授夫妇（《悲惨世界》译者），知名国画家、雕塑家袁晓岑教授等专家、学者先后在学院执教。学院现有教职工625人，其中大学本部有教职工525人，附属学校有教职工100人。在大学本部274名专任教师中，具有教授职称的有35人，具有副教授职称的有103人，享受国务院特殊津贴专家5人，省级有突出贡献的专家4',274277);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南民族大学',' ','云南',1,9,0,0,0,' ','暂空',' ','http://www.ynni.edu.cn/','云南民族大学是一所培养包括汉族在内的各民族高级专门人才的综合性大学。学校坐落在四季如春、风景秀丽的中国昆明，创建于1951年8月1日，是中华人民共和国最早成立的民族高等院校之一，也是国家民族事务委员会与云南省人民政府共建的省属重点大学，学校是国家新增博士授权规划建设单位。党和国家领导人历来十分关心重视云南民族大学的建设和发展。老一辈党和国家领导人毛泽东、刘少奇、朱德、邓小平、江泽民接见过我校师生代表，周恩来、彭德怀、陈毅、班禅额尔德尼·确吉坚赞、李岚清、李铁映等亲临学校视察，关心各族师生的成长进',1771050);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西藏警官高等专科学校',' ','西藏',0,4,0,0,0,' ','暂空',' ','oldname": "西藏警官高等专科学校','西藏警官高等专科学校，其前身是1956年9月成立的西藏公安训练班。1957年7月至1959年3月，经国务院批准迁到北京，在中央人民公安学院（现公安大学）内建立西藏班。1960年4月，中央人民公安学院西藏班迁回拉萨办学，称自治区公安学校。1965年3月改称西藏自治区政法干校。1973年6月改称西藏自治区公安局公安学校，1974年12月恢复西藏自治区政法干校，1978年5月又复称为西藏自治区公安局公安学校。1980年11月经自治区政府批准，易名为西藏自治区人民警察学校，隶属自治区公安厅，并正式开办学',30246);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西藏大学',' ','西藏',1,0,0,1,0,' ','暂空',' ','http://www.utibet.edu.cn','西藏大学是西藏自治区唯一一所综合性大学，由原西藏大学和原西藏农牧学院于2001年合并组建而成。合并前的两所学校都有着光荣的历史和传统。原西藏大学办学历史可追溯到1951年，西藏刚刚和平解放，进藏人民解放军在现西藏大学校本部办起了藏文干部训练班，后历经西藏军区干部学校、西藏地方干部学校、西藏行政干部学校三个阶段。随着西藏教育事业的发展，为解决小学、中学师资紧缺的问题，1965年，在西藏行政干部学校的基础上，建立了西藏自治区第一所师范学校。1975年，在西藏师范学校的基础上，成立了西藏历史上第一所高',1666908);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西藏民族大学',' ','西藏',1,9,0,0,0,' ','暂空',' ','http://www.xzmy.edu.cn','西藏民族学院位于古都咸阳渭水之滨，是西藏自治区所属的一所办在内地的民族高等学校。她是西藏创办最早的高等学校，其前身是1958年建校的西藏公学。1965年，经国务院批准，改为西藏民族学院。学院校训为“爱国、兴藏、笃学、敬业”。建校51年来，西藏民族学院认真贯彻党的教育方针，不断提高教育质量和办学效益，使学校得到了很大的发展。今天的西藏民族学院已经具备了较强的办学实力，已发展成为一所以文为主,文、理、经、法、管、教育相结合的多学科的现代化高等学校。现设有民族学、少数民族经济、中国哲学(西藏宗教方向)',704770);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西藏藏医学院',' ','西藏',1,3,0,0,0,' ','暂空',' ','http://www.ttmc.edu.cn/','西藏藏医学院坐落在闻名世界的日光城拉萨，位于雄伟壮丽的布达拉宫东北侧，占地面积82133平方米，建筑面积32281.63平方米。校园环境优美，设施齐备，传统文化特色浓厚。图书馆珍藏有珍贵的《大藏经》及2100多卷传统藏医药典籍与近百幅传统藏医药教学彩色唐卡；实验标本中心收集有藏药材标本1000余种；藏医药天文星算博物馆是全国首家专门展示藏民族优秀传统文化——藏医药和天文星算的博物馆。学院现设有8个县级机构，1所专家门诊、1家藏药厂、1个采认药基地和2所教学医院、1所非直属附属医院、1个非直属教学',101613);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北大学',' ','陕西',1,0,0,1,0,' ','暂空',' ','http://www.nwu.edu.cn','西北大学肇始于1902年的陕西大学堂，1912年始称西北大学，1923年8月改称国立西北大学。1937年抗战爆发后，国立北平大学、国立北平师范大学、国立北洋工学院等内迁来陕，组成国立西安临时大学，1938年更名为国立西北联合大学，1939年8月复称国立西北大学。建国初期，西北大学为中央教育部直属的综合大学，1958年归属陕西省主管，1978年被确定为全国重点大学。现为国家“211工程”重点建设院校、教育部和陕西省共建高校。学校现有太白校区、桃园校区、长安校区三个校区，总占地面积2360余亩。现有',5886229);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安交通大学',' ','陕西',1,0,1,1,1,' ','暂空',' ','http://www.xjtu.edu.cn/','西安交通大学是国家教育部直属重点大学，其前身是1896年创建于上海的南洋公学，1921年改称交通大学，1956年国务院决定交通大学内迁西安，1959年定名为西安交通大学，并被列为全国重点大学。西安交通大学是首批进入国家“985工程”重点建设九所大学之一、首批进入国家“211工程”建设的九所大学之一，1999年被国家确定为以建设世界知名高水平大学为目标的学校。2000年4月，国务院决定，将原属卫生部已有60余年办学历史的西安医科大学、原属中国人民银行已有40余年办学历史的陕西财经学院并入西安交通大',8504800);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北工业大学',' ','陕西',1,1,0,1,1,' ','暂空',' ','http://www.nwpu.edu.cn/','西北工业大学坐落于古都西安，是一所以发展航空、航天、航海工程教育和科学研究为特色的多科性、研究型、开放式大学，隶属于工业和信息化部。是“卓越大学联盟”成员高校之一。脉源三支，强强融合，文化厚重，特色鲜明。西北工业大学由西北工学院和西安航空学院于1957年10月在西安合并成立，1970年哈尔滨工程学院航空工程系整体并入西北工业大学。1938年国立北洋工学院、北平大学工学院、国立东北大学工学院、私立焦作工学院在汉中组建国立西北工学院，1946年迁至咸阳，1950年更名为西北工学院。1952年交通大学',6045229);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安理工大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xaut.edu.cn','西安理工大学属中央和陕西省共建、以陕西省管理为主的高校。学校建校于1949年5月1日，前身是北京机械学院和陕西工业大学于1972年合并组建的陕西机械学院，1994年1月经批准更名为西安理工大学。学校现有金花、曲江、莲湖3个校区和大学科技园，占地总面积135万平方米，建筑面积73万平方米。学校现设材料科学与工程学院、机械与精密仪器工程学院、印刷包装工程学院、自动化与信息工程学院、计算机科学与工程学院、水利水电学院、经济与管理学院、艺术与设计学院、理学院、人文与外国语学院、土木建筑工程学院、研究生学',5099526);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安电子科技大学',' ','陕西',1,1,1,1,0,' ','暂空',' ','http://www.xidian.edu.cn','西安电子科技大学是以信息与电子学科为主，工、理、管、文多学科协调发展的全国重点大学，直属教育部，是国家“优势学科创新平台”项目和“211工程”项目重点建设高校之一、首批35所示范性软件学院的高校之一和首批9所获批设立集成电路人才培养基地的高校之一。学校前身是1931年诞生于江西瑞金的中央军委无线电学校，是毛泽东等老一辈革命家亲手创建的第一所工程技术学校。1958年学校迁址西安，1966年转为地方建制，1988年定为现名。建校84年来，学校始终得到了党和国家的高度重视，是我国“一五”重点建设的项目',6007687);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安工业大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xatu.edu.cn','历史沿革西安工业大学创建于1955年，前身为西安第二工业学校，是国家“一五”计划156个重点建设项目的军工配套项目之一，具有鲜明的军工特色。1960年升格为西安仪器工业专科学校；1965年升格为西安工业学院，成为国家部署在西北地区唯一的一所兵工本科院校；2006年经教育部批准更名为西安工业大学。学校具有博士学位授予权。2011年被列为陕西省重点建设的高水平教学研究型大学。2012年陕西省人民政府、中国兵器工业集团共建西安工业大学。学校曾隶属于国家第二、一、三、五机械工业部、兵器工业部、国家机械工',4059115);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安建筑科技大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xauat.edu.cn/zh-cn/html/06zhshjy/bkshzhsh.html','西安建筑科技大学坐落在历史文化名城西安，南眺驰名中外的唐代大雁塔，北临举世闻名的明代长安城墙。学校总占地4300余亩，校园环境优美，办学氛围浓郁。学校办学历史源远流长，其办学历史最早可追溯到始建于1895年的北洋大学，积淀了我国近代高等教育史上最早的一批土木、建筑、环境类学科精华。1956年，在全国第三次高等学校院系调整时由原东北工学院、西北工学院、青岛工学院和苏南工业专科学校的土木、建筑、环境类系（科）整建制合并而成，时名西安建筑工程学院。1959年和1963年，曾先后易名为西安冶金学院、西安',4025040);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安科技大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xust.edu.cn','西安科技大学历史悠久，底蕴深厚。学校办学历史可以追溯到1895年成立的北洋大学工学院采矿冶金科，1938年迁并于西北工学院矿冶系，1957年并入西安交通大学，1958年从西安交通大学分出成立独立的西安矿业学院，是当时隶属原煤炭工业部仅有的2所5年制本科院校之一。1998年学校实行“中央与地方共建，以地方管理为主”，划转陕西省。1999年更名为西安科技学院，2003年更名为西安科技大学。学校是国家安全生产监督管理总局和陕西省人民政府共建高校、教育部卓越工程师教育培养计划实施高校、国家建设高水平大学',4887314);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安石油大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://zb.xsyu.edu.cn/index.html','西安石油大学是西北地区惟一一所以石油石化为特色的多科性普通高等学校，是陕西省人民政府和中国三大石油公司共建院校，是陕西省“十二五”期间高水平大学建设工程入选院校。学校肇始于1951年的西北石油工业专科学校。1958年，经国务院批准，成立西安石油学院。1969年，学院因“文革”而改厂。1980年，西安石油学院恢复重建。2000年，由中国石油天然气集团公司所属划转为中央与地方共建、以陕西省为主管。2003年，更名为西安石油大学。2009年，陕西省人民政府与中国三大石油公司签署了共建西安石油大学协议。',4541708);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西科技大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://zsxx.sust.edu.cn/','陕西科技大学是我国西部地区唯一一所以轻工为特色的多科性大学，是国家“中西部高校基础能力建设工程”建设高校，是“十二五”期间陕西省重点建设的高水平大学，是陕西省人民政府与中国轻工业联合会、中国轻工集团公司共同建设的重点高校。学校创建于1958年，时名北京轻工业学院，是新中国第一所轻工高等学校;1970年迁至陕西咸阳，改名为西北轻工业学院;1978年被国务院确定为全国88所重点院校之一;1998年学校划转到陕西省，实行中央与地方共建、以地方管理为主的体制;2002年经教育部批准，更名为陕西科技大学;',2823228);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安工程大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xpu.edu.cn','西安工程大学是我国西部地区唯一以纺织服装为特色的高等学校。其前身为1912年创办的北京高等工业专门学校机织科，其后历经国立北平大学工学院纺织系、西北联合大学纺织系、西北工学院纺织系等发展阶段。1978年成立西北纺织工学院，隶属纺织工业部。1998年划转为中央与地方共建，以陕西省管理为主。2006年，经教育部批准更名为西安工程大学。学校历经100余年的发展，已经成为一所以工为主，纺织、服装为特色，工、理、文、管、经、法、教、艺术等多科性特色鲜明的高校。学校现为教育部“卓越工程师教育培养计划”高校，',4181982);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长安大学',' ','陕西',1,1,1,1,0,' ','暂空',' ','http://www.chd.edu.cn/','长安大学直属国家教育部，是教育部和交通运输部、国土资源部、住房和城乡建设部、陕西省人民政府共建的国家\"211工程\"重点建设大学，是国家\"985工程\"优势学科创新平台建设高校，2000年由始建于二十世纪50年代初的原西安公路交通大学、西安工程学院、西北建筑工程学院合并组建而成。学校座落于历史文化名城西安，现有校本部和渭水2个校区、太白山和梁山2个教学实习基地，校园面积3745亩。校本部毗邻西安大雁塔，渭水校区位于国家级西安经济技术开发区。60多年来，长安大学逐步发展成为以工为主，理工结合，人文社会',9841194);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北农林科技大学',' ','陕西',1,2,1,1,1,' ','暂空',' ','http://www.nwsuaf.edu.cn','西北农林科技大学地处中华农耕文明发祥地、国家级农业高新技术产业示范区——陕西杨凌，现为教育部直属全国重点大学，国家“985工程“和“211工程“重点建设高校。现任党委书记梁桂、校长孙其信。学校前身是创建于1934年的国立西北农林专科学校及后来相继在杨陵建立的相关科教单位。1999年9月，经国务院批准，由原西北农业大学、西北林学院、中国科学院水利部水土保持研究所、水利部西北水利科学研究所、陕西省农业科学院、陕西省林业科学院、陕西省中国科学院西北植物研究所等7所科教单位合并组建为西北农林科技大学。建',5491786);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西中医药大学',' ','陕西',1,3,0,0,0,' ','暂空',' ','http://www.sntcm.edu.cn','陕西中医学院的前身是1952年创建于西安的西北中医进修学校，1959年升格为陕西中医学院，1961年迁至古都咸阳，是陕西惟一一所培养高级中医药人才的普通高等院校，是1978年中共中央56号文件确定的全国8所重点建设的中医院校之一，1978年成为国务院学位委员会批准的首批硕士研究生招生单位，1981年获得硕士学位授予权，1990年被教育部批准为首批有条件接收国外留学生、港澳台地区学生的高等院校。经过60多年的建设和发展，陕西中医学院已成为一所以中医药专业为主体，医、理、工、文、管多学科专业相互支撑',1941445);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西师范大学',' ','陕西',1,4,1,1,0,' ','暂空',' ','http://www.snnu.edu.cn','陕西师范大学是教育部直属、国家“211工程”重点建设的一所师范大学，是国家培养高等院校、中等学校师资和教育管理干部的重要基地，被誉为西北地区“教师的摇篮”。学校创建于1944年，前身是陕西省立师范专科学校，1954年定名为西安师范学院。1960年与陕西师范学院合并，成立陕西师范大学，1978年划归教育部直属。建校60多年来，在刘泽如、李绵、郭琦等老一代教育家的领导下，在几代师大人的努力下，学校立足西部，面向全国，已发展成为一所有重要影响的综合性一流师范大学，为国家培养各类毕业生13万余人，形成“',4397654);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('延安大学',' ','陕西',1,0,0,0,0,' ','暂空',' ','http://www.yau.edu.cn','延安大学坐落在革命圣地延安，是由毛泽东同志亲自命名、中国共产党创办的第一所综合性大学。1941年，中共中央政治局决定将陕北公学、中国女子大学、泽东青年干部学校合并成立延安大学，吴玉章任校长。1943年至1944年，延安鲁迅艺术文学院、自然科学院、民族学院、新文字干部学校和行政学院相继并入，其历史可以上溯到1937年。在艰苦的战争岁月，延安大学努力贯彻学以致用的方针，培养了大批优秀的专业人才，为抗日战争和解放战争的胜利，为新中国的繁荣和富强做出了重要贡献，在中国高等教育史上有着重要的历史地位和特殊',2707022);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西理工大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.snut.edu.cn','陕西理工学院是一所历史悠久、办学特色鲜明的多科性大学。学校坐落在素有“西北小江南”之美誉的中国历史文化名城??汉中市。物华天宝、人杰地灵，深厚的历史文化底蕴和优美的自然环境，为学校的建设和发展提供了得天独厚的条件。学校校园占地约1700余亩，建筑面积52万平方米。陕西理工学院是一所涵盖哲学、经济学、法学、教育学、文学、历史学、理学、工学和管理学的多科性大学。学校坚持科学的发展观，坚持人才培养、科学研究与地方经济建设协调发展，以提高学科建设水平和教育教学质量为中心，培养具有创新精神和实践能力的高级',2693372);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宝鸡文理学院',' ','陕西',1,4,0,0,0,' ','暂空',' ','http://www.bjwlxy.edu.cn/#','宝鸡文理学院位于炎帝故里、周秦文化发祥地陕西省宝鸡市，占地1070亩，是一所以教师教育为主，具有文、史、哲、理、工、管、法、教育等多学科的省属普通高校。其前身为1958年创办的宝鸡大学（本科），1992年经原国家教委批准，与1978年成立的宝鸡师范学院合并，更名为宝鸡文理学院。学院现设政法、中文、历史、外语、教育、艺术、经济管理、数学、物理、化学化工、地理科学和环境工程、机电工程、计算机科学、电子电气工程、体育系和社会科学部等16个系（部）28个本科专业，另设有成人教育学院。学院面向全国招生，拥',1723572);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('咸阳师范学院',' ','陕西',1,4,0,0,0,' ','暂空',' ','http://www.xync.edu.cn','咸阳师范学院是一所以教师教育为特色的省属本科院校。学校创建于1978年5月，原名“陕西师范大学咸阳专修科”。1978年12月，经教育部批准，成立“咸阳师范专科学校”。2001年5月，咸阳师范专科学校与1983年成立的陕西省咸阳教育学院合并，成立咸阳师范学院。2004年10月，陕西广播电视大学咸阳市分校并入咸阳师范学院。学校位于历史文化名城——咸阳，占地面积68余万平方米，校舍建筑面积44万平方米。有39个实验室，教学科研仪器设备总值9300余万元。图书馆现有馆藏图书文献158万册（其中电子图书4',1654531);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('渭南师范学院',' ','陕西',1,4,0,0,0,' ','暂空',' ','http://www.wnu.edu.cn/','渭南师范学院始于1923年中国共产党建立的赤水职业学校，经历了“抗大式”的赤水农业职业学校、20世纪初期的渭南师范学院、渭南师范专科学校和渭南教育学院，到新世纪的渭南师范学院，沧桑九十余载。如今，已成为一所教师教育为主要特色的高校，正朝着建设具有特色鲜明的高水平地方师范大学的目标迈进！学校地处渭水华岳之间的渭南市。承载着悠久历史文明与深厚文化积淀的秦东大地，传诵着仓颉造字、杜康酿酒、关雎诗经的动人传说,抒写着渭华起义点燃中国革命圣火、渭北和陕甘革命根据地星火燎原的光辉史篇，颂扬着药王孙思邈、史圣',1374739);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安外国语大学',' ','陕西',1,5,0,0,0,' ','暂空',' ','http://www.xisu.edu.cn','西安外国语大学是新中国最早建立的4所外语院校之一，是西北地区唯一的一所主要外语语种齐全的普通高校。1951年，经中央人民政府教育部批准，由兰州大学文学院俄文系、西北大学外国语文系俄文组、俄文专修科合并组建了西安外国语大学的前身——西北俄文专科学校。1952年，中共西北局党校俄文班并入西北俄文专科学校，学校开始招生。1958年更名为西安外国语学院。1979年开始招收研究生，1986年经国务院学位委员会批准，正式成为硕士学位授权单位。2006年经教育部批准，学校更名为西安外国语大学。2011年被陕西',3769091);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北政法大学',' ','陕西',1,11,0,0,0,' ','暂空',' ','http://www.nwupl.edu.cn','西北政法大学位于世界历史文化名城西安，是一所法学特色鲜明，哲学、经济、管理、文学等多学科相互支撑、协调发展的多科性大学，是全国法律人才培养的重要基地、西北地区法学研究的中心、陕西省人文社会科学研究的重要基地。学校立足陕西、面向西北、服务全国，培养了一大批优秀人才，为国家，特别是西北地区的经济社会发展和民主法制建设做出了突出贡献。西北政法大学具有光荣的革命传统和丰厚的文化底蕴，迄今已有74年的建校历史。学校的前身是1937年中国共产党在延安创办的陕北公学；1941年陕北公学与中国女子大学、泽东青年',4042497);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安体育学院',' ','陕西',1,7,0,0,0,' ','暂空',' ','http://www.xaipe.edu.cn','西安体育学院创建于1954年，坐落于古城西安小雁塔西侧，是国家体育总局与陕西省共建院校，是西北地区唯一一所体育高等学府。学院占地696亩，各类在校生8000余名，在校教职工716名，专任教400余名，具有副教授以上专业技术职称人员150余名。建院50多年来，已为国家培养出两万余名体育专业人才，其中大部分已成为体育战线及其它行业的优秀骨干。学院担负着为国家培养体育师资、教练员、运动员和体育科研、体育新闻、体育表演、体育英语、体育经营与管理等体育相关专业的各类高级专门人才的重要任务。学院设有研究生部',559541);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安音乐学院',' ','陕西',1,8,0,0,0,' ','暂空',' ','http://www.xacom.edu.cn','西安音乐学院位于陕西西安南郊，建校于1949年，是中国西北地区惟一一所高等音乐学府。学院现有八个专业系，附设中等音乐学校、成人教育学院、培训学院和四个乐团（交响乐团、民族管弦乐团、合唱团、交响管乐团），硕士研究生专业覆盖舞蹈以外的所有专业方向。现有教职工480余人，学生3300余人。在中国人民解放战争走向全面胜利的1948年，贺龙将军在晋绥解放区创建了西北艺术学校，其二部“音乐部”即西安音乐学院前身。1949年5月，西北艺术学校随西北军政大学西渡黄河迁往西安，二校合并后定名为“西北军政大学艺术学',200600);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安美术学院',' ','陕西',1,8,0,0,0,' ','暂空',' ','http://www.xafa.edu.cn','西安美术学院位于西安市南郊，校园占地三百多亩，校舍建筑面积十万余平方米，校园环境幽雅，青树参天、绿草茵茵、民间石刻艺术品遍地林立，人文气氛浓厚。西安美术学院建立于1949年，是建国初期中央人民政府按照全国总体布局，在西北大区设立的唯一一所高等美术学府。她的前身是西北军政大学艺术学院，建院55年来，西安美术学院从一所窑洞大学，发展成为拥有中国画、油画、版画、雕塑、平面设计、建筑环境艺术、服装、装潢、陶瓷艺术、美术教育、美术史论等11个系、34个专业，以及附属中等美术学校、基础部、成人教育学院、艺术',293052);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州大学',' ','甘肃',1,0,1,1,1,' ','暂空',' ','http://www.lzu.edu.cn/','【基本情况】兰州大学是教育部直属的全国重点综合性大学，是国家“985工程”和“211工程”重点建设高校之一。学校创建于1909年，其前身是清末新政期间设立的甘肃法政学堂，是甘肃近代高等教育开端之标志，开启了西北高等教育的先河。1928年扩建为兰州中山大学，1945年定名为国立兰州大学。新中国成立后，兰州大学坚持党的教育方针，坚持正确的办学方向，发展壮大，迅速崛起。在高等学校院系调整中，被确定为国家十四所综合性大学之一；在“开发大西北”、“建立战略后方”的战略目标指引下，成为国家高等教育格局中具有',7050005);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州理工大学',' ','甘肃',1,0,0,0,0,' ','暂空',' ','http://www.gsut.edu.cn/w/','兰州理工大学坐落在黄河之滨的甘肃省省会兰州市，前身是始建于1919年的甘肃省立工艺学校，1958年，在组建兰州工学院的基础上，将甘肃交通大学并入，同年10月1日正式定名为甘肃工业大学。1965年学校划归第一机械工业部，同时将东北重型机械学院和北京机械学院的水力机械、化工机械、石油矿场机械和焊接工艺及设备专业成建制全部迁入，并从湖南大学、合肥工业大学抽调一批教师来校工作。1998年转制为“中央与地方共建，以地方管理为主”的院校。2003年更名为兰州理工大学。经过90多年的建设与发展，学校已成为一所',3498313);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州交通大学',' ','甘肃',1,0,0,0,0,' ','暂空',' ','http://www.lzjtu.edu.cn/','兰州交通大学创建于l958年，由唐山铁道学院（现西南交通大学）和北京铁道学院（现北京交通大学）部分系科成建制迁兰组建，原名兰州铁道学院，是我国第三所铁路院校。50年代末至90年代末，学校隶属铁道部领导，实行行业办学管理体制。2000年，根据国家统筹规划，划归甘肃省管理，实行“中央与地方共建，以地方政府管理为主”的管理体制。2003年4月，经教育部批准，更名为“兰州交通大学”。学校校园占地面积1565亩，建有3个校区，1个国家级大学科技园区。校舍建筑面积为80.88万平方米，总资产23.37亿元，',3932008);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃农业大学',' ','甘肃',1,2,0,0,0,' ','暂空',' ','http://www.gsau.edu.cn/','甘肃农业大学是教育部本科教学评估优秀学校、农业部和甘肃省政府共建大学，前身是1946年10月创建于兰州的国立兽医学院，我国著名兽医学家、兽医教育家、中国科学院院士、现代兽医学奠基人之一的盛彤笙先生担任首任院长。1950年，改名为西北兽医学院；1951年，改名为西北畜牧兽医学院；1958年，与筹建中的甘肃农学院合并成立甘肃农业大学。学校坐落在兰州市安宁区，占地70.12万平方米，建筑面积50.21万平方米。现设有17个学院（教学部），53个本科专业，拥有动物医学等5个国家级特色专业建设点；1个国家',1333750);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北师范大学',' ','甘肃',1,4,0,0,0,' ','暂空',' ','http://www.nwnu.edu.cn/','【办学历史】西北师范大学为甘肃省人民政府和教育部共同建设的重点大学、国家重点支持的西部地区十四所大学之一。其前身为国立北平师范大学，发端于1902年建立的京师大学堂师范馆，1912年改为“国立北京高等师范学校”，1923年改为“国立北平师范大学”。1937年“七七”事变后，国立北平师范大学与同时西迁的国立北平大学、北洋工学院共同组成西北联合大学，国立北平师范大学整体改组为西北联大下设的教育学院，后改为师范学院。1939年西北联合大学师范学院独立设置，改称国立西北师范学院，1941年迁往兰州。抗日',2123719);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州城市学院',' ','甘肃',1,0,0,0,0,' ','暂空',' ','http://www.lzcu.edu.cn/','学校简介兰州城市学院是经教育部批准成立的一所普通公办全日制本科大学。学校位于甘肃省省会兰州市，风景秀丽、环境幽雅。现占地面积700余亩，校舍建筑面积39.69万平方米，教学仪器设备值9100万元，图书馆馆藏纸质图书76.2万册，电子期刊8500余种，博硕论文50万篇，专题数据库9个。学校现设有15个二级学院，有甘肃省城市发展研究院等16个研究院（所），并设全国体育传统项目学校体育师资培训甘肃基地、“全国中小学教师继续教育网”非学历培训甘肃站、甘肃省职教师资培训中心、甘肃省小学校长培训中心、甘肃省',1041373);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陇东学院',' ','甘肃',1,4,0,0,0,' ','暂空',' ','http://www.ldxy.edu.cn/','陇东学院的前身是1978年成立的庆阳师范高等专科学校。2001年庆阳地区农业科学研究所和庆阳地区农业学校整体并入，2003年经教育部批准成立陇东学院。2012年庆阳卫校整体并入。学校实行省地共建、以省为主的办学体制。学校现占地面积1400余亩，校舍建筑面积55万平方米。固定资产总值10亿多元，教学仪器设备总值1.1亿元。馆藏图书149万册(其中电子图书52万册)。学校现有全日制在校生16071人。教职工1073人，其中专任教师794人。专任教师中教授、副教授288人，具有博士、硕士学位的教师49',592480);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天水师范学院',' ','甘肃',1,4,0,0,0,' ','暂空',' ','http://zjc.tsnu.edu.cn','【办学历史】学校始建于1959年。1988年以“为基础教育培养合格师资，方向明确，成绩显著”受到国家教委的表彰奖励。2000年由天水师范高等专科学校升格为天水师范学院，开始本科招生。2011年10月，被教育部列为培养教育硕士专业学位研究生试点工作建设单位，2013年开始招生培养教育硕士。几十年来，学校秉承“困境中求生存，奋斗中求发展”的精神，已为社会培养了数以万计的以基础教育师资为主的高素质应用型人才，得到了社会的普遍认可。近年来，学校立足自身实际，紧跟高等教育结构调整趋势，初步形成了教师教育、',898250);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河西学院',' ','甘肃',1,0,0,0,0,' ','暂空',' ','http://www.hxu.edu.cn','河西学院始建于1958年，2001年经教育部批准升格为河西学院。2012年学校通过了教育部专家组对我校的本科教学评估。学校坐落在美丽富饶的河西走廊中部城市—张掖，历史上又称“甘州”。张掖历史悠久，文化灿烂，风光优美，物阜名丰，是全国著名的商品粮基地，素有“金张掖”之美称，有诗赞曰：“不望祁连山顶雪，错将张掖认江南”。张掖经济发达，交通便利，国道312线和兰新铁路贯穿全境，是国务院公布的历史文化名城。学校占地面积1002亩，实验农场面积1706亩；建筑面积37万多平方米，固定资产7.3亿元，仪器设',854119);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北民族大学',' ','甘肃',1,9,0,0,0,' ','暂空',' ','http://www.xbmu.edu.cn ','西北民族大学前身是西北民族学院，成立于1950年8月，是新中国成立后创建的第一所民族高等院校，是国家民族事务委员会直属的综合性普通高等学校。2003年4月，经教育部和国家民委批准，更名为西北民族大学。学校位于甘肃省兰州市，占地3071亩，分西北新村校区和榆中校区。建校60多年来，为国家培养了各级各类人才15万余人。现有56个民族全日制在校生26000余人。经过60多年的发展，学校形成了以本科教育为主体，研究生教育、预科教育、继续教育、职业教育和国际教育等协调发展的办学格局。拥有学士、硕士、博士三',3044663);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青海大学',' ','青海',1,0,0,1,0,' ','暂空',' ','http://www.qhu.edu.cn','青海大学坐落于青海省省会——“夏都”西宁市北郊，是一所国家重点支持、清华大学对口支援、教育部和青海省共同建设的地方多科性大学。学校校园占地2850亩，校舍建筑总面积60万平方米，其中教学及行政用房20.3万平方米。固定资产总值为6.09亿元，教学科研仪器设备总值为7357万元，学校图书馆面积1.18万平方米，藏书115万余册。公开出版《青海大学学报》、《青海大学医学院学报》、《青海畜牧兽医杂志》、《青海农林科技》等学术期刊。学校下设医学院、农牧学院、财经学院、化工学院、农林科学院、畜牧兽医科学院',2047449);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青海师范大学',' ','青海',1,4,0,0,0,' ','暂空',' ','http://www.qhnu.edu.cn','青海师范大学建于1956年。学校以本科教育为重点，兼有研究生教育、留学生教育、成人教育和职业教育，学科专业覆盖文学、理学、法学、工学、经济学、教育学、历史学、管理学、艺术等9大门类，已成为文理为主，多学科协调发展，具有教师教育、高原地域和民族特色的省属重点大学。学校是博士学位授予单位、西部地区理科藏汉双语教师培养培训重点院校、全国培养高水平运动员试点院校、国家社会体育指导员培训基地、全国汉语言水平测试（HSK）主考单位、教育部“长江学者和创新团队计划”入选单位、CALIS青海省文献信息服务中心、',943738);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青海民族大学',' ','青海',1,9,0,0,0,' ','暂空',' ','http://qhmuzs.university-hr.com/','青海民族大学创建于1949年12月，是青海建立最早的高校和新中国建校最早的民族院校之一，是全国首批获得硕士学位授予权单位。近年来，学校经历了两次大的结构布局调整，2002年青海师范专科学校实质性整合到我校，2005年青海省铁路高级技术学校移交我校。2009年，与北京工业大学签订合作协议。2011年，青海省人民政府与国家民委签订协议共建青海民族大学。2012年，教育部确定天津大学对口支援青海民族大学。学校现有19个学院、4个直属教学系部，学科专业设置涵盖文学、理学、法学、经济学、管理学、教育学、历',658492);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏大学',' ','宁夏',1,0,0,1,0,' ','暂空',' ','http://www.nxu.edu.cn','宁夏大学位于宁夏回族自治区首府银川市，是自治区唯一的省部共建综合性大学和“211工程”立项建设高校。学校始建于1958年，1997年12月，原宁夏大学与宁夏工学院、银川师专（含宁夏教育学院）合并；2002年2月，宁夏大学与宁夏农学院合并，组建了新的宁夏大学。在50年的办学历程中，学校弘扬“不怕困难，不畏风寒，根深叶茂，本固枝荣”的“沙枣树”精神和一代代宁大人“艰苦创业、负重拼搏”的传统，开拓创新，积极进取，各项事业不断发展。“十五”期间，学校紧紧抓住合并办学、“一省一校”重点建设、对口支援、西部',2104405);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏医科大学',' ','宁夏',1,3,0,0,0,' ','暂空',' ','http://www.nxmu.edu.cn/','宁夏医科大学的前身是宁夏医学院，创建于1958年，是宁夏唯一的一所高等医学院校。1962年，学校并入宁夏大学，改称宁夏大学医学系。1972年，上海铁道医学院搬迁至银川，与宁夏大学医学系合并，重建宁夏医学院。2002年，宁夏卫生学校、宁夏护士学校并入宁夏医学院。2008年8月，经教育部和自治区人民政府批准，学校正式更名为“宁夏医科大学”。建校以来，已培养了2万余名各层次医学人才，遍布宁夏的城市和乡村，撑起了宁夏医疗卫生事业的这片蓝天。目前，学校生源覆盖全国25个省（自治区、直辖市），现有全日制在校',989957);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏师范学院',' ','宁夏',1,4,0,0,0,' ','暂空',' ','http://www.nxtu.cn','宁夏师范学院始建于1975年。2006年2月经教育部批准，在原固原师范高等专科学校的基础上成立宁夏师范学院。学院主要任务是培养德、智、体、美全面发展的中小学师资、医疗护理专业人才及社会急需的应用型人才。',297374);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆大学',' ','新疆',1,0,0,1,0,' ','暂空',' ','http://www.xju.edu.cn','新疆大学是全国重点大学，是国家“211工程”建设高校、国家西部大开发重点建设高校、国家教育部与新疆维吾尔自治区人民政府共建高校。『历史沿革』新疆大学的前身是创办于1924年的新疆俄文法政专门学校。1935年1月改建为新疆学院，1960年10月1日,正式成立新疆大学，1978年被国务院确定为新疆唯一的全国重点大学，1997年被列入国家“211工程”重点建设高校，2000年被确定为国家西部大开发重点建设高校。2000年12月30日，新疆大学与原新疆工学院合并组建新的新疆大学。2004年，学校成为新疆',2104911);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('塔里木大学',' ','新疆',1,0,0,0,0,' ','暂空',' ','http://www.taru.edu.cn/','塔里木大学原名塔里木农垦大学，位于塔里木河畔的阿拉尔市。为了屯垦戍边和开发塔里木对各类人才的需要，在原国家副主席王震将军的倡导和关怀下，于1958年创建，他亲笔题写了校名，生前一直任名誉校长。学校是国务院学位委员会、教育部批准的首批具有学士学位授予权的本科院校，2003年获得硕士学位授予权，2004年5月经教育部批准，更名为塔里木大学。原为农业部直属院校，现为中央和新疆生产建设兵团共建。学校经过半个多世纪的风雨历程，发扬南泥湾优良传统和抗大作风，形成了“艰苦创业、民族团结、求真务实、励志图强”的',841585);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆农业大学',' ','新疆',1,2,0,0,0,' ','暂空',' ','http://www.xjau.edu.cn','新疆农业大学是自治区重点建设的一所多科性农业高等院校。1952年8月1日，在毛主席和周总理的亲切关怀下，由王震将军在原中国人民解放军第二步兵学校基础上创建了八一农学院，1995年4月21日学校更名为新疆农业大学。60年来，新疆农业大学植根于新疆这片沃土，栉风沐雨、艰苦创业、锐意进取。目前学校已发展成以本科教育为主，博士后科研流动站、博士硕士研究生教育、继续教育、留学生教育等多层次、多规格的办学体系，现有各类在校生21500余人，本科生16000余人，博士、硕士研究生2000余人。拥有教职工161',983543);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石河子大学',' ','新疆',1,0,0,1,0,' ','暂空',' ','http://www.shzu.edu.cn/','石河子大学位于荣获“联合国人居环境改善最佳范例迪拜奖”的新疆石河子市。学校有着65年的办学历史，是国家“211工程”重点建设高校和国家西部重点建设高校，现由教育部和新疆生产建设兵团共建。以北京大学为组长单位，华中科技大学、华东理工大学、华中农业大学为副组长单位，重庆大学、江南大学、对外经济贸易大学、南京师范大学、华南农业大学为成员单位的高校团队对口支援石河子大学。石河子大学前身诞生于1949年9月中国人民解放军解放新疆的进军途中。1996年4月，学校由农业部部属的石河子农学院、石河子医学院、兵团',3638929);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆医科大学',' ','新疆',1,3,0,0,0,' ','暂空',' ','http://www.xjmu.edu.cn','新疆医科大学坐落于新疆维吾尔自治区首府乌鲁木齐市风景秀丽的鲤鱼山下，是一所具有光荣传统的高等医学院校，其前身新疆医学院始建于1954年，1956年建成招生。新疆医学院的兴建，受到了毛泽东、周恩来、朱德等中央领导人的亲切关怀和大力支持，毛泽东主席亲自决策将新疆医学院的建设纳入“一五计划”156个重点项目。1998年6月新疆医学院与新疆中医学院合并成立了新疆医科大学，江泽民同志题写了校名。2011年11月4日，国家卫生部与自治区签署了共建我校的协议，为学校各项事业的发展带来了新的动力和机遇。学校有4',1298387);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆师范大学',' ','新疆',1,4,0,0,0,' ','暂空',' ','http://www.xjnu.edu.cn','新疆师范大学创立于1978年12月，其前身是乌鲁木齐市第一师范学校和新疆教师培训部。在学校30多年的发展历程中，新疆师范大学始终坚持政治家、教育家办教育，努力成为支撑新疆教育事业和经济社会发展的坚强力量；始终不渝地追求科学、文明、进步与真理，秉承“博学笃行，为人师表”的校训，形成了“团结、敬业、求实、创新”的优良校风；始终以民族振兴和社会进步为己任，为新疆经济社会发展特别是教师教育事业的发展做出了重要贡献。学校先后荣获全国民族团结进步先进集体、开发建设新疆先进集体、自治区文明单位等荣誉称号。学校',797776);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('喀什大学',' ','新疆',1,4,0,0,0,' ','暂空',' ','http://www.ksu.edu.cn/','喀什大学是前身是喀什师范学院，是一所多民族、多学科、多层次的高等院校。1962年建校，2003年6月，获得硕士学位授权资格。2007年在教育部本科评估中被评为优秀。2015年4月顺利通过了全国高等学校设置评议委员会专家评议，更名为—喀什大学。现设有数学与统计学院、人文学院、法政学院、物理与电气工程学院、土木工程学院、化学与环境科学学院、生命与地理科学学院、计算机科学与技术学院、体育学院、艺术学院、教育科学学院、中国语学院、外国语学院、思想政治理论课教学研究部、继续教育学院、网络中心、心理健康教育',384985);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('伊犁师范学院',' ','新疆',1,4,0,0,0,' ','暂空',' ','http://www.ylsy.edu.cn ','伊犁师范学院是新疆维吾尔自治区设立的一所全日制普通高等师范院校。前身为1948年成立的新疆省立伊犁专科学校，1980年5月经国务院批准为本科院校，更名为伊犁师范学院。建校近60年来，学院弘扬“品正学实”的校训，逐步形成了文学、理学、法学、经济、教育、历史、工程和管理等多学科协调发展的格局。学院共有2个校区，设有18个院（系），拥有本科专业27个，硕士学位授权点3个，专科专业19个。学院占地面积1602亩，另有2500亩土地作为后备教学发展用地和2000亩土地的校办农场。学院现有在编教职工1322',375310);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('和田师范专科学校',' ','新疆',0,4,0,0,0,' ','暂空',' ','oldname": "和田师范专科学校','学校简介新疆和田师范专科学校（以下简称和田师专）坐落于祖国西部巍巍昆仑北麓、塔克拉玛干沙海南缘、古丝绸之路南道历史名城——和田市。1978年12月，经国务院、原国家教育部批准，在原和田地区师范学校（成立于1938年）的基础上建立的和田师专，是一所以专科教育为特色的自治区区属全日制师范类院校。和田师专建立近三十年来，已为南疆各地州培养了专业人才1万余名，成为南疆培养中小学合格师资的教师教育重要基地和培养适合南疆经济社会发展需要的各类专门人才的摇篮。目前，学校在职教职员工386人，具有副高以上专业技',69784);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆财经大学',' ','新疆',1,6,0,0,0,' ','暂空',' ','http://www.xjufe.edu.cn/','新疆财经学院是一所全日制财经管理类高等学府，是新疆维吾尔自治区重点建设院校之一。学院坐落在乌鲁木齐市风景秀丽的北京路上，南、北及公园3个校区总面积62.6万平方米，绿化面积41万多平方米。学院的前身是1950年成立的新疆省人民政府干部学校；2000年原新疆财经学院、新疆经济管理干部学院、新疆财政学校含新疆税务学校合并成立新的新疆财经学院。经过50多年的建设与发展，已初具规模。1998年获得硕士学位授予权，2001年获得了MBA办学权。学院拥有居领先地位的校园网络，现代化图书馆达到了国内高校一流水',1134183);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆艺术学院',' ','新疆',1,8,0,0,0,' ','暂空',' ','http://www.xjart.edu.cn/','新疆艺术学院位于新疆维吾尔自治区首府乌鲁木齐市，是西北地区唯一的地方特点显著、民族特色浓郁、专业性较强的综合性高等艺术学府。学院的前身是1958年成立的新疆艺术学校。1960年升格为新疆艺术学院。1962年又调整为新疆艺术学校。1987年经原国家教育委员会批准恢复成立新疆艺术学院。2002年学院通过了国家教育部“本科教学随机性水平评估”的合格验收。几年来，在少数民族舞蹈、音乐、戏剧、美术教学方面已显示出突出的优势，已成为边疆艺术人才成长的摇篮。',210863);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北科技师范学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.hevttc.edu.cn/','河北科技师范学院是一所具有硕士学位授予权的省属高等学校。学校前身始建于1941年，1975年开始举办高等教育，1977年开始招收本科生，2006年获得硕士学位授予权。学校是教育部全国首批职教师资培训重点建设基地、农业部现代农业技术培训基地、国家级科技特派员创业培训基地、河北省职业教育研究基地、河北省非物质文化遗产研究基地、中国科协首批全国科普教育基地。设有秦皇岛市农业科学研究院、国家科技基础条件平台——家养动物种质资源平台、冀东文化研究中心、新农村发展研究中心。学校位于沿海城市秦皇岛市。设秦皇岛',2125226);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原电力高等专科学校',' ','山西',0,1,0,0,0,' ','暂空',' ','oldname": "太原电力高等专科学校','太原电力高等专科学校是公办全日制省属高等院校。学校始建于1955年，1978年开始通过高考招收专科层次的学生。1981至1991年曾改建为太原工学院（现太原理工大学）电力分院，举办过11年本科教育。1997年学校由原国家教委确定为全国示范性高等工程专科重点建设学校。2000年经山西省人民政府批准，太原电力高等专科学校与山西大学实施联合办学，成立山西大学工程学院，属公办全日制本科层次办学机构，隶属于山西大学。学校设有电力工程系、动力工程系、土木工程系、自动化系、管理工程系、计算机工程系、环境工程系',408005);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西中医学院',' ','山西',1,3,0,0,0,' ','暂空',' ','oldname": "山西中医学院','学校概况山西中医学院的前身为1978年创办的山西医学院中医大学班，1989年经原国家教委批准，正式成立山西中医学院，2001年开始招收硕士研究生，是山西省重点建设高校、山西省人民政府和国家中医药管理局共建高校、教育部首批卓越医生（中医）教育培养计划改革试点高校。2016年4月教育部正式批准筹建山西中医药大学。经过30多年的建设发展，学校现已建成为一所以中医药学科为主，具有鲜明中医药特色、产学研医紧密结合的高等中医药院校，是山西省中医药人才培养、科技创新、医疗及社会服务和文化传承的中心。学校基础设',1013687);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吕梁学院',' ','山西',1,0,0,0,0,' ','暂空',' ','http://www.llhc.edu.cn/','吕梁高等专科学校创办于1978年，是一所省属普通高等学校。学校坐落于晋西新兴城市吕梁市，北领武当风光，西润黄河秀色，307国道、太柳高速公路和正在建设中的太中银铁路在这里交汇，与省会太原、尧都临汾、圣地延安毗邻。校园占地面积1485.39亩，建筑面积26.5万平方米，在建建筑面积14..3万平方米。校园内基础设施齐全，环境幽雅，是莘莘学子理想的生活、学习和科研场所。占地1039亩的新校园即将投入使用。办学规模：学校现有18个教学系（部）、40个本、专科专业，涵盖了文史、理工、外语、法律、管理、经',595842);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('呼伦贝尔学院',' ','内蒙古',1,0,0,0,0,' ','暂空',' ','http://www.hlbrc.cn/','呼伦贝尔学院位于内蒙古自治区东部，坐落在被誉为中国最美的草原、全国最佳民族风情魅力城市的呼伦贝尔市，是一所立足呼伦贝尔，服务内蒙古，面向全国的全日制综合性普通高等学校，是呼伦贝尔市唯一一所本科院校。学院的前身为四所专科学校，即海拉尔师范专科学校、呼伦贝尔盟教育学院、呼伦贝尔管理干部学院和内蒙古广播电视大学呼伦贝尔盟分校。1977年恢复高考后，这四所专科学校开始招收本专科学生，奠定了呼伦贝尔高等教育发展的基础。1993年，原呼伦贝尔盟行署批准四校合并组建呼伦贝尔大学，1997年，原国家教委批准四校',668004);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东白云学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://www.baiyunu.edu.cn','广东白云学院广东白云学院是广东省属民办全日制普通本科院校。1989年由谢可滔先生创办。1999年成立白云职业技术学院。2005年升格本科院校并更名广东白云学院。是中国应用技术大学联盟倡始成员单位，全国新建本科院校联盟副理事长单位。学校校园占地面积1300多亩，建筑面积40万平方米。各类教学生活设施齐全，有标准塑胶田径运动场、体育馆、网球场等运动场馆；各类实验（实训）室、学生科技创新项目工作室共250多间；藏书150多万册；千兆校园网，校园实现了信息化管理。现有全日制在校学生17000多人，面向全',1329830);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙医学院',' ','湖南',1,3,0,0,0,' ','暂空',' ','http://www.csmu.edu.cn','长沙医学院是国家全日制普通高等学校(二本)，由全国人大代表、全国教育系统先进工作者、全国优秀教育工作者何彬生教授创办。其前身为湘南中等卫生职业技术学校，1996年更名为湘南卫生中等专业学校，1999年升格为湘南医学高等专科学校，2001年校本部迁至长沙市，2005年经国家教育部批准升格为长沙医学院，2011年通过国家教育部本科教学工作合格评估。学校认真贯彻党和国家的教育方针，遵循高等教育规律，依法治校。坚持千年长医、济世惠民的办学宗旨;坚持办学以教师为本，教学以学生为本的办学理念;弘扬“厚德、博',2398897);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.hnzj.edu.cn/','一、基本情况河南职业技术学院的前身为河南省机器制造技工学校，创建于1954年12月。1956年更名为河南省工人技术学校，1960年更名为河南省工业技术师范学校，1961年更名为河南省技工教育师范学校，1992年更名为河南职业技术教育学院。1999年3月，经国家教育部批准改制为新型高等职业院校，现在是100所国家示范高职院校之一。学院先后被确定为国家级高技能人才培训基地、国家职业教育数控技术实训基地、国家汽车运用与维修专业技能型紧缺人才培养培训基地、首批国家高技能人才培养示范基地、省级技能人才公共',1385106);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('民办明达职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.mdut.cn/','明达职业技术学院1995年筹建，1996年招生，1999年经国家教育部批准正式建校，成为具有独立颁发大专学历文凭资格，实行计划内招生的全日制民办普通高等学校。目前学院设有信息传媒系、机电工程系、建筑工程系、经济贸易系、汽车工程系、基础部等6系部近30个专业。学院占地近800亩，总建筑面积约12万平方米，馆藏图书27万余册，各类现代化教学和生活设施一应俱全。学院坚持以培养实用型人才为办学目标，强化学生技能教育和动手操作能力。学生技能考试考证通过率和高级工证书获取率连续多年较高，学院先后与上海、苏州',25649);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙民政职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.csmzxy.com','“国家示范性高等职业院校”长沙民政职业技术学院是全国首批28所国家示范性高等职业院校建设院校之一。学院创办于1986年，1999年经国家教育部批准升格为高等职业技术学院，2000年实行国家民政部与湖南省共建、以省管理为主的新体制，为湖南省教育厅直属的普通高等院校。学院坚持立足民政、面向社会、适应市场、开放办学，以服务为宗旨，以就业为导向，走产学研结合的发展道路，面向民政行业和区域现代服务业工作一线，培养德、智、体全面发展，心理健康，具有“爱众亲仁”道德精神和“博学笃行”专业品质的高素质技能型专门',403878);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西工业职业技术学院',' ','陕西',0,0,0,0,0,' ','暂空',' ','http://ZSW.SXPI.COM.CN','“国家示范性高等职业院校”陕西工业职业技术学院地处历史文化名城咸阳，其前身是创建于1950年的原国家级重点中专——咸阳机器制造学校，是新中国最早建立的职业院校之一。1999年，经教育部批准改制升格为西北地区首家实施高等职业技术教育的全日制普通高校，隶属陕西省教育厅管理。建校58年以来，学院已向社会输送高、中职毕业生6万余名。学院可支配的有效土地面积58.1万平方米、建筑面积36.6万平方米，各种现代化教学和基础设施齐备，资产总额达4.3亿元人民币。近年来，学院先后荣获“全国职业教育先进单位”、“',290023);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('公安海警学院',' ','浙江',1,11,0,0,0,' ','暂空',' ','oldname": "公安海警学院','公安海警高等专科学校是公安部直属的高等院校，实行现役制（副军级），列入中国人民武装警察边防部队序列。学校地处浙江省宁波市北仑区，占地面积500余亩，目前在校生规模2200余人，专职教师260余人，其中副高以上职称60余人。学校于1983年经国务院批准组建，现下设基础部、船艇指挥系、机电管理系、电子技术系、后勤系、进修系等五系一部，开设有船艇指挥、机电管理、计算机应用、通信技术、机要和后勤管理等专业，担负着为全国边防部队培养船艇指挥、机电管理、通信技术指挥、后勤管理等方面人才的任务。',598916);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南工业职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hunangy.com','“国家示范性高等职业院校”湖南工业职业技术学院是1999年经教育部批准设立的高等职业技术学院。学院位于长沙市岳麓山大学城含浦科教园，占地1031亩，建筑面积33.6万平方米，固定资产5.32亿元，教学仪器设备原值5680万元，馆藏图书75万册。学院现有专任教师386人，从行业企业聘请了一线技术人员222人担任兼职教师。专任教师中具有副教授以上高级技术职务的教师135人，湖南省新世纪121人才工程人选2人，湖南省“教学名师”3人，湖南省优秀“双师型”教师1人，湖南省青年骨干教师7人，省级专业带头人',170905);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('顺德职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://218.13.33.164/48m/Main/Main.asp','“国家示范性高等职业院校建设计划”骨干高职院校顺德职业技术学院成立于1999年3月，是一所经国家教育部批准、顺德政府投资兴建的全日制公办普通高等职业技术院校。学校现设有4个学院（设计学院、经济管理学院、继续教育学院、创业培训学院）、7个系（机电工程系、电子与信息工程系、医学系、应用化工技术系、人文教育系、外语系、酒店及旅游管理）和思想政治理论课教学部，现有37个招生专业，全日制在校学生12630人。学校现有专任教师500多人，其中硕士学位以上教师223人，副高以上教师118人。拥有国家级教学团队',665177);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东商业职业技术学院',' ','山东',0,6,0,0,0,' ','暂空',' ','http://www.sict.edu.cn/','山东商业职业技术学院是1999年经教育部批准设立的省属高职院校，办学历史可追溯到1936年的济南惠鲁商业学校。经过70多年的积淀，学校以“创高职名校、施优教于民”为目标愿景，秉承“尚德蕴能，日精日新”的校训，坚持质量立校、特色兴校、科研强校，形成了优质的教育资源和优良的育人环境，坚持“德育为先、能力为重、学做结合、合作育人”的人才培养理念，坚持为社会用人需求服务，为学生就业成才服务，为学生终生幸福奠基，教学质量、办学特色得到社会各界的广泛肯定。学校是国家示范性高职院校、全国职业教育先进单位、全国',744947);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东轻工职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdqy.edu.cn','广东轻工职业技术学院是1999年经教育部批准成立的全日制高等职业技术学校，由广东省人民政府主办、广东省教育厅直接管理。前身是创建于1933年的“广东省立第一职业学校”，至今已有83年的职业教育历史，先后为社会培养了9万余名毕业生。学校现有全日制普通高职在校学生21700人。2006年6月，学校接受教育部高职高专院校人才培养工作水平评估，评估结果为优秀。2007年，被列为省级示范性院校建设单位。2008年，被列为国家示范性院校建设单位。2011年6月，以优秀等级通过教育部、财政部组织的示范验收。学',425431);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://zjc.wtc.edu.cn/zs/','武汉职业技术学院是国家教育部批准独立设置、湖北省人民政府主办、湖北省教育厅直属的全日制普通高等学校，最早办学历史可以追溯到1972年。学校坐拥“武汉·中国光谷”的中心地利，抢占了高职教育发展的战略高地，开创了区域化、国际化、现代化高职办学的成功范例。学校整体办学条件、办学实力、办学水平跃居湖北省高职院校前列，成为湖北高职教育的知名品牌、在全国高职教育领域产生了重要影响。学校是国家示范性高职院校、国家紧缺人才培训基地、全国职业教育师资培训重点建设基地;是全国职业教育先进单位、首届全国高等职业院校就',1158607);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('漯河职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.lhvtc.edu.cn/','河南工业大学漯河工学院学院简介河南工业大学漯河工学院由河南工业大学和漯河市政府合作举办，是经河南省教育厅批准的河南工业大学的二级学院，主要培养区域经济社会发展需要的本科层次技术技能型人才。学院成立了由河南工业大学、漯河市政府、双汇集团等参与的理事会，重大事项由理事会研究决定。河南工业大学统筹安排年度招生计划、招生录取、学籍管理和毕业证书发放工作。学院根据区域经济社会发展需要开设专业，人才培养方案由河南工业大学会同漯河市政府、合作企业共同制定。河南工业大学漯河工学院2016年度计划招收食品营养与检',235277);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州石化职业技术学院',' ','甘肃',0,4,0,0,0,' ','暂空',' ','http://www.lzpcc.com.cn/','schoolcode": "622002100',295504);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西工业职业技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.jxgzy.cn/Dept_ZJC/Web_ZSW/','江西工业职业技术学院是1999年经教育部批准成立的江西省首批独立设置的公办全日制综合性高等职业院校，是江西省示范性高等职业院校。学院创办于1954年，先后经历了调整、升格、合并、更名等发展阶段，至今已有近60年的办学历史，在江西省乃至全国高职教育整体布局中占有重要的位置。学院座落在“物华天宝、人杰地灵”的江南历史文化名城——南昌，现有三个校区(瑶湖校区、青山湖校区、高新校区)，占地面积46万平方米。各校区生活服务与运动设施配套齐全，尤其是瑶湖校区位于南昌市昌东高校园区内，毗邻风景秀丽的艾溪湖和瑶',117840);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁对外经贸学院',' ','辽宁',1,6,0,0,0,' ','暂空',' ','http://www.luibe.edu.cn/','辽宁对外经贸学院是经教育部批准设立的本科层次全日制民办普通高等学校，2005年由辽宁对外经贸职业学院升格而成，隶属于辽宁省教育厅。学校位于大连旅顺经济开发区，校园占地面积65.3万平方米，建筑面积20余万平方米，在校生11780人。教师624人，其中，副教授以上职称教师270人，外籍教师20人。图书馆藏书126.6万册，校园网络接点1.1万个，各类实验室75个。学校以经济学为主，文学、管理学、艺术学、法学协调发展，下设4个学院18个系，22个本科专业，5个专科专业，面向全国30个省（自治区、直辖',1476773);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三门峡职业技术学院',' ','河南',0,6,0,0,0,' ','暂空',' ','http://www.smxpt.cn','三门峡职业技术学院，是1999年经教育部批准成立的一所公办全日制高等专科院校。学院坐落在被誉为“黄河明珠”、“天鹅之城”的三门峡市。校园东临市区、南连天鹅湖、西接陕州古城、北承黄河故道，九曲黄河顺势环绕，形成校区三面环水、绿树掩映的美丽景象。校内砚池碧波荡漾，园林绿化四季如春，是莘莘学子读书求学的理想场所。学院占地1400亩（另有2000亩新校区正在规划建设中），校舍建筑38万㎡；各项教学生活设施齐全，布局合理、功能完善；已建成建筑、模具、数控、汽车、金工、电力、生物、化工、旅游、物流和应用电子',314758);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州铁路职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zzrvtc.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校郑州铁路职业技术学院是教育部首批命名的全国高职高专院校人才培养工作水平评估“优秀院校”。创建于1951年，1958年升格为郑州铁道学院，并接收培养越南留学生。1999年，经国家教育部批准，同意在郑州铁路机械学校、郑州铁路教育学院、郑州铁路卫生学校、郑州铁路师范学校合并基础上建立郑州铁路职业技术学院。建校55年来，学校坚持“服务铁路，面向社会”的办学思想，为全国铁路和各行各业培养各类人才6万余人。先后被授予“全国职业教育先进单位”、国家级“军民共建社会',765390);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.dlvtc.edu.cn/web/guest','大连职业技术学院是大连市人民政府直属的全日制普通高等职业院校，主要为大连市经济社会发展培养高素质劳动者和技术技能人才。她的前身是大连管理干部学院(始建于1979年)，1999年经教育部批准改制为大连职业技术学院，开始走上高等职业技术教育之路，2001年根据市政府的工作安排，大连工业学校和大连师范学校并入学校。学校始终坚持准确的发展定位和“以服务发展为宗旨、以促进就业为导向”的办学理念，综合办学能力、社会服务能力和人才培养质量不断提高，已为经济社会发展输送了近5万名各类专业技术人才。2007年学校',643873);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽源职业技术学院',' ','吉林',0,0,0,0,0,' ','暂空',' ','http://www.lyvtc.cn','辽源职业技术学院是1999年7月26日由国家教育部批准成立的一所公立的、专科层次的普通高等学校。学院的前身建于1958年，前后经历了辽源矿业学院、辽源矿业专科学校、辽源煤炭工业学校和吉林工业学校，办学已有五十年历史。2004年学院被教育部、建设部确定为全国71所建筑行业技能型紧缺人才培养工程点校之一，2005年学院又被教育部、财政部确定为国家资助煤矿安全实训基地建设点校之一，2006年学院顺利通过教育部高职高专人才培养工作水平评估，2007年被吉林省教育厅确定为首批重点建设示范性高等职业院校，2',64550);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('无锡职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.wxit.edu.cn','无锡职业技术学院是一所国有公办的省属全日制普通高等职业院校，坐落在风景秀美、人杰地灵的太湖之滨——江苏省无锡市。学校前身是1959年3月原国家农机部创办的无锡农业机械制造学校;1979年3月下放至江苏省机械工业厅，更名为江苏省无锡机械制造学校;1999年7月经教育部批准独立升格为普通高等专科学校，更名为无锡职业技术学院，隶属江苏省教育厅。学校是国家首批28所“国家示范性高等职业院校”之一，2012年经上级批准试办四年制高职本科教育，培养本科层次的技术技能人才。学校现有中桥、太湖两个校区。中桥老校',403582);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏建筑职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jsjzi.edu.cn','江苏建筑职业技术学院是省属公办全日制高校，国家示范性高职学院，创建于1979年，原为军校，1999年经教育部批准首批升格为江苏省高职学院，2011年更名为江苏建筑职业技术学院。学校地处徐州市泉山风景区，占地1080亩。学校新老校区连片建设，校园环境优美，教学生活设施齐全，文化活动丰富多彩，是“全国职业院校魅力校园”和“江苏省花园式学校”。学校共有8个二级学院和3个教学部，全日制在校生近13000人。拥有4个国家示范建设专业，10个省级品牌、特色专业，25门国家级、省级精品课程，有“建筑施工技术”',407184);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京工业职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.niit.edu.cn','南京工业职业技术学院（简称“南工院”）创办于1918年，前身是我国近现代著名民主革命家、社会活动家、教育家黄炎培先生在上海创办的中华职业学校——我国教育史上第一所以“职业”两字冠名的职业教育学校。90多年来，学校历经中华职业学校时期（1918-1952）、上海机械学校时期（1952-1960）、南京机电学校时期（1960-1999）、南京工业职业技术学院时期（1999年至今）。在近一个世纪的历史中，学校先后为国家培养了近十万名各类建设和管理人才。学校一直秉承“敬业乐群”的校训和“手脑并用、双手万',675175);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海东海职业技术学院',' ','上海',0,0,0,0,0,' ','暂空',' ','http://www.esu.edu.cn','上海东海职业技术学院是一所公益性的民办大学，现任董事长曹助我、校长项家祥、党委书记赵佩琪。学校创建于1993年初，1993年7月，民办东海学院（筹）获上海市高教局批准筹建，1995年学院所有专业经市教委专家评审，全部获得参加国家学历文凭考试资格，并被确定为上海市唯一一所国家教委实施国家学历文凭考试的参照点院校。1999年7月，民办东海学院（筹）经国家教育部批准，成为具有颁发国家学历文凭资格的全日制普通高校，纳入国家招生计划,更名为“民办东海职业技术学院”。后又经上海市人民政府批准更名为“上海东海',297691);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京工业职业技术学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.bgy.org.cn','历史与现状北京工业职业技术学院是一所以工科专业为主、独立设置的公办普通高等职业学院。学校前身为创建于1956年的北京煤炭工业学校，1994年开始举办高等职业教育，1999年正式改制为职业技术学院。学校2000年被教育部确定为全国首批示范性职业技术学院建设单位；2002年被财政部、教育部确定为中央财政重点支持建设的示范性职业院校；2003年在教育部组织的高职高专院校人才培养工作水平评估中，被确定为全国首批八所优秀院校之一；2005年被评为北京市首批“依法治校”示范校；2002年和2006年两次被评',523772);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海工程技术大学',' ','上海',1,1,0,0,0,' ','暂空',' ','http://www.sues.edu.cn','上海工程技术大学是工程技术、经济管理、艺术设计等多学科互相渗透、协调发展的全日制普通高等学校。学校的主校区坐落于上海松江大学城。松江校区占地近1200亩，总建筑面积23万平方米。一座现代化、信息化、人文化、园林化的美丽校园矗立在浦江上游，镶嵌在佘山之侧，为师生员工提供了良好的教学、科研、学习和生活环境。学校经过36年的建设，已发展成一所学科门类齐全、办学特色鲜明的高等学府。学校作为国家教育部“卓越工程师教育培养计划”首批试点高校之一，不断深化教学改革，创新人才培养模式，提高教育质量”。目前，学校',3328327);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京信息职业技术学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.bitc.edu.cn','北京信息职业技术学院创建于1954年，是国家教育部批准的公办全日制普通高等职业技术学院，是国家骨干高职学院、北京市示范性高职院校,是北京市职业院校信息技术类专业教师培训基地所在单位,是全国普通高校毕业生就业工作先进集体,是全国高等教育学籍学历管理工作先进集体。学院是市财政全额拨款事业单位，隶属于北京电子控股有限责任公司，业务归属北京市教育委员会领导。学院现有院本部(酒仙桥)、东校区(东坝)和南校区(花乡)等三个校区，总占地面积350亩，建筑面积21.8万平方米。学院拥有先进的教学设备，有完备的基',553996);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京电子科技职业学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.dky.bjedu.cn/','北京电子科技职业学院是一所北京市属公办高等职业院校，位于北京经济技术开发区凉水河畔，占地807亩，是北京职教园区所在地，北京经济技术开发区内唯一一所高等院校。学校办学历史可追溯到1958年，1999年成为国家首批独立设置的高职学院。学校坚持以服务为宗旨、以就业为导向的高职办学方针，确立了“立足开发区，面向首都经济，融入京津冀，走出环渤海，与区域经济联动互动、融合发展，培养适应国际化大型企业和现代高端产业集群需要的高技能人才”的办学定位。学校在2004年全国高职高专院校人才培养工作水平评估中获得优',581442);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津天狮学院',' ','天津',1,0,0,0,0,' ','暂空',' ','http://www.tianshi.edu.cn/','天津天狮学院是由天津天狮集团有限公司投资兴办，于2008年4月经国家教育部批准成立的全日制民办普通本科高校。其前身为1999年7月教育部批准成立的天狮职业技术学院，目前是天津市唯一一所民办本科高校。学院实行董事会领导下的院长负责制，天津天狮集团有限公司总裁李金元先生任学院董事长，天津市原副市长陆焕生教授任学院名誉院长。天狮集团是一家涉足零售、旅游、金融、国际贸易、电子商务等诸多领域，融产业资本、商业资本和金融资本于一身的跨国企业集团。天狮集团业务辐射190多个国家，在110个国家和地区建立了分公',735502);
