insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东水利电力职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdsdxy.cn/','“国家示范性高等职业院校建设计划”骨干高职院校广东水利电力职业技术学院是1999年7月经教育部批准成立的一所公办全日制高等院校。学院隶属广东省水利厅，业务上接受广东省教育厅指导。学院前身是1952年10月成立的广州土木水利工程学校，文革期间停办，1973年5月复办改名为广东省水利电力学校。1999年7月经教育部批准，升格为广东水利电力职业技术学院。建校至今已有五十六年历史。目前校园总面积为917.9亩，由广州从化校区、天河校区、飞来峡实训基地、江湾实习电站等组成，已有建筑面积22多万平方米；固定',416667);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.nbpt.edu.cn/','“国家示范性高等职业院校”宁波职业技术学院于1999年由教育部正式批准设立，是一所从事高等职业技术教育的全日制综合性高等院校，是国家示范性高等职业院校首批立项建设院校。学院地处宁波经济技术开发区，毗邻北仑港，占地近50公顷，校舍建筑面积20多万平方米。学院下设10个系（院）、1个教育集团，10大类43个专业，并涵盖了机电、模具、计算机、电子信息、生化、物流等与当地产业密切相关的专业。全日制高职在校生8000多名，非全日制学生和各类培训人员16000人。中国科学院贺贤土院士任学院院长，中国工程院朱',131367);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('温州职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.wzvtc.cn/','“国家示范性高等职业院校”温州职业技术学院是1999年经教育部批准创办的全日制高职院校，由浙江省教育厅领导管理，温州市人民政府主办。现为国家示范性高职建设院校、全国职业教育先进单位、全国高职高专人才培养工作水平评估优秀学校、浙江省高职教育研究会理事长单位。学院坐落于风景秀丽的温州高教园区，校园总占地637亩，建筑面积22.4万平方米，总投资超5.6亿元。拥有教学设备总金额8600多万元，图书馆藏书108万册。师资力量雄厚，现有专任教师420人，副高以上职称146人(其中正教授20人)，双师素质教',108892);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南信息职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hniuzsjy.cn','湖南信息职业技术学院位于长沙市望城区，是湖南省经济和信息化委员会直属的唯一一所公办高职院校，是湖南省首批经国家教育部批准的高等职业技术学院之一，是国家教育部高职高专人才培养工作水平评估优秀院校、湖南省示范性高等职业学院、湖南省文明高等学校、湖南省“园林式单位”、湖南信息产业职业教育集团牵头单位和理事长单位、湖南省普通高校招生就业工作先进单位。学院现有全日制在校生8000余人，教师队伍实力雄厚，副教授以上高级职称220人、硕士学位以上教师154人、教学名师74人，设有信息工程系、机电工程系、计算机',89753);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建船政交通职业学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.fjcpc.edu.cn','“国家示范性高等职业院校”福建交通职业技术学院是教育部、财政部确定的全国首批28所“国家示范性高等职业院校建设计划”的建设单位之一，是福建省唯一一所经教育部批准成立的省属公办高等职业技术学院，其溯源最早的前身校为创办于1866年的中国近代官办第一所高等实业学堂——福建船政学堂，曾培养出如严复、詹天佑、邓世昌、陈季同、萨镇冰等众多的仁人志士和民族英雄。学校座落在福建省福州市仓山区，占地面积580多亩。现设有船政学院、交通土建工程系、汽车运用与工程机械系、交通工程管理系、安全技术与环境工程系、信息技',184187);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西机电职业技术学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.gxcme.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校广西机电职业技术学院的前身是创办于1958年的广西机械工业学校，座落在北部湾经济区中心城市南宁市美丽的相思湖畔，1999年经教育部批准升格为公办普通高等学校，隶属广西壮族自治区经济委员会。现有专兼职教师650人，其中教授、副教授、高级工程师、讲师等中级以上职称教师496人；全日制在校生10386人，函授学员678人。校园占地776亩，校舍面20.96万平方米，固定资产2.2亿元，校园环境清新优雅，文化氛围浓厚，是学习和生活的理想学府。一、五十年风雨兼',198881);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛滨海学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.binhaicollege.com','青岛滨海学院始建于1992年，1999年成为山东省首所民办专科院校，2005年升格为山东省首批民办本科高校，2009年具备学士学位授予权。学校设立文理基础学院、大专文理基础学院、商学院、信息工程学院、机电工程学院、外国语国际合作学院、建筑工程学院、教育学部、医学院、艺术传媒学院和思想政治理论课教学科研部等教学单位。学校共开设33个本科专业、35个专科专业，涵盖文、理、工、管、经、艺、医、教育等学科门类。学校现有在校生17000余人.屡开先河，理念先进。学校是建国后山东省首所民办高校，秉承“以兴教',994225);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽职业技术学院',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://www.ahzybys.com/','★国家示范性高等职业院校★★安徽省首批地方技能型高水平大学建设单位★★安徽省“四年一贯制”应用型本科人才培养试点学校★【院校性质】直属于安徽省教育厅，全日制公办普通高等学校。【历史沿革】创办于1958年，1959年曾升格为“安徽纺织工学院”;1999年，举办高等职业教育，是教育部批准的安徽省首批高职院校之一。【基础设施】占地1598亩，在校学生16000余人，校园建筑面积45万平方米，各类配套设施齐备，是合肥市花园单位;图书馆藏书将近100万册;学生宿舍和教学场所安装了空调，学习生活条件优良。【',496183);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆航天职业技术学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqepc.cn','◆中国人民解放军定向培养直招士官高校◆教育部“首批现代学徒制试点院校”◆重庆市“示范性高等职业院校”◆中国航天工业总公司“甲级优秀学校”◆中国航天科技集团公司“精神文明单位”◆重庆市“精神文明先进集体”◆新中国成立60年“重庆教育功勋著名高职院校”◆重庆市“高等学校质量工程先进单位”◆重庆市“普通高等教育学生教育管理工作先进集体”◆重庆市“职业教育科研工作先进单位”◆重庆市“普通高校毕业生就业工作先进集体”◆重庆市“学校后勤工作先进集体”※中央财政支持“电工电子及自动化实训基地”※中央财政支持“',232368);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古建筑职业技术学院',' ','内蒙古',0,1,0,0,0,' ','暂空',' ','http://www.imaa.edu.cn/','内蒙古建筑职业技术学院是内蒙古自治区唯一一所独立设置的建筑类高等院校。国标代码：10871；自治区招生代码：771。办学层次：高职高专；隶属于内蒙古自治区人民政府，上级教育主管部门为内蒙古自治区教育厅；办学性质：公办。学院前身是成立于1956年的内蒙古建筑学校，1994年被教育部评定为全国六所建筑类重点中专之一。1999年7月独立升格为普通高等学校。2007年被教育部、财政部确定为“国家示范性高等职业院校建设计划”立项建设单位，2010年6月通过国家验收。2009年被教育部评为“全国普通高校毕业',286466);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('伊春职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','oldname": "伊春职业学院','伊春职业学院是1999年7月经教育部直接批准，在黑龙江大学伊春分校与黑龙江省伊春师范学校合并基础上设立的国家公办全日制普通高校，保留黑龙江大学伊春分校校牌，2006年继续招收本科生。她坐落在祖国林都、中国最佳避暑胜地、国家级园林城市??伊春市。学院固定资产2亿元，占地面积36万平方米，建筑面积15万平方米，教学仪器设备总价值2100万元，图书馆现有藏书25万册。学院现有教职工408人，其中专兼职教师274人，专任教师210人，具有高级职称的111人。优良的硬件条件、宽松的育人环境吸引了一大批国内',54705);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北工业职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hbcit.edu.cn/','河北工业职业技术学院是国家百强示范高职院校之一，是河北省规模最大、办学历史最悠久的公办省属冶金类高职院校，河北省钢铁冶金职教集团牵头单位，2011年在教育部、财政部示范高职院校验收中获得优秀，并成为我省首批本科工程教育试点院校，2012年，在教育部高职高专院校综合评比中排名第一，2014年荣获全国毕业生就业典型50强。学院拥有50多年办学历史，位于河北省省会石家庄市，1999年经教育部批准成立专科层次高职院校(下设宣钢分院)，隶属河北省教育厅。现有南校区、北校区和温塘校区三个校区，占地1300余',809954);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江万里学院',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.zjwu.net/','浙江万里学院是在一所具有50余年办学历史的省属普通高校基础上进行管理模式和运行机制改革的新型高校，创新办学十年，被教育专家作为“中国特色现代大学制度的范例性实践”。学校地处宁波，占地1400余亩，建筑面积65万平方米，分设钱湖、回龙等校区。校园风景优美，教学设施完善，获省级“文明校园”、“平安校园”、“绿色学校”、“治安安全示范单位”等荣誉称号。学校为“国家硕士专业学位研究生教育试点单位”“全国地方院校新闻传播学应用型人才培养试点单位”和“浙江省计算机专业应用型人才培养试点单位”，现拥有4个国家',1402523);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽建筑大学',' ','安徽',1,1,0,0,0,' ','暂空',' ','http://www.ahjzu.edu.cn/','安徽建筑大学是安徽省唯一一所以土建类学科专业为特色的多科性本科高校，始建于1958年，坐落在安徽省合肥市。学校是教育部本科教学工作水平评估优秀院校、省级博士学位授予权立项建设单位、国家“卓越工程师教育培养计划”实施高校、国家节约型公共机构示范单位、全国大学生社会实践先进单位、省人才工作先进单位、省优秀教学管理集体、省就业工作先进单位、省普通高校毕业生就业工作标兵单位、省高校后勤工作先进单位、合肥市卫生先进单位、合肥市安全文明校园、合肥市花园单位。学校设有11个学院，现有55个本科专业，涵盖工、管',2413238);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽科技学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.ahstu.edu.cn/zsw/','安徽科技学院是一所办学历史悠久、文化底蕴深厚、多学科协调发展的省属本科院校，始建于1950年，1965年开始举办本科教育。60多年的栉风沐雨，一代代安科人潜心耕耘、励志践行，薪火相继、砥砺奋进，学校事业发展取得可喜成绩。多年的探索实践、长期的办学积淀，孕育出深厚独特的校园文化，形成了“艰苦奋斗、开拓创新、求真务实、无私奉献”的安科精神。现有全日制本科生、硕士研究生16100人，已累计为国家培养各类高级专门人才8万余名。学校占地面积1700余亩，拥有滁州凤阳校区和在建的蚌埠龙湖校区。凤阳校区位于明',1490942);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('枣庄学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://210.44.232.18/','枣庄学院是经国家教育部批准成立的省属公办全日制普通本科院校，其前身是枣庄师范专科学校。学校坐落在山东省枣庄市。京沪铁路、京沪高铁、京台高速公路、岚曹高速公路和104国道、206国道穿境而过，交通十分便利。学校现有全日制本专科在校生16000余人，特聘中国科学院院士、著名数学家、博士生导师、北京师范大学原校长王梓坤先生和中国科学院院士、激光与光电子科学家、天津大学精密仪器与光电子工程学院教授、博士生导师姚建铨先生为名誉校长。学校秉承“兼爱、尚贤、博物、戴行”校训，全面贯彻落实党的教育方针，树立“办',1278210);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东工艺美术学院',' ','山东',1,8,0,0,0,' ','暂空',' ','http://www.sdada.edu.cn','山东工艺美术学院位于山东省济南市，始建于1973年，是目前中国独立建制的31所普通高等艺术院校之一，是首批山东省特色名校。学校是学士学位、硕士学位授予单位，现已形成本科教育、研究生教育、高等职业教育、继续教育和附中教育的多层次办学格局，逐渐发展成为特色鲜明的高等设计艺术院校。学校现有千佛山校区和长清校区两个校区。现设视觉传达设计学院、建筑与景观设计学院、工业设计学院、服装学院、造型艺术学院、现代手工艺术学院、数字艺术与传媒学院、人文艺术学院、应用设计学院、继续教育学院、公共课教学部、思想政治理论',298968);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黄淮学院',' ','河南',1,0,0,0,0,' ','暂空',' ','http://www.huanghuai.edu.cn  ','黄淮学院是2004年经教育部批准升格的一所全日制普通本科高校，具有43年的办学历史。是教育部应用技术大学改革战略研究试点院校、中国应用技术大学联盟副理事长单位、河南省首批示范性应用技术类型本科院校，河南省文明单位、河南省园林绿化先进单位，河南省党风廉政建设先进单位。学校位于历史悠久、文化底蕴厚重，素有“豫州之腹地、天下之最中”美称的驻马店市。校园环境清新优雅，办学条件优良，公共服务设施齐全。学校占地2460多亩，建筑面积58万平方米，教职工1200余人，设有16个二级学院，全日制普通在校生2万余',1402742);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('平顶山学院',' ','河南',1,0,0,0,0,' ','暂空',' ','http://www.pdsu.edu.cn','平顶山学院：百舸争流竞中原(国标代码:10919)在豫招生代码：普通本专科6125专升本6126中外课程合作6127软件类6128平顶山学院是一所经教育部批准、由河南省人民政府主办的综合性全日制普通本科院校。学校坐落在平顶山市新城区的平西湖畔，滨临白龟湖国家湿地公园，依山傍水，自然环境优美，人文底蕴深厚。学校前身是创建于1959年10月的平顶山师范学校，2004年5月经教育部批准升格为本科院校。学校坚持立足地方服务行业，逐步形成了以工科等应用学科为主、工、管、理、文、法、艺等多学科协调发展的学科',1119094);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北科技学院',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.hbust.com.cn/','承荆楚文化之遗韵，传赤壁战鼓之余音沐泉都千年之温度，润桂乡百里之馨香；展北伐铁军之豪气，得鄂南山水之精灵。在有着湖北南大门、武汉后花园之称的咸宁市市区，嵌着一颗美丽的明珠——湖北省属普通高等院校湖北科技学院。湖北科技学院是一所区位优势突出，交通十分便利的高校位于湘鄂赣交界处的鄂南地区，北临武汉70公里，南距岳阳120公里，武汉城市圈、长株潭两个“两型社会”试验区与长江经济带的建设为学校发展带来了良好的机遇；京广铁路、武广高铁、106国道、107国道、京港澳高速、杭瑞高速、大广高速、长江黄金水道把',1455117);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北医药学院',' ','湖北',1,3,0,0,0,' ','暂空',' ','http://cx.hbmu.edu.cn/','郧阳医学院地处秦巴余脉、武当北麓、享有“中国汽车城”美誉的湖北省十堰市中心。学院环境幽雅，景色宜人，教学设施齐全，管理服务规范，是鄂豫川陕渝毗邻地区十地（市）唯一的一所医学本科院校。其前身为武汉医学院郧阳分院，1994年经原国家教委批准，成为独立设置的本科普通高等院校；2006年获教育部本科教学水平评估优秀结论；2008年被教育部确定为硕士学位授权立项建设单位。学院占地41.7万平方米，固定资产3.5亿元。图书馆藏书78.24万册，订阅国内外期刊14000多种（含电子期刊）。办有中国科技论文统计',1369630);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黄冈职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbhgzy.com.cn','“国家示范性高等职业院校建设计划”骨干高职院校我院是经教育部批准设立的一所综合性全日制普通高等院校。她积累了五十多年职业技术教育的经验。国务委员、原教育部部长陈至立来学院检查工作时充分肯定了学院在职业技术教育方面所取得的成就。原国家教委领导称誉学校“南湖明珠，职教模范”。学院占地面积1000余亩，建筑面积28万平方米．在校生逾万人，专任教师600余人，其中教授、副教授职称210人，从中国人民大学、武汉大学、华中科技大学、华中农业大学等名校聘请了12名知名院士、教授为学院客座教授来院授课，聘请外籍',126309);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长江职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.cjxy.edu.cn/','长江职业学院位于九省通衢的江城武汉，坐落在“武汉?中国光谷”新技术开发区腹地，直属湖北省教育厅领导。学校纳入国家统招计划，面向全国招生，具有独立颁发学历文凭资格。在校学生1.3万余人。学校开设文、经、艺、工、管等学科35个专业，设有“6院、1部、1中心”，即艺术学院、旅游与外语外贸学院、经济管理学院、工学院、国际学院、继续教育学院、中专部及现代技术教育中心。学校建立了校企合作的互通国际传媒学院。拥有一支专业精湛、敬业奉献、为人师表的教师队伍，各专业均配有学科带头人。学校秉承“平民教育”，坚持“铸',279343);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁农业职业技术学院',' ','辽宁',0,2,0,0,0,' ','暂空',' ','http://www.lnnzy.ln.cn/','“国家示范性高等职业院校”建校60年来，学院人才辈出，硕果累累，为农业一线培养输送各类专门人才3万多名，毕业生中有一大批已成为商界精英、行业明星、创业标兵、技术能手，有的还走上了各级领导岗位。科技研究成果丰硕，《农业高等职业教育人才培养模式改革创新的研究与实践》和《高职高专教育农学类专业人才培养规格和课程体系改革、建设的研究与实践》研究课题分别荣获国家级教学成果二等奖，园艺技术专业入选国家精品专业，园艺技术专业、生物技术及应用专业先后被列为国家教改试点专业，园艺技术专业、畜牧专业、农产品质量检测',88358);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽三联学院',' ','安徽',1,1,0,0,0,' ','暂空',' ','http://www.sanlian.net.cn/','安徽三联学院坐落于安徽省省会合肥，主校区地处合肥大学城翡翠湖畔，是国家教育部批准建立的、具备学士学位授予权的普通本科高校。校园占地面积781亩、校舍面积29万平方米，现有全日制在校生13000余人。学院建有各类实验室70余个、教学科研仪器设备总值6000余万元、馆藏纸质图书70余万册。学院坚持“以教学为中心”，牢固树立“依法办校、专家治校、质量立校、特色强校、人才兴校”的办学理念，秉承“厚德博学、砺能树人”的校训，全面推进素质教育，以服务地方（行业）经济建设和社会发展需要为导向，以应用型人才培养',416601);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州工艺美术职业技术学院',' ','江苏',0,8,0,0,0,' ','暂空',' ','http://www.sgmart.com/','“国家示范性高等职业院校建设计划”骨干高职院校苏州工艺美术职业技术学院系经中华人民共和国教育部、江苏省人民政府批准设立的高等艺术院校，学院以工艺美术设计和现代艺术设计教育为主体，兼设各类成人教育。学院凭借悠远的工艺美术教育传统，醇厚的吴中地理文化，现代化的教学设施和雄厚的办学实力，形成了传统工艺美术设计与现代艺术设计并重的办学模式。学院地处苏州国际教育院南区，拥有行政楼、国际交流中心、教学楼、图书馆、美术馆、体育馆、实训大楼、学生公寓、专家公寓等建筑群体。设有现代教育技术中心和实训中心。学院现设',138738);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮北职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.hbvtc.net','淮北职业技术学院是1999年7月经国家教育部批准面向全国招生的公办全日制普通高等学校，是安徽省示范性高职院校。学院办学实力雄厚，举办专科及职业教育已40多年，其中以高等职业技术教育为主，同时开展中等职业技术教育、职业技术培训、成人高等教育和广电远程教育。2002年被教育部、财政部确定为国家级示范性高职教育重点建设单位。高职教育现设有机电工程系、医学系、计算机科学技术系、财经系、建筑工程系、电子信息工程系、旅游管理系、工艺美术系和基础部，共开设61个专业，各类在校在册生二万余人。学院坐落在国家级文',106688);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林华桥外国语学院',' ','吉林',1,5,0,0,0,' ','暂空',' ','http://www.hqwy.com/hqwy/','吉林华桥外国语学院是吉林省重点高校。学校创办于1995年;2003年被教育部批准为民办普通高等本科院校;2005年获得学士学位授予权并开始联合招收硕士研究生;2007年成为全国民办高校中第一所也是目前唯一一所“省重点高校”;2010年成为全国唯一的国家教育体制改革——“探索非营利性民办高校办学模式”试点单位;2011年经国务院学位委员会批准，成为首批培养专业学位研究生(翻译硕士)的民办高校，2013年成为教育部非营利性民办高校联盟主席单位，是吉林省培养应用型高级外语外事人才的重要基地。学校坐落在',733763);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潮汕职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.chaoshan.cn/','潮汕职业技术学院是1999年经国家教育部批准设立的普通高校。学院位于粤东潮汕平原普宁市区，普宁市是闻名全国的商贸名城，“中国纺织服装之都”，地理位置优越，比邻粤东名城汕头市、潮洲市，广东省高速公路网池尾出口距学院仅3公里，国道324线在学院门口经过，距广梅汕铁路揭阳站40公里，厦深高速铁路普宁站4公里，潮汕国际机场40多公里，交通十分发达，快捷便利，这为在校就读的全国各地学子提供极大的交通方便；学院规划用地500亩，建筑面积12万平方米，设施配套完善，环境优美，是学子求学和成才的好地方。学院积累',97963);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('杨凌职业技术学院',' ','陕西',0,2,0,0,0,' ','暂空',' ','http://www.ylvtc.cn/','“国家示范性高等职业院校”杨凌职业技术学院是1999年9月经教育部批准，由原国家级重点中专陕西省农业学校、陕西省水利学校和省部级重点中专陕西省林业学校合并改制而成的一所省属公办全日制普通高等学校。学院历史可追溯到1934年于右任先生和杨虎城将军创建的国立西北农林专科学校附设高职，是我国举办职业教育最早的院校之一。在七十多年的发展历程中，共培养各类专业技术人才七万多名，为陕西乃至西北地区经济发展做出了重要贡献。2001年被教育部确定为全国重点建设的31所示范性职业技术学院之一。2005年在高职高专',357849);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵阳学院',' ','贵州',1,0,0,0,0,' ','暂空',' ','http://zjc.gyu.cn/','贵阳学院坐落在贵阳市龙洞堡新城，是在原贵阳师专和金筑大学两所专科学校有机整合的基础上，于2004年5月经教育部批准成立的普通本科院校，具有学士学位授予权，实行省市共建以贵阳市为主的办学体制。2010年12月作为全国普通高等学校本科教学工作合格评估的10所试点学校之一，接受并通过了教育部专家组的评估。一、历史沿革贵阳学院主要由原贵阳师范高等专科学校和贵阳金筑大学合并组建而成。贵阳师范高等专科学校于1978年成立，原址在贵阳市马王庙，原名为贵阳师范学院贵阳专科班，1982年更名为贵阳市教师进修学院，',1113665);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('六盘水师范学院',' ','贵州',1,4,0,0,0,' ','暂空',' ','http://www.lpssy.edu.cn/','六盘水师范学院(以下简称“学校”)地处素有“江南煤都”之称和“中国凉都”之誉的贵州省六盘水市，是2009年3月经教育部批准成立，实行“省市共建、以市为主”管理体制的全日制普通本科院校。六盘水师范学院前身是1978年成立的贵阳师范学院六盘水专科班;1985年组建六盘水师范专科学校;1993年更名为六盘水师范高等专科学校;2003年、2006年，六盘水市师范学校、六盘水市体育运动学校先后并入六盘水师范高等专科学校;2009年3月，六盘水师范高等专科学校升格为六盘水师范学院。在37年的办学实践中，学校',240540);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆工程学院',' ','新疆',1,1,0,0,0,' ','暂空',' ','http://www.xjie.edu.cn/','新疆工程学院（原新疆工业高等专科学校）位于乌鲁木齐市经济技术开发区(头屯河区)金桥路和南昌路236号，成立于1958年，建校之初为新疆煤矿学校，1994年更名为新疆工业高等专科学校，2012年升格为本科院校——新疆工程学院。学校占地面积1800亩，设有采矿工程系等11个教学系部，开设6个本科专业和63个专科专业，其中煤矿开采技术、矿山机电等6个专业已列入自治区重点产业紧缺人才专业建设计划。学校有1个国家级精品建设专业，3个自治区特色专业，2个自治区级教学团队。学校建有19个实验中心，下设82个校',270269);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆农业职业技术学院',' ','新疆',0,2,0,0,0,' ','暂空',' ','http://www.xjnzy.edu.cn/','蓬勃发展中的新疆农业职业技术学院新疆农业职业技术学院是2000年6月经自治区人民政府批准成立的以举办高等职业教育为主的全日制普通高等学校。其前身--昌吉农业学校，创办于1959年。学院位于新疆昌吉市，占地面积3698亩，其中，昌吉校区校园面积近600亩，建筑面积15万平方米。呼图壁东泉校区3098亩。学院校园环境优雅、绿树成荫，交通便利。建有面积为1.4万平方米的实验实训大楼，内有各类实验实训室46个，拥有各类先进的实验仪器设备4000余台（件）；按照“资源共享、互惠多赢”理念与昌吉州共建了1.',238133);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昌吉学院',' ','新疆',1,0,0,0,0,' ','暂空',' ','http://www.cjc.edu.cn/','昌吉学院坐落在中国西部新疆天山北坡经济带中心---美丽的昌吉市,距首府乌鲁木齐34公里,有乌奎高速、机场高速和乌昌高等级公路与之相连。她的前身是成立于1959年的昌吉师范学校，1985年升格为昌吉师范专科学校，2001年5月经国家教育部批准升格为本科院校昌吉学院。学院现有西、南两个校区,占地面积819亩,各类建筑面积20万平方米。在昌吉市新区已规划1900亩校区，新校区用现代大学理念进行规划设计,按照现代化、信息化、生态化、网络化和高起点、高标准、高水平、高质量的要求,建设自治区一流的高校校园。',364720);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津职业大学',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjtc.edu.cn/','“国家示范性高等职业院校”天津职业大学是经天津市政府批准，教育部备案的公办综合性全日制普通职业院校。1978年建校，前身是从事本科教育的南开大学第二分校、天津大学化工分校。1982年底更名为天津职业大学。2006年被教育部、财政部确定为首批28所“国家示范性高等职业院校建设计划”立项建设单位之一。2009年被教育部、天津市政府确定为滨海新区技能型紧缺人才培养基地。学校现有河北、北辰两个校区，占地面积765759平方米，建筑总面积30余万平方米，教学科研仪器设备总值近7391万元，资产总值5621',1317883);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('唐山学院',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.tsc.edu.cn','唐山学院地处中国近代工业的摇篮、素有“京东宝地”之美誉的河北省唐山市，是一所以工为主，工、经、管、文、法、艺等多学科协调发展的公办全日制普通本科院校。1956年建校，前身为唐山市工业夜大学(原唐山铁道学院，现西南交通大学)。1985年，西南交通大学智力支援唐山成立了唐山分校。1996年，学校与西南交通大学开始联办本科专业，先后培养了8届毕业生，西南交大专家组至今仍在发挥作用。2002年，经教育部批准，唐山高等专科学校、西南交通大学唐山分校、唐山市职工大学三校实质合并组建本科层次的普通高等学校——',1146796);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('邯郸职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hdvtc.edu.cn/ ','“国家示范性高等职业院校建设计划”骨干高职院校邯郸职业技术学院(邯郸大学)座落在历史文化名城——邯郸市东南部高教区。邯郸位于河北省南部，是春秋战国时期赵国的国都，历史悠久，人文景观丰富，文化底蕴厚重，被誉为中国成语典故之都。邯郸毗邻京津，具有立体化、枢纽型综合交通优势，是四省交界区经济中心。学院创建于1983年，是公立全日制综合性普通高校。1987年，学院被联合国教科文组织接纳为亚太地区教育革新为发展服务计划联系中心(APEID)成员，学院图书馆被确定为联合国教科文组织的藏书点；1996年，学院',194347);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳大学',' ','辽宁',1,0,0,0,0,' ','暂空',' ','http://zsjy.syu.edu.cn','沈阳大学是一所涵盖经、法、教、文、史、理、工、农、管等9大学科门类，以本科教育为主体，同时拥有硕士研究生教育、高等职业技术教育、继续教育和留学生教育的多层次、多类型办学的综合性大学，办学历史可追溯到1906年建立的“奉天实业学堂”和“新民公学堂”。学校现有望花南街-联合路、文萃路、新民三个校区，风光秀丽、景色宜人，校区占地面积64.4万平方米，建筑面积54.7万平方米。学校始终将本科教育作为立校之本，为区域经济建设和社会发展服务，立足沈阳，面向辽宁，辐射全国，把培养高级应用型人才作为特色人才培养',2327274);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('抚顺职业技术学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://www.fvti.com','抚顺职业技术学院（原名抚顺大学）1983年建校，1995年在全省率先开展高等职业技术教育；1997年被国家教委确定为全国17所示范性职业技术大学之一；2004年被国家教育部、国防科工委、国家机械工业联合会确定为数控技术技能型紧缺人才培养培训基地；经辽宁省教育厅批准，从2005年起抚顺职业技术学院依托辽宁石油化工大学，独立招收高职专科毕业生开展本科高职教育。学院占地面积235亩，建筑面积58164平方米，固定资产达1.2亿元。学院拥有校园网络，数字化的图书，馆藏图书近23万册，80个校内现代化的实',70847);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四平职业大学',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.jlsppc.cn','四平职业大学（吉林师范大学应用工程学院）地处四平市风光秀丽、景色宜人的山门风景区，始建于1983年，是一所以培养应用型人才为主，工、管、文、理等多学科协调发展的全日制公办普通高等职业专科院校。学校具有良好的办学条件和教学环境，占地面积14.3万平方米，建筑面积9.7万平方米，拥有图书馆、体育馆、学术报告厅和室外运动场，设有现代化的多媒体教室、语音实验室、电子阅览室及各专业实训实践基地等，学校总资产1.3亿元。是求知治学的理想场所。学校设有机械工程系、电子信息工程系、计算机工程系、管理工程系、外语',67961);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('牡丹江大学',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.mdjdx.cn/mdnew/index.asp','牡丹江大学始建于1983年，坐落在素有“塞北江南”之称的全国旅游城市——牡丹江市，是经教育部批准的普通高校。学校现占地面积38.2万平方米，建筑面积16万平方米，固定资产总值2.8亿，新建海林实习实训基地一期工程将于今年9月投入使用，占地30万平方米二期工程正在筹建。学校设施完备、环境优美。建有工程实训中心、机械车床加工室、电拖实训室、信息安全存储实验室、会计模拟实验室、画室、金蝶ERP实验室、动漫工作室、影视制作室、电工电子实验室、多媒体微机室等100多个现代化实验实习实训室;设有管乐演奏厅、',355633);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮阴工学院',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.hyit.edu.cn/','淮阴工学院为江苏省属普通本科院校，坐落于敬爱的周恩来总理故乡、全国历史文化名城——江苏省淮安市。学校现有枚乘路、北京路两个校区，校园占地2070亩，校舍建筑面积57万平方米，拥有完善的基础设施和现代化的教学实验仪器设备，固定资产总值13.29亿元，其中各类教学科研仪器设备值1.43亿元，馆藏图书133.6万册，中外文期刊、报纸1800多种，中外文数据库25个。学校设有机械工程学院、电子与电气工程学院、计算机工程学院、建筑工程学院、生命科学与化学工程学院、交通工程学院、经济管理学院、数理学院、人文',907756);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('连云港职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.lygtc.net.cn/','连云港职业技术学院是一所由省政府领导和管理，实行省市共建的综合性全日制普通高校。学校的前身是1983年3月由教育部批准成立的连云港职业大学，1999年3月易名为连云港职业技术学院。学校于2008年9月迁入占地1150亩、建筑面积33万平方米、功能设施更为齐全和现代化的新校区。现开设60多个专业，涵盖工业制造、农林、生化、土建、制造、电子信息、环保、轻纺食品、财经、旅游、公共事业、艺术、外语等12个高职高专专业目录大类,初步形成了文、理、工、经、艺、农等学科门类较为齐全的办学格局。学校现有各类在校',75169);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('镇江市高等专科学校',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://zsw.zjc.edu.cn','镇江高等专科学校(简称镇江高专)是教育部批准成立的一所综合性公办普通高校，位于全国文明城市、江南古城江苏省镇江市区。学校具有百年办学史和七十多年专科教育史，是教育部人才培养工作水平评估优秀学校、国家专利导航试点工程(江苏)研究基地、国家学前教育改革发展示范区师资培养基地、全国“双证制”工作研究会副主任单位。办学理念先进，注重学生发展。学校坚持“立足就业、服务发展”的人才培养理念，构建了基于“能胜岗+能转岗”目标的“4F8M”人才培养模式,获江苏省教育成果一等奖。近年来，多名学生获得“中国大学生十',71222);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南通职业大学',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.ntvc.edu.cn/','南通职业大学创办于1973年，是一所经省政府、教育部批准设置的具有工、文、管理和艺术类的综合性全日制普通高等学校。学校坐落在“中国近代第一城”――南通，共有4个校区，校园总面积1000多亩，建筑面积30多万平方米。现有各级各类在校生17000多人，专职教师中正、副教授占34.2%，具有“双师”素质的教师占专业教师的83.2%，专职教师中博士、硕士研究生比例达到46.5%，外籍教师10多人。学校专业特色鲜明，面向市场需求和社会发展需要设置专业。设有机械工程、电子工程、化学工程、建筑工程、纺织工程、',103727);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常州工学院',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.czu.cn/','常州工学院位于经济发达、文教昌盛、交通便捷、美丽富饶的“长三角”历史文化名城常州，是经教育部批准建立的本科层次的普通高等学校。学院占地近1000亩，校舍建筑面积近35万平方米，学院拥有完善的教学、科研、生活、运动设施，环境优雅，景色宜人。学院设有机电工程学院、电子信息与电气工程学院、计算机信息工程学院、土木建筑工程学院、工商管理学院、质量工程学院、外国语学院、人文社科学院、艺术与设计学院、法政学院、理学院、教育学院、延陵学院（公有民营）、应用技术学院、成人（继续）教育学院、体育教学部等16个二级',1022643);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江科技学院',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.zust.edu.cn/','浙江科技学院是一所以工学为主，集工学、理学、文学、艺术学、经济学、管理学、教育学为一体的多学科全日制省属本科院校，其前身为成立于1980年的浙江大学附属杭州工业专科学校。学校坚定不移地走特色办学之路，逐步形成借鉴德国应用科学大学（FH）办学经验，结合中国国情，培养具有实践能力、创新精神和国际素养的高素质应用型专门人才的办学特色。学校主校区坐落于素有人间天堂美誉的历史文化名城——杭州，规划土地面积1690亩。学校总建筑面积44万平方米，教学科研仪器设备总值2.06亿元，馆藏纸质图书111.4万册，',2015354);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波工程学院',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.nbut.cn/','宁波工程学院是经国家教育部批准设立的全日制普通高校。经过25年的建设发展，已成为一所以工科为主，管理、经济、文学、理学等多学科协调发展的本科院校，培养具有高新技术和现代管理知识的应用开发型高级技术人才。学院管理规范，治学严谨，校风优良，环境优美，先后获得浙江省文明单位、浙江省平安校园和宁波市文明单位等称号。学院地处宁波市中心，占地面积850亩，校舍建筑面积28.9万平方米。目前，政府投资14个亿、占地面积1370亩、按“科技、人文、生态”理念规划的新校区正在继续建设之中，其中建筑面积达13万平方',1527720);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.hfuu.edu.cn/','合肥学院是一所在“改革中诞生、开放中成长、创新中发展”起来的本科院校。其前身合肥联合大学1980年建校，在新中国放射化学奠基人杨承宗先生倡导下，由中国科学技术大学等7所高校共建，实行“适当收费、不包分配、按社会需求设置专业、后勤社会化”的办学模式，在中国高等教育改革方面发挥了探索作用，被誉为中国高等教育改革的“小岗村”。目前，学校是国家“服务国家特殊需求人才培养项目”———学士学位授予单位开展培养硕士专业学位研究生试点工作单位，首批承担“卓越工程师教育培养计划”的61所大学之一，中德共建示范性应',2253360);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('芜湖职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.whit.ah.cn/','芜湖职业技术学院是全国首批、安徽首所国家示范性高等职业院校和国家高职高专人才培养工作水平评估优秀院校，为安徽省首家单独招生改革试点院校，安徽省“四年一贯制”技术技能型本科人才培养模式改革试点院校。学院创建于1983年，原名芜湖联合大学。2006年被教育部、财政部列为“国家示范性高职院校建设计划”首批28所立项建设院校之一，2009年学院以优秀等级通过教育部、财政部国家示范性高等职业院校项目验收。2012年5月，经安徽省人民政府批准，学院与芜湖信息技术职业学院合并，为学院跨越式发展注入新的活力。学',539475);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门理工学院',' ','福建',1,1,0,0,0,' ','暂空',' ','http://www.xmut.edu.cn/','厦门理工学院（前身鹭江职业大学）与厦门经济特区同时诞生，同步发展，是厦门市惟一一所实行省市共建、以市为主的领导管理体制的省属公立本科高校。在二十多年的办学过程中，面向厦门经济特区、服务地方经济发展，为地方培养了两万多名具有现代工程技术能力和富有创业素养的应用型专门人才，被誉为“地方经济的人才摇篮”。2004年，鹭江职业大学正式升格为本科院校，并更名为厦门理工学院。自此学校步入了快速发展的新时期：招生规模稳步增长，生源质量屡创新高，学生广受社会好评。厦门理工学院已经成为福建省最受考生欢迎的高校之一',4509819);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛大学',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.qdu.edu.cn','学校位于历史文化名城青岛，背依浮山，面向黄海，承载着深厚的传统文化，也充满了浓郁的时代气息。追溯历史，青岛大学几经学科分流和多源汇聚。学校最早的历史源头是青岛特别高等专门学堂，创建于1909年。随着历史的变迁，学堂的医学教育独树一帜，先后经历了青岛医学校、青岛医科大学、青岛医学专门学校等不同办学时期。学校的另一历史源头是私立青岛大学，创建于1924年，后来又经历了国立青岛大学、国立山东大学等不同的办学时期。1946年，国立山东大学合并了青岛医学专门学校，设置了医学院。1956年，医学院从山东大学',9259962);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('烟台大学',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.ytu.edu.cn/','烟台大学(学校代码：11066)坐落于“中国最佳魅力城市”—烟台，东临黄海，西依青山，风景秀丽，气候宜人，是国内距海最近、拥有海岸线最长的滨海大学，是山东省属重点综合性大学。烟台大学创建于1984年7月，经教育部特批，北京大学、清华大学共同选派教学、科研、管理骨干来校援建，把支援烟大纳入长期工作计划。1990年成立了“北大、清华支援烟台大学建设委员会”，定期研究指导烟台大学的教学、科研、学科建设及改革发展，使烟台大学有一个较高的办学起点和高水平的发展。建校以来，在各级党委、政府的关怀以及海内外各',7241555);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潍坊学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.wfu.edu.cn','潍坊学院是教育部批准建立的一所公办省属全日制综合性普通本科高校，已有60多年的办学历史。学校坐落在世界著名风筝都、全国文明城市、中国优秀旅游城市——山东省潍坊市。学校具有优越的办学条件、雄厚的师资力量和鲜明的办学特色，是山东省高等教育应用型人才培养特色名校立项建设单位、山东省首批研究生联合培养基地，是学子求学成才的良好选择。2015年，学校将面向全国29个省(市、自治区)招生，总招生计划5770人，其中本科4850人、专科920人。学校现有主校区和安顺校区两个校区，占地面积2100亩，校舍建筑面',2989901);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中州大学',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zhzhu.edu.cn/','中州大学创建于1980年。是国家教育部批准成立的一所公办的全日制综合性普通高等学校，老一辈无产阶级革命家、时任中共中央副主席的陈云同志亲笔为学校题写了校名。学校座落在河南省省会郑州市北大学城，占地面积1000余亩，以高起点、高标准、高质量建设的生态园林化、现代智能化的中州大学，处处焕发着勃勃生机。校园内树木成荫，环境幽雅，是莘莘学子学习深造的理想场所。学校设有工程技术学院、化工食品学院、信息工程学院、管理学院、经济贸易学院，文化与传播学院、艺术学院、体育学院、外国语学院、聋人艺术设计学院、继续教',228606);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('开封大学',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.kfu.edu.cn','开封大学是经河南省人民政府批准、教育部备案，由开封市人民政府主办的一所全日制综合性普通高校，创建于1980年，成立之初为河南师范大学(现河南大学)分校;1981年更名为开封市走读大学;1984年更名为开封大学。多年来，开封大学秉承“明德、励学、笃行、创新”的校训，遵循“以德治校、质量强校、科研兴校、特色立校”的办学理念，坚持“以就业为导向，以育人为根本，以服务为宗旨”的办学宗旨，形成了“就业、培养、招生”相结合、“生产、教学、科研”相结合的办学模式和以“学历教育为主，集职业培训、技术服务、技术开',974483);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('洛阳理工学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://zs.lit.edu.cn','洛阳理工学院地处国务院公布的首批历史文化名城、新中国现代化工业城市和科技研发基地、被誉为“千年帝都，牡丹花城”的十三朝古都、中原经济区副中心城市——洛阳。2007年3月，经教育部批准，由洛阳工业高等专科学校和洛阳大学合并组建，是省属全日制普通本科院校。建校60年来，学校秉承“让政府放心，让社会满意，让学生受益”的办学理念，坚持“行业性、地方性、应用型”的办学定位，在人才培养、科学研究、社会服务和文化传承创新等方面取得了显著成效，先后为国家建材行业和地方经济社会发展培养输送了16万多名高级专门人才',3312004);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新乡学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.xxu.edu.cn','新乡学院是一所公办全日制普通本科院校，办学历史可以追溯到1949年成立的太行公立新乡师范学校，2007年经教育部批准，由原新乡师范高等专科学校、平原大学、新乡市教育学院合并升格为本科院校，2014年通过教育部本科教学工作合格评估。学校秉持“开放、包容、求实、创新”的校训，树立“以人为本，厚德强能，开放创新，和谐发展”的办学理念，弘扬“艰苦奋斗，自强不息”的办学精神，坚持走“开放办学、合作办学、错位办学、因地制宜办学”之路，致力于构建“人本化、开放式、合作型、求错位、接地气”的办学模式，实施了“质',1401537);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江汉大学',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.jhun.edu.cn','江汉大学是2001年10月经教育部批准组建，武汉市委、市政府举全市之力易地兴建的一所综合性大学。学校本部坐落在武汉经济技术开发区三角湖畔，这里湖山环抱，水天一色，风景秀丽，幽雅宜人，是理想的学习园地。学校另在汉口和武昌设有2个校区。总占地面积2110余亩，建筑面积46万平方米。学校设有商学院、政法学院、人文学院、艺术学院、机电与建筑工程学院、物理与信息工程学院、数学与计算机科学学院、化学与环境工程学院、医学与生命科学学院、教育学院、外国语学院、体育学院、继续教育学院、高等职业技术学院、现代艺术学',2938860);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('荆州理工职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.jzlg.cn/','沙市职业大学（沙市大学）是1983年经国家教育部和湖北省人民政府批准成立的一所涵盖工学、经济、管理、文学、艺术等多学科的公办全日制普通高等学校。学校地处美丽富饶的江汉平原、座落在人文荟萃的荆州。位于城区中心地带的校园北接市区主干道荆州大道，南出江津大道，东傍顺驰太阳城，西邻鳞次栉比的市府楼群。学校校风严谨，学风浓厚，环境幽雅，人际亲和，是学习深造的理想之地。学校占地面积48万平方米，建筑面积22万平方米。现已开设电光源技术、机电一体化技术、动漫设计与制作等二十七个专业。学校教学手段先进，设施完备',61057);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三峡大学',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.ctgu.edu.cn','历史沿革三峡大学是经国家教育部批准，由原武汉水利电力大学(宜昌)和原湖北三峡学院于2000年6月29日合并组建。原武汉水利电力大学(宜昌)的前身是于1978年成立的葛洲坝水电工程学院，先后隶属于水利电力部、能源部、电力工业部、国家电力公司，1996年与原武汉水利电力大学合并，组成新的武汉水利电力大学，成为“211工程”建设大学的重要组成部分。原湖北三峡学院是由原宜昌师范高等专科学校、宜昌医学高等专科学校、宜昌职业大学于1996年合并而成。学校办学历史最早可以追溯至1946年，1978年开始举办本',3807454);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙学院',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.ccsu.cn','长沙学院是2004年5月经教育部批准，在长沙大学(专科)基础上改建升格的普通本科高等学校。学校实行省市共建共管、以长沙市为主管理的体制。学校坐落在中部历史文化名城--湖南省长沙市，位于风光秀丽的浏阳河畔，眺巍巍岳麓，依洪山古寺。校园湖光山色，风景秀美，为\"湖南省园林式单位\"。学校占地132.55万平方米，现有两个校区，校舍建筑面积30.77万余平方米，教学科研仪器设备总值9661.42万元，图书馆藏书126.41万册，全日制在校生13339人。学校现有15个教学系(院、部)，35个本科专业，基本',1814732);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州大学',' ','广东',1,0,0,0,0,' ','暂空',' ','http://www.gzhu.edu.cn','广州大学是经教育部批准，于2000年由广州师范学院、华南建设学院（西院）、原广州大学和广州高等师范专科学校等高校合并组建而成的综合性大学，实行广东省与广州市共建、以市为主的管理体制。学校于1958年开始招收普通本科生，1983年获硕士学位授予权，2006年获博士学位授予权。广州师范学院创办于1958年；华南建设学院（西院）创办于1984年；广州教育学院创办于1953年；广州高等师范专科学校创办于1985年；原广州大学成立于1983年。学校现有大学城、桂花岗两个校区，占地面积2127.28亩，建筑',6240754);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.cdu.edu.cn','成都大学是一所由成都市人民政府主办的全日制普通本科高等学校，实行“省市共建、以市为主”的办学体制。经过三十载的办学历程，已发展成为以工学、文学、管理学为主,经济学、理学、法学、教育学、农学、医学等九大学科门类协调发展的学科结构体系,具有一定办学规模的城市型综合大学。成都大学始建于1978年，此后三易校址，始黉门街，历花圃路、荷花池，1999年成都市人民政府斥资数亿，置地千余亩建设具有现代气派的成都大学新校区。2002年7月，学校整体迁至自然风光优美、人文底蕴丰厚的成都十陵历史文化风景区。校园环境',1913622);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安文理学院',' ','陕西',1,4,0,0,0,' ','暂空',' ','http://www.xawl.org','西安文理学院是2003年经教育部批准，由西安市政府主办、省市共建、面向全国招生的一所全日制普通本科高校。先后由西安大学、西安师范专科学校、西安教育学院、西安幼儿师范学校和西安师范学校合并而成，办学历史可追溯至1903年设立的陕西师范学堂。最早的本科专业为1990年批准的汉语言文学、数学和1997年批准的地理科学。学校位于陕西省西安市，占地741亩，共有高新校区、太白校区和书院校区三个校区，建筑面积35.6万平方米，图书资料139万册，教学仪器设备总值1.09亿元。学校现有教职工1170人。其中专',1955608);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京京北职业技术学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.jbzy.com.cn/','北京京北职业技术学院是北京市人民政府批准、教育部备案、独立设置的全日制高等职业技术院校，是北京市示范性高职学院。它的前身是1984年成立的北京建筑工程学院怀柔分院，2002年经北京市人民政府批准，更名为北京京北职业技术学院，成为北京郊区第一所独立设置的高职院校。学院坐落在风景秀丽、空气清新、荣膺“生态福地、京郊明珠、中国影城、国际会都”美誉的怀柔城区，周围环境优美、交通便利、风景名胜聚集，是学习生活的理想之地。学院占地186亩，建筑面积62159平方米;现有全日制在校生2100人，成人教育在校生',666713);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京交通职业技术学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.jtxy.com.cn/','北京交通职业技术学院前身是北方交通大学昌平分校，北方交通大学昌平分校建于1984年，随着学校的发展和办学水平的提高，于1999年改建为北方交通大学昌平职业技术学院，经过6年的发展，于2005年1月经北京市人民政府批准，北京交通职业技术学院正式成立，创造了学院发展史上新的里程碑。北京交通职业技术学院座落于有北京后花园之称的昌平区，毗邻十三陵，八达岭高速路从门前穿过，气候怡人，环境优美，属北京之最。学院占地150亩，建筑面积69800平方米，教职工200余人，在校生1080人，与北京交通大学、北京邮',238298);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华北科技学院',' ','河北',1,1,0,0,0,' ','暂空',' ','http://zsjy.ncist.edu.cn/','华北科技学院是国家安全生产监督管理总局惟一直属高校，其前身是原国家煤炭工业部1984年投资兴建的北京煤炭管理干部学院分院;1993年改制为华北矿业高等专科学校，面向全国招生;1997年被原国家教委评为全国27所示范性高等工程专科重点建设学校之一;2002年升格为普通本科院校，更名为华北科技学院;2011年获得工程硕士(安全工程领域)专业学位研究生培养资格。学校占地面积800亩，建筑面积43万平方米。教学科研仪器设备总值1.90亿元。计算机网络、多媒体等现代教育技术应用广泛，是中国教育科研网城市节',4053394);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民武装警察部队学院',' ','河北',1,10,0,0,0,' ','暂空',' ','http://www.wjxy.edu.cn/','中国人民武装警察部队学院（简称武警学院），隶属于公安部，是唯一一所为公安边防、消防、警卫部队培养指挥管理和专业技术警官的现役一本院校。学院坐落于新兴的现代化城市河北省廊坊市，毗邻北京、天津，区位优越，交通便利，环境优美。学院学科专业齐全，特色鲜明，涵盖军事学、法学、工学、管理学等多个学科门类，共开设边防管理、边防指挥、消防工程、火灾勘查、电子信息工程、核生化消防、消防指挥、抢险救援指挥与技术、思想政治教育、警卫学等10个本科专业。拥有法学、安全科学与工程、公安学、公安技术、军队指挥学、军队政治工',1087656);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东警官学院',' ','广东',1,11,0,0,0,' ','暂空',' ','http://www.gdppla.edu.cn/','广东警官学院的前身是建立于1949年11月的广东省公安干部学校，五十多年来，学院经历了政法干部培训，政法中专、大专，公安专科、本科教育的历程，目前是华南地区第一所也是唯一的公安本科院校。五十多年来为社会输送了十多万政法类专门人才，被誉为“南粤警官摇篮”。广东警学院办学特色有浓厚的岭南文化作背景。敢为人先，是岭南文化、广东文化的精华。一、服务公安队伍建设的办学理念服务公安队伍建设的办学理念，始于建校初期的广东公安干部学校，从单一的公安政法干警培训教育，到本科公安教育的办学历程，办学理念成为厚实的历',1139871);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('深圳职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.szpt.edu.cn','“国家示范性高等职业院校”深圳职业技术学院成立于1993年，在深圳市委、市政府的大力扶持下，学校取得了较快发展。目前，学校占地面积2520亩，校舍建筑面积47.5万平方米。全日制在校生1.7万人，毕业生就业率高，平均就业率达到98%，一直位居全省高校前列。学校办学条件优越。有教学科研行政仪器设备3.58万台（套），总值3.06亿元；纸介图书藏书达到110万册，另有报刊2000余种，电子图书60余万种，多媒体视听资料近2万种；建有主干带宽4000兆、拥有2.7万个信息点的校园网，校园基本实现了信息',827191);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('民办南华工商学院',' ','广东',0,6,0,0,0,' ','暂空',' ','http://www.nhic.edu.cn','南华工商学院是1993年经广东省人民政府批准，原国家教委备案，广东省总工会在广东省总工会干部学校的基础上创办的以文科为主、兼含工科的全日制普通高等专科院校，是全国第一所由社会团体——工会创办的具有普通高等专科学历教育的学校，属国有民办新机制。学院行政主管部门为广东省总工会，业务主管部门为广东省教育厅，学院和干校两块牌子，一套班子，实行党委领导下的院长负责制。学院自建校以来共培养了9691名高等技术应用型专门人才。他们中的大多数已成为广东省尤其是珠三角地区各行业的中坚力量，为各地的经济建设和社会发',264784);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都工业学院',' ','四川',1,1,0,0,0,' ','暂空',' ','http://www.cdtu.edu.cn/','成都工业学院是四川省人民政府举办、省教育厅主管的公办全日制普通本科学校。学校创办于1913年，是辛亥革命后四川省举办的第一所实业学校。历经四川省立第一甲种工业学校、成都无线电机械学校、成都电子机械高等专科学校等多个历史发展阶段。长期隶属电子工业部，2000年调整为四川省教育厅主管。学校现为四川省首批“卓越工程师教育培养试点高校”。建校一百余年来，学校从未间断过办学，始终秉持“手脑并用、学做合一”的校训和“严谨、朴实、勤奋、创新”的校风，为国家培养了一大批优秀人才。陈毅元帅曾于1916-1918在',828532);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('扬州大学',' ','江苏',1,0,0,0,0,' ','暂空',' ','http://zhaoban.yzu.edu.cn/','扬州大学是江苏省人民政府和教育部共建高校、江苏省属重点综合性大学，是全国首批博士、硕士学位授予单位，全国率先进行合并办学的高校。1992年，学校由扬州师范学院、江苏农学院、扬州工学院、扬州医学院、江苏水利工程专科学校、江苏商业专科学校等6所高校合并组建而成。学校办学历史最早可以追溯到1902年由近代著名实业家、教育家张謇先生创建的通州民立师范学校和通海农学堂(后成为南通学院的一部分)。原江苏农学院和扬州师范学院便是在南通学院农科和通州师范学校文史科的根基上发展起来的。参与合并的其他4所高校也均有',4451725);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('私立华联学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.hlu.edu.cn/','私立华联学院创立于1990年。1994年经广东省政府批准、教育部备案，成为省属具有学历教育资格的普通高等学校，同年参加全国高考统一招生。学校在招生资格、贫困学生助学贷款、优秀学生获国家奖学金以及学历等方面，与公办普通高校享受同等待遇。毕业生的学历文凭由国家教育部统一印制，统一编号，并实行电子注册。学院本部位于广州市天河区东圃小新塘合景路，毗邻科学城、奥林匹克体育中心和世界大观。全校占地面积400多亩，校舍建筑面积13万多平方米。学校设有15个系，44个专业，现有全日制在校学生7500多名。学院设',138011);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三江学院',' ','江苏',1,0,0,0,0,' ','暂空',' ','http://www.sju.js.cn/','江苏首家民办本科高校三江学院地处六朝古都、历史文化名城——南京，是经国家教育部批准设置的江苏省首家民办本科普通高校，也是全国最早成立的四所民办本科高校之一。三江学院创办于1992年，2002年2月经国家教育部批准升格为本科高校。2006年5月通过江苏省学士学位授权单位评审。2007年5月我校作为全国首家民办本科高校接受了教育部普通高等学校本科教学工作水平评估，2008年3月以良好的成绩通过教育部普通高等学校本科教学工作水平评估。学校现有铁心桥主校区、东校区、南校区、东山校区、竹山路校区和一个联合',1182810);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武警后勤学院',' ','天津',1,10,0,0,0,' ','暂空',' ','http://www.wjyxy.com ','中国人民武装警察部队后勤学院是在中国人民武装警察部队医学院的主体基础上，武警工程学院后勤专业并入而成，是武警部队惟一的高等医学院校，编制为副军级，隶属武警部队后勤部。学院位于天津市河东区成林道，占地面积703.8亩，总建筑面积21万余平方米。建院二十多年来，学院在武警部队党委和后勤部党委的正确领导下，艰苦奋斗，开拓进取，全面建设实现了跨越式发展：由分散办学到集中办学，由卫校、医专到医学院，由中专教育、大专教育、本科教育、联合培养硕士研究生教育到联合培养博士研究生教育，由单纯的全日制普通教育到全日',314194);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中华女子学院',' ','北京',1,5,0,0,0,' ','暂空',' ','http://zhaosheng.cwu.edu.cn/','中华女子学院地处首都北京，是直属中华全国妇女联合会、教育部批准备案的全国第一所公办全日制本科女子普通高等学校。学校前身是1949年由宋庆龄、何香凝、蔡畅、邓颖超、康克清等老一辈妇女革命家创建的新中国妇女职业学校。六十五年来，学校几经跨越式发展，1995年更名为中华女子学院，2002年正式转制为普通高等学校。学校始终秉承老一辈妇女革命家确立的为新中国培养新女性的办学目标，致力于贯彻男女平等基本国策,推动两性和谐发展，服务妇女全面发展，服务经济社会发展，服务国家总体外交，以“崇德、至爱、博学、尚美”',2176320);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('齐齐哈尔医学院',' ','黑龙江',1,3,0,0,0,' ','暂空',' ','http://218.8.131.159/','齐齐哈尔医学院位于闻名世界的丹顶鹤的故乡，风景秀丽的嫩江之畔。学院始建于1946年，至今已有67年的办学历史。校园规划占地面积110万平方米，建筑面积27万平方米。学院设有25个教学机构。现有教职工4623人，其中副高职以上人员900余人，专任教师中具有博士、硕士学位人员的比例为63%。现有普教、成教、留学生等各类在校生18623人。设有本科专业19个，涵盖了医学、理学、工学、管理学、法学等5个学科门类。其中，精神医学专业为国家一类特色专业，临床医学、精神医学、药学、预防医学等4个专业为省级重点',1138127);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京信息科技大学',' ','北京',1,1,0,0,0,' ','暂空',' ','http://www.bistu.edu.cn/','北京信息科技大学是由原北京机械工业学院和北京信息工程学院合并组建，以工管为主体、工管理经文法多学科协调发展，以培养高素质应用型人才为主、北京市重点支持建设的全日制普通高等学校。学校秉承\"勤以为学，信以立身\"的校训，发扬\"抢抓机遇、迎难而上、争先创优、挑战自我\"的新大学精神，坚持办学指导思想，明确发展目标和办学定位，传承办学优势与特色，紧紧依靠广大师生员工，抓建设、促改革、谋发展，内涵建设与外延发展等各项事业取得显著成效。面向未来，学校正以坚定步伐朝着\"在电子信息、现代制造与光机电一体化、知识管理',2326412);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北体育学院',' ','河北',1,7,0,0,0,' ','暂空',' ','http://www.hepec.edu.cn','河北体育学院是河北省唯一一所独立建制的本科体育院校，是全省体育教学与科研中心。经过20多年的艰苦奋斗与拼搏进取，一个昔日办学条件十分简陋的学院，已经成长为充满朝气、蓬勃发展并具有美好未来的新型高等体育院校。河北体育学院恢复重建于1984年，1985年由原国家教委正式批准建院，是河北省唯一一所独立建制的高等体育专业本科院校。学院坐落在河北省会石家庄市风景秀丽的滹沱河畔，环绕校园的是梨果飘香的太平河生态园区。“河北体育学院”最早出现在1956年的天津（当时的河北省省会）。后来天津成为直辖市，河北省省',261612);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄职业技术学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.sjzpt.edu.cn/','石家庄职业技术学院（原石家庄大学）创建于1984年，是经国家教育部批准、石家庄市人民政府主办的一所全日制普通高等院校；是教育部、信息产业部、建设部确定的计算机应用与软件技术、工业与民用建筑专业领域技能型紧缺人才培养基地；是河北省重点建设的示范性高职院校。经过二十多年的建设，学院发展成为以全日制高职教育为主体，多种办学形式并存，以特色骨干专业为重点，工、经、管、法、文、艺多专业协调发展的高职院校。目前，学院设有管理系、经济贸易系、信息工程系、建筑工程系、化学工程系、机电工程系、电气与电子工程系、艺',200554);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽阳职业技术学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://www.419.com.cn/','辽阳职业技术学院的前身是辽东省辽阳师范学校，始建于1949年。1996年与辽阳师范专科学校合并，更名为辽阳师范高等专科学校。1999年9月经辽宁省人民政府批准，辽阳师范高等专科学校、辽宁经济管理干部学院辽阳分院、辽宁广播电视大学辽阳分校、辽宁工运学院辽阳分院、辽阳商业职工中专五所学校合并，2000年10月，更名为辽阳职业技术学院。目前学院拥有各级各类在校生7510人，班级271个，全日制学生5402人，其中高职高专学生数4811人。设有10个系、部，开设大专、本科两个教学层次，共35个高职和师范',87775);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阜新高等专科学校',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://www.fxgz.com.cn/','阜新高等专科学校是国务院批准，教育部备案的一所全日制普通高校。学校座落在辽宁省阜新市市区,东临三?一八公园，西靠迎宾大桥，南与阜新火车站仅一河之隔，北面是阜新经济技术开发区，交通便利，景色宜人。学校占地面积15万平方米，建筑面积7.5万平方米，教学设备总值1394万元，固定资产总额达1.1亿元。学校教学设备先进，设施齐全，拥有语音广场、微机广场和动画制作、汽车修理、电子通讯、家电制冷、动物解剖、植物组培等大型实验室50余个，校外实训基地20余个。学校现有一院三部六系。一院即农业职业技术学院；三部',72978);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连大学',' ','辽宁',1,0,0,0,0,' ','暂空',' ','http://www.dlu.edu.cn/','大连大学的历史可以追溯到始建于1949年4月成立的大连大学（1950年7月大连大学建制撤销，1978年10月成立大连工学院旅大分校，后为大连工学院分院，1983年11月大连工学院大连分院独立办学更名为大连大学）、1948年3月成立的关东文法专门学校（后为大连师范专科学校）、1950年10月成立的旅大市卫生学校（后为大连市卫生学校）。1987年10月，大连大学、大连师范专科学校、大连市卫生学校三校合一，组建新的大连大学，大连大学设工学院、师范学院、医学专科学校。时任大连市副市长赵亚平出任新的大连大',5002840);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林工商学院',' ','吉林',1,6,0,0,0,' ','暂空',' ','http://www.jlbtc.edu.cn','吉林工商学院是2007年3月经国家教育部批准，由原吉林财税高等专科学校、吉林商业高等专科学校和吉林粮食高等专科学校实体合并组建的省属公办本科院校。原三所学校均成立于上世纪50年代，在半个多世纪的办学历程中，打造了财会、餐旅、粮食、食品等一批名牌专业，为国家建设培养输送了数以万计的优秀人才。学校坐落在北国春城长春市，设有和平、西安、卡伦三个校区，占地面积115.28万平方米，建筑面积30.6万平方米，卡伦校区规划总建筑面积53.81万平方米，现已完成起步区建筑面积19.5万平方米;图书馆藏书179',926597);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京工程学院',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.njit.edu.cn/','南京工程学院是一所江苏省所属的公办全日制普通本科院校，具有本科学生的教育培养资格及相应的学位授予权。学校是全国应用型本科院校专门委员会主任委员单位，全国服务特需硕士专业学位研究生培养单位联盟副理事长单位和中国产学研促进会常务理事单位，也是国家首批“卓越工程师教育培养计划”实施高校、国家“CDIO工程教育模式改革研究与实践”试点高校之一和江苏省协同创新中心培育建设单位。学校经过近百年的办学历程，如今的南京工程学院已成为一所以工学为主的高等工程应用型本科院校，学科专业涵盖工学、经济学、管理学、文学、',2911656);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京审计大学',' ','江苏',1,6,0,0,0,' ','暂空',' ','http://www.nau.edu.cn/','南京审计学院是我国唯一以“审计”命名、以审计为品牌、经管法文理工等学科相互支撑、协调发展的财经类高等院校。学校前身为始建于1983年的南京财贸学院；1987年更为现名；1991年由审计署直属；2000年调整为江苏省与审计署合作共建、以江苏省管理为主；2002年原南京金融高等专科学校并入，组建新的南京审计学院；2009年被国务院学位委员会列为硕士学位授权立项建设单位。学校与中国审计事业同步发展，“为审而立、依审而兴、靠审而强”。办学管理体制调整后，审计署依然一如既往地全方位支持南京审计学院事业发展',4059220);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沙洲职业工学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.szit.edu.cn/website/','1、悠久的办学历史，深厚的文化底蕴沙工创建于1984年，是全国第一所市(县)公办大学，被写入中国高等教育史，载入《中国共产党的七十年》大型历史画册。原全国政协副主席、中科院院士、著名科学家钱伟长教授曾长期担任学院名誉院长，胡耀邦题词“沙工有如扬子水，不尽人才滚滚来”，费孝通题校训“勤奋、求实、开拓、进取”，得到李岚清、刘延东等多位中央领导的关心。沙工已成为张家港人民“以知识改变命运”的重要历史见证。学院先后获得\"江苏省平安校园\"、\"国家职业技能鉴定所\"、\"全国计算机信息高新技术智能化考试站\"、\"',52171);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('蚌埠学院',' ','安徽',1,1,0,0,0,' ','暂空',' ','http://www.bbc.edu.cn/','蚌埠学院于2007年3月由中华人民共和国教育部批准（学校编号11305），由办学历史近30年的蚌埠高等专科学校、蚌埠教育学院、蚌埠职工大学合并组建，是一所以工为主，工学、人文学科、理学、管理学、社会学科多学科协调发展的教学型普通本科院校。2004年5月国家教育部批准在三校合并的基础上筹建蚌埠学院。学院严格按照国家教育部关于“加大投资力度，加快校园规划和建设，加强学科专业建设和师资队伍建设，努力实现三所院校的实质性融合，不断改善办学条件，着力增强学校的科研能力、提高学术水平，争取尽快达到国家规定的',607000);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('池州学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.czu.edu.cn/','池州学院是一所省属普通本科高等学校，坐落在中国第一个生态经济示范区、《中国21世纪议程》试点地区和素有“千载诗人地”美誉的安徽省池州市市区。学院前身是池州师范专科学校，创办于1977年，2007年1月经教育部批准升格为池州学院。学校校园规划面积1868亩，学校规划总建筑面积约54万平方米，新建约24万平方米教学、行政和生活服务用房。建有千兆校园网、开路与闭路播放的教育电视台及卫星远程教育系统；图书馆现有纸质图书71.26万册，电子图书28万册，各类期刊1500余种，拥有中国期刊全文数据库、重庆维',532061);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮南联合大学',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://www.hnuu.edu.cn','淮南联合大学成立于1984年9月，是经省政府批准、国家教育部备案的全日制综合性普通高等专科学校,位于中国较大城市——安徽省淮南市。学校北临曲折蜿蜒的汤汤淮水，南依郁郁葱葱的舜耕山脉，紧临城市交通主干道，环境优美，交通便利。建校20多年来，学校在安徽省教育厅、淮南市委、市政府和社会各界的关心和支持下，各项工作取得了长足的发展。先后获得“安徽省普通高校学生管理工作优秀单位”、“安徽省普通高校招生先进工作集体”、“安徽省普通高校先进教务处”、“安徽省高校毕业生就业工作先进集体”、“安徽省高校治安综合治',98804);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三明学院',' ','福建',1,0,0,0,0,' ','暂空',' ','http://218.5.241.16/','办学历史三明学院是公办省属全日制普通本科院校，坐落在福建省中部新兴工业城市——三明市，是全国创建精神文明先进城市和国家卫生城市、园林城及中国优秀旅游城市。学校的前身是三明高等专科学校，办学历史还可以追溯到1903年陈宝琛创办、被誉为“闽师之源”的全闽师范学堂。近年来，学校先后被评为福建省党的建设和思想政治工作先进高等学校、福建省文明学校、全国精神文明建设工作先进单位、全国绿化模范单位。办学条件现有校园面积1012亩，各类建筑面积21.57万平方米，其中教学行政用房面积12万平方米，学生公寓面积6',1246072);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('龙岩学院',' ','福建',1,12,0,0,0,' ','暂空',' ','http://www.lyun.edu.cn/','岩学院是经教育部批准设立的福建省属公办、全日制本科高校。学校前身为创办于1958年的龙岩师范高等专科学校，历经半个世纪的发展，已成为闽西红土地上唯一所全日制本科院校。学校位于福建省西部（通称闽西）中心城市龙岩市内美丽的奇迈、凤凰山麓，傍依著名爱国主义教育基地——古田会议纪念馆、国家4A级风景名胜区——冠豸山、“世界文化遗产”——永定客家土楼。龙岩市毗邻厦门经济特区、闽南金三角开放区，是闽粤赣三省的交通枢纽和物流集散地；与江西省赣州、广东省梅州两市接壤，航空、铁路、高速公路四通八达，是著名的老区、',996658);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建商学院',' ','福建',1,6,0,0,0,' ','暂空',' ','http://www.fjcc.edu.cn/','福建商业高等专科学校是省教育厅直属的商科类普通高等学校，位于风景秀丽的福州市鼓楼区五凤山下。其办学渊源可追溯到创建于1906年的“福州青年会书院”。解放后，由私立福州青年会商业职业学校(青商)、福建省立高级商业职业学校(高商)、福州市立初级商业职业学校(市商)三校合并，后几迁其址，几易其名，1974年定名为福建省商业学校。1984年经国家教育部批准升格为高等专科学校。目前占地916.81亩潘渡校区正在新建。学校先后获得中共中央宣传部、团中央授予“社会实践活动先进单位”，省人事厅授予“2004-2',185316);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('漳州职业技术学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.fjzzy.org','“国家示范性高等职业院校”漳州职业技术学院前身为创办于1984年的漳州职业大学，是由漳州市人民政府举办的一所全日制公办普通高职院校。2002年4月，经福建省人民政府批准，更名为漳州职业技术学院。学院坐落于漳州市区风景秀丽的马鞍山麓，是漳州市高校园区的主体学校。近年来，学院找准培养一线高技能人才的办学定位，适时抓住福建省建设海峡西岸经济区、漳州市实施“工业强市”发展战略的良机，确立了“规模适度，夯实基础，强化特色，争创一流”的办学思路，办学质量和办学水平不断提高，各方面成效显著。学院占地1250亩',108338);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('闽西职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.mxdx.net','“国家示范性高等职业院校建设计划”骨干高职院校闽西职业技术学院是经福建省人民政府批准设置、教育部备案的公办全日制普通高校，是福建省示范性高等职业院校、全国建设行业技能型紧缺人才培养培训院校。学校创办于1983年，前身为闽西职业大学，2004年2月，经福建省人民政府批准，更名为闽西职业技术学院。学院占地30万平方米，规划用地45万平方米，建筑面积20万平方米，校园环境优美、安全和谐、布局合理，享有“花园式”学府的美誉。学院地处闽粤赣三省的交通要道和物资集散地――福建省龙岩市。龙岩是全国著名革命老区',76669);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黎明职业大学',' ','福建',0,0,0,0,0,' ','暂空',' ','oldname": "黎明职业大学','黎明职业大学创办于1984年，是由泉州市人民政府举办的省属公办全日制高等职业院校，福建省首批示范性高职院校。学校继承梁披云先生的教育思想，弘扬“学生本位、爱心教育、手脑并用”的“黎明教育理念”，规模、质量、效益稳步提高。2005年以来，学校先后荣获全国职业教育先进单位、全国高职高专人才培养工作水平评估优秀院校、福建省文明学校、福建省大中专毕业生就业工作先进集体、全国高等教育学籍学历管理工作先进集体、全国高职高专院校科研工作先进单位、全国大中专院校“三下乡”社会实践先进集体、闽台教育交流合作先行先',608350);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌工程学院',' ','江西',1,1,0,0,0,' ','暂空',' ','http://www.nit.edu.cn/','南昌工程学院是教育部批准的具有普通本科学历教育招生资格的公办高等学校，原为水利部直属院校，2000年经国务院调整为“中央与地方共建，以地方管理为主”。其前身是1958年创建的江西水利水电学院，1978年恢复专科层次办学，1992年更名为南昌水利水电高等专科学校，是国家教育部的全国示范性高等工程专科重点建设学校,2004年5月经教育部批准，升格为本科院校南昌工程学院。学校紧抓高等教育的发展机遇，及时确定学校发展定位：立足江西，面向全国，兼顾水利行业；以本科教育为主，专科教育和继续教育为辅，拓展研究',2218281);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东警察学院',' ','山东',1,11,0,0,0,' ','暂空',' ','http://www.sdpc.edu.cn','山东警察学院是一所具有光荣革命传统的学校，其前身是山东省警官学校，1946年创建于山东省临沂。学院目前主要承担两项任务：一是培养全日制公安本、专科大学生；二是承担山东省、全国和国外警察的培训任务，是公安部确定的全国县市公安局长两个培训基地之一，公安部、商务部确定的国家援外培训基地之一，从2006年开始，承担国外警察的培训任务。60多年来，在山东省委、省政府、省公安厅和省教育厅的领导下，学院始终坚持政治建警、政治建校，坚持主动服从服务于警务实战需要，坚持培养应用型人才，坚持从严治警、从严治校，为各',707963);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南工学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.hneeu.edu.cn','河南工学院始建于1975年，是教育部批准设立的省属公办普通本科高校，是全国就业先进工作单位和全国毕业生就业50强高校。学校位于郑洛新国家自主创新示范区、中原城市群核心区重要城市、豫北工业名城——新乡市。学校占地面积1127余亩，校舍建筑面积46万平方米，馆藏适用纸质图书150万余册，电子图书17万余种;教学科研仪器设备总值1.8亿余元，与许继集团、中兴通讯和美国罗克韦尔公司等国内外知名企业联建了150多个功能齐全的校内实验实训基地，拥有国家级、省级示范性实训基地11个，国家职业技能鉴定所1个，市',1744497);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安阳工学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.ayit.edu.cn/','安阳工学院坐落于历史文化名城、中国八大古都之一的河南省安阳市。安阳位于晋冀豫交汇处，西依太行、东临沃野、南望河洛、北联京畿，交通便利，京广铁路、京广高铁、京港澳高速公路贯穿南北，长兖铁路、南长高速公路横跨东西。安阳是甲骨文的故乡、《周易》发祥地、岳飞故里，是曹操高陵、人工天河红旗渠和中国文字博物馆所在地，文化底蕴深厚。安阳工学院是一所公办全日制普通本科高校，前身为安阳大学，始建于1983年，2004年5月经教育部批准升格为普通本科院校，更名为安阳工学院，2013年11月以优异成绩通过教育部本科教',1523429);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北警官学院',' ','湖北',1,11,0,0,0,' ','暂空',' ','http://www.hbpa.edu.cn/','中国湖北警官学院位于九省通衢的中国中部中心城市武汉市解放大道86号，是2002年5月经教育部同意、湖北省政府批准建立的一所普通公安本科院校。其前身可追溯至1949年7月成立的“湖北省人民政府公安厅干部训练班”，历经了在职民警培训、中专、大专、本科学历教育等发展阶段。2005年4月，学院取得学士学位授予资格。学院现有占地面积992亩，包括学院本部472亩，南湖驾驶培训中心112亩，（随州）实战训练基地408亩；建筑面积273593平方米；教学仪器设备总值达3649.9万元；图书馆藏书60万册（其中',689993);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鄂州职业大学',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.ezu.net.cn','“国家示范性高等职业院校建设计划”骨干高职院校鄂州职业大学位于鄂州市风景旖旎的莲花山麓、洋澜湖畔，依山环水，拥绿叠翠，景色宜人。学校创办于1984年，1991年后，鄂州广播电视大学、鄂州师范学校、鄂州卫生学校、鄂州工业学校等相继整体并入。经过20多年来的艰苦奋斗，学校已建设成为一所以土建、制造专业大类为主，其他专业大类协调发展的全日制普通高职院校。一、学校发展目标和培养人才规格定位学校发展目标定位学校根据行业经济和中部地区经济发展需要，结合区域经济社会发展和自身实际，解放思想，转变观念，深化改革',148848);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('荆楚理工学院',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.jcut.edu.cn/','荆楚理工学院是经教育部批准于2007年3月在原荆门职业技术学院、沙洋师范高等专科学校合并基础上建立的一所公立省属本科层次的普通高等学校。学校位于素有“荆楚门户”之称的历史文化名城——荆门中心城区，坐落在风景秀美的白龙山麓。学校设有16个教学院（部），有全日制普通本、专科在校生17000余人，现有专科专业点58个，本科专业点11个，并在今后几年内,以每年6-10个本科专业的速度递增，涵盖理、工、教、医、文等学科门类。通过深化教学改革，调整专业结构，积极开展人才培养模式的探索与实践，形成了数控技术、',1055389);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南工程学院',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.hnie.edu.cn/','湖南工程学院始建于1951年，坐落在一代伟人毛泽东的故乡湘潭市。2000年6月，经教育部批准，由湘潭机电高等专科学校与湖南纺织高等专科学校合并组建而成。湘潭机电高等专科学校创建于1951年，原隶属于国家机械工业部，是全国示范性高等工程专科重点建设学校;湖南纺织高等专科学校创建于1978年，原隶属于湖南省纺织工业厅。学校实行中央与湖南省共建，以湖南省管理为主的管理体制。学校升本以来，始终坚持应用型办学定位。2007年，以优异成绩顺利通过了教育部本科教学工作水平评估。2010年6月，获批为教育部“卓',2144415);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('仲恺农业工程学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://zsb.zhku.edu.cn/default.aspx','仲恺农业工程学院是一所以农、工学科为优势，农、工、理、经、法、管、文、艺协调发展的多科性省属本科大学。学校坐落在历史文化名城——广州，校园集云山之神秀，汇珠水之灵气，是读书治学的理想园地。学校已有88年的办学历史，前身为仲恺农工学校，是第一次国共合作时期，近代民主革命著名的政治活动家何香凝先生等提议、国民党中央为纪念廖仲恺先生爱护农工的意愿而决定创办的。学校于1927年招生，何香凝先生首任校长15年。1984年，经教育部、农牧渔业部批准，学校升格为本科院校，定名“仲恺农业技术学院”，国家副主席王',1198383);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('五邑大学',' ','广东',1,0,0,0,0,' ','暂空',' ','http://zsb.wyu.cn/','1985年，乘着改革开放的春风，内外合力，一所全日制本科综合性大学—五邑大学在中国第一侨乡广东省江门市应运而生。江门市辖新会、台山、开平、恩平、鹤山五市（区）素称五邑而闻名海内外，学校由此得名。学校占地约1000亩，天沙河蜿蜒横贯其中，一河两区。校园内湖光敛滟，幽雅宁静，可谓是“远山含翠教读胜地，近湖吐烟治学佳境”。建校以来，学校坚持“面向地方，服务社会”的办学方针，在人才培养、科研和社会服务、对外交流与合作、内部管理等方面形成了四大办学特色。学校专业设置与经济社会发展紧密结合，现设有管理学院和',1829463);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西体育高等专科学校',' ','广西',0,7,0,0,0,' ','暂空',' ','http://www.gxtznn.com/','广西体育高等专科学校成立于1956年6月，是自治区人民政府主办，自治区体育局和教育厅共同管理的普通高等学校。现设有体育与教育系、运动系、运动医学系、体育产业与传媒系、土木建筑工程系、公共课教学部及1所附属职业中专等7个教学单位，形成了教育学、文学、医学、建筑学、管理学等学科专业。开设有心理咨询、体育教育、运动训练、社会体育、体育保健、体育服务与管理、高尔夫运动技术与管理、高尔夫服务与管理、运动休闲服务与管理、民族传统体育、新闻采编与制作、营养与食品卫生、表演艺术、运动康复、休闲旅游、服装表演、建',59230);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('梧州学院',' ','广西',1,0,0,0,0,' ','暂空',' ','http://www.gxuwz.edu.cn','梧州学院创办于1985年，前身是广西大学梧州分校。2003年10月，广西大学梧州分校与梧州教育学院、梧州师范学校合并组建新的广西大学梧州分校，同时向教育部申报升格为本科院校。2006年2月，教育部批准，在广西大学梧州分校的基础上建立梧州学院。梧州学院是一所多科性地方全日制普通高等本科院校，实行“区市共建，以市为主”的管理体制。梧州学院地处素有千年古城、百年商埠、世界人工宝石之都美誉的广西梧州市。梧州是广西高等教育的发源地。1928年，广西大学在梧州建校办学。目前，梧州学院校园内仍保存有广西大学建',834579);
