insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('攀枝花学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.pzhu.cn','攀枝花学院是教育部布点在川西南、滇西北唯一一所以工为主的综合性普通本科院校。学校座落于我国西部最大的钢铁工业基地、中国著名的“钒钛之都”、全国优秀旅游城市、全国唯一一座以花命名的山水园林城市--攀枝花市。学校现占地1260亩，校舍面积50余万平方米，固定资产总值6.07亿元，馆藏纸质图书181万册，电子资源113万种（册），教学科研仪器设备总值1.2亿元，下设19个教学和科研单位，开设48个本科专业，41个专科专业，专业设置涵盖理、工、文、法、经、管、医、艺、农9个学科门类，普通本专科在校生及研',1361174);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('玉溪师范学院',' ','云南',1,4,0,0,0,' ','暂空',' ','http://www.yxnu.net','玉溪师范学院是一所云南省属全日制普通本科院校，学校创建于1978年，是一所以师范专业为基础，哲学、经济学、法学、教育学、文学、理学、工学、农学、管理学、艺术学等多学科共同发展的综合性大学。2007年学校顺利通过教育部本科教学工作水平评估，获得“良好”成绩，先后荣获“全国精神文明建设工作先进单位”、“全国学校艺术教育先进单位”、“云南省文明学校”、“全国文明单位”、“国家级节约型公共机构示范单位”等荣誉称号。学校坐落于滇中著名的“中国十佳休闲宜居生态城市”玉溪市，距昆明市90公里，与红塔集团毗邻，',721686);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('楚雄师范学院',' ','云南',1,4,0,0,0,' ','暂空',' ','http://www.cxtc.edu.cn/','楚雄师范学院是云南省属全日制普通本科高校。2001年5月，经国家教育部批准，在原楚雄师范高等专科学校和楚雄民族师范学校合并基础上组建，融全日制普通高等教育与继续教育、职业技术教育、广播电视教育于一体。2009年荣获中央文明委授予的\"全国文明单位\"称号，是云南省高校中第一家获此殊荣的学校。学校位于享有\"东方人类故乡\"、\"恐龙之乡\"、\"优质野生菌王国\"、\"中国彝族文化大观园\"美誉的云南省楚雄彝族自治州州府楚雄市，有优越的气候条件，常年平均气温16℃，冬无严寒，夏无酷暑，四季如春。距云南省会昆明100',558712);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南警官学院',' ','云南',1,11,0,0,0,' ','暂空',' ','http://www.ynpsc.edu.cn','云南警官学院，始建于1950年。历经云南省公安学校、云南省人民警察学校、云南公安高等专科学校等历史时期，是我国西部省区唯一的公安本科学院。学院是云南省教育厅与云南省公安厅共建、以公安厅管理为主的全日制公办普通本科院校。学院占地570亩，现有教职工429人。其中，专任教师261人，副教授及以上职称人员119人，硕士、博士81人。全日制本专科在校生7000余人。设有禁毒系、侦查系、治安管理系、刑事科学技术系、法律系、计算机科学与技术系、马列主义及社会科学教研部、基础课程教研部、警察体育教研部、警务实',1040698);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明学院',' ','云南',1,0,0,0,0,' ','暂空',' ','http://www.kmu.edu.cn','昆明学院是经国家教育部批准建立的公办全日制综合性本科大学；位于四季如春、风光秀丽的昆明市。学院现有农学院、医学院、经济系、美术与艺术设计系等25个院、系及公共教学部。学院现有25个本科专业，66个专科专业，全日制本专科在校生1.2万余人；有省级重点培育学科；国家级、省级重点建设专业；省级特色专业；国家级、省级精品课程；省级教学团队；省级实验教学示范中心和省级人才培养创新实验区等高层次建设项目19个；学科专业覆盖哲学、经济学、法学、教育学、文学、历史学、理学、工学、农学、医学、管理学等11个学科门',1763464);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('榆林学院',' ','陕西',1,4,0,0,0,' ','暂空',' ','http://www.yulinu.edu.cn','榆林学院是一所陕西省省属综合性本科院校.建校40多年来，先后培养出万余名具有较高层次的实用型人才，为区域经济和社会发展做出了突出的贡献。学院位于国家历史文化名城、国家级能源重化工基地——榆林市，占地840亩，校舍建筑面积29.2万平方米。校园高楼林立，道路纵横。春夏则鸟语花香，绿树成荫；秋冬则风清沙白，青松长翠。风景独好，环境优雅，是求学治学的理想之地。学院现有教学仪器设备总值32550万元.图书馆藏图书60余万册,并设有168座的电子阅览室和教学科研用计算机1000多台,专业实验室135个，多',1037588);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('商洛学院',' ','陕西',1,4,0,0,0,' ','暂空',' ','http://www.slxy.cn/','商洛学院是陕西省教育厅直属的一所多科性本科学院，其前身是成立于1976年的“陕西师范大学商洛专修科”，1984年经陕西省人民政府批准更名为“商洛师范专科学校”，2006年2月经教育部批准升格为本科院校。学院位于著名作家贾平凹故乡、陕东南生态园林城市——商洛市，占地465亩，校舍面积182226m2。教学仪器设备总值2545万元。有纸质图书62万册，电子图书29万册，纸质期刊1100余种，电子期刊4600种。学院现有教职工463人，其中专任教师319人，副教授以上职称118人；长期聘请中科院院士侯',822118);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安康学院',' ','陕西',1,0,0,0,0,' ','暂空',' ','http://www.aku.edu.cn','安康学院的前身是创建于1959年的安康大学，1963年因国家经济困难停办。1978年重新设立“陕西师范大学安康专修科”，1984年经陕西省人民政府批准，更名为“安康师范专科学校”。2006年2月14日经教育部批准改建为全日制省属本科院校--\"安康学院\"。学院座落在秦巴腹地，汉水之滨，被誉为“西安后花园”的西部名城——陕西省安康市。安康交通便捷，襄渝、西康、阳安铁路在此交汇，316、210国道和西汉、西康高速穿境而过。学院北眺汉江，南倚国家级香溪洞风景区，校园花木繁茂，碧草如茵，风光秀丽，环境幽雅',749678);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安培华学院',' ','陕西',1,0,0,0,0,' ','暂空',' ','http://www.peihua.cn/zhaosheng/default.aspx','西安培华学院是2003年经国家教育部批准升格为西部首家、面向全国招生的民办本科普通高等院校。学校前身可追溯到1928年由陕西女子职业教育促进会筹办、陕西省教育厅批准设立的“西安第一女子平民职业学校”，在学校发展过程中“七迁其址，九易其名”。1984年经陕西省人民政府批准，国家教委备案，由著名教育家姜维之先生在恢复原培华女子职业学校的基础上，恢复成立了新中国首家专门招收女性学生，开展女性教育的普通高校“西安培华女子大学”。2003年经国家教育部批准升格为普通本科学校，并更名为西安培华学院，2007',1944462);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃政法学院',' ','甘肃',1,11,0,0,0,' ','暂空',' ','http://www.gsli.edu.cn','甘肃政法学院始建于1956年，是一所省属普通高等院校，经过多年的建设和发展，特别是近几年的快速发展，现成为我国重要的政法院校之一。学校地处西北政治、经济、交通中心的甘肃省兰州市，位于兰州市科教中心的安宁区，文化气氛浓郁、环境优美、交通便利。学校占地545亩，校舍建筑面积22万平方米。图书馆藏书近40余万册，中外文报刊近2000种。现设有法学院、公安分院、管理学院、人文学院、计算机科学学院、行政学院、艺术学院、继续教育学院、人民武装分院、东方现代管理学院等10个二级学院和体育教学部、甘肃省政法干部',1015316);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北方民族大学',' ','宁夏',1,9,0,0,0,' ','暂空',' ','http://www.nun.edu.cn/','北方民族大学(原西北第二民族学院，院校代码：11407)是国内唯一建立在少数民族自治区的部属综合性民族高校，直属国家民族事务委员会，坐落在素有“塞上江南”之称的宁夏回族自治区首府银川市。学校始建于1984年，2008年1月教育部批准在原西北第二民族学院基础上正式更名为北方民族大学。学校现占地1150亩，校舍面积30万平方米，现有教职工924人，其中，专任教师654人。现面向全国31个省(市、区)招生，截至2007年12月，有全日制在校生10000余人。办学条件优良，教学设施完备，校园环境幽雅，被',2481343);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国石油大学（北京）',' ','北京',1,1,0,1,0,' ','暂空',' ','http://www.cup.edu.cn/','中国石油大学是国家教育部直属，首批进入“211工程”建设的全国重点大学。是一所石油特色鲜明，以工为主，多学科协调发展的研究型大学。中国石油大学（北京）是中国石油大学的重要组成部分，坐落在风景秀丽的北京十三陵旅游区，通过八达岭高速公路直达北京市区；这里风景秀丽、环境幽雅、空气清新，是读书与做学问的胜地。2000年2月，在国务院部门（单位）所属学校管理体制和布局结构调整过程中，中国石油大学成建制由中国石油天然气集团公司划归教育部直属。经国家教育部批准，2000年6月，中国石油大学成立研究生院，200',2164752);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国地质大学(北京)',' ','北京',1,12,1,1,0,' ','暂空',' ','http://www.cugb.edu.cn/','中国地质大学（北京）是教育部直属的以地质、资源、环境、地学工程技术为主要特色，理工为主，理、工、文、管、经、法多学科协调发展的全国重点大学。是我国最早建有研究生院的33所高校之一，是首批进入国家“211工程”建设和“优势学科创新平台”建设的大学。学校的前身是1952年由北京大学、清华大学、天津大学和唐山铁道学院的地质系（科）合并组成的北京地质学院。1987年经国家教委批准，学校更名为中国地质大学，在京、汉两地独立办学，分别称中国地质大学（北京）和中国地质大学（武汉）。中国地质大学（北京）名师荟萃',1764921);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学',' ','北京',1,1,0,0,0,' ','暂空',' ','http://www.buu.edu.cn/','北京联合大学是1985年经教育部批准成立的北京市属综合性大学，其前身是1978年北京市依靠清华、北大等大学创办的36所大学分校。北京联合大学以培养适应国家特别是首都经济社会发展需要的高素质应用性人才为己任，经过30多年的建设与发展，学校的综合实力显著增强，形成了经、法、教、文、史、理、工、医、管、艺等10个学科相互支撑、协调发展，以本科教育为主，研究生教育、高职教育和继续教育协调发展的完备人才培养体系，是北京市重点建设的应用型人才培养基地，也是北京地区规模最大的高校之一。学校拥有14个校区，分布',4056229);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学应用文理学院',' ','北京',1,0,0,0,0,' ','暂空',' ','http://www.cas.buu.edu.cn/','北京联合大学应用文理学院的前身是1978年建校的北京大学第一分校（1983年更名为北京大学分校）和中国人民大学二分校，1985年两所分校分别归入北京联合大学并于1994年合并，成立了北京联合大学应用文理学院。目前，学院有全日制本专科在校生3100余人，是一所以培养本科生为主，兼有研究生教育、成人教育和留学生教育，包含文学、理学、工学、历史学、经济学、法学、管理学等多学科的学院。我院以发展应用学科、培养应用型专门人才为办学方向。三十年来，根据首都经济、科技、文化的实际状况和发展趋势，不断优化学科专',66262);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学商务学院',' ','北京',1,6,0,0,0,' ','暂空',' ','oldname": "北京联合大学商务学院  曾用名：北京工业学院第一分院','学院始建于1978年，前身为北京工业学院（现北京理工大学）第一分院，历经30余年的发展，目前，学院已建设成为一所以经济学、管理学为主的普通本科学院。学院现有工商管理（国际商务管理）、财务管理、会展经济与管理、市场营销（国际物流）、国际经济与贸易、金融学、信息管理与信息系统、电子商务等八个本科专业，现有全日制在校生2200余人。学院以工商管理一级学科硕士学位授权点以及国际贸易学和管理科学与工程等校级重点建设学科为支撑，以服务经济与贸易研究所、国际商务研究所、会展经济研究中心等6个研究所为科研平台，',132615);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学师范学院',' ','北京',1,4,0,0,0,' ','暂空',' ','http://tc.buu.edu.cn/','北京联合大学师范学院前身为1978年成立的北京师范大学分校，1985年更名为北京联合大学职业技术师范学院，2003年改为现名。该院是一所教育部批准的具有高等学历教育招生资格的北京市属公立普通高等学校，主要为北京市中等职业学校和北京市普通中等学校培养师资，并为首都经济社会发展培养高级专业技术人才。学院办学以本科层次为主，也有部分高职(专科)专业。现设艺术设计、音乐学、电子信息工程、计算机科学与技术、生物技术、食品科学与工程、应用心理学、金融学、会计学、英语、汉语言文学等本科专业和外事文秘、会计、电',114599);
