insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学管理学院',' ','北京',1,0,0,0,0,' ','暂空',' ','oldname": "北京联合大学管理学院','北京联合大学管理学院成立于2002年3月，是一所年轻而富有朝气的、致力于经济管理类人才培养的学院。学院以企业管理学、金融学、管理科学与工程、区域经济学等学科为依托，以北京中小企业发展研究中心、首都金融研究中心、首都经济与企业发展研究所、应用经济与管理研究所等科研机构为平台，设有金融学（含保险方向）、会计学、信息管理与信息系统、工商管理（含国际商务方向）、财务管理、电子商务6个本科专业，在校生约2400人。自2010年起，管理学院实行“大类招生、分级教学、学生自主、多元发展”的人才培养模式，各专业',11199);
