insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学旅游学院',' ','北京',1,0,0,0,0,' ','暂空',' ','oldname": "北京联合大学旅游学院','旅游学院创建于1978年，是我国最早建立的专门培养旅游管理高级人才的学院。学院主要面向国家特别是首都旅游产业，培养基础扎实、实践能力强，具有社会责任感和国际视野的高素质旅游管理人才。学院现有旅游管理、酒店管理、财务管理、会展经济与管理、英语、日语6个本科专业，其中旅游管理是国家级特色专业建设点、北京市重点建设学科和硕士学位授权点。学院另有酒店管理、烹饪工艺与营养、应用日语等4个高职专业，目前有全日制在校本专科学生2300余人。近年来，学院不断加强教学建设与改革，在国家级特色专业、国家级优秀教学团',11165);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学生物化学工程学院',' ','北京',1,0,0,0,0,' ','暂空',' ','oldname": "北京联合大学生物化学工程学院','生物化学工程学院是一所面向现代都市工业、现代服务业和文化创意产业，以工为主、工程和管理相结合、工程和艺术相结合的综合性学院。学院以本科教育为主，开展多层次办学，为首都现代化建设培养了大批应用性高级专门人才。学院位于朝阳区垡头小区，校园环境优美，毗邻欢乐谷，占地面积约120亩，建筑面积53249平方米，学院由教学楼、实验楼、办公楼、食堂及多功能厅、学生公寓和体育场等建筑群组成。所有在校生均可安排住宿，校园宽带网接通到教室、办公室和学生公寓。学院现有全日制在校生3100余人，现有信息与控制工程系、生',8511);
