insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学信息学院',' ','北京',1,0,0,0,0,' ','暂空',' ','oldname": "北京联合大学信息学院','北京联合大学信息学院主要是培养信息技术（IT）人才,其前身是1978年建立的清华大学分校、北京邮电学院分院等。学院设有四个系、三所和一中心，即计算机工程系、软件工程系、电子工程系、通信工程系、信息技术研究所、微点电子应用技术研究所、传感网与可靠性和工程技术应用中心。我院有“计算机科学与技术”、“软件工程”、“信息与通信系统”和“电子科学与技术”四个一级学科，其中“计算机科学与技术”和“软件工程”是一级硕士学位点。“计算机应用技术”学科是北京联合大学首批硕士学位授权学科点之一。“信息与通信工程”是',5232);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学应用科技学院',' ','北京',1,0,0,0,0,' ','暂空',' ','oldname": "北京联合大学应用科技学院','应用科技学院是北京联合大学专门从事高等职业教育的一所学院，位于风景优美的北京市昌平区十三陵景区石牌坊。学院专业设置有IT类、经济管理类、媒体艺术设计类、语言类等，面向北京市快速发展的现代服务业，培养服务于文化创意产业、金融商贸服务业和IT产业一线的高素质技术技能型专门人才或高素质技术服务人才。学院与阿里巴巴、神州数码、用友、中国人寿、幸星、同美、世纪工厂等多家企业建立合作关系，与多所国外大学有交流合作项目，优秀学生推荐参加专升本考试，通过考核后按照北京市规定升入本校相关专业学习二年，完成学习后获',13048);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京联合大学自动化学院',' ','北京',1,0,0,0,0,' ','暂空',' ','oldname": "北京联合大学自动化学院','北京联合大学自动化学院成立于2002年，其前身系三所学院调整合并而成。第一所是清华大学二分校，后改名为自动化工程学院，校址设在丰台区沙子口。其后与清华大学分校合并到搬到黄化门校区，继之又搬迁至北四环小营北京联合大学校本部，并入北京联合大学信息学院。第二所是北京航空学院分校，后改名轻工建材学院，校址设在宣武区盆儿胡同，以后搬迁至北四环小营北京联合大学校本部并入北京联合大学信息学院。第三所是北京工业大学一分校，后改名北京联合大学机械工程学院，校址在朝阳区三里屯。2002年，将北京联合大学信息学院自动',9481);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京城市学院',' ','北京',1,0,0,0,0,' ','暂空',' ','http://www.bcu.edu.cn/','北京城市学院是经国家教育部批准成立的一所综合性普通高校，具有颁发国家承认的研究生、本科学历学位资格，并举办中职、高职专科教育。北京城市学院创建于1984年，是新中国第一所实行公有民办体制的新型高校，以务实、创新而著称，被誉为中国高等教育改革的一面旗帜。建校以来，学校始终恪守为民办学的公益性宗旨，坚持以市场为导向，以应用型、实用性为特色，以服务区域发展为目标，走“本科立校，依法治校，优质强校，特色兴校”的发展道路；学校实施“适合教育、全人教育、有效教育、实用教育”的育人理念，全力创建高水平大学，全',2830520);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北金融学院',' ','河北',1,6,0,0,0,' ','暂空',' ','http://www.hbcf.net','河北金融学院创建于1952年，原属中国人民银行总行，2000年划归河北省人民政府，实行“中央与地方共建、以地方管理为主”的管理体制，是华北、西北地区唯一一所具有鲜明金融特色的省部共建院校。学院秉承“以学生为本”的教育管理理念，突出对学生的关爱和服务，充分发挥管理的内在育人功能，获得良好的教育效果。先进的办学条件和办学理念，以及浓郁的文化氛围，滋养着我院学子特有的志趣和气质。学院占地1323亩，现有专任教师479人，其中，副高职以上教师占教师总数的36%，硕士以上学位教师占教师总数的50.5%。学',2454086);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('张家口职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.zhz.cn','张家口职业技术学院的前身是创建于1984年的张家口大学，1997年经教育部批准更名为“张家口职业技术学院”，2001年根据张家口市大中专院校布局调整的精神，学院与国家级重点中专—张家口市农业机械工程学校合并，组建新的张家口职业技术学院，2004年省部级重点中专——张家口工业交通学校并入我院。经过多年的建设和发展，我院现已形成了以工科为主，涵盖文、经、管、艺等专业群协调发展的办学格局。我院设主校区和南校区，规划占地750亩，目前占地425.9亩，建筑面积14.2万平方米，固定资产总值1.53亿元，',114004);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('集宁师范学院',' ','内蒙古',1,4,0,0,0,' ','暂空',' ','http://www.jntc.nm.cn','集宁师范学院是一所具有深厚历史渊源和文化底蕴的学校。1931年，原绥远省在集宁创办绥远省立第二师范学校，开启了乌兰察布近现代师范教育的先河。1958年，依托之前设置的集宁师范学校，集宁师范专科学校正式诞生。1983年成立乌兰察布盟教育学院与乌兰察布师范专科学校合署办学。1995年，乌兰察布师范专科学校更名为集宁师范高等专科学校。2006年，在全国高职高专人才培养工作水平评估中被评为“优秀”。2009年3月26日教育部批准我校升格为本科高校，校名为集宁师范学院。建校50年来，集宁师范学院立足边疆少',260161);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古丰州职业学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.qcdx.net/','内蒙古丰州职业学院青城学院(原内蒙古青城大学)是1983年经自治区政府批准、国家教育部备案由民盟内蒙古区委创办，学院代码11429，报考代码778，是内蒙古自治区成立最早的高等职业学院。学院成立30年来，在各级领导和社会各界人士的关怀下不断发展壮大，为自治区培养了上万名专业技术人才。内蒙古丰州职业学院校本部位于呼和浩特市高校区，毗邻内蒙古农业大学，与内蒙古大学、内蒙古师范大学，人文环境优雅，交通便利，生活设施完善;部分专业在大学城教学。学院拥有一支实力雄厚、以高级职称教师为龙头，“双师型”教师为',40384);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁科技学院',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.lnist.edu.cn ','辽宁科技学院坐落在辽宁省本溪高新技术产业开发区，是一所以工科为主、多学科发展的省属普通本科院校。学校的前身是始建于1948年的本溪冶金高等专科学校，隶属于原冶金工业部。曾先后经历了本溪工科职业学校、本溪煤铁公司工业专门学校、本溪钢铁工业学校、本溪钢铁学院、本溪钢铁学校等阶段。1996年12月，本溪冶金高等专科学校与本溪市高等职业专科学校联合办学，1997年被教育部确定为27所示范性专科重点建设学校。1998年6月，随着国家高等教育体制改革，学校划归辽宁省政府管理。2000年11月，经辽宁省政府批',1039403);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春汽车工业高等专科学校',' ','吉林',0,1,0,0,0,' ','暂空',' ','oldname": "长春汽车工业高等专科学校','“国家示范性高等职业院校”长春汽车工业高等专科学校始建于1978年，是经国家教育部批准、由中国第一汽车集团公司出资主办的一所以汽车、机电、计算机等专业为主，专业门类齐全的综合性普通高等专科学校。是教育部指定的国家汽车工业实用型紧缺技术人才的重点培养基地。学校坐落于长春市西南部美丽的汽车城内，由中心校区、东校区和西校区构成。占地面积19.1万平方米，校舍建筑面积9.8万平方米。专业实验、实训基地37个、实习工厂4个、校外实习基地51个，教学设施齐全，教学条件优越。学校依托中国第一汽车集团公司雄厚的',355242);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春工程学院',' ','吉林',1,1,0,0,0,' ','暂空',' ','http://www.ccit.edu.cn','长春工程学院是2000年由三所具有50年办学历史的全国示范性高等工程专科重点建设学校（原长春建筑高等专科学校、长春工业高等专科学校、长春水利电力高等专科学校）合并组建的一所以工为主，工、管、文、理等学科协调发展的多科性普通本科大学，隶属于吉林省人民政府。一、历史沿革学校的办学历史可追溯至新中国建立初期。早在1951年9月到1953年7月，为在短时期迅速恢复和建立国家基础工业体系，经东北人民政府、中央人民政府燃料工业部筹划和批准，分别成立了长春土木建筑高级职业学校、长春测量地质技术学校和长春水力发',2187721);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林农业科技学院',' ','吉林',1,2,0,0,0,' ','暂空',' ','http://www.jlnku.com','吉林农业科技学院是一所以培养应用型人才为主的省属公办全日制本科院校，学院分南北两个校区：南校区位于吉林经济技术开发区，北校区坐落在吉林省左家自然保护区。学校现设有动物科学学院、动物医学学院、食品工程学院、植物科学学院、中药学院、生物工程学院、经济管理学院、信息工程学院、机械工程学院、建筑工程学院、文理学院、外语学院、高等职业技术学院、社会科学、体育等十五个教学院（部）。现有本科专业30个，专科专业29个，涵养农学、理学、工学、经济学、管理学、文学、教育学七大学科门类，在校生10706人，其中留学',643218);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春金融高等专科学校',' ','吉林',0,6,0,0,0,' ','暂空',' ','http://www.cjgz.edu.cn  ','长春金融高等专科学校是中国人民银行创办的、吉林省唯一的中央与地方共建金融院校。学校集金融人才培养、金融行业培训、金融科技创新、金融文化传承与创新为一体，是吉林省重要的金融教育产业服务基地，金融行业人才供给的主渠道。学校位于“中蒙俄”经济走廊的节点城市，长吉图、长吉一体化、长东北发展战略叠加区——吉林省长春市，座落在城市交通主干道人民大街南端，依傍在风景秀丽的南湖之滨。学校1978年在吉林省财政金融学校的基础上恢复建立，1983年在校内创办中国人民银行吉林省分行职工大学，1984年升格为长春金融专',229755);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林警察学院',' ','吉林',1,11,0,0,0,' ','暂空',' ','http://www.jljcxy.com/','吉林警察学院是一所以培养具有较高政治素质、扎实理论基础、较强创新能力和实战技能的应用型公安人才和经济建设适用人才的具有鲜明特色的全日制本科院校。吉林警察学院办学历史悠久，其前身是创建于1949年9月的吉林省公安干部学校，1980年改建为吉林省人民警察学校，1985年3月升格为吉林公安专科学校，1993年9月更名为吉林公安高等专科学校，2010年3月升格为吉林警察学院。学院坐落于北国春城长春市，位于亚洲最大的人工林长春净月潭边。学院占地630.4亩，建筑面积16.85万平方米,分为净月校区和南校区',476639);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江东方学院',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.dfxyzj.com/','黑龙江东方学院于1992年创建，是改革开放后由几位离退休教育家创办的黑龙江省第一所民办普通高校。1993年8月，参加全国统一高考招生招收专科生;1995年4月，经原国家教委批准具有专科学历教育资格;2000年8月，在全国统考考生中招收本科生，与黑龙江大学合作办学联合培养;2003年4月，经国家教育部批准升格为本科普通高校;2007年10月，经黑龙江省学位委员会批准获得学士学位授予权;2010年，被黑龙江省政府确定为“特色应用型本科试点建设院校”;2011年10月，经国家学位委员会审定批准，成为专',1222723);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.hljp.edu.cn/','黑龙江职业学院简介67年职业教育办学历史422名副教授以上教师队伍9个国家级重点专业20门国家和省级精品课5个国家级职业教育实训基地555家订单培养协议合作企业800家战略合作大中型企业1.6万名全日制高职在校生5272人招生计划98%以上毕业生就业率黑龙江职业学院于2012年经省政府批准、教育部备案，由黑龙江科技职业学院（原黑龙江畜牧兽医职业学院）并入黑龙江工商职业技术学院更名组建的一所公办全日制普通高校，主要从事高素质技术技能人才培养。学院办学历史悠久，始建于1948年，是省内办学规模最大、',770977);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海电机学院',' ','上海',1,1,0,0,0,' ','暂空',' ','http://www.sdju.edu.cn/','上海电机学院是一所以工学为主，经济学、管理学、文学、艺术学等学科协调发展的全日制普通本科院校。学校前身是创建于1953年的上海电机制造学校，1985年在全国首批试点举办五年制技术专科教育，2002年被列为国家重点建设高职高专院校。2004年9月，经上海市人民政府批准，升格为全日制普通本科高校。2011年10月，学校被国务院学位委员会列为“服务国家特殊需求人才培养项目”专业学位研究生试点单位，开始硕士研究生教育。学校明确提出“技术立校，应用为本”的办学方略，立足上海，辐射“长三角”，服务区域社会经',1794790);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京晓庄学院',' ','江苏',1,4,0,0,0,' ','暂空',' ','http://www.njxzc.edu.cn/','南京晓庄学院坐落在“六朝古都”南京市，是一所具有悠久办学历史和光荣革命传统的公办全日制本科院校。2000年3月，经教育部批准，由南京师范专科学校、南京教育学院、南京市晓庄师范学校组合新建南京晓庄学院。她的历史可以追溯到1927年3月由伟大的人民教育家陶行知先生创办并任校长的晓庄试验乡村师范。1951年经周恩来总理批准复办南京晓庄学校，至今已有80多年的办学历史。建校80多年来，南京晓庄学院积淀了深厚的人文底蕴，凝练了鲜明的办学特色。陶行知、蔡元培、陈鹤琴等一批中国近代著名教育家曾在此执教，原国务',1339005);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('扬州市职业大学',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.yzpc.edu.cn','扬州市职业大学是一所全日制综合性高等职业技术院校，创建于1984年。由扬州市职业大学、扬州教育学院、扬州环境资源职业技术学院和扬州市广播电视大学合并而成，是扬州市属唯一的一所普通高等职业技术院校。学校现有教职工近1730名(含后勤服务总公司员工532人)，有专任教师892人，其中具有硕士和博士学位的教师643人，教授61人，二级教授3人，三级教授13人，副教授409人。有全国优秀教师4人，省“333工程”培养对象18人，省“六大人才高峰”培养对象2人，省“青蓝工程”培养对象46人，江苏省中青年专',283812);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江警察学院',' ','浙江',1,11,0,0,0,' ','暂空',' ','http://www.zjjcxy.cn','浙江警察学院坐落于杭州钱塘江南岸滨江高教园区，是浙江省唯一的公安本科院校，学院前身是创办于1949年5月的浙江省公安干校，历经浙江省公安学校、浙江公安专科学校、浙江公安高等专科学校等时期，2007年3月经教育部和浙江省人民政府批准，升格更名为浙江警察学院。学院现设有侦查学、刑事科学技术、治安学、交通管理工程、法学、计算机科学与技术等6个本科专业和侦查、刑事技术、交通管理、治安管理、警察指挥与战术、警察管理、公共事务管理、法律文秘等10个专科专业。建有警务实战教学训练基地、模拟犯罪现场、车辆驾驶训',393000);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('衢州学院',' ','浙江',1,0,0,0,0,' ','暂空',' ','http://www.qzu.zj.cn/','衢州学院是一所本科层次的公办全日制普通高等学校。2010年3月经教育部批准在原浙江工业大学浙西分校（创建于1985年）的基础上建立衢州学院。学校坐落于中国优秀旅游城市、国家卫生城市、国家园林城市――浙江省衢州市市区。学校现有9个系，开设了6个本科层次专业及29个专科（高职）层次专业，普通全日制在校生近6000人。学校师资力量雄厚、教学设施完备、实验实训条件完善。建校以来，学校为社会输送了近18000名毕业生，学校就业率和就业质量一直保持较高水平，毕业生深受社会的欢迎。地理位置：学校坐落于衢州市市',396553);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建警察学院',' ','福建',1,11,0,0,0,' ','暂空',' ','http://www.fjpsc.edu.cn/','福建警察学院是一所以培养公安机关人民警察和公共安全保卫专业人才为主的全日制本科院校,是福建省唯一的公安高等学府，实行省公安厅和省教育厅双重领导，以省公安厅为主的管理体制。学院的前身是始建于1949年8月的福建省警务干部学校，1950年更名为福建省公安干部学校。1984年10月，在福建省公安干部学校的基础上创办福建公安专科学校。2000年4月，福建省人民警察学校和福建公安高等专科学校合并，成立新的福建公安高等专科学校。2007年5月15日，教育部批准正式建立福建警察学院。经过60年的建设和发展，学',314687);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('莆田学院',' ','福建',1,0,0,0,0,' ','暂空',' ','http://www.ptu.edu.cn/','莆田学院是一所经教育部批准成立，由福建省领导和管理，实行省市共建办学体制，拥有理、工、文、管、医、教等多学科的综合性公立本科大学。学校座落在福建东南沿海黄金海岸、湄洲湾畔的莆田市区。校园占地面积1171亩，建筑面积40万多平方米。馆藏图书85万册，国内外期刊2252种，拥有万方及中国期刊等网络数据库，教学仪器设备总值7000万多元，拥有现代教育技术中心（包括计算中心、电教中心、网络中心）、9个实训场室、21间计算机机房、95间多媒体电教室、8间交互式语音室和省级电子信息实验教学示范中心、医学基础',1468543);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建华南女子职业学院',' ','福建',0,6,0,0,0,' ','暂空',' ','http://www.hnwomen.com.cn','福建华南女子职业学院位于自然环境优美、人文环境厚重的福州仓山学区。学院前身为创办于1908年的私立华南女子文理学院，1984年10月，旅居海内外的老华南校友在余宝笙博士的带领下以华南女子文理学院暨附中校友会的名义复办了福建华南女子职业学院，是全国第一所具有国家承认学历教育招生资格的全日制民办女子普通高等学校，学制三年，面向福建省招生。学院设有实用英语、服装设计与工程、鞋类设计与工艺、儿童教育、营养食品检验与管理、公共事业管理、涉外旅游管理、外贸英语、国际经济与贸易和家政专业。学院师资力量雄厚，专',56703);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州职业技术学院',' ','福建',0,12,0,0,0,' ','暂空',' ','http://www.fvti.cn/','福州职业技术学院位于美丽的东南沿海开放城市——福建省省会福州市，是福州市人民政府举办的全日制普通高等学校，坐落在福州大学城，占地443亩（尚有预留地600亩），依山傍水，荔树成荫，环境优美，首期投入3亿多元，建筑总面积12.97万平方米。2006年12月以优秀等级通过教育部高职高专院校人才培养工作水平评估。2008年7月被确定为福建省首批示范性高职院校。现为中国高等职业技术教育研究会副会长单位、中国西部地区教育顾问单位、福建省高教学会副会长单位、福建省高等职业教育研究会会长单位、福建省职业教育先',83255);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西警察学院',' ','江西',1,11,0,0,0,' ','暂空',' ','oldname": "江西警察学院','江西公安专科学校（原江西省公安学校）创办于1951年，是江西省公安厅主管的一所培养公安实用型专门人才的大专学校。1951年江西省公安学校创立，负责全省公安机关股、所、科、局级在职干部的轮训，校址设在南昌市系马桩。1958年，省人民委员会决定将江西省公安学校改建为江西政法学院（江西行政学院一部划归该院）为省属普通高校，副省长王卓超兼任院长和党委书记，校址迁到至南昌市文教路。学院设立马列、公安和政法3个教研室，开设政法专业，学制两年。当年招收普通大专生两个班146人。同时，举办一年制非学历教育，轮训',530828);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('九江职业大学',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jjvu.jx.cn/templet/default/','九江职业大学是经国家教育部批准的公办全日制普通高校，隶属于九江市人民政府。是一所具有百年办学历史、三十年大专办学经验，招收高中起点应用型四年制本科班、三年制大专和初中起点五年制大专的综合性高校，现有在校生15000余人。学校坐落在庐山脚下、长江之滨，镶嵌于“千年古城，山水画图”的魅力九江城区，校园环境优美，地理位置优越，文化底蕴深厚。学校投资十多亿元建设的千亩新校园，建筑面积38万多平方米，四人间学生公寓具有独立卫生间，并安装了空调、宽带等，学生的学习、生活各项设施齐备。新校区正以现代化的建筑、',235454);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新余学院',' ','江西',1,1,0,0,0,' ','暂空',' ','http://www.xyc.edu.cn/','新余学院是一所全日制公办普通本科院校。学院坐落在新型工业城市江西省新余市。学院占地2000亩，校园环境优美，教学和生活设施完善，是读书治学的理想园地。校舍建筑面积45.46万平方米，教学楼、实验楼、图书馆、体育场等建筑典雅大气、充满现代气息;建有122个校内实验(实训)室，装备了设备先进的计算机房、多媒体教室和语音室，教学科研仪器设备总值8012万元;图书馆藏书137.6万册，中外期刊1200余种，电子文献资源丰富，是中国学术期刊文献检索咨询二级站。学院有全日制在校生13000余人，设有新能源科',788174);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东交通学院',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.sdjtu.edu.cn','山东交通学院始建于1956年，2000年由交通部划转山东省，实行中央与地方共建，以地方管理为主的管理体制。2002年经教育部批准升为普通本科高校。2007年交通部与山东省签署文件共建山东交通学院。2009年省政府批准为山东省船舶与海洋工程专业人才教育培训基地，省教育厅批准为山东省首批研究生联合培养基地单位。2011年学校成为“全国高校毕业生就业50强”典型经验高校，国务院学位委员会批准为培养硕士专业学位研究生试点工作单位。2013年获批成为山东省高等教育应用型人才培养特色名校立项建设单位。学校占',3732521);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南工程学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.haue.edu.cn','河南工程学院是2007年3月经国家教育部和河南省人民政府批准设立的一所普通公办本科高校。学院具有五十多年的办学历史。多年来学院依托两大传统支柱产业——煤炭行业和纺织行业，紧紧围绕全省经济社会发展的需要，为地方、行业培养了大量的工程技术和管理人才，并在办学实践中形成了鲜明的办学特色，办学水平和育人质量得到了社会各界的广泛认同。学院地处轩辕故里——郑州新郑市龙湖镇，郑州市区内有桐柏路校区和南阳路校区。校园总占地面积2668亩，建筑面积50多万平方米。各项建筑均符合现代化办学标准，校内滨湖公园、休闲景',3345852);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('焦作大学',' ','河南',0,0,0,0,0,' ','暂空',' ','http://www.jzu.cn','焦作大学（公办，国标代码：11522），座落在中国优秀山水旅游城市—河南省焦作市，环境幽雅，交通便利，是大学生成才深造的理想场所。焦作大学创建于1981年，历经筹备创建（1980年筹建至1986年正式更名为焦作大学）、充实整顿（1987年开始充实整顿，至1992年通过国家教委验收、1993年省编委批准焦作大学为副市级规格）、探索提高（1994年至1999年，实施568规划，提出“当龙头、争示范、出特色、办本科”）及快速发展（2000年至今，开展新校区建设，实现两个校区办学；扩大招生，达到万人规模',329450);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉音乐学院',' ','湖北',1,8,0,0,0,' ','暂空',' ','http://www.whcm.edu.cn/','中国武汉音乐学院是培养高级音乐专门人才的学府，位于武昌解放路255号。学院历史悠久，最早可追溯到1928年成立的武昌艺术专科学校音乐科。70余年的办学历程，培养了八千余名音乐人才，为我国音乐事业的发展作出了卓越的贡献。学院名称历经变化，1985年定名为武汉音乐学院，首任党委书记、院长童忠良教授；现任党委书记杨锋教授、院长赵德义教授。学院以本科教育为主，积极发展研究生教育，是全国首批获硕士学位授予权的单位。学院设附中、附小旨在培养高等音乐人才后备军。为了适应社会音乐教育的需要，学院创设了成人教育学',154781);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南城市学院',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.hncu.net','湖南城市学院简介湖南城市学院是教育部批准成立的第一所以“城市”命名的公办全日制省属普通本科院校。学校位于素有“鱼米之乡”、“竹子之乡”、“芦苇之乡”美誉的湘中古城益阳，文化底蕴丰厚，自然环境优美。学校的办学历史可以追溯到创建于1970年春的益阳地区师范专科学校和创建于1978年10月的益阳基础大学。经过40多年的建设发展，2002年3月，经国家教育部批准，益阳师范高等专科学校、湖南城建高等专科学校合并升格为湖南城市学院。多年来，学校的建设与发展得到了各级领导的重视与关怀。周强、李肇星、蒋建国、杜',1510316);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南工学院',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.hnpu.edu.cn','湖南工学院创建于1975年，座落于中南重镇雁城衡阳，是经教育部批准，由湖南建材高等专科学校和湖南大学衡阳分校合并成立的一所公办全日制普通本科高等学校，教育部卓越工程师培养计划高校。学校立足湖南，面向全国，是一所以工为主，工、经、管、文、理多学科协调发展，具有较强的科技服务能力，培养“重基础、重技术、重能力、高素质”应用型人才的本科院校。学校现有三个校区，占地1400余亩，校舍建筑面积40万平方米。全日制在校学生19000余人。在职教职工1000余人，专任教师中教授70人，副教授300余人，博、硕',1792000);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南财政经济学院',' ','湖南',1,6,0,0,0,' ','暂空',' ','oldname": "湖南财政经济学院','湖南财政经济学院地处湖南省长沙市风景秀丽的湘江之滨、岳麓山下，是一所由湖南省人民政府主办、湖南省教育厅主管、湖南省财政厅协管的全日制本科院校。学院前身是1933年创办的“厚生会计讲习所”，创办者是熊崇煦、李肖聃、王季范、陈家瓒等我国近代著名的教育家和经济学家。他们早年求学于日本，归国后致力于传播经世济用理念和西方先进的管理方法。学校创办后，便秉承“厚德载物，惠及民生”的湖湘学派经世济用理念，成为中国近代会计技术传播和会计人才培养的摇篮之一。1942年，厚生会计讲习所改名为湖南私立厚生会计补习学校',1375267);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南警察学院',' ','湖南',1,11,0,0,0,' ','暂空',' ','http://www.hnpolice.com','湖南警察学院（原湖南公安高等专科学校）位于历史文化名城——湖南省长沙市，是经国家教育部批准创建的湖南唯一一所培养公安专门人才的全日制普通高等专科学校。学校自1949年建校以来，历经半个多世纪的艰苦创业，形成了公安类特殊专业学历教育与非公安类普通专业学历教育并存、学历教育与在职民警培训并举的办学模式。学校地处高速发展的长沙经济技术开发区，占地面积788.45亩，建筑面积21万多平方米，环境优美，为省级园林式单位。学校教学设施完备，拥有现代教育技术中心、射击馆、模拟街区、电子阅览室等现代化教学设施和',454353);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南工业大学',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.hut.edu.cn','湖南工业大学是一所具有50多年办学历史的多科性大学，是\"服务国家特殊需求博士人才培养项目\"高校。2006年2月，经教育部批准，学校由株洲工学院更名为湖南工业大学，同年合并了株洲师范高等专科学校和湖南冶金职业技术学院。学校以包装教育为特色，是我国第一个被国际包装协会(IAPRI)接纳的会员单位，是中国包装联合会包装教育委员会的主任单位，也是全国高校中惟一的中国包装联合会副会长单位和中国包装技术培训中心。1995年，学校作为首批试点的院校，率先通过原国家教委本科教学工作合格评估。2003年，学校被国',4118936);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南女子学院',' ','湖南',1,5,0,0,0,' ','暂空',' ','http://www.hnnd.com.cn','湖南女子学院的前身是成立于1985年的湖南女子职业大学(由时任中共中央总书记胡耀邦题写校名)，2010年3月18日经教育部批准升格为全日制普通本科院校。学校是新中国成立后第一所公办全日制女子普通高校，是全国妇联与湖南省人民政府共建的一所女子学院，是世界女子教育联盟成员。学校现有教职员工524人，其中教授、副教授等高级专业技术职务人员229人，博士、硕士315人。拥有新世纪百千万人才工程国家级人选、享受国务院政府特殊津贴专家、国家级教学名师、湖南省新世纪121人才工程人选等高层次专家。学校现有在校',561057);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东金融学院',' ','广东',1,6,0,0,0,' ','暂空',' ','http://www.gduf.edu.cn/','学院现有普通全日制在校学生11958人。教职工925人，专任教师690人，正高职称55人，副高职称167人，具有硕士学历303人，博士48人。同时，还聘有一批校外的学者和金融界的专家担任兼职（客座）教授。学院分设广州校本部和肇庆校区，占地面积998亩，建筑面积约29万平方米，环境优美，教学和生活设施齐全；建有计算机网络中心、语音室、多媒体教室、电子阅览室等；图书馆拥有纸质图书121万册、电子图书15万种、中外文期刊2167种；还建有模拟银行、金融、国际贸易、电子商务、财务会计、保险、工商管理和商',3410795);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('电子科技大学中山学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://zsb.zsc.edu.cn/','电子科技大学中山学院前身为1986年成立的中山大学孙文学院，1995年学院独立建制，更名为中山学院，实行省市共管，以市为主的管理体制。2002年，学校更名为电子科技大学中山学院，由中山市人民政府与电子科技大学合作共建，实行理事会领导下的院长负责制，由中山市人民政府市长和电子科技大学校长担任理事长。学校主办方之一的电子科技大学是教育部直属的“985”、“211”重点高校。电子科技大学中山学院是伟人故里——中山市的最高学府，它坐落于环境优美的中山市区莲峰山麓，校内华盖擎天，蓊郁葱翠，是中山市“园林式',572203);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西科技师范学院',' ','广西',1,0,0,0,0,' ','暂空',' ','http://www.gxlztc.net/','广西科技师范学院(简称“广西科师”)的前身为创办于1958年的宜山师范专科学校，1959年与柳州市师范专科学校合并后改名为柳州师范专科学校，1960年从宜山搬迁到柳州办学。1962年学校在全国高校调整中停办。1970年在柳州民族农业技术学校基础上组建柳州地区师范学校，1982年在校内设柳州地区教师进修学院。1984年升格为柳州地区师范专科学校。1994年更名为柳州师范高等专科学校。2005年柳州财经学校并入。2012年开始在来宾办学，2015年升格为广西科技师范学院。学校现有占地面积共1617.',125469);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西财经学院',' ','广西',1,6,0,0,0,' ','暂空',' ','http://www.gxufe.cn/','广西财经学院位于广西首府南宁市。2004年5月，经国家教育部批准，由广西财政高等专科学校和广西商业高等专科学校合并组建，是一所以经济管理类学科为主，文学、法学、理学、工学相互支撑、协调发展的地方性、教学型全日制普通本科院校和广西壮族自治区人民政府重点支持建设的十所高校之一。学校坚持”立足广西，面向基层，服务社会，辐射东盟”的办学定位，以本科教育为主，积极发展研究生教育，适度发展优质专科教育和留学生教育，培养知识结构合理，专业结构合理，专业基础扎实，具有创新精神和较强能力的高素质应用型专门人才。学',2705753);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆科技学院',' ','重庆',1,1,0,0,0,' ','暂空',' ','http://www.cqust.edu.cn','重庆科技学院是一所全日制公办普通本科院校，由两所具有65年办学历史，办学声誉良好的原中央部委属学校——重庆工业高等专科学校和重庆石油高等专科学校(全国示范性高工专重点建设学校)合并组建，于2004年5月经国家教育部正式批准设立。学校以工为主，以石油与化工、冶金与材料、机械与电子、安全与环保为特色，涵盖理、工、经、管、法、文、艺，是一所多学科协调发展，行业优势突出，办学特色鲜明，蕴藏着勃勃生机与较大发展潜力的高等学校。学校2011年9月成为全国“卓越工程师教育培养计划”试点单位，同年10月成为“服',3288558);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都纺织高等专科学校',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.cdtc.edu.cn/','公办全日制普通高等学校国家骨干高等职业院校四川省首批高端技术技能型本科招生院校四川省普通高等学校毕业生就业工作先进单位招生代码：5141学校概况：学校创立于1939年，前身为国立中央技艺专科学校。成都纺织高等专科学校是四川省政府举办的全日制普通高等学校，直属四川省教育厅，是中国西南地区唯一独立建制的纺织类高等学校，四川省首批示范性高等职业院校、国家首批骨干高等职业院校、中国纺织服装高技能人才培训基地、四川省普通高等学校毕业生就业工作先进单位。2013年学校作为四川省首批高端技术技能型本科招生院校',578985);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('文山学院',' ','云南',1,0,0,0,0,' ','暂空',' ','http://www.wstc.net/','文山学院是2009年经国家教育部批准成立的一所全日制普通本科院校，坐落于云南省文山壮族苗族自治州首府所在地文山城中心。文山东临广西，西通昆明，南接越南社会主义共和国，北与曲靖毗邻，是原全国人大常委会副委员长楚图南先生的故乡，素有中国“三七之乡”的美誉。文山气候宜人，风景秀丽，其中尤以国家AAAA级景区----丘北普者黑的万亩荷塘和广南坝美的“世外桃源”田园风光为最。州内居住着汉、壮、苗、彝、瑶、回、傣等11个民族，民族风情浓郁，民族文化丰富。文山学院前临盘龙河，后依西华山，校园内风光别致，树木葱',408649);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明冶金高等专科学校',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.kmyz.edu.cn/','“国家示范性高等职业院校”“冶金高技能人才的摇篮”是昆明冶专一张精致的名片！学校是教育部批准的全日制公办普通高校，始建于1952年，已为国家培养输送了五万多名技术管理人才，其中三万多名成为冶金矿业行业的重要骨干。学校现已发展成为首批全国评估优秀高职院校、国家重点建设示范高职院校、首批全国实施职业资格证书国家级试点院校、云南省高职教育排头兵院校、云南省高等教育改革试点学校，已经办成云南省高技能人才培养基地、云南省外派劳务冶金矿业专业基地、中德职教师资培训基地、全国高职高专创新教育重点基地、中国创造',698396);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南医学高等专科学校',' ','云南',0,3,0,0,0,' ','暂空',' ','oldname": "云南医学高等专科学校','经云南省人民政府以及教育部批准，云南医学高等专科学校于2003年5月正式成立。云南医专前身为始建于1936年的云南省卫生学校。目前，学校坐落于四季如春的昆明，占地面积共633亩。省乡村医生函授总校、省远程医学中心、省全科医生转型培训中心也建在该校。学校在岗教职工近236名，副教授及以上高级职称66名。此外，还聘请各类高级专家长期在我校任教。学校与加拿大莫哈克学院、加拿多学院及联邦学院建立了不同层次的交流关系。学校具有能满足教学需要的实验室和先进的远程教学系统。临床实习医院60余家。学校成立后将开',206960);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安财经学院',' ','陕西',1,6,0,0,0,' ','暂空',' ','http://zhaosheng.xaufe.edu.cn/','西安财经学院是中央与地方共建，由陕西省主管，以经济和管理类学科为主，经、管、文、法、理、工相互支撑、协调发展的多学科普通高等学校。学校位于古都西安，与驰名中外的盛唐人文名胜大雁塔和壮丽典雅的陕西历史博物馆毗邻，地理位置优越，环境优雅，历史积淀丰厚。学院占地面积1579亩，校舍建筑面积52.45万平方米，藏书151万册，中外文期刊1634种，教学仪器设备总值6629万元。学校设有经济、管理、会计、统计、信息、文法、政治与行政7个二级学院和研究生、公共外语、体育3个教学部。拥有统计学、企业管理学、财',4507167);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃民族师范学院',' ','甘肃',1,4,0,0,0,' ','暂空',' ','http://www.gnun.edu.cn/','甘肃民族师范学院是1985年经原国家教委批准成立的我省唯一一所民族师范高等院校。主要面向甘肃民族地区及周边川、青、藏、滇等民族地区招生、服务。时任中央总书记的胡耀邦同志为学校题写了校名。建校23年来，全校广大教职工高举改革与发展旗帜，发扬“缺氧不缺精神，艰苦不缺志气”的优良传统，打造“大爱无华、昂扬向上、追求和合、自强不息”的阳光品质，构建“各美其美、美人之美、美美与共”的和谐校园，艰苦奋斗，深化改革，锐意创新，走过了及不平凡的发展历程，现已建成以教师教育为主体，民族学科为特色，覆盖人文学科、社',380184);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('乌鲁木齐职业大学',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.uvu.edu.cn','“国家示范性高等职业院校建设计划”骨干高职院校新疆乌鲁木齐职业大学位于乌鲁木齐市幸福路723号，是一所综合性全日制普通高等职业院校，是国家示范性骨干高等职业院校和自治区示范性高等职业建设单位。学校占地2351亩（其中2080亩新校区一期已建成），总资产达2.65亿元，教学实验仪器设备总值6700余万元，建有108个校内实验（实训）室128个校外实训室，图书馆藏书60多万册。学校现有在编教职工725人，在校生达1.4万人，有16个教学单位64个专业，高级职称教师148人，专任教师中双师素质教师比例',167510);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('连云港师范高等专科学校',' ','江苏',0,4,0,0,0,' ','暂空',' ','http://www.lygsf.cn','连云港师范高等专科学校是2000年3月经教育部批准成立的一所全日制普通高等学校，由原连云港教育学院、海州师范学校与连云港师范学校合并组建而成（2002年连云港市经济管理干部学校并入）。2011年与江苏师范大学（原徐州师范大学）合作建办江苏师范大学连云港校区。自1914年我校前身“省立第八师范”成立始，我校已有近百年办学历史，培养了4万多名致力于苏北乃至全国基础教育事业的人民教师，涌现出一大批教育专家、教学名师和骨干教师，如著名心理学家朱智贤教授，著名教育家刘百川教授，中国工程院院士、河口海岸专家',300036);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北经济学院',' ','湖北',1,6,0,0,0,' ','暂空',' ','http://www.hbue.edu.cn/','学校概况：湖北经济学院是2002年9月经教育部批准组建的一所全日制省属公办普通本科院校。学校办学历史最早可以追溯到1907年张之洞创办的“湖北商业中学堂”。学校位于历史文化名城武汉市，坐落在风景秀丽的汤逊湖畔，占地面积1740亩，校舍建筑面积62万平方米。美丽的校园带碧水，襟远山，风清气爽，林木葱茏，环境怡人，是读书治学的理想场所。办学规模：全日制研究生、本专科在校学生1.6万人。办学思想：秉持“以学生为中心、立德树人”的办学理念，努力打造一流本科教育，积极发展研究生教育，着力培养有思想、有能力',3141982);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('钦州学院',' ','广西',1,0,0,0,0,' ','暂空',' ','http://www.qzhu.edu.cn','学校地处北部湾顶端的钦州市，是广西沿海唯一一所公立本科院校，是广西唯一一所获得国家高级海船员培养培训资质和国家一级渔业船员培训资质的高校。2011年，广西区政府同意在钦州学院基础上筹建北部湾大学，2013年成为教育部首批应用型大学联盟理事高校，2014年成为广西高校整体转型发展试点单位。2016年成为教育部产教融合创新实验项目基地高校。学校滨海校区占地2070亩。设有海洋学院、海运学院、机械与船舶海洋工程学院、经济管理学院、陶瓷与设计学院、人文学院、国际教育学院、工程训练中心等18个教学单位。面',896988);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西水利电力职业技术学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.gxsdxy.cn','广西水利电力职业技术学院坐落于中国绿城、全国文明城市、广西壮族自治区首府南宁市，是一所以水利、电力、机电、建筑、计算机与信息等工科类专业为主，经济、管理等人文类专业有机结合的创新型高职学院。学院始建于1956年，原名南宁水利学校。1970年，更名为广西水电学校。2002年，升格为广西水利电力职业技术学院。学院是国家骨干高职院校、全国水利高等职业教育示范院校、广西示范性高职院校、广西职业教育攻坚示范性高职院校。学院现有教职工619人，专任教师398人，其中高级职称121人，占教师比例30.4%，拥',173294);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国青年政治学院',' ','北京',1,11,0,0,0,' ','暂空',' ','http://www.cyu.edu.cn/','中国青年政治学院是在1948年创建的中央团校基础上于1985年12月成立的，是共青团中央直属的唯一一所普通高等学校，由教育部和共青团中央共建。中国青年政治学院成立后，与中央团校两块牌子，一套机构，承担普通高等教育和共青团干部培训的双重职能。历任校长均由共青团中央书记处第一书记兼任，冯文彬、胡耀邦、韩英、王兆国、胡锦涛、宋德福、李克强、周强、胡春华、陆昊等同志先后兼任校长，现任校长为秦宜智同志。党委书记为倪邦文研究员，常务副校长(法人代表)为王新清教授。中国青年政治学院成立以来，坚持“质量立校、特',1807253);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京青年政治学院',' ','北京',0,5,0,0,0,' ','暂空',' ','http://www.bjypc.edu.cn','北京青年政治学院是北京市属普通高等学校，位处北京望京地区，毗邻中国社会科学院研究生院、中央美术学院。1986年9月正式成立。建院之初，改革开放的总设计师邓小平同志亲笔为我院题写了院名。党和国家领导人、北京市委市政府领导等先后多次来我院视察、调研，现场办公，指导工作。北京市委办公厅专门发文建议成立北京青年政治学院院务指导委员会，原北京市委副书记金鉴、汪家镠、李志坚同志先后担任了北京青年政治学院院务指导委员会一、二、三届主任。学院注重抓基础建设、重点建设和全面建设，取得了长足的进步。办学条件日臻完善',374118);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北华航天工业学院',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.jiaoyu.nciae.edu.cn/index0.asp','北华航天工业学院是由国家国防科技工业局、中国航天科技集团公司、中国航天科工集团公司与河北省人民政府共建的一所公办普通本科院校，是服务国家（航天）特殊需求人才硕士专业学位研究生教育试点单位。学校坐落在河北省廊坊市市区，始建于1978年，先后隶属于第八机械工业总局、第七机械工业部、航天工业部、航空航天工业部、航天工业总公司。1999年划转到河北省，实行“中央与地方共建、以地方管理为主”的管理体制。著名运载火箭与卫星技术专家、国家最高科学技术奖获得者孙家栋院士为学校名誉校长。学校占地面积753亩，分东',2221876);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西省财政税务专科学校',' ','山西',0,6,0,0,0,' ','暂空',' ','http://www.sxftc.edu.cn:8089/schoolww/','山西省财政税务专科学校坐落在山西省省会太原市，是一所培养高等财经应用型人才的全日制高等专科学校，是首批国家示范性高等职业院校。学校前身是1963年成立的山西会计学校，1985年在其基础上建立了山西省财政税务专科学校，隶属于山西省财政厅。2002年经山西省人民政府批准，学校与太原理工大学联合举办太原理工大学财经学院。学校秉承“至诚至信，至善至美”的校训，坚持“立足专科，控制规模，强化实践，突出特色，打造精品，创建名校”的办学思路，遵循“能力本位，工学结合，校企合作，持续发展”的高等财经类职业教育理',314268);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河套学院',' ','内蒙古',1,0,0,0,0,' ','暂空',' ','http://www.hetaodaxue.com','阴山南麓，黄河弯头，美丽富饶的河套平原孕育了一所立足西部，面向全区，以培养高素质应用型人才为己任的多科性本科院校——河套学院。学院设有12个教学系部，全日制本专科在校生1万余人。专任教师498人，有教授44人，教学名师19人，教坛新秀13人，优秀教学团队23个。现开设本科专业21个，专科专业31个，已建成自治区级品牌专业13个、精品课程21门、优质精品课程2门。学院坚持走内涵发展之路，深化教育教学改革，提升人才培养质量，积极探索应用型人才培养模式，根据行业发展需要，每年选派教师到国内外重点大学进',162451);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳工程学院',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.sie.edu.cn/','沈阳工程学院坐落在中国历史文化名城、东北工业重镇、辽宁省省会—沈阳市。学院隶属辽宁省人民政府,是一所以工程教育为主体，本科教育、高职教育协调发展的多科性、开放式、应用型的本科院校。学院校园规划用地77万平方米，现有占地面积60余万平方米，规划建筑面积35万平方米，现有建筑面积27万平方米，固定资产总值6.5亿元。学院校园设计理念先进、结构布局时尚、功能设施完善，充分体现了人文精神、环保意识与自然生态三者的和谐统一，成为沈阳科教文化长廊中一道靓丽的新景观。学院设有电气工程系、动力工程系、自动控制工',1555339);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁省交通高等专科学校',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://zsjy.lncc.edu.cn/','辽宁省交通高等专科学校坐落于历史文化名城沈阳，是全国首批28所国家示范性高等职业院校，全国毕业生就业工作50强院校。学校建于1951年,是新中国第一所公路交通类专门学校。在63年办学历史中，三易隶属（东北局、交通部、辽宁省交通厅），三易校址（沈阳皇姑区、抚顺新宾县、沈阳沈北新区），七易校名（东北交通学校、交通部沈阳公路工程学校、辽宁交通学校、辽宁省交通工程学校、辽宁交通学院、辽宁公路专科学校、辽宁省交通高等专科学校），举办过中专、本科、专科、高等职业教育，经历了坎坷的办学历程，走出了一条不平凡的',360128);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海立信会计金融学院',' ','上海',1,6,0,0,0,' ','暂空',' ','http://zhaosheng.lixin.edu.cn/','上海立信会计金融学院是根据国家和上海经济社会发展战略、上海提升财经类高校整体发展实力的规划，为适应上海建设“四个中心”、具有全球影响力的科技创新中心和自由贸易试验区的迫切需要，形成与社会主义现代化国际大都市地位相匹配的财经教育影响力的迫切需要，经上海市委、市政府批准，于2016年3月由上海立信会计学院和上海金融学院合并组建而成的一所会计、金融特色鲜明的公办全日制普通高等学校。上海立信会计学院是由被誉为“中国现代会计之父”的著名教育家潘序伦于1928年创建，2003年升格为本科院校，2011年获得',4215567);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮海工学院',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.hhit.edu.cn ','硕士学位授权立项建设单位、江苏省师资队伍建设先进单位、江苏省科技工作先进高校、全国绿化模范单位。淮海工学院是省属公办普通本科院校，面向全国招生。学校位于沿海对外开放城市—江苏省连云港市，主校区坐落在著名的国家AAAA级名胜风景区花果山西麓。校园气候宜人，风景优美，是理想的学习和生活场所。2010年2月，国务院学位委员会批准淮海工学院为全国新增硕士学位授权立项建设单位。学校目前占地2246亩，固定资产总值12.8亿元，校舍面积54万多平方米。全校拥有各类教学实验仪器设备5万多台套，总值1.7亿元，',1464998);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波大学',' ','浙江',1,0,0,0,0,' ','暂空',' ','http://www.nbu.edu.cn','宁波大学是一所在改革开放中成长起来的新兴地方综合性大学，是浙江省、教育部和宁波市共建高校。1986年由世界船王包玉刚先生捐资创立，邓小平同志题写校名，建校之初，由浙江大学、复旦大学、中国科学技术大学、北京大学、原杭州大学五校对口援建，高起点地开始了办学历程。1996年，原宁波大学、宁波师范学院和浙江水产学院宁波分院三校合并，组建新的宁波大学。1992年列为全国高校招生第一批录取院校，1995年首批通过原国家教委本科教学工作合格评价，2000年被浙江省人民政府列为省重点建设大学，2003年接受教育',3380204);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江传媒学院',' ','浙江',1,5,0,0,0,' ','暂空',' ','http://www.zjicm.edu.cn/','浙江传媒学院是国家广播电影电视总局和浙江省人民政府共建共管的一所培养广播影视及其他传媒人才的高等院校，目前全国培养广播影视及其他传媒专门人才的两个主要基地之一，素有“北有北广，南有浙广”之称。建校以来已经为中央和地方各省、市、自治区电视台、电台，为市、县级的电视台、电台及社会影视制作单位输送了大批专业人才。学院现设有新闻与文化传播学院、影视艺术学院、电子信息学院、动画学院、播音主持艺术学院、管理学院、国际文化传播学院、音乐学院、继续教育学院和社科部、公体部等9个二级学院和2个教学部，开设了广播电',3172527);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南财政税务高等专科学校',' ','河南',0,6,0,0,0,' ','暂空',' ','http://www.hacz.edu.cn','河南财政税务高等专科学校位于河南省会郑州，其前身是始建于1963年的河南省会计学校，1985年6月经河南省人民政府批准，组建成立河南省财政税务专科学校，1992年更名为河南财政税务高等专科学校。学校隶属省财政厅，业务上受省教育厅领导，是我省建校较早的一所财经类普通高等专科学校。学校现设有财政税务系、会计系、对外经济贸易系、金融系、工商管理系、信息工程系、工程经济系、法律系、文化传播系、外语系和社科部、体育部、继续教育学院等13个教学单位，开设有财政、税务、会计等35个专业（含专业方向）。学校现有',311344);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南阳理工学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.nyist.edu.cn','南阳理工学院是一所全日制公办普通本科院校，位于历史文化名城河南南阳。南阳历史悠久、人杰地灵，是中华文明发祥地之一，古丝绸之路源头之一，是楚风汉韵荟萃之所，哺育了张衡、张仲景、诸葛亮、范蠡等历史名人，滋养了冯友兰、彭雪枫、姚雪垠、二月河等现当代英才;生产总值居河南省第三位，是中国优秀旅游城市、国家园林城市、全国科技进步先进市、国家创新型试点城市、南水北调中线工程重要水源地和渠首所在地。学校前身是1987年5月河南省人民政府批准筹建的南阳大学。1993年1月国家教委批准定名为南阳理工学院;原张仲景国',2378372);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东石油化工学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://zs.gdupt.edu.cn/','广东石油化工学院是广东省人民政府与中国石油化工集团公司、中国石油天然气集团公司、中国海洋石油总公司共建的公办普通本科高校，华南地区唯一一所石油化工特色院校，教育部“卓越工程师教育培养计划”试点高校。学校地处中国南方最大的石化生产基地和水果生产基地、美丽的海滨城市，广东省茂名市，占地面积135.41万平方米(约2031亩)(含新校区)。学校1956年开始石化特色办学，秉承“艰苦奋斗、求实献身”传统，培养了大批管理精英、技术骨干及各类应用型人才，遍布全国各地石油化工行业企业和各级政府机关、科研院所、',1998355);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南师范大学',' ','海南',1,4,1,0,0,' ','暂空',' ','http://www.hainnu.edu.cn/','海南师范大学是海南省办学历史悠久、教师教育特色鲜明、综合办学实力较强的教育部、海南省人民政府共建高校，坐落在美丽的滨海旅游城市、国家历史文化名城——海口市。海南师范大学前身为建于清康熙四十四年（1705年）的琼台书院，1949年秋正式创建“国立海南师范学院”。2007年经国家教育部批准更名为海南师范大学。2009年海南省政府将学校列为省重点大学进行建设。2013年经国务院学位委员会批准为博士学位授予权单位。2015年被列为教育部、海南省人民政府共建高校。六十多年来，学校始终坚秉承“崇德尚学，求是',2682799);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆理工大学',' ','重庆',1,1,0,0,0,' ','暂空',' ','http://www.cqut.edu.cn','重庆理工大学前身是创办于1940年的国民政府兵工署第11技工学校(对外化名\"士继公学\")。1965年4月，升格为重庆工业学院，隶属于第五机械工业部。其后学校又与北京理工大学、南京理工大学等七所高校一起隶属于国家兵器部，即兵工七子。从1991年开始隶属于中国兵器工业总公司，为兵工行业、国防建设输入了大量优秀人才。1999年，由中国兵器工业总公司划转重庆市管理，更名为重庆工学院。2001年，重庆市经济管理干部学院整体并入。2009年经教育部批准更名为重庆理工大学。学校现有教职工1589人，其中专任教',5881560);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川民族学院',' ','四川',1,9,0,0,0,' ','暂空',' ','http://www.scun.edu.cn/','甘孜藏区高等教育的历史始于上世纪40年代。1948年设立国立康定师范专科学校，1950年学校停办；1960年，根据省人民委员会批示成立了“甘孜专科学校”，开设师范类专业和预科；1961年，因国家调整，学校停办；1985年设立“康定民族师范专科学校”，同年，时任中共中央总书记胡耀邦为学校题写了汉文校名；1986年，时任全国人大副委员长第十世班禅大师为学校题写了藏文校名。1992年，学校更名为“康定民族师范高等专科学校”。校训是：团结、奋进、重道、精业。学校位于情歌的故乡四川康定。校园面积41266',575194);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黔南民族医学高等专科学校',' ','贵州',0,3,0,0,0,' ','暂空',' ','http://www.qnmc.cn','黔南民族医学高等专科学校是一所具有高等学历教育及招生资格的全日制公办普通高等医学专科学校。学校位于全国优秀旅游城市－－贵州省都匀市。校园依山傍水，环境幽雅，是一个读书的好地方。学校开设临床医学、临床医学（全科医学方向）、护理、助产、药学、医学检验技术、计算机信息管理（医学方向）等7个专科专业及专业方向，学制均为三年。2010年在省、内外招收参加全国普通高等学校统一招生考试的考生。少数民族班（三年制）只面向贵州省招收少数民族考生。学校教学设施齐全，有附属医院2所，省内、外教学、实习基地80余所。学',136516);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武警工程大学',' ','陕西',1,10,0,0,0,' ','暂空',' ','http://www.wjxy.edu.cn/','中国人民武装警察部队工程大学，原名中国人民武装警察部队工程学院。这里是武警部队培养“讲政治、懂技术、会管理、能指挥，具有创新精神和实践能力，忠于党和人民的现代警官”的基地，进行科学研究的中心。她占地560亩，建筑面积20万平方米，建筑宏伟，绿树成荫，设施先进，环境幽雅，是读书治学的理想园地。学院原名中国人民武装警察部队技术学院，1983年经国务院和中央军委批准成立，后经三次体制任务调整，发展成为培养内卫指挥、政治工作、后勤管理和专业技术警官的综合性军事学院。1998年９月，经国务院和中央军委批准',502457);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林师范高等专科学校',' ','广西',0,4,0,0,0,' ','暂空',' ','http://www.glnc.edu.cn','桂林师范高等专科学校于2000年经教育部批准成立，由区市共管、以市管为主。其前身是创建于1938年的广西省立桂林师范学校，具有光荣的革命传统，被誉为“桂北革命的摇篮”，并以其独特的办学方式和办学成就，成为全国办学成绩突出，对革命贡献较大的四所著名师范之一。学校获得教育部高职高专人才培养工作水平评估“优秀”等级，是“全国学校艺术教育先进单位”“全国首批国家级语言文字规范化示范校”“全国普通话培训测试先进单位”。学校现有信义、甲山2个校区，校园面积430.38亩，建筑面积17.95万平方米。占地89',194004);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东工商学院',' ','山东',1,6,0,0,0,' ','暂空',' ','http://www.ccec.edu.cn/','省级花园式单位和文明校园管理学、经济学分别名列中国大学管理学和经济学100强山东省应用型人才培养特色名校立项建设单位山东省高等学校协同创新中心省级大学生创业孵化示范基地设有国际交流学院并与40余所国外高校建设了友好合作关系山东工商学院，原名中国煤炭经济学院，始建于1985年12月，1998年9月由原煤炭部管理改为中央与山东省共建，以山东省管理为主，2003年2月更为现名。学校坐落在“最佳中国魅力城市”——烟台，依山傍海，风景秀丽，是省级花园式单位和文明校园。学校占地93.2万平方米，校舍建筑面积',4252184);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('呼和浩特民族学院',' ','内蒙古',1,0,0,0,0,' ','暂空',' ','http://www.imnc.edu.cn/','呼和浩特民族学院是由国家教育部批准，内蒙古自治区人民政府主办的普通本科高等学校，主要面向内蒙古和蒙古语文协作八省区、辐射全国招生。她的前身是原内蒙古民族高等专科学校，始建于1953年。学校一直秉承“以人为本，特色立校，弘扬民族精神，传承民族文化”的办学理念，注重民族性、区域性和应用性特色，以“培养民族人才、发展民族文化、服务民族经济”为宗旨，为中央、自治区和蒙古语文协作八省区培养了3万多名少数民族各类人才，在经济社会发展、传承繁荣民族文化、促进民族团结进步、维护边疆稳定、社会和谐等方面做出了积极',192250);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春大学',' ','吉林',1,0,0,0,0,' ','暂空',' ','http://zsb.ccu.edu.cn/','长春大学坐落在吉林省长春市，是一所省属全日制综合性大学。现有三个校区，总占地面积103万平方米。学校前身是1949年由原东北人民政府工业部创办的长春工业会计统计专门学校。后历经多次易名于1987年经原国家教育委员会批准，原吉林科技大学、吉林机电专科学校、长春外国语专科学校、长春职业大学4所学校合并，组建成长春大学。2000年8月，原吉林省林业学校并入长春大学。学校现有20个教学院部，50个本科专业，3个硕士学位授权一级学科，2个硕士专业学位授权点。学校专业涵盖经济学、法学、教育学、文学、理学、工',2488672);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海出版印刷高等专科学校',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.sppc.edu.cn/','历史悠久，部市共建——学校创建于1953年，是新中国建立最早的出版印刷类高等学校，在行业中享有盛誉，是全国唯一的部市共建的高专，60多年来为我国的出版印刷业培养了数万名高层次骨干和行业高级管理人才。2005年被国家新闻出版广电总局确定为“国家印刷出版人才培养基地”；2010年，学校被列为国家100所骨干建设高职院校，同年，学校被确认为国家高等职业教育专业教学资源库建设单位（印刷与数字印刷技术）。上海出版印刷高等专科学校是中国出版印刷人才培养的摇篮，被誉为我国出版印刷业的“黄埔军校”。2015年，',192698);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安航空学院',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xihangzh.com/','西安航空学院是一所以工科为主、多学科协调发展的全日制普通高等学校，位于历史文化名城西安。学校创建于1955年,原名为西安航空工业学校，隶属原航空工业部。1957年合并兰州航空工业学校。1960年升格为专科学校，更名为西安航空工业专科学校，1961年恢复为西安航空工业学校。1985年经原国家教委批准升格为高等专科学校，更名为西安航空技术高等专科学校。1999年划转地方，隶属陕西省人民政府，为中央与地方共建院校。2012年经教育部批准升格为普通本科院校，更名为西安航空学院。学校现有沣惠、阎良两个校区',1477624);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南城建学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.hncj.edu.cn/','河南城建学院是河南省唯一一所以工科为主、以“城建”为特色的多学科协调发展的省属本科高校，也是全国仅有的以“城建”命名的两所本科高校之一。学校始建于1983年，前身是河南城建高等专科学校，2002年3月，经国家教育部批准，升格为本科院校。学校设有土木与交通工程学院、管理学院、市政与环境工程学院、建筑与城市规划学院、能源与建筑环境工程学院、测绘与城市空间信息学院、艺术设计学院、计算机与数据科学学院、电气与控制工程学院、材料与化工学院、生命科学与工程学院、数理学院、外国语学院、法学院、国际教育学院、继',1912469);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西职业技术学院',' ','广西',0,2,0,0,0,' ','暂空',' ','http://www.gxzjy.com','广西职业技术学院前身是创建于1965年的广西劳动大学，先后历经广西农学院热带作物分院(本科)、广西农垦职工大学、广西农工商职业大学等历史阶段。1998年经国家教育部批准，成为广西首批独立改制的公办高等职业院校。学院先后被评为全国职业教育先进单位、教育部高职高专人才培养工作水平评估优秀院校、全国高等教育学籍学历管理工作先进集体、全国高校后勤十年社会化改革先进院校、全国职业院校魅力校园、自治区“卫生优秀学校”、自治区安全文明校园和南宁市生态园林单位、国家示范性骨干高职院校建设优秀单位、全国首批百所高',107404);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('防灾科技学院',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.cidp.edu.cn/','防灾科技学院隶属于中国地震局，是全国仅有的以防灾减灾高等教育为主、学科门类齐全的综合性全日制普通高等学校。学院始建于1975年。学院面向全国招生，现有全日制在校生8000余人。学院坚持“人本治校、特色立校、人才强校、和谐兴校”的办学理念和“优化结构、深化改革、强化特色、提高质量”的人才培养思路，立足防震减灾行业，面向经济社会发展，逐步形成了以全日制本科教育为主体，兼有硕士研究生教育、成人学历教育、行业技术培训等并存的多层次、多类型、多形式的现代办学体系，培养具有强烈社会责任感、较强创新精神和实践',1852423);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('承德石油高等专科学校',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.cdpc.edu.cn/','“国家示范性高等职业院校”承德石油高等专科学校是中央与地方共建，以河北省人民政府管理为主的一所培养高等工程技术应用性人才的普通高等专科学校，是教育部确定的全国“示范性普通高等工程专科重点建设学校”。学校座落在我国著名的历史文化名城河北省承德市。学校前身是1903年创办于天津的“北洋工艺学堂”，是我国兴办最早的工科院校之一。解放前校名为“河北省立工学院附设高级工业职业学校（河北高工）”，新中国成立后，1951年全国高等学校院系调整，并入天津大学，曾名为“天津大学附设石油学校”，1953年成立“天津',346851);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽东学院',' ','辽宁',1,0,0,0,0,' ','暂空',' ','http://www.ldxy.cn/','辽东学院是经教育部批准成立的省属本科院校，位于中国最大边境城市、全国优秀旅游城市——辽宁省丹东市。丹东地处黄海之滨，鸭绿江畔，风景秀丽、气候宜人，四季如画，是学子们求学和生活的理想之地。辽东学院分为南北两个校区，南校区沿江而建，与朝鲜民主主义人民共和国新义州市隔江相望，环境幽雅，景色迤俪；北校区坐落于著名旅游胜地五龙山麓，林木葱茏，空气清新。校园占地面积1547亩，建筑总面积47.6万平方米，固定资产总值4.2亿元。现有在校生1.7万人。学校学科门类齐全、结构合理，现有专业涵盖了经济学、法学、教',1146618);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('仰恩大学',' ','福建',1,6,0,0,0,' ','暂空',' ','oldname": "仰恩大学','仰恩大学于1987年由爱国华侨吴庆星先生及其家族设立的仰恩基金会创建，经国家教委（现教育部）批准为全日制普通高等学校。1988年由福建省政府办学。为着国家教育改革的需要，从1994年7月起，仰恩大学作为中国教育改革的试点，由仰恩基金会独立办学。仰恩大学是全国第一所具有颁发国家本科学历证书和授予学士学位资格的私立大学。仰恩大学位于海上丝绸之路的起点——历史文化名城泉州市北郊。校园依山傍水，风景秀丽，环境幽雅。学校占地面积2000余亩，校舍建筑面积52万平方米，体育场馆面积14余万平方米。学校所有教',1018134);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('九江职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jvtc.jx.cn/','“国家示范性高等职业院校”“全国毕业生就业典型经验高校”——九江职业技术学院是经教育部批准成立的全日制（公办）普通高等学校，其前身九江船舶工业学校创建于1960年，先后隶属于原第一机械工业部、第三机械工业部、第六机械工业部、中国船舶工业总公司。1994年经原国家教委批准举办高等职业教育，是全国首批举办高等职业教育的10所学校之一。1999年学校由国防科工委划转江西省人民政府管理，是唯一一所省教育厅直属高职院校。2003年通过教育部组织的全国高职高专院校人才培养工作水平评估，2004年被财政部确定',549180);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('濮阳职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.pyvtc.cn/','濮阳职业技术学院是经河南省人民政府批准成立，国家教育部备案，具有普通高等学历教育招生资格的全日制公办高等专科学校。学院坐落在集国际花园城、国家卫生城、国家园林城、国家文明城、国家优秀旅游城、国家历史文化名城等多项桂冠于一身的国家名城——濮阳市。学院邻近京九铁路、濮鹤高速公路、大广高速公路和106国道，交通便利；濮上园、绿色庄园、世锦园三大4A级生态名园环抱校园，环境优美。学院占地面积1162亩，总建筑面积22万平方米。校园大道宽阔平坦，道路两旁华灯高矗，林荫夹道，花木葱茏，草坪堆翠，人与自然和谐',143684);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南警察学院',' ','河南',1,11,0,0,0,' ','暂空',' ','http://xsc.hnp.edu.cn/','河南警察学院是一所本科层次的普通高等学校。学院前身是1949年2月成立的中共豫西区党委社会部保卫干部训练班，历经河南省公安干部学校、河南省人民警察学校、河南公安高等专科学校等发展时期。2010年3月，经教育部、省政府批准设立河南警察学院。2012年8月，经省编办同意，开封警校、洛阳警校正式并入河南警察学院。学院是我省唯一的公安院校，是全省公安机关的重要组成部分，承担着学历教育和在职民警培训双重职能，实行参照公务员法管理。学院现有三个校区，分别位于郑州、开封和洛阳，占地总面积1800余亩。现有教职',848258);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北财经高等专科学校',' ','湖北',0,6,0,0,0,' ','暂空',' ','oldname": "湖北财经高等专科学校','湖北财经高等专科学校是湖北省属培养财经、管理类高级应用型人才的全日制普通高校。学校座落在著名的武汉东湖风景区，与梨园广场相邻。湖北财经高等专科学校，前身为中央税务干校中南分校，50年沧桑巨变，学校已发展成为固定资产过亿元的现代化普通财经高校。占地200余亩，校舍面积13万余平米。学校现有专兼职教师185人，其中正副教授66人，讲师87人，“双语型”教师18人，博士学位教师3人。学校现有普通全日制在校学生近4500人。学校现设有财政税务、国际经济与贸易、会计、审计、经济管理和经济信息等6个系，开设',191047);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉城市职业学院',' ','湖北',0,4,0,0,0,' ','暂空',' ','http://www.whcvc.edu.cn/','schoolcode": "427000300',108447);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆工商大学',' ','重庆',1,6,0,0,0,' ','暂空',' ','http://www.ctbu.edu.cn/','重庆工商大学是一所经济学、管理学、文学、工学、法学、理学等学科协调发展的、具有鲜明财经特色的多科性大学。学校由中央和地方共建，以重庆市政府管理为主，被国家确定为\"中西部高校基础能力提升计划\"重点支持高校。近年来，学校党委和行政审时度势，紧紧抓住高等教育体制改革的重大历史机遇，积极更新教育观念，凝练形成“厚德博学、求是创新”的校训，牢固树立“质量立校、人才强校、特色兴校”的办学理念，坚定不移地走规模扩张向内涵发展的转型之路，经过全校师生员工的艰苦奋斗和顽强拼博，学校管理日臻完善，特色逐步彰显，竞争',6613071);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江工程学院',' ','黑龙江',1,1,0,0,0,' ','暂空',' ','http://www.hljit.edu.cn','黑龙江工程学院坐落在风光秀丽的北国名城哈尔滨，有东校区、西校区和松北校区3个校区，总占地面积近80万平方米，是一所以工为主，多学科协调发展的全日制普通本科院校。学校秉持“明德求真，知行合一”校训，坚持“应用为本，追求卓越”的办学理念，立足地方，面向全国，为交通、测绘、汽车、机电、建筑等行业，培养应用型高级专门人才。学校现有17个二级教学院（系、部、中心）、成教学院、职业技术学院，拥有逸夫图书馆、工程文化博物馆、教学实验楼、大学生文体活动中心等现代化的教学设施。学校面向全国招生，现有全日制在校生1',1570681);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陇南师范高等专科学校',' ','甘肃',0,4,0,0,0,' ','暂空',' ','http://www.lntc.edu.cn','陇南师范高等专科学校，是国家教育部批准成立的公办全日制普通高等师范专科学校，位于素有“陇上江南”美称的甘肃省成县。学校创办于1937年，原名为“甘肃省成县师范学校”；2001年与甘肃省礼县师范学校合并，更名为“陇南师范学校”；2003年，经教育部批准，改建为陇南师范高等专科学校。在70年的办学历程中，学校为社会培养输送了2万余名中小学教师和其它各类人才。学校占地面积341亩，建筑面积10万多平方米，固定资产1.34亿元，教学仪器设备总值1041万元，馆藏图书36.2万册，中外文期刊700多种。建',167145);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('定西师范高等专科学校',' ','甘肃',0,4,0,0,0,' ','暂空',' ','http://www.dxatc.cn','定西师范高等专科学校位于定西市定临路4号，是教育部批准设置的普通高等专科学校。建校29年，校园面积358亩，后山绿化面积450亩，校舍建筑面积11.68万平方米，固定资产1亿多元，其中教学仪器设备值1430多万元，图书馆藏书28万余册。现有教职工222人，专任教师196人，其中高级职称教师53人，占27.04%。研究生学历教师31人。先后聘请外籍教师8人，兼职教师10人。近三年来教师在地级以上刊物发表学术论文800多篇，编写或参编教材、专著48部，其中一部著作获2002年甘肃省社会科学优秀成果一',127290);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('张掖医学高等专科学校',' ','甘肃',0,3,0,0,0,' ','暂空',' ','oldname": "张掖医学高等专科学校','张掖医学高等专科学校是在始建于1952年的张掖卫校基础上，于2003年4月经教育部批准改建的一所医学高等专科学校。学校位于甘肃省河西走廊中部、黑河之滨、古丝绸之路重镇、素有“金张掖”之称的历史文化名城张掖市，是甘肃西部唯一的一所全日制普通医学高等专科学校。学校改建后，实行省、市共建共管以市为主的管理模式。多年来，在张掖市委、市政府的正确领导和省、市教育、卫生主管部门的直接指导下，学校领导班子抢抓机遇、审时度势，在精心谋划和科学论证的基础上，学校提出了“立足河西、面向全省、服务周边，争取把学校建成',197657);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南医学院',' ','海南',1,3,0,0,0,' ','暂空',' ','http://www.hainmc.edu.cn','海南医学院于1993年经中华人民共和国教育委员会批准成立，隶属海南省人民政府主管，是海南省惟一的一所省属公办普通高等医学院校。学校前身是由留日人士林筱海先生于1947年创建的私立海强医事技术（职业）学校与1948年创建、宋子文先生任第一任董事长的私立海南大学医学院于1951年合并而成的海南医学专门学校及新中国成立后续办的海南医学专科学校、海南大学医学部。学校办学65年来，共向社会输送各类医药卫生技术人才4万余人，其中包括院士1名、省部级干部2名，毕业生遍布全世界10余个国家和港澳台地区；已形成普',1579541);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆维吾尔医学专科学校',' ','新疆',0,3,0,0,0,' ','暂空',' ','http://www.xjumc.cn/','新疆维吾尔医学专科学校是1989年9月国家教委正式批准成立的。学校占地总面积66667平方米，建筑面积达19000平方米。学校用维吾尔语授课，开设2个专业，26门课程，其中13种维医、药专业课程教材是由本校组织著名老专家编写而成，还开设有成人教育科。学校现有教职1248人（含附属医院职工），专职教师和兼职教师90人，其中教授（含主任医师）、副教授（含副主任医师）15人、讲师（含主任医师30人）、助教（含医师）45人。学校拥有教学楼1座，实验楼2座，其中有7个实验室、1个电教室、5个标本室以及配套',40935);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东莞理工学院',' ','广东',1,1,0,0,0,' ','暂空',' ','http://www.dgut.edu.cn','东莞理工学院1990年筹办，1992年4月经国家教委批准正式建院，2002年3月经国家教育部批准变更为本科全日制普通高等院校，诺贝尔物理学奖获得者杨振宁博士任名誉院长。学院是广东东莞的第一所普通本科院校。东莞市经济实力位居“全国综合实力百强城市”第十二位、全国地级市第一位，是“最佳中国魅力城市”、“中国最具经济活力城市”、“国际花园城市”之一。东莞市雄厚的经济实力、良好的发展环境，为学院办学奠定了坚实的基础和广阔的空间。学院现有两个校区，主校区面积1500亩，座落在科技与山水共一色的松山湖科技产',1947389);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('邢台职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.xpc.edu.cn/','邢台职业技术学院(原中国人民解放军军需工业学院)原隶属中国人民解放军总后勤部，2002年7月移交河北省直属管理，是一所省属公办全日制普通高校，面向全国招生，招生批次为本科二批、专科提前批、对口专科批、专科批(含中德合作办学)，是河北省招生规模最大的专科院校。学院始终站在高职教育改革的第一方阵。2009年11月，通过教育部、财政部验收，正式成为全国首批、河北省第一所“国家示范性高等职业院校”。学院毕业生以过硬的业务素质、职业能力受到用人单位欢迎，先后有2000余家用人单位与学院建立了长期合作关系，',588479);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春医学高等专科学校',' ','吉林',0,3,0,0,0,' ','暂空',' ','http://www.cmcedu.com','长春医学高等专科学校座落在吉林省长春市经济技术开发区内，始建于1936年，是经教育部批准的全日制普通高等专科学校。73年的历史积淀出深厚的人文底蕴，在“教以惠生、学以达仁”办学理念的统领下，医专人凝练了校训“医道天德”，将“爱”与“德”的教育融入到人才培养的各个环节。学校2001年与长春职工医科大学、医学情报所合并，2007年与长春市直属机关业余大学合并。通过资源整合，改善办学条件，强化内涵建设，学校各项工作蓬勃发展，2007年，学校在教育部人才培养水平评估中取得了优秀的成绩，被吉林省教育厅确定',431806);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安电力高等专科学校',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xaepi.edu.cn/','西安电力高等专科学校的前身是西安电力学校，创建于1953年。1994年3月升格为西安电力高等专科学校，隶属于国家电网公司。西安电力高等专科学校是西北电网、陕西电网唯一的一所电力类普通高校，具有多层次、多规格、多类型的办学特点。五十多年来，学校坚持“重德育人、精严细实、产学结合、服务电力”的办学理念，为国家电力事业培养了数以万计的高素质技能型专门人才和管理干部，享有“西北电力黄埔”的美誉。1999年4月，西安电力高等专科学校与西北电业职工大学合并，成立西北电力职工培训中心，实现了教育资源共享。经过',421113);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东电力高等专科学校',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sepc.edu.cn','山东电力高等专科学校是经原国家教育委员会和电力工业部批准设立的一所普通高等工程专科学校，隶属山东电力集团公司。前身系始建于1958年的青岛动力专科学校，1994年经原国家教委批准，由山东省电力学校重新升格为山东电力高等专科学校，1997年由泰安迁入济南新建校区。1999年，山东电力集团公司又将山东电专与山东电力研究院合并，组建了新的山东电力高等专科学校，现有教职工627人，全日制在校生4219人。经过几年来的探索和实践，形成了鲜明的院校合一，系所合一，学研产紧密结合、协调发展的办学特色。我校教学',337841);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州电力高等专科学校',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zepc.edu.cn','●全国高等工程专科示范性重点建设学校●培养电力建设人才的摇篮和基地●具有70余年的办学历史郑州电力高等专科学校是一所集电力、动力、经贸、机电、电子信息类专业为一体的普通高等工程专科学校，是培养电力建设、生产和管理人才的摇篮和基地。一、地理位置学校位于河南省郑州市管城回族区商城路东段，与著名商代遗址和郑州古城隍庙毗邻，环境优美，交通便利，占地面积十余万平方米，分为教学区、学生宿舍区、及三个职工家属区，教学及生活设施完善，校园学术文化氛围浓郁，是青年学子求学成才的理想学府。二、历史沿革学校的前身是创',374604);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首钢工学院',' ','北京',1,1,0,0,0,' ','暂空',' ','http://www.sgit.edu.cn/','首钢工学院始建于1978年，其前身是北京钢铁学院分院，是国家教育部批准成立的一所工科高等院校。1994年经教育部批准更名为首钢工学院。学院以冶金、机电、建筑等工科专业为主，行业优势突出，办学特色鲜明，多学科多层次协调发展。1999年以前，学院以培养本科生为主，主要面向首钢分配。1999年以后，随着我国高等职业教育的大力发展，学院确立了以培养有鲜明职业特色、适应我国产业发展和产业经济结构调整的高素质应用型人才为目标的办学方针，开始了高等职业教育的创新和实践。学院位于京西五环路晋元桥西北侧，毗邻首钢',295212);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北经贸大学',' ','河北',1,6,0,0,0,' ','暂空',' ','http://www.heuet.edu.cn/','河北经贸大学是河北省重点建设的骨干大学，是一所以经济学、管理学、法学为主，兼有文学、理学、工学和艺术学的多学科财经类大学。学校由河北财经学院、河北经贸学院、河北商业高等专科学校合并组建而成，至今已有60余年的办学历史。学校位于滹沱河畔，地处石家庄市西部生态区滨水景观带，占地2950余亩，绿化面积达40%以上，被誉为“花园式”校园。学校分北、南、西三个校区办学，校本部设在北校区，主要举办本科、研究生和留学生教育，南、西校区为独立二级学院——经济管理学院。学校实行二级管理，现有15个本科学院、5个教',3631393);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海杉达学院',' ','上海',1,6,0,0,0,' ','暂空',' ','http://www.sandau.edu.cn/','上海杉达学院是在上海浦东开发开放的背景下，于1992年6月由上海交通大学、北京大学、清华大学部分教授发起创办的全日制民办大学。1992年8月，经上海市高等教育局批准筹办，校名为“杉达大学”，并于当年秋季面向全国招生；1994年2月，经国家教育委员会批准正式建校，校名为“杉达学院”；2002年3月，经国家教育部批准设置为全日制普通本科院校，校名为“上海杉达学院”；2005年8月，经上海市学位委员会批准，增列为学士学位授予单位。2012年4月，学校被上海市教委列为上海民办高校示范校创建校。2013年',1313271);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黄河科技学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.hhstu.edu.cn/','北依滔滔黄河，南望巍巍嵩山，岿然坐落于中原历史文化名城——河南省郑州市。雄浑中原，弦歌不绝。黄河科技学院在这里诞生，发展、壮大。黄河科技学院创办于1984年，本部位于河南省郑州市，是经教育部批准成立的一所民办普通本科高校。1994年第一个经国家教委批准实施专科学历教育，2000年第一个经教育部批准实施本科学历教育;2004年取得学士学位授予权;2008年通过教育部普通高等学校本科教学工作水平评估;2013年被教育部批准为全国首批“应用科技大学改革试点战略研究单位”，并成为中国应用技术大学联盟的首',1950538);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海政法学院',' ','上海',1,11,0,0,0,' ','暂空',' ','http://zs.shupl.edu.cn/','上海政法学院始建于1984年。前身是上海市政法管理干部学院、上海法律高等专科学校和上海大学法学院。2004年9月，上海市政府批准上海政法学院为独立设置的普通本科院校。学校坚持“立足政法、服务上海、面向全国”，依托政法系统的行业优势，走“以需育特、以特促强”的创新发展道路，秉承“一切为了学生”的办学理念，充分发展学生个性，拓宽学术创新空间，提供自由选择学习机会，努力培养基础扎实、具有社会责任感、具备创新精神和实践能力的高素质应用型、复合型人才。学校坚持科学发展，“贴近实际，注重应用”的办学特色鲜明',2321881);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林旅游学院',' ','广西',1,6,0,0,0,' ','暂空',' ','http://www.glit.cn/','桂林旅游高等专科学校创办于1985年，始称桂林旅游专科学校。1994年正式定名为桂林旅游高等专科学校。学校坐落在山水甲天下的国际旅游名城桂林，拥有骖鸾、雁山两个校区，占地1885亩。学校设有旅游与休闲管理系、酒店管理系、旅游外语系、导游系、商务系、视觉艺术系、艺术表演系、基础部、社科部、公共外语部等7系3部，29个专业，另设有维伯国际教育合作部，面向全国招生。现有在校生近8700多人。校舍建筑面积21万多㎡，教学仪器设备总值3000多万元，图书藏量111.6万余册（纸质图书69.9万余册）。二十',157543);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贺州学院',' ','广西',1,0,0,0,0,' ','暂空',' ','http://www.hzu.gx.cn/','贺州学院是广西一所全日制公办普通本科院校。校址位于湘、桂、粤三省（区）交界的美丽的贺州市。学院以培养基础教育师资和地方经济社会发展需要的应用型人才为主要目标，坚持立足贺州、服务周边、面向全国、奉献社会的办学宗旨，全面推进学院的改革发展。［历史沿革］学院前身是梧州师范高等专科学校,其办学历史可以追溯到创建于1943年的广西省立平乐师范学校，后更名或改建为贺县师范学校、八步师范学校、梧州地区教师进修学院、梧州地区教育学院、梧州师范高等专科学校等，2006年2月经国家教育部批准正式建立贺州学院。65年',570563);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安医学院',' ','陕西',1,3,0,0,0,' ','暂空',' ','http://www.xiyi.edu.cn','西安医学院是陕西省人民政府举办的一所全日制普通本科院校，创建于1951年，其前身是陕西省卫生学校。1959年省政府设陕西省卫生干部学院建制。1994年国家教委批准成立陕西医学高等专科学校。2006年2月经教育部批准，升格为本科院校，更名为西安医学院。学校由含光校区、未央校区、高新校区组成，总占地面积76万㎡，总建筑面积101.36万㎡，固定资产总值11.06亿元，其中教学科研仪器设备总值1.34亿元。图书馆纸质藏书116.73万册，电子图书410.12万册。学校建成了较为完善的实践（实习）教学基',2915670);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('民办四川天一学院',' ','四川',0,6,0,0,0,' ','暂空',' ','http://www.tianyi.edu.cn','四川天一学院创办于1991年。1994年经原国家教委批准，成为我国首批、西南地区最早一所国家承认学历资格的全日制民办普通高校。2000年，经四川省教育厅批准，与成都理工大学、四川师范大学合作办学，优秀学生实施跨校本科教育。2000年经四川省教育厅批准，独立举办成人学历教育。2002年经中华人民共和国驻澳大利亚使馆推荐，教育部批准，与澳大利亚HOLMESGLEN学院合作举办中澳高等学历教育和出国留学教育，面向全国各省市招生。学院具有鲜明的办学特色，倡导创新的办学思维，推行全新的办学模式，在高效的运',168001);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江树人学院',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.zjsru.edu.cn/','浙江树人大学创办于1984年，是改革开放以来我国最早成立的全日制民办本科高校之一。经过近30年的发展，学校已建成为一所占地面积近500亩，建筑面积32万平方米，藏书量200余万册，在校生16000余名，涵盖经济、管理、文学、工学、法学等学科的普通高校。学校建有管理学院等9个二级学院，设有土木工程等34个本科专业;其中，国际经济与贸易专业为国家特色专业，环境工程、工商管理、计算机科学与技术、国际经济与贸易专业为浙江省重点专业。此外，学校还成立了中国民办高等教育研究院、中国服务经济研究中心、陶瓷研究',362723);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('九江学院',' ','江西',1,1,0,0,0,' ','暂空',' ','http://www.jju.edu.cn/','九江学院是经国家教育部批准设立的全日制综合性本科国有公办普通高等院校,面向全国30个省（市、自治区）招生。办学历史可上溯至1901年由美国基督教卫理公会创办的但福德学校，现办学体制为军地共建、省市共建。学校坐落在长江中下游的历史文化名城——江西省九江市，当长江要津，抱庐山奇秀，滨浩淼鄱湖。名山、名江、名湖、名城，造就了九江学院的灵气，尤其是具有千年历史、享有“天下书院之首”美誉的白鹿洞书院，更为九江学院赋予深厚的文化气息和办学氛围。学校占地2968亩，现有主校区、浔东校区、庐峰校区、南湖校区四个',2647398);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东工业大学',' ','广东',1,1,0,0,0,' ','暂空',' ','http://www.gdut.edu.cn/','广东工业大学由原广东工学院、广东机械学院和华南建设学院（东院）于1995年6月合并组建而成，学校已有50余年的办学历史，是一所以工为主、工理经管文法艺结合的、多科性协调发展的省属重点大学。学校坐落在中国南方名城广州，地理位置优越，校园占地总面积3348亩，拥有大学城校园及东风路、龙洞等多个校区。大学城校园突出工科特色，多个学科相互促进协调发展，创设多个协同创新科研平台。东风路校区突出艺术创意和社工服务氛围的营造，建设设计创意园和成果展示馆。龙洞校区突出管理学与理学氛围的营造，打造环境优美、恬静怡',5068935);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东外语外贸大学',' ','广东',1,5,0,0,0,' ','暂空',' ','http://www.gdufs.edu.cn','广东外语外贸大学是1995年6月由原广州外国语学院和原广州对外贸易学院合并组建的广东省涉外型重点大学。原广州外国语学院成立于1965年，是原国家教委（现教育部）直接管理的3所外语院校之一。原广州对外贸易学院成立于1980年，是原国家外经贸部（现商务部）直接管理的4所外贸院校之一。学校位于中国著名的历史文化名城和华南经济中心广州市。学校现有3个校区，总面积2172亩，其中北校区坐落在国家风景名胜区广州市白云山北麓，占地面积821亩；南校区位于广州大学城，占地面积1095亩；大朗校区坐落在广州市大朗',3420847);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('佛山科学技术学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://202.192.168.61/jiaowu/jwcjwc/fszsb/','佛山科学技术学院地处历史文化底蕴深厚、经济社会发展活力强劲的城市——广东省佛山市，是一所国家正式批准设立的全日制本科大学。学校办学历史可追溯到1958年创办的佛山师范专科学校和华南农学院佛山分院。1995年3月国家教委批准佛山大学和佛山农牧高等专科学校合并组建佛山科学技术学院，2005年经广东省人民政府批准佛山职工医学院和佛山教育学院并入佛山科学技术学院。2002年学校顺利通过教育部组织的本科教学工作合格评价，2007年以优良成绩通过教育部组织的本科教学工作水平评估，2013年7月经国务院学位委',1454103);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆电力高等专科学校',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqepc.com.cn ','“国家示范性高等职业院校建设计划”骨干高职院校重庆电力高等专科学校创建于1953年，学校已有50余年办学历史，是一所为国家培养高等应用型技术人才的全日制普通高等学校。学校坐落在重庆市李家沱长江大桥北桥头,交通方便，环境优美。建校50年来，积淀了优良的办学传统和经验,已为全国电力系统和其它工矿企事业单位培养输送了近3万名各类专门人才，在社会上享有较高的声誉。学校占地19.42公顷，建筑面积13万平方米。现有教职工353人，专兼职教师300余人，其中教授、副教授、副研究员等高级职称人员90余人。学校',228159);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中央司法警官学院',' ','河北',1,11,0,0,0,' ','暂空',' ','http://www.cicp.edu.cn','燕赵地，畿辅疆；历史悠，文脉长。中央司法警官学院坐落于我国历史文化名城——河北省保定市，位于京津石三角中心地带，距首都北京130公里。校园树木葱茏，碧草如茵，环境幽雅，为河北省园林式单位。学院是司法部直属的惟一普通高校，承担着培养全国司法行政尤其是监狱劳教系统高层次专门人才和培训司法行政系统领导干部与高级警官的重任，被誉为我国“中高级司法警官的摇篮”。半个世纪沧桑砥砺。学院创建于1956年，历经公安部劳改工作干部学校、司法部劳改工作干部学校、中央劳改劳教管理干部学院、中央司法警官教育学院等几个时',1880155);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('徐州工程学院',' ','江苏',1,0,0,0,0,' ','暂空',' ','http://www.xzit.edu.cn','徐州工程学院坐落于“一城青山半城湖”的历史文化名城—江苏省徐州市，是一所以培养高级应用型人才为主的全日制普通本科院校，校园风景如画、四季常青，小桥流水、亭台楼阁，美不胜收。获批教育部“卓越工程师教育培养计划高校”，江苏省高校毕业生就业工作先进集体、大学生创业教育示范校、高等学校和谐校园，是全国大体协桥牌分会主席和舞龙狮舞分会副主席单位，面向全国25个省市自治区招生。学校现有3个校区，占地面积1990.5亩，校舍面积57.39万平方米，固定资产17.97亿元，教学仪器设备值1.67亿元，设有14个',1274862);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南职业技术学院',' ','海南',0,0,0,0,0,' ','暂空',' ','http://www.hcvt.cn','“国家示范性高等职业院校”海南职业技术学院是海南省第一所独立设置的多科性高等职业技术院校，隶属于海南省人民政府。2002年被教育部、财政部确定为中央财政重点支持建设示范性职业院校项目的学院。2003年11月通过教育部评估，成为全国首批通过教育部高职高专人才培养工作水平评估的院校之一。目前正在积极进行国家示范性高等职业院校的申报工作。学院位于海南省的省会海口市，环境优美，交通方便。校园占地232亩，校舍面积18万多平方米（桂林洋新校区占地760亩,正在筹建中），馆藏图书60多万册，拥有各类实训、实',148311);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东财经职业学院',' ','广东',0,6,0,0,0,' ','暂空',' ','http://www.gdufe.com/','广东财经职业学院（原广东财税高等专科学校）是一所经国家教育部批准成立备案，由广东省财政厅主管的公办全日制普通高等院校，座落在美丽的广州白云山风景区北麓，交通便利、风景优美、环境优雅，具有坚实的办学基础和良好的教学风气，是广东省目前唯一一所财经类高等院校。学院前身是组建于1980年的广东成人财经学院，1995年改建为广东财税高等专科学校，2001年7月更名为广东财经职业学院。在20余年的办学过程中，学院形成了以财经技能教育为重点的办学特色，着力培养学生在财经行业第一线进行操作、指导和管理的能力，以',391554);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南第一师范学院',' ','湖南',1,4,0,0,0,' ','暂空',' ','http://www.hnfnu.edu.cn/','湖南第一师范学院，其前身为南宋时期张栻创办的长沙城南书院，1903年始立为湖南师范馆。1912年和1914年相继改为湖南公立第一师范学校和湖南省立第一师范学校，解放后更名为湖南省第一师范学校。2008年升格为普通高等本科院校，并更为现名。学校有“千年学府、百年师范”的美誉。这里群星灿烂，名人辈出：中国共产党的“一大”代表毛泽东、何叔衡、李达，上世纪四十年代我党“五大书记”之一任弼时，中华民国开国元勋黄兴，“难酬蹈海亦英雄”的民主革命家陈天华，清代中兴名臣曾国藩、左宗棠、胡林翼，以及一大批杰出人物',1288219);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江交通职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.zjvtit.edu.cn/ksexport/9/_index.jsp','“国家示范性高等职业院校建设计划”骨干高职院校浙江交通职业技术学院前身为创建于1958年的浙江省交通学校，1999年经教育部批准成立，属公办普通高等学校。学院于2001年被教育部确定为全国示范性职业技术学院建设单位，2002年被评为全国职业教育先进单位，2004年被评为全国交通职业教育先进单位。2005年被浙江省教育厅评为全省普通高校毕业生就业工作优秀单位，同年被浙江省综治委学校及周边治安综合治理工作领导小组授予“平安校园”称号并保持至今。2006年被浙江省人民政府授予“浙江省职业教育先进单位”',99941);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州民航职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.caac.net','广州民航职业技术学院1999年3月经教育部批准成立，是中国民航总局唯一一所独立设置实施高等职业教育的全日制普通高等院校。学院1980年建校，历经了技校、普通中专、全国重点中专、高职学院四个办学阶段。学院秉承“立足民航、服务地方、面向世界”的办学理念，形成了“依托行业、产学合作、双师双证、国际通用”的办学特色，是培养中国民航高技能人才的“摇篮”和广东省高职高专教育优质资源。2000年被教育部确定为首批15所全国重点建设示范性职业技术学院建设单位之一，2004年在全国高职高专院校人才培养工作水平评估',430794);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海第二工业大学',' ','上海',1,1,0,0,0,' ','暂空',' ','http://www.sspu.edu.cn/','上海第二工业大学成立于1960年，是一所以工科见长，管、经、文、理、艺多学科协调发展，培养学以致用应用型人才的全日制普通高等学校。学校地处上海浦东金桥地区，紧邻上海自由贸易试验区，与众多世界500强企业毗邻，具有天然的产教融合和国际交流的区域优势。学校占地面积近800亩，校园风景优美，教学与实验实训设施完备，图书藏书丰富，数字化校园网络全覆盖，各类运动场馆和生活设施齐全，充分满足学生成长与成才的需求。学校以本科教育为主，同时拥有专业学位研究生、留学生和高职教育。学校现设有工学、理学、管理学、艺术',2694745);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州番禺职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://121.33.253.219/zs/','广州番禺职业技术学院（原名番禺理工学院、番禺职业技术学院）1993年筹建，1997年9月教育部正式批准备案，是全国首批、广州市属第一所公办全日制普通高等职业院校。2003年被广州市政府确定为市属高等职业教育龙头院校。2005年4月以“优秀”成绩通过教育部人才培养工作水平评估。2006年12月被教育部、财政部确定为“国家示范性高等职业院校建设计划”首批28所立项建设院校之一。2009年12月通过教育部、财政部的验收，被正式批准为首批国家示范性高等职业院校。2010年11月被广东省人民政府授予“广东',397955);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏经贸职业技术学院',' ','江苏',0,6,0,0,0,' ','暂空',' ','http://www.jseti.edu.cn','“国家示范性高等职业院校建设计划”骨干高职院校江苏经贸职业技术学院Jiangsuinstituteofeconomictradetechnology招生代码：1156(普通类、艺术类、体育类)1756(中外合作办学、海外本科直通车)江苏经贸职业技术学院的前身是江苏商业管理干部学院，具有50多年的办学历史，是一所培养应用型、复合型、创业型高技能经营管理人才的全日制公办普通高校，现已成为江苏现代服务业人才最重要的培养基地之一。2005年，在江苏省“高职高专人才培养工作水平评估”中获得“优秀”等级。2',255290);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京特殊教育师范学院',' ','江苏',1,4,0,0,0,' ','暂空',' ','http://www.njty.edu.cn/','南京特殊教育职业技术学院坐落于“博爱之都”南京，是目前全国唯一一所独立设置的、以培养特殊教育师资为主的普通高等学校。学院始建于1982年，前身是教育部创办的中国第一所特殊教育师范学校，是我国政府与联合国儿童基金会的合作项目单位。学院自1997年开始培养五年制大专生，2002年升格为普通高等学校，2009年成为中国残疾人联合会和江苏省人民政府共建单位。学院先后成为“江苏省特殊教育师资培训中心”和“全国残疾人职业教育师资培训基地”，并荣获“全国特殊教育先进单位”、“江苏省文明单位”“江苏省精神文明建',443099);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林交通职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.jjtc.com.cn/','“国家示范性高等职业院校建设计划”骨干高职院校吉林交通职业技术学院始建于1958年，是一所以交通类专业为主的工科高等职业技术院校。学院坐落于吉林省长春市高新技术开发区，占地面积22万平方米，建筑面积13.6万平方米，建筑风格独特，校园绿树成荫，环境优雅。学院多年来坚持立足交通，服务社会的办学宗旨，形成了治学严谨、言传身教的教风和勤学善思、学以致用的学风。学院在2006年教育部高职高专人才培养工作水平评估中获得优秀等级。2007年被确定为吉林省首批重点建设示范性高等职业院校。目前，学院设有道路与桥',129746);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海商学院',' ','上海',1,6,0,0,0,' ','暂空',' ','http://www.sbs.edu.cn/','上海商学院是市属公办本科普通高校，前身为1950年建校的中央税务学校华东分校。学校现有在校生10000余名，开设26个本科专业。毕业生深受用人单位欢迎，近年来就业率稳定在98%左右，在全市高校中位居前列。学校坚持“以商立校应用为本”的办学理念，紧跟国家教育综合改革和经济结构调整步伐，紧贴上海国际贸易中心建设需要，关注和适应国内外商业发展环境，侧重培养“厚德博学、经世济民”的商务管理专门人才。学校聚焦商业（商务）开展理论与实务研究，瞄准新兴就业市场更新人才培养计划，透析国内外商业环境提升商业服务能',3219224);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbvtc.edu.cn/','“国家示范性高等职业院校”湖北职业技术学院的前身是孝感职业技术学院是1998年3月经国家教育部批准，由原孝感教育学院、孝感市财贸学校、孝感市卫生学校和孝感市机电工程学校四所大中专院校合并组建的一所新型高校。2003年4月，经湖北省人民政府批准，国家教育部备案，孝感职业技术学院更名为湖北职业技术学院。学院占地1010亩，建筑面积38.8万平方米。现有教职工894人，专任教师650人。在校生总数10508人。学院师资力量雄厚，有一支既有较高学科理论水平，又掌握现代技术（技能）的“双师型\"教师队伍。有',198744);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉船舶职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.wspc.edu.cn','“国家示范性高等职业院校”武汉船舶职业技术学院创建于1950年，是一所中央与地方共建、由湖北省人民政府管理的全日制普通高等学校。学院以“立足船舶、服务军工、面向社会”为办学定位，“以人为本、质量立院、特色创优”为治校方针，半个多世纪以来，为国家工业化和国防现代化建设培养输送了各级各类人才4万余名。学院面向全国招生，现有全日制在校生12000人。学院坐落在素有“九省通衢”之称的武汉市中心城区，南濒月湖，北临汉水。学院环湖而建，别具现代风格，是莘莘学子求学深造的理想园地。学院拥有一支素质较高、结构合',275834);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江建筑职业技术学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.hict.org.cn','黑龙江建筑职业技术学院位于北方名城哈尔滨市，是一所公办全日制普通高等学校，是全国第一批和黑龙江省第一所独立建制的高等职业技术学院。2009年10月，学院成为首批28所国家示范性高等职业院校建设单位。目前，学院是教育部建设部紧缺人才示范性培养培训基地、全国高职高专校长联席会议主席团单位、建设部教育协会高职高专与成人教育专业委员会主任委员单位、黑龙江省高职高专校长联系会主席单位、黑龙江省高校院校设置委员会委员单位、联合国教科文组织产学合作教席理事会理事单位。学院在68年的办学历程中取得了丰硕的成果，',351739);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('九州职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jznu.com.cn/','九州职业技术学院创建于1993年，原名九州大学（筹），是江苏省第一批独立设置的两所民办普通高校之一，2002年经江苏省人民政府批准、国家教育部认定，正式更名为九州职业技术学院，成为具有独立颁发国家承认文凭资格的全日制普通高等院校。学院坐落于全国历史文化名城徐州南郊风景区的泉山南麓，与中国矿业大学、徐州师范大学、徐州工程学院、徐州建筑职业技术学院等高校相毗邻，现有在校生近6000人，设有机电工程系、土木工程系、计算机工程系、经济与管理系、文学与法政系和应用外语系六个系，共32个专业，以工科为主，涵',36188);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙航空职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.cavtc.cn/','学院创办于1973年，1998年3月升格为高等职业院校，隶属空军装备部领导，是全军唯一一所国民教育性质的普通高校，是湖南省示范性高职院校和首批卓越职业院校建设单位，主要承担为军队装备修理和地方经济建设培养技术技能人才的任务。学院占地近800亩，建筑面积30余万平方米，总资产4.5亿元，教学仪器设备总值1.105亿元，藏书62.8万册。在校学生9200余人,在岗教职工719人，离退休职工298人。现有跳马、圭塘两个校区和洞井教育培训中心，设有航空机电设备维修学院、航空电子设备维修学院、航空机械制造',233251);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('包头职业技术学院',' ','内蒙古',0,1,0,0,0,' ','暂空',' ','http://www.btzyjsxy.cn/SiteFiles/Inner/page.aspx?s=1','包头职业技术学院是一所经教育部批准建立的全日制高等职业院校，是百所国家示范性高等职业院校之一，位于内蒙古自治区最大的工业城市、全国文明城市——包头。前身是创立于1956年的国家级重点中等专业学校——包头机械工业学校。学院原隶属于中国兵器工业总公司，1998年经教育部批准建立包头职业技术学院。2004年被教育部等六部委确定为承担国家技能型紧缺人才培养培训院校之一；2008年被原国家国防科技工业委员会确定为首批国防科技工业职业教育实训基地之一。学院总占地面积1000余亩，建筑面积为36万余平方米。教',168495);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黄河水利职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.yrcti.edu.cn','“国家示范性高等职业院校”一、办学历史悠久学院前身是国立黄河流域水利工程专科学校，始建于1929年，历经黄河水利专科学校、黄河水利学院、黄河水利学校等历史沿革，1998年3月经原国家教委批准，改制为黄河水利职业技术学院。二、国家支持有力学院原隶属国家水利部，现实行中央与河南共建，属全额预算拨款的公办高等院校。1999年被教育部、财政部列入“面向21世纪教育振兴行动计划”支持建设院校，2005年教育部等七部委授予我院为全国职业教育先进单位，并被评为全国高职高专人才培养工作水平评估优秀院校。2006',307332);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东培正学院',' ','广东',1,6,0,0,0,' ','暂空',' ','http://www.peizheng.com.cn','广东培正学院（原培正商学院）是一所经国家批准设立，招收国家任务生的全日制民办普通高等学校。2004年被省教育厅定为省级示范性软件学院试办单位。2005年3月经教育部批准升格为本科院校。学院坚持以学生就业为导向，以社会发展需要为准则，树立“以人为本”、“以学生为主体”的教育理念，以素质教育和培养学生创新精神为核心，走“质量立校、专家治校、科研兴校”的道路；坚持因材施教和个性发展的教育原则；详细人物小传以梁尚立董事长、何厚煌常务副董事长为代表的一批热心祖国教育事业的培正人和社会知名人士，多年来，为培',607722);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东松山职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdsspt.net','广东松山职业技术学院是一所国有全日制普通高等专科学校，已有27年举办高等教育的历史。学院前身为韶钢职工大学，地处韶关市南郊，邻近京珠高速公路，紧靠广韶公路，毗邻粤北名胜南华寺，为原广东工业大学校址。校园面积达840亩，先后开设过40多个专业。学院下设电气工程系、计算机系、机械工程系、外语系、经济管理系、基础教学部和实习工厂等教学系部。现有学生4200人，教师309人，其中副高以上职称有50人，在职攻读硕士研究生学位20余人，构成了一支以教授、教授级高级工程师、副教授、高级工程师为中坚，讲师、工程',167175);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('金华职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://zsw.jhc.cn/','“国家示范性高等职业院校”金华职业技术学院创办于1994年，在金华理工学院基础上筹建，继承了浙江农业机械学校、金华贸易经济学校、金华师范学校（含原义乌师范学校）、金华农业学校、金华卫生学校等6所国家和省部级重点中专的职业教育传统，具有102年的办学历史。现为浙江省批办最早、规模最大、专业门类最多、率先通过高职高专人才培养工作水平优秀评估的高职院校，是国家示范性高职院校立项建设单位、全国职业教育先进单位、浙江省文明单位、全国高职高专校长联席会议成员单位、中国高等职业技术教育研究会常务理事单位、中国',208729);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('日照职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://gotorzu.rzpt.cn/','学校简介日照职业技术学院是教育部批建的山东省第一所公办职业技术学院，是国家示范性高等职业院校和山东省首批骨干示范性职业技术学院。学院先后被评为国家科技成果重点推广项目技术依托单位、国家紧缺人才培养基地、全国艺术教育先进单位、国家教育技术实训仿真示范建设基地、山东省教书育人先进单位、省级文明单位、山东阳光留学教育试验基地和山东省高技能人才鉴定基地，是高职高专人才培养工作水平评估优秀院校、校园文明和德育工作评估双优院校、国家示范性高等职业院校优秀院校和全国毕业生就业工作典型经验高校。诺贝尔物理学奖获',2871354);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('盘锦职业技术学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://www.pjzy.net.cn/','盘锦职业技术学院是一所为生产、管理、服务第一线培养高级技术应用型人才的公立综合性全日制高等学校，是辽宁省第一所以“职业技术学院”命名的地方高校，是盘锦唯一高等学府。学院座落在具有“鱼米之乡”、“石油之城”美称的盘锦市区。学院占地266亩，建筑面积11万平方米，建有完善的校园网络系统和功能齐全的基础和专业实验室，有实习基地27个，现代化教学设备达到国家规定的标准。学院现有教职工410人，其中专任教师245人，教授、副教授80人，“双师型”教师占教师总数的40%，学院拥有一批省、市专业学科带头人。学',102166);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都航空职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.cap.edu.cn/','公办全日制普通高等学校全国首批国家示范性高等职业院校中国人民解放军定向培养直招士官试点学校全国职业技术教育先进单位中航工业高技能人才培训基地四川省高技能人才培训基地地理位置：学校地处成都市东郊(国家级)经济技术开发区——龙泉驿，占地900余亩。历史沿革：学校创建于1965年，原隶属于中国航空工业部。是教育部批准设立的首批职业技术院校。1999年，学校划转四川省，实行中央与地方共建，是四川省教育厅直属的普通高等学校。师资队伍：学校450余名专任教师中，70%以上具有研究生学历(学位)。国家级教学团',831084);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川电力职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.scdy.edu.cn','“国家示范性高等职业院校”2001年，学校经四川省人民政府正式批准命名为\"四川电力职业技术学院\"，其前身是始建于1917年的原国家级重点中专----成都水力发电学校。学院是四川省电力公司后备人才培养基地和生产人员培训基地，融教学、培训、生产、科研咨询于一体。学院2006年在“高职高专院校人才培养工作水平评估”中获“优秀”等级。2008年7月被教育部、财政部列为全国100所国家示范性高等职业院校建设单位。经过近90年的办学积淀和不断探索，学院逐步形成了具有自身特色的先进办学理念。遵循教育部\"以就业',243366);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('许昌职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.xcitc.edu.cn','招生代码：6276软件职业技术学院代码：6322许昌职业技术学院是一所公办全日制普通高职院校，河南省示范性高等职业院校。学院座落在曹魏故都许昌，占地1326亩。校园绿树常青、绿草如茵，环境优美。学院现有教职工950人，专任教师849人，其中具有研究生学历教师180人，教授、副教授及其他高级专业技术人员192人，“双师素质”教师415人。学院.NET技术开发教学团队、文秘专业教学团队和机电一体化专业教学团队被河南省教育厅确定为省级高等学校教学团队。近年来，学院教师发表论文879篇，其中被EI收录5',87892);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('曲阜远东职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.fareast-edu.net/','曲阜远东职业技术学院是经教育部批准，由台湾著名教育家、企业家王乃昌博士投资与曲阜市人民政府合作创办的一所民办普通高等学校，国家计划内全省高等学校统一招生，实施高等学历教育。学校坐落在孔子故里曲阜市石门山风景区，校园内山水、林木、古建筑相映，环境幽静，景色宜人，是读书治学的好地方。学校目前占地300余亩，建筑面积8万余平方米，有设备完善的教学楼、图书馆、教师和学生公寓、餐厅、礼堂等。图书馆藏书12万册，期刊齐全。学校有电子、通信工程等专业实验室6个，微机室10个，多媒体教室8个，语音室6个，微机6',69177);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽商贸职业技术学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.abc.edu.cn/','安徽商贸职业技术学院是2000年经省政府批准、教育部备案，由百年老校——安徽省商业学校和安徽商业职工大学合并建立的公办全日制专科层次的高等职业院校，隶属于安徽省教育厅。学院自组建以来，抓住高等职业教育发展机遇，励精图治，改革创新，办学水平与人才培养质量不断提高，2005年获安徽省高职高专人才培养水平评估“优秀”院校，2008年被省教育厅、财政厅确定为首批“安徽省示范性高等职业院校”立项建设单位，2010年被教育部、财政部确定为国家骨干高职院校立项建设单位。学院坐落于长江之畔的历史名城芜湖，占地5',252701);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽水利水电职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahsdxy.ah.edu.cn/','【学院概况】安徽水利水电职业技术学院是一所具有62年办学历史的公办全日制普通高校。学院为国家示范性高等职业院校、全国高职高专人才培养工作水平评估优秀单位、国家高技能人才培养基地、全国水利系统文明单位、全国职业教育先进单位和安徽省普通高校毕业生就业工作标兵单位。学院面向社会，培养适应生产、建设、管理、服务一线需要的高等技术应用性专门人才。开设水利、建筑、资源与环境、市政、机电、机械、电子信息及管理工程等八大类共50多个专业。校园环境优美，办学条件优越，师资力量雄厚，办学特色鲜明，办学质量优秀，毕业',616588);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阜阳职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.fyvtc.edu.cn/','阜阳职业技术学院是经国家教育部批准筹建、安徽省人民政府批准成立的公办普通高等学校，为安徽省首批示范高职院校、国家百所骨干高职院校立项建设单位。学校于1956年建校，1981年经安徽省人民政府批准为独立设置的成人高校阜阳教育学院。1998年开始举办高等职业教育，是安徽省起步较早的两所高职院校之一。2001年6月，更名为阜阳职业技术学院。2009年，阜阳建筑学校并入。2013年9月，阜阳卫生学校并入，为学校跨越式发展注入新的活力。学校现有老校区占地400亩，校舍建筑面积23.8多万平方米，教学仪器设',191098);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('硅湖职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.usl.edu.cn/','一、前言在中国长三角的核心地区，矗立着一座举世瞩目的新兴城市——江苏省苏州昆山市。这是一方沃土，文化底蕴丰厚，孕育了百戏之祖昆曲、水乡明珠周庄、奇石之王昆石，还有名闻遐迩的阳澄湖大闸蟹！她更是一方热土，历代英才辈出，创立圆周率的祖冲之、“富可敌国”的沈万三、“国家兴亡，匹夫有责”的顾炎武、创著《治家格言》的朱柏庐以及当代著名作曲家丁善德、航天英雄费俊龙，他们的成就与光辉令昆山人引以自豪和骄傲。解放后，特别是改革开放三十年来，昆山更是以其超凡的胆略、博大的胸怀和无尽的活力，深深地吸引了包括世界50',35339);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('柳州职业技术学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.lzzy.net','柳州职业技术学院是柳州市人民政府举办的一所全日制普通高等学校，坐落在祖国西南工业重镇广西柳州市，是全国100所、广西仅有的2所国家示范性高职院校之一、广西4所首批示范性高职院校之一。学院占地面积75.3万平米，固定资产总值约4.7亿元，教学仪器设备价值1.79亿元，图书107.56万册，拥有中央财政支持的实训基地3个，自治区示性实训基地14个，柳州市千万元设备值生产性实训基地2个。学院现有全日制高职生1.1万余人，教职工699人，其中专任教师507人，双师素质专任教师390人，具有研究生学历或硕',88567);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津中德应用技术大学',' ','天津',0,1,0,0,0,' ','暂空',' ','http://www.zdtj.cn/','天津中德职业技术学院是一所天津市属全日制独立设置的公办高职院校，是中国与德国、日本、西班牙三国政府在职业教育和培训领域最大的合作项目，是国家示范性优秀建设高职院校。学院以服务社会为导向，与企业、社会建立产学研结合的战略伙伴关系，成为“校企合作、校社联携”新型的“相互作用学校”，构筑校企之间“双向进入、互助双赢”的开放式教育机制。为此，在学校文化环境建设规划中，突出国际化办学特色，营造美观大方、品位高雅、内涵丰富、特色鲜明的校园环境文化。学院目前拥有总资产近15亿元人民币、各类先进设备和仪器上万台',396292);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泰州职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.tzpc.edu.cn/','泰州是一座具有2100多年历史的文化名城，州建南唐，文昌北宋，又是中国经济最发达的长三角地区十五个中心城市之一，江苏省先进装备制造业基地、全国生物医药创新基地，2015年全国GDP城市排名进入前50强。泰州职业技术学院是1998年3月经原国家教委批准建立的公办普通高校，也是江苏省第一所冠以“职业技术学院”的高职院校，办学历史近60年，桃李遍及大江南北。学院新校区坐落在国家级医药高新区，毗邻中国医药城，占地1190亩，建筑面积24.36万平方米，是全国一流的现代化校园、生态校园、文化校园。学院始终',81792);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州警官职业学院',' ','贵州',0,11,0,0,0,' ','暂空',' ','http://www.gzjgxy.cn/','贵州警官职业学院是经贵州省委、省人民政府批准，国家教育部和公安部备案，由贵州省公安厅和教育厅共同管理的全日制公安司法大专院校，是贵州省培养造就公安司法专门人才的重要基地.学院的前身为1949年成立的贵州省人民政府行政干部学校及1950年成立的贵州省人民政府公安干部学校。1950年贵州省人民政府行政干部学校更名为西南人民革命大学贵阳分校。1954年，西南人民革命大学贵阳分校与贵州省人民政府公安干部学校合并，为贵州省行政干部学校。1955年—1957年，贵州省行政干部学校分为贵州省公安学校和贵州省政',230898);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西警察学院',' ','山西',1,11,0,0,0,' ','暂空',' ','http://www.sxpc.edu.cn','山西警察学院肇始于1949年成立的山西省公安学院。1981年山西省人民警察学校挂牌成立。1984年公安部管理干部学院山西分院筹建。1991年公安部管理干部学院山西分院和山西省人民警察学校合并办学。2000年经教育部批准建立山西警官高等专科学校。2016年教育部批准建立山西警察学院。1978年以来，学院培养全日制毕业生共3.85万余人，函授、自考生2.4万余人，培训在职民警4万余人。毕业生中涌现出了一批公安英烈、公安英模、全国劳模、爱民模范、优秀人民警察、全国人大代表、联合国维和警察等，相当一部分',264167);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南方医科大学',' ','广东',1,3,0,0,0,' ','暂空',' ','http://portal.smu.edu.cn/zs/','南方医科大学（原第一军医大学）教育部批准的具有高等学历教育招生资格的全国重点院校教育部本科教学工作水平评估全优院校全国百强院校院校国标代码：12121招生批次：本科第一批专科第三批A线（广东）专科（7个协作省、区）由有招生计划的省区确定校训：博学笃行尚德济世学校概况南方医科大学前身为中国人民解放军第一军医大学，1951年创建于抗美援朝的战火烽烟之中,从北国到南粤，从军队到地方，几度使命更替、历经艰难困苦，却信念始终如一、精神传承不止。南方医科大学1979年就被确定为全国重点大学。2004年8月更',2978996);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川警察学院',' ','四川',1,11,0,0,0,' ','暂空',' ','http://www.scpolicec.com/','四川警察学院始建于1950年的川西人民行政公署公安厅公安学校，1956年建立四川省公安学校，1965年筹建西南人民警察学校（本科，文革初停建）。1980年中专学历教育分出单独建立四川省人民警察学校，1984年四川省公安学校改建为四川省公安管理干部学院。1999年两校合并筹建并于次年经教育部批准正式建立四川警官高等专科学校。2006年2月经教育部批准建立四川警察学院。2011年10月学校经国务院学位委员会批准为警务专业硕士研究生培养试点单位，是全国第一所开展警务硕士专业学位研究生教育的省属公安本科',850540);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京森林警察学院',' ','江苏',1,11,0,0,0,' ','暂空',' ','http://zsjyc.forestpolice.net/','南京森林警察学院由国家林业局主管，是我国唯一一所承担着培养、输送高素质森林公安、森林消防专业人才，培训在职森林公安民警和森林防火指挥员，开展森林公安和森林防火科学研究重任的公安本科院校，在我国公安和林业高等教育布局中地位特殊、使命重大。学校前身是1941年建立的南京特别市立第一职业学校，1947年从该校分离出来成立南京市农业职业学校独立办学;新中国成立后经专业调整，学校数易校名，于1954年建立林业部南京林业学校;1994年9月，改建为林业部南京人民警察学校;2000年3月，升格为南京森林公安高',626035);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南医药学院',' ','湖南',1,3,0,0,0,' ','暂空',' ','http://www.hnmu.com.cn','湖南医药学院是一所国家公办的全日制普通医学本科院校，1924年始创于湖南长沙(仁术护病学校)，2000年升格为怀化医学高等专科学校，2014年经教育部批准升格为湖南医药学院。学校占地面积1635.03亩，校舍建筑总面积22.5万平方米。学校现面向全国26个省、市、自治区招生，在校全日制大学生7200余人。学校现有专任教师519人，其中，教授61人，其他正高职称教师43人;副教授64人，其他副高职称教师94人;博士24人，硕士264人。享受政府特殊津贴专家2人，博士生导师1人，硕士生导师2人，省级',437035);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆工业职业技术学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqipc.net/zsjy/','重庆工业职业技术学院是经重庆市人民政府批准、教育部备案、独立设置的全日制公办普通高等院校,学校坚持把立德树人作为学校教育的根本任务，主要为装备制造、汽车、电子信息和现代服务业培养具有良好道德品质、勇于开拓创新、就业创业能力强的应用型、复合型、创新型高素质高技能人才。学校是全国首批国家示范性高等职业院校。学校前身是重庆机器制造学校。1956年，由原国家机械工业部创建;2000年，重庆机器制造学校和重庆机械职工大学合并升格为重庆工业职业技术学院;2006年，成为全国首批28所国家示范性高等职业院校;',359245);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽新华学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.axhu.cn/','安徽新华学院坐落于包公故里、科教基地、历史名城安徽省省会合肥，地处合肥市国家高新技术产业开发区大蜀山森林公园南麓，是经教育部批准，拥有学士学位授予权的省属普通本科高校。学校前身是安徽新华集团投资有限公司于2000年投资创办的安徽新华职业学院，2005年5月，学校经教育部批准升格为普通本科高校，更名为安徽新华学院。2005年12月，安徽新华学院党委正式成立，成为安徽省第一个成立党委的民办高校。2009年，顺利通过学士学位授予权评审，成为安徽省首家获得学士学位授予权的民办本科高校。学校坚持“教育为民',244072);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('铜陵职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.tlpt.net.cn','铜陵职业技术学院是一所全日制公办专科院校。学院坐落在长江中下游南岸，中国古铜都—铜陵市城区。铜陵市北临长江，南依黄山、九华山，改革开放以来，铜陵经济建设和社会发展呈现出飞速发展态势，各项经济指标列安徽省前列，铜陵的发展对学院的发展和毕业生就业提供了良好的机遇和支持。学院占地1330亩（含在建新校区），建筑面积15万多平方米。教学仪器设备总值3178万元，图书馆藏书42万余册，各专业实验室、实训室56个、校外实习基地60个、多媒体教室16个，教学计算机580台，并建有校园网络。学院文体设施齐全，生',97118);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽警官职业学院',' ','安徽',0,11,0,0,0,' ','暂空',' ','http://www.ahjgxy.com/','2000年6月，由安徽省司法学校、安徽省警官学校、安徽省政法干部学校合并组建的安徽警官职业学院成立。在省司法厅领导和教育厅指导下，警院紧紧围绕行业需求和经济社会发展对警法技能人才的需求，积极探索警法职业教育教学规律，明确了“以生为本、办学为民”的办学理念和“立足司法行政、面向政法系统、服务社会需求”的办学定位，确立了“政治建校”为首的五项办学原则，警院从而迈上发展的快车道。警院坚持以立德树人为根本，以服务发展为宗旨，以促进就业为导向，不断加强软硬件建设，优先投资兴建了警体运动训练中心、学术交流中',427321);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮南职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.hnvtc.ah.edu.cn/','淮南职业技术学院是经安徽省人民政府批准，教育部备案的高等职业院校。现有2个校区，占地600亩，建筑面积23万平方米。在校生1.2万余人，其中高职8668人，技师200人，技校生3630人，每年为集团公司安全培训8000人次，管理类、技术类、继续教育培训3000人次。现有教职工567人，其中专任教师324人;具有副高以上专业技术职务的120人，具有硕士及以上学位教师107人，从企业聘请兼职教师180人。学院现有采矿工程系、通风与安全系、煤矿机电系、信息与电气工程系等7个高职系;设有煤矿开采技术、矿',116203);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州交通职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://www.gzjtzy.net/','贵州交通职业技术学院是经贵州省人民政府批准成立，国家教育部备案的全日制普通高校。是一所以交通为特色的综合性高职院校，也是贵州省目前唯一的国家示范性高职院校。学院以培养交通类建设、管理、服务人才为主，并积极适应社会发展需求培养经济、信息、旅游、计算机、艺术等实用型高端技能专业人才，面向全国20个省(市)招生，在招收高职(专科)学生的同时兼招中职学生。学院分南、北及清镇三个校区，老校区(南院、北院)坐落于贵阳市城区西北，依山傍水，校园面积510亩，建筑面积23万多平方米，建有教学综合楼、实训楼、图书',232936);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州航天职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://www.gzhtzy.com','贵州航天职业技术学院由贵州省人民政府批准成立，经国家教育部正式备案的一所以工科为主的公办普通全日制高校(高职教育)。学院地处历史文化名城遵义市，占地500余亩。现有六个系(机械工程系、汽车工程系、电子工程系、计算机科学系、经济管理系、基础科学系)和二部(社会科学教学部、成教部)。学院开设27个专业，其中模具设计与制造是贵州省省级示范专业。学院依托航天科工集团的优势资源，设有两个校区和实训中心，拥有计算机教学中心、加工中心、机加实习厂，汽车检测与维修等多个实习厂，建有经国家劳动社会保障部门批准的国',122294);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南大众传媒职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://zsw.new.hnmmc.cn:8080/index.html','湖南大众传媒职业技术学院是湖南省教育厅、湖南省广播电视局和湖南广播电视台共建，省教育厅直属的公立普通高等院校。学院依托湖南传媒产业和其他文化产业优势，将传媒实践与传媒教育紧密结合，在全国率先创立“前台后院”的校企结合办学模式，形成了鲜明的传媒高等职业教育特色。学院坚持以就业为导向，主要为广播、电影、电视、电信部门以及网络公司、广告公司、音像公司、动画公司、游戏公司、报社、杂志社、通讯社、出版社等传媒机构培养高素质技能型专门人才，同时为其他企事业单位培养急需的特色人才。作为专门培养传媒职业技能型人',203954);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('永州职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.hnyzzy.com/','“国家示范性高等职业院校”永州职业技术学院是2000年7月经湖南省人民政府批准教育部备案的国立公办高等职业技术学院。学院占地面积4019亩，建筑面积40多万平方米，固定资产总值5亿多元，馆藏图书83万余册。现有在职教职员工1100余人，其中正高职称79人，副高职称287余人。开设了43个高职专业，涵盖了农业、医卫、商贸、理工、人文经管等专业门类，在校学生18000人。学院组建以来，始终坚持以服务为宗旨，以就业为导向，走产学研相结合的发展道路，致力于为地方经济和社会发展培养高技能专门人才和高素质劳',106005);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南铁道职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.hnrpc.com','湖南铁道职业技术学院是一所以工科专业为主、文管经等专业共同发展的高等职业院校。主要面向轨道交通、装备制造、电子信息、商贸管理等行业，培养适应岗位需求的准工艺师、准技师等高端技能型人才。学校始办于1951年，2000年7月，经湖南省人民政府批准，原株洲铁路电机学校和铁道部工业职工大学合并升格为湖南铁道职业技术学院。2004年，在教育部高职高专院校人才培养工作水平评估中获得“优秀”。2009年通过教育部、财政部验收，成为全国首批28所国家示范性高等职业院校之一。2009年11月，学校由中国南车集团移',401369);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南涉外经济学院',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.hieu.edu.cn/','湖南涉外经济学院是经国家教育部批准成立的民办普通本科院校。学校由湖南猎鹰实业有限公司投资创建于1997年，原名“湘南文理专修学院”;1998年迁址长沙市岳麓区麓谷园，更名为“湖南涉外经济学院(筹)”;2000年经湖南省人民政府批准设置为高职专科学校;2005年经国家教育部和湖南省人民政府批准，升格为本科院校。2009年，学校通过学士学位授予权评估，获得学士学位授予权。同年，学校加入劳瑞德国际大学联盟。目前，学校有全日制在校学生26158人，其中统招本科学生20467人，统招高职专科学生3976人',1355092);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南科技职业学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','oldname": "湖南科技职业学院','“国家示范性高等职业院校建设计划”骨干高职院校湖南科技职业学院于2001年8月经湖南省人民政府批准成立，由湖南中华职业教育社主办，是湖南省人民政府与中华职业教育社共建的、全国中华职业教育社系统唯一的公办高等职业学院。学院以“至诚至公、精业乐业”为核心价值观，以“走个性化、多样性、开放式之路，育高素质、高技能型人才”为办学理念，以创建国内一流示范性高职学院为奋斗目标。2003年12月，学院被教育部批准为湖南省唯一的首批试办国家示范性软件职业技术学院的高等院校。2005年12月，顺利通过了教育部高职',92002);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春东方职业学院',' ','吉林',0,0,0,0,0,' ','暂空',' ','http://www.dfzyxy.net','长春东方职业学院简介长春东方职业学院的前身是长春东方大学，1992年由吉林省老教授协会举办、原吉林省教育委员会批准。吉林省老教授协会，荟萃了中央设置在长春的高等院校和研究机构的专家、以及吉林省内多方面的知名教授和学者，其中一批长期坚持在教学、科研、医疗第一线的老教授，组成了长春东方大学的专职教师队伍；吉林省老教授协会会长担任学校校长。2000年6月，吉林省政府批准、国家教育部审定为具有颁发国家大学专科学历文凭资格、参加国家计划统一高考招生的普通高等学校，定名“长春东方职业学院”。设有文、理、工、',49348);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海口经济学院',' ','海南',1,0,0,0,0,' ','暂空',' ','http://www.hkc.edu.cn/','海口经济学院是一所以经济(公共)管理专业和特色专业为主的全日制普通本科院校。创办于1974年。其前身先后是海口市业余大学、海口职业大学、海口经济职业技术学院。学院刚刚升格，同时实施本科教育与专科教育，并由目前以高职（专科）教育为主逐步过渡到以本科教育为主。现有全日制学生一万多名，校园面积2288亩（含桂林洋高校区1735亩），建筑面积40多万平方米；有实验实训室146间，多媒体教室35间、语音实验室20间、座位5722个；教学用计算机2422台，教学仪器设备总值5120万元；图书馆馆舍2.49万',812907);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武昌首义学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hustwb.edu.cn','华中科技大学武昌分校是经教育部批准兴办的本科层次的全日制普通高等学校，是中国现代教育发展体系中按照新机制、新模式运行的创新型大学。学校秉承“借政策之力，集专家之智，走创新之路，成育材之地”的宗旨，依托华中科技大学,传承名校优良传统，集聚社会优质资源，借鉴先进管理经验，在与新世纪同步中，实现了跨越式的发展。原华中工学院院长、著名教育家朱九思教授和原中共中央委员、著名军事家刘精松上将担任名誉校长。学校坐落于风景秀丽的武昌南湖之滨，占地面积1000余亩，建筑面积48万平米。校内教学大楼、实验楼、图书馆',1403838);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常州信息职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.ccit.js.cn/','常州信息职业技术学院是江苏省首家信息职业技术学院，隶属于江苏省经济和信息化委员会。学院具有五十余年的办学历史，1962年创办，初名常州市勤业机电学校，1980年更名为常州无线电工业学校，2000年10月经江苏省人民政府批准由常州无线电工业学校和常州市电子职工大学合并组建成立常州信息职业技术学院。学院地处中国经济最发达的长三角腹地——常州，坐落在风景秀丽、充满现代气息，集教学、科研、培训、职业技能鉴定和社会服务于一体的常州高职园区。近几年，学院抓机遇、谋发展、促提升。2003年学院被教育部确立为国',327952);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东农工商职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','oldname": "广东农工商职业技术学院','广东农工商职业技术学院前身为创办于1952年的广东农垦机务学校，1984年开始举办大专学历教育，2000年转制为职业技术学院。半个多世纪以来，学院坚持以人为本，特色强校，求实创新，和谐发展的办学理念,立足农垦，面向广东，服务社会的办学定位，为各类企事业单位培养了4万多名专业技术人才和经营管理人才，目前有全日制在校生1万8千余人。2005年接受全国高职高专院校人才培养工作水平评估被确认为优秀院校。2012年被省教育厅确定为广东省示范性高职院校建设项目立项建设单位。学院主校区坐落于改革开放前沿的广州',626704);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.qtc.edu.cn/','青岛职业技术学院是一所省市共管、以市管为主的全日制普通高校。学院于2000年10月在原青岛市职工大学的基础上成立，2002年4月与原青岛教育学院合并，组建成新的青岛职业技术学院。原青岛市职工大学成立于1977年;原青岛教育学院成立于1951年，著名生物学家、教育家童第周和国学大师陆侃如曾任学院早期领导人。学院主要从事全日制高职教育和社会职业技能培训，兼顾成人学历教育，中外合作学历教育及中小学教师、校长培训等，具有接收外国留学生资质和聘请外国文教专家资格。现有西校区(青岛市黄岛区)、南校区(青岛市',361888);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东新安职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gdxa.cn/','广东新安职业技术学院创办于1998年，2000年广东省人民政府正式批准，在深圳市南山区政府大力扶持下，由杰出的人民教育家王屏山亲手创办的一所全日制普通高等院校。现有在校生共4683人。被授予“广东省首届十佳民办大专院校”、“2005年全国百强民办院校”“广东省民办高校就业竞争力十强”等荣誉称号。学院拥有坚强的领导班子，雄厚的师资力量。教师比例中研究生学历或硕士及以上学位的教师占32%，高级职称教师占26%。学院属大专层次，学制三年，开设有生物技术及应用、生物技术及应用（城市园林方向）、生物技术及',155264);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('威海职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.weihaicollege.com/','威海职业学院是一所公办、全日制普通高等职业院校。全国首批“国家示范性高等职业院校”、教育部高职高专人才培养工作水平评估优秀等级学校、国家高技能人才培养示范基地、山东省教育工作先进单位、山东省制造业紧缺人才培训基地、山东省机械制造专业人才外派劳务基地、山东省普通高校毕业生就业工作先进集体、山东省高等学校德育工作优秀单位、山东省高等学校校园文明建设工作优秀单位、山东省船舶工业校企合作示范单位、山东省服务外包人才培训机构和人才实训基地、全省高校师德建设先进集体、全省图书工作先进集体。学院地处威海市科技',458339);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('佛山职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.fspt.net/','佛山职业技术学院是1999年经广东省人民政府批准，由佛山市人民政府举办的一所全日制普通高校。学院分为两个校区，即南海校区和卫国路校区，总占地面积500多亩。学院拥有一支经验丰富、业务能力强的“双师型”教师队伍，设有文理兼容的3大类15个主干专业，共有各类学历教育在校学生3000余人。学院主动适应我省经济建设和社会发展的需要，以培养生产、服务、管理第一线需要的应用型技术人才为培养目标。学院既招收参加全国统一高考的普通高中毕业生，同时又招收参加全国统一高考或单独命题考试的职业高中、中专和技校应届毕业',278883);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdp.edu.cn','“国家示范性高等职业院校建设计划”骨干高职院校山东职业学院创建于1951年，1958年至1963年曾升格为济南铁道学院，1980年被公布为首批国家级重点中专学校。2000年，山东省人民政府批准将济南铁路机械学校改建为山东职业学院。2004年11月，学院由隶属铁道部改为由山东省教育厅直属管理，先后为铁路和山东省培养了4万余名德才兼备的技术人才。学院校园占地825亩，建筑面积25.5万平方米。设有机械工程、电气工程、信息工程、土木工程、生物工程、管理等六个系，在校生8701名。学院在长期的办学历程中',604692);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东劳动职业技术学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdlvtc.cn/','山东劳动职业技术学院(山东劳动技师学院)是山东省人力资源和社会保障厅直属的国办全日制普通高等院校。学院始建于1955年，是全国第一所高级技工学校，办学底蕴深厚，具有培养“专科学历+技师职业资格”高端技术技能人才的独特优势。学院是“全国职业教育先进单位”，“国家高技能人才培养示范基地”，“山东省高等教育技能型特色名校”，并获得“国家技能人才培育工作突出贡献奖”。牵头组建了全省最大的职教集团——山东省机械行业职业教育培训集团。学院位于美丽的泉城济南。现有槐荫和长清两个校区，占地面积1050亩，建筑面',234213);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('莱芜职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.lwvc.edu.cn/','莱芜职业技术学院是2000年10月经山东省人民政府批准建立的一所全日制普通高等职业院校，目前，学院分新老两个校区办学，占地面积2406亩，建筑面积33万平方米；学院现有固定资产4、5亿元，实验实习设备总值4650万元，馆藏图书61万册。建有105个专业实验实训室和68个校外实习实训基地。学院在职教职工646人，专任教师501人。教授、研究员22人，副教授、副研究员103人，具有硕士以上学位138人，其中，博士后1人，博士5人。现有全日制在校生9286人。学院设有机电系、经济管理系、计算机系、师范',76856);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东女子学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.sdwu.edu.cn/','山东女子学院是由教育部和山东省人民政府批准、山东省教育厅和山东省妇联共同主管的全日制普通本科院校，是山东省唯一一所女子院校，全国三所女子普通本科院校之一。学校前身是创建于1952年的山东省妇女干部学校，1987年、1995年分别更名为中国妇女管理干部学院山东分院、中华女子学院山东分院，2010年改建为普通本科院校——山东女子学院。学校以实施普通本科教育为主，同时承担妇女干部培训、职业资格培训、成人学历教育等任务。学校是山东省教师教育基地、全国和山东省妇联妇女/性别研究培训基地、全国和山东省家庭教',845355);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('烟台南山学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.nanshan.edu.cn/','烟台南山学院是由国家大型民营企业南山集团投资30多亿元兴办、教育部批准的普通本科高等院校。学校地处胶东半岛气候宜人的新兴海滨城市烟台龙口，现有南山及东海两处校区。南山校区紧邻国家4A级旅游景区—南山景区；东海校区位于渤海海滨森林公园内，校园北500米是天然海滨浴场，西1000米是亚洲最大的滨海国际高尔夫球场。学校现设有机电工程学院、自动化工程学院、软件工程学院、商学院、工商管理学院、信息科技学院、旅游管理学院、机械工程学院、艺术学院、外事翻译学院等二十三个二级学院。建校十几年来,学校坚持以服务经',530025);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽工业经济职业技术学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.ahiec.net/','安徽工业经济职业技术学院始建于1983年，2001年6月经安徽省人民政府批准、教育部备案，是一所全日制公办高职院校，现为全国高职高专院校人才培养工作水平评估优秀单位、安徽省示范性高职院校、国土资源部职业教育先进单位、安徽省文明单位、全省就业工作先进单位、安徽省普通高校毕业生就业工作标兵单位，安徽省普通高校大学生创新创业教育示范院校，安徽省学生资助工作先进单位。学院位于合肥市包河区，现有普通全日制高职在校生12000余人，占地531.39亩，校舍建筑面积21.36万平方米。设有旅游管理学院、机械与',304093);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('济宁职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.jnzjxy.com.cn','济宁职业技术学院成立于2000年10月，是山东省第一批国办高等职业院校，山东省首批技能型人才培养特色名校。学院占地955亩，现有教职工667人。全日制在校生12700余人。学院设17个教学、科研单位，开设16大类62个专业，拥有65个校内实训中心，681家校外实习基地，校内建成了济宁市装备制造业公共实训基地。学院先后荣获“全省职业教育先进单位”、“山东省技能人才培养先进单位”、“全省职业教育科研工作先进单位”、“全国文明单位”等荣誉称号。2014年确立为山东省首批教育信息化试点单位;2015年获',135851);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州电子信息职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://www.gzeic.com/','贵州电子信息职业技术学院是经国家教育部批准，独立设置的全日制普通高等职业技术学院，是一所以工科为主，集文、理、经、管等学科为一体的综合性职业学院。学院被教育部等六部委列为数控加工技术领域、计算机应用与软件技术领域两个方面的紧缺人才培养培训基地；劳动和社会保障部、信息产业部在我院设立了全国电子信息产业高新技能人才培训基地。近年来，学院先后获得了“全国职业教育工作先进单位”、“全国职业指导工作先进学校”、“贵州省职业教育先进单位”、“贵州省信息产业‘十五’先进单位”、“贵州省绿化先进单位”、“贵州省',396256);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南生物机电职业技术学院',' ','湖南',0,2,0,0,0,' ','暂空',' ','http://www.hnbemc.com/','湖南生物机电职业技术学院是一所具有110年办学历史的全日制公办普通高等院校，是拥有单独招生资质的省级示范性高职学院。世界著名杂交水稻专家袁隆平院士为学院名誉院长。学院前身“湖南私立修业学堂”始建于1903年。民主革命先驱黄兴、教育家徐特立、开国领袖毛泽东是学院早期教师的杰出代表，培养了革命先烈毛泽覃、国家领导人王首道等一大批国家栋梁之才。学院占地面积2070亩，分两区一场即东湖校区、马坡岭校区、产学研示范场。总建筑面积23万平方米，有图书馆2座，馆藏图书100余万册，各类实验实训场所92个，教学',96281);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西生态工程职业技术学院',' ','广西',0,2,0,0,0,' ','暂空',' ','http://www.gxstzy.cn','广西生态工程职业技术学院是广西壮族自治区人民政府举办，广西林业厅、教育厅管理的全日制普通高等学校，学校是自治区示范性高等职业学院、首批国家高技能人才培养示范基地、自治区特色高校、广西珍贵乡土树种良种培育中心、中国(南方)现代林业职业教育集团理事长单位。学校位于风景秀丽的柳州市柳北区，校园绿树环绕、湖光倒映，风景优美，是自治区“绿色大学”。学校创办于1956年，至2016年，学校已培养各类毕业生50000多人。学校占地面积18000亩，校园面积1100多亩，建筑面积30多万平方米，教学科研仪器设备',46369);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('恩施职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.eszy.edu.cn/','恩施职业技术学院是省委省政府于2000年8月批准由原恩施教育学院、恩施财校、恩施工校、恩施农校、恩施林校、恩施供销学校合并组建成立的一所高等职业技术学院，是州人民政府主办的全州唯一的一所全日制普通高等院校。“十一五”以来，全院教职工坚持以服务为宗旨，以就业为导向，以培养高技能人才为根本，以提高人才培养工作水平为重点，努力服务民族经济发展，走出了一条加快发展、特色发展、全面发展的新路子。学院先后荣获“湖北省省级文明单位”、“湖北省高校先进基层党组织”、“湖北省农村劳动力转移培训阳光工程先进单位”，',36322);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南国土资源职业学院',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.yngtxy.net/','云南国土资源职业学院是一所办学历史较为悠久的省属全日制普通高校。学院的前身是1978年创办的云南省地矿局职工大学。2001年4月，经云南省人民政府“云政复〔2001〕83号”文批准，教育部正式备案，学校改制更名为“云南国土资源职业学院”，实现了由成人高等教育向普通高等教育、由面向地质系统办学向国土资源大行业和社会需求办学的跨越式转变，是云南省首批成立的全日制高职院校之一，也是云南省屈指可数的高等教育办学历史超过30年的高职院校之一。经过30余年的发展，学院现有昆明经济技术开发区校区、小板桥校区、',80860);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北软件职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hbsi.edu.cn/','学院简介河北软件职业技术学院（HEBEISOFTWAREINSTITUTE）1972年建校，坐落于历史文化名城——保定市。学院直属河北省教育厅，是河北省首家独立建制的以软件技术教育为主的高等职业院校，是教育部批准的国家35所示范性软件职业技术学院建设单位之一。学院坚持“立足高职、服务IT”的办学定位和“依托现代新兴产业，重点发展软件技术和动漫技术教育”的办学特色，坚持“知行合一、厚德强能、追求卓越、彰显特色”的办学理念和“全面贯彻党的教育方针，坚持育人为本，德育为先，加强素质教育，以服务为宗旨，',121082);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西交通职业技术学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.gxjzy.com','广西交通职业技术学院前身为创建于1958年的国家重点中专广西交通学校，隶属于广西壮族自治区交通运输厅。2002年经自治区人民政府批准，升格为公办全日制普通高等职业院校，是广西唯一一所培养交通运输专门人才的高等学校，荣获教育部高职高专人才培养工作水平评估优秀院校、全国交通职业教育示范性院校、国家现代学徒制试点单位、广西高等教育综合改革首批试点高校、广西示范性高等职业院校、广西首批特色高校建设单位、广西高端应用型本科人才联合培养试点单位、广西唯一军队士官直招定点培养院校。学院占地面积1200多亩，分',111518);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南交通职业技术学院',' ','云南',0,1,0,0,0,' ','暂空',' ','oldname": "云南交通职业技术学院','“国家示范性高等职业院校”云南交通职业技术学院是2001年4月经云南省人民政府批准，由原云南省公路局职工大学、云南省交通学校、云南省交通职工中等专业学校三校合并组建的一所以服务为宗旨，就业为导向，依托行业，服务交通，面向社会，以产学研结合为人才培养途径，重点培养公路建设、汽车运用技术、运输、管理、信息技术及水运等方面下得去、留得住、用得上的高等技术应用性专门人才的公办专科院校。学院是国家汽车运用与维修类技能型紧缺人才培养基地，中央财政支持建设的国家职业教育汽车实训基地，中国扶贫开发协会云南劳务培',255084);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉生物工程学院',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.whsw.net','武汉生物工程学院是在著名科学家钱学森院士亲切关怀下创办起来的我国第一所生物工程类普通本科高校，也是湖北省目前唯一一所拥有学士学位授予权的民办本科高校。学校坚持“立校为公、办学为民、依法治校、回报社会”的办学宗旨，以钱学森精神和延安精神为办学指导思想，以办全国高水平民办大学为目标，不断改善办学条件，提高办学水平和教育教学质量，促进学校全面、协调和可持续发展。学校占地面积1312亩，建筑面积65万平方米，在校生16000余人，教职员工1300余人，其中教授、副教授223人，图书馆藏书120多万册，实',959623);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西工业职业技术学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.gxic.net','广西工业职业技术学院是2003年8月经广西壮族自治区人民政府批准由原广西南宁化工学校和广西轻工业学校合并升格成立的高等职业学校，隶属广西壮族自治区工业和信息化委员会，是教育部高职高专院校人才培养工作水平评估优秀学校和广西示范性高等职业院校重点培育单位。2013年10月，经广西壮族自治区人民政府批准，贵港职业学院并入。原广西轻工业学校始建于1956年，原广西南宁化工学校始建于1958年。原贵港职业学院的前身是贵港市师范学校，始建于1904年。学院现有校园总占地面积1038亩。固定资产总值4.5亿元',113887);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北石油职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.pvtc.edu.cn/','河北石油职业技术学院坐落在“京津走廊”上的明珠城市--廊坊，创办于1978年，隶属于中国石油天然气管道局(CPP)，是河北省人民政府批准、教育部备案的全日制普通高等职业技术院校，是一所以服务石油天然气长输管道及其配套专业为特色和优势，普通高等职业技术教育、成人高等教育和企业岗位培训协调发展的综合性企业大学，面向全国招生。学院坚持“以服务为宗旨，以就业为导向”的办学指导思想，明确了“立足企业、辐射河北、面向全国”的发展定位，建立了与企业发展和社会需求相结合的办学机制。30年来，学院形成了以石油天然',262614);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉工贸职业学院',' ','湖北',0,12,0,0,0,' ','暂空',' ','http://whitvc.zsxx.e21.edu.cn/','武汉工贸职业学院创办于1988年,是经国家教育部批准直属湖北省教育厅领导,具有独立颁发学历文凭资格面向全国招生的全日制普通高等学校。学院座落在素有“九省通衢”美称的湖北省武汉市，位于东湖风景区磨山景区，南邻中国光谷、东湖新技术开发区，北倚武汉植物园，东抵多所重点大学，西至东湖之滨，校园依山傍水、绿树成荫、环境幽雅，是典型的园林式学府。武汉工贸职业学院面向全国招生。目前开设有机电一体化技术、电子信息工程技术、商务英语、国际经济与贸易、物流管理、环境艺术设计等特色鲜明的21个专业(方向)。涵盖文、理',56600);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西国际商务职业技术学院',' ','广西',0,6,0,0,0,' ','暂空',' ','http://www.gxibvc.net ','广西国际商务职业技术学院是一所以培养适应现代商务事业发展需要的国际化应用人才为宗旨的国家公办全日制高等院校，是培养广西商务行业经理人才的摇篮。学院隶属于广西壮族自治区商务厅，前身是创建于1965年的广西对外经济贸易学校，2002年升格为高等职业学院，更名为广西国际商务职业技术学院。在50年的办学历程中，我院先后荣获全国精神文明建设工作先进单位，教育部高职高专人才培养工作水平评估优秀等级，“全国毕业生就业典型经验高校”，首批“全国青年创业示范园区”，全国外经贸职业教育先进集体;自治区示范性高职院校',123190);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西农业职业技术学院',' ','广西',0,2,0,0,0,' ','暂空',' ','http://www.gxnyxy.com.cn','【基本情况】广西农业职业技术学院(以下简称“学院”)创办于1942年，是广西唯一的以农牧类专业为主的自治区直属公办高等职业院校。学院的前身是国家级重点中专广西农业学校，2002年8月经自治区人民政府批准升格为全日制普通高等学校，2002年7月被教育部、劳动和社会保障部、国家经贸委授予“全国职业教育先进单位”荣誉称号，2005年12月被广西壮族自治区教育厅、发改委、财政厅、人事厅、劳动和社会保障厅、农业厅、扶贫办授予“全区职业教育先进单位”荣誉称号，2007年7月被批准为副厅级建制的自治区直属高校',90449);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长治职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.czzy.cn','长治职业技术学院于2000年9月由原长治职工大学、晋东南煤矿学校、长治农业学校合并组建而成，属于国务院授权后、经山西省人民政府首批批准、国家教育部备案、由长治市政府举办的地方性高等职业技术学院。长治职业技术学院座落在全国十大魅力城市之一、国家级园林城市、山西省环保先进城市——长治市，校园占地面积390亩，教学行政用房6.24万平方米，教学仪器设备值1062万元，学生宿舍面积近3万平方米，图书馆藏书约30余万册，学院下设教学系部12个，初步形成了以工科为主、专业齐全的多学科专业格局。学院现有教职工',91425);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北建材职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hbjcxy.com/','河北建材职业技术学院是经河北省人民政府批准、教育部备案的、全国唯一以“建材”命名的高等职业技术学院。2005年被国家劳动和社会保障部评为“创新能力培训实验学校”，被中国教育科学研究院和全国高职高专协作会评为“中国十大特色高职院校”。是河北省建材职业教育集团牵头单位，是教育部支援西部教育顾问院校。学院的前身是1978年建立的国家建材局秦皇岛玻璃工业技工学校，1985年改建为国家建材局管理干部学院秦皇岛分院，1998年与河北建材工业学校合并成立河北建材职工大学，2001年改建为河北建材职业技术学院，',192399);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潍坊职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdwfvc.com/','优越的地理位置——潍坊职业学院是2001年7月经山东省人民政府批准组建的一所公办全日制普通高职院校，学院坐落在著名世界风筝都、中国优秀旅游城市——山东省潍坊市，潍坊市是山东半岛都市群最大城市，是中国人居环境奖城市、国家环保模范城市、国家卫生城市、国家园林城市。学院是山东省高等教育技能型人才培养特色名校、山东省单独招生资格高校、山东省“3+2”分段培养本科生首批试点高校、山东省首批高等职业教育和技师教育合作培养试点学校，分为奎文、滨海两个校区，占地面积2500余亩，建筑面积47万平方米，教学科研仪',302745);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('柳州铁道职业技术学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.lztdzy.com/','柳州铁道职业技术学院是柳州市人民政府和广西壮族自治区教育厅管理的公办普通高等学校，是我国西南地区第一所轨道交通类专业高职学院。官塘新校区位于柳东新区官塘大道柳州职教园区。新校区占地面积66万多平方米，规划校园建筑面积28万平米，现已建成使用建筑23万平方米。学校现有全日制高职在校生12000多人，其中本科生40人;教职员工662人，其中专任教师467人，具有副高及以上职称130多人。设有9个二级学院、2个教学部及1个教育中心，开设普通高等职业教育招生专业41个。学校创办于1956年，是广西壮族自',265813);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明工业职业技术学院',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.kmvtc.net/','昆明工业职业技术学院是一所国有公办普通高等职业技术院校。学院以冶金、机械、材料、电气等工科专业为主，培养适应现代工业企业生产所需的高级应用型人才。学院是云南省“高级技能人才培养基地”、昆明市“文明单位”，2007年被中国教育报中国名校600家编委会选入《中国名校600家》A类学校。学院具有招收留学生资质，目前有留学生在校就读。2007年11月学院通过国家教育部人才培养工作水平评估，结论为“良好”。2008年9月，经云南省教育厅批准，以学院为龙头组建成立了“云南工业职业教育集团”。11月，经云南省',73322);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('烟台职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.ytvc.com.cn','“国家示范性高等职业院校建设计划”骨干高职院校烟台职业学院是经山东省人民政府批准，烟台市人民政府主办的全日制普通高校。学院2001年7月正式建校。六年来，学院实施“特色立校”、“创新兴校”和“人才强校”三大战略，以过硬的办学实力、鲜明的办学特色和优质的办学品牌树立了良好的社会形象和办学声誉，在专业建设、招生就业、校企合作、联合办学等诸多方面走在了全省高职院校前列。一、过硬的办学实力办学条件。学院占地面积1500余亩，建筑面积36万平方米。固定资产8.5亿元，实训设备8500余万元，图书134万册',293512);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南交通职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnjtzy.com.cn','湖南交通职业技术学院是隶属于湖南省交通运输厅的一所全国示范性高等职业院校，已有五十多年办学历史。举办高职教育以来，学校始终坚持“立足交通行业，面向市场经济，服务富民强省”的办学定位，始终坚持“厚德树人、技能立业、特色兴校”的办学理念，始终坚持“好品德、好技能、好使用、好形象”的育人标准，形成了“校企合作、订单培养”的办学特色，为交通事业发展和地方经济建设培养高素质技术技能型人才，学校的品牌影响和综合实力走在了全省高职院校和全国交通类高职院校的前列，先后荣获了“全国普通高校毕业生就业工作先进集体”',190347);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南商务职业技术学院',' ','湖南',0,6,0,0,0,' ','暂空',' ','http://www.hnswxy.com','湖南商务职业技术学院始建于1950年，是新中国最早设立的商科学校之一。六十多年来，学院为社会培养了大批高级技能型人才，很多已经成为商界学界政界的精英。近年来，学院按照“对接产业、融入行业、提升质量、打造特色”的办学思路，坚持为湖南区域经济服务，积极探索“就业导向、校企合作、工学结合”的人才培养模式，狠抓内涵建设，深化教育教学改革，使学院得到了快速发展。2008年获教育部人才培养工作水平评估优秀等级;2009年进入湖南省示范性高职学院序列;2010年被评为省文明高校;2011年跨入全国供销合作社系',172365);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北政法职业学院',' ','河北',0,11,0,0,0,' ','暂空',' ','http://www.helc.edu.cn/','河北政法职业学院是一所具有59年历史的高等院校，隶属于中共河北省委政法委。前身是河北省建设学院，成立于1949年7月，当时的河北省人民政府副主席杨秀峰同志兼任院长。1954年4月，改建为河北省政法干部学校。1983年11月，改建为河北政法管理干部学院，承担法律专业成人大专学历教育和全省政法系统在职干部岗位培训。1998年4月，与河北省司法厅、河北省律师协会联合建立了河北律师学院，定期对执业律师进行培训。2001年4月，改建为河北政法职业学院，举办高等职业教育，并继续承担成人学历教育、岗位培训和大',223267);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥通用职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.hftyxy.com','合肥通用职业技术学院，是经安徽省人民政府批准建立的全日制高等职业技术学院，由中国机械工业(集团)公司合肥通用机械研究院举办。校址位于合肥市长江西路888号合肥通用机械研院内。校园占地400余亩，建筑面积近10万平方米。校园设施齐全，环境幽静，是读书成才的理想场所。合肥通用机械研究院是具有50余年积淀的国家一类研究院，拥有一支精干的科技队伍，一批国内领先的科研设备，具有丰富的科技工作经验和丰硕科技成果，文化底蕴深厚，在机械、石油化工等众多行业具有特殊地位和广泛影响。学院认真落实教育部关于高职高专院',73266);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沧州职业技术学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.czvtc.cn/','学院简介:办学性质：沧州职业技术学院是经河北省政府批准，教育部备案的国办全日制普通高校，实施专科层次的高等职业技术教育。办学理念：以社会需求为导向，以能力培养为手段，以质量求生存，以特色创优势，为地方经济建设和社会进步服务。培养方向：以掌握成熟技术、管理规范为目标，培养适应生产、建设、管理、服务第一线需要的德、智、体、美全面发展的高素质、高技能应用性专门人才。办学条件：学院占地面积1120亩，建筑面积12万平方米，图书馆藏书30余万册，实验设备总值3000余万元，教学科研环境优越。学院生活设施齐',122918);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北能源职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ',' http://www.hbnyxy.cn','河北能源职业技术学院是经河北省人民政府批准，教育部备案的省属普通高等学校，面向全国招生。是一所以工科为主，多学科相结合的普通高等职业技术学院。学院位于环渤海“京、津、唐”经济技术开发区的河北省唐山市区，交通便利，。学院占地27.9万平方米，校舍建筑面积11万平方米，学院图书馆藏书36万余册。建有实践矿井、综合实习车间、电气实验实训中心等56个校内实践教学基地和与企业合作建设的81个校外实训基地。建有标准的田径运动场、篮排球场和舒适、整洁的生活园区,有良好的学习和就业环境。学院已有36年的高等教育',134673);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南体育职业学院',' ','湖南',0,7,0,0,0,' ','暂空',' ','http://www.hntyxy.net/','湖南体育职业学院座落在长沙市体育新城，占地面积689亩。院内风景秀丽，优雅宁静。是我省唯一一所体育教育与运动训练兼融的全日制普通高等院校，担负体育教育和竞技体育的双重任务，以培养社会需要的应用型体育人才和优秀运动员人才为已任。是名副其实培养体育明星的摇篮，打造“体育湘军”的基地。学院现有教职员工769人。机构设置有10个处室（党办、院办、人事处、训练竞赛处、教务处、学生工作处、后勤财务处、保卫处、产业办、老同志管理办）。教辅单位设二系（体育系、社会体育系）、二部（继续教育部、思想政治理论课教学部',62110);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄铁路职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.sirt.edu.cn/','“国家示范性高等职业院校”石家庄铁路职业技术学院创建于1950年，原为铁道兵工程学院财材系，1994年独立办学，更名为石家庄铁路工程学校。2000年经河北省人民政府批准改建为高等职业技术学院，隶属于铁道部、中国铁道建筑总公司。2005年6月移交河北省人民政府管理，是一所具有优良传统的全日制普通公办高校。学院现有南、北两个校区，占地面积566亩，建筑面积22.5万平方米，固定资产总值2.2亿元，教学仪器设备总值3619万元，图书馆藏书近58万册（电子32万册），建有完备的校园网，有测绘技术、建筑技',547356);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南工程职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hngczy.cn','湖南工程职业技术学院是经湖南省人民政府批准、国家教育部备案，隶属于国土资源部门独立设置的一所国有公办全日制普通高等院校。在50余年栉风沐雨的历程中，学院秉承\"传承地质文化，培养高素质技能型工程人才\"的办学理念，遵循\"德技共举、知行合一\"校训，坚持依托地勘、服务社会的宗旨，改革创新，科学发展，先后获得\"国土资源职教工作先进单位\"、\"国土资源部地勘行业职业技能鉴定工作评估优秀鉴定站\"，\"省直机关文明单位\"、\"省技能人才培养先进单位\"、\"湖南省重品质守信用和谐示范单位\"、\"长沙市花园式单位\"等荣誉称号',112241);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东营职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.dyxy.net/','“国家示范性高等职业院校建设计划”骨干高职院校一、学院概况东营职业学院是山东省人民政府批准、国家教育部备案、东营市人民政府举办的一所全日制普通高等专科院校。学院占地面积3000余亩，规划建筑面积43万平方米，已建成建筑面积29.5万平方米；教学仪器设备总值5120万元，全日制在校生14000余人，各类成人教育学员5000余人；学院设有文法、经济、教育、艺体、计算机、工业工程、农业工程等七个系，一个中专部和一个农科所，在职教职工810人；学院建有机械、电子、电工、数控机床加工、金工、生化、会计模拟',116199);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('聊城职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.lctvu.sd.cn','聊城职业技术学院坐落于有“江北水城、运河古都”美誉的国家历史文化名城——山东省聊城市，2000年10月经山东省人民政府批准成立，是一所专科层次的公办全日制普通高等学校。学院坚持“以人为本，质量立校”的办学理念，走“产学研一体、做学教合一”的办学路子，不断凝练办学特色，深化内涵建设，2010年8月被山东省教育厅和财政厅确定为首批“山东省示范性高职院校”建设单位，2012年10月被山东省教育厅和财政厅确定为首批“山东省高等教育技能型特色名校”立项建设单位。是解放军总医院（301医院）在全国建立的护理',277605);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兴安职业技术学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.nmxzy.cn','兴安职业技术学院坐落在大兴安岭南麓素有“内蒙古延安”美称的乌兰浩特市。是2001年经内蒙古自治区人民政府批准的全区首批独立设置的高职院校，自治区示范院校。学院现占地面积50万平方米，1500亩新校址正在建设中。学院现有教职工634人，各类专业技术人员421人，其中，教授23人，副教授182人，硕士学位以上97人，双师型教师143人。学院高职类本科、五年应用型本科有10个招生专业，专升本23个招生专业，颁发全日制本科毕业证书，本科现在校生1232人。33个专科招生专业，涵盖文、理、农、工、医五大类',61590);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京农业职业学院',' ','北京',0,2,0,0,0,' ','暂空',' ','http://www.bvca.edu.cn','“国家示范性高等职业院校”北京农业职业学院是经北京市政府批准、教育部备案的全日制普通高等职业院校，是北京地区唯一独立设置的从事高等农业职业教育的综合性院校，隶属北京市农村工作委员会领导。一、指导思想学院坚持以邓小平理论和“三个代表”重要思想为指导，以科学发展观统领各项工作，贯彻党的教育方针，遵循“开门、开发、开放”的“三开式”办学方针，秉承“立德修业、求知笃行”的校训精神，坚持“适度扩大、重在调整、强调创新、突出特色、确保质量”的发展方针，坚定高职教育理念，以涉农专业为特色，积极改革和创新人才培',252911);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京政法职业学院',' ','北京',0,11,0,0,0,' ','暂空',' ','http://www.bcpl.cn/','北京政法职业学院是经北京市人民政府批准、教育部备案的全日制高等职业院校，隶属于中共北京市委政法委员会领导，同时接受北京市教育委员会业务领导和指导。学院立足北京、服务政法、面向与法律相关的现代服务业，以应用法律教育为主，突出“立德明法、重能强技”人才培养特色，重点培养从事法律辅助性工作的高素质应用型法律人才和基层法律实务人才。学院是北京市高级人民法院书记员培训基地，为首批北京市示范性高职院校建设单位。学院占地面积37.2万平方米，校舍建筑总面积8.7万平方米，建筑总面积15万平方米的新校区正在规划',296990);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国劳动关系学院',' ','北京',1,11,0,0,0,' ','暂空',' ','http://www.ciir.edu.cn','中国劳动关系学院是中华全国总工会直属的唯一一所公办普通高等学校。学校的前身是创建于1949年的中华全国总工会干部学校，2003年经教育部批准为普通本科院校，并更名为中国劳动关系学院。学校具有60多年办学历史，是一所教学型、应用型、多科性大学，涵盖经济学、法学、文学、工学、管理学、艺术学等六大学科。学校设有工会学院、劳动关系系、经济管理系、法学院、公共管理系、安全工程系、文化传播学院、数学与计算机教学部、外语教学部、思想政治理论教学部、体育教学部、高等职业技术学院、工会干部培训学院和继续教育学院等',1872571);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('拉萨师范高等专科学校',' ','西藏',0,0,0,0,0,' ','暂空',' ','http://www.xzlssf.org','拉萨师范高等专科学校位于拉萨市娘热路43号，其前身为拉萨市文教局创办的“师训班”。1975年8月经西藏自治区人民政府批准，拉萨市师范学校正式成立，1978年迁至现址。2006年2月14日，经国家高校设置委员会批准，升格为师范高等专科学校。经过40多年的持续发展，学校办学规模不断扩大，办学水平稳步提高。目前，已初步形成师范教育为主、非师范教育并存发展的办学格局。学校现占地205亩，建筑面积43534.76平方米，固定资产7190万元。现有教职工202人，其中专任教师164人。教师中有正高职称1人、',50403);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('克拉玛依职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.kzjsxy.net','克拉玛依职业技术学院是一所以工科为主、工文兼备的国家普通高等职业院校，面向全国部分省区招生。学院创建于1956年，伴随着祖国的石油石化产业及西部大开发而不断发展壮大。现已培养四万余名服务于祖国石油石化产业及地方经济建设的高素质技能型人才。学院是新疆石油石化职教园区理事会理事长单位，2006年全国高职高专人才培养水平评估优秀，2010年成为国家示范性高职院校。学院环境优美，现代化的文化中心、图书馆、教学楼、文体及生活设施一应俱全。由美国NBBJ公司规划设计，克拉玛依市政府、新疆油田公司投资50多亿',111271);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津滨海职业学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjbpi.com/website/','天津滨海职业学院是由地方政府主办的一所全日制综合性公办高等院校。具有50多年的办学历史。2000年经天津市人民政府批准，国家教育部备案，转为高等职业学院。天津滨海职业学院坐落在天津滨海新区核心区。滨海新区位于天津东部沿海，面积2270平方公里。地处环渤海经济带和京津冀城市群的交汇点，是亚欧大陆桥最近的东部起点；拥有世界吞吐量第五的综合性港口和北方最大的航空货运机场；四通八达的立体交通和信息通讯网络，使滨海新区成为连接国内外、联系南北方、沟通东西部的重要枢纽。120多家世界500强企业已在此投资建',405638);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津工程职业技术学院',' ','天津',0,1,0,0,0,' ','暂空',' ','http://www.tjeti.com','天津工程职业技术学院坐落于天津市滨海新区，是一所以工科为主的全日制综合性高职学院，前身为天津大学石油分校。学院占地779亩，建筑面积20多万平方米，有在校生8000余人。办学理念学院坚持“立足行业，面向社会，贴近经济，服务企业”的办学方向；坚持“以服务为宗旨、以就业为导向、走产学研相结合的道路，努力办好让人民满意的职业教育”的办学思路，坚持“立足行业与面向社会并举，学历教育与职业培训并举，职前教育与职后教育并举”的办学理念，努力打造以能力为基础、借鉴CBE理论和DACUM方法、深化教学改革的教学',172202);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江艺术职业学院',' ','黑龙江',0,8,0,0,0,' ','暂空',' ','http://www.hljyzy.com','黑龙江艺术职业学院是黑龙江省唯一的一所国办全日制综合性艺术职业学院，是教育部评定的国家级重点艺术院校。学院创建于1959年，其前身是黑龙江省戏曲学校，1972年更名为黑龙江省艺术学校。2002年经黑龙江省人民政府批准，与黑龙江省文艺干部学校合并成立黑龙江艺术职业学院。学院办学目标明确，办学特色鲜明，以高等艺术职业教育为主，以中等艺术职业教育及成人艺术教育培训为辅，面向全国招生。学院现有二个校区，主校区位于哈尔滨市南岗区和兴路149号，分校区位于南岗区中兴街21号，总占地面积160亩。现有大中专学',50699);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海行健职业学院',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.shxj.cn/','上海行健职业学院是经上海市人民政府批准，国家教育部备案，由政府主办的全日制高等职业学院。2001年，学院以新的理念、新的机制、新的教学设施、新的面貌展现在上海高职舞台。学院以高等职业教育为主体，同时附设成人高等教育中心、职业技能培训中心、社区教育中心、上海电视大学闸北分校。经过十年的不懈努力，学院高等职业教育现拥有学前教育、信息技术与机电工程、经济管理、商务外语系、应用艺术系五个系二十余个专业，目前各类在校生近12000余人，其中全日制高职学生5000余人。除上海生源外，还面向浙江、福建、江西、',96451);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海城建职业学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.shumc.edu.cn/','中共上海市建设和交通工作委员会党校成立于1985年，主要承担上海市建设交通系统副局级、处级领导干部及中青年后备干部轮训、培训等任务，开设近20种类型班次，年培训近2600人次。上海城市管理职业技术学院是上海市人民政府批准、教育部备案的全日制普通高校，隶属于上海市建设和交通委员会。其前身上海市建设职工大学是由1956年创办的原上海市业余土木建筑学院和建设系统八所职工大学于1994年优化组合而成。另设有电大建设分校和附属中专。学院主要承担普通高职教育、普通中职教育、成人高等学历教育、继续教育和岗位培',217296);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海交通职业技术学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.scp.edu.cn/','上海交通职业技术学院是上海市人民政府批准、教育部备案的全日制普通高等院校，由上海民航、铁路、港口、交运等四所行业院校合并而成，分东、南、西、北四个校区，本部设在北校区上海市呼兰路883号。学院是上海从事高等职业教育历史最长的院校之一,以强大的陆海空综合交通为依托，立足市场、背靠行业，引入国际化、现代化、信息化高等职业教育办学模式，设有汽车工程、港口机械、航空运输、轨道交通、物流管理、经济管理和信息技术等系，开设了近20个具有特色的综合交通类专业，其中《集装箱运输管理》》和《汽车运用技术》被教育部',166711);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海海事职业技术学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.sma.edu.cn','上海海事职业技术学院是一所经上海市政府批准、国家教育部认可，具有独立事业单位法人资格的专门培养高等海事技术技能型人才的普通高校，隶属于中国海运（集团）总公司,教育业务直接由上海市教委领导。学院具有50年的职业教育办学历史，于1986年率先试办航海类高职教育，从事高等职业教育20余年,是上海市从事高等职业技术教育历史最长的院校之一。1998年首批通过国家海事局船员教育和培训质量体系认证，并报国际海事组织。学院现已发展成为集学历教育、成人教育、船员培训和干部培训等功能于一体的高职院校。学院地处上海市',232939);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海电子信息职业技术学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.stiei.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校上海电子信息职业技术学院是一所公办的综合类的全日制高等职业技术学院，已有四十余年高等专科层次的办学历史。主要培养电子信息产业的技术应用和经营管理的专门人才，是上海市示范性高等职业技术院校建设单位。学院已连续四年被评为“上海市文明单位”。上海电子信息职业技术学院地处奉贤区的中心镇--奉城镇，奉城镇是上海市政府重点规划建设的“一城九镇”之一。学院占地435亩，其中教学用地350余亩，生活园区80余亩，建筑面积13万平方米，建有教学中心、技术中心、图文信息',158154);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西能源职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.sxny.cn/','陕西能源职业技术学院是一所实施高等职业技术教育的综合性国立全日制普通高等学校。位于咸阳市文林路，辖设临潼医学校区和西安培训中心。占地总面积333.08亩，总建筑面积188677平方米，资产总额达2.5亿元，图书馆藏书50万册，校园网、计算机中心、多媒体教室、信息技术中心以及标准化运动场地等机构及设施齐备。学院现设有理、工、医、护等专业32个,形成了以高职教育与继续教育并举、学历教育与非学历培训并存，课程教育与远程教育共济的多层次的办学格局。现有专兼职教师675人，其中校内教师340人，企业兼职教',233300);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃建筑职业技术学院',' ','甘肃',0,1,0,0,0,' ','暂空',' ','http://zj.gcvtc.edu.cn/','甘肃建筑职业技术学院是甘肃省内唯一一所建筑类高等职业院校。学院始建于1958年，由甘肃省建筑工程学校、甘肃省建筑职工工程学院、甘肃省建筑职工中等专业学校三所学院于2001年经甘肃省政府批准合并组建为甘肃建筑职业技术学院，2004年底整建制划转省建设厅管理，成为全民所有制事业学校，2008年底顺利通过教育部人才培养工作水平评估，2009年移交省教育厅管理，现为“省级示范性高职院校建设单位”。学院以全日制高等职业教育为主，兼有成人高等教育、岗位培训、职业技能培训等办学形式，全日制在校生6570人，继',435424);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆机电职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','oldname": "新疆机电职业技术学院','新疆机电职业技术学院，建校于1957年，是新疆维吾尔自治区人民政府批准、国家教育部备案独立设置的普通大专院校。学院有近半个世纪的办学历史，六十、八十、九十年代三次被教育部评为国家级重点学校。作为新疆制造业领域唯一的高职院校，我院为新疆制造业和其他领域培养和输送了大量的专业技术人才。新疆机电职业技术学院是经新疆维吾尔自治区人民政府批准成立的一所以工科为主,多学科、多层次、公办、全日制普通高等职业技术学院。2003年起在全国25个省市参加普通高考招生。在校生达6000余人。2005年起在西北五省及河',116263);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆轻工职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.xjqg.edu.cn/ddzs','学院是自治区首批成立的公办普通高等职业院校，位于乌鲁木齐市，占地2300亩，建筑面积37万平方米，总资产5.6亿元。现有教职工666人，专任教师365人。其中，博士、硕士研究生学历160人，教授、副教授专业技术职务教师129人。国家及自治区教学名师9人，自治区级教学能手3人，自治区级教学团队6个。面向食品、轻纺、石油、煤化工、机电及机械制造等行业开设专业58个，其中，国家重点专业7个，自治区特色专业6个。建有132个实验实训室和23个实训车间。建有中央财政、自治区财政投资的实训基地13个。目前，',352651);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津青年职业学院',' ','天津',0,5,0,0,0,' ','暂空',' ','http://www.tjqnzyxy.cn','天津青年职业学院有近60年的办学历史，是经天津市政府批准，国家教育部备案设立的公办全日制高等职业技术学院，属国家普通高等院校系列。学院依托培养优秀青年干部的学府天津市团校而建立。近60年来，学院培养了数万名优秀青年干部，其中很多同志已经走上重要领导岗位。1993年，天津团校与烹饪专科学院合并设立成人高校天津青年职业技术职工学院；1999年，为天津市首批试办高职教育院校；2001年3月经天津市政府批准，国家教育部备案，成为天津市首批转制的高等职业学院。2011年，学院被市委、市政府确定为天津海河教',184925);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('酒泉职业技术学院',' ','甘肃',0,1,0,0,0,' ','暂空',' ','http://www.jqzy.com/','“国家示范性高等职业院校建设计划”骨干高职院校酒泉，是我国古代飞天艺术的故乡，新中国石油工业的摇篮，现代航天工业的诞生地和核工业的发源地，也是全国乃至全球最主要的制种基地之一和未来的“风电之都”。——酒泉职业技术学院的诞生与发展，就是其古代文明与现代文明共同催孕的结果。学院于2001年经甘肃省人民政府批准、国家教育部备案，在酒泉教育学院和酒泉工业学校基础上成立，是甘肃省成立最早的地方性高职院校，2003初，相继整合了酒泉农林科技学校、酒泉市财经学校等优质教育资源，现为国家星火计划农民科技培训学校',154995);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('保定职业技术学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.bvtc.com.cn/','保定职业技术学院是一所国办综合类普通高等职业院校，座落在历史文化名城河北省保定市，位于保定市中心地带，地理位置优越。学院前身始建于1935年，具有81年悠久的办学历史。现有全日制在校生9900余人。学院办学条件优越，校园占地面积420亩，校舍建筑面积18.5万平方米，固定资产总值3.5亿元，教学科研仪器设备总值5387.23万元，各类图书资料104.73万册，拥有设备先进、功能完备的校园计算机网，教学用计算机2849台，有合计4920个座位的现代化多媒体教室。学院师资力量雄厚，师资结构合理。现有',429362);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏理工学院',' ','宁夏',1,1,0,0,0,' ','暂空',' ','http://www.nxist.com/','宁夏理工学院位于宁夏回族自治区山水园林新型工业化城市——石嘴山市，校园西畔新月海，东邻生态园，南滨黄河故道，北望巍巍贺兰。学院占地面积1800余亩（水域面积3000余亩），设计建筑总面积35万平方米。宁夏理工学院是教育部批准的本科民办高校，经过20多年的发展，已成为一所以工为主，理、经、管等多学科协调发展的普通高等学校。多年来，学院不断深化教学改革，强化教学管理，以培养应用型、技能型、创新型本、专科人才为目标。现已形成以本科生为主，专科生、成人教育和中、高级技术人员、管理人员、职教师资培训等多层',283921);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南农业职业技术学院',' ','云南',0,2,0,0,0,' ','暂空',' ','http://www.ynavc.com/','云南农业职业技术学院是经云南省人民政府批准，具有高等学历教育资格的公办全日制普通高等院校，面向全国招生。学院办学历史悠久，始建于清光绪三十年(公元l904年)的云南蚕桑学堂，首开云南职业教育之先河，现主要以畜牧兽医、农学与园林类专业为主，计算机应用、经济管理类专业兼备，培养高等技术应用型专门人才。学院分小哨校区和茭菱校区，拥有良好的办学条件，具有配置优化、管理科学、运行高效的教学科研保障体系。学院占地面积1078亩。建有多媒体教室、语音室、电教室、电子阅览室和备课中心等现代化的教育教学设施，有近',54319);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南司法警官职业学院',' ','云南',0,11,0,0,0,' ','暂空',' ','http://www.yncpu.net/','一、办学条件学院地处昆明市官渡区大板桥镇，占地347亩，毗邻规划建设中的新昆明国际机场。学院总建筑面积（含一分校）44994平方米，生均占地面积107平方米，教学行政用房面积19647平方米，教学及科研仪器设备总值790万元。学院图书馆2147平方米，藏书32.14万册，其中电子图书19万册。学院拥有现代化的体育运动场、警体射击训练场，驾驶训练场，建有涵盖4个类别13个现代化专业实验室的实训中心，在全省建立了20个教学科研实习基地，满足了教学和科研工作的需求。二、师资队伍学院现有教职工200人，',128305);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南文化艺术职业学院',' ','云南',0,8,0,0,0,' ','暂空',' ','http://www.ynarts.cn/','云南省艺术学校成立于1956年，是云南省第一所培养艺术类专门人才的公办中等专业学校。2004年5月，经云南省人民政府批准，在云南省艺术学校基础上成立普通高等全日制职业院校——云南文化艺术职业学院，将学校办学层次从中专提升到大专，集大、中专办学一体化。学院现为全国仅有的18所省级公办艺术类高职院校之一，是经文化部批准的文化行业专业人才定点培训机构和中国演出家协会演员资格评定单位，也是中国艺术职业教育学会常务理事单位和云南省民族事务委员会批准、授牌的云南省少数民族艺术人才培训中心，同时是中国音乐学院',41139);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南体育运动职业技术学院',' ','云南',0,7,0,0,0,' ','暂空',' ','http://www.ynasc.com/','云南体育运动职业技术学院2004年5月26日经云南省人民政府批准（云政复[2004]58号），国家教育部备案，由原云南省体育运动学校升格而成，是一所培养适应云南省体育事业发展需要，德、智、体、美全面发展的高级应用型人才的公办全日制普通高等职业技术学院（专科层次）。学院地处滇池之滨国家旅游度假区，与云南民族村、昆明海埂体育训练基地、云南大学滇池学院、昆明学院南校区、滇池湖畔高尔夫球场相邻，占地面积256亩，并拟新征学院发展用地100余亩，校园开阔，绿树成荫、花草覆地，学习训练环境优雅，目前有在校生',30618);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京财贸职业学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.bjczy.edu.cn/','北京财贸职业学院始建于1958年，是全国“百所国家示范性高等职业院校”之一，被首都服务业誉为“商界黄埔军校”和“经理的摇篮”，学院培养的学生得到用人单位普遍好评，高职毕业生就业率多年稳定在99%以上。学院占地面积432.3亩，校园环境优美，是花园式校园，在校生近八千人，学院新建了1.7万平方米的校内实训基地——财贸实训大楼，为创新财经类专业教学实训创造良好条件。学院设有金融系、工商管理系、立信会计学院、信息物流系、旅游系、广告艺术学院、国际教育学院、基础教学部、素养教学部9个教学系部（院），开设',555151);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青海卫生职业技术学院',' ','青海',0,3,0,0,0,' ','暂空',' ','http://www.qhwszy.edu.cn/','。',75901);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京经贸职业学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.csuedu.com/','北京经贸职业学院，是经北京市人民政府批准、教育部备案，具有独立颁发高等教育学历证书资格的普通高校，参加全国计划内普通高校统一招生，国家承认学历。北京经贸职业学院前身是中华社会大学，成立于1982年，是由聂真、刘达、范若愚、张友渔等老教育家发起创办，经北京市政府工农教育办公室批准成立的我国改革开放后第一所民办大学。学院位于北京市良乡高教园区，校园周边学术气氛浓厚，交通方便，环境优雅，目前学院占地面积200余亩，已完成教学和生活用房第一期工程建筑面积6万余平方米，教学和生活设施比较齐全。学院以高等职',274888);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京经济技术职业学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.bibt.edu.cn/index.asp','北京经济技术职业学院是2003年3月经北京市人民政府批准、国家教育部备案，参加国家计划内统一招生，具有独立颁发国家承认的大专学历文凭资格的全日制民办普通高等学校。学院设有6个系（院）部。目前开设金融（商业银行方向）、金融与证券、商务英语（学前英语教育方向）、市场营销、国际经济与贸易、物流管理、旅游英语、酒店管理、计算机应用技术、会计、新闻采编与制作、主持与播音等20多个高职专业（或专业方向）。学院地处国家级经济开发区--京东燕郊开发区，临近通州、顺义、平谷等区。校区总占地面积336亩，建筑面积近',265578);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京戏曲艺术职业学院',' ','北京',0,8,0,0,0,' ','暂空',' ','http://www.bjxx.com.cn','北京戏曲艺术职业学院是经北京市人民政府批准、教育部备案的全日制普通高等学校，是北京市唯一一所公办艺术类高等职业学院。学院的前身是创建于1952年的北京市私立艺培戏曲学校，1953年由北京市政府接管，更名为北京市戏曲学校。著名表演艺术家郝寿臣、马连良、孙毓敏等曾任学校校长。2002年12月，学院成立，正式举办全日制高等职业教育，2006年北京市艺术研究所与学院合并，充实了学院的教学、科研力量。60年来，几代艺术家辛勤耕耘，为学院艺术教育事业的发展打下了良好的基础。学校从单一的戏曲专业发展到京剧、地',127306);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京汇佳职业学院',' ','北京',0,5,0,0,0,' ','暂空',' ','http://www.hju.net.cn','北京汇佳职业学院是经教育部备案，北京市人民政府批准，北京市教育委员会主管的，具有高等学历教育计划内全国统一招生资格的，全日制民办普通高等职业院校。学院坐落在北京中关村科技园区昌平园，北依龙凤山，南邻京密引水渠，西接八达岭高速，依山傍水，风景秀美，交通便捷。学院占地367亩，建筑面积8.36万平方米，校园建筑按照国际现代教育理念设计，外观典雅，颇具特色。设有教育系、文化创意系、娱乐经济与管理系、基础部等4个系部，开设“影视动画、学前教育、体育服务与管理”三大特色专业群为主的学前教育、城市轨道交通控',218501);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京现代职业技术学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.moderncollege.com.cn','北京现代职业技术学院成立于2003年8月，是一所由顺义区政府举办，北京市教委主管的公办全日制普通高等职业院校。学院位于顺义城区东南侧，毗邻著名的潮白河畔。占地面积169.47亩，建筑面积72760平方米，固定资产总值1亿余元，教学仪器设备总值2600余万元。图书馆建筑面积6635.8平方米，藏书14万册；多媒体教室80个；拥有汽车实训中心、制造实训中心、管理实训中心和基础实训中心，7个专业计算机房，校内实验实训室22个，校外实训基地28个，校企合作单位100余家；有标准塑胶田径运动场、人工草坪足',168892);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆能源职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.xjnyedu.com','新疆能源职业技术学院是经自治区人民政府批准国家教育部备案的以\"能源专业\"为特色的全日制普通高等院校。学院位于乌鲁木齐市乌奇公路56号神华集团新疆能源公司旁，学制：三年，毕业后颁发国家承认教育部统一印制的大专毕业证书。新疆现代职业技术学院主管单位是自治区教育厅；行业支持和就业指导单位是自治区煤炭工业管理局、自治区煤炭安全监察局，毕业安置单位是国有大型企业神华集团新疆能源公司、徐矿集团新疆天山矿业等。新疆现代职业技术学院占地面积200亩，建筑面积37713平方米，学院的基础设施配套完整。教学楼、办公',45203);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东科学技术职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gdit.edu.cn','“国家示范性高等职业院校建设计划”骨干高职院校广东科学技术职业学院（又名广东省科技干部学院）是广东省政府批准设立的普通高等学校，正厅级建制。学院成立于1985年，2003年4月经省政府批准，国家教育部备案，改制为普通高校。经过20余年的发展，学院已形成了大专学历教育、继续教育与科技培训、科技开发共同发展的办学格局。学院设广州和珠海两个校区，校园面积2000余亩，现有全日制在校生16000余人，教职工1000余人，其中专任教师700多人，具有正高职称的30人，副高职称的156人。学院教学仪器设备总',526514);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东食品药品职业学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdyzy.edu.cn','广东食品药品职业学院是于2007年5月经教育部备案，广东省人民政府批准，在原广东化工制药职业技术学院的基础上更名的一所以工科为主，工理经管文结合的公办全日制普通高等职业院校。其前身是1965年6月16日创办的广东省中药材学校，校址在广州市塞坝口；1974年3月迁址至浆栏路并更名为广东省商业技校第一分校；1979年9月改为广东省医药技工学校；1983年成立广东省医药职工中专学校，与广东省医药技工学校一起办学；1984年迁址至广州市天河区龙洞，1986年3月成立广东省医药学校，2003年5月升格为广',233151);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东东软学院',' ','广东',1,1,0,0,0,' ','暂空',' ','http://www.nuit.edu.cn','广东东软学院(以下简称东软学院)是经教育部批准设立的全日制普通高等本科学校，成立于2002年。是广东省首批省级示范性软件学院、广东省首批依法治校示范校、国家技能型紧缺人才培养基地。2014年经教育部批准升格为普通本科高校。学院由东软控股、亿达集团共同投资举办，是一所以工学为主，管理学、人文学等学科专业相互支撑、协调发展的全日制普通本科高等院校。学院设有计算机科学与技术系、信息技术与商务管理系、数字艺术系、英语系、日语系和国际合作部等5系3部，共开设15个本科专业和20多个泛IT专科专业。在办学层',189649);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州康大职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.kdvtc-edu.cn/','广州康大职业技术学院由广州康大科技产业有限公司投资兴建，经广东省教育厅、省政府正式批准成立的一所具有独立颁发学历证书资格的普通民办高校。学院座落在广州市广汕公路旁著名生态风景区增城市镇龙，距广州市30多公里，校址建在华南师范大学与广州康大科技产业有限公司联合创办的康大教育园内，园内有华南师范大学增城学院、华南师范大学软件职业技术学院、华南师范大学附属康大学校、华南师范大学与澳大利亚南昆士兰大学联合创办的华澳国际会计学院、华南师范大学与美国因卡奈特大学联合创办的华圣教育中心等多所本、专科层次的高等',212698);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('珠海艺术职业学院',' ','广东',0,8,0,0,0,' ','暂空',' ','http://www.zhac.net/','珠海艺术职业学院是经广东省人民政府批准，国家教育部备案，纳入国家统招计划，颁发电子注册学历文凭资格的普通高等艺术院校。学院坐落于依山傍海的珠海市金湾区大学园区，毗邻香港、澳门，地理位置优越，环境优美，交通便利。学院师资队伍结构合理，聚集了一批有丰富教学经验的教授、音乐教育家、舞蹈教育家、艺术设计师等。学院注重培养学生既要有扎实的理论基础，又要有较强的艺术实践经验。学院目前有五个系（舞蹈系、音乐系、艺术设计系、文管系、旅游系），十三个专业方向（舞蹈表演与教育、舞蹈编导、音乐表演与教育、电子音乐制作',140094);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东行政职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gdxzzy.cn/','广东行政职业学院是由广东省行政管理干部学院和南方成人经贸学院合并改制、经广东省人民政府批准、国家教育部备案的全日制国民教育普通高校（办学性质：公办普通高校）。广东省行政管理干部学院成立于1985年，1990年更名为广东行政学院，学院主要承担国民学历教育和国家公务员培训任务。先后为社会输送了11500多名大专毕业生，培训32000多名国家公务员，省市机关、珠三角地区、港澳地区各类行政管理人员30万人次，对全省行政人员和专业技术人员进行计算机培训和考核达40余万人次。学院还与英国、法国、美国、新加坡',174450);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东体育职业技术学院',' ','广东',0,7,0,0,0,' ','暂空',' ','http://www.gdvcp.net','广东体育职业技术学院前身是广东省体育运动学校。广东省体育运动学校创建于1956年，是国家级重点体育中等职业技术学校、国家高水平体育后备人才基地，也是广东省优秀运动队的重要后备人才基地。建校以来至2005年底共向国家、省、八一等优秀运动队输送了1210名优秀体育后备人才，培养出27个（73人次）世界冠军，132个（389人次）全国冠军，并为社会培养了一大批合格的中小学体育师资。历年来，我校涌现了江嘉良、冼东妹、陈小敏、孙彩云、董兆致、刑芬、余壮辉、吉泽标、廖文芬、符晓云、刘秋荣等一大批为国家作出突',70742);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南工业和信息化职业学院',' ','河南',0,12,0,0,0,' ','暂空',' ','http://www.hciit.edu.cn/','河南工业和信息化职业学院是经河南省人民政府批准、国家教育部备案，由河南省工业和信息化委员会主管、省教育厅负责教学管理工作的一所公办全日制专科层次普通高等院校。学院坐落在美丽的太行山南麓、豫西北新兴的中国优秀山水旅游名城——焦作市。这里山川锦绣、人杰地灵、环境优雅、交通便利，与郑州隔河相望，郑焦城际铁路半小时即可到达。学院人文荟萃，师资雄厚，是莘莘学子成才深造的理想场所。学院创建于1975年，历经河南省煤矿学校、焦作煤炭工业学校、河南工程技术学校、河南理工大学高等职业学院等时期。2013年3月经河',19582);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南水利与环境职业学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zzslxx.com','河南水利与环境职业学院，隶属于河南省水利厅，是经河南省人民政府批准，教育部备案的具有普通高等学历教育招生资格的全日制公办普通高等职业院校，前身为河南省郑州水利学校，创建于1955年，2002年与华北水利水电学院创办水利职业学院，2013年是经河南省人民政府批准，教育部备案独立升格为河南水利与环境职业学院。学院国标代码：12582，河南省招生院校代码：6192',56634);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海震旦职业学院',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.aurora-college.cn/','上海震旦职业学院是由上海市人民政府批准设立的国家计划内的全日制普通高校,学制三年,大专学历。1903年我国著名爱国主义教育家马相伯先生创办了震旦大学，即2005年并入上海交通大学的上海第二医科大学的前身。1984年张惠莉女士等忠诚党的教育事业的有识之士弘扬震旦精神，创办新时代的震旦教育集团,并在其后成立上海震旦职业学院。学院于2004年、2006年连续两届被评为上海市教育委员会平安单位、文明单位。学院位于上海市宝山区罗店镇市一路88号，占地200亩，设有8系1部，共21个专业，以应用型文科、保健',126307);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海民远职业技术学院',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.min-yuan.com','上海民远职业技术学院是纳入国家正式招生计划的全日制普通高等院校。学制三年，独立颁发国家承认的大专文凭。上海民远学院位于浦东新区，紧邻张江高科技园区。校园环境优美，小桥、凉亭、湖水相映成趣。多媒体教室、电子阅览室等现代化教学设施一应俱全。新建富有现代化气息的教学楼、图文信息中心、学生宿舍楼、实训中心等将于新学年迎接新同学的到来，将为培养新世纪各类专业人才提供更加美好的教学和生活条件。学院着力培养“勤奋、敬业、合作、创新”校风。学院管理严格、教学规范、教学质量有保证。学院在教学上，突出高职教育特色，',46223);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海思博职业技术学院',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.shsipo.com','上海思博职业技术学院是2003年经上海市人民政府批准建立的独立设置的全日制高等职业技术学院。学校占地500亩，已完成的建筑物11万平方米，在建工程3.5万平方米，2012年8月被市教委批准为上海市特色（示范）院校建设单位。学校坚持“双主体办学，全方位育人”的育人模式、“相信人人有才，帮助人人成才”的育人理念、“素能一体、德技并重”的培养方针以及“首岗能胜任、转岗能适应、升岗有潜力”的培养目标，形成了较为先进的办学思想和教育理念。学校根据区域经济转型升级的人才需求，致力于培养发展型、复合型技术技能',153360);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海立达职业技术学院',' ','上海',0,6,0,0,0,' ','暂空',' ','http://newweb.lidapoly.edu.cn/','学校简介上海立达职业技术学院是经上海市人民政府批准成立的全日制民办普通高校，纳入国家统一招生计划，具有独立颁发国家大专学历文凭资格。学校地处上海市松江区，建校总投资3亿元，占地400亩，校舍总面积13万平方米，在校生规模5000余名，近年来已面向全国24个省市进行国家普通高校计划内统一招生，建校十二年来已向社会输送毕业生1.3万余名。整个校园在欧式建筑风格和典雅园林景致的交相辉映下营造出宁静、舒适的学习和生活氛围。学校拥有高标准的计算机房、语音室、多媒体教室、图文信息中心、学生活动中心、实训大楼',190073);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海工艺美术职业学院',' ','上海',0,8,0,0,0,' ','暂空',' ','http://www.shgymy.com','上海工艺美院是经上海市人民政府和国家教育部批准的独立设置的艺术设计类特色高等职业院校，隶属于上海国盛集团有限公司。学院已有50余年办学历史，逐渐形成了“继承传统、创造当代、拓展未来”的发展思路，积累了较强的综合办学实力。学院抓住上海建设“设计之都”和创意产业发展的契机，建立了“上海市数码艺术公共实训基地”、“上海市工艺美术公共实训基地”、“中国黄金创意产业中心”、“现代环境设计中心”、“景观设计与教育中心”等，拥有“东方学者团队”、国际设计师、工艺美术大师、民间特艺大师，逐步形成以生产型实训工场',232359);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳航空职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.syhzy.cn/','沈阳航空职业技术学院是经辽宁省政府批准，由教育部备案的省属全日制高等职业技术学院（设有沈飞校区和黎明校区两个校区，两校区分别独立管理）。沈阳航空职业技术学院为公办院校，所有招生计划均为国家统招计划，面向全国招生。学生毕业后颁发沈阳航空职业技术学院普通高等职业教育专科毕业证书，并由辽宁省高等学校毕业生就业指导局办理派遣手续。学院位于东北重工业装备中心城市沈阳市皇姑区北陵公园北侧，毗邻具有中国“歼击机摇篮”之称的沈阳飞机工业（集团）有限公司，创建于1953年11月,隶属于中国航空工业集团公司下属的沈',77154);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁体育运动职业技术学院',' ','辽宁',0,7,0,0,0,' ','暂空',' ','oldname": "辽宁体育运动职业技术学院','辽宁省体育运动技术学院于1985年经省政府批准成立，1999年经省教育厅批准培养全日制普通高等职业专科学生（学院代码12591），是省内一所培养和造就优秀运动员及高水平体育专业人才的体育院校，学院设有社会体育和运动训练两个专业。经过多年的教学、训练和科研实践，各个方面已经具有相当规模，并取得了可喜的成绩。学院设有球类、柔道、摔跤、游泳、举重、羽毛球、乒乓球、网球等9个运动项目24支运动队，先后培养出王军霞、孙福明、丁美媛、王楠等世界著名运动员，共培养出14位奥运会冠军；56人151次获得世界冠军',42992);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁职业学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://www.lnvc.cn/','辽宁职业学院位于中国辽宁省北部文化名城铁岭市，坐落在龙山脚下、柴河之滨。是一所省属全日制普通高等学校，是国家教育部、财政部重点投资建设的国家骨干高等职业院校。始建于1956年。58年的办学历史，使学院积累了丰富的职业教育经验，取得了令人鼓舞的办学成就，成为一所校园环境优美、办学条件优越、专业特色鲜明、文化底蕴深厚的现代高职学院。坚实的办学基础学院占地3678亩，建筑面积14万平方米，实训室65个，实训基地28个，图书馆藏书33万册，固定资产总值4.47亿元，在校生7000人。现有专任教师365人',151316);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁林业职业技术学院',' ','辽宁',0,2,0,0,0,' ','暂空',' ','http://www.lnlzy.cn/','辽宁林业职业技术学院是一所公办性质的全日制普通高等学校，前身为辽宁省林业学校，始建于1951年。2003年经辽宁省人民政府批准，成为独立设置的高等职业技术院校。学校地处沈阳市苏家屯区,占地面积48万平方米。学校是辽宁省示范性高等职业院校，国家首批百所现代学徒制试点单位，先后被国家教育部评为“全国毕业生就业典型经验50强高校”被省教育厅评为“辽宁省高校创业教育示范校”。作为中国(北方)现代林业职教集团理事长单位、全国文化育人与生态文明建设工作委员会主任单位，学校办学基础雄厚，总资产达8亿元。拥有国',111323);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳职业技术学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://www.vtcsy.com','“国家示范性高等职业院校”沈阳职业技术学院是一所经辽宁省人民政府批准的综合性普通高等职业技术学院。学院位于辽宁省省会、辽宁中部城市群中心——沈阳。沈阳庞大的装备制造企业群是学院进行高职教育强有力的依托。学院是教育部确定的国家高职高专“数控技术”高技能紧缺型人才培养基地，“计算机应用与软件技术”高技能紧缺型人才培养基地，国家“数控技术”类学生实训（师资培训）基地，辽宁省职业院校师资、院校长培训基地和沈阳市装备制造业技能培训基地。学院绿树成荫，环境优美，占地面积779亩，建筑面积近21万平方米，图书',201462);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁理工职业学院',' ','辽宁',0,6,0,0,0,' ','暂空',' ','http://www.lndhdx.com','锦州商务职业学院是2003年4月省政府批准教育部备案的省属民办普通高等学校。学院隶属于省教育厅，招生列入全国统一招生计划，学院的办学规模(十一五期间)是在校生3000名。学院颁发毕业证书国家承认学历。学院新校址位于锦州经济技术开发区西海路东段2号，依山傍海、风景秀美，南望风景旖旎的笔架山，东临碧波千帆的渤海湾，东南角更是亚洲最大的天然海滨浴场；校园距海边仅5分钟行程，从锦州火车站乘虎跃快客沿渤海大道30分钟即可到达；校园占地200亩投资6000万元完成建筑面积5.2万平方米教学生活建筑群；教学设',84925);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('保险职业学院',' ','湖南',0,6,0,0,0,' ','暂空',' ','http://www.bxxy.com/','保险职业学院是经国家教育部和湖南省人民政府批准，由中国保险管理干部学院(成立于1986年)改制成的全国唯一一所从事保险教育的全日制公办普通高等学校，由中国人寿保险(集团)公司和中国再保险(集团)公司主管，教育业务由湖南省教育厅管理。学院地处长沙市天心区，紧邻湖南省政府新址.我院保险专业图书藏量为全国各高校之最。教学、生活设施配套齐全，校园环境幽雅，是长沙市“花园式单位”和“省级园林式单位”。学院师资力量雄厚，现有正教授11名，副教授70多名，专任教师中，有研究生学历的教师占72.4％。建院以来学',87190);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南外贸职业学院',' ','湖南',0,6,0,0,0,' ','暂空',' ','http://www.hnwmxy.com','湖南对外经济贸易职业学院是2003年7月由湖南省对外经济贸易学校和湖南省经济贸易学校合并升格而成，办学历史可追溯到1954年成立的湖南省粮食学校。学院是由湖南省商务厅主办，集学历教育、职业培训为一体的全日制高等职业学院。我院现有南校区、北校区和新校区三部分。南校区位于长沙市莫家坳，北校区位于长沙市火车南站望岳村。南北两个老校区总占地面积258亩。校舍总建筑面积11万平方米，其中教学行政用房面积4.95万平方米。固定资产7629.9多万元，教学仪器设备总价值1599.80万元。新校区位于望城县丁字',115513);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南网络工程职业学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.hnevc.com/','湖南网络工程职业学院于2003年7月经湖南省人民政府批准成立，由湖南省教育厅主办主管，依托湖南广播电视大学教育教学资源办学的全日制普通高等学校。学院位于长沙市南北主干道芙蓉南路，毗邻中南大学铁道学院校区，中南林业科技大学，学风浓厚，交通便利，环境优美。学院是教育部“全国高职高专院校人才培养工作水平评估”优秀学校。学院坚持以服务为宗旨，以就业为导向，以能力为本位，以开放为特色，致力于培养高素质、高技能、应用型技术人才。学院遵循高职教育发展的规律，借鉴国内外高职教育先进经验，依托广播电视大学丰富的教',51527);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湘潭大学兴湘学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://xxxy.xtu.edu.cn/','湘潭大学兴湘学院成立于2001年8月，是经教育部和湖南省人民政府批准设立，由湘潭大学举办的一所独立学院，是湖南省首家由综合性全国重点大学举办的独立学院，坐落在毛泽东同志的故乡——湖南省湘潭市。学院在中国科学研究评价中心发布的《2007中国独立学院排行榜》中名列全国第35位，居湖南省独立学院首位。学院依托湘潭大学的综合办学优势，共享湘潭大学一流的师资队伍、图书资料、教学设施和大学文化。学院根据科技发展趋势和社会发展需求确定学科建设和人才培养模式，以就业为导向，重点发展应用型专业。目前所设置的32个',323599);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('邵阳职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.syzycn.cn','邵阳职业技术学院是经湖南省人民政府批准，国家教育部备案，国家公办的全日制普通高等学校。学院坐落在邵阳市城南梅子井，交通便利，环境怡人。校园占地面积680余亩，建筑面积21万平方米。全日制在校学生5000余人。学院设有机电工程系、财经贸易系、工商管理系、信息与传媒艺术系、生物工程系等5个教学系和继续教育部、中专部，开设24个大专专业和26个中专专业。现有教职工359人，专任教师239人，其中：正副教授87人，博士和硕士研究生30人，双师型教师52人。经省教育厅批准，2009年我院除面向全国17个省',42256);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南司法警官职业学院',' ','湖南',0,11,0,0,0,' ','暂空',' ','http://www.hnsfjy.cn','湖南司法警官职业学院是经国家教育部备案的公办全日制普通高等学校，是湖南省唯一一所警察体制的职业院校，教育部高职院校人才培养工作水平评估“优秀”学校。面向全国招生，学生学籍由教育部电子注册，国家承认学历。学院位于长沙市芙蓉区远大二路1069号，依偎在风景秀丽的浏阳河畔，校园环境优美，处于黄花国际机场、长沙火车站、汽车东站等区域的中心位置，交通便利。学院在办学定位上，坚持以服务为宗旨，以就业为导向，走警、学、研结合发展道路；坚持学历教育与职业培训并举，立足行业谋发展、立足市场设专业、立足警字凝特色、',133868);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙商贸旅游职业技术学院',' ','湖南',0,6,0,0,0,' ','暂空',' ','http://www.hncpu.com','长沙商贸旅游职业技术学院是经湖南省人民政府批准成立、国家教育部备案，由长沙市人民政府主办、教育业务由省教育厅主管的一所全日制公办普通高等学校。现有南北两院，分别位于交通便利的长沙市韶山南路578号和劳动西路143号。学院以人为本，培养商贸旅游类应用性高等职业技术人才，目前在校学生达4000余人。学院办学成绩斐然，连续多年被授予省、市“文明单位”、省、市“花园式单位”、“长沙市职业学校招生工作先进单位”、“湖南省毕业生就业工作先进单位”称号，2003年被教育部授予“全国职业技术学校职业指导工作先进',143811);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南工业大学科技学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnut-d.com/','湖南工业大学科技学院系湖南工业大学按新体制、新模式创办的独立学院。湖南工业大学是一所以工科为主，工、理、管、文、经、法等多学科协调发展的多科性大学。学校是我国第一个被国际包装协会（IAPRI）接纳的会员单位，是教育部全国普通高校包装工程专业教学指导分委员会、中国包装联合会包装教育委员会的主任单位，也是全国高校中唯一的中国包装联合会副会长单位和中国包装技术培训中心。学校占地面积3千多亩，图书馆藏书150余万册，全日制在校生34000多人，专任教师正高职称的227人、副高职称的636人，聘有两院院士',270129);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆三峡职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqsxedu.com','重庆三峡职业学院是重庆市人民政府举办的一所全日制普通公办高等学校，位于三峡库区重庆万州，交通便利。经过80年深厚文化底蕴的积淀和建院以来超常规、跨越式发展，学院已建设成为一所历史悠久、文化厚重、设施现代、环境优美的高等学府。是教育部重庆市首批单独招生试点院校，全国毕业生就业典型经验高校50强，中国现代农业职教集团副理事长单位，中国都市农业职教集团副理事长单位，重庆市示范性高等职业院校，重庆市大学生创业示范基地，重庆市普通高校毕业生就业示范中心，三峡库区唯一中外合作办学院校，重庆市职业教育工作先进',121414);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆工贸职业技术学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqgmy.cn','重庆工贸职业技术学院是重庆市人民政府举办的全日制普通高等职业学校，自1936年建校以来，为国家培养了6万多名技术人才和管理人才。学院位于三峡库区腹地、长江乌江交汇处的榨菜之乡——涪陵。学院占地33万平方米，校舍面积24.89万平方米，固定资产5.31亿元，馆藏图书56.1万册，教学用计算机2734台，教学实验仪器设备总值5920万元，有建筑实训中心等9个校内生产性实训基地，下设工程造价实训室等116个校内实训室。学院现有教职工336人，专任教师257人，教师中博士1人、硕士80人，教授9人、副教',752985);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆机电职业技术学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqevi.net.cn','重庆机电职业技术学院（教育部代码：12607）是以重庆兵器职工大学为基础改制、经重庆市人民政府批准（渝府［2003］104号）、国家教育部备案（教发函［2003］145号），现唯一隶属西南兵工局的国有公办普通高等院校。学院教学科研队伍实力雄厚、结构合理、爱岗敬业，现有专、兼职教师312人，其中博士、硕士研究生83人，教授、副教授94人，讲师132人，双师素质教师220人。长期以来，学院注重培养学生的职业道德和操作技能，以“基础厚、技术精、能力强、素质高”为标准，以培养“立足兵工，服务地方，适应社',153695);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆工程学院',' ','重庆',1,1,0,0,0,' ','暂空',' ','http://www.zdsoft.com.cn/web/guest/home','◆中央财政支持建设院校◆国家级示范性软件学院◆全国计算机紧缺型人才培养基地◆全国物联网技术应用人才培养基地◆全国动漫游戏产业人才培养基地◆全国青年就业创业见习基地◆重庆市软件产业人才培养基地◆重庆市网络信息安全人才培养基地◆重庆市创意产业人才培养基地◆重庆市服务外包人才培养基地◆重庆市大学生创业示范基地◆重庆市首批示范性众创空间◆重庆市级大学生微企创业孵化园◆重庆市普通高校就业先进集体重庆工程学院是经教育部批准设立的一所以工学为主，以软件、电子信息为特色，经济管理和人文艺术等学科专业协调发展的全',431089);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆电子工程职业学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqcet.edu.cn','◆全国一百所“国家示范性高等职业院校”之一◆国家教育部人才培养工作“优秀”院校◆国家级高等教育教学成果一等奖获得院校◆中国高等职业技术教育研究会副会长单位◆电子信息产业国家高技能人才培训基地◆全国重点建设职业教育师资培养培训基地◆全国技能大赛承办院校◆财政全额拨款公办院校重庆电子工程职业学院是由重庆市政府举办、重庆市教委主管、市教委与市经信委共建的全日制普通高等院校。学校创建于1965年，1999年成为重庆第一所高职学院，是国家示范性高等职业院校，是中国高等职业技术教育研究会副会长单位和重庆市高',559542);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆大学城市科技学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqucc.com.cn/default.html','重庆大学城市科技学院是经国家教育部批准设立的一所以本科教育为主的综合性全日制普通高等学校。学校是按照教育部相关文件规定，由教育部直属重点大学、国家“211工程”和“985工程”重点建设高校——重庆大学参与举办的独立学院。学校位于重庆市永川区著名风景区茶山竹海山麓，依山傍水，风景秀丽。学校建设了设施先进的图书馆、教学楼和实验室。其中图书馆可提供222万余册图书、3450GB电子图书供师生学习使用；教学楼可容纳20000余名学生同时学习，且所有教室都配备了成套多媒体教学设施。学校已建成建筑类、电气技',2569789);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州大学华软软件学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.sise.com.cn/','广州大学华软软件学院创办于2002年，是经教育部批准举办的本科层次独立学院，是全国300多家独立学院中唯一的一所软件学院。先后获得“中国十大品牌独立学院”、“中国十大优势专业院校”、“21世纪最具发展潜力教育机构”、“全国教育系统网络示范单位”等荣誉称号。学院交通便利，校园环境优雅写意，教学、娱乐、生活设施齐备，是一所环境怡人、教学设备完善、教学管理现代、教育理念先进的现代化大学。现有9系1部3个研究所。九系分别为：软件工程系、网络技术系、计算机系、数码媒体系、游戏系、电子系、管理系、国际经贸系',987123);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中山大学南方学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.nfsysu.cn/','中山大学南方学院是依据教育部教发[2003]8号文的精神，由中山大学与广东珠江投资集团合作，经教育部批准设立的独立学院（教发函[2006]87号，院校代码为：12619），是一所多学科全日制应用型本科高等学校。校园建设学院位于素有”北回归线上的明珠”和”都市桃园”之誉、以温泉著称于世的广州市从化，距广州市中心约80公里。校园布局有致，秀丽宁静，是陶冶情操、读书治学的胜境。学院占地面积1000亩，规划建筑面积45.6万平方米。目前已建成并投入使用的建筑面积35.3万平方米，在建工程的建筑面积2.8',2139853);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东外语外贸大学南国商学院',' ','广东',0,6,0,0,0,' ','暂空',' ','http://www.gwng.edu.cn/web/928025/zsb','广东外语外贸大学南国商学院是一所由广东外语外贸大学举办、国家教育部批准建立的本科层次的独立学院。学校位于中国著名历史文化名城和华南经济文化中心广州市，坐落在广州市白云区105国道(广从公路)旁，占地面积900余亩。校园环境幽雅、空气清新、湖光山色、绿树成荫。学校办学设备设施完善，师资力量优良，教学管理严格，是莘莘学子治学成才修养成人的理想之地。学校目前设有英语语言文化学院、东方语言文化学院、西方语言文化学院、国际经济与金融学院、国际工商管理学院、中国语言文化学院、信息科学技术学院、大学英语教学部',793350);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东海洋大学寸金学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gdcjxy.com/Admissions/index.asp','广东海洋大学寸金学院于1999年由广东海洋大学与湛江寸金教育集团合作创办，2006年经教育部批准为独立学院。2010年具有专插本招生资格。2012年获得学士学位授予权。2013年通过广东省教育厅独立学院本科教学基本状态评估。学院校舍总面积36万多平方米，其中教学行政用房18万多平方米，教室面积为12.3万平方米，多媒体教室座位数21112个，学生宿舍14.5万多平方米，教工宿舍和其他生活用房3.5万多平方米。图书馆藏书总量200多万册(含电子图书)。建有各类公共实验室、专业实验室96个，可容纳4',388853);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华南农业大学珠江学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.scauzhujiang.cn/zsb/','华南农业大学珠江学院是经国家教育部批准设立、实施本科层次教育的独立学院。学院以全日制普通本科教育为主，纳入国家统一计划招生。学院秉承面向市场的办学特色，理论与实践并重的教学特色，讲求实际的育人特色，瞄准前沿的发展特色，根据社会经济发展对人才的需要，设置了14个本科专业（26个专业方向）、4个专科专业，涵盖文、理、工、经、管五大学科门类，学科结构合理，办学特色鲜明。2008年在校学生达7600余人。◆优良教风学风、优质教育教学华南农业大学是广东省和农业部“九五”、“十五”共建“211工程”全国重点',460790);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建林业职业技术学院',' ','福建',0,2,0,0,0,' ','暂空',' ','http://www.fjlzy.com/Index.html','“国家示范性高等职业院校建设计划”骨干高职院校福建林业职业技术学院是2003年2月经福建省人民政府批准设立的一所全日制公办普通高等学校。学院前身福建林业学校创建于1953年，曾先后被评为省部级重点中专、国家级重点中专、林业部优秀中等林校、福建省文明学校。2007年10月，学院顺利通过教育部高职高专院校人才培养工作水平评估，并荣获“优秀”等级。2008年7月又被正式确定为“福建省示范性高等职业院校”。福建林业职业技术学院是2003年2月经福建省人民政府批准设立的一所全日制公办高等学校。学院为“福建',63455);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建信息职业技术学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.mitu.cn','“国家示范性高等职业院校建设计划”骨干高职院校福建信息职业技术学院是2003年2月经省政府批准、教育部备案,以工科为主、工商结合、以信息技术为特色的全日制普通高职院校,直属福建省教育厅。学院源于1906年创办的福州青年会书院,已有102年办学历史。2008年,学院被省教育厅、财政厅、发改委确定为福建省首批示范性高职院校。2008年7月,学院被教育部、财政部确定为\"国家示范性重点培育的高等职业院校\"。学院坚持“以服务为宗旨,以就业为导向,以能力为本位,以质量求发展,以特色创一流”的办学宗旨,秉承“',91227);
