insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建电力职业技术学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://fjdy.zhiye.chaoxing.com/','福建电力职业技术学院创办于1984年，是经福建省人民政府批准在原国家级重点中专泉州电力学校办学基础上组建的高职（专科）层次的普通高等职业学院，由福建省电力有限公司举办，业务由福建省教育厅和福建省电力有限公司共同管理。学院坐落于历史文化名城泉州市北郊，与国家级风景名胜区清源山、四季如春的西湖公园为邻，现有校园面积500多亩，建筑面积约15万平方米，素有“花园式校园”美誉。学院教学设施完善，实验、实训设施齐全，建有投资3000万元的输配电线路技能实训场、火电厂和变电站仿真系统等先进的实训设施和各类实',160940);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门海洋职业技术学院',' ','福建',0,2,0,0,0,' ','暂空',' ','http://www.xmoc.cn','厦门海洋职业技术学院前身是爱国华侨领袖陈嘉庚先生创办于1920年的集美水产学校。学院坐落于风景秀丽的“海上花园”厦门经济特区，现有思明和翔安两个校区，占地面积732亩，在校生5400人。学院立足厦门经济特区，坚持“以能力为本位,以就业为导向”的办学方向。设有航海技术、工商管理、生物技术、信息技术、机电工程等五个系二十四个专业，办学特色鲜明。学院十分注重内涵建设，坚持依法治校、质量立校，依照ISO9001质量管理体系的国际先进管理模式，建立了教育和培训质量体系，确保人才培养的质量。学院与厦门50多',141518);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建农业职业技术学院',' ','福建',0,2,0,0,0,' ','暂空',' ','http://www.fjny.com','福建农业职业技术学院是一所经福建省人民政府批准的公办全日制普通高等学校，其前身为创办于1902年的浙股、粤股蚕务学堂，具有一百多年的办学历史。学院现有相思岭和首山两个校区，相思岭校区（院部）位于福州市南郊相思岭（福厦路37公里处），首山校区位于福州市仓山区上三路190号。学院占地面积667亩，建筑面积12万平方米，教学仪器设备总值1500余万元，图书馆藏书30万册。现有生物技术系、动物科学系、园艺园林系、经济管理系、信息技术系等5个系23个专业，在校生近3000人。学院师资力量雄厚，现有教职工2',57175);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门医学院',' ','福建',1,3,0,0,0,' ','暂空',' ','http://www.xmmc.edu.cn','1.初识厦医专深入了解会喜欢培养社会需要人才成就学生美好未来---办学宗旨厦门医学高等专科学校是公办普通高等学校，位于“国际花园城市”、“全国文明城市”厦门岛内，坐落在美丽的环岛路旁，依山面海，环境优美。学校始建于1900年，办学110多年来培养了二万多名医药卫生技术人才。学校由校本部和两所附属医院组成。附属口腔医院为三级专科医院，有口腔综合治疗机200余台。附属厦门市第二医院为三级综合性医院，科室设置齐全，编制床位1000张。目前开设医学类(临床医学，口腔医学，针灸推拿)、护理类(护理，助产)',1819504);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建卫生职业技术学院',' ','福建',0,3,0,0,0,' ','暂空',' ','http://www.fjwx.com.cn/','福建卫生职业技术学院是2005年5月福建省政府批准，经国家教育部备案，在原福建卫生学校和福州卫生学校的办学基础上合并成立的。在几十年的办学历程中，学校先后吸纳了福州医士学校、南安中药材学校、福州助产学校、福建育才卫生学校的办学资源，现已发展成一所培养护理、药学、医学以及医学相关类医药卫生人才的高等职业院校，隶属福建省卫生厅。近年来，学校努力实践“业精德诚”的校训，抓住机遇，深化改革，学院发展取得了历史性的跨越。福建卫生学校创建于1954年，为全国重点卫生中专学校，是我省最早的药学和医学检验技术人',104775);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州医学高等专科学校',' ','福建',0,3,0,0,0,' ','暂空',' ','http://www.qzygz.com','“国家示范性高等职业院校建设计划”骨干高职院校泉州医学高等专科学校坐落在著名的侨乡、历史文化名城、中国最佳魅力城市—泉州。她的前身是惠世高级护校，1934年5月由英国教会驻泉代表贾丽德主持创办，1952年12月与晋江医士学校合并组建晋江医士学校。1958年学校升格为泉州大学医学院；1960年调整为泉州医学专科学校；1963年改为晋江地区卫生学校；1986年随地改市又更名为泉州卫生学校。1993年被省政府批准为我省首批省、部级重点中专学校；2000年5月被国家教育部命名为国家级重点中专学校；200',251999);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.cdp.edu.cn','成都职业技术学院是成都市人民政府举办的全日制普通高等学校，是教育部高职高专人才培养工作水平评估优秀级院校、国家（示范）骨干高职院校和四川省示范性高职院校建设单位。学院以培养现代服务业高端技能型人才为主，开设旅游类、计算机及软件类、财经金融类、工商管理与房地产类、护理类、汽修类专业40余个，在校学生万余名。学院位于成都市，现有高新校区、花源校区、青羊校区、锦江校区四个校区，占地面积530余亩，建筑面积25万平方米。办学条件优良，教学设施一流；是国家级计算机及软件技术实训基地，中国软件名城（成都）软',490672);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川化工职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://zjc.sccc.edu.cn','四川化工职业技术学院是经四川省人民政府批准、教育部备案的全日制普通高等学校，属国家正规公办高等院校。学院始建于1953年，办学历史源远流长。建校初期，先后由重庆高工校、成都高工校、内江化工校、五通桥化工校、吉林工业校、成都工业校、重庆合成材料学校、自贡化工专科学校等学校和相关专业并入，1959年，经教育部批准升格为泸州化工专科学校，2003年正式更名为四川化工职业技术学院。50余年来，在各级领导的亲切关怀下，学院不断发展壮大。党和国家领导人贺国强、顾秀莲曾亲临学校视察；郭沫若同志曾为学校题词并题',107504);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川水利职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.swcvc.net.cn','四川水利职业技术学院属省属普通全日制高等学校。地处“世界文化遗产”?都江堰，得水利科技之地利优势，承古堰之人杰地灵，引无数学子求学成才．学院创建于一九五六年，前身为四川省水利电力学校，是首批国家级重点中专。办学历史悠久，誉称四川水利水电“黄埔军校”，培养了众多水利水电人才。大学专科教育自一九六0年始已逾四十年历史，建校四十七年来已有二万多大中专毕业生分布在西南西北，为西部水利职业技术教育行业龙头学校。二000年经四川省人民政府批准在原四川省水利电力学校基础上设立四川农业大学水利电力学院，独立招生',204066);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南充职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.nczy.com','南充职业技术学院是经四川省人民政府批准，国家教育部备案建立的公办全日制普通高等学校。学院坐落在嘉陵江畔的川东北中心城市??四川省南充市。学院占地1580余亩，校园内绿树成荫，鸟语花香，环境优美。现有建筑面积150813平方米，固定资产总额1.2亿元，教学仪器设备总值2110余万元，拥有先进的教学用微机1440台，馆藏图书近40万册。现有专任教师313人，其中具有副教授以上高级职称的90人，讲师、工程师等中级职称154人，“双师型”教师75人，常年聘请外籍教师2?3人，外聘实践技能教师和实习实训指',223319);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内江职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.njvtc.cn','内江职业技术学院是经四川省人民政府批准建立的一所综合性的全日制公办普通高等学校。学院地处历史悠久，风景如画，人文荟萃的四川省内江市，坐落于川东南最大的城市广场——大洲广场和风景秀丽的西林公园、著名国画大师张大千纪念馆旁。学院文化氛围浓厚，是莘莘学子求学的理想场所。学院有着悠久的办学历史，在长达51年的办学实践中,积累了丰富的教学和实践经验，秉承“厚德、博学、求实、创新”的校训，发扬“同舟共济、自强不息”的内江职院精神，艰苦奋斗、团结协作、求真务实、开拓创新、敬业奉献，营造出积极向上、和谐健康的育',115539);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川航天职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.sacvt.com','办学历史悠久四川航天职业技术学院是经四川省人民政府批准，国家教育部备案成立，面向全国招生的全日制普通高等院校。具有41年的办学历史。学院的教育教学工作得到了各级领导的高度赞扬和充分肯定。十六届中央委员、中国航天科技集团总经理张庆伟和“神舟”五号、“神舟”六号载人飞船总指挥、中国空间技术研究院院长袁家军多次视察学院，对学院为航天科技战线培养了一大批知识型、应用型的高技能人才给予了高度评价。四川省委书记张学忠亲笔为学院题写校名，省长张中伟为学院题词：“面向航天，心系四川”。中共成都市委、市政府重点工',251797);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川邮电职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.sptpc.com','四川邮电职业技术学院创办于1956年，2003年4月在原四川省邮电学校的基础上经四川省人民政府批准成立，是中国西部第一所独立设置的邮电通信类高等职业技术学院。近六十年来，学院伴随国家邮电通信与信息业的建设和发展而不断壮大，办学实力与办学水平不断提升。学院系省级文明单位。2006年，接受教育部高职高专人才培养工作水平评估获得“优秀”级。2010年8月，被四川省财政厅、教育厅确定为四川省首批“省级示范性高等职业院校建设计划”立项建设单位。2010年12月，被财政部、教育部确定为“国家示范性高等职业院',301852);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波城市职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.nbcc.cn/','宁波城市职业技术学院是经浙江省人民政府批准，在原宁波大学职业技术教育学院的基础上组建的独立设置的公办普通全日制高等院校。学院由宁波大学、宁波市教育局共同投资建设。学院位于东海之滨、甬江之畔的浙江省宁波市，分二个校区。本部地处宁波高教园区南区，占地503亩，建筑面积12万平方米，校园内设施齐备，是理想的学习、生活场所。校园外有占地千亩的中央绿化带、藏书丰富的园区图书馆、符合国际比赛标准的现代化体育场馆、环境舒适的商务休闲中心、比较发达的公交系统，特别是集聚了包括宁波诺丁汉大学、浙江大学宁波理工学院',94698);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江同济科技职业学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.zjtongji.edu.cn/','浙江同济科技职业学院于2003年1月经省政府批准建立，是一所由浙江省水利厅主管的公办全日制高职学院。学院占地面积442亩，校舍建筑面积12.4万平方米，现有教师246人，其中副高级以上专业技术职务教师78人，具有硕士研究生以上学历或学位的教师75人，现有在校生4300余人。学院立足浙江，依托行业，充分利用浙江水利行业优势、同济大学学科优势，建设以大土木类专业为主体，水利水电、建筑艺术类专业为特色，相关专业协调发展，培养生产、建设、管理一线需要的高技能人才的高职院校。学院各类教学设施齐全，建有12',62392);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南科技大学潇湘学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://xxxy.hnust.edu.cn','湖南科技大学潇湘学院是经教育部正式批准的全日制本科独立学院。学院实行董事会领导下的院长负责制，由湖南科技大学校长任董事长。学院坐落在一代伟人毛泽东的故乡——湖南省湘潭市，占地面积688亩。学院于2001年开始招生，现有在校学生8407人。学院主要依托湖南科技大学的师资力量和办学资源培养本科层次的应用型高级专门人才。学院现有专任教师478人，其中教授52人，副教授182人，具有博士学位者106人，硕士学位者202人。学院的办学理念是“至善、至诚、至厚、至精”，以科学发展观统领全局，把教育质量作为学',309658);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南华大学船山学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://csxy.usc.edu.cn','南华大学船山学院是2001年8月经湖南省人民政府批准成立、2004年经国家教育部首批确认，由南华大学按新机制、新模式举办的全日制本科独立学院。学院坐落在历史文化名城——湖南省衡阳市，与南华大学校区紧密相连，融为一体，环境优美。学院依托南华大学雄厚的师资力量和优质的教学资源，结合独立学院特有的灵活高效的运作优势开展教育教学活动。学院秉承和发扬南华大学“明德、博学、求是、致远”校训，坚持“以人为本，以学生为本”的办学理念，视办学质量和办学特色为生命，深化教学改革，注重学生应用和创新能力培养。学院面向',326276);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南商学院北津学院',' ','湖南',0,6,0,0,0,' ','暂空',' ','http://www.bjxy.net.cn','湖南商学院北津学院（以下简称北津学院）是经湖南省人民政府（湘政函[2001]138号）首批批准设立并经国家教育部（教发函[2004]10号）确认的独立学院之一，由湖南商学院举办，按照全新的管理模式和办学机制运行。北津学院从2001年秋季开始招生，现有来自23个省（市、区）的在校学生8300余人。2014年，学院面向全国16个省（市、区）招生1408人。北津学院位于长沙市雷锋大道旁，与湖南商学院本部毗邻，占地近500亩，总投资3亿余元，交通便利，环境优美，设施配套齐全，布局科学合理。北津学院依托湖',370663);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南师范大学树达学院',' ','湖南',0,4,0,0,0,' ','暂空',' ','http://sdw.hunnu.edu.cn/','树达学院是由湖南师范大学举办，2001年8月经湖南省人民政府批准成立、2004年1月获国家教育部审核确认的一所实行新的办学体制和机制，旨在培养适应国家和湖南省经济社会发展需要的本科应用型人才的独立学院。学院紧紧依托湖南师范大学雄厚的师资力量，先进的教学仪器设备，丰富的图书资料，优良的校风和办学传统，独特的自然环境和人文环境以及优秀的校园文化，坚持“以学生为根本，以教学为中心，以质量为生命，以管理为重心”的办学理念，致力于培养综合素质高、专业基础好、实践能力强，具有创新精神的应用型人才。学院开办的',329968);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南农业大学东方科技学院',' ','湖南',0,2,0,0,0,' ','暂空',' ','http://www.hnaues.com/','湖南农业大学东方科技学院坐落于历史文化名城——长沙，位于久负盛名的浏阳河畔，是湖南农业大学于2002年按照新机制举办、经湖南省人民政府批准、国家教育部首批确认的全日制本科独立学院。校园环境幽雅，设施先进，教育教学质量过硬，学院先后获得“全国教育教学管理示范院校”、“全国创建‘平安校园’示范学校”、湖南省高校“先进基层党组织”等殊荣，现为湖南省独立学院联席会主席单位，中国独立学院协作会副理事长单位。学院实行董事会领导下的院长负责制。湖南农业大学校长、博士生导师周清明教授任董事长，博士生导师、留德博',262469);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中南林业科技大学涉外学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.zswxy.cn','中南林业科技大学涉外学院(原中南林学院涉外学院)成立于2002年6月，国标编码12656，是经湖南省人民政府批准设立，国家教育部确认，具有独立法人资格的全日制本科独立学院。举办者中南林业科技大学(原中南林学院)始建于1958年，原隶属林业部，现为湖南省人民政府和国家林业局重点建设高校，是一所以本科生和研究生教育为主的具有博士学位授予权的教学研究型多科性大学。2005年教育部本科教学水平评估为“优秀”。学校学科门类齐全，涵盖理、工、农、文、经、法、管、教等八大学科门类，设有20院4部一所一中心，有',613038);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南文理学院芙蓉学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://fur.huas.cn','湖南文理学院是一所多学科全日制普通本科院校，至今已有五十多年的办学历史。学校坐落在拥有全国文明城市、国家卫生城市、国家园林城市、中国首届魅力城市、国际花园城市、全国交通管理模范城市等美誉的湘西北历史文化名城——常德，这里人文荟萃，环境优美，交通便利，水陆空交通网络直通全国各地，是莘莘学子理想的求学之地。学校占地面积1791亩，校舍建筑总面积45万平米，820多名专任教师中有教授115人，副教授396人，博士117人；现有17个教学院、2个教学部和一个独立学院——芙蓉学院，47个本科专业，涵盖文、',201866);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南理工学院南湖学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnist.cn','湖南理工学院南湖学院是2002年6月经湖南省人民政府批准成立，经国家教育部首批确认的全日制本科独立学院，是一所理、工、文、经、管、法、教等多学科协调发展的本科高校。学院在2005年1月教育部检查评估中评定为优秀，在湖南省教育厅近两年年度办学情况检查中均名列前茅。学院坐落在国家历史文化名城、长江沿岸开放城市湖南省岳阳市南湖风景区，依山傍水，环境优美。学院现有校园面积506亩，校舍建筑面积18万平方米，校内教学大楼、实验楼、图书馆、计算机中心、语音教学中心、多媒体教学中心、校园网、电视台、运动场、学',237042);
