insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('无锡科技职业学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.wxstc.cn/zsb','无锡科技职业学院创办于2003年，是经国家教育部备案、江苏省人民政府批准，由无锡市人民政府主办、无锡市人民政府新区管理委员会承办的普通全日制高等职业学院。学院地处无锡新区的中心位置。无锡新区的综合实力在全国54个国家级高新技术开发区中名列前茅，无锡新区的光伏新能源产业园、超大规模集成电路产业园、传感网创新示范区、创意文化产业园、生命科技园、临空总部经济园、机电动力装备产业园（轨道交通产业园）等众多的科技园区，北京大学、南京大学、中国科学院等45所国内知名高校、科研院所的在锡机构，200多家研发机',218810);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('盐城卫生职业技术学院',' ','江苏',0,3,0,0,0,' ','暂空',' ','http://www.ycmc.edu.cn','公办专科历史久学校是一所全日制专科层次的公办普通高等学校，坐落在东方湿地之都、仙鹤神鹿世界——江苏省盐城市。学校前身为新四军军部于1941年7月创办的华中卫生学校，1958年成立盐城医学专科学校、盐城中医专科学校，1964年7月改办江苏省盐城卫生学校。2005年3月升格为盐城卫生职业技术学院。70多年来，先后为社会培养了四万余名卫生技术人才。卫生大类专业全学校是获教育部批准设置临床医学专业的高职院。开设临床医学、护理、药学、医学影像技术、康复治疗技术、医学检验技术等18个专业，专业类型基本覆盖高',161641);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('扬州环境资源职业技术学院',' ','江苏',0,2,0,0,0,' ','暂空',' ','http://www.jseea.cn/schoolinformation/1302678318501.html','普通类招生代码：1366中外合作办学招生代码：1465海外本科直通车招生代码：1799扬州环境资源职业技术学院创办于1956年，她坐落在中国历史文化名城、联合国“人居奖”城市----扬州，总占地750亩，建筑面积26万平米，主校区位于扬州城南风景秀丽的高旻寺景区，校园建筑古雅，花草芬芳，绿树成荫。行走其间，移步换景，或见湖光潋滟，亦闻书声朗朗，云水相融，人文荟萃，呈天人合一之境。学院秉承“自强不息，明德树人”的校训精神，坚持以“天人合一”、“面向人人”和“君子不器”的绿色教育理念，引领教育教学改',265956);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州经贸职业技术学院',' ','江苏',0,6,0,0,0,' ','暂空',' ','http://www.szjm.edu.cn','苏州经贸职业技术学院是一所隶属于江苏省教育厅的全日制公办普通高等学校，座落在苏州国际教育园北区，南依层峦叠翠的上方山国家森林公园，东临风光秀丽的国家级太湖风景区石湖景区，山水相依、清秀宜人。学院占地面积820亩，建筑面积25万平方米，教学仪器设备总值1亿余元，图书馆藏书77万册,电子图书5800GB。学院现有在校生近万人，教职工近500人，副高级以上职称140余人。学院秉承“育厚德之人，炼强技之才”的校训，坚持“以提高质量为根本，以校企合作为灵魂，以服务社会为己任”的办学理念，办学50年来，为社',388961);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州工业职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.siit.cn','苏州工业职业技术学院是2003年7月经江苏省人民政府批准成立、教育部备案的一所公办专科层次的全日制普通高校，坐落于苏州国际教育园南区，占地560多亩，建筑面积近20万平方米，校园环境优美，周边交通便利，生活设施齐全，共享资源丰富。几年来，学院在理念创新、校企合作、产学结合、订单培养、实训基地、精品教材、技能竞赛、科技研发、双师团队、技术专利、海外交流等方面取得了令人注目的成绩，培养了大批高素质技能型人才，为地方经济建设作出了积极的贡献，赢得了良好的社会声誉。学院设有机电工程、电子工程、信息工程和',226103);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州托普信息职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.szetop.com/portal/center/','苏州托普信息职业技术学院是经江苏省人民政府批准、教育部备案，列入国家统一招生计划，具有独立颁发文凭资格的全日制民办普通高校。学院秉持“厚德、重能、实用、创新”的校训，坚持“以学生为主体，以教育教学为中心，以就业为导向”的办学理念，努力为学生成人成才创造良好条件。大力推进教育教学改革，积极探索和实践“校企合作、工学结合、顶岗实习”的人才培养模式。学院2007年通过了江苏省教育厅组织的人才培养工作水平评估;2009年被省教育厅评为“文明宿舍单位”;2010年通过省教育厅高校公共卫生管理考核;2011',59082);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州卫生职业技术学院',' ','江苏',0,3,0,0,0,' ','暂空',' ','http://www.szhct.edu.cn','百年学府·薪火相传苏州卫生职业技术学院是一所具有百年历史的江苏省省属全日制公办专科层次普通高校，其前身是1911年美国基督教会创办的私立博习医院护士学校，是江苏省示范性高等职业院校。学院坐落于素有“人间天堂”之称的古城苏州，现有三个校区分别位于古城区、石湖风景区和古镇木渎，学院共占地50.2万平方米(753亩)，建筑面积30多万平方米。现有全日制在校生8000多人，专任教师近400人，高级职称比例占到36.2%，硕博士比例占67.19%。拥有教育部行执委和专委会6人，省优秀教学团队2个、省科技创',538278);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东南大学成贤学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://cxxy.seu.edu.cn','东南大学成贤学院成立于2003年6月，是经教育部批准，由东南大学与优质社会资源合作举办的独立学院。学院具有独立法人资格、独立校园、独立颁发文凭和实行相对独立的管理运作,学校座落在历史文化名城南京。东南大学成贤学院的举办学校东南大学是中央直管，教育部直属的全国重点大学，是国家“985工程”和“211工程”重点建设的大学之一。是全国首批通过国家“本科教学优秀评价”的三所重点大学之一，是全国高校科研十强之一。东南大学成贤学院实行董事会领导下的院长负责制，中国工程院院士、东南大学校长顾冠群任董事会董事长',870286);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('无锡商业职业技术学院',' ','江苏',0,6,0,0,0,' ','暂空',' ','http://www.wxic.edu.cn','无锡商业职业技术学院是一所省属公办、主要面向现代服务业培养高素质技术技能型人才的全日制普通高等学校。学校前身是创建于1965年的江苏省无锡商业学校，2000年10月经江苏省人民政府批准升格更名为无锡商业职业技术学院。2008年被确定为江苏省示范性高职院校建设单位。2013年学校被列为江苏省现代职教体系建设试点项目单位，与南京财经大学合作实行“3+2”分段培养应用型本科人才。学校现有九龙、姚湾、锡惠三个校区，占地面积1600余亩，建筑面积近30万平方米，固定资产总值达10亿余元，馆藏纸质图书80余',235308);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南通航运职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.ntsc.edu.cn  ','“国家示范性高等职业院校建设计划”骨干高职院校南通航运职业技术学院（以下简称“学院”）是一所经江苏省人民政府批准设置的全日制普通高等学校，创建于1960年，隶属于江苏省交通厅。是教育部高职高专人才培养工作水平评估优秀院校、江苏省首批省级示范性高等职业院校建设单位、国家海事局委托实施教育扶贫的高职院校、教育质量管理体系通过国家海事局认证的高职院校和具有国家海事局认可的与国际接轨的15项海员培训资格的高职院校。学院座落于风景秀丽的中国长三角地区最适宜人居的花园城市、著名的教育之乡——江苏省南通市。学',154766);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西艺术职业学院',' ','山西',0,8,0,0,0,' ','暂空',' ','http://www.sxyz.com','山西艺术职业学院是2000年9月21日经山西省人民政府批准，由原山西文化艺术学校、山西省电影学校以及山西省唯一的一所成人艺术类高校山西职工文学院合并组建而成。其前身是始建于1951年的山西文化艺术学校（五十年代曾一度升格为山西艺术学院），是我国开始重点发展高等职业教育以后批准成立的第一所艺术职业学院。学院分为三年制专科（高中、中专、职高起点）、五年一贯制高职（初中起点）、五年制中专（小学起点的舞蹈、音乐小班）及成人大专（脱产和函授）和专升本（函授）等几个办学层次。03、04年学院与太原师范学院联',61748);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州英华职业学院',' ','福建',0,5,0,0,0,' ','暂空',' ','http://www.fzacc.com/','一、学院历史福州英华职业学院，前身为美国教会创办之“英华书院”,迄今已有128年历史。曾为国家培养了如侯德榜、陈景润等20多位院士及一大批科学家经济学家和社会学家；前国府主席林森等政治名流、福州市委第一任书记方景灏等40余位革命烈士。百年英华为我国科学与社会经济的发展做出了卓越的贡献，在福州教育史上曾有相当影响，成为福州人民宝贵的文化遗产。由海内外校友无偿捐资及努力，1994年成为我省第一批学历文凭大专高校，校名为“福州英华外国语学院”；2001年又成为福建省首批普通高等职业学院，更名为“福州英',47658);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门华厦学院',' ','福建',1,0,0,0,0,' ','暂空',' ','http://www.hxxy.edu.cn/','厦门华夏职业学院成立于1993年的厦门华厦职业学院是由厦门市政协原主席蔡望怀倡导和兴办的，是厦门市首家民办全日制普通高等职业学院，是完全公益性的教育机构。1997年被民办高教委授予全国民办高校先进单位，2001年经福建省人民政府批准，教育部备案，正式建校，纳入全国统一招生，是厦门市政府重点扶持的民办高校。2005年获得教育部和财政部联合颁发的中央职业教育实训基地建设支持奖励专项基金。2006年获得由中共福建省委组织部、省教育厅、省发改委等多家单位联合授予的福建省职业教育先进单位称号。2007年1',115330);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('闽南理工学院',' ','福建',1,1,0,0,0,' ','暂空',' ','http://www.mnust.edu.cn','闽南理工学院是经国家教育部批准创办的以工学学科为主，兼有理学学科、管理学学科、经济学学科等多种学科协调发展，以光电技术应用为鲜明特色，光、机、电一体化为优势，具有独立颁发高等学历文凭资格的全日制普通本科高等学校。学院前身系泉州光电信息学院，始建于1998年。十年来，学院根据海西经济和社会发展需要设置专业，其中光电技术应用专业的设置填补福建省及周边省份高职专业设置的空白，已毕业六届毕业生，就业率均在92%以上。学院的办学成绩得到省、地、市各级政府的重视和社会各界的好评，2006年福建省人民政府同意',719693);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州纺织服装职业学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.qzfzfz.com/','泉州纺织服装职业学院是一所由省政府批准直属省教育厅管理的具有颁发国家高等教育大专文凭资格的全日制普通高等院校。学校座落于海峡西岸的\"中国休闲服装名城\"—泉州市石狮市区。学院2008年以良好的成绩顺利通过教育部高职高专人才培养水平的评估。福建省预备重点项目建设的高职院校学院作为福建省唯一的一所纺织服装高等院校，是福建省2008年社会事业方面的预备重点扩建项目的院校。学院还同时是国务院国开发办[2005]25号文件确定的第二批国务院扶贫办劳动力转移培训示范基地。学院占地面积350亩，已投资1亿多元，',35510);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安欧亚学院',' ','陕西',1,6,0,0,0,' ','暂空',' ','http://zhaosheng.eurasia.edu/','西安欧亚学院成立于1995年，是一所经国家教育部批准，以管理、经济为主，艺术、文学、教育、工学等协调发展的国际化应用型普通本科高校。我们主张为学生提供有用的教育，使学生“学了有用，学得受用”；懂得学生的个性化特征，和当今市场经济最新鲜的变革，并且能够前瞻性地把握现代社会对人才的需求。我们致力培育优秀的毕业生，使其具备专业才能和批判性思维，而且追求终身学习，以应付全球化的工作环境。商科教育领航者作为本地区应用技术大学商科教育领航者，西安欧亚学院以商科为载体，利用先进的教育理念，改变了传统的商科的知',1805007);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安外事学院',' ','陕西',1,0,0,0,0,' ','暂空',' ','http://zsb.xaiu.edu.cn/Index.shtml','西安外事学院创建于1992年，是国家教育部批准的本科层次普通高等学校。目前，以实施本科教学为主，同时举办专科层次的高等职业教育。下设外国语、经济管理、信息工程、人文、医学、国际合作、成人与网络教育等7个二级院，附设职业中专、博迪学校。17个系，开设本科专业10个、高职专业25个、助学类专业23个。涵盖商科、人文科学、社会科学、理科、工科、农科、医科等7个大学科门类。多学科协调发展，具有良好的办学基础和鲜明特色。截至2006年7月，各类在校学生3万余名，其中高职生1.5万余名。一、基础设施学院位于',2544685);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安翻译学院',' ','陕西',1,5,0,0,0,' ','暂空',' ','http://zs.xafy.edu.cn/index.php?m=content&c=index&a=lists&catid=44','西安翻译学院由我国民办教育拓荒者丁祖诒先生创办。学院座落在西安市南郊风景秀丽的终南山下，其前身为创建于1987年的西安翻译培训学院。2000年经陕西省人民政府批准成立西安翻译职业学院，实施全日制高等职业教育。2005年经教育部批准升格为本科高校，更名为西安翻译学院。2009年，获得学士学位授予权。学院始终坚持“育人为本、德育为先、能力为重、全面发展”、“办学以教师为本，教学以学生为本”的办学理念；坚持“质量立校、人才强校、科研促校、特色兴校”办学思路；坚持“不以营利为目的，取之于学，用之于学”公',1681080);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西京学院',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://zhaosh.xijing.edu.cn/','西京学院是具有研究生教育资格的普通高校，同时也是国家批准的西北地区唯一一个自学考试综合改革试点单位和主考院校。学院占地1391亩，校舍建筑面积605285㎡。现设有研究生部、基础部、工程技术系、机电工程系、经济管理一系、经济管理二系、人文科学系、艺术学院、高职一部、高职二部、文理学院等11个系(部、院)和工程研究院、高等教育研究所、文化产业研究所、艺术研究所、新闻传播研究所及各专业教研室等40个教科研机构。开设2个硕士专业，29个本科专业，23个专科专业。现有各类在校生2.4万多人。学院科学定位',1889517);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏民族职业技术学院',' ','宁夏',0,9,0,0,0,' ','暂空',' ','http://www.nxmzy.com.cn/','宁夏民族职业技术学院是经自治区人民政府批准，教育部备案公布的公办全日制普通高等职业院校，也是吴忠职业技术教育中心。学院具有颁发全日制职业技术大、中专层次学历证书和中、高级国家职业资格证书的资质。学院前身为1952年创建的宁夏民族公学，期间1956年更名为吴忠师范学校。2001年2月由原吴忠师范学校、吴忠职业技术教育中心、吴忠职工中专合并成立吴忠职业技术学院，是全区最早成立的高等职业技术院校。2006年12月经自治区人民政府批准更名为宁夏民族职业技术学院，并于2007年5月由教育部备案公布，历史沿',71781);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三亚城市职业学院',' ','海南',0,5,0,0,0,' ','暂空',' ','http://www.sycsxy.cn/','三亚城市职业学院是经海南省政府批准、教育部备案的一所全日制普通高等学校。学院坐落在素有“南中国度假天堂”之称的海南省三亚市，是三亚市第一所旅游性质的大学。面向世界全方位开放的旅游都市三亚，承载着“国际旅游岛”的风采，阳光、沙滩、椰韵、海风……南山海上观音、高尔夫球场、超星级酒店……都浓缩在城市的景观之中。正在建设中的三亚城市职业学院新校区耸立在三亚市大学园区，占地面积300亩、建筑面积15万m2。高速公路、环岛旅游观光火车绕校而过，交通网络纵横、方便快捷。校园依山而建、环境优美。几何、开放式的教',57995);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大庆职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.dqzyxy.net','【地理位置】大庆职业学院位于“绿色油化之都、天然百湖之城、北国温泉之乡”、“中国魅力城市”——大庆市萨尔图区，与刚刚建成的大庆奥林匹克公园紧邻，距离大庆火车站15公里、大庆长途客运枢纽站10公里、大庆飞机场10公里，交通便利。【办学历史】学院是黑龙江省人民政府批准、国家教育部备案，列入普通高等学校招生计划的一所公办全日制高等职业院校。学院行政隶属中国石油集团大庆油田公司，如今已有60余年的办学历史，是中国石油重点综合培训基地、黑龙省唯一以石油石化主体专业为主的“国家示范性高等职业院校”。【基本理',108544);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津渤海职业技术学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjbhzy.com','天津渤海职业技术学院是天津市人民政府批准，国家教育部备案公办全日制普通高等专科学校，是天津市高职院校首家通过教育部高职高专人才培养工作水平评估的单位，是天津唯一一所化工类高职学院。学院有着深厚的历史积淀，有近50年的职业教育办学历史。特别是近几年来，依托行业办学，坚持“立足化工、面向社会、服务经济”的办学宗旨；坚持“市场运作，突出特色，开放办学，校企结合”的办学理念；坚持工学结合、校企合作的办学模式；坚持“教育至上，质量至上，服务至上”的教育理念；坚持规模、质量、品牌协调发展，实施“三尖工程”（',209975);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津电子信息职业技术学院',' ','天津',0,1,0,0,0,' ','暂空',' ','http://www.tjdz.net/','天津电子信息职业技术学院是全国100所国家示范性高等职业院校之一。2011年初迁入新建的国家级高等职业教育改革实验区-----天津海河教育园。新校区占地面积49.7公顷，总建筑面积20.6万平方米，其中一期已建成16.82万平方米，建设总投资10.47亿元。主要建成了软件学院教学实训楼、电子技术系教学实训楼、经济管理系教学实训楼、机电技术系教学实训楼、图书馆、行政办公楼、学生食堂、教工食堂及综合服务楼、学生宿舍等。二期建设规划已完成，资金预算为2亿元。学院下设软件学院（设有网络技术系、软件技术系',321860);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津机电职业技术学院',' ','天津',0,1,0,0,0,' ','暂空',' ','http://www.suoyuan.com.cn/','天津机电职业技术学院坐落在天津市风光秀丽的子牙河畔，毗邻平津战役纪念馆。是经天津市政府批准国家教育部备案的具有高等学历教育招生资格的全日制国办普通高等院校，是为经济建设与社会发展培养高素质技能型人才的高等职业技术学院。学院被国家教育部门批准为国家技能型紧缺人才培养基地，是天津市第一家通过ISO9001质量管理体系(国际)认证的高等院校，以培养社会满意的职业人才为方向，具有鲜明的办学特色。天津机电职业技术学院依托天津机电工业和滨海新区，以培养高素质技能型人才为主要方向；以“实事求是，崇尚科学”为校',183209);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津现代职业技术学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.xdxy.com.cn/','天津现代职业技术学院成立于2001年，是一所经天津市人民政府批准、国家教育部备案，独立设置的公办全日制高职院校，学院设置了应用文科、应用工科及艺术类共34个专业。专业设置以市场需求为导向，学院现有7个二级学院，两个教学部，其中有4个国内领先的特色专业，3个天津市级教学改革试点专业。学院是首批进入国家职业教育改革创新示范区——中国天津海河教育园的院校之一、滨海新区技能型紧缺人才培养基地，2010年被教育部和财政部确定为全国示范性骨干高职院校建设单位。国家高等职业教育“生物技术及应用专业教学资源库”',264098);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津公安警官职业学院',' ','天津',0,11,0,0,0,' ','暂空',' ','http://www.tjjingyuan.cn','天津公安警官职业学院是教育部批准的具有高等学历招生资格的专科层次普通高等学校，是天津市唯一的一所公安高校，录属于天津市公安局和天津市教委。学院始建于1949年1月，具有悠久的历史和光荣的革命传统，历经天津市公安学校、天津市人民警察学校、天津市政法干部管理学院公安分院等发展时期，承担普通中专教育、成人高等教育、高等职业教育及各级各类公安民警培训任务，至今已有数万名毕业生工作在本市及十几个省、市、自治区的政法机关和企事业单位保卫部门，大多数同志走上了各级领导岗位，成为业务骨干。天津公安警职业学院有着',299084);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江林业职业技术学院',' ','黑龙江',0,2,0,0,0,' ','暂空',' ','http://www.hljlzy.com/','黑龙江林业职业技术学院是2001年3月8日经黑龙江省人民政府批准、教育部备案、国家公办、独立设置的普通高等职业技术学院。隶属于黑龙江省森林工业总局，是黑龙江省文明单位标兵、花园式单位、三育人先进单位、全国职业教育先进单位、中华人民共和国劳动和社会保障部特有工种职业技能鉴定站．学院的前身是以始建于1958年的原国家级重点普通中等专业学校牡丹江林业学校为主，将原牡丹江林业师范学校、牡丹江林业干部学校、牡丹江林业技工学校、东京城林业机电学校合并组建的．学院校园占地40万平方米，建筑面积13万平方米。学',99949);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江农业职业技术学院',' ','黑龙江',0,2,0,0,0,' ','暂空',' ','http://www.hljnzy.net','黑龙江农业职业技术学院是经黑龙江省人民政府批准、国家教育部备案、独立设置、面向全国招生的一所培养高等职业技术人才的全日制普通高等院校。学院始建于1948年，经历了东北农学院合江分院、合江地区五?七大学等时期，尤其是文革后恢复招生一直开展专科、本科教育。2001年3月8日经黑龙江省人民政府批准、教育部备案，由原黑龙江省佳木斯农业学校改建为黑龙江农业职业技术学院，是黑龙江省第一批晋升的高职院校之一。建校57年来，经过几代人的共同努力，学院不断发展壮大，面貌发生了根本性的变化，各项事业取得了长足的进展',79859);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江农业工程职业学院',' ','黑龙江',0,2,0,0,0,' ','暂空',' ','http://www.hngzy.com','黑龙江农业工程职业学院是一所独立设置的公办全日制普通高校，始建于1949年，座落于北国冰城哈尔滨市。2001年晋升为高等职业院校，2010年被确定为国家示范性高等职业院校。经过65年的建设发展，学院办学实力和社会影响力与日俱增，多项办学指标位列全国高职前30强。学院是国家首批“能力建设试点院校”和“宝马英才培训基地”；是国家“数控技术专业领域”和“汽车运用与维修专业领域”技能型紧缺人才培养培训基地；国家“电工电子与自动化技术”和“数控技术”职业教育实训基地。学院占地面积204万平方米，建筑面积2',279941);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江农垦职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.nkzy.com/','黑龙江农垦职业学院坐落于北国冰城哈尔滨市江北大学城，是一所具有五十多年办学历史的公办全日制综合性高等职业院校，行政隶属于黑龙江省农垦总局。学院前身为黑龙江农垦卫生学校、经济学校、师范学校。2001年3月，晋升为高职学院;2003年9月，从佳木斯搬迁至哈尔滨。学院立足垦区、面向龙江、辐射全国，坚持以服务为宗旨、以就业为导向的方针，走产学研训结合发展之路，培养面向生产、建设、服务、管理第一线的高端技能型专门人才。学院占地面积近40万平方米，校舍16.2万平方米，资产总值4.04亿元，教学仪器设备总值',130786);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江司法警官职业学院',' ','黑龙江',0,11,0,0,0,' ','暂空',' ','oldname": "黑龙江司法警官职业学院','黑龙江司法警官职业学院是经黑龙江省人民政府批准成立、国家教育部备案的政法类全日制普通高等学校。学院主要为司法行政系统培养人民警察，同时也为社会培养法律服务、经济管理、计算机应用等其他高等职业人才。学院承担着全省在职司法行政干警和共青团干部的培训任务。2009年5月，学院被中央政法委、中组部、教育部、司法部等十一部委列入全国政法干警招录培养体制改革试点单位，成为东北三省及内蒙古自治区唯一一所政法干警招录培养体制改革司法类试点院校。学院现有A、B两个办学区。A区位于哈尔滨市南岗区学府路383号;B区',203478);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连商务职业学院',' ','辽宁',0,6,0,0,0,' ','暂空',' ','http://www.dbiedu.com/','学院坐落在气候宜人、景色秀丽的海滨城市--大连。学院占地300亩，建筑面积6万多平方米。学院现有综合教学主楼一座、专业教学楼2座、学生公寓3座、星级外教楼1座，并有草坪足球场1个，标准蓝、排球场10个学院下设10个系部、48个专业；4个培训中心，2所附属学校。拥有国家学历考试试点、高等教育自学考试及远程教育、开放教育等多种形式的办学模式。学生毕业后颁发国家承认的专、本科学历文凭。学院与国内外众多名牌大学建立了紧密的合作办学关系。与美国休斯顿大学合作办学，学员前两年在本院学习，后两年到美国学习（9',121212);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津轻工职业技术学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjlivtc.edu.cn/','天津轻工职业技术学院是2001年经天津市人民政府批准、教育部备案成立的公办全日制普通高等职业技术学院。学院坐落于国家职业教育改革创新示范区--天津海河教育园区，占地面积800亩，建筑面积16万平方米，良好的教学条件，完善的基础设施，为学生营造了优越的学习、实践和生活环境。学院以\"注重内涵建设，立足行业发展，服务滨海新区\"为办学指导思想，走出了一条品牌建设、特色发展的办学之路。学院是天津市职业教育先进单位，天津市示范性职业院校；2010年被教育部、财政部确定为国家百所示范性骨干高职院校立项建设单位',221718);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京科技经营管理学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://1985edu.good-edu.cn/','北京科技经营管理学院，1985年建成并开始招生。2001年4月5日经国家教育部批准，成为一所具有独立颁发国家承认学历证书资格的全日制普通高等学校（中华人民共和国教育部教发函［2001］49号文件），面向全国统一招生。学院现已发展成为文、理、工、经、管、艺等多学科协调发展的普通高等院校。目前，学院拥有中关村、沙河大学城南和沙河大学城北三处校区，毗邻中国人民大学、北京师范大学、中央财经大学、北京理工大学、北京航空航天大学、北京邮电大学、外交学院等多所著名大学。学院环境优美、交通便利，学术气氛浓厚、教',441789);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('荆州职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.jzit.net.cn/test/index.asp','荆州职业技术学院是经国家教育部批准设立的一所面向全国招生的全日制公办普通高等院校，办学历史悠久,人文底蕴深厚。学院坐落于沃野千里、美丽富饶的江汉平原腹地的历史文化名城荆州，东接武汉，西望三峡，南濒长江，地理位置优越，交通便捷。学院2003年被财政部确定为“中央与地方共建高校”，拥有“数控技术、电工电子、计算机应用与软件技术”三个国家示范性实训基地；是湖北省人民政府确定的“湖北省高技能人才培养示范基地”；是湖北省省级文明单位和湖北省农村劳动力转移就业阳光工程示范基地；学院团委被团中央命名表彰为“全',81284);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉工程职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.wgxy.net','学院简介：武汉工程职业技术学院是2001年经湖北省政府批准，教育部备案的一所以工程学科为主的全日制普通高等专科(高职)学校。学院位于长江之滨，既揽江城之秀美，更拥钢城之壮阔。学院占地1172亩。教职工798人，其中：专职教师445人，教师中副教授以上职称者160人，具有研究生学历或硕士学位的80人，符合“双师型”教师280人，享受武汉钢铁(集团)公司专家津贴的“科技三种人”12人，武汉市学科带头人7人。学院常年从生产厂矿、科研院所、高等院校聘请高级工程师、专家学者来院讲学任教，并长期聘请外籍教师',238025);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南环境生物职业技术学院',' ','湖南',0,2,0,0,0,' ','暂空',' ','http://www.hnebp.edu.cn','院创办于1975年，坐落在五岳独秀的南岳衡山，前身为湖南林业专科学校，。2001年3月经湖南省人民政府批准、国家教育部备案更为现名，2004年4月经省人民政府批准原衡阳市卫生学校并入我院，2004年教育部组织的人才培养工作水平评估获得优秀并经复查认定。同时我院为中国林业科学研究院专业硕士湖南培养基地，招生农业推广（林业和区域经济方向）和风景园林专业硕士生。2007年9月，我院经省人民政府批准为省级示范性高等职业学院项目建设单位。近年来，学院先后被评为全国职业教育先进单位、全国绿化模范单位、湖南省',162147);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('仙桃职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbxtzy.com','仙桃职业学院是经湖北省人民政府和国家教育部批准成立的一所公办全日制普通高等院校，坐落在驰名中外的“中国体操之乡”——湖北省仙桃市，即奥运冠军李小双、李大双、杨威、郑李辉、廖辉的家乡。学院所在地东连武汉、西接荆州、南眺长江、北依汉水，风景秀丽，交通便捷。校园建设气势恢弘。学院占地面积1000亩，校舍面积36.5万平方米，拥有科技大楼、教学大楼、医学大楼、信息大楼、艺术大楼、成教大楼、学生活动中心、图书馆、学术交流中心等现代化大型建筑。教学、生活、娱乐场地，区域分明，功能完善；自然、建筑、人文景观，',65079);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东建设职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdcvi.net','广东建设职业技术学院，是经省人民政府批准的全日制公办普通高等院校。属大学专科层次，主要培养适应建筑业和社会发展需要的生产第一线的实用型、创新型高级技术和管理人才。环境优美，设施完善。学院位于广州市白云区新广花二路638号。校园占地面积6万?，建筑面积7．5万多?，在校生3500多名，教职工300多名。教学楼、实验楼、图书馆、体育馆、计算中心、网络中心、语言中心、校内实训基地配套齐全。还建有400m环形跑道的田径场和各种类型的运动场地，能满足学生运动的需要。学生宿舍为公寓式宿舍，有独立的卫生间和冲',260493);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东女子职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://zsxx.gdfs.edu.cn/','广东女子职业技术学院是由广东省妇联主办、经广东省人民政府批准成立的华南地区唯一一所公办女子高校。学校至今有30多年的办学历史，其前身是1981年由广东省妇联创办的广东省妇女干部学校和1992年成立的广东女子中等专业学校，2001年升格为广东女子职业技术学院。2013年11月，学校被确定为省级示范性高职院校建设单位。2015年12月，学校被认定为广东省依法治校示范校。学校主校区位于广州市番禺区，毗邻广州亚运城，校园环境幽雅，办学条件优良，教学设施完善。校内建有126个专业实训室，8个“校中厂”,1',318412);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东机电职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdmec.cn','广东机电职业技术学院是一所经广东省政府批准设立、国家教育部核准备案的全日制公立普通高等院校。学院办学历史悠久，办学力量雄厚。办学思路抓改革创特色，抓内涵促发展，抓管理促效益办学目标建成办学特色鲜明、在省内有较高知名度和认可度的示范性高职学院。办学条件学院校现有在校生规模6055人；占地92.7亩，总建筑6.8万平方米；专任教师212名，兼职教师43名；专业33个；各类教学仪器设备3172.86万元；图书55万余册；建有校园网；有校内实训中心10个，并与一些知名企业合作，建立了54个校外实训基地；',655022);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北轻工职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbliti.com','湖北轻工职业技术学院是湖北教育厅直属的公办全日制普通高等职业技术院校。学院创办于1956年，前身隶属于原国家食品工业部，1972年更名为“湖北省轻工业学校”，隶属于湖北省一轻工业局；1987年与原联邦德国“汉斯赛德尔基金会”合作，引进联邦德国“双元制”教学模式；1996年经教育部批准试办五年制高职；2001年4月,经湖北省人民政府批准组建“湖北轻工职业技术学院”，隶属湖北省轻工行业管理办公室；2004年划转到湖北省教育厅直属管理。学院占地面积163.4亩，其中马房山校区153.1亩，卓刀泉校区1',67859);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('商丘职业技术学院',' ','河南',0,6,0,0,0,' ','暂空',' ','http://www.sqzy.com.cn','“国家示范性高等职业院校”商丘职业技术学院是2001年经河南省人民政府批准、教育部备案的一所独立设置的公办全日制高等职业院校。2006年，学院在河南省高职高专院校人才培养工作水平评估中，以各项评估指标全优的成绩被评为优秀院校；2007年被教育部、财政部确定为国家示范性高等职业院校立项建设单位。学院2003年被教育部批准建立国家技能型紧缺人才培训基地；2004年经河南省教育厅批准成立商丘示范性软件职业技术学院；2005年、2006年中央财政支持建立职业教育实训基地2个，省财政支持建设实训基地3个；',102467);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州科技学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.zzist.net/','郑州科技学院是教育部批准成立的本科高校，创办于1988年，具有20多年办学历史，由全国人大常委会副委员长韩启德院士任首席顾问。学院先后被评为“全国民办高校先进单位”、“中国民办高校综合实力20强”、“全国学生就业示范民办高校”，2008年被评为“河南省大中专就业工作先进集体”、“中国十大品牌本科民办大学”、“中国就业质量十强民办大学”。学院地处郑州市二七区，校园环境幽美，占地1200亩，校舍建筑面积40万平方米，有电教中心，实验实训中心和文体中心。学院设本、专科专业35个，在校生18000余人，',1005463);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('平顶山工业职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.pzxy.edu.cn','学院简介：平顶山工业职业技术学院是由国家教育部备案、河南省人民政府批准成立的公办全日制高等职业技术院校，是河南省高职高专教学水平评估优秀学校、国家示范性高等职业院校、全国文明单位和全国高校毕业生就业工作先进单位、全国就业竞争力示范院校。学院校区占地面积1454亩，建筑面积41.27万平方米，固定资产总值4.48亿，教学仪器设备总值1.84亿，图书馆藏书107.04万册。现有机电类、建筑类、汽车类、计算机类、电子信息类、管理类、医学类、食品类、化工类、服装、艺术类、矿山类11大专业群，开设专业55',466738);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东岭南职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.lingnancollege.com.cn','广东岭南职业技术学院是经广东省人民政府批准成立，国家教育部备案，面向省内外招收高等学历教育国家任务生、具有学历证书颁发权的全日制综合性省属普通高等院校。学院根据国家对高等职业技术教育的任务和培养目标要求，致力于培养我国经济建设和管理一线急需的、适应国际需求的高等技术应用型专门人才。全校现设信息工程学院、现代制造学院、经济管理学院、外语外贸学院、艺术设计学院、药学院六个二级学院和一个基础课部，开设33个大专业，50多个专业方向，现有全日制大专在校生8400余人。校内还设置有继续教育学院，它同时是国',338882);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('周口职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zkvtc.edu.cn/','周口职业技术学院是经河南省人民政府批准成立，国家教育部备案，具有普通高等学历教育招生资格的全日制公办高等职业技术学院。学院坐落在“三皇故都”周口市，地理位置优越，校园环境幽雅，师资力量雄厚，教学设施完善。学院占地面积1325亩（北校区806亩,南校区519亩），总建筑面积23.3万平方米，固定资产总值4.4亿元，馆藏图书48.6万册，教学仪器设备总值3908万元，校内实验实训场所48个，校外实习实训基地59个，多媒体教室和语音室座位2919个，教学用计算机878台，标准田径运动场2个，篮球、排球',47514);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川机电职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.scemi.com','“国家示范性高等职业院校建设计划”骨干高职院校四川机电职业技术学院是专科层次普通高等院校。学院地处中国西部钢铁钒钛之都??四川省攀枝花市，坐落在景色秀丽的金沙江南岸。总占地886亩。校园绿树成荫，花香四溢。有马家田、金江两大校区。固定资产超过1．3亿元。有与专业配套的实习室55个，实训基地32个，先进设备或系统927台(套)，计算机780台。图书馆藏书20万册，图书检索实现计算机管理。建有与国际互联网相连的校园网。有400米和300米跑道绿茵运动场各1个，标准游泳池2个，多功能室内体育馆2座，沙',123983);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北交通职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbctc.edu.cn/','学院是经湖北省人民政府批准、教育部备案的全日制公办普通高等学校，由湖北省交通运输厅主办，是交通运输部交通职业教育示范院校和湖北省示范性高等职业院校，是湖北省公路交通职业教育品牌建设单位。学院以“重德、励志、笃学、尚能”为校训，坚持“立足交通，面向社会，以服务为宗旨，以就业为导向，走产学研结合之路”的办学指导思想，坚持“育人为本、德育为先，质量为本、技能为先，以人为本、服务为先”的办学理念。60年来共培养毕业生4.5万名，为交通运输事业和社会经济发展提供人才保障，被誉为“湖北交通人才的摇篮”。全国',507884);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('绵阳职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.myvtc.edu.cn/','“国家示范性高等职业院校”学院是经四川省人民政府首批批准成立的、以专科层次为主体的全日制公办普通高等学校，省级文明单位。在校学生8000余名。2005年我院被国家教育部等七部委评为“全国职业教育先进单位”，在国务院召开的全国职教大会上被隆重表彰。我院连续三年毕业生就业率都在95%以上，学生就业渠道广，待遇好，在四川省教育厅对全省普通高校的排名中名列前茅。学院现占地面积800余亩(含新征地400亩)。建有教学大楼、实验大楼、图书馆、学术礼堂、标准化田径运动场、游泳池、实训工厂等，教学设施完善，校园',251241);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆海联职业技术学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.ccca.edu.cn/','重庆海联职业技术学院创建于1999年，是经重庆市人民政府批准、教育部备案，纳入国家招生计划、面向全国招生的民办全日制普通高等专科院校。2009年经重庆市人民政府批准西南政法大学全面接管了学院，成为学院的投资者、举办者和管理者。2013年9月，四川泛美教育集团和重庆斌鑫集团有限公司成立重庆新元投资有限公司融入资金与西南政法大学共同举办学院。四川泛美教育集团是一家专门培养航空人才的大型教育集团，我院将借助四川泛美教育集团航空教育的资源优势和西南政法大学的教育管理优势，共同努力开创航空职业教育的崭新篇',236685);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆信息技术职业学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqeec.com/','重庆信息技术职业学院是经重庆市人民政府批准、教育部公布备案的全日制普通高等院校（教育部国标代码：12755），是国家技能型紧缺人才培养院校、重庆市示范性软件学院、重庆市高职单独招生院校、重庆市高技能人才培养基地、重庆市笔记本电脑人才培养基地、毕业生定向就业培训定点培训机构；学院位于重庆市第二大城市、千古文化古都万州，占地近1000亩，在校生近10000人，面向全国招生。学院环境优美、山水相映、风景如画，是一所典型的花园式大学。学院倡导敬天爱人和忠孝仁义，恪守“忠诚、专业、团队、责任、荣誉、国家”',290225);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆传媒职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqcmxy.com/','重庆传媒职业学院是重庆市唯一一所以文化传媒类为主，艺术、经管等多学科相互渗透，突出应用和创新特色的全日制普通高等院校（教育部代码12756），国家计划内统一招生。学院位于充满活力和机遇的中国最年轻的直辖市、国家五大中心城市之一的重庆，校园地处重庆三环内铜梁教育园区，属于“半小时经济圈”核心扩散区，距市中心仅四十分钟车程，交通畅达，环境优美。学院教育教学设施齐全、设备先进，建有标准化图书馆、电子阅览室、高标准配置演播室、摄影摄像室、影视后期制作室、微机室、语音室、电视节目制作室、画室、艺术创意室、',142992);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆警察学院',' ','重庆',1,11,0,0,0,' ','暂空',' ','http://www.cqjy.com.cn/','重庆警官职业学院是一所由重庆市人民政府主办，重庆市公安局和重庆市教育委员会共同管理、以市公安局管理为主的全日制公安类普通高等职业学院。学院于2001年3月在原重庆市公安学校和原重庆市人民警察学校的基础上建立。学院现拥有石桥铺和松山两校区，共占地240885平方米（361亩），有建筑总面积45461.97平方米，固定资产总值约5538万元，其中教学设备总值643.50万元。学院图书馆面积达3487.80平方米，藏书16.72万册。建有400米跑道标准田径足球运动场、战术攀登训练墙、篮球场、机动车训',182977);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆城市管理职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cswu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校重庆城市管理职业学院是由重庆市人民政府主办、国家民政部与重庆市人民政府共建、重庆市教委直属的一所专科层次的全日制公办普通高等学校，是高职高专人才培养工作水平评估优秀院校、全国职业教育先进单位、中澳职教项目C-TAFE办学模式试点院校、国家技能型紧缺人才培养院校、重庆市市级示范性高职院校建设单位、全国普通高校毕业生就业工作先进集体，是西部地区培养城市管理和社会工作服务型高技能专门人才的主要基地。学校始建于1984年，原为国家民政部创办的民政部重庆民政学',170618);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆工程职业技术学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqvie.edu.cn/','重庆工程职业技术学院是由重庆市人民政府举办，重庆市教育委员会主管的公办全日制普通高等院校。学院1951年由煤炭工业部创办，1998年举办高等职业教育，2001年成为独立设置的高等职业院校。2005年接受教育部高职高专人才培养工作水平评估，荣获优秀等级。2007年入选国家示范性高等职业院校建设单位，2010年通过教育部、财政部验收，建成国家示范性高等职业院校。现有全日制高职专科在校学生12000余人，全日制本科在校学生176人，成人教育学生3000余人，非全日制成人学历教育学生和各类培训学员850',899031);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川交通职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.svtcc.edu.cn','“国家示范性高等职业院校”四川交通职业技术学院是经四川省人民政府批准建立的普通高等院校，国家公办，始建于1952年。隶属于四川省交通厅。学院是全国职教先进单位，2007年被教育部、财政部确定为国家示范性高等职业院校建设单位。规模学院位于美丽富饶的国际花园城市成都市温江区。校园占地1033亩；全日制在校生近12000人；设有道桥系、汽车工程系、经济管理系、计算机工程系、人文社会科学系、建筑工程系、自动化工程系、航运工程系（筹）等8个系，开设了社会急需的40个专业。学院现建有国家级精品专业1个，教育',473019);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川工商职业技术学院',' ','四川',0,12,0,0,0,' ','暂空',' ','http://www.sctbc.net/','四川工商职业技术学院是国家教育部授权四川省人民政府批准建立的公办全日制普通高等学校，是全省首批独立升格的高等职业技术学院。学院位于风景秀丽的世界文化遗产地、历史文化名城、中国优秀旅游城市、中国最佳魅力城市、中国长寿之乡——都江堰市。距西南政治经济文化中心——成都市仅20分钟车程。学院社会声誉良好是教育部人才培养水平优秀级学校，部、省、市职教先进单位，全国体育卫生工作先进单位，省级最佳文明单位和省级校风示范校，形成了培养具有“正确的价值取向、良好的信息素养、过硬的专业能力、积极的生活态度”的专门人',177463);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川工程职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.scetc.net/','一、学校简介四川工程职业技术学院是一所以工为主，装备制造、能源动力与材料、电子信息、土木建筑、交通运输、财经商贸、教育与体育、旅游、文化艺术等多科型协调发展的公办全日制普通高等学校，全日制高职专科面向全国高考招生，位于国家重大技术装备制造业基地～四川德阳（距成都市40余公里），校园占地1200余亩，全日制在校学生1.2万余人，隶属于四川省经济和信息化委员会。悠久的办学历史学校始建于1959年，是五十多年前国家在西南与中国二重、东方电机、东方汽轮机等大型国有企业一起布点的学校。1960-1962年',565959);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川建筑职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.scatc.net/','四川建筑职业技术学院是公办全日制高职院校，隶属于四川省住房和城乡建设厅。学院源起于1956年成立的成都城市建设工程学校，1958年更名为成都建筑工程学校。1963年迁建至四川德阳，1980年更名为四川省建筑工程学校，并被教育部确定为全国重点中专。1992年被四川省委、省政府命名为省级文明单位，1994年再次被原国家教委确定为重点中专。2001年，经四川省人民政府批准，原四川省建筑工程学校、四川省建筑职工大学、四川省城市建设学校升格为四川建筑职业技术学院。2003年，原中国第二重型机械有限集团公司',651787);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('汕尾职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.swvtc.cn/','汕尾职业技术学院座落在风光旖旎的粤东滨海城市----广东省汕尾市城区，是一所由汕尾市人民政府创办，省政府批准，国家教育部备案的专科层次公办普通高等院校。学院确立“以人为本”理念，以促进学生健康成长为宗旨，关注学生全面和谐发展。树立“人才培养质量是学院生存与发展生命线”的观念，注重抓好教学质量，严抓考风，促进教风、学风教育和良好校风的形成。推行“双证书”制，积极组织开展行业职业资格和专业技术等级考核认证，提升毕业生专业技能素质和就业竞争力。学生实践动手能力强，参加相关比赛获得较好成绩，如参加“挑战',103544);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('达州职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.dzvtc.edu.cn','达州职业技术学院是一所公办综合性全日制普通大学,学院有数十年的办学底蕴，厚积了较强的办学实力。2006年面向全国20多个省（市）招收大学生。学院座落于风景秀丽的洲河之滨——达州市，到达渝高速公路、飞机场、火车站仅一步之遥。学院现占地面积441亩，实习基地125.8亩，校舍建筑面积近20万平方米。学院有现代化的教学设施，建有先进的语音室、多媒体室、计算机中心等各类综合性实验室、专业实验室58个，校外实训基地35个，有各类生产及教学设备590台（套），可同时容纳2400名学生进行各种专业实习实训。计',328758);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('济源职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.jyvtc.com','济源职业技术学院是经国家教育部批准的一所公办普通专科院校，位于中原城市群之一的河南省济源市。学院占地面积767亩，全日制在校生12000余人。教职工616人，专任教师468人，兼职教师106人，河南省学术带头人6人，教授、副教授130余人，具有硕士以上学位教师140余人，“双师素质”教师150余人。学院设有11个教学系部，27个高职专业，其中河南省教改试点专业3个，河南省“工学结合、半工半读”改革试点专业3个。学院设有机械电子、计算机科学、商务与旅游、冶金化工、现代教育与信息等八大实验实训中心；',68714);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('罗定职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.ldpoly.com','罗定职业技术学院是一所以教育类专业为基础，理、工、经、管、文综合发展的公立全日制普通高等专科院校。其前身是创建于1928年的广东罗定师范学校，是一所历史悠久并在省内外享有盛誉的中等师范学校，2001年5月升格为罗定职业技术学院。2001年以来，全体教职工秉承“艰苦创业，勤俭办学，敢于人先”的传统，以发展为主题，以改革为动力，勇于开拓，艰苦奋斗，学院先后被评为“广东省社会科学普及示范基地”、“广东省中小企业培训示范基地”和“广东省语言文字工作先进单位”。学院位于西江南岸，坐落于广东省首批历史文化名',149564);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阳江职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.cnyjpt.cn/zsb/index.html','阳江职业技术学院是2001年5月经广东省人民政府批准设立，阳江市人民政府举办的综合性普通高等学校，学院实行党委领导下的院长负责制。目前有英语教育、语文教育、数学教育等6个师范类专业，有旅游管理、模具设计与制造、数控技术等24个非师范类专业，全日制在校学生6790人。设有中文系、艺术与管理系、外语系、计算机科学系、机电系、数学系、生命科学与技术系、思想政治理论课教学部、体育部等9个教学系（部）。现有在岗教职工395人，学院在编专任教师296人中，有硕士研究生54人，教授2人，副教授、高级工程师等6',183421);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河源职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.hycollege.net/','河源职业技术学院位于有“青山绿湖碧水城”美誉的全国优秀旅游城市——广东省河源市市区，是2001年经省人民政府批准，在具有70多年办学历史的广东老隆师范学校的基础上设立的，属国家教育部注册、地方政府投资的一所公办全日制高等职业院校。学院占地面积1500亩，背靠梧桐山，面向东江河，紧依东环路，风光旖旎，交通便利，校园内教学大楼、学生公寓、师生饭堂、图书馆、实训中心、室内体育馆等基础设施一应俱全，人工湖、贤能广场、“铭德榕”、“桃李园”“双馨园”、“书香园”等人文景点蕴寓丰富的文化内涵。得天独厚的地理',197133);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('秦皇岛职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.qhdvtc.com/','“国家示范性高等职业院校建设计划”骨干高职院校秦皇岛职业技术学院是2001年4月30日经河北省人民政府批准、国家教育部备案的全日制公办高等职业技术学院。学院自成立以来多次被评为省市级“文明单位”、“职业教育先进单位”、“模范基层党组织”。2005年被教育部、人事部、劳动和社会保障部等七部委联合授予“全国职业教育先进单位”荣誉称号。学院2003年整体搬迁到著名避暑胜地——“中国夏都”北戴河，占地708亩，建筑面积12.85万平方米。现有全日制在校大专生7837人，生源来自河北、北京、上海、内蒙、陕',315193);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('晋城职业技术学院',' ','山西',0,0,0,0,0,' ','暂空',' ','http://www.sxjczy.com.cn','晋城职业技术学院坐落在衔接山西和中原大地、被誉为“太行明珠”的山西省晋城市，是2001年5月17日经山西省人民政府批准、国家教育部备案，由原晋城市教育学院、晋城师范学校、晋城市中等专业学校、晋城市文化艺术学校四校合并组建的一所以高等职业技术教育为主体，集师范教育、艺术教育为一体的区域性多科类全日制高职院校。学院现有在校生7600人,专任教师374人，其中具有高级职务以上的教师78人，中级职称163人，具有硕士以上学位的203人(包括在读).“诚明乐业”是学院的校训，“以学生为根本，以发展为核心，',134946);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西建筑职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://zsc.sxatc.com/','山西建筑职业技术学院是国家示范性骨干高职院校，也是山西省办学历史最长、规模最大的高职学院之一，前身是成立于1952年的国家级重点中专山西省建筑工程学校，隶属于山西省住房和城乡建设厅，业务上受山西省教育厅领导。学院占地693.8亩，其中老校区占地188.8亩，新校区占地505亩。全日制在校生9411人，本专科函授生2000余人，年职业培训规模达到1万人次以上。设有建筑工程系、市政工程系、建筑与艺术系、机电工程系、设备工程系、工程管理系和计算机工程系等7个系30个专业;实验实训教学部、思想政治理论教',393529);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西工程职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.sxgy.cn','山西工程职业技术学院是国家示范性高等职业技术学院，具有60多年职业教育办学历史，隶属于山西省教育厅。学院位于太原市中心，东邻龙潭公园，西靠汾河景区。60多年来，学院秉承“励学重行”的校训，积淀了深厚的文化底蕴，积累了丰富的职业教育经验，造就了一支高素质的教师队伍，为国家培养了47000余名大中专毕业生,被誉为“钢铁摇篮”。2010年8月学院顺利通过教育部、财政部的验收，成为全国100所“国家示范性高等职业院校”之一。学院设有冶金工程、机械工程、电气工程、建筑工程、计算机工程、管理工程六个系，现开',129874);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西交通职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.sxjt.edu.cn','学院概况：山西交通职业技术学院是山西省唯一一所国家教育部备案的交通类公办全日制普通高等职业技术学院，隶属于山西省交通厅，教学上受教育部、山西省教育厅指导。前身为创办于1958年的山西省交通学校。学院坚持“服务交通行业建设，融入山西经济发展，挖掘人才需求走势，锁定市场设置专业，瞄准岗位培养人才，强化技能突出特色，坚持质量立校观念，实现协调持续发展”的办学理念，培养面向生产建设、管理、服务第一线需要的、下得去、留得住、用得上，实践技能强、具有良好职业道德的技术应用型人才。学院坚持以人为本理念，实施“',171082);
