insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南司法警官职业学院',' ','河南',0,11,0,0,0,' ','暂空',' ','http://www.hnsfjg.com','河南司法警官职业学院是以高等司法警官教育为主，多种职业教育并举，培养应用型人才的全日制普通警察院校。1984年，为了适应不断发展的司法行政工作的需要，河南省人民政府批准成立河南省劳改警察学校，后更名为河南司法警官学校。2001年，我校被批准升格为河南司法警官职业学院。学院占地818亩。正在建设中的郑东新区新校园占地500余亩，总投资2.8亿元，规划建筑面积16.3万平方米，建成之后，一座现代化、生态化、信息化、园林式的新校园将屹立在龙子湖高校园区，为我院提供广阔的发展空间。学院设有五系两部，包括',164974);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北传媒学院',' ','河北',1,8,0,0,0,' ','暂空',' ','http://www.hebic.cn','河北传媒学院是一所以传媒、艺术类专业为主，艺术学、文学、工学、管理学等多学科兼容的普通全日制本科院校，教育部备案的国际代码为12784。学校建于2000年，2007年3月经教育部批准升为本科院校，更名为河北传媒学院，2009年6月获得学士学位授予权。2011年10月经国家学位委员会批准获得全日制硕士研究生招生资格，成为全国首批进行硕士研究生教育的民办高校，进入国家高端人才培养行列。学校位于河北省石家庄市，设有兴安、警安和学府三个校区。学校现有影视艺术学院、新闻传播学院、艺术设计学院、舞蹈艺术学院',803935);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('唐山职业技术学院',' ','河北',0,3,0,0,0,' ','暂空',' ','http://www.tsvtc.com','唐山职业技术学院，是国办全日制普通高等学校，实施专科层次高等职业技术教育。学院位于具有百年历史的沿海重工业城市----唐山市。唐山处于环渤海湾中心地带，南临渤海，北依燕山，距北京、天津各百余公里，是环渤海经济圈的重要组成部分，被誉为“中国近代工业的摇篮”和“北方瓷都”。学院占地面积300余亩，建筑面积10万余平方米，固定资产12650万元，其中教学设备总值2300万元。学校教学设备先进，建成了校园网和105个专业实验室及模拟实验室，拥有计算机1280台，图书馆藏书27.14万册。学院有教职工81',147743);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('衡水职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hsvtc.cn','衡水职业技术学院是国办全日制高等专科学校。建有高标准的综合教学楼，实训楼，图书楼，学生公寓及带有大型看台的标准运动场，同时拥有大型计算机信息中心、电教中心、宽带校园网，多媒体教室，财会、电子、制冷、数控、农林、工艺美术等现代化专业实验室。图书馆藏书13万余册，中外文报刊、杂志500余种。学院师资力量雄厚，拥有一支专兼结合、素质优良的师资队伍。在教学中坚持理论与实践并重，注重学生实际操作能力的培养，学生修业期满颁发国家正式的大学专科毕业证书及相关技术等级证书，同时学生还可以参加全省专接本考试，继续',70506);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('唐山工业职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.tsgzy.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校唐山工业职业技术学院座落在河北省唐山市。唐山——“中国近代工业之摇篮”，南临渤海，北靠燕山，毗邻京、津，是“大北京”发展战略的重要组成部分。唐山曹妃甸港口开发已列为“国家东部沿海重点建设项目”,经济发展已走在河北省前列。唐山工业职业技术学院，是经河北省政府批准建立的普通高等专科学校。与河北省唐山市技师学院实行一体化管理，培养应用型高技能人才,毕业生同时获大专学历证书和高级职业资格证书。学院占地616亩，建筑面积17.26万平方米，教学设备值3048万',127381);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津商务职业学院',' ','天津',0,6,0,0,0,' ','暂空',' ','http://www.tcc1955.edu.cn/','天津对外经济贸易职业学院是国家批准设立的天津市唯一一所国际贸易和经贸外语类的全日制高等职业院校，隶属于天津市商务委员会和天津市教育委员会领导。2007年学院被天津市教委确定为天津市示范性高等院校建设计划项目单位。学院位于天津市河西区，占地面积244亩，同时控规122亩，建筑面积10万余平方米。校园环境幽雅，教学设施齐备。学院设有五系、三部和三个中心，即经贸外语系、国际贸易系、国际物流系、商务管理系和会计系；基础课教学部、社会科学教学部和体育教学部；信息中心、实训中心和培训中心。学院目前在校生70',353993);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江工商职业技术学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://www.zjbti.net.cn/','浙江工商职业技术学院是浙江省人民政府批准成立的一所普通高等职业院校，有着90多年的办学历史，前身为创建于1914年的“宁波公立甲种商业学校”。1975年7月，经省人民政府批准，学校直属省商业厅（现省商业集团公司）领导，成为面向全省招生和分配的中等专业学校，校名为“浙江宁波商业学校”。1998年，积极筹建“宁波工商职业技术学院”，并于同年12月获省人民政府批准。2001年5月，更名为“浙江工商职业技术学院”，成为浙江省人民政府首批批准成立的四所高职院校之一。学校位于中国商业名城、现代化的港口城市—',212184);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('台州职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.tzvtc.com/','台州职业技术学院是政府举办的全日制普通高校，教育部高职高专院校人才培养工作水平评估优秀等级学校，教育部指定“国家紧缺人才培养基地”。现任党委书记潘璋德，校长金则新，名誉校长洪起超。学校位于新兴现代化港口城市台州市开发区，占地619亩。学校下设机电工程学院、电气系、计算机系、土木系、生化系、投资理财系、工商管理系、经济贸易系、外语系、公共管理系、体艺部、艺术学院和成人教育学院。现有30个专业，文理兼容，其中机电一体化技术应用与电气自动化技术两个专业是国家级教学改革试点专业和浙江省重点专业，在校生7',165496);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江工贸职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.zjitc.net/','浙江工贸职业技术学院地处温州市，为省属公办全日制高等院校，是浙江省首批全国优秀高职院，2007年被评为浙江省首批示范高职院。学院现占地600多亩，在校生规模近7000人，专任教师284人，设有轻工系、信息工程系、汽车与机电工程系、电子工程系、经贸系、管理系、基础部、成教学院、浙江工贸技师学院、瑞立机电学院等10个教学单位。学院拥有温州冶金集团、温州冶金房开公司等非教育产业（经济实体），实现了学院教育资源与非教育资源的合理利用、优化配置，形成了校企一体、产学一体、学做一体的人才培养模式。学院确立并',64245);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江越秀外国语学院',' ','浙江',1,5,0,0,0,' ','暂空',' ','http://www.zyufl.edu.cn/ ','浙江越秀外国语学院是经国家教育部批准建立的全日制普通本科高校，也是浙江省目前唯一独立设置的外国语学院。学院座落于历史文化名城、鲁迅故乡——绍兴，1981年由“和平老人”邵力子的侄子、绍兴市著名教育家——邵鸿书先生创办，印度著名诗人泰戈尔中国唯一的学生魏风江先生曾任首任校长。先后曾荣获省级“依法治校示范校”、“平安校园”、“毕业生就业工作优秀单位”、“保卫工作创新单位”等荣誉称号。学院现占地649亩，校舍建筑面积21万平方米，馆藏图书89万册，建有多媒体室、语音室、计算机中心、卫星闭路电视系统、外',341420);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鹤壁职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.hbzy.edu.cn','鹤壁职业技术学院是经河南省人民政府批准，教育部备案的一所涵盖工、农、医、经、文、艺术等学科的综合性公办高等职业技术院校，坐落于中国优秀旅游城市鹤壁市新市区，交通便利，西靠京广铁路，东临京珠高速公路、鹤濮高速，107国道穿城而过，经济发达，环境优美，是读书就学的理想境地。学院现占地面积1200亩，总建筑面积31.5万平方米。馆藏纸质图书53.2万册，电子图书13.2万册，各类期刊1232种；学院实验实训教学设施齐全，拥有教学用计算机1587台，建有实验实训中心20个，附属医院3个，校外实习实训基地',234875);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南工业职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.hnpi.cn','“国家示范性高等职业院校建设计划”骨干高职院校河南工业职业技术学院坐落在著名的国家级历史文化名城、中国优秀旅游城市——南阳市，是隶属于河南省人民政府的一所普通高等学校。南阳历史悠久，山川秀丽，拥有众多具有深厚文化底蕴的人文景观和引人入胜的自然景观。这里物华天宝、人杰地灵。南阳独玉是中国四大名玉之一，素有“东方翡翠”之称。在辉煌灿烂的中华文明史册上，南阳是一个名人辈出的地方，商圣范蠡、科圣张衡、医圣张仲景、智圣诸葛亮、哲学家冯友兰、作家姚雪垠、二月河、发明家王永民等，都为这座美丽的城市增添了迷人的',385131);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌理工学院',' ','江西',1,0,0,0,0,' ','暂空',' ','http://zsb.nclg.com.cn/','南昌理工学院是教育部批准设置的普通本科高校，学士学位授予权单位。校园占地面积4000余亩。目前已建校舍119万平方米，馆藏图书280万册，各种教育资产总计达18亿，教学仪器设备总值1.76亿。现有在校生3万余人。学校设有16个学院5个部9个基地。学校以“航天科教，兴我中华”为办学宗旨，秉承“科学、务实、厚德、创新”的校训。学校现有专任教师1807名，副高以上职称比为33.3%，硕士以上学历比为58.7%。学校自有专任教师中，有博、硕导82人，博士69人，获国务院特殊津贴的10人，省新世纪百千万人',2044005);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北工程技术学院',' ','河北',1,6,0,0,0,' ','暂空',' ','http://www.hbfsh.com/','华北科技学院是国家安全生产监督管理总局惟一直属高校，其前身是原国家煤炭工业部1984年投资兴建的北京煤炭管理干部学院分院;1993年改制为华北矿业高等专科学校，面向全国招生;1997年被原国家教委评为全国27所示范性高等工程专科重点建设学校之一;2002年升格为普通本科院校，更名为华北科技学院;2011年获得工程硕士(安全工程领域)专业学位研究生培养资格。学校占地面积800亩，建筑面积43万平方米。教学科研仪器设备总值1.90亿元。计算机网络、多媒体等现代教育技术应用广泛，是中国教育科研网城市节',436068);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古警察职业学院',' ','内蒙古',0,11,0,0,0,' ','暂空',' ','http://www.imppc.cn/','内蒙古警察职业学院(内蒙古警察训练总队)是内蒙古自治区唯一的公安类高等院校，列入公安系列，系自治区公安厅直属参公管理单位，面向全区公安机关培养公安专门人才，同时承担全区公安民警在职培训、公安理论和实战研究及应急处突等职能。人财物由自治区公安厅管理，教育行政业务由自治区教育厅管理。学院前身是1948年成立的内蒙古公安部直属训练科。1980年改建为内蒙古自治区人民警察学校。2001年经自治区人民政府批准组建为内蒙古警察职业学院。2002年原内蒙古自治区司法学校并入学院。学院为副厅级建制，内设正处级机',134628);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海济光职业技术学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.shjgu.edu.cn/','上海济光职业技术学院是1993年经上海市人民政府批准设立、2001年纳入国家统一招生计划的全日制普通高等院校。首任董事长为土木建筑领域著名专家、两院院士、同济大学名誉校长李国豪；现任董事长为原上海市副市长夏克强。学院坚持公益性、非营利性办学原则。学院资产均系社会公益性资产。学院坚持“求实求新，人人成才”的办学理念，以务实精神办好高职，以创新意识培育人才，以社会责任取信于民，赢得了社会信誉和赞扬。学院被评为上海市特色高职院校建设校，上海市民办高校示范校创建校，荣获中国民办高等教育优秀院校，上海市文',242411);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海建桥学院',' ','上海',1,6,0,0,0,' ','暂空',' ','http://www.gench.edu.cn','上海建桥学院(ShanghaiJianqiaoUniversity)是一所以本科教育为主体、专科教育和继续教育为两翼，培养生产、管理、服务第一线应用型专门人才的多科性民办全日制普通高等学校。学校创办于2000年4月，由上海建桥集团出资举办。2001年4月，学校获批为上海建桥职业技术学院，主要从事专科层次的高等职业教育;2003年学校被列为上海市11所示范性高职院校建设单位之一。2005年9月，经上海市人民政府批准、国家教育部备案，同意在上海建桥职业技术学院的基础上建立上海建桥学院，学校成为以本科',1250106);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海工商外国语职业学院',' ','上海',0,5,0,0,0,' ','暂空',' ','http://zs.sicfl.edu.cn/','上海工商外国语职业学院成立于2001年，投资总额5.4亿元，占地面积304亩，面向全国30个省份招生，现有在册学生7400余人，是上海市办学规模最大的民办高校之一。已为社会输送21000多名合格毕业生，近三年新生录取报到率在85%左右，毕业生就业率在99%以上，连年列上海民办高校第一名，居全市高职高专院校前列。学院现有专、兼职教师360余人，专任教师中教授14人，副教授39人，博士9人，硕士研究生109人，外籍教师近20人，留学归国教师27人，形成了一支数量保证、结构合理、素质精良的师资队伍。学',156754);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海科学技术职业学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.scst.edu.cn/','上海科学技术职业学院是经上海市人民政府批准、国家教育部备案的一所公办全日制高等职业院校。2012年7月被上海市教委确定为“上海市特色高等职业院校”建设单位。学院地处上海市有“国际汽车城”之称的嘉定城区，目前在校高职生四千余人。校园环境优雅，是上海市“花园单位”。学院前身是建于1959年的“上海科技专科学校”。2003年至今连续保持“上海市文明单位”荣誉。连续13年被授予“上海市安全文明校园”称号。学院设有商贸管理学院、通信与电子信息、机电工程、人文与社会科学四个院（系），下设20余个专业。目前已',174305);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津国土资源和房屋职业学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjgsxy.com.cn','天津国土资源和房屋职业学院是一所经天津市人民政府批准，教育部备案，天津市国土资源和房屋管理局举办的公办全日制普通高等职业院校，招生纳入全国高等学校统一招生计划。目前在校生达6600余人。近年来，学院按照职业教育规律办学，加强软、硬件建设，树立“为学生成才成功精心策划，为学生终身发展高度负责”的精神，在办学质量和特色上下功夫，用爱心和责任培养每一位学生成人成才成功，让培养出来的学生真正满足经济社会的需求。学院特色：学院是全国房地产行业培训中心、天津滨海新区干部培训基地、天津市奥林匹克教育示范校和首',209897);
