insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮安信息职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.hcit.edu.cn/','淮安信息职业技术学院是由江苏省人民政府主办、江苏省经济和信息化委员会主管的全日制高等学校，肇建于1978年。2005年获评江苏省首轮高职高专人才培养工作水平评估“优秀”院校;2007年获选江苏省首批示范性高职院校建设单位。学校七次获评“江苏省高校毕业生就业工作先进集体”，毕业生就业竞争力跻身全省公办专科院校六强。学校占地1080亩，坐落在国家大学科技园—淮安科教产业园，设电子工程、计算机与通信工程、商学、国际交流、机电工程、电气工程、汽车工程、传媒艺术等二级院系，现有在校生11000余人，建筑面',65913);
