insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州工业园区职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.ivt.edu.cn','苏州工业园区职业技术学院(英文简称SIPIVT)，坐落于中国最具竞争力的国家级开发区——中新合作苏州工业园区，由新加坡总理吴作栋先生亲自提议，1997年经江苏省人民政府批准设立。学院成功借鉴新加坡南洋理工学院的办学模式，形成了混合所有制、校企紧密融合、国际合作交流三大办学特色。学院遵循“国际职教理念、本土创新实践、区域成果分享”的办学思路，紧贴园区产业的需求和学生可持续发展的要求，立足园区，面向长三角，辐射全国，为区域经济发展培养了2万多名具有国际竞争力的高素质技术人才，获得企业、学生、政府、社',97495);
