insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽体育运动职业技术学院',' ','安徽',0,7,0,0,0,' ','暂空',' ','http://www.ahty.net/','安徽体育运动职业技术学院是我省唯一一所省属专科全日制高等体育院校，隶属安徽省体育局。学院的前身安徽省体育学校，创办于1954年，是全国第一批体育中专学校。50多年来，随着形势的发展，学校历经数次变迁，2001年报经安徽省人民政府批准成立安徽体育运动职业技术学院。学院的办学目标是，培育高水平体育后备人才，培养“高精尖”竞技体育人才，培训与体育行业有关的各类实用技能人才。安徽省编委批准学院编制为278个。目前，拥有教职工数251人，离退休101人。专业技术人员中，高级职称61人，中级职称76人，初级',43324);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('滨州职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.bzpt.edu.cn/','滨州职业学院是2001年7月经山东省人民政府批准，合并滨州农业学校、卫生学校、工业学校、经济学校和滨州市农业科学研究所组建的全日制普通高等学校，坐落于渤海之滨的黄河三角洲中心城市—滨州市。学院占地面积3549.71亩，建筑面积80.35万平方米，总资产17.8亿元，教学、仪器设备总值1.5亿元，图书馆藏书184万册。学院编制949人，在编在职教职工870人，专任教师668人，其中教授35人、副教授222人，博士11人、硕士271人、国家有突出贡献的中青年专家1人、省有突出贡献的中青年专家2人、国',358235);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东科技职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdzy.com.cn/','山东科技职业学院简介山东科技职业学院是一所省属公办全日制普通高校，国家示范性高等职业院。学院坐落于“世界风筝都”——潍坊，始建于1978年。学院在校生13000名，办学涉及纺织服装、外语、经济管理、机电、机械、土木、生物与化学、信息、水产、艺术传媒、汽车等11个领域、49个专业。优秀的学院品牌：学院被授予全国“工人先锋号”、全国教育改革创新典型学校、全国职业教育管理创新学校、全国职业院校就业竞争力示范院校、全国高校学生公寓管理服务工作先进单位、全国百强职校、省级文明单位、全省职业教育先进集体、山',272518);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆房地产职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqfdcxy.com','重庆房地产职业学院成立于1984年，前身为重庆科技进修大学，由重庆高校退休老年工作者协会创办，2001年经教育部批准建立重庆巴渝职业技术学院，2005年由重庆鸥鹏实业有限公司出资举办，2008年更名为重庆房地产职业学院。学院是由重庆新鸥鹏集团与重庆大学按混合所有制模式组建的高等教育机构，主管单位为重庆市教育委员会，属非营利性非企业法人单位。建校三十年来，已培养了2.4万面向房地产及相关产业的高素质技术技能型人才，为推动我市经济建设和社会发展作出了积极贡献。是重庆市民办高校中唯一一所市级示范（骨干',144669);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安顺职业技术学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.asotc.cn','学院现设有应用医药系、护理系、经济管理系、农业工程系、信息与电子科学系、旅游系、现代工程系等七个系26个大专专业和32个中专专业；现有全日制在校学生7000余人；学院师资力量雄厚，有专职教师286人，正教授1人，副高以上职称教师60余人，具有博士、硕士学历和在读硕士研究生57人；学院教学设施先进，有各类专业实验室60余个，实训基地50余个，计算机600多台；学院具有多种职业技能培训和鉴定资质，是省级劳动力转移培训示范基地。学院将职业教育定位于就业教育，以培养应用型人才为目标，实行学历证书与职业资',97604);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黔东南民族职业技术学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.qdnpt.com','山川秀丽，气候宜人，资源丰富，民族风情浓郁，素有“森林之州”、“歌舞之州”美誉，被世界乡土文化保护基金会授予的全球18个生态文化保护圈之一，“世界最大的民族博物馆”、“人类疲惫心灵的最后家园”的贵州黔东南，孕育了黔东南民族职业技术学院。学院坐落在黔东南苗族侗族自治州首府、中国优秀旅游城市、“苗岭明珠”——凯里市。黔东南民族职业技术学院是2001年8月贵州省人民政府批准、国家教育部备案由黔东南苗族侗族自治州卫生学校、黔东南苗族侗族自治州民族林业学校、黔东南民族农业学校、黔东南苗族侗族自治州财贸学校',70628);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黔南民族职业技术学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.qnzy.net/','黔南民族职业技术学院是经贵州省人民政府首批批准成立，国家教育部备案的具有高等学历教育招生资格的公办高等职业院校。坐落于全球绿色城市、中国优秀旅游城市、黔南布依族苗族自治州首府——都匀市。学院占地面积1200余亩。校内山水相连，绿树成荫，植物覆盖率达80%以上。是都匀市花园式单位。现有教职员工近350人，专兼职教师200余人，高级以上职称80人，双师型教师90人，在校生近8000人。设有管理系(旅游经济系)、财经系、计算机科学系、生物工程系、机电工程系、思想政治教育工作部、培训中心，形成“五系一部',48112);
