insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南科技信息职业学院',' ','云南',0,0,0,0,0,' ','暂空',' ','http://www.ynkexin.cn/','云南科技信息职业学院是云南省第一所独立建制的经云南省人民政府批准、教育部备案的全日制民办普通高等职业学院，面向全国招生，具有独立颁发国家高等专科学历文凭证书资格。云南省教育厅是学院的主管部门，党的关系隶属中共云南省委高校工委。学院与昆明世博园和金殿风景区毗邻，风景秀丽、环境宜人、交通便捷、校区占地880余亩，在校生7000余人，现有四个办学校区、5个学部28个专业。学院具备了良好的育人环境和满足人才培养需要的现代化教学设备和设施。2006－2008年，被云南省教育厅授予“德育先进”、“招生先进”',53680);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西双版纳职业技术学院',' ','云南',0,0,0,0,0,' ','暂空',' ','http://www.xsbnzy.com','西双版纳职业技术学院成立于2001年，是一所由原西双版纳教育学院等三所学校合并成立的高职院校。学院占地200亩，座落于著名旅游城市西双版纳州景洪市中心，校园环境优美，办学特色鲜明。学院现有在职教职工346人，在校学生4225人；设语言文学系等8个教学系部，开设应用泰国语等29个专业（其中3个为省级重点建设示范专业）；建有中央财政支持的“国家紧缺人才—旅游专业实训基地”等多个教学基地，形成了“校内、校外、跨国”三结合的完备实习实训体系。学院坚持“立足西双版纳，面向东南亚，辐射省内外”的办学定位，积',52025);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西国防工业职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.gfxy.com','陕西国防工业职业技术学院是一所由陕西省人民政府举办的公办全日制普通高校。1958年建校以来，学院立足陕西，面向国防，为国家航空、航天、兵器、船舶等军工企事业单位和机电、电子、化工、能源、建筑、计算机信息、经贸等行业培养了大批高素质高技能人才。学院为国家国防科技工业高职教育实训基地、中央财政支持建设实训基地、全国数控技术人才培训基地、全国紧缺人才培训基地、教育部高职高专人才培养工作水平评估优秀院校、陕西省首批高职高专示范建设院校、陕西省首届高等职业教育先进院校、陕西国防工业职业教育集团理事单位。学',389283);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安航空职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xihang.com.cn/','西安航空职业技术学院始建于1958年，是一所经教育部批准的具有高等学历教育招生资格的全日制普通高等职业院校，直属陕西省教育厅，面向全国招生。学院是国家重点建设的百所示范性高等职业院校之一。学院位于中国著名航空城——西安市阎良区，处于国家级航空产业基地——西安阎良国家航空高技术产业基地的核心区。学院先后被评为“全国职业教育先进单位”、“陕西省职业教育先进单位”、“省级文明校园”。2006年在教育部高职高专院校人才培养工作水平评估中被评为“优秀”。2010年又以优秀的成绩通过了国家示范院校建设项目验',881851);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西财经职业技术学院',' ','陕西',0,6,0,0,0,' ','暂空',' ','http://www.sxptife.net/','陕西财经职业技术学院是一所全日制公办普通高等职业院校，创建于1960年4月，其前身为陕西省财政学校，先后更名为陕西省统计财经学校、陕西省财政会计学校、陕西省统计财金学校、陕西省财经学校等，年由西安迁建咸阳。2001年9月独立升格改制为陕西财经职业技术学院。2011年7月由陕西省财政厅划转陕西省教育厅管理。学院地处历史文化名城咸阳，东临省会西安，北邻西安咸阳国际机场，交通十分便利。学院建有117个多媒体教室、24个校内实训室、60个会计实验教室、150多个校外实习基地。图书馆藏资料丰富，校园网覆盖',383723);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西交通职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.scct.cn','陕西交通职业技术学院创建于1952年,其前身为国家级重点中专——陕西省交通学校。2001年经省人民政府批准晋升高等教育系列,隶属陕西省交通厅,业务上省教育厅指导,是培养公路交通技术与管理人才的高等职业技术学院。近年来，学院按照“立足交通、面向社会、依托陕西，服务西部”的方针，坚持以服务为宗旨，以就业为导向，走产学研结合的办学之路；紧紧瞄准市场需求设置专业，构建以交通类专业为主体，信息、经济、管理、服务等多专业并存的专业体系；以能力为本位，培养面向社会和交通现代化建设、生产、管理、服务一线需要的，',389578);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西职业技术学院',' ','陕西',0,6,0,0,0,' ','暂空',' ','http://www.spvec.com.cn/','陕西职业技术学院前身是陕西省“九五”重点建设项目陕西省职业教育中心，成立于1995年10月20日。2001年，经省政府批准在省职教中心基础上成立了陕西职业技术学院。学院是陕西省教育厅直属的全日制公办普通高等学校。学院被教育部、财政部确定为“国家示范性高等职业院校建设计划”骨干高职院校立项建设单位，是教育部高职高专院校人才培养工作水平优秀院校，陕西省示范性高等职业院校建设单位，陕西省县及县以下管理干部、职中校长职教师资培训中心，教育部、财政部支持的职业教育实训基地建设项目实施院校，中国与意大利政府',578347);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州外语职业学院',' ','甘肃',0,5,0,0,0,' ','暂空',' ','http://www.lzcfs.cn','兰州外语职业学院创建于1998年，2001年经教育部备案、甘肃省人民政府批准成立，是一所具有独立颁发学历资格证书、纳入国家统一招生计划、面向全国招生的全日制普通高等院校。学院位于兰州市和平高等教育园区，占地面积500多亩，建筑面积13.5万多平方米，目前在校学生8200多人。学院建有38个校内实践基地，在京沪、长三角、珠三角等地建有40多个稳定的校外实习实训基地，办学条件优越。学院设有教育系、艺术系、财政金融系、会计学系、工商管理系、信息技术系、人文科学教学部等教学单位;开设应用英语、应用阿拉伯',381511);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州职业技术学院',' ','甘肃',0,0,0,0,0,' ','暂空',' ','http://www.lvu.edu.cn','兰州职业技术学院是兰州市政府所属普通高等院校。目前占地面积321亩，在校生4812人，专任教师289人，设6个系40个专业，教学实训实验设备总值4000多万元。其前身是1975年成立的兰州市第三十五中学，1984年改制为兰州市职业技术学校，1998年、1999年先后合并兰州工科职业学校和兰州市第十七中学，2001年升格为普通高等职业院校。学院始终坚持高等教育办学方向，坚持职业教育办学类型，以“国粹砺人，文化立院”为办学特色和理念，实施科学发展、可持续发展和跨越式发展战略，培养高素质、高技能、零适',238396);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃警察职业学院',' ','甘肃',0,11,0,0,0,' ','暂空',' ','http://www.gsjcxy.com/','甘肃警察职业学院是高职层次的全日制公安普通高等职业学校，隶属甘肃省公安厅。学院坐落在兰州市城关区左家湾169号，占地面积182亩。教学楼、实验楼、图书信息中心、警务训练馆、警官培训中心、警苑大学生公寓、办公楼等教学、办公、生活设施齐全。具有结构合理，整体素质良好的教师队伍。现开设刑事侦查、警察管理、交通管理、治安管理、经济犯罪侦查、禁毒6个专业和计算机应用、刑事技术、特警3个专业方向。综合规模为2500人。附设公安自学考试辅导站，在职公安民警和在校学生可参加中国人民公安大学开办的公安管理专业本、',307654);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃林业职业技术学院',' ','甘肃',0,2,0,0,0,' ','暂空',' ','http://www.gsfc.edu.cn/','“国家示范性高等职业院校”甘肃林业职业技术学院是一所具有高等学历教育招生资格的公办全日制普通高等学校，创建于1956年。为适应西部生态环境建设的需要，2001年7月经甘肃省人民政府批准，由原国家级重点中专甘肃省林业学校改建为高职学院。学院主动适应经济社会的发展要求，坚持“以服务为宗旨，以就业为导向”的办学方针，加强校企、校地合作，不断改善办学条件，推进教育教学改革，优化专业课程结构，增强社会服务功能，大力加强内涵建设，教育教学质量稳步提高，在领导能力、综合水平、教育教学改革、专业建设、社会服务等',90559);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃工业职业技术学院',' ','甘肃',0,1,0,0,0,' ','暂空',' ','http://www.gipc.edu.cn/','甘肃工业职业技术学院是一所公办全日制普通高等院校。学院始建于1979年，原隶属中国核工业总公司，现由甘肃省教育厅主管。面向全国招收高中毕业生及“三职生”。学院位于中国历史文化名城甘肃省天水市，毗邻闻名中外的麦积山石窟。学院占地388亩，校舍面积127333平方米，有标准的高校学生餐厅和清真餐厅。教学、实习、运动、生活设施配套齐全，布局合理，花木繁茂、碧草如茵、环境优雅。经过三十年的发展壮大，学院积累了丰富的办学经验，具有较强的办学实力和强劲的发展潜力。现有资源勘查工程系、建筑工程系、信息工程系、',133756);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏工业职业学院',' ','宁夏',0,1,0,0,0,' ','暂空',' ','http://www.nxngzy.com/SCMS/','宁夏工业职业学院，是经教育部批准的具有全国招生资质的普通高等职业院校，隶属于神华宁夏煤业集团公司，是宁夏唯一由国有企业开办的普通高等院校。学院依托宁夏煤炭企业，经历了二十多年的发展历程，积累了深厚的文化底蕴和丰富的教育教学资源，具有得天独厚别人无法比拟的优势。其一，神华宁煤集团强势崛起，经济实力雄厚，对学院的投资逐年加大。目前学院拥有两个校区：①银川市西夏区朔方路5号（校本部）；②灵武市宁东镇中心区（南校区）。其二，神华宁夏煤业集团公司是宁夏回族自治区“一号工程”——宁东能源化工基地（全国13个',45682);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昌吉职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.cjpt.cn','昌吉职业技术学院是经自治区人民政府批准、国家教育部备案的一所公办全日制普通高等职业院校。学院座落于新疆天山北坡经济带、古丝绸之路黄金古道昌吉市中心，交通便利、环境优美。目前，学院共有教职工454人，其中专任教师294人，高级职称104人，双师素质教师122人，在校学生近万名。学院实行院系两级管理，下设七系三部（电气工程系、计算机系、机械工程系、能源动力系、建筑工程系、工商管理系、旅游系、思想政治教育教学与研究部、基础部、体育部）、六个中心（大学生心理健康教育与咨询中心、资助管理中心、成人教育中心',165019);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东服装职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.svict.com/','山东服装职业学院创建于2000年，是一所经山东省政府批准、教育部备案的国办普通高等学校。学院位于历史文化旅游名城山东省泰安市，坐落在泰山风景名胜区，北依驰名中外的东岳泰山，南临五千年文化发祥地大汶河，文化底蕴深厚，区位优势明显。学院现占地600亩，建筑面积20余万平方米，在校生近9000人。现有普通高职、“3+2”本科对口贯通培养、成人本科、试点本科、网络本科等办学层次。设有服装工程与管理系、服装设计与表演系、艺术与设计系、国际贸易系、财经系、旅游系、信息工程系、机电系和基础部等教学机构。学院立',71969);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('德州科技职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sddzkj.com/','德州科技职业学院是国家教育部认定、山东省政府批准、独立设置的高职院校。2001年，省教育厅批复该院为“全省十所试点本科院校”之一，形成本科、高职专科及中专教育相结合的立体教育格局。学院坚持“开放式办学，现代化管理，全方位育人”的办学理念，遵循以素质为基础、以能力为重点、以技能为核心的复合型教学模式；按照以市场需求和学生就业为导向、以提高学生就业能力为目标，强化学生的应用、创造和创新能力，努力为社会培养具有较强竞争力的高技能应用型、复合型、创新型人才。目前，学院设有主校区、青岛校区，面向全国招生，',71509);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潍坊科技学院',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.wfkjxy.com.cn/','潍坊科技学院是经教育部批准成立的全日制普通本科高校。学院坐落于闻名中外的中国蔬菜之乡——山东省寿光市。学院现占地面积2400亩，校舍总建筑面积76万平方米；教学科研仪器设备总值7289.93万元，建有机械实训中心、机电实训中心、化学化工实训中心、蔬菜花卉实训中心、建筑工程实训中心、计算机教学中心、网络服务中心，106个实验室；馆藏纸质图书114.5万册，电子图书20.97万册；总资产8.9亿元。现有11个院系，全日制在校生22719人，教职工1352人，外籍教师33人。学院坚持“以生为本，质量为',490195);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东力明科技职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.6789.com.cn/2009/index.asp','山东力明科技职业学院是在山东中西医结合大学的基础上建立而成，山东中西医结合大学创建于1986年，是由我国第一代从事中西医结合研究和临床实践的王显明教授创办,是我国第一所以中西医结合为校名的高等学府，也是我省改革开放以来创办最早的私立医科大学。2001年经山东省人民政府批准、国家教育部备案，在山东中西医结合大学的基础上建立山东力明科技职业学院，纳入国家计划内统招，独立颁发学历文凭，并由单一的中西医结合专业扩展为以中西医结合医疗和护理为主要办学特色，集医学、理工、经管、艺术等学科门类共同发展的综合性',129342);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湘潭职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.xtzy.com','湘潭职业技术学院成立于2001年8月，是经湖南省人民政府批准、国家教育部备案、面向全国招生的国办全日制普通高校。学院地处湘潭市高新技术产业开发区，位于河东大道与丝绸大道交汇处，与新建的湘潭市政中心毗邻，东联长潭高速公路，交通便利。学院占地面积300亩（目前规划再扩征土地110亩），建筑面积18万平方米，固定资产近3亿元。现有在籍学生12000人，教职工420人，其中，教授10人，副教授63人，副高以上职称教师109人，“双师型”教师130名，具有研究生学历或硕士以上学位教师60多人。还聘请了一批',78497);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郴州职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.czzy-edu.com','学院位于“全国优秀旅游城市”和享有“中国女排腾飞基地”、“有色金属之乡”等美誉的湖南“南大门”――郴州市城区，是“全国中小型企业职工培训示范基地”、湖南省“职业教育先进单位”、湖南省“文明高校”和“湖南省首批教育科学学科研究基地”。107国道、京广铁路、武广铁路客运专线、京珠高速公路等纵贯市区，交通便捷，距长沙和广州仅3小时车程。今年秋季，学院将搬迁至风景秀丽、气势灵动的王仙岭新校区，与湘南学院一道共建郴州新兴大学人文园区，旁边紧邻王仙岭国家级生态旅游公园，为青年学子提供更加优良的学习环境。学院',56181);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('娄底职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.ldzy.com','“国家示范性高等职业院校建设计划”骨干高职院校娄底职业技术学院是经湖南省人民政府批准成立的公办全日制普通高等学校，系湖南省示范性高等职业院校，以培养社会急需的高素质、高技能人才为目标。校园占地面积1045亩，建筑面积30万平方米。现有全日制在校学生11000余人。湘中明珠娄底市，系全国绿化模范城市、全国生态旅游城市、全国十佳宜居城市、省级园林城市。境内交通便利，湘黔、洛湛铁路在这里相汇，沪昆（上海-昆明）、太澳（太原-澳门）高速公路在这里交叉，207、320国道及4条省道贯通全市。娄底职院就座落',55638);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('张家界航空工业职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.zjjhy.net','张家界航空工业职业技术学院，是2001年经湖南省人民政府批准建立的全日制普通高等学校，也是湖南省唯一一所为国防工业培养高素质技能型、应用型人才的高职院校。其前身为原第三机械工业部013基地技工学校，1983年改办为大庸航空工业学校，1998年更名为张家界航空工业学校。学院先后隶属于原第三机械工业部、中国航空工业部、中国航空航天工业部、中国航空工业总公司。1999年划归湖南省国防科学技术工业办公室管理。学院位于旅游名城张家界市，具有良好的办学条件。专任教师中，副教授及以上职称教师达32%，4名教师',93589);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州城市职业学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.gzytvc.cn/','贵州亚泰职业学院是2001年经贵州省人民政府批准成立，教育部备案，纳入国家计划内全国高校统一招生，贵州省唯一由个人全额投资创办的民办高校。学院办学层次为计划内专科学历教育和本科自考助学教育。学生毕业由学院颁发国家电子注册的大专文凭或经国家考试合格颁发本科自考文凭。学院位于贵阳市花溪区鸿源路8号，毗邻贵州大学和花溪公园。学院规划占地面积1320亩，建筑面积400000平方米，能容纳在校生30000人。现在册专兼职教师367人，其中具有副高以上职称126人，占34%；双师型教师162人，占45%。学',76959);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明艺术职业学院',' ','云南',0,8,0,0,0,' ','暂空',' ','http://www.kmac.org.cn/','云南省第一所获准设立的艺术职业学院云南省第一所获准引进外籍专家的艺术职业学院云南省第一所获准面向全国招生的艺术职业学院云南省第一所通过教育部人才培养工作水平评估的艺术职业学院昆明艺术职业学院是云南省人民政府批准、国家教育部备案，实施专科学历教育的普通高校，前身为聂耳音乐学校。学院地处滇池之滨，校园占地200余亩，建筑面积7万多平米，环境优美，设施齐全。现设有聂耳音乐学院、设计与传媒学院、舞蹈、应用外语等院系；有来自25个省、区30多个民族的在校生3000余人。学院汇聚了一批国内外有较高知名度的专',41567);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青海警官职业学院',' ','青海',0,11,0,0,0,' ','暂空',' ','http://www.qhjyedu.com/','青海警官职业学院是经青海省政府批准由原青海省人民警察学校和原青海省司法警官学校合并组建而成的全日制高职院校，隶属于青海省公安厅。学院是青海省唯一一所培养公安、政法专门人才的专科层次的职业院校，设有治安系、侦查系、法律系、基础部、警训部、民族部、成人教育部、技能培训部等8个教学单位，开设治安管理（含汉藏双语治安管理）、交通管理、安全保卫、警察管理、信息网络安全监察、警察指挥与战术、侦查、刑事技术、刑事执行、法律事务（含汉藏双语法律事务）、司法助理（含汉藏双语司法助理）、书记官等12个专业。学院拥有',73983);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江医药高等专科学校',' ','浙江',0,3,0,0,0,' ','暂空',' ','http://www.zjpc.net.cn/','浙江医药高等专科学校的前身为创建于一九八四年的浙江省医药学校，一九九九年十二月经浙江省人民政府批准筹建浙江医药职业技术学院，二零零二年二月经国家教育部批准正式建立，成为全国最早独立设置、浙江省惟一一所医药类专科层次高等院校。学校行政隶属浙江省食品药品监督管理局，教育业务管理隶属浙江省教育厅。学校坐落于浙江东部海滨港城宁波市南高教园区，区内风光旖旎，环境优美，学术氛围浓郁，与诺丁汉大学(中国校区)、浙江大学宁波理工学院、浙江万里学院等六所本专科院校共享教育教学资源。占地约233亩，总建筑面积16万',170947);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江机电职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.zime.edu.cn/default.html','“国家示范性高等职业院校”浙江机电职业技术学院是经省人民政府批准建立的以培养机电工程技术、经贸管理、设计艺术高素质技能型专门人才为主的高等院校，是教育部第一批“国家高技能型紧缺人才培养项目”院校之一，同时也是浙江省机电项目高技能人才培训基地、浙江省职教师资培训基地。学校创办50余年来，优良的办学水平受到了教育部的肯定，在2006年教育部高职高专人才培养工作水平评估中被评为“优秀学院”，并形成了独特的办学优势和特色：明确的办学定位——学院坚持“服务浙江经济、突出技术教育、创建一流学院”的办学定位，',136314);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江建设职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.zjjy.net/','“国家示范性高等职业院校建设计划”骨干高职院校浙江建设职业技术学院是经浙江省政府批准的首批高等职业技术学院之一，是公办全日制普通高等院校。学院位于杭州钱江二桥南岸萧山高教园区，占地面积522亩，建设投资3亿多元，建筑面积达15.8万平方米。自1958年建校以来，学院积累了五十余年的办学经验，形成了较为完善的教学管理体系。学院现设有建筑系、建筑工程系、城市建设工程系和经济管理系，开设有21个专业。其中，建筑工程技术、建筑经济管理为浙江省高职重点建设专业。学院现有全日制在校生6000多人；在编教职员',195824);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江艺术职业学院',' ','浙江',0,8,0,0,0,' ','暂空',' ','http://www.zj-art.com/','浙江艺术职业学院是浙江省唯一一所国办全日制综合性高等艺术院校。1999年12月，浙江省人民政府批准浙江艺术学校、浙江省电影学校合并筹建浙江艺术职业学院；之后，浙江省艺术研究所成建制划归。2002年1月，省政府批准正式建院。浙江艺术职业学院的前身具有50年的办学史。作为学院前身主体的浙江艺术学校，曾先后四次被教育部、文化部评定为“国家级重点艺术中专”。学校教学成果显著，教学特色鲜明，在全国同类学校中享有较高的知名度。在办学历程中，名师荟萃，巨擘亲授：有京剧表演艺术家、“江南活武松”盖叫天，越剧表演',63395);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江经贸职业技术学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://www.zjiet.edu.cn/','浙江经贸职业技术学院的前身是创办于1979年的浙江供销学校。2002年初，省政府批准正式建院，是一所以“经贸”为特色，文、理、工、经、管多学科调协发展的公办普通高校。新校区坐落在杭州下沙高教园东区，占地575亩，建筑面积20万平方米，教学仪器设备总值4227.96万元。学院注重工学结合的人才培养模式探索与创新，现为“全国高等职业教育经贸类实训基地”、“国家技能型紧缺人才培养基地”、“国家级‘计算机应用与软件设计’实践性教学基地”、“国家级商业信息化技术实训中心”和“连锁经营管理专业省级示范性实践',170604);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江商业职业技术学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://www.zjvcc.edu.cn/','浙江商业职业技术学院简介浙江商业职业技术学院前身为创办于1911年的杭州中等商业学堂。近百年来，秉承“诚、毅、勤、朴”的校训，坚持“育人为本，服务社会”的办学方针，培养了近6万余名专业技术人才，涌现出经济学家骆耕漠、爱国人士章乃器等著名校友，被誉为“浙商人才培养的摇篮”。学院主校区坐落在风景如画的钱塘江南岸杭州滨江高教园区内，另有杭州市内德胜校区，占地面积569亩，建筑面积逾19万平方米，教学仪器设备总值近4000万元，图书馆藏书达60万册。设有6系31个专业及方向，形成了涵盖经贸、管理、旅游、',229658);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江经济职业技术学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://www.zjtie.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校浙江经济职业技术学院是全日制省属公办高职院校，前身为浙江省物资学校（隶属于原浙江省物资局），创建于1978年，2002年1月，经浙江省人民政府批准正式升格为高职学院。2003年10月，整体搬迁至杭州下沙高教园区。目前，校园占地面积576.5亩，建筑面积22万平方米，在校生7200余人，馆藏图书总量近70万册。主要面向为制造业服务的现代物流业培养高素质技能型人才，构建了行业发展所需的物流装备应用、物流运营技术、物流信息技术、物流装备后服务、房地产等专业',114441);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江旅游职业学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','oldname": "浙江旅游职业学院','“国家示范性高等职业院校建设计划”骨干高职院校浙江旅游职业学院是全国第一所独立设置的公办旅游高等职业院校，隶属于浙江省旅游局。2006年11月，学院通过教育部高职高专人才培养工作水平评估并获得优秀。2008年8月被浙江省教育厅、浙江省财政厅确定为浙江省示范性高职院校建设单位。现已成为浙江省旅游高等教育中心、旅游科研中心、旅游信息中心、旅游教育对外交流的重要窗口。学院位于钱塘江畔，校园占地580亩，总建筑面积20.5万平方米。现有专任教师275名，其中教授、副教授70余名，硕士、博士160余名，并',106936);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江育英职业技术学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://www.zjyyc.com/','浙江育英职业技术学院系经浙江省人民政府批准创办的民办普通高校。学制三年，学生列入国家统一招生计划。学生毕业，学院颁发国家承认学历的高职(专科)文凭。浙江育英职业技术学院以“厚德载物，自强不息”为校训，努力实现高校、企业、社区的全方位合作，为21世纪现代服务业培养全面发展的具有优势品质与能力的高等技术应用性职业人才。学院立足杭州，面向浙江，融入“长三角”，强烈的现代科技气息，高尚的社区环境与校园浓郁的文化氛围息息相通，莘莘学子置身其间，耳濡目染，求知欲望油然而生，激发出对理想境界的强烈追求。区域环',71608);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江警官职业学院',' ','浙江',0,11,0,0,0,' ','暂空',' ','oldname": "浙江警官职业学院','“国家示范性高等职业院校”浙江警官职业学院是一所以培养监狱劳教人民警察、基层司法工作者以及安全防范技术、司法信息技术等专门人才为主的司法类高等职业院校。主管部门为浙江司法厅、浙江省教育厅、浙江省监狱管理局。学院前身为1984年建立的浙江省第三人民警察学校，2000年5月经浙江省人民政府批准筹建浙江司法警官职业学院，2002年1月经浙江省人民政府批准正式成立浙江警官职业学院，分下沙、花坞两个校区。是浙江省职业教育先进单位和浙江省示范性高等职业院校，是全国高等法律职业教育改革试点与推广单位、全国政法',155163);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江金融职业学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://www.zfc.edu.cn/','“国家示范性高等职业院校”浙江金融职业学院位于美丽的钱塘江畔、杭州下沙大学城，前身为创办于1975年的浙江银行学校，2000年6月升格为高职学院，2003年被评为浙江省第一家高职高专人才培养工作优秀等级学校，先后获得“全国金融职业教育先进集体”、浙江省“职业教育先进单位”等荣誉称号，是浙江省省级文明单位、全国金融职业教育委员会主任委员单位、教育部高职高专经济类教学指导委员会秘书长单位、中国高等职业技术教育研究会常务理事单位、浙江省职业技术院校党建研究会会长单位。2006年12月，学院被教育部、财',432495);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江工业职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.zjipc.com/','浙江工业职业技术学院是经浙江省人民政府批准设立的全日制公办高等院校。学校座落在历史文化名城绍兴市，位于风景秀丽的镜湖新区内，环境幽雅。学校占地面积503亩，建筑面积二十三万平方米，是全国职业教育先进单位、全国高职高专优秀学院和浙江省示范高职建设院校。学校下设机电工程学院、数字控制工程学院、电气工程学院、纺织工程学院、建筑工程学院、计算机学院、经济管理学院、实训教学中心、人文社科部、继续教育部等7院2部1中心，在校学生8000余人，教职工521人，其中具有高级职称教师117人,双师型教师190余人',70393);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('杭州职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.hzvtc.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校杭州职业技术学院系由杭州市人民政府主办的高等职业技术学院。学院在杭州市经委系统原6所职工大学和2所中专基础上，于1998年3月经原国家教委批复筹建，2002年1月经浙江省人民政府批准正式建立。2007年学院被评为全国高职高专人才培养工作水平评估优秀学校。学院坐落于素有“人间天堂”美誉的杭州，位于下沙“大学城”，毗邻杭州经济技术开发区，占地1000余亩。设管理工程系、艺术系、化学工程系、信息电子系、友嘉机电学院（与友嘉实业集团合作共建）、新通国际学院（',130985);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('嘉兴职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','oldname": "嘉兴职业技术学院','嘉兴职业技术学院是经浙江省人民政府批准并报国家教育部备案正式建立的一所全日制普通高职院校，位于东海之滨上海南翼杭州湾北岸的中国共产党诞生地—嘉兴市区南湖之畔，具有明显的区位优势和良好的就业创业环境。学院具有59年的办学历史，曾被原国家教委授予“教学改革先进单位”，国家农业部评为“科教兴农先进学校”、“全国农业技能鉴定工作先进单位”，是中共浙江省委、省人民政府授予的“省级文明单位”、“省级综合治理先进集体”，还拥有“高职高专人才培养工作水平评估优秀学校”、“省教育科研先进单位”、省市“平安校园”等',77531);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖州职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.hzvtc.net.cn/','湖州职业技术学院是一所经浙江省人民政府批准成立的公办全日制高职院校，是教育部教育发展研究中心“应用型人才培养实验研究项目学校”。学校是全国高职高专优秀学校、浙江省“文明单位”和“平安校园”。学校位于全国优秀旅游城市浙江省湖州市，湖州东临上海、南接杭州、北临南京，历史悠久、山水清远、交通便利、经济发达、风光优美。校园占地600亩，校舍17万平方米。学校环境优美，布局合理，小桥流水、亭台榭阁、丹桂绿茵、春柳映波、拱桥拂云，是学生求知成才的理想之地。现有全日制学生7500余人，成人业余学生近5000人',109789);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('绍兴职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.sxvtc.com/','古城绍兴，永和亭山，山阴大道，四季咏春。绍兴职业技术学院9000余名莘莘学子，沐阳光、润雨露，蓬勃向上。一所年轻向上、充满生机的学校。十年坚韧执著求自强，办学规模实现了从1000人到4000人，从7000人到9000人的跨越式发展。国家教育部、人力资源和社会保障部、工业和信息化部等六部委选定的全国技能型紧缺人才培养培训示范性基地。浙江省高技能人才培训基地（电子信息）。2005年获得全省高职院校唯一“A类”研究课题，顺利完成《浙江省地理信息系统ZJEGIS》的开发。国家教育部周济部长作序，中国地图',85783);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('衢州职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.qzct.net/','衢州职业技术学院是一所集制造、电子信息、材料与能源、财经、艺术设计、医药卫生、语言文化等专业的公办全日制高等学校，主要培养面向生产、建设、管理、服务第一线的高素质技能型人才。学院坐落在国家历史文化名城——浙江省衢州市。衢州有着1800多年的历史，素有重教兴学的传统，居浙江上游，南接福建，西连江西，北临安徽，为浙、闽、赣、皖四省辅轴，素有“四省通衢”之美称，区位优势得天独厚。校园东临钱塘江上游——衢江，西接杭金衢高速公路，占地面积300余亩，建筑面积13万平方米，拥有教学科研大楼、实验楼、艺术楼、',51193);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('丽水职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.lszjy.com/','丽水职业技术学院是经浙江省人民政府批准设立的全日制公办高等学校。学院始建于1946年，建校基础是创办于1953年的省部级重点中专浙江林业学校和创办于1976年的省属浙江丽水商业学校，2000年12月合并组建升格为高职院，2004年4月通过浙江省教育厅高职高专人才培养工作水平合格评估，2007年6月通过教育部专家组评估，被确定为“全国优秀高职院校”，2008年8月，成功申报浙江省示范性高职院校建设单位。学院现占地470亩，校舍建筑面积15.8万平方米，教学仪器设备总值3200多万元；现有教职工36',177371);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津医学高等专科学校',' ','天津',0,3,0,0,0,' ','暂空',' ','http://www.tjyzh.cn','“国家示范性高等职业院校”天津医学高等专科学校是一所面向全国招生、专科层次的普通高等院校。由原天津市职工医学院、天津市护士学校合并后组建而成，有着26年专科教育、成人继续教育和近百年护理教育的历史。学校同时作为“天津市卫生职业教育中心”、“天津市继续医学教育中心”和“天津市卫生职工岗位培训中心”已形成学历教育、职前职后教育、继续教育和岗位培训一体的现代化医学高等职业教育体系。教育部高职高专相关医学专业教育教学指导委员会和卫生部卫生职业教育教学指导委员会秘书处设在我校。多年来学校秉承办人民满意的教',662465);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津开发区职业技术学院',' ','天津',0,0,0,0,0,' ','暂空',' ','oldname": "天津开发区职业技术学院','天津开发区职业技术学院是天津市人民政府根据国务院授权批准，教育部备案成立的全日制普通高等学校，由天津开发区管委会主办、主管。其主要任务是为滨海新区和全国各地培养高素质技能型人才。学院坐落在天津开发区滨海学院区，得天独厚的区位优势、现代先进的办学理念和特色鲜明的教育教学，促进了学院的跨越式发展，得到各级领导和社会的广泛认可。学院被天津市人民政府授予\"职业教育先进单位\"称号；时任教育部部长周济院士来院视察时，对学院紧贴区域经济办学、坚持产教结合、校企联合携手育人的作法给予充分肯定，评价\"办学很有特色',77154);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津艺术职业学院',' ','天津',0,8,0,0,0,' ','暂空',' ','http://www.arttj.cn/','天津艺术职业学院成立于2002年，是经天津市政府批准、国家教育部备案成立的国办全日制艺术类高职学院，其前身是具有50多年历史、被教育部、文化部评定为“国家级重点艺术中专”的天津市艺术学校和具有20多年历史的文化部直属、中国唯一培养曲艺人才的专业学校——北方曲艺学校。学院以传承、发展中国民族文化艺术和服务天津文化强市发展战略为宗旨，以社会需求为导向，走产学研相结合的发展道路，努力培养大批德才兼备、适应文化大发展大繁荣需要的高素质应用性多层次多类型的专门人才。学院现设有戏剧系（包括戏曲表演与伴奏、话',105350);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津交通职业学院',' ','天津',0,1,0,0,0,' ','暂空',' ','http://www.tjtvc.com','天津交通职业教育始于1958年。2002年经天津市人民政府批准成立天津交通职业学院，隶属于天津市交通（集团）有限公司。学院占地面积700余亩、建筑面积19。2万平方米、在校生8169人。建院以来，学院党政班子坚持\"经营学院、突出特色、服务社会、开拓创新\"，以\"每天都是代表作\"为校训，以\"厚德润教、师表育人\"为教风，以\"博学而笃志，切问而近思\"为学风，内涵建设逐步深化、办学质量不断提高、社会效益显著提升。学院是国家汽车运用与维修专业领域技能型紧缺人才培养培训基地、教育部、天津市人民政府共建天津滨海',263971);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('邢台医学高等专科学校',' ','河北',0,3,0,0,0,' ','暂空',' ','http://www.xtmc.net/','邢台医学高等专科学校是公办全日制普通专科学校，座落在有着3500年历史的省级文化名城——邢台市。邢台市地处河北省南部，土地面积1.3万平方公里，总人口670万，北距北京400公里，京广铁路、京九铁路、京深高速公路穿市而过，交通便利，旅游资源丰富，城市环境优美，文化底蕴深厚。学校始建于1964年，占地面积604亩，建筑面积26.8万平方米；现有教师457人，设有临床医学、护理、口腔医学、药学等13个专业；图书馆建筑面积10001平方米，藏书33.82万册，中、外文报刊千余种；实验中心建有4栋实验楼',188455);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北艺术职业学院',' ','河北',0,8,0,0,0,' ','暂空',' ','http://www.hebart.com/','河北艺术职业学院于2001年6月经省政府批准成立，是省内唯一一所国办全日制综合性高等艺术院校，其前身是在国内外享有盛誉的河北省艺术学校，始建于1955年。学院现设有音乐、舞蹈、戏剧、美术、艺术教育、文化管理、传媒7个系，共26个专业。教师队伍学科配套，业务精良，实践经验丰富，荟萃了一大批省内外艺术名家：钢琴教师林剑、二胡教师王文旺荣获我国奖励音乐教师的最高奖——“区永熙”优秀音乐教育奖；声乐教师陈虹、二胡教师王曙亮获中国音乐“金钟奖”；戏剧系教师彭蕙蘅是第十三届中国戏剧“梅花奖”得主；美术系主任',91338);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北旅游职业学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.cdtvc.com/','河北旅游职业学院是河北省唯一以旅游命名，以培养旅游类、现代农业类、现代管理类和信息机电类高技能专门人才为主的综合性公办全日制高职院校，是河北省重点建设的示范性高等职业院校，其前身是创建于1950年的热河省农林专科学校。学院坐落于国家历史文化名城、著名旅游胜地——河北·承德。承德市拥有五千年红山文化和三百年山庄文化，拥有世界文化遗产避暑山庄和周围寺庙群，拥有全国首批历史文化名城和首批中国优秀旅游城市等多项桂冠。学院毗邻避暑山庄等著名景区，学习、生活环境优美、气候宜人;紧邻京沈高铁承德站，到达北京仅',205483);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西机电职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.sxjdxy.org','山西机电职业技术学院创建于1958年，座落在全国十大魅力城市之一的上党古城——长治市，位于长治市高新技术产业开发区，地处太焦铁路及长太、长邯、长晋高速公路之枢纽，位置优越，交通便捷。学院占地面积400余亩，建筑面积近10万平方米，建有“数控实训中心”等6大校内实训中心，包括各类实验实训室54个，建有校外“科研、实习、就业基地”100余个，教学仪器设备值2480余万元，图书馆藏书28.6万余册，运动场地1.8万余平方米。计算机校园网内联教学、科研、管理、服务各系统，外接国际互联网，实现了网上资源共',94397);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西戏剧职业学院',' ','山西',0,8,0,0,0,' ','暂空',' ','http://www.shanxixjxy.com','山西戏剧职业学院是2002年经山西省人民政府批准，由原山西省戏曲学校和山西省戏剧研究所合并组建的一所综合性高等艺术职业学院。学院现有在校生2000余人，开设有戏曲、音乐、舞蹈、话剧影视、美术、文化艺术管理、旅游管理等七个系近二十个专业。建校50多年来，为社会培养了各类艺术人才万余名，毕业生多数成为我省文艺领域的骨干，有的成为享誉省内外的知名艺术家，为繁荣山西乃至全国文化艺术事业做出了突出的贡献。由于办学成果显著，至1980年以来，连续三次被国家教育部确定为国家级重点中专，2001年被文化部授予“',71544);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西财贸职业技术学院',' ','山西',0,6,0,0,0,' ','暂空',' ','http://www.sxcmvc.com','山西财贸职业技术学院始建于1978年8月，前身为国家重点中专学校——山西省财贸学校，2002年4月升格为山西财贸职业技术学院，是国家教育部、省政府批准建立的、隶属于山西省教育厅的全日制专科层次的公办普通高等学校。山西财贸职业技术学院位于太原市民航南路26号，占地面积220余亩，校舍面积7万余平方米，教学和生活设施齐全。拥有可供6000余名学生使用的教学楼、学生公寓、餐厅、礼堂、标准运动场，并设有11个电子阅览室，建有40多个先进的并与专业配套的现代化实验实训室，所有教室均配备多媒体投影设备，教学',85522);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西林业职业技术学院',' ','山西',0,2,0,0,0,' ','暂空',' ','http://www.sxly.com.cn','山西林业职业技术学院前身是山西林业学校，成立于1952年。2002年4月经山西省人民政府批准、国家教育部备案，改制晋升为专科层次的公办高等职业技术学院。学院为山西省文明单位，院团委是全国“五四红旗团委”。学院在2007年国家教育部人才培养工作水平评估中被评为优秀，2010年被评为全国“百佳创新型学校”，2013年被确定为山西省示范性高等职业院校立项建设单位。学院位于太原市胜利桥东汾河之畔，西邻汾河公园，北接森林公园，校园占地200余亩。学院设有林学系、园艺系、园林系、经济贸易系、信息工程系、旅游',195753);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西水利职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.sxsy.com.cn/ ','山西水利职业技术学院是一所以工科为主，文理结合的公办全日制普通高等学校。在半个多世纪的办学历程中，学院坚持“立足山西、面向全国，立足水利、面向市场，立足当前、面向未来”的办学思想，秉承“上善若水，敦学笃行”的校训，与时俱进，深化教育教学改革，加快学院发展步伐，全面提高教育教学质量和整体办学水平，积累了丰富的办学经验和深厚的文化底蕴。2008年在全国高职高专人才培养工作水平评估中，被评为“优秀”院校，2009年被水利部确定为部级示范院校建设单位。学院以高等专科教育为主，面向全国招生。设有水利工程系',60836);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西工程技术学院',' ','山西',1,0,0,0,0,' ','暂空',' ','http://www.sxit.edu.cn/','阳泉职业技术学院（太原理工大学阳泉学院）是一所以工为主，兼有经、管、法等学科的普通高等院校，前身是阳泉煤炭专科学校，隶属于山西省教育厅。现学校本科名称为太原理工大学阳泉学院，专科名称为阳泉职业技术学院。学校地处山西的东大门——阳泉，位于阳泉市经济技术开发区学院路，学校规划用地1200亩，现有建筑面积19万平方米。学校设有资源系、机电系、建工系、信息系、管理系、经贸系、社科系、基础部、体育部、实验实训中心10个教学单位。开设有11个本科专业和30个专科专业，初步形成了特色鲜明、充满活力的专业群体。',97861);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古体育职业学院',' ','内蒙古',0,7,0,0,0,' ','暂空',' ','http://www.nmtyxy.com/','内蒙古体育职业学院隶属于自治区体育局，其前身为始建于1965年的内蒙古体育运动学校，2001年6月，经自治区人民政府批准组建成立，是自治区唯一的体育高等院校。学院办学形式为全日制高等职业教育;培养目标是培养有较高学历层次和较高素质的竞技体育人才、合格的中小学体育师资，社会需要的有较高专业水平和特长的体育实用人才;还承担全区体育教练员、体育管理人员的继续教育培训任务。办学层次以大专层次的高等职业教育为主，兼办中专及中专预科班。学院成立以来，在自治区党委、政府的关怀下，在自治区体育局和教育厅的领导下',49279);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁金融职业学院',' ','辽宁',0,6,0,0,0,' ','暂空',' ','http://www.lnfvc.cn','辽宁金融职业学院是经省政府批准、教育部备案的辽宁省唯一一所公办全日制金融类高等职业学院，迄今已有50多年的办学历程，已经成为培养银行、保险、证券、会计、IT业、管理等方面高级应用型人才的基地，在金融领域具有广泛的社会影响和良好的社会声誉。学院位于省会城市——沈阳，座落在生机勃勃的沈北新区，交通便利，环境优雅。教学设施先进、完备，建有教学办公楼、图书馆、文体馆、标准化学生公寓、餐饮中心和功能齐备的高标准实训大楼。学院拥有一支专业结构合理、教学经验丰富、专兼结合的“双师型”教师队伍，设有金融系、会计',126518);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁广告职业学院',' ','辽宁',0,8,0,0,0,' ','暂空',' ','http://www.ggxy.com','辽宁广告职业学院，建于1993年，为国内广告专业院校之首创。学院坐落在沈阳北郊，与辽宁大学（文科校区）、沈阳师范大学等十余所高校共同形成了一个新兴的沈北大学城，一个崛起的高校文化圈。学院引为骄傲的创始人、著名的美学家、博士生导师、沈阳市优秀专家高凯征教授始终引领着全院师生，以全新的教育理念和辛勤的创造劳动历11年倾心设计和构筑这个令人向往的求知成才的温馨家园。本院为国家教育部批准的全日制普通高等职业学校。来自18个省、80个市县的5000名学子在本院参与民主管理，使用一流设施，攻读广告策划、广告',57407);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁机电职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.lnmec.net.cn','辽宁机电职业技术学院坐落在中国最美丽的边境城市——丹东市，与朝鲜新义州市隔鸭绿江相望。学院始建于1965年，系辽宁省教育厅直属普通高等职业院校，被国家教育部确定为“全国重点建设职教师资培训基地”、“国家数控技术紧缺人才培养基地”、国家教育部和财政部重点支持的“电工电子与自动化实训基地”。2007年，学院高质量通过了辽宁省教育厅组织的高职院校人才培养工作评估，顺利通过了ISO9001：2000质量管理体系认证。2008年，被确定为辽宁省首批示范性高等职业院校立项建设单位。学院占地面积530亩，教学',74243);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁经济职业技术学院',' ','辽宁',0,6,0,0,0,' ','暂空',' ','http://www.lnemci.com/zs/','辽宁经济管理干部学院（辽宁经济职业技术学院）地处辽宁中部城市群核心城市沈阳，坐落在辽宁极具发展潜力的沈北新区，地理位置优越。学院始建于1984年1月，是经省人民政府批准、国家教育部备案的省属管理干部院校（正厅级）和公办普通高职院校。1997年，学院被省教育厅列为全省第一批15所高职教育试点院校之一,2009年,成功获批省级示范性高职院校建设单位。”学院现有国民教育在校生7334人，年培训各类人员5500人次；占地面积569.64亩，藏书38.9万册，固定资产总值2.63亿元；现有教职工582人，',198825);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁石化职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.lnpc.edu.cn','“国家示范性高等职业院校建设计划”骨干高职院校辽宁石化职业技术学院2002年经辽宁省政府审批（辽政[2002]12号文）实行辽宁省教育厅与中国石油锦州石化公司合作办学的体制，是全省唯一一所校企合作、工学结合、依托行业优势培养高等技术应用性人才的专科层次的普通高校。坐落在交通便利、文化发达的美丽海滨城市——锦州。学院秉承“严谨、求实、创新、和谐”的校训，坚持以人为本，德育为先，严格管理，强化职业能力训练,提升学生综合素质,构建和谐校园。学院是辽宁省思想政治工作先进单位，2006年被辽宁省委高校工委',100808);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林司法警官职业学院',' ','吉林',0,11,0,0,0,' ','暂空',' ','http://www.jlsfjy.cn/','吉林司法警官职业学院系经吉林省人民政府于2002年1月14日以吉政函[2002]2号文件正式批准成立的专科层次的普通高等职业院校。主管部门是吉林省司法厅，部颁代码为12901。学院前身是吉林省司法学校和吉林省监狱警官学校，具有二十多年独立举办高中后普通中专教育的历史和十余年联办成人本、专科教育的经验。在省委、省政府及有关部门的亲切关怀下，在上级主管部门的正确领导下，几代警院人风雨兼程三十年，学校由小到大，发展迅速。今天的学院已经发展成为一所以培养基层监狱、劳教人民警察、人民法院和人民检察院的司法',149326);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林电子信息职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.jltc.edu.cn','吉林电子信息职业技术学院是教育部备案、吉林省所属的高等职业技术学院，是全国高职高专人才培养工作水平评估优秀学校，并被吉林省政府评为职教工作先进单位，被省人事厅、省教育厅评为就业工作省级先进单位。学院设有计算机系、机械电子系、材料及资源系、工商管理系、旅游系、外语系、基础部、体育教研部、“两课”教研部、继续教育学院、职业技能培训中心等十一个教学单位，设有电气自动化技术、机电一体化技术、电子信息工程技术、计算机网络技术、冶金技术等42个专业，跨电子信息、制造、资源开发与测绘、材料与能源、土建、财经、',136672);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林工业职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.jvcit.edu.cn/','“国家示范性高等职业院校”国家百所示范性高职院校建设单位纳长白之秀，汲雾凇之美的北国江城吉林市，坐落着一所国家高职示范建设院校----吉林工业职业技术学院。学院1950年创建于长春，时名为“吉林省工业专科学校”；1953年迁至当时的省会吉林市，为适应国家重点建设项目、全国第一个大型化学工业基地——吉林化学工业公司对化工人才的迫切需要，易名为“吉林省吉林化学工业学校”；1959年吉林化工区陆续投产及扩建，急需培养大量高层次专业人才，学校遂升格为“吉林化学工业专科学校”，后来经历“吉林省石油化工学校',104505);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鹤岗师范高等专科学校',' ','黑龙江',0,4,0,0,0,' ','暂空',' ','http://www.hgtc.org.cn','鹤岗师范高等专科学校是于2002年3月经国家教育部批准正式成立的鹤岗市唯一一所全日制普通高等院校。学校占地面积近50万平方米，建筑面积10万平方米，固定资产总值1.33亿元，教学仪器设备总值1620万元，图书馆藏书34.2万册。现有附属小学一所，有稳定的校外实验、实训基地50个，可较好地满足学生实验实训的需要。有集办公自动化、公文无纸化传输、网络视频会议、教学管理和监控、多媒体教学、远程教学等为一体的校园网络系统，网络终端585个。有音乐体育综合楼一座，内设篮球馆、排球馆、乒乓球馆、体操馆、舞蹈',70741);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨电力职业技术学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.hl.sgcc.com.cn/hdy/','哈尔滨电力职业技术学院位于哈尔滨香坊区，毗邻高新技术产业开发区，始建于1958年。雄厚的教学资源学院拥有18300平方米的教学大楼，17168平方米的实验、实训基地，18862平方米的学生宿舍，4000平方米的俱乐部，17909平方米的体育场，配有体操房、健身房、乒乓球室等。学院实习、实验设备价值2197万元，有125MW、200MW、300MW、600MW和220KV变电站的仿真机，教学用计算机609台，37个现代化多媒体教室，49个校内实验实训室，12个校外实训基地，在籍学生3350人。精干',157864);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨铁道职业技术学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.htxy.net','国家百所骨干院校首批建设单位国家技能人才培育突出贡献单位中央财政支持的重点专业7个，地方财政支持的重点专业5个，中央财政支持建设的示范性实训基地3个国家级精品课程1门，省级精品课程12门全国文明单位黑龙江省示范性高等职业院校重点建设单位黑龙江省普通高等学校毕业生就业工作先进集体学院始建于1959年，前身为哈尔滨铁路工程学校。2002年晋升为高职学院，是黑龙江省5所示范性高职院校之一。由黑龙江省人民政府与中国铁路工程总公司共建。2009年进入省级文明单位标兵，2010年成为国家百所骨干院校首批建设',669881);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大兴安岭职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.dxalu.org.cn','大兴安岭职业学院是一所公办全日制综合性普通高等院校，是中国最北的大学，也是大兴安岭地区唯一的高等学府。学院位于国家级生态功能区—黑龙江省大兴安岭地区首府加格达奇甘河南岸，与大兴安岭映山红滑雪场、文体中心、寒温带植物园和今年通航的嘎仙机场毗邻。学院是2003年在原大兴安岭师范学校、卫生学校、林业学校和广播电视大学的基础上组建而成。以最早成立的卫生学校为起点，开办职业教育已有40余年的历史，培养各类人才二万余人，其中包括茅盾文学奖获奖作家、教科研知名专家、社会管理优秀领导者和工商界有成就的企业家等一',74944);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江农业经济职业学院',' ','黑龙江',0,2,0,0,0,' ','暂空',' ','http://www.nyjj.net.cn','“国家示范性高等职业院校”黑龙江农业经济职业学院是经省政府批准、教育部备案、面向全国招生的公办全日制综合性普通高等学校，隶属于黑龙江省农业委员会。现有在校生8600人。悠久的办学历史学院始建于1958年，前身是东北农学院牡丹江分院，系当时牡丹江市唯一本科建制的高等学府。建校以来，先后被农业部、教育部、省委、省政府授予A等一级学校、教育改革先进单位，省级文明单位、省级花园式单位、省级绿色学校、省级综合治理先进单位、省高校安全保卫工作先进单位、全省毕业生就业工作先进集体等荣誉称号，05年被中国教育科',79521);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨职业技术学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.hzjxy.net/','哈尔滨职业技术学院是哈尔滨市为满足经济社会发展对技术技能型人才不断增长的需求，按照城市晋位、产业晋位、教育晋位的思路，高标准、高起点创办的一所主要面向装备制造业和现代服务业的高职院校。多年来，学院以专业建设为重点，以人才培养模式创新和课程改革为突破口，以师资队伍和实训条件建设为保障，全面深化教育教学改革，促进内涵建设上水平，为省市经济社会发展提供了重要的人才支撑。多次获得省级“文明单位标兵”、“职业教育先进单位”、“教育系统先进集体”、“普通高等学校学生工作先进集体”、“就业工作先进集体”、“职',315548);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海农林职业技术学院',' ','上海',0,2,0,0,0,' ','暂空',' ','http://www.shafc.edu.cn','上海农林职业技术学院在上海市农业学校办学基础上于2002年4月建立，办学历史最早可追溯到1947年，是上海市唯一一所以都市农林和现代生物技术应用为特色的高等职业技术院校。现隶属于上海市教育委员会，是上海市特色高职院校建设单位。学院秉承“为农服务特色立校”的办学宗旨，弘扬“尚农乐耕生生不息”的校园文化精神，为创建全国一流农林职业院校目标而努力奋斗。目前在校生近4千人，教职工292人，副高以上职称41名，专业教师中“双师素质”比例占83%。学院现设有园艺园林系、动物科学技术系、农业生物与生态技术系、',115971);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海邦德职业技术学院',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.shbangde.com/','上海外国语大学贤达经济人文学院系国家教育部2004年批准的上海第一批全日制本科独立学院，由上海外国语大学和上海贤达投资有限公司合作举办。学校现有虹口、崇明两个校区。虹口校区地处市区东北部，毗邻上海外国语大学；崇明校区地处崇明县陈家镇，托斯卡纳和新古典主义风格的优美建筑与特有的生态环境相得益彰。学校依托上海外国语大学雄厚的师资力量以及外语教学与研究等方面的优势，构建以语言、经济类学科为重点、其他学科（专业+英语）协调发展的人才培养体系。目前开设涉及文学、法学、经济学、管理学、教育学和艺术学6大门类',91696);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海中侨职业技术学院',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.shzq.edu.cn','学院简介：上海中侨职业技术学院（简称“中侨学院”）始创于1993年。是一所全日制普通高等职业技术学院，纳入国家统一招生计划。学院浦东校区位于康桥，毗邻上海迪士尼主题公园；金山校区位于张堰，占地500余亩，全欧式建筑，软硬件设施齐全，绿化覆盖37%以上，现有5500余名师生在校学习生活。历经二十几年发展，中侨学院在“积淀内涵，创新发展”中形成“做人做事做学”的校训精神和“创新创业创值”的发展思路。学校以创办一所“专业特色鲜明、办学风格新型”国内外知名一流现代职业教育大学为目标，学院现有7个系1个二',146041);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泰州师范高等专科学校',' ','江苏',0,4,0,0,0,' ','暂空',' ','http://www.tzsz.net/','泰州师范高等专科学校是经国家教育部批准成立的一所公办普通高等院校。2007年,荣获国家教育部人才培养工作水平评估优秀等级；连续10年5届被评为江苏省文明单位；先后荣获江苏省文明学校、江苏省安全文明校园、江苏省思想政治工作优秀单位、江苏省大学生社会实践先进单位等荣誉称号。先进的办学理念学校诞生于1941年，办学历史悠久，办学底蕴深厚。长期以来，坚持“以生为本”、“科学发展”的先进办学理念，秉承“立德、立功、立言”的校训、“求真、求善、求美”的校风和“笃学善思、创新致用”的学风，形成了“紧跟时代步伐',135343);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泰州学院',' ','江苏',1,0,0,0,0,' ','暂空',' ','http://www.tzu-edu.cn/','泰州学院位于素有“汉唐古郡、淮海名区”美誉的国家历史文化名城——江苏省泰州市。泰州学院是一所公办本科院校，其历史可以追溯至1941年我党在黄桥革命老区创办的江苏省第一所培养抗日干部和教师的学校——“泰兴师范学校”和建国后江苏省首批成立的省属中师——“泰州师范学校”，2001年两校合并成立泰州师范高等专科学校，2013年在泰州师范高等专科学校基础上成立泰州学院。学院总占地面积772亩，全日制在校学生6500多人。现有人文学院、旅游管理学院、数理信息学院、外国语学院、教育与心理科学学院、机电工程学院',54700);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太湖创意职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.thcyzy.org/','院校国标代码：12918院校江苏省代码：1959院校名称：太湖创意职业技术学院主管部门：江苏省教育厅办学性质：民办普通高职教育办学层次：专科太湖创意职业技术学院，是2007年3月经江苏省人民政府批准成立的全日制普通高等学校，旨在为创意产业和现代服务业培养富有创新精神、创业志气和创造能力的应用型人才。学院地处文化名城无锡市，位于青山、龙山之间，环境优美，交通便捷，是莘莘学子读书、成才的理想场所。学院教育以“育人成才”为目标，坚持“两个面向”：面向企业，按需施教;面向学生，因材施教。学院实施产学合作',25593);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('炎黄职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.yhust.edu.cn','炎黄职业技术学院是涟水籍台胞、台湾东南科技大学、经国管理学院创办人蒋志平先生1999年筹资举办，经江苏省人民政府批准、教育部备案的一所全日制普通民办高校。学院执行国家统招计划，颁发经教育部监制、学信网注册的普通大学专科学历文凭。学院地处江苏省淮安市涟水，位于京沪高速公路、宁连高速公路交汇处，毗邻淮安涟水国际机场，同时徐淮盐、连淮扬镇高铁路线即将贯通淮安涟水，交通十分便利。校园规划用地990亩，校舍建筑面积11万平方米，可容纳在校生8000余人。学院设有“四系”30个专业，即：时尚造型设计系、建筑',48233);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京科技职业学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.njcc.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校南京化工职业技术学院地处素有“中国化工摇篮”之称的中国重化工基地——南京市大厂镇，隶属于江苏省教育厅。前身是1958年中国化学工业的创始人范旭东先生创办的“远东第一大厂”——永利化学工业公司錏厂厂办校。50多年来，学院置身于扬子石化、南化集团、金陵石化、仪征化纤等国家特大型化工企业和南京国家化工园之中，历经南化化工学院、南京化工学院南化分院、南京化工学校等变迁，学院始终坚持化工方向不动摇、坚持培养化工人才不动摇、坚持发展化工主干专业不动摇、坚持以服务',66065);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('正德职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.zdxy.cn/','我院是1998年江苏省人民政府批准建立、国家教育部备案的全日制民办普通高校，由南京航空航天大学校友会和南京江宁经济技术开发总公司共同出资。2007年，为更好地依托南京航空航天大学的教学资源，把学院进一步办大办强，在原江苏省委书记李源潮的指示下，改由南京航空航天大学校友会独家举办。我院1999年起面向江苏省招生，2004年招生范围扩大到安徽、浙江、福建、山西、山东等省份。学院实行董事会领导下的校长负责制。原南京航空航天大学教务长、博士生导师张焕春教授任院长。学院坚持“正德厚生”校训和“以人为本，质',44711);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('钟山职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.zscollege.com','钟山职业技术学院是1998年经江苏省人民政府、国家教育部批准建立的一所全日制民办普通高等院校。学院坐落在六朝古都南京东郊钟灵毓秀的紫金山麓，沪宁高速、南京二桥高速、宁杭国道、南京绕城公路、即将建成通车的地铁二号线在这里交汇，迎送南来北往的莘莘学子。学院由江苏省人民政府原副秘书长潘春林任党委书记，南京工程学院原副院长朱皑强教授任院长。2006年12月学院以良好的成绩通过了国家级高职高专院校人才培养工作水平评估。学院目前占地638亩，近8000名在校生分布在全院10个系（院）的43个专业。钟山职业技',133126);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('无锡南洋职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.wsoc.edu.cn','独特的区域优势学院地处国家经济实力强劲的“长三角经济中心城市”无锡，坐落于著名的太湖山水城旅游区，毗邻三国城、鼋头渚等5A级风景名胜。地理位置得天独厚，周边名企荟萃，为有志青年的择业、创业提供了广阔的发展天地。优质的品牌专业群学院形成了汽车类、建筑类、航空类、机电类、会计类和国际本科等在江苏高校中独具特色、优势显著的品牌专业群。其中学院汽车技术服务专业群被列入江苏省“十二五”高等学校重点专业;“汽车技术服务实训基地”被江苏省教育厅授予“省级高职实训基地”。多元化的教学模式建立了融专业教学、岗位培',47498);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽中医药高等专科学校',' ','安徽',0,3,0,0,0,' ','暂空',' ','http://www.ahzyygz.com/','安徽中医药高等专科学校座落于具有“皖江明珠、创新之城”美誉的国家级开放城市、皖江城市带双核心城市——芜湖，交通便捷，铁路、高速公路通往全国各大城市。学校前身为芜湖中医学校，始建于1960年，2002年3月经教育部批准，独立升格为安徽中医药高等专科学校，是教育部第一批设立的中医药专科层次的公办高校。学校位于芜湖市城南高校园区内，傍依奥林匹克公园，校园环境优美。学校现为教育部中医药职业教育教学指导委员会副主任委员单位、全国中医药职业技术教育学会常务副理事长单位、全国中医药高职高专教育教材建设指导委员',457731);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽医学高等专科学校',' ','安徽',0,3,0,0,0,' ','暂空',' ','http://www.ahyz.cn/','学校是一所隶属于安徽省卫生厅主管的公办普通专科学校，坐落于安徽省省会合肥，具有近60年的办学历史，有芜湖路校区和芙蓉路校区，环境优美、交通便利，是合肥市政府授予的“花园单位”。学校秉承“厚德精医，博学笃行”的校训精神，办学成效突出，是全国精神文明建设工作先进单位、全国卫生系统先进集体，安徽省示范性高等职业院校；2011和2012年度连续两年荣获安徽省普通高校毕业生就业工作标兵单位，2012年安徽省普通高校大学生创新创业示范校，2012年在安徽省高职院校人才培养工作评估中以“优秀”成绩通过；获评教',678579);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('亳州学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.bznc.net.cn/','亳州学院是一所全日制公办普通本科高等学校，坐落在“国家历史文化名城”、“全国首批优秀旅游城市”、全国“四大药都之首”——安徽亳州。这里是曹操、华佗的故乡，老子、庄子、花木兰、陈抟老祖都曾在这里留下历史活动轨迹和丰富的精神遗产，这里文化底蕴深厚，尊师重教氛围浓郁。学校源于1909年的蒙城师资讲习所，1952年创办蒙城初级师范学校，1956年更名为蒙城师范学校，2002年升格为亳州师范高等专科学校，2016年3月经国家教育部批准，独立升格为亳州学院。百余年的办学历史，已为地方培养了3万多名合格的基础',231185);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州理工职业学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.qzit.edu.cn/page/home/qzit/Index.aspx','泉州理工职业学院（原泉州中营职业学院）是一所具有独立颁发国家学历文凭资格的民办全日制普通高等职业技术学院，坐落在全国首批历史文化名城、国际花园城市——福建省泉州市。主校区位于市中心，与客运中心站毗邻而居，现已建成校舍10.23万平方米，拥有28个专业实验室和47个校内外实训基地，图书馆藏书20余万册（另有电子图书46万册），校园雅致、功能齐备，是学生养德求知学技能的理想场所。新校区占地500亩，位于素有“泉南佛国”、“海滨邹鲁”之美誉的中国品牌之都——晋江市的内坑镇，毗邻高速铁路泉州南站、福建省',68649);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西司法警官职业学院',' ','江西',0,11,0,0,0,' ','暂空',' ','http://www.jxsfjy.cn/','江西司法警官职业学院是2002年4月经江西省人民政府批准，国家教育部备案的政法类普通高校。与江西省政法干部学校，同一建制。同时承担普通学历教育和在职政法干警的继续教育、岗位培训任务。行政上隶属省司法厅领导；业务上接受省教育厅指导。学院分设昌北校区和长?校区。学院拥有教学大楼、田径运动场、散打馆、健身房、电子阅览室、微机室、语音室、多媒体教室、闭路电视系统、刑侦实验室、校园网等现代化教学设施；有模拟法庭、射击训练场、汽车驾驶训练场等实训场所，并在县级法院、检察院、司法局和监狱、律师事务所建立了固定',141065);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西陶瓷工艺美术职业技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.jxgymy.com/','江西陶瓷工艺美术职业技术学院是经江西省政府批准、国家教育部备案的一所公办综合性普通高等高等学府，是中国工艺美术和陶瓷行业大型专业院校，它以工艺美术类和陶瓷类工程为特色，集文、艺、理、工、商、贸于一体，面向各行业培养既有较高综合文化素养，又有较强实际操作能力的高等应用型人才。学院始创办于1958年，后因历史原因停办，1979年在现校址恢复办学。学院现有占地面积389亩，建筑面积10.12万平方米，专职教师200多名，他们中有享受国务院特殊津贴的“有突出贡献的中青年专家”、教授级高级工程师、国家及省',73487);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('渤海船舶职业学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.bhcy.cn/','“国家示范性高等职业院校建设计划”骨干高职院校渤海船舶职业学院原隶属于中国船舶工业总公司，是我国北方唯一一所以培养船舶工业高、中级专业技术人才为主、面向全国招生、国有公办的全日制高等职业技术学院。学院位于辽宁省葫芦岛市，现有龙湾、望海、莲花三个校区，校园占地面积38万平方米，建筑面积14万平方米，目前在校生8000余人。校园环境优美、设施完善，是莘莘学子读书治学、成才发展的理想家园。建校以来，学院主动适应我国船舶工业发展的需要，面向市场调整优化专业结构，开设37个高职专业，其中，船舶工程技术(船',95101);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西旅游商贸职业学院',' ','江西',0,6,0,0,0,' ','暂空',' ','http://zsb.jxlsxy.com/','江西旅游商贸职业学院是经江西省人民政府批准组建的江西唯一一所旅游类公办全日制高等院校，是全省示范性高等职业学院。学院秉承“求真笃行”的办学理念，致力于为社会培养应用型、技能型、复合型高级职业人才。学院现有两个校区，青山湖校区位于南昌市洪都北大道1131号，毗邻风景秀丽的青山湖风景区；昌北校区位于国家南昌经济开发区。校园占地面积1000多亩，建筑面积达30多万平方米，绿化面积达70％以上。学院现有在校生18000多人，专兼职教师1100多人，其中教授、副教授职称占30％，本科以上学历达100％，外',142185);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西电力职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jxdlzy.com/','江西电力职业技术学院创建于1958年，是一所为全国电力系统和工矿企事业等单位培养应用型技术人才的公办全日制普通高等院校。建校近50年来，积淀了厚重的文化底蕴、优良的办学传统和成功的育才经验，所培养的毕业生以专业基础知识扎实、动手能力较强、爱岗敬业，受到各用人单位的欢迎。学院连续多年被江西省教育厅授予毕业生就业先进单位。2003年被教育部确定为国家职业教育改革试点院校。学院位于南昌市昌北经济技术开发区内，占地680亩，院内绿树成荫，空气清新，亭台楼阁和壁画长廊错落有致，良好的生态环境使学院成为一所',190168);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西环境工程职业学院',' ','江西',0,2,0,0,0,' ','暂空',' ','http://www.hjxyzsc.icoc.cc/','江西环境工程职业学院是一所国家教育部备案的公办全日制普通高等学校（国标代码12934），是我国当今唯一以“环境工程”命名独立设置的高等学校。学校创办于1953年，至今已有54年办学历史。地理位置优越学院座落在我国历史文化名城赣州。赣州是一座千年古城，是近代中国革命的红土地，是我国客家人最大的居住地，也是我国目前最大的一个地级市，人文景观丰富，文化底蕴深厚。赣粤高速、蓉厦高速和京九铁路穿城而过，便捷的交通和秀丽的风光，使这个国家级优秀旅游城市成了国内外游客们的游览胜地。学院前对省内最精致的主题公园',113654);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西艺术职业学院',' ','江西',0,8,0,0,0,' ','暂空',' ','http://www.jxysedu.com/','江西艺术职业学院是经江西省人民政府批准，在（江西省文艺学校）基础上成立的江西省唯一的一所国办综合性艺术类高等专业院校。学院群楼林立，山水相映，是一个环境幽雅、布局合理的花园式学校。近几年，学院推进教育教学改革，拓宽办学渠道，扩大办学规模，提高教学质量，走上了快速发展的道路。学院现有舞蹈、音乐、戏剧表演、美术、综合艺术五个系和一个中专部，设有舞蹈表演、音乐（声乐、器乐）表演、艺术设计、影视表演、戏曲表演、杂技表演等17个专业，形成了多渠道，多层次办学的新格局。学院创办于1951年，至今已有半个多世',68218);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鹰潭职业技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.jxytxy.com.cn','鹰潭职业技术学院（江西师大鹰潭学院）是经江西省人民政府批准，国家教育部备案成立的普通高校，是为适应江西省建设和社会发展需要，以鹰潭教育学院为龙头，以鹰潭师范为基础，融入江西铜业公司技校教育资源组建而成的，学院设校本部和江铜校区。学院地处鹰潭市南郊320国道边，道教发源地龙虎山下，浙赣、皖赣、鹰厦铁路干线交汇处。学院具有开办普通高等专科教育15年历史，共培养全日制大学专科毕业生1856人，开办了数学、中文、计算机、英语、音乐、美术、体育、生化、旅游、电子、初等教育等13个专业，现有在校生2000余',40716);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西信息应用职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jxcia.com','学院性质学院是经省人民政府批准，教育部备案的公办专科层次普通高校。学院位于南昌市迎宾中大道西侧，毗邻明珠般的象湖公园和闻名遐迩的“八大山人书画纪念馆”，优美的自然风光与深厚的人文传统完美融合，相得益彰。学院下设信息技术系、环境工程系、电子工程系、现代设计与管理系。以社会及人才市场需求为导向，形成了以教育部重点建设的信息技术类专业为特色，集工、文、经、艺为一体的学科专业群。在近50年的办学基础上，学院以“服务为宗旨，就业为导向，产学研结合”的办学方针，并以“诚信、博学、创新、求实”为院训，培育了良',86573);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西交通职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://zsb.jxjtxy.com/','“国家示范性高等职业院校建设计划”骨干高职院校学院地处南昌经济技术开发区，是江西省惟一一所培养交通建设专门技术和管理人才的综合性学院。学院创建于1956年，前身为南昌公路工程学校，隶属于交通部。1958年，学校升格为本科院校，更名为江西交通学院，并培养了四届本科毕业生，后因国家政策调整，只保留中专部，即江西省交通学校。学校在1993年被评为省部级重点中专学校，2000年被教育部批准为首批国家级重点中专；2002年经江西省人民政府批准，升格为江西交通职业技术学院。学院涵盖工学、文学、经济学、管理学',124388);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西财经职业学院',' ','江西',0,6,0,0,0,' ','暂空',' ','http://www.jxvc.jx.cn/s/32/main.htm','江西财经职业学院是一所隶属于江西省财政厅的全日制公办普通高等专科学校，其前身是创办于1960年的江西省财务会计学校，1988年升格为江西财经学院九江分院(普通专科)，2002年更名为江西财经职业学院。2009年被列为首批“江西省示范性高等职业院校”;2012年学院被教育部评选为50所“全国毕业生就业典型经验高校”;2013年荣获教育部“全国大学生满意度50强高校”;2013年被教育部、财政部评为全国首批国家骨干高职院校建设优秀单位。学院坐落在庐山脚下、长江之滨、鄱阳湖畔的中国十大魅力城市江西省九',228275);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西应用技术职业学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jxyyxy.com/','学院品牌佳：我院始建于1958年，经过多年的积淀，办学优势显著，是首批国家示范性(骨干)高职学院，是老牌的公办名校，是高职高专人才培养工作水平评估和江西省高校毕业生就业工作评估“双优学校”，资源勘查类专业综合实力居全国同类院校首位。综合实力正在向全国一流的高等职业学院迈进。优势专业多：开设了53个专业，有6个中央财政支持的重点专业，2个省级财政支持的重点专业，5个省级示范专业，6个省级特色专业，4个省级人才培养模式创新试验区，1个国家级骨干教师培训基地，2个省级实训中心，10个院级重点专业，2门',124923);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西现代职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jxxdxy.com/','“国家示范性高等职业院校建设计划”骨干高职院校学院性质省属公办全日制普通高等院校，办学历史近30年。江西省唯一一所公办高等职业教育先进单位。地域环境座落在我国历史文化名城，军旗升起的地方。唯一同时处于“珠江三角洲”、“长江三角洲”经济圈及“闽东南”经济圈中的江西省省会城市--南昌。办学规模学院地处江西省南昌市昌东大学园区，占地600余亩，建筑面积23万平方米。目前学院下设化工学院、艺术设计系、信息科学与管理学院、机械学院、电子学院、商务学院、建设学院、文化旅游学院、继续教育学院等9个院系，设置文',157702);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西工业工程职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jxgcxy.net/','我院创办于1956年，为部属全日制公办本科高等院校。国家进行高等教育院系调整以后，历经国家级重点中专学校——江西省第一工业学校等阶段，1999年经省政府批准试办高职教育，2002年经省人民政府批准，升格为全日制普通高等职业院校，在2004年教育部高职高专院校人才培养工作水平评估中被评为优秀，率先通过了江西省高职院校人才培养工作评估，2011年获批江西省示范性高职院校，2014年经省教育厅批准开设3个本科专业。学院座落在中国工人运动的策源地安源。这里是中国近代工业文明的发祥地，具有悠久的历史和深厚',76265);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东圣翰财贸职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.suu.com.cn/','山东圣翰财贸职业学院是经山东省人民政府批准、国家教育部备案，在山东联合大学基础上成立的全日制普通高等院校。1999年，山东长城实业集团总公司投资近亿元建设山东联合大学，开始学历文凭招生；2000年，与山东师范大学合作办学，招收高职专科学生，成为省内最早设立的普通高职专科院校之一；2001年，经山东省人民政府批准（鲁政字[2001]370号）、国家教育部备案，在山东联合大学基础上成立了山东圣翰财贸职业学院，使学院成为全日制普通高等院校，招生面向全国、列入国家统招计划。“坚持一个中心，抓好两支队伍，',62519);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东水利职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdwrp.com/','梦想开始的地方办学品牌—如果你渴求一滴水，我愿意倾其一片海……良好的品牌信誉，让你的求学选择更加放心。山东水利职业学院作为省属公办全日制普通高校，是以水利为特色，以工科为优势的综合性院校，具有50多年职业教育办学的历史。学院始终坚持“以人为本，以水为魂”的办学理念，秉承“上善若水”的品格，敞开“海纳百川”的胸怀，专注内涵发展，打造核心竞争力，是全国首批水利高等职业教育示范院校、山东省首批技能型人才培养特色名校、山东省高校就业工作优秀院校和山东省德育工作优秀高校，荣获“全国职业院校魅力校园”、“山',236955);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东畜牧兽医职业学院',' ','山东',0,2,0,0,0,' ','暂空',' ','http://www.sdmyxy.cn/','学院是山东省唯一的国办畜牧兽医类普通高等学校，1955年建校，直属山东省农业厅、教育厅管理，为国家示范性(骨干)高职院校、山东省示范高职院校。学院座落在美丽的世界风筝之都——潍坊市中心，与市委、市政府、潍坊市电视台等毗邻，地理位置优越，交通条件便利。学院占地2800亩，分两个校区和两个独立设置的教学实训区，校园环境幽雅，人文环境得天独厚。学院面向全国30个省份招生，现有全日制在校生9700余人，畜牧兽医类在校生数量全国第一，是全国最大的畜牧兽医人才培养基地。多年来，在国家和社会的大力支持下，学院',145983);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州澍青医学高等专科学校',' ','河南',0,3,0,0,0,' ','暂空',' ','http://www.shuqing.org','郑州澍青医学高等专科学校建于1984年，教育部批准的民办普通高等院校。学校位于郑州市二七区马寨经济开发区，郑少（郑州市至登封少林寺）高速入口处，毗邻公园，环境幽雅，属花园式单位，是一个良好的学习和生活环境。经过25年的发展，学校现占地面积597亩，建筑面积19万平方米，在校生7000余名的规模，形成两部、四系，临床医学、临床医学（妇产方向）、中医学、护理、口腔、美容、康复、药学、计算机九个专业方向的建制，组成了一支以省内知名专家、学者为骨干的教师队伍，拥有多媒体教室、电教语音播放的现代化教学系统',188747);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州师范学院',' ','河南',1,4,0,0,0,' ','暂空',' ','http://www.zznu.edu.cn/','郑州师范学院坐落于黄河之滨，嵩山之麓的河南省省会郑州,是经教育部批准，于2010年3月在郑州师范高等专科学校的基础上建立的一所全日制普通本科院校，其前身是1949年成立的河南省立郑州师范学校，至今已有60多年的办学历史。学院占地面积1060亩，建筑面积46万余平方米，各类实验实训室160多个，教学仪器设备总价值1.66多亿元;有附属中、小学6所，校外实习实训基地131个;图书馆拥有纸质图书150余万册、电子图书59.6万册、报刊8099种、电子资源数据库8个。建有千兆光纤接入、万兆双核心校园网。',1789466);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('焦作师范高等专科学校',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.jzsz.cn/','焦作师范高等专科学校是经国家教育部批准，在原焦作教育学院、焦作师范学校和沁阳师范学校的基础上合并组建的一所以教师教育为主的全日制普通高等学校。其办学历史可追溯到创办于1907年（清光绪33年）的“怀庆府师范学堂”，至今已有106年的办学历史。栉风沐雨，世纪沧桑，弘文励教，薪火相传。建校百余年特别是新中国成立以来，学校始终高扬师范教育旗帜，秉承“育人为本、德育为先、学术唯真、教学至上”的办学理念，发扬“艰苦奋斗、严谨治学、自强不息、开拓创新”的办学传统，已累计为社会培养输送10万余名毕业生，为河南',340100);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北中医药高等专科学校',' ','湖北',0,3,0,0,0,' ','暂空',' ','http://www.hbzyy.org/','湖北中医药高等专科学校是经国家教育部批准成立的公立全日制普通高等学校，坐落于全国历史文化名城荆州。学校集现代教育、医学科研、临床医疗为一体。良好的校园环境、先进的教学设施、优秀的师资队伍、科学的管理水平，形成了优良的办学传统和良好的校风。为改善办学条件，学校从2004年开始启动新校区建设，新征地500亩，一期工程有教学楼、实验楼、图书馆、行政楼、学生公寓和食堂等，总建设面积约15万平方米，现已全面同步开工，2006年投入使用。现有教职员工598人。拥有一支学历层次高、职称结构合理、具有较强教学和',289988);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉航海职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.whim.edu.cn/','武汉航海职业技术学院建校于1955年，是教育部批准、交通部审核的公办综合性普通高等院校，2012年入选中国海军士官生试点培养院校，是全国交通职业教育工作先进单位、全国技能人才教育突出贡献单位、湖北省职业教育先进单位、湖北省最佳文明单位。学校坐落在九省通衢、美丽的江城武汉，现有喻家湖校区、东湖校区、葛店校区等三个校区，下设航海技术系、旅游系、船舶工程系、轮机工程系、汽车工程系、经济与管理学院、航运物流学院、信息工程学院、培训学院、继续教育学院、国际教育学院、研究生处等12个办学单位，各类在校学生8',177958);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东邮电职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gupt.net','广东邮电职业技术学院是经广东省人民政府批准设立的公办全日制普通高等职业院校，于2002年4月28日正式挂牌成立，隶属于广东省电信实业集团公司，专科层次。其前身是有着五十多年办学历史、被誉为“广东邮电黄埔军校”的广东省邮电学校。学院以学历教育、在职培训及在职研究生教育、管理服务为主营业务，集教、科、产为一体，搭建以服务通信行业为主、覆盖社会各行业的多元化经营、多方位发展的运作模式。学院地处广州市天河高校文化区，毗邻华南师范大学、暨南大学、广东技术师范学院，面向天河公园，与华南快速干线、科韵快速路、',198070);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('汕头职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://stzy.stedu.net','汕头职业技术学院是2002年3月29日经广东省人民政府批准，由汕头市人民政府主办，在原汕头教育学院（含汕头幼儿师范学校）、汕头商业供销学校、汕头机电学校和汕头市教师进修学校等院校的基础上合并设立的全日制普通高职院校。学院原占地面积约632.4亩，2009年3月，市政府已批给学院扩展用地619.1亩，学院总共占地1251.5亩。学院由院本部、金园校区、新津校区和东墩校区四个校区组成。院本部位于风光秀丽的汕头市濠江区东湖山麓，现有436.4亩地，加上扩展用地共占地1055.5亩；金园校区位于汕头市区',288912);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('揭阳职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','oldname": "揭阳职业技术学院','揭阳职业技术学院是1999年经广东省人民政府批准成立的一所公办全日制普通高等学校。学院坐落于交通便捷、风景幽雅的揭阳市区紫峰山下，占地500亩，预留用地500亩。教学、科研、实验、生活、文体等设施俱全，建筑面积达9多万平方米。学院现设有5系1部共24个专业，立足揭阳，面向广东及福建、江西等省份招生，现有在校生达4290多人。学院自创建以来，吸引了一大批来自全国各地高校的具有教授、副教授高级职称和博士、硕士学历专业技术人才，一支教学水平高、知识结构合理、乐于奉献的高素质师资队伍正肩负起教育、教学、',146470);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('深圳信息职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.sziit.com.cn','“国家示范性高等职业院校建设计划”骨干高职院校深圳信息职业技术学院是一所国有公办全日制高等职业技术学院。学院实施全日制学历教育，面向深圳（全省）招收三年制、二年制全日制学生，面向深圳（全省）招收三年制大专、专升本成教学生。学院面向“珠三角”经济发展的主导产业、以计算机类专业和电子信息类专业为重点，专业设置以信息制造业和信息服务业为内涵核心，已建立软件工程系、电子通信技术系、计算机应用系、信息控制与制造系、信息经济系、应用英语系、基础教育部、继续教育部等6系2部。开设有软件技术、软件技术（游戏软件',320912);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('清远职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.qypt.com.cn/','清远职业技术学院成立于2002年，是经广东省人民政府批准、国家教育部备案的综合性公办高等院校。学院坐落在有“珠江三角洲后花园”之称的中国优秀旅游城市——清远市。清远市毗邻广州国际机场，京珠高速、京广铁路纵贯南北，距广州市中心仅60公里，交通便捷。学院规划用地3461亩，目前校园建成面积近千亩，建筑面积17万平方米，拥有较为完善的教学及教学辅助设施。建院以来，学院以科学发展观为指导，确立了“以就业为导向，以质量求生存，以创新谋发展，以特色铸品牌”的办学理念，走提升内涵、扩大规模、创建特色相统一的可',262404);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东工贸职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','oldname": "广东工贸职业技术学院','广东工贸职业技术学院是一所直属广东省教育厅领导的全日制普通高等职业院校。学院座落于风景秀丽的白云山东麓，位于广州市天河区广州大道北。交通方便，环境优美。学院坚持以邓小平理论和“三个代表”重要思想为指导，全面贯彻党的教育方针；紧紧围绕“立足广东，面向市场，贴近行业，为工业技术、商贸经济服务”的办学定位；按照“以发展求进步，以特色创品牌，以质量上层次”的办学思路；致力于为广东经济社会发展培养高素质高技能人才，初步形成以高职教育为主，成人教育与职业技能培训为辅的多元化办学格局。学院设有机电工程系、计算',274374);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东司法警官职业学院',' ','广东',0,11,0,0,0,' ','暂空',' ','http://www.gdsfjy.cn','招生代码：12960广东司法警官职业学院是省政府批准设置、具有高等学历招生资格的省属公办全日制普通高等院校。原全国政协副主席叶选平为学院题写校名。学院设龙洞、石井两校区。学院为副厅级建制，归省司法厅管理，在教学及专业建设等方面受教育厅、教育部指导。学院为广东司法行政系统人才培养基地、广东司法行政系统干警素质提升基地、广东司法行政工作应用型理论研究基地。学生着人民警察制服，在校期间实行警务化管理。学院占地面积308亩，现有教职工312人，其中教师204人，具有高级职称教师80人，在校专科学生460',411532);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东亚视演艺职业学院',' ','广东',0,8,0,0,0,' ','暂空',' ','http://www.atvcn.com','广东亚视演艺职业学院创办于2000年，是一所经广东省人民政府批准成立、教育部备案、纳入国家统一招生计划的全日制普通高等院校。学院主要围绕戏剧影视类、音乐舞蹈类、艺术设计类、经济管理类、信息工程类开设了18个招生专业。学院是国家音响调音师及录音师职业技能资格鉴定点、国家演出经纪人资格证考点和中国舞蹈家协会舞蹈教师培训基地。学院座落于中国最具魅力、最具经济活力的现代制造业名城、全国文明城市——东莞市。学院建有图书馆、影视实训摄影棚、影视后期编辑室、录音棚、多功能实验剧场、实训演播厅、戏剧排练厅、舞蹈',95632);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东省外语艺术职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gtcfla.net/','广东外语艺术职业学院是一所经广东省人民政府批准建立的公办全日制普通高等学校。学院在原广东省教育厅直属重点师范学校??“广东外国语师范学校”和“广东艺术师范学校”的基础上于2001年组建成立，至今已有21年的大专办学历史，积累了高等教育管理和基础教育师资培养、培训的丰富经验，设外语系、音乐系、美术系、学前教育系、信息技术系、中文系和继续教育学院等7个教学单位。学院以基础教育师资培养为核心，以外语艺术专业为特色，电子信息、艺术设计、传媒、公共事业、旅游等多门类学科并举。是广东省唯一一所实行五年制专科',231096);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川托普信息技术职业学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.scetop.com/','招生代码5164国家级示范性软件职业技术学院国家技能型紧缺人才培训基地四川省普通高等学校毕业生就业工作先进集体四川托普信息技术职业学院创办于2000年，是经四川省人民政府批准、教育部备案的全日制普通高等学校，是教育部批准的国家级示范性软件职业技术学院。也是国家技能型紧缺人才培训基地、电子信息产业国家高技能人才培训基地、全国服务外包人才培训基地、四川省电子信息产业青年技师培养基地。学院立足经济社会发展需求，主动对接四川省支柱产业——电子信息产业，努力打造“信息技术”特色，开设了社会亟待需求的36个',149254);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川国际标榜职业学院',' ','四川',0,8,0,0,0,' ','暂空',' ','http://www.polus.edu.cn/','四川国际标榜职业学院是国家批准的全日制普通大学，是当今世界时尚造型界最具权威的教育机构“国际标榜”在中国设立的唯一排他性高等学历教育机构，也是迄今为止我国时尚造型艺术行业唯一的高等院校。学院引进国际先进的教育理念，倡导现代艺术、现代科技与现代经济相结合的办学思想，吸收和传承优秀民族文化和民间艺术精华，坚持“服务大众、回馈社会”的办学宗旨，秉承“以人为本、学以致用”的教育原则，围绕全面小康社会人们对生命活力美、外表形式美、生活方式美等日益增长的健康生活需求，充分利用国际教育资源，突出中国民族特色。',99187);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都农业科技职业学院',' ','四川',0,2,0,0,0,' ','暂空',' ','http://www.cdnkxy.edu.cn/','成都农业科技职业学院是经四川省人民政府批准建立的普通高等院校。学院始建于1958年，是成都市人民政府主办的第一所高等职业学院，也是四川省唯一一所以农为主的高等职业学院。学院在50多年的办学历程中，始终把情感系于“三农”，把理想系于农村，把教室放在田野，把出路寄于创新，形成了“亲农、事农、兴农，敬业、勤业、创业”的学院文化精神，铸就了“为三农服务、为兴农育人”的办学传统和办学特色。学院2006年在教育部组织的全国高职高专人才培养工作水平评估中获得“优秀”，2014年被评为省级示范性高等职业院校，现',389490);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宜宾职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.ybzy.cn/','宜宾职业技术学院是由宜宾市政府主办、四川省人民政府批准、教育部备案的面向全国招生的全日制普通高等专科院校。历史悠久，位置优越学院始建于1934年，秉承“鼎承大同，钵传天工”的校训，办学80多年来为国家培养了大量的技术和管理人才。学院位于万里长江第一城——四川省宜宾市。坐落在风景秀丽的金沙江畔，占地1000余亩，依山傍水，绿树成荫，是一所绿色园林式学院。宜宾市是世界名酒五粮液的故乡，拥有厚重的历史文化、多元并驾的产业优势、陆海空联运的交通优势，有闻名中外的国家5A级风景区蜀南竹海、世界地质公园兴文',169466);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泸州职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.lzy.edu.cn  ','泸州职业技术学院是经四川省人民政府批准建立的一所综合性的全日制普通高等学校。学院地处历史文化名城泸州市，坐落于风景秀丽的长江之滨，是省级园林式单位，教读条件十分优越。学院占地面积522.5亩，校舍建筑面积155676平方米；按照校园规划和土地预留，学院占地将扩展到1000亩左右。学院荟萃着光荣传统、优良校风和高等教育办学经验。其前身泸州师范学校是具有光荣革命传统的百年名校，泸州教育学院拥有数十年的高等教育办学底蕴、四川省水利机电学校具有先进的办学理念和鲜明的办学特色。学院拥有先进、完备的教学、文',192217);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('眉山职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.msvtc.net/ ','眉山职业技术学院地处东坡故里、千载诗书城----眉山市，是2002年4月经四川省人民政府批准组建、国家教育部备案的眉山唯一一所全日制公办普通高等学校。学院前身是原眉山师范学校和眉山农业学校。原眉山师范学校始建于1927年春，是经教育部验收通过的标准化建设优秀的师范学校。原眉山农业学校始建于1958年，是省部级重点中专学校。学院地处眉山市眉州大道东延段，规划占地1000亩，第一期工程建设400亩，建筑总面积86390平方米。办学指导思想：贯彻党的教育方针，坚持科学发展，坚持以服务为宗旨，就业为导向',166267);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都艺术职业学院',' ','四川',0,8,0,0,0,' ','暂空',' ','http://www.cdartpro.cn','成都艺术职业学院是经四川省人民政府批准成立、教育部备案、直属四川省教育厅领导的一所全日制普通高等职业院校。立足文化创意产业和城市化建设的服务面向，成艺办学特色突出，人才培养成效显著，已经形成了以设计艺术类为主体，音舞表演类和管理类专业为“两翼”的合理专业结构。设计艺术类专业在西部地区同类学校中规模最大、办学历史较长，并与本科院校联合办学，举办过四川美术学院成都分院、四川师范大学设计艺术学院，办学12年培养了数以千计的设计艺术专门人才，被誉为西南部地区的应用型“设计师”的摇篮。依托国家城市化建设的',183222);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.sczyxy.cn/','四川职业技术学院是由四川省人民政府主办，四川省人民政府、遂宁市人民政府共建，省教育厅主管的全日制普通高等学校，学校坐落于国家级优秀旅游城市、四川省第二大交通枢纽遂宁市。学院现有新老两个校区，占地近1100亩。新校区建筑总面积30余万多平方米，固定资产总值达6亿元，学院具有近百年的办学历程，积淀了深厚的人文底蕴，积累了丰富的办学经验。学院现有教职工750人，专任教师600人，其中具有正、副高级职称200余人，博士、硕士研究生310余人，有享受政府津贴专家3人，省学术和技术带头人1人，省突出贡献专家',298794);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('玉溪农业职业技术学院',' ','云南',0,2,0,0,0,' ','暂空',' ','http://www.yxnzy.net/','玉溪农业职业技术学院的前身是国家级重点中专玉溪农业学校，学校成立于1956年，1991年经省教委评估认定为“合格中专学校”；1993年被省政府认定为“省部级重点中专学校”，同年省政府批准建立“云南省玉溪烟草栽培学校”；1996年被省科委、团省委定为“云南省青年星火技术培训示范基地”，并获云南省农业中专教学规范化管理一等奖；1997年省教委命名为“省级文明学校”；1998年被省委、省政府命名为“省级文明单位”；1999年被省委、省政府命名为“文明单位工作先进单位”；2000年首批步入国家级重点中专',34799);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青海畜牧兽医职业技术学院',' ','青海',0,2,0,0,0,' ','暂空',' ','http://www.qhxmzy.com.cn/','“国家示范性高等职业院校”青海畜牧兽医职业技术学院位于青藏高原与黄土高原的分界岭—日月山东南40公里，素有“海藏咽喉”之称的西宁市湟源县城。始建于1940年，前身是青海省湟源畜牧学校，1994年、2000年先后两次被教育部确定为首批国家级重点中专学校；1998年被农业部确定为全国农业职业技术教育培训示范基地。2002年经省人民政府批准，升格为青藏高原惟一的全日制农牧类高等职业院校。是中央财政重点支持建设的示范性职业技术院校之一。2006年，通过“国家高职高专教学水平评估”，被评定为“优秀”；20',35972);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青海交通职业技术学院',' ','青海',0,1,0,0,0,' ','暂空',' ','http://www.qhctc.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校1976年12月青海省交通厅在原青海省交通厅干部训练班的基础上成立了青海省交通技术学校（同厅干训班合署办公，1984年厅干训班与交通学校分离），属技工性质，1979年7月升格为青海省交通学校，属中专性质。1984年12月，省交通厅在青海省交通中学的基础上成立了青海省交通职工中专学校，同时在省运输二中的基础上成立了青海省交通技工学校；1996年6月，省交通厅决定交通职工中专学校与交通技校合并，保留青海省交通技工学校牌子；1998年省交通厅决定省交通学校',56762);
