insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('伊犁职业技术学院',' ','新疆',0,2,0,0,0,' ','暂空',' ','http://www.ylzyjs.cn','伊犁职业技术学院是经国家教育部和新疆维吾尔自治区人民政府批准设立的一所全日制公办普通高等院校,学院坐落在素有“塞外江南”之称的伊犁哈萨克自治州首府伊宁市。学院成立于2002年3月，是由有50余年办学历史的自治区重点中等专业学校－新疆伊犁畜牧兽医学校、伊犁农业学校和伊犁农牧机械化学校合并而成。学院占地面积1300余亩，学院建筑面积达十万平方米，图书馆藏书15万册。学院设有动物科学系、农业工程系、机械电子工程系、商学系、基础部、信息技术中心、思想政治理论研究室、继续教育培训中心、图书馆等教学、教辅部',33275);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西机电职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://zs.jxjdxy.com/','江西机电职业技术学院是经省人民政府批准成立，由国家教育部备案的公办普通高校，办学47年，是我省机电行业唯一的一所办学实力最强的培养高等专科应用性专门人才的学府，学院实施大学专科多层次教育，面向全国招生。现有全日制在校学生人数7500余人。学院新址位于风景秀丽、大学荟萃的南昌市昌北经济开发区。教学设施学院占地235亩，建筑面积18.2万平方米，教学设施和运动场(馆)齐全，图书馆藏书20余万册，有各学科实验室32个，数控机床等各类机床165台，先进的多媒体教室17个。学生公寓宽敞明亮，每个寝室电话、',103901);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉铁路职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.wru.com.cn','“国家示范性高等职业院校”武汉铁路职业技术学院是由湖北省人民政府主办的在鄂唯一一所具有铁路专业特色的全日制普通高等院校。学院连续14年被评为湖北省文明单位，是全国德育工作先进学校、全国群众体育先进单位、铁道部教育先进单位、湖北省2008年抗雪救灾先进单位。是高职高专院校人才培养工作水平评估优秀学校，湖北省示范性高等职业院校建设立项单位。学院位于国家级高新开发区——武汉东湖高新开发区风景秀丽的江夏汤逊湖畔，占地776亩。学院经财政部、教育部批准，创建了我国第一个国家高速铁路实训基地。建有104个先',714872);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉软件工程职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.whvcse.com/nindex.asp?nid=181','“国家示范性高等职业院校建设计划”骨干高职院校武汉软件工程职业学院是武汉市人民政府主办的综合性高等职业院校，是湖北省唯一一所被教育部评审认定的全国示范性软件职业技术学院建设单位，是教育部等六部委确定的“计算机应用与软件技术”、“汽车运用与维修”技能型紧缺人才培养培训基地，2006年被评为湖北省职业教育先进单位。学院地处“光谷”腹地，总占地面积1293.71亩，建筑面积40.86万平方米，仪器设备总值5717万元，计算机5113台，实训（实验）室186间，稳定的校外实习实训基地120余个；图书馆馆',195649);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北三峡职业技术学院',' ','湖北',0,12,0,0,0,' ','暂空',' ','http://sd.powereasy.net:7150/','湖北三峡职业技术学院是经省政府批准、教育部备案，宜昌市委、市政府主办的全日制高等学校。办学历史可追溯到1949年8月成立的宜昌专署财经学校。学校座落于举世闻名的三峡工程所在地、世界水电旅游名城——湖北省宜昌市区中心，依山傍水，环境优美，风景秀丽，先后被评为省级文明单位、省级园林式单位、省级平安校园。学校占地面积1621亩，建筑面积32.82万平方米，固定资产4.13亿元，图书馆纸质藏书59.6万册，教学仪器设备总值7783万元；建有55个校内实训基地，251个校外实训基地。目前已拥有4个国家职业',197859);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('随州职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.szvtc.cn/','随州职业技术学院是湖北省人民政府批准，国家教育部备案的一所全日制普通高等院校，是一所以文、理、工、医、电子、信息、教育、艺术等多门类多学科体系为主，集普通大、中专及远程教育于一体的综合性职业技术学院。学院坐落在全国历史文化名城??湖北省随州市。这里是中华民族的始祖炎帝神农的诞生地，也是“世界第八大奇迹”曾侯乙编钟的出土地。随州地理位置优越，气候宜人，交通便利，汉丹铁路、宁西铁路、京广铁路、316国道、107国道、312国道纵横其境，正在兴建的孝襄高速公路和随岳高速公路穿越随州。学院完善的办学条件',84533);
