insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉科技职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.whuvt.com/','武汉科技职业学院是2002年经湖北省人民政府批准,国家教育部备案成立的一所民办普通高等职业学院。学院始建于1984年，经过20余年的建设与发展，学院已经成为一所以电子信息应用技术为主，工、经、管、文等专业协调发展，初具办学特色，享有良好社会声誉的高等职业学院。一、历史沿革武汉科技职业学院是由原民办东湖大学和原民办武汉科技工程学校组建而成。东湖大学创办于1984年5月，武汉科技工程学校创办于1993年5月。2001年，湖北省教育厅、湖北省发展计划委员会（鄂教发[2001]64号）批准以“武汉科技工',63145);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉外语外事职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.whflfa.com','武汉外语外事职业学院于2002年经湖北省人民政府批准成立，并经国家教育部备案，是中南地区唯一一所专门培养外语外事人才的高等职业学院，属专科层次全日制普通高校。学院座落在美丽的东湖之滨、磨山脚下，位于落雁岛风景区中部；东邻正在兴建的武汉火车站，西连烟波浩渺的东湖，南倚婀娜多姿的磨山，北望车水马龙的友谊大道；环境优雅、风光秀丽，是莘莘学子治学深造的理想场所。学院占地面积约480亩，建筑面积30余万平方米，累计投入3亿余元建成包括教学楼、运动场、图书馆、各类实验室、标准学生公寓、学生食堂、标准田径场、',72003);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉信息传播职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.whinfo.cn','武汉信息传播职业技术学院是经湖北省人民政府批准、国家教育部备案、湖北省教育厅领导的具有独立颁发大学专科文凭的全日制普通高等学校，学制三年。学院由著名的知音期刊出版集团有限公司投资主办，以印刷技术、新闻出版与发行为特色，实行学历证书与职业技术等级证书“双证”制度，培养知识面宽、社会适应能力强的高等技术应用型专门人才。学生可按国家相关规定，通过不同方式取得本科深造的机会。学院位于中国光谷——武汉东湖高新技术开发区（庙山科技园）的中心位置，占地面积480亩。校园布局合理，环境优美，拥有配套的现代化教学',47882);
