insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建农林大学东方学院',' ','福建',0,12,0,0,0,' ','暂空',' ','http://www.fjdfxy.com/','福建农林大学东方学院是2002年由福建农林大学申办，2003年底经教育部首批批准为实施全日制本科教育的独立学院，学院坐落在东海之滨的风景秀丽的琅岐岛上。学院占地面积500多亩，校舍面积90000平方米。图书馆藏书35万册，校园网络已覆盖全校。学院创办六年来坚持“以人为本、机制创新、质效双求、办出特色”的办学理念，开拓创新，稳步发展，目前全院有理、工、经、管、文、法6大学科门类，设有6个系，19个专业（含专业方向），在校生7000多人。学院充分利用具有72年办学历史的福建农林大学的雄厚师资力量、教',336507);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波大红鹰学院',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.nbdhyu.edu.cn/','宁波大红鹰学院是一所经国家教育部批准设立的全日制民办普通本科高校，由宁波大红鹰教育集团投资举办。学校前身为创办于1997年12月的宁波职业教育专修学院。2001年4月，经浙江省人民政府批准创建宁波大红鹰职业技术学院，2002年5月，正式成立;2003年11月，成为全国创办国家级示范性软件职业技术学院的35所高校之一;2007年6月，以优秀成绩顺利通过教育部高等院校人才培养工作水平评估;2008年4月，经教育部批准升格为本科院校，并更名为宁波大红鹰学院。学校地处国家历史文化名城、现代化国际港口城市',339386);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江东方职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.zjdfc.com/','浙江东方职业技术学院是经浙江省人民政府批准建立，国家教育部同意备案的全日制普通高校，列入国家教育部普通高校招生计划，面向浙、甘二省招生。学院位于最具活力的沿海开放城市之一的温州市。被温州市人民政府命名为花园式校园，浙江省文明单位、平安校园。学院主动适应浙江省经济和社会发展需要，以培养高等技术应用型专门人才为根本任务，注重专业建设，努力办人民满意的高职学院。现设物业管理、电气自动化技术、计算机应用技术、计算机信息管理、应用电子技术、包装技术与设计、印刷技术、财务管理、会计、国际经济与贸易、市场开发',55541);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('义乌工商职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.ywu.cn/','义乌工商职业技术学院（简称义乌工商学院）坐落在国际性商贸名城义乌市，是一所工商贸易学科优势较强的公办全日制普通高等院校。学院前身是创办于1993年的杭州大学义乌分校，院长由王珉教授担任。学院地处义乌城区，依山傍水，占地1000余亩，建筑面积23.9万平方米。秀丽的自然景色，独特的建筑风格和庄重的人文景观融为一体，是一所优美的山水园林式大学。学院目前有正式教职工334名，全日制中外学生约8800余人，非全日制学员1000余人。学院下设工商管理、计算机工程、外语、艺术设计、土木工程、现代文秘、旅游、',83389);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛飞洋职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.feiyangcollege.com/','★学院简介：青岛飞洋职业技术学院是一所经山东省政府批准、国家教育部备案的民办、全日制普通高等院校，学院坐落于青岛市城阳区。目前，学院下设17个二级学院,开设了39个市场急需的紧缺、热门统招专科专业，在校学生9600余人，现有教职员工800余人，其中王益姝、王德祥、李鲁晋等多名教授享受国务院政府特殊津贴。★历史沿革学院前身是青岛飞洋经贸进修学院，成立于1996年，2002年经山东省人民政府批准改建成为青岛飞洋职业技术学院。★基本办学条件我院始终把教学质量作为学院发展的生命线，为了提高产、学、研和师',42694);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东英才学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.ycxy.com/cn/index.html','山东英才学院坐落在济南高新区大学园区。是国家教育部批准成立的具备独立颁发学历文凭资格的全日制普通本科高校。学院实际占用地1100余亩。学院现设机械制造与自动化工程学院、计算机电子信息工程学院、外国语学院、经济管理学院、商学院、学前教育学院、艺术学院、建筑工程学院、前卫艺术学院、医学院、农学院、国际交流学院等12个二级学院。一、园林式生态校园现代化教学设施学院绿草如茵，空气清新，环境优美，设施完善，具有浓厚的科学文化气息，是学子们理想的读书成才之地。学院教学生活设施先进，配套齐全。教学楼、综合楼、',513365);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东交通职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdjtzyxy.com ','山东交通职业学院为专科层次省属公办普通高等院校，实行山东省交通运输厅和山东省教育厅双重领导、以山东省交通运输厅为主的管理体制。是山东省唯一一所国办交通类高等职业院校，学院先后被中华人民共和国交通运输部确定为“全国交通职业教育示范院校”、被山东省教育厅确定为山东省省级示范性高等职业院校，2012年学院被山东省教育厅确定为山东省名校工程首批立项建设单位，2015年教育部全国高校首批现代学徒制试点单位。学院拥有中心校区、北校区、东校区和泰山校区四个校区，环境幽雅，交通便利，各类教学设施齐全，教学条件优',202145);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淄博职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.zbvc.cn/','淄博职业学院坐落于齐国故都、工业名城、国家园林城市、全国文明城市山东省淄博市。鲁中地区独特的经济结构和文化底蕴赋予了学院尚工重商、医学人文并举的专业特色。学院在2006年教育部人才培养工作水平评估中获得优秀等次;2007年入选国家示范性高等职业院校建设单位，2010年以优秀成绩通过验收;先后获得全国职业教育先进单位、全国精神文明建设工作先进单位等国家、省、市级综合荣誉及单项荣誉500余项。建设一流跨越发展淄博职业学院组建于2002年7月，是经教育部批准成立的具有高等学历教育招生资格的公办全日制普',247887);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东外贸职业学院',' ','山东',0,6,0,0,0,' ','暂空',' ','http://www.sdwm.cn/','山东外贸职业学院隶属于山东省商务厅，是山东省最早也是唯一专门从事外经贸教育的全日制普通高等职业院校。学院前身是创建于1964年的山东省对外经济贸易学校和创建于1984年的山东省对外经济贸易职工大学。1988年开始招收普通专科学生，是省内同类院校中最早举办普通专科教育的学校，同年山东大学设立国际贸易专业首届学生由我院培养，1992年至2001年青岛大学国际贸易、涉外会计、商务英语等专业共有十届专科学生由我院培养，1998年成为山东省首批高职试点学校，2002年成立山东外贸职业学院，2014年山东省',254781);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛酒店管理职业技术学院',' ','山东',0,6,0,0,0,' ','暂空',' ','http://www.qchm.edu.cn/','青岛酒店管理职业技术学院是2002年经山东省人民政府批准、教育部备案的全日制大专层次普通高校，是我国第一所独立设置的酒店管理学院，隶属于山东省教育厅和山东省商业集团有限公司。学院继承和发扬60余年办学历史所积淀的优良传统和作风，真抓实干，锐意改革，开创了生机勃勃的发展局面。先后荣获山东省“职业教育先进单位”、“省级文明单位”、“中国商业职业教育先进单位”等称号。在2006年山东省高职高专人才培养工作水平评估中，在2007年山东省高校德育与校园文明建设检查评估中，均以优异成绩荣获优秀等级。学院开创',541521);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东信息职业技术学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdcit.cn/','山东信息职业技术学院是公办省属普通高等学校，由山东省信息产业厅、山东省教育厅主管。学院具有近30年的计算机、电子信息技术类专业办学历史。2003年11月被国家教育部批准首批试办“国家示范性软件职业技术学院”，2004年4月被国家劳动和社会保障部、信息产业部确立为国家首批“电子信息产业高技能人才培养基地”。学院现有占地面积630亩，校舍建筑面积16.3万平方米，图书馆藏书90万余册，资产总值近3亿元；建设了现代化的实验室、实训室、多媒体教室，以及电子实训生产线，各类实验实训仪器设备达5000多台套',91445);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淄博科技职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.zbvc.edu.cn/','淄博科技职业学院是在淄博卫校的基础上，经山东省人民政府批准、教育部备案、淄博市政府主办的一所全日制普通高等学校，省级文明单位。学院坐落于齐国古都、聊斋故里、足球故乡、陶瓷名城――淄博市。学院坚持“团结勤奋，求实创新”的精神，与时俱进，确立了“以学生为中心，以学生成才为标准；以市场为导向，以质量为立校之本”的办学思路，积极加强软硬件建设，通过了ISO9000质量管理体系认证，不断强化内部管理，全面提高教育教学质量。学院下设基础部、医学技术系、护理系、药学系、经济管理系、中专部及校医院、附属医院各一',32482);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛港湾职业技术学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.qdgw.edu.cn/','青岛港湾职业技术学院坐落在青岛西海岸经济新区，由交通部于1975年建校，现由山东省教育厅主管，青岛港(集团)有限公司主办，是一所具有港航特色的国有公办全日制高等职业院校。学院紧紧抓住国家“一带一路”建设的发展机遇，立足港航，服务社会，遵循“以德立身、以技立业”育人理念，全力打造核心竞争实力，大力加强内涵建设，不断提升人才培养质量。现为国家骨干高职院校、交通职业教育示范院校和山东省高职示范院校。一、深度融合，发挥校港一体优势举办方青岛港高度重视职业教育，在新校区建设、实习实训设备、专业技术人才等方',169099);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江南影视艺术职业学院',' ','江苏',0,8,0,0,0,' ','暂空',' ','http://www.jnys.cn','学院概况●江苏省人民政府批准国家教育部正式备案的全日制普通高等学校，具有颁发国家认可的大学专科学历证书资格●专业培养文化艺术人才的专业性艺术院校，设置演艺人才、传媒人才、航空旅游人才、动漫人才、艺术设计人才五大人才培养体系●地处长江三角洲最发达地区——无锡，人文荟萃经济富饶，具有特别优越的就业优势●学校落户无锡藕塘职教园，与近所高普通等学校汇聚一起，具有特别好的共享教学资源●与百家名企成立文化艺术人才培养联盟，学生就业形势好，历年毕业生就业率超过96%●办学特色鲜明，被国家教育部有关部门命名为“',80439);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江大学城市学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.zucc.edu.cn/','浙江大学城市学院成立于1999年7月，是国家教育部和浙江省人民政府批准设立，由浙江大学、杭州市人民政府合作办学，并与浙江省电信实业集团共同发起创办的全日制本科普通高校。现任院长是浙江大学博士生导师吴健教授，现任党委书记是浙江大学党委委员胡礼祥教授。学院位于杭州市拱墅区，占地1000余亩，校舍面积40万余平方米。校园环境优雅，基本设施先进，办学条件齐备。学院依托浙江大学的综合办学优势，根据现代科学技术发展趋势和地方经济社会发展需求设置专业。设有计算机与计算科学学院、信息与电气工程学院、医学与生命科',2000346);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江大学宁波理工学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.nit.zju.edu.cn','浙江大学宁波理工学院成立于2001年6月，是由宁波市人民政府和浙江大学合作创办，经教育部和浙江省人民政府批准，由宁波市政府投资建设，浙江大学负责办学管理的全日制普通本科院校，是具有独立法人资格的宁波市属事业单位。2013、2014年，学校连续两年位居中国独立学院排行榜第一名。学校位于宁波市高教园区，占地约1200亩，建设总投资8.4亿元，总建筑面积约36万平方米。学校拥有先进齐全的教学、实验配套设施，投入1.9亿元建成各类高标准实验室55个。千兆带宽的专用光纤直通浙江大学本部，共享各类数字资源。',1156425);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('杭州医学院',' ','浙江',0,3,0,0,0,' ','暂空',' ','http://www.zjmc.edu.cn/','浙江医学高等专科学校是经国家教育部批准的全日制普通高等院校。学校由浙江省人民政府领导和管理，省教育厅和省卫生厅共建，培养医药卫生类高等应用性专门人才。学校前身为省立女子产科学校，创建于1925年，后经历浙江省杭州卫生学校、杭州医学院、杭州卫生学校，1975年建立浙江省卫生学校，1999年筹建浙江医学职业技术学院，2004年经教育部批准正式建立浙江医学高等专科学校。学校以科学发展观为指导，主动适应教育强省和卫生强省战略要求，弘扬“求真、博爱”精神，面向基层培养卫生职业技术人才，力争形成一个多专业协',182572);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江纺织服装职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.zjff.net/','浙江纺织服装职业技术学院是一所从事高等职业技术教育的公办全日制普通高校，由宁波市人民政府举办和管理，为浙江省示范性高等职业院校建设单位。学院坐落于宁波市北高教园区，占地800余亩。现有在校生逾万名，教职工600人。学院下设纺织学院、服装学院、艺术与设计学院、商学院、机电与信息工程学院、人文学院、成教学院、国际学院、易斯戴学院等九家二级学院。学院遵循“立足行业，服务全省，校企联动，特色发展”的办学定位，坚持为区域经济和社会发展服务，充分利用和依托浙江纺织服装产业发展优势，构建以纺织、服装、艺术为主',56653);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('杭州科技职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','oldname": "杭州科技职业技术学院','杭州科技职业技术学院于1999年12月经浙江省人民政府批准筹建，2009年2月，浙江省人民政府下文批准杭州科技职业技术学院正式建院。（一）学院性质为公办普通高等职业院校，由杭州市人民政府主办，国家教育部备案。（二）学院定位立足杭州、面向浙江、辐射长三角，培养面向生产、建设、管理、服务第一线的高素质技能型专门人才。以城建类专业为重点，电子信息类、艺术设计传媒类、经济管理类等多学科专业协调发展，将建成以普通高职教育为主，兼顾成人教育，具有“一体两翼”办学特色的高职院校。（三）办学规模2013年全日制',88759);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江长征职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.zjczxy.cn/','浙江长征职业技术学院是一所由民革浙江省委员会与嘉宏控股集团有限公司联合举办，经浙江省人民政府批准、教育部备案的全日制普通高等职业技术学院。学院位于杭州市西湖区小和山高教园区，园区内已建成浙江工业大学、浙江科技学院、浙江教育学院等院校，与午潮山国家森林公园相伴，与西溪国家湿地公园相邻，育人环境优良，教学资源丰富。学院规划占地面积740亩，总建筑面积20余万平方米，建有教学楼、实验实训楼、图书信息楼、体育馆、学生公寓等不同功能的楼宇19座，图书馆藏书95.3万册，建有各类校内实践基地66个。学院积极',120497);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('嘉兴南洋职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.jxnyc.net/','嘉兴南洋职业技术学院(简称南洋学院)是浙江省人民政府正式批准的全日制高等职业技术学院，由上海交通大学教育集团和嘉兴市教育发展投资公司共同举办。南洋学院凭借上海交通大学雄厚的师资力量和百年名校的办学经验，培养能够熟练运用基础理论知识，有较强的外语、计算机能力，掌握专业实用技术的适应时代发展需要的高素质应用型技术人才。南洋学院由上海交通大学具有丰富办学经验的专家、教授直接管理。现任院长潘斌系上海交通大学教授、博士生导师，享受国务院特殊津贴，在交大担任过教学院长。办学以来已有近百位上海交大教授和专业人',61492);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江广厦建设职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.guangshaxy.com/','浙江广厦建设职业技术学院是经浙江省人民政府批准、教育部备案，由广厦控股公司投资近五亿元创办的全日制高职学院。学院占地1000亩，建筑面积32万平方米，藏书达85万册，电子书25万册，教学科研仪器设备资产6000多万元;下设5个二级学院，开设27个专业;在校生12000余名，教职员工800余人;拥有一流的教学、实验实训、运动和生活设施。学院投资方中国广厦控股公司以建筑和房地产为主导产业，是浙江省人民政府重点培育的26家大型企业之一，全国民营企业排名第五，浙江省民营企业排名第一。学院认真贯彻《高等教',68310);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('杭州万向职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.wxpoly.cn/','杭州万向职业技术学院是经浙江省人民政府批准建立，由杭州市人民政府和著名企业万向集团公司合作举办、并获香港理工大学全力支持的全日制高等职业技术院校。学院坐落在素有人间天堂之称的杭州市，毗邻杭州西溪国家湿地公园，交通便捷，环境宜人。校区占地面积280亩，总建筑面积9万余平方米。在规划建筑中的新校舍，计划于2009年落成，将提供7万余平方米新增面积，包括新的图书馆、教学大楼、实训大楼、行政大楼和学生活动中心等。为配合浙江省及国家经济和社会发展之需要，学院设有三个专业群：制造业专业群、服务业专业群、绿色',74026);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙环境保护职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hbcollege.com.cn/','长沙环境保护职业技术学院，创建于1979年，其前身为国家环境保护总局长沙环境保护学校。2002年学校升格为公办全日制普通高校，现由环境保护部和湖南省人民政府共建，是目前国内唯一一所以专门培养高素质技能型环保一线人才为宗旨的高职学院。建校30年来，已有2万多环保学子从这里走向社会，成为全国各地、各行各业的环保栋梁之材，成为基层县、市环保局的骨干力量。多年来，在环境保护部和湖南省委、省人民政府的正确领导和大力支持下，学院始终坚持社会主义办学方向，全面贯彻党的教育方针，围绕教育教学中心，不断深化内部管',112988);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南艺术职业学院',' ','湖南',0,8,0,0,0,' ','暂空',' ','http://www.arthn.com','湖南艺术职业学院(附设中专湖南省艺术学校)坐落在风景秀丽的岳麓山下，是经湖南省人民政府批准，由省文化厅主管的湖南省唯一一所公办艺术类高等院校。学院创办于1951年，已有50多年的办学历史，现设有戏剧、音乐、舞蹈、影视、美术、社文六个专业系和艺术培训部、艺术实践部，设有戏曲表演、音乐表演、舞蹈表演、影视表演、电视节目制作、计算机音乐制作、舞台艺术设计、人物形象设计、环境艺术设计、产品造型设计、广告设计与制作、播音主持、编导、影视动画、导游、酒店管理等二十多个专业。办学层次既有高中毕业起点的二、三年',94314);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南机电职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnjdzy.net/','湖南机电职业技术学院是经湖南省政府批准、教育部备案的公办全日制普通高等院校，是教育部高职高专人才培养工作水平评估优秀的高等院校，是湖南省示范性高职院校，所属湖南机电高级技工学校是经人力资源和社会保障部批准的高级技工学校。学院座落在长沙市万家丽北路，左眺星沙明珠，右瞰世界之窗，占地面积728亩，建筑面积近32万平方米，现有全日制在校学生12000余人，教职工600余名，其中教授、副教授95人，“双师型”教师127人，教育部高职高专教学指导委员会委员2人，国家机械工业联合会专业建设指导委员会委员7人',102861);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.cszyedu.cn/','长沙职业技术学院是长沙市人民政府主办的一所全日制普通高等职业院校，其前身是由谭嗣同夫人李闰等人于1902年创办的湖南省浏阳师范学校。百年名校，人才辈出，胡耀邦、杨勇等同志都曾在我校就读。学院现有雷锋(主)校区、浏阳(东)校区和天心(南)校区三大校区，占地面积600余亩，在校学生7300余人;学院现有教职员工580余人，其中正、副教授83人;拥有博士、硕士研究生学历的教师145人;“双师型”教师276人。学院设置有机械工程系、汽车工程系、建筑与艺术设计系、经济贸易管理系、人文与信息科学系、特殊教育',152966);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('怀化职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.hhvtc.com.cn/index.html','怀化职业技术学院是由原湖南省安江农校和怀化机电工程学校合并组建的一所公办全日制高等院校。2003年4月经湖南省人民政府批准、教育部备案成立。实行省农业厅与怀化市人民政府共建、市政府为主的管理体制。学院是杂交水稻发源地，中国工程院院士袁隆平为名誉院长。学校位于湖南西部的交通枢纽城市——怀化市。现有湘黔、枝柳、渝怀三条铁路，209、320两条国道交汇于此，邻接已经通航的芷江机场、海内外知名的风景名胜区张家界和旅游胜地湘西凤凰。院本部位于城西舞水河畔，依山傍水，交通方便，风景秀丽。学院现占地面积591',66423);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('岳阳职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.yvtc.edu.cn','岳阳职业技术学院是湖南省岳阳市人民政府主办的一所综合性高职院校。学院占地984亩，建筑面积27.24万平方米，固定资产4.96亿元。学院拥有教职员工1115人，其中教授、主任医师34人，副教授、高级工程师221人，双师型教师226人。学院有10系（院）、2部、5所、3实体，同时依托企业建有1个产学研联盟、1个工程技术中心。学院开设机械、电子、商贸物流、临床医学、护理、药学、园林技术和影视动画等高职专业28个，面向全国23个省市招收三年制高职专科学生。目前学院有在校学生13017人，继续教育学生4',76555);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常德职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.cdzy.cn','常德职业技术学院(学院代码：13039)是经湖南省人民政府批准、教育部备案，由原常德卫生学校、常德农业学校、常德机电工程学校于2003年合并组建，是一所全日制国有公办普通高等学校。学院坐落于享有“桃花源里的城市”美誉之称的湖南省常德市。学院办学历史悠久，文化底蕴深厚。原常德农业学校创办于1905年，是湖南省创办最早的农业学校，也是全国创办最早的八所农业学校之一。原常德卫生学校作为湘西北地区唯一的一所医卫学校成立于1951年。原常德机电工程学校成立于1977年。一个多世纪来，为当地经济发展和社会进',132954);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙南方职业学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.nfdx.net/index.htm','巍巍麓山,苍翠如盖,千年书香不绝;滚滚湘江,波光鳞鳞,自古英才辈出。长沙南方职业学院(原南方大学国际代码13041)是经湖南省人民政府批准成立、教育部备案的全日制普通高等院校。学院位于中国历史文化名城——湖南省首府长沙市。麓山之南,湘江左岸。学院占地近千亩,建筑面积30余万平方米.校园依山傍水,花繁树茂,碧草如茵,环境幽雅,景色宜人。雄伟建筑与巍巍群峰辉映，课堂书声与林间鸟语相闻。学院现设有四院六系一部,信息技术学院、管理学院、数控与模具技术学院、继续教育学院、外语系、旅游文化系、汽车技术系、电',155494);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潇湘职业学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.hnxxc.com','潇湘职业学院是经湖南省人民政府批准、国家教育部备案，并于2006年12月通过教育部高职高专院校人才培养工作水平评估，列入国家统招计划的一所全日制普通高等院校。潇湘职业学院创建于1992年5月，2005年2月，学院进行重组，原娄底潇湘职业学院创办人郝钢通过股权出让的方式，引进湖南省优秀民营企业——大汉控股集团合作联办，为学院腾飞搭建了新的发展平台。学院实行董事会领导下的院长负责制，系省属优秀院校和精神文明单位，是一所独具特色、培养高技能专门人才的综合性大学。学院坐落在中国十大宜居城市之一、素有“湘',30888);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南化工职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnhgzy.com','湖南化工职业技术学院是由湖南省人民政府举办、湖南省教育厅主管的国有公办全日制普通高校。创建于1958年，前身为湖南省化学工业学校。1998年与湖南省资江化学工业学校合并，2003年升格为湖南化工职业技术学院，2010年合并湖南化工机械学校(湖南工业高级技工学校)。学院是湖南省示范性(骨干)高职院校，于2007年通过了湖南省党建工作评估，2008年通过了教育部人才培养工作水平优秀评估，2010年通过了湖南省文明高校评估，2013年被评为湖南省大学生就业创业示范校，2013年被评为湖南省教育信息化试',61357);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南城建职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnucc.com','湖南城建职业技术学院由原省建筑学校、原省建材学校、原长沙职工大学（省建筑高级技校）合并而成，由省建工集团主管，迄今已有50多年办学历史，近3万名毕业生和所培训的1万多名项目经理遍及省内外建筑、建材行业，被誉为建设人才的摇篮和百万建筑湘军的“黄埔军校”。现有分别位于湘潭和长沙的高新、新湖、南湖3个校区和1个技校、1个继续教育学院，面向全国近30个省市区招生，近三年新生平均报到率为86.4%，毕业生当年年底平均就业率为91.79%。湖南城建职业技术学院为省建筑职教集团牵头单位、省建设教育协会职校委员',161281);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南石油化工职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnshzy.cn','湖南石油化工职业技术学院是经湖南省人民政府批准、国家教育部备案的一所国有公办全日制普通高等学校。学院坐落在岳阳市长炼工业区。三十多年来，依托中石化长岭炼化公司办学，形成了鲜明的校企融合、工学结合的特色，办学质量受到了省教育厅和用人单位的好评。是国家教育部人才培养工作水平评估优秀高校，湖南省文明高校。专业设置合理学院设有石化工程、自动化工程、机械工程、信息管理四个系和公共课部。具有理工类、经济类等20多个专业的开设资格和能力。现在招生专业均为市场对技能型人才需求非常大的专业。师资力量雄厚学院专任教',64541);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('乐山职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.lszyxy.com/','乐山职业技术学院是2002年经四川省人民政府批准，由原乐山工业学校、乐山卫生学校、乐山财贸学校三所国家级重点中专合并组建成立的含医药卫生、财经旅游、机械电子类的综合性全日制普通高等院校，属教育部国家技能型紧缺人才培养基地院校。是“国家职业技能鉴定所”、“四川省职业技能鉴定智能化考试站”。学院开办三年、五年制普通高职大专和三年制中专学历教育。学院建校以来，坚持以质量求生存，以改革求发展，以适应社会经济发展需求，为社会培养高技能人才为目标，不断深化教育教学改革，狠抓教学管理，努力提高教学质量，坚持走',196736);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('雅安职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.yazjy.com','雅安职业技术学院是经省政府批准、国家教育部备案成立的国办全日制普通高等学校。学院地处历史文化名城、全国优秀旅游城市??雅安市市区，位于川藏运输线和108国道交会处，距省会成都120公里且高速公路相连，距双流国际机场约100公里。雅安是世界茶文化的发祥地、熊猫故乡，是四川西部的交通枢纽。学院具有50多年的办学历史，有较强的办学实力和丰富的办学经验。分设育才路、青年路、建新路校区，占地313亩，校舍建筑面积15万平方米，运动场地6万余平方米，馆藏图书26.5万册。教学及后勤服务设施完善配套，教学资源',345911);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州工业职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://www.gzky.edu.cn/','贵州工业职业技术学院的前身是创建于1958年的贵州省化工学校，2002年3月经省政府批准升格为独立建制的高职学院，名称为“贵州科技工程职业学院”，学院成立以来，在省委、省政府及省直各部门的大力扶持下办学实力不断增强，办学水平不断提高，已从原来的单一学科向多学科并举发展，已涵盖化工、生物制药、机电、电气、计算机、建筑、材料、水利、电力、交通、矿山、电子商务、物流等领域，经省府函[2009]21号文批准，学院于2009年2月1日更名为贵州工业职业技术学院。学院以理工为主，文史兼容的高等职业学院，坐落',200567);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州电力职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://www.csgedu.cn/','贵州电力职业技术学院是全国率先成立的电力高等职业技术学院之一，也是新组建的南方电网（含黔、粤、滇、桂、琼五省区）内唯一的电力类高校，且是贵州省内经批准实施中外联合办学的首家高校。学院被教育部、劳动和社会保障部、国家经贸委评为“全国职业技术教育先进单位”，被贵州省委、省政府授予“省级文明单位”称号。学院坐落在享有全国“避暑之都”美誉的、风景秀丽的国家级风景区贵阳市红枫湖畔。校园依山傍水，绿树成荫，造型各异的现代建筑掩映其中，景色宜人。贵州电力职业技术学院通过实施强强联合，教育资源重组，各类教育资源',148750);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('六盘水职业技术学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.lpszy.cn/','六盘水职业技术学院是2002年6月经贵州省人民政府批准由六盘水市原农业学校、原财经学校、原卫生学校、原农业科学研究所、原林业科学研究所、原农业机械研究所(“三校三所”)合并组建、教育部备案的全日制、公办综合性高等职业技术学院，已故著名社会活动家、全国人大常委会原副委员长费孝通先生为学院题写了校名。学院坐落于中国凉都·六盘水美丽的梅花山脚下德坞湖畔，校园依山近湖，青山环抱，空气清新，四季宜人。校园规划用地534亩，总建筑面积26.36万平方米。图书馆藏书45万册。全日制在校生9000余人。学院教学',46889);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('铜仁职业技术学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.trzy.cn/','铜仁职业技术学院是2002年6月经贵州省人民政府批准，占地面积1160亩。2015年12月16日，国家民族事务委员会和贵州省人民政府签署共建铜仁职业技术学院协议，学院成为国家民委、贵州省人民政府“省部共建”高校。学院目前有全日制在校学生30758人，其中本科生372人，高职生13386人,留学生153人。学生生源来自俄罗斯、老挝、越南、印度尼西亚、印度、喀麦隆、叙利亚、尼泊尔、蒙古、巴基斯坦、卢旺达、索马里、埃塞俄比亚13个国家和全国26个省市自治区。现有教职工802人，有正高职称教师41人，副',120441);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('滁州职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.czc.net.cn','滁州职业技术学院位于安徽省滁州市，坐落在国家4A级风景名胜区---琅琊山下，是滁州市人民政府举办的综合性普通高等职业院校，为安徽省示范性高职院校建设单位。学院成立于2002年7月，占地面积1140亩，建筑面积31万平方米，校园环境优美，设施完善。学院设有机电系、土木系、信息系、经贸系、汽车系、食品与环境系、传媒系、基础部、安徽滁州技师学院等9个教学单位，设置高职专业45个，其中，有28个专业与滁州市的智能家电、先进装备、新型化工、新能源、农副产品深加工、硅基材料等六大支柱产业结构相吻合。学院现有',257024);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('池州职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.czgz.cn','池州职业技术学院是一所由省政府批准成立的公办全日制普通高等院校，是安徽省示范高职院校、安徽省第一批高职高专教学改革试点学校，第二批自主招生试点学校，曾荣获“安徽省职业教育先进单位”等多项荣誉称号。学院坐落在中国第一个生态经济示范区、中国人居环境奖城市、中国优秀旅游城市、国家级园林城市、全国双拥模范城、中国旅游竞争力百强城市、安徽省历史文化名城池州市区，南倚闻名世界的佛教圣地九华山，北临长江。现校园占地面积700余亩，建筑面积25万平方米。其中，教学行政用房10.8万平方米，教学仪器设备总值1.1',50409);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宣城职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','oldname": "宣城职业技术学院','宣城职业技术学院于2002年经安徽省人民政府批准、教育部备案正式成立，是我市唯一一所公办全日制普通高等专科院校。当时由原宣城师范学校、宣州教育学院和宣城广播电视大学分校组建而成，2006年10月市技工学校正式并入。其中宣城师范学校成立于1914年，是一所具有百年历史沿革的老校，肖楚女、恽代英、柔石等都在此战斗工作过，文化底蕴十分深厚，在省内外深具影响力。学院文化气息浓厚，环境优美，是连续四届省级文明单位。学院经过几年的努力，尤其是近两年的快速发展，积累了一定办学条件，现占地面积830亩，有各类在',94468);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽广播影视职业技术学院',' ','安徽',0,8,0,0,0,' ','暂空',' ','http://www.amtc.cn/','安徽广播影视职业技术学院是直属安徽省教育厅管理、接受安徽省新闻出版广电局业务指导的公办高等院校，也是全国最早举办广播影视类高等职业教育的院校之一，并在安徽省高等职业院校人才培养工作评估中荣获“优秀”等级。学院位于合肥市新站区高教基地文忠路与学府路交口，占地面积618.89亩。学院始建于1960年，时称安徽省中级广播学校，1984学校更名为安徽广播电视学校,面向全省招生。2002年7月，升格为安徽广播影视职业技术学院,面向全国招生。学院设有新闻系、影视艺术系、信息工程系、播音主持系、管理系;以及基',64276);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽外国语学院',' ','安徽',1,5,0,0,0,' ','暂空',' ','http://www.aflu.com.cn','安徽外国语学院是经国家教育部批准的全日制本科高校，是全国十三所独立设置的高等外语院校之一，是中部地区唯一一所本科外国语学院。学院秉承“学兼中外，知行合一”的校训，以培养涉外白领为目标，致力于将学生培养成既精通外语，又具有国际视野，熟悉国际经济和贸易规则的复合型、应用型涉外领域专门人才。学院坐落在安徽省合肥市繁华大道西，环境优美，景色秀丽，欧式特色鲜明，外语氛围浓厚。学院坚持“高层次、高起点、有特色、国际化”的建校方针，建设了国际贸易、模拟报关、市场营销和证券投资等先进的实验实训室48个，其中“国',155875);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北交通职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hejtxy.edu.cn  ','河北交通职业技术学院是公办全日制普通高等职业技术院校，隶属于河北省教育厅，学校始建于1949年，历经交通部石家庄公路工程学校、河北石家庄交通学院、河北石家庄交通专科学校、河北交通学校、河北交通职业技术学院等历史时期，举办过本科、专科和高职等层次和类别的教育，是交通部在全国最早创办的六所公路工程院校之一。学院占地面积684.2亩，图书馆藏书64万余册。学院设有8系2部，48个专业，8个校内实训基地88个实训室(中心)，273个校外实训基地，同时设有河北省大学生汽车驾驶培训中心。有2个全国交通系统重',257225);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北化工医药职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hebcpc.cn/template/mainpage01.asp','学校始终与民族命运共浮沉，与时代脉搏同起伏。百年前，中国半殖民地半封建社会程度进一步加深，有识之士努力寻求振兴中华之路，认识到实业救国、教育救国的重要社会功效，在此背景下宣统元年（1909年）清政府于保定设置了“直隶省立补习学堂”，直隶提学使司委任省视学张良弼先生为第一任堂长。1912年，学堂更名为直隶甲种工业学校，是直隶仅有的一所中等工业专门学校，1934年定名为河北省立保定高级工业职业学校。1937年日寇全面侵华，保定沦陷，学校被迫停课并迁址河南省新安县石寺镇、陕西省周至、西安等地艰难办学。',371351);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄信息工程职业学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.sjziei.com/','石家庄信息工程职业学院是经河北省人民政府批准、国家教育部备案、面向全国招生的国办全日制高等职业院校。学院为河北省职业与成人教育先进单位、省级文明单位、河北省高校首家绿色学校、中国物流教学十大创新品牌院校、中国青年发展研究院高职教育研究基地。学院建有石家庄国家动漫产业发展基地创业孵化园、国家软件产业基地、河北省托福考点，与微软（中国）合作建立“微软IT学院”，是首批采用微软标准作为教学标准的院校之一。学院位于石家庄高新技术产业开发区，分南、北两个校区，总占地面积1457亩，现有建筑面积30万平方米',214696);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北对外经贸职业学院',' ','河北',0,5,0,0,0,' ','暂空',' ','http://www.hbvcfl.com.cn/','河北外国语职业学院是目前河北省唯一独立建制的国办外语高等院校，地处举世闻名的北戴河和南戴河两大避暑胜地的交界处，北依戴河，南濒渤海。学院占地528亩，建筑面积20万平方米。2014年学院面向全国招生2900人。学院现有在校生7463名，长、短期留学生200余名;教职工523名，其中正副教授147名;从国外留学回来的教师45人;还聘有60多名兼职教师和20余名外籍教师，另有50多位青年教师在国内外攻读硕士、博士学位。学院设有小学教育系、学前教育系、英语系、国际商务系、西语系、东语系、旅游系、传媒系',266099);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北美术学院',' ','河北',1,8,0,0,0,' ','暂空',' ','http://www.hbafa.com/','学校简介河北美术学院是经教育部批准设立的全日制普通本科美术高校，是我国独立设置的九所美术学院之一,是河北省唯一一所省属专业本科美术高校,也是全国普通高等学校毕业生就业工作先进集体。学校现占地1380亩，位于石家庄市空港工业园区，交通十分便利。学校设有造型艺术学院、城市设计学院、传媒学院、环境艺术学院、动画学院、工业设计学院、服装学院、书法学院、雕塑学院、文创学院、国际教育学院、继续教育中心等12个二级学院（中心），开设绘画、环境设计、动画、雕塑、书法学、服装与服饰设计、视觉传达设计、广播电视编导',388001);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏职业技术学院',' ','宁夏',0,0,0,0,0,' ','暂空',' ','http://www.nxtc.edu.cn/','“国家示范性高等职业院校”宁夏职业技术学院是一所隶属于自治区人民政府领导的普通高等学校。她的主要任务是举办以工科为主，集机械制造、电子信息、自动化、农林牧渔、生物化工、经济管理、教育与艺术等为一体的高等职业教育和中等职业教育，为我区及周边省区的经济建设和社会发展培养高技能型专门人才。学院占地769.8亩，建筑面积116045平方米，固定资产8131.55万元；实训基地2个，实验室41个；教学实验设备3510台件；图书21万册，电子图书37400种，电子期刊折合96万册。学院辖三个校区，校本部位于',71859);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏工商职业技术学院',' ','宁夏',0,6,0,0,0,' ','暂空',' ','http://www.nxgs.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校宁夏工商职业技术学院的前身为宁夏经贸职业技术学院，2002年10月，经自治区人民政府批准，由原宁夏商业学校、宁夏经贸学校、宁夏供销学校合并组建而成。2006年9月，经自治区人民政府批准，教育部备案，正式更为现名，是自治区重点建设的示范性高职学院之一。学院占地1560亩，教学仪器设备总值达943.14万元，拥有各类藏书44.47万册。现有教职工247人，其中教师153人。教师中有教授1人，副教授54人，讲师59人，“双师型”教师54人，国家级考评员42',74908);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏财经职业技术学院',' ','宁夏',0,6,0,0,0,' ','暂空',' ','http://www.nxcy.edu.cn/','“国家示范性高等职业院校”宁夏财经职业技术学院于2002年10月经自治区人民政府批准，2003年7月由原宁夏财经学校和原宁夏银行学校合并组建成立，隶属于自治区教育厅。学院占地122亩，校舍总建筑面积45000平方米，“十一五”期间规划总占地面积472亩。固定资产4818万元，馆藏图书38万册。现有教职工194人，其中专任教师90人，专任教师中教授1人，副教授35人，“双师素质”教师45人。学院面向全国8个省、区招生，全日制在校生3040人。学院设有7个行政处室，9个教学、教辅、后勤机构。开设了会',139900);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏司法警官职业学院',' ','宁夏',0,11,0,0,0,' ','暂空',' ','oldname": "宁夏司法警官职业学院','宁夏司法警官职业学院是经国家司法部和宁夏回族自治区人民政府批准、国家教育部备案的全日制专科院校。学院位于银川市西夏区学院东路高校园区，占地面积346亩，交通便利、环境优美、教学生活设施完善。除开展全日制教育外，学院还挂中国政法大学宁夏函授总站、中华全国律师函授中心宁夏辅导总站、宁夏法官进修学院、宁夏司法干部培训中心4块牌子，并在山东东营设立山东校区。学院是中央政法委等11部委确定的全国政法院校招录培养体制改革试点单位，是宁夏教育厅确定的首批重点建设的自治区示范性高职院校，是宁夏司法行政系统干部教',91611);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阿克苏职业技术学院',' ','新疆',0,0,0,0,0,' ','暂空',' ','http://www.akszy.com','阿克苏职业技术学院是经新疆维吾尔自治区人民政府批准成立的具有高等学历教育招生资格的集理工、农牧、财经、医学、艺术、体育、师范、信息技术为主的一所综合性全日制公办普通高等职业院校。2008年以优秀等次通过教育部高职高专人才培养工作水平评估，2011年被列入自治区第二批示范性高职院校建设计划。2012年以优秀的成绩通过自治区大学生思想政治评估。是经自治区教育厅批准开展高考单独招生改革试点、优秀中职毕业生直升高职试点的院校。学院规划占地面积2000亩，现已完成建设1042亩，建筑面积17万余平方米，建',54423);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('巴音郭楞职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.xjbyxy.cn','巴音郭楞职业技术学院是于2002年2月26日经自治区人民政府批准成立的巴州唯一一所高等学府。学院现有汉、蒙、维、回等七个民族教职工528人，其中专任教师398人，其中本科学历者201人，高级专业技术职务者114人，“双师型”教师43人。在校学生4800余人。学院现有10个系部（经济管理系、生物工程系、机械电气工程系、艺术系、教育科学系、电子信息工程系、旅游经济系、石油化工工程系、矿产资源系和基础课教学部），共开设51个中职专业和25个高职专业。学院经过五年的发展，现占地面积2700亩，资产总值近',62660);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('金肯职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jku.edu.cn','金肯职业技术学院是由全国三八红旗手鲍莉萍女士于1996年8月投资创建，2000年经江苏省人民政府批准、教育部备案的一所省属全日制民办普通专科院校。学院地处南京市江宁区，背靠独具自然风光的大小铜山，面向南京禄口机场和亚洲第一的南京铁路南站，依山傍水的830亩校园，亭台楼阁交相辉映，春涛湖畔湖水荡漾，园林式的校园是莘莘学子们学习生活的理想之所。经过16年的改革发展，学院已经形成了以汽车、建筑、机械类等专业为特色，电子信息，财经，艺术设计类专业协调发展的符合区域产业需求的专业布局。开设29个专业，其中',87953);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常州轻工职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.czili.edu.cn/','常州轻工职业技术学院ChangzhouInstituteofLightIndustrytechnology学院概况常州轻工职业技术学院是一所具有五十余年办学历史，隶属于江苏省教育厅管理的公办全日制普通高等学校；是教育部高职高专人才培养工作水平评估优秀院校、江苏省高职示范园区建设单位。先后荣获“江苏省文明单位”、“江苏省高校毕业生就业工作先进集体”、“省属国家职业技能鉴定所先进单位”、“江苏省高校文明宿舍先进集体”等称号。学院坐落在环境优美、交通便利的常州科教城内，与有着江南水乡风貌的千年古城——',275127);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常州工程职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.czie.net','2005年10月14日，国家主席胡锦涛视察常州科教城时，与我院党委书记吉飞（左二）、院长颜惠庚（左一）亲切握手常州工程职业技术学院是一所省属全日制公办普通高校，全国化工行业职业教育先进单位，江苏省职业教育工作先进单位，江苏省示范性高职园区建设院校，江苏省教育厅高职高专人才培养工作水平评估优秀高校，2009年荣获江苏省高等学校和谐校园与江苏省平安校园等称号，连续多年获得常州市文明单位标兵。具有50多年的办学历史。学院坐落于常州高等职业教育园区中心位置，占地1100余亩，在册学生1.1万余人。学院环',194938);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏农林职业技术学院',' ','江苏',0,2,0,0,0,' ','暂空',' ','http://www.jsafc.edu.cn/','“国家示范性高等职业院校”全国职业教育先进单位全国五四红旗团委江苏省高校思想政治教育工作先进集体江苏省高校毕业生就业工作先进集体江苏省大学生创业教育示范校江苏省安全文明学校学院概况:江苏农林职业技术学院创办于1923年，是一所国有公办全日制普通高校，也是江苏省唯一的农业类国家示范性高职院。学院主校区坐落于南京东郊全国优秀旅游城市句容市城区，占地3312亩，在校生12000多人，分校区与南京江宁大学城毗邻，环境优美。学院先后被列为全国职教会议参观现场、江苏省高职院校实训基地建设参观现场、江苏省第六',158558);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('建东职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.czjdu.com/','建东职业技术学院是由江苏省人民政府批准建立、教育部备案、从事高等教育的民办全日制普通高校。学院坐落于长三角腹地——江苏常州，悠久的历史积淀和得天独厚的地理位置，为学院的发展提供了丰厚的文化底蕴和鲜活的时代气息。学院位于常州市国家级高新技术开发区内，北临沪宁高速公路，毗邻著名文化旅游景点——中华恐龙园。发达的经济基础，悠久的历史积淀，得天独厚的地理位置，为学院的发展提供了可靠的物质保障、丰厚的文化底蕴和现代化的时代气息。校区占地46万平方米(690亩)，总建筑面积17.2万平方米，图书馆藏书30万',44406);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京铁道职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.njrts.edu.cn/','办学历史：南京铁道职业技术学院是江苏省示范性高职院校。学校前身是创建于1941年的南京特别市立第一职业中学，1955年起更名为南京铁路运输学校，2002年升格为高等职业技术学院，2004年学校成建制由原铁道部划转江苏省管理。学校是长三角地区唯一一所轨道交通高职院校，是江苏省教育厅、上海铁路局和浦口区政府三方共建的高职院校，也是轨道交通行业办学历史最长、影响力最大的高职院校之一，被誉为华东地区轨道交通人才培养的“黄埔军校”。建校70年多来，学校坚持与中国铁路同行的办学理念，坚持立德树人、服务行业、',1173041);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('徐州工业职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.xzcit.edu.cn/','学校隶属江苏省教育厅，是全日制公办院校和江苏省示范性高职院校，全日制在校生12000余名。现有材料工程学院、工商管理学院、化学工程学院、机电工程学院、建筑工程学院、信息与电气工程学院等六个二级学院，共有40多个专业，其中，江苏省政府品牌专业1个(高分子材料工程技术)，中央财政支持重点建设专业4个(药品生产技术、物流管理、高分子材料工程技术、精细化工技术)，省级示范建设专业4个(机械制造与自动化、光伏材料制备技术、应用电子技术、物联网应用技术);省级品牌专业2个(橡胶工程技术、精细化工技术);省级',284420);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏信息职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jsit.edu.cn','江苏信息职业技术学院是一所具有五十六年办学历史，现隶属江苏省教育厅管理的普通公办高等学校；是教育部批准的首批35所国家级示范性软件职业技术学院，教育部高职高专人才培养工作水平评估优秀院校；学院先后荣获“江苏省高校毕业生就业工作先进集体”、“江苏省精神文明建设先进单位”、“江苏省安全文明校园”、“中国十强软件学院”等称号。省委、省政府及省教育厅非常关心学院的改革发展，学院现任党委书记夏成满、院长陆国平，均为年富力强、经验丰富的高等教育管理专家。学院地处中国东部经济最发达的长三角地区腹地，位于太湖明',190954);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宿迁学院',' ','江苏',1,0,0,0,0,' ','暂空',' ','oldname": "宿迁学院','宿迁学院创办于2002年，是教育部批准设立的全日制普通本科院校。学校按照“省市共建、八校联建”的体制与模式办学，建校以来，一直与南京师范大学、苏州大学、扬州大学、江苏大学、南京工业大学、江苏师范大学、南京财经大学、南京工程学院等八所省属高校合作举办本科教育，借助联建高校的资源，实现了高起点、高水平办学。学校设有法政学院、文理学院、信息工程学院、建筑工程学院、外国语学院、商学院、机电工程学院、艺术与传媒学院等八个二级学院和体育部，现有36个本科专业(其中，自办专业16个，与各联建高校合作举办专业2',366296);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宿迁职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.sqzyxy.com/index.html','宿迁职业技术学院创建于2000年，是经江苏省人民政府批准、教育部备案的一所全日制普通民办高校，实施专科层次高等职业教育。学院位于宿迁市湖滨新城高教园区风景秀丽的骆马湖边，拥有500亩独立校园，70000平方米校舍，环境优雅，设施先进，师资充足。学院所在地宿迁市地处陇海经济带、沿海经济带、长三角经济带的交叉辐射区，京杭大运河纵贯南北，京沪、宁宿徐、徐宿淮盐高速公路穿境而过，水路干线四通八达，交通十分便利。是著名的水乡、花乡和名酒之乡。人文环境在古朴中彰显时尚元素，于现代中蕴含雄浑气魄。在办学过程中',29384);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京信息职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.njcit.cn/','南京信息职业技术学院是一所具有六十余年办学历史的全日制公办普通高校，隶属于江苏省经济和信息化委员会，现为国家示范性(骨干)高职院校。悠久的办学历史：学院前身是创建于1953年的新中国第一所电子类中专校——南京无线电工业学校，周恩来总理、陈毅、谭震林、习仲勋副总理等党和国家领导人曾到校视察。2002年经国家教育部批准，学校独立升格为南京信息职业技术学院。学院先后为国家培养了五万余名电子信息类专业人才，在信息行业内享有盛誉。先进的教学设施：学院位于美丽的仙林大学城，占地1000余亩，固定资产超过10',193599);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江海职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jhu.cn','江海职业技术学院位于著名历史文化名城扬州古运河畔，1999年经江苏省人民政府批准、教育部备案，具有独立颁发专科文凭资格的民办全日制高职院校。现在全日制在校生8500人。学校荣誉全国先进社会组织、教育部全国高校校园文化建设优秀成果奖、江苏省大学生就业工作先进单位、江苏省平安校园、江苏省安全文明校园、江苏省园林式单位、江苏省高校文明宿舍先进单位。优良的师资队伍有各类教师407人。教师队伍中，有教授、副教授175人，“双师素质”教师179人;有研究生学历和硕士学位的218人。他们当中有的是国家、部、省',55121);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常州机电职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.czmec.cn/','学院概况常州机电职业技术学院坐落于全国首个以高职教育为显著特色的常州科教城内，创办于1963年，隶属江苏省教育厅，现为国家示范(骨干)高职院校建设优秀单位、全国毕业生就业典型经验50强高校。学院占地942亩，建筑面积30多万平方米，教学仪器设备总值近2亿元，资产总额8亿多元。现有全日制在校生近万人，教职工700多人。学院创新校企合作办学体制机制，打造跨界互动、功能多元的合作育人平台。校内建有江南装备制造技术产教园，校外设立8个校企合作工作站，统筹政、行、校、企四方资源，推进校企深度合作，增强办学',74882);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门工学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.xit.edu.cn','华侨大学厦门工学院（以下简称“厦门工学院”）是经国家教育部批准、由华侨大学与福建金帝集团有限公司共同创办的全日制本科层次独立学院。厦门工学院位于福建省厦门市集美文教区，毗邻园博苑，依山傍海，风景绮丽；坐拥大学城，与集美大学、华侨大学厦门校区、厦门理工学院等多所高校比邻而居，学术氛围浓厚。校园占地702亩，规划总建筑面积62.5万平方米，总投资12亿元，规划在校生规模10000人。目前，已完成了36万平方米的建筑面积及教学、生活和实验室等相关配套设施的建设。整个校园精致典雅、古朴厚重，教学和生活设',1172363);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安思源学院',' ','陕西',1,0,0,0,0,' ','暂空',' ','http://zb.xasyu.cn/','西安思源学院是国家教育部批准设立的普通本科高校。学校东依白鹿原，西临浐河灞水，南眺巍巍秦岭，北瞰西安世界园艺博览会园址，环境优美，空气清新，风景宜人。经过近二十年发展，已经成为一所以本科教育为主、高等职业教育为辅，经、管、文、理、工、医等学科齐全的综合性大学。学校现有12个二级学院，70余个本、专科专业，校园占地1，480余亩，建筑面积30余万平方米，在校全日制学生17，000余人。多年来，学校以建设高水平大学为目标，以学科专业建设、师资队伍建设、高效管理平台建设为主线，强化教学过程管理和学生学',1229358);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安高新科技职业学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xhtu.com.cn/','西安高新科技学院创立于1999年，是经国家教育部备案，陕西省人民政府批准，由十八博士创办的具备独立颁发学历证书资格的国家统招民办普通民办高校。学院现有位于西安市大学城的长安校区和太平校区。校园环境优雅，办学条件一流，教学设施先进，基础设施完整，是一所新型的以计算机和信息学科为主的高等学府。下设计算机科学与技术系、软件技术系、信息商务系、信息管理系、工程管理系、基础课部、实验实训中心、北京交通大学西安教学中心。学院已建51个专业实验室，2个校内实训基地，53个校外实习基地，百余家就业诚信合作单位。',142596);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西国际商贸学院',' ','陕西',1,6,0,0,0,' ','暂空',' ','http://www.csiic.com/','陕西国际商贸学院是由著名企业步长集团1997年投资创办，经教育部批准的全日制普通本科高校。曾荣获“陕西省教育系统稳定安全先进集体”、“陕西省诚信先进单位”、“陕西省职业技能鉴定工作先进单位”等荣誉称号，是陕西省就业力和就业率较高的产、学、研一体化的教学型高校。2008年3月学院创始人、步长集团董事长赵步长教授和学院董事长赵超博士双双被选为第十一届全国人民代表大会代表，受到党和国家领导人的亲切接见。学院被评为2009年首届中国民办高校毕业生执行力最强年度样本院校。学院位于咸阳市大学园区，资产5亿多',373301);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安城市建设职业学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xacsjsedu.com','为了适应西安国际化大都市建设和发展的需要，经陕西省人民政府批准，国家教育部备案，将“西安三资职业学院”更名为“西安城市建设职业学院”。学院创建于1993年，是面向全国招生的全日制普通高校。以培养综合素质高、适应城市化建设和管理的高端技能型人才为培养目标，下设建筑工程学院、经济管理学院、交通旅游学院、机电信息学院、传媒艺术学院、成人教育学院、网络教育学院和基础部，开设了40余个专业，现有在校生8000余人。学院占地530亩，建筑面积20万平方米。校园环境优美，设施齐全，功能完善。学院拥有24小时天',186013);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西服装艺术职业学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.sxfu.org','陕西服装艺术学院是陕西省政府批准、教育部备案，纳入国家统一招生计划的普通高等院校。学院下设国际时装设计分院、艺术工程分院、科技经管分院、继续教育分院、人力资源分院和中等专业学校。办学十余年来，学院形成了以服装、艺术类专业领衔，经管、信息类专业比翼的高等教育办学特色，以强势行业背景、特色专业优势、保障就业信誉被评为“中国十大专业特色院校”。陕西服装艺术学院所属的陕西斯卡雷·博德制衣制衣公司与重点大学陕西科技大学强强联合，共同创办独立本科院校——陕西科技大学镐京学院。“高层嫁接”、“整体移植”、成功',147814);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西服装工程学院',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.sxfu.org/','schoolcode": "612005400',89697);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南能源职业技术学院',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.ynny.cn/','云南能源职业技术学院的前身是云南省煤炭工业学校，1978年经当时的云南省革委会批准建校，1980年秋季正式招生，1998年被评定为“省部级重点中专”，2000年经省教育厅批准更名为“云南省工程技术学校”，2002年10月，经省人民政府批准组建成立“云南能源职业技术学院”，2003年秋季开始招收三年制和五年制大专，2007年11月在省教育厅组织的“高职高专人才培养工作水平评估”中被评定为“良好”。学院隶属于云南省教育厅、云南省煤炭工业局、云南煤矿安全监察局和云南省财政厅领导和管理。在近30年的办学',54288);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江阴职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jypc.org/','江阴实力雄厚的产业群孕育了江阴唯一一所公办普通高校——江阴职业技术学院。学院占地512亩，建筑面积20余万平方米，各类教学科研仪器设备总值达3000多万元。设有机电工程系、电子信息工程系、化学纺织工程系、计算机科学系、艺术设计系、管理系、外语系、基础部、体育部等7系2部，另有成人教育学院。围绕地方产业结构开设40多个专业，其中机电一体化、电脑艺术设计、计算机网络技术、电气自动化、服装设计等5个专业为省级特色专业。目前，在校生近8000人，教职工519人，其中专任教师318人，中高级职称教师占专任',49054);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西建设职业技术学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.gxjsxy.cn','★2015年全国就业五十强★国家现代学徒制试点院校其他办学荣誉●全国大中专学生志愿者暑期“三下乡”社会实践活动先进单位●自治区示范性高职院校●自治区文明单位●自治区高等学校安全文明单位●自治区学生资助工作先进单位●广西就业先进工作单位●自治区高校毕业生就业工作先进集体重点实验实训基地●国家建设行业实施技能型紧缺人才示范培养培训基地●国家高技能人才培养示范基地●中央财政支持的职业教育建筑技术专业性实训基地●中央财政支持的职业教育计算机应用与软件技术实训基地●中央财政支持的职业教育工程造价实训基地●',158291);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏建设职业技术学院',' ','宁夏',0,1,0,0,0,' ','暂空',' ','http://www.nxjy.edu.cn/','宁夏建设职业技术学院是2002年9月经自治区人民政府批准、教育部备案的专科层次的公办高等职业学院，占地342.83亩，总建筑面积61317.8平方米。教学仪器设备总值924.62万元。学院师资力量雄厚，现有教职工230人，教师206人，其中专任教师中高级职称47人，中级职称43人，“双师素质教师”37人，另有兼职教师61人。学院面向内蒙、甘肃、新疆、青海、陕西、山西、山东、河南、河北、黑龙江、吉林、辽宁、湖北、江西、重庆、湖南、海南、四川、宁夏等19个省、市、自治区招生，现有全日制在校生近400',76916);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.cvit.com.cn/index.shtml','随着吉林省、长春市经济社会的迅猛发展，为了整合资源，加快职业教育发展，更好地为区域经济社会发展提供高端技能型专门人才，2000年,长春市委、市政府组建了一所以高等职业教育为主、社会培训与认证教育协调发展的“城市高职”——长春职业技术学院。学院占地面积578亩，建筑面积25.9万平方米，教职工900人，全日制在校生10105（算函授10204）人，年社会培训超万人次。根据吉林省和长春市的汽车、农产品加工、轨道交通装备、光电信息、旅游、物流等支柱、优势产业的需求，学院设立了汽车、工程、轨道交通、食品',200766);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('临汾职业技术学院',' ','山西',0,0,0,0,0,' ','暂空',' ','http://www.lfvtc.cn','临汾职业技术学院是国家教育部备案、山西省人民政府批准设立、面向全国招生的一所独立设置的公办全日制综合性高等专科院校。学院座落在有着五千年灿烂文明的历史文化古城—临汾。临汾“南通秦蜀，北达幽并，东邻雷霍，西控河汾”，历来为兵家必争的战略要冲。如今，同蒲铁路贯穿南北，高速公路四通八达，交通十分便利，信息尤其发达。临汾，古称平阳。位于山西省中南部，地处黄河中游，这里物华天宝，人杰地灵，文化博大精深，历史源远流长。是中华民族和黄河文明的重要发祥地。据《尧典》记载：原始公社时代的帝尧即建都平阳。数千年间，',94754);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京师范大学珠海分校',' ','广东',0,0,0,0,0,' ','暂空',' ','http://zsb.bnuz.edu.cn/','学校简介北京师范大学珠海分校(原北京师范大学珠海教育园区)是教育部批准设立、由北京师范大学和珠海市人民政府合作举办、进行本科层次教育的全日制普通高等学校。建设珠海分校是北京师范大学建设世界一流大学宏伟目标的重要组成部分。在教育部有关批复精神的指导下，珠海分校坚决执行北京师范大学提出的“资源国有、学校举办、市场运作、全新模式”的建校方针，坚决贯彻“以质量求生存，以创新求发展，以贡献求支持，以共赢求合作”的发展思路，在学校建设与发展中大胆进行教学模式、管理方式、运行机制、国际合作以及产学研一体化方面',2657670);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉大学珞珈学院',' ','湖北',0,0,0,0,0,' ','暂空',' ','http://luojia-whu.cn/','◆中国工程院院士、原武汉大学校长刘经南教授为学院首席顾问◆各系主任均由武汉大学教授、博士生导师担任◆学院与武汉科技大学联合开展研究生培养◆湖北省转型发展试点本科高校◆学院在全国软件大赛、电子设计竞赛等竞赛中的获奖等级和获奖人数位列全国同类院校之首◆大学英语四六级通过率高于部分二本院校，学生考研率、就业率均居湖北同类高校前五◆2013年全国独立学院本科毕业生质量排名中位列湖北省独立学院第二位◆21世纪中国教育改革创新示范院校◆2015年位列中国综合类独立学院第15位，跻身中国高水平独立学院；新生质',782715);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连理工大学城市学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.dl-city.com/','大连理工大学城市学院经国家教育部批准成立于2003年3月，是东北首家按新机制、新模式设立的独立学院，是大连理工大学和中国城市建设控股集团有限公司（国企）下属的全资子公司大连松源企业集团合作创办的新型普通本科院校。学院地处中国十大最具经济活力的滨海城市——大连，校园占地761亩，建筑面积25万平方米。学院各类办学设施齐全、先进，为学生创造了一流的学习和生活条件。学院根植于大连理工大学，由大连理工大学全面负责学院的教学和管理。学院充分发挥大连理工大学的综合办学优势，根据社会发展的实际需求设置专业，目',1855584);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳工业大学工程学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://gcxy.sut.edu.cn/','沈阳工业大学工程学院（院校代码13199）成立于2004年1月，是经国家教育部批准备案的独立学院。学校面向全国招生，是承办工科、经济、管理、法学等学科门类的全日制普通本科高等学校。学校设教学办、学生办等五个办公室及化工、机械、自动化、计算机、经管、法学等六个系，开设十六个本科专业，包括化学工程与工艺、应用化学、高分子材料与工程、环境工程、过程装备与控制工程、机械设计制造及其自动化、材料成型及控制工程、自动化、电气工程及其自动化、测控技术与仪器、计算机科学与技术、会计学、市场营销、国际经济与贸易、',103354);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳航空航天大学北方科技学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://jwc-nstc.sau.edu.cn/jwweb/','沈阳航空工业学院北方科技学院（以下简称沈航北方科技学院）创建于1999年，是经国家教育部批准，由沈阳航空工业学院按新的机制和模式举办的实施普通高等学校全日制本科教育的独立学院，学制四年。沈航北方科技学院位于文化气息浓郁的沈阳市皇姑区（将迁往沈阳市沈北新区沈北路81号），毗邻风景优美的北陵公园，环境优雅、空气清新、交通便利。学院校园现占地面积13.33万平方米，教学行政用房面积4.86万平方米，教学仪器设备1586.64万元，图书10.35万册。沈航北方科技学院以服务航空工业和振兴东北老工业基地为',251631);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳工学院',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.syyyy.com.cn','沈阳工学院是经国家教育部批准的一所民办普通高等学校。学校始创于1999年，2004年迁至辽宁省抚顺经济开发区(沈抚新城)。学校北依浑河，西临沈阳世博园，交通便利、环境优雅、景色宜人。学校始终坚持以就业为导向，积极探索人才培养规律，现已发展成为工科实力雄厚，管理学、经济学、艺术学、农学等学科相互支撑的多科性大学。学校设有机械与运载学院、经济与管理学院、信息与控制学院、艺术与传媒学院、能源与水利学院、生命工程学院、创新创业学院、基础教育学院等8个学院;本科专业54个，在校本科生15324人;高职专业',651374);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连工业大学艺术与信息工程学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://www.caie.org/','大连工业大学艺术与信息工程学院是经中华人民共和国教育部批准，于2002年6月正式成立的普通高等学校独立学院。是大连工业大学按照新体制、新机制举办的新型普通本科院校。学院坐落于大连开发区大学城，毗邻空气清新的大黑山风景区、三面环海的金石滩风景区，风光秀丽，气候宜人。现代教学与生活设施体系完善，教学楼、图书馆、体育馆等楼群端宁恢弘，井然有致。教室、学生公寓分别配置了多媒体、宽带网等设施，为师生提供便利、舒适的环境。学院目前在校生人数4300余人，教师总数306人，其中专任教师219人。具有高级以上职',576456);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国医科大学临床医药学院',' ','辽宁',0,3,0,0,0,' ','暂空',' ','oldname": "中国医科大学临床医药学院','中国医科大学临床医药学院是经辽宁省教育厅于2003年1月批准成立、教育部予以确认，由中国医科大学按新机制、新模式举办的新型独立学院。临床医药学院办学层次为全日制普通本科，面向全国招生。以培养医学基础扎实、专业技能精良、外语能力较强、综合素质全面为办学特色。目前在校生人数900人，设有专业：临床医学、医学影像学、医学检验、预防医学、口腔医学、法医学、护理学、药学（临床药学）、生物医学工程、生物科学与生物技术、信息管理与信息系统等11个专业。规划在校生人数为4000人。临床医药学院的教学和管理工作由',240375);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连医科大学中山学院',' ','辽宁',0,3,0,0,0,' ','暂空',' ','http://www.dmuzs.edu.cn/','大连医科大学中山学院始建于1999年，经教育部2004年3月确认为新机制、新模式的独立学院。学院由大连医科大学与大连金真源企业集团公司合作举办。学院地处滨城大连市高新园区爱贤街28号（邮编116023），座落于园区动漫产业带内，校园依山傍海，环境宜人。学院占地面积442.55亩，教学行政用房面积7.5万平方米，教学仪器设备值1725.57万元，图书15.94万册，教职工495人。学院依托大连医科大学雄厚的师资队伍和丰富的教育资源，采用新机制组建了强有力的专兼职教师队伍，在396人的专任教师中，教',437202);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁医学院医疗学院',' ','辽宁',0,3,0,0,0,' ','暂空',' ','http://ylxy.lnmu.edu.cn/','辽宁医学院医疗学院地处渤海之滨，坐落于风景秀丽的辽宁西部中心城市、历史文化名城——锦州，原名锦州医学院医疗学院，是2004年3月经教育部批准，按照新机制、新模式举办的独立学院。2006年8月,伴随锦州医学院更名为辽宁医学院而更名为辽宁医学院医疗学院。是由辽宁医学院举办的、具有独立法人资格的本科层次全日制普通高等学校。学院具有独立的校园，现占地面积10.14万平方米，建筑面积5.13万平方米，新投入使用的7000平方米的现代化图书馆，为学生们提供了丰富的图书信息。此外，还建有设施齐全、配备一流的教',319623);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁师范大学海华学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://zszx.lshhxy.cn/','欢迎莘莘学子报考辽宁师范大学海华学院!辽宁师范大学海华学院是2000年7月经辽宁省教育厅批准成立、2003年12月教育部首批确认、由辽宁师范大学按新机制和新模式举办的全日制普通本科学院(独立学院)，全日制在校生6500余人。学院坐落在沈阳新行政中心浑南新区，北依浑河，南邻沈阳新南站，校区景色宜人，交通便利。校园占地面积540亩，图书馆藏书30多万册。学院图书馆、阅览室、多媒体教室、计算机实验室、语音实验室、模拟法庭、会计专业实验室、画室、摄影棚等教学基础设施一流，草坪足球场、篮球场、羽毛球场等运',235478);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨石油学院',' ','黑龙江',1,1,0,0,0,' ','暂空',' ','http://www.hip.edu.cn','哈尔滨石油学院创建于2003年，始称大庆石油学院华瑞学院，2012年经教育部批准独立设置并更名为哈尔滨石油学院，是一所具有学士学位授予权的民办本科普通高校。学校设有石油工程学院、化学工程学院、机械工程学院、土木工程学院、信息工程学院、自动控制工程学院、经济管理学院、外语学院、艺术学院、国际教育学院等10个二级学院和3个公共教研部。开设28个本科专业，涵盖工、经、文、管、艺五个学科门类，在校本科生及留学生9000人，初步形成了以工科为主、多学科协调发展的培养本科层次应用技术型人才的办学格局。学校占',188362);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北大学知行学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hudazx.cn','学院概况办学性质：湖北大学知行学院创办于2000年，是经国家教育部批准、由湖北大学举办的具有学士学位授予权的公办全日制普通高等学校，是湖北省首批应用转型发展试点高校。全日制在校学生近10000人。办学定位：学院坚持以人才培养为核心，以提高办学质量和科技创新能力为主线，深入推进产教融合、校企合作和开放办学，全面实施综合改革，将学院建设成为规模结构合理，优势特色鲜明，创新创业能力突出，对区域经济社会发展有重大贡献的应用技术型高校。经过十多年的建设发展，学院在人才培养、科学研究、社会服务和文化传承创新',381958);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉科技大学城市学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.city.wust.edu.cn/','武汉科技大学城市学院于2002年经湖北省人民政府批准成立，并经国家教育部核准备案。属本科层次的独立学院，具有独立法人资格。学院坐落于风景优美的武汉市东湖之滨，紧邻武汉火车站。现有占地面积860余亩，资产总值近10亿元，建筑面积近50万平方米，教学及生活设施完备、先进。其中教学仪器设备总值8000余万元，图书馆馆藏各类图书140余万册（含电子图书61余万册）。学院还建有千兆校园宽带网络、广播电台、英语教学专用无线广播台等。学院目前设有本科专业31个，其中“工商管理”被省教育厅评为省级重点培育学科，',1541280);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三峡大学科技学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://210.42.35.198','三峡大学科技学院是经国家教育部备案、湖北省教育厅批准，由三峡大学与宜昌教育实业开发总公司合作举办的具有独立事业法人地位的高校独立学院。其前身是2000年9月成立的三峡大学宜昌分校;2002年8月，经省教育厅批准更名为三峡大学科技学院;2004年2月，经国家教育部批准重新确认为实施高等学历教育的高校独立学院。学院于2010年通过了湖北省独立学院本科专业教学水平评估，2012年通过了湖北省独立学院学士学位授予权单位及专业审核评估，具有自主颁发学士学位资格。学院位于三峡工程所在地、世界水电之都、中国旅',966618);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江汉大学文理学院',' ','湖北',0,0,0,0,0,' ','暂空',' ','http://www.jdwlxy.cn:8080/\n','江汉大学文理学院是经国家教育部批准设立的实施全日制高等学历教育的普通高等学校（独立学院）。学院位于国家级经济开发区——武汉经济技术开发区，毗邻江汉大学本部，坐依风景如画的三角湖畔。校园环境幽雅，教学设施齐备，能容纳万余名学子学习生活。学院面向全国招生。学生毕业后颁发由国家统一印制署名江汉大学文理学院的毕业证书，符合学士学位授予条件者，江汉大学文理学院授予国家认可的学士学位证书。学院设有24个本科专业、17个专科专业，40余个专业模块（方向），覆盖文、理、工、农、经、管、法七大学科门类。现有各类在',361643);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北工业大学工程技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://gcxy.hbut.edu.cn','●全国先进独立学院●全国教育改革创新示范(院)校●中国最具影响力“独立学院”●2015中国十佳独立学院●2015年度中国魅力高校●教育部—中兴通讯ICT产教融合创新基地●国家职业技能鉴定所●湖北省大学生创业示范基地●湖北省大学生实习实训基地●湖北省平安校园●湖北省高校“先进基层党组织”学院国标代码：13238湖北招生代码：4627办学性质湖北工业大学工程技术学院始建于2002年，是由湖北工业大学举办、经国家教育部批准独立设置的公办全日制普通高等学校，具有学士学位授予权，在二本批次招生。办学理念学',773078);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉工程大学邮电与信息工程学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.witpt.edu.cn','武汉工程大学邮电与信息工程学院是武汉工程大学按照新机制和新模式举办的以实施全日制普通本科教育为主的独立学院，是国家教育部首批确认具有办学资格的独立学院，是一所以培养通信、电子、材料、化工、经济和管理等学科领域高级应用型专门人才为主导的普通高等院校。学院注册地址为湖北省武汉市虎泉街366号，武汉东湖新技术开发区“武汉·中国光谷”的中心，设有武昌校区和邮科院校区。武昌校区紧连武汉工程大学老校区，与武汉工程大学共享办学资源，邮科院校区由武汉邮电科学研究院参与联合办学，位于风景秀丽的东湖之滨，南望山脚下',582245);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉纺织大学外经贸学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://cibe.wtu.edu.cn/','武汉纺织大学外经贸学院，是2003年经国家教育部批准设立的一所工、文、经、管、艺相结合、相互渗透的本科独立学院。学院座落于景色秀丽的武汉江夏阳光大道，占地面积500亩，建筑总面积为11.2万㎡，目前在校生规模6682人，其中本科生4486人，专科生2196人。学院周围大专院校、科研院所林立，具有浓厚的人文学术氛围，是学习、研究的理想之地。2008年被中国教育研究院评为“全国最佳独立学院”。学院外经贸学院坚持育人为本理念。创建了SMT三位一体育人新模式，制定了“立志修身，学以致用”的院训，要求全体',612492);
