insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长江大学工程技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://gcxy.yangtzeu.edu.cn/','长江大学工程技术学院是由长江大学举办的一所全日制普通本、专科层次学历教育的独立学院，是湖北省唯一一所以石油主干学科为特色的独立学院。学院位于长江中游的历史文化名城——荆州，校园环境秀丽，被湖北省政府命名为园林式学校。在第十一届中国国际教育品牌创新峰会上，长江大学工程技术学院荣获2012中国最具品牌价值独立学院奖。学院现有在校学生近9000人，设有石油资源系、机械系、化学工程系、城市建设系、信息系、管理系、外语系、基础教学部、体育教学部、思想政治理论课部、实验实训中心、高等教育研究所、石油新技术研',480804);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长江大学文理学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://wlxy.yangtzeu.edu.cn/','长江大学文理学院是2004年4月，经湖北省教育厅同意、国家教育部批准成立，由长江大学举办的独立学院。长江大学是中央与湖北省共建，省属规模最大、学科最全的综合性大学，是湖北省重点建设骨干高校，拥有理、工、农、医、文、管、经、法、教、史10大学科门类，拥有3个博士点，46个硕士点。长江大学雄厚的师资力量、先进的实验设备、齐全的学科门类以及综合性大学深厚的人文科学底蕴都为长江大学文理学院的办学提供了最优的教育资源。长江大学文理学院位于长江大学古朴、典雅的城中校区(原荆州师范学院)，交通便利，设施齐备，',586005);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北汽车工业学院科技学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://kjxy.huat.edu.cn/\n','湖北汽车工业学院科技学院（国标代码：13248）创办于2003年，是一所经国家教育部批准成立的本科层次独立学院。面向省内外招生，学生来自于全国25个省份，目前在校全日制本专科学生共计四千五百余名。学院的办学理念是坚持以人为本，践行教学、管理育人；依托大工程背景，发展比较优势；笃志、创新、务实，培养应用型、创新型人才。依托校本部的智力、人才资源优势，切实加强教师队伍和管理队伍建设，继承校本部“以汽车产业链”构建本科教育学科框架的思想，充分利用好东风汽车公司大工程背景这一得天独厚的优势，并尽可能地创',258669);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北医药学院药护学院',' ','湖北',0,3,0,0,0,' ','暂空',' ','http://yhgj.hbmu.edu.cn/','郧阳医学院药护学院是经国家教育部批准、湖北省唯一独立设置的医学本科独立学院(教发函[2004]12号)。学院地处中国汽车城---湖北省十堰市中心，占地面积近300亩，与校本部连为一体，校园环境优美，学术氛围浓厚，徜徉其中，既感医学之神圣，又沐人文之灵气，是求学深造的理想之地。药护学院依托具有40年历史的郧阳医学院办学，教学条件完备，设施先进。郧阳医学院是湖北省重点建设的省属医学院校之一，学校（含附属医院）现有教职员工5000余人（其中高级职称810人，硕士、博士学位600余人），固定资产总值超过',370655);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北民族学院科技学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbmykjxy.cn/\n','湖北民族学院科技学院是国家教育部和湖北省人民政府批准设立、由拥有七十年办学历史的湖北民族学院举办的独立普通高等学院。学校办在湖北民族学院主校区内，其师资、仪器、图书、校舍等资源共享，有着深厚的校园文化底蕴和良好的育人环境。学校设有文学与传媒学院、财经政法学院、生物科学与技术学院、信息工程学院、理学院、化学与环境工程学院、外国语学院、艺术学院、医学院、体育学院、民族与社会学学院、预科部等十二个院(部)。开设了26个本科专业和14个专科专业以及民族本科预科班。学院面向全国招生，学生毕业，成绩合格，由',201063);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北经济学院法商学院',' ','湖北',0,6,0,0,0,' ','暂空',' ','http://fsxy.hbue.edu.cn','湖北经济学院法商学院是经国家教育部批准设立的一所全日制普通本科独立学院。学院成立于2003年3月,座落在风景秀丽的武汉市江夏藏龙岛科技园区汤逊湖畔，占地面积500余亩，校园环境优美，交通便利，拥有良好的办学条件，教学和生活设施配套齐全，是读书治学的理想场所。学院依托与湖北经济学院毗邻的区位优势，根据社会经济发展需求和湖北经济学院优势资源设置专业，逐步形成了多学科协调发展的格局。学院现设有经济学系、国际经济与贸易系、金融系、财政与公共管理系、工商管理系、会计系、信息管理系、旅游与酒店管理系、物流与',960928);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉体育学院体育科技学院',' ','湖北',0,7,0,0,0,' ','暂空',' ','http://www.wipe.edu.cn/','武汉体育学院体育科技学院创办于2003年，是经湖北省教育厅批准、国家教育部审核的一所全日制本科体育类独立学院。学校座落在武汉市江夏区藏龙岛科技园区环岛路1号，风景秀丽，交通便利，是理想的学习生活园地。校园占地面积近600亩，建筑面积10余万平方米，建有各类室内室外运动场馆及教学楼、实验楼，图书馆，目前馆藏图书达20万余册。学校的举办方为武汉体育学院。武汉体育学院系博士授权单位，优质的办学质量和良好的社会声誉既是学校办学的有力保障，也是办学的优势之所在。作为独立办学实体，我校的办学体制和运行机制采',322403);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北师范学院文理学院',' ','湖北',0,4,0,0,0,' ','暂空',' ','http://www.wlxy.hbnu.edu.cn\n','湖北师范学院文理学院是经省教育厅、省发展与改革委员会批准，由湖北师范学院按教育部要求独立设置的二级学院，2003年12月通过教育部专家组评估验收。教育部代码为13256，湖北省招生代号为4657。学院位于湖北省黄石市团城山开发区桂林南路，东邻磁湖，西依柯尔山，南视古矿冶，北接人民广场，自然景色得天独厚，人文景观优雅灵秀。校园现有面积200余亩（规划用地800余亩），建筑面积8万多平方米，有价值千万元的仪器设备，有百兆出口的校园网。图书、电子文献资料丰富，教学生活设施一应俱全。湖北师范学院文理学院',337581);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北文理学院理工学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbasstu.net/\n','襄樊学院理工学院是由襄樊学院举办的、并经国家教育部批准成立的公办独立学院，是一所面向全国招生的多科性本科院校。学院位于湖北省第二大城市、全国十大魅力城市之一的襄樊市。这里是中原文化与荆楚文化交汇处，也是三国文化的发祥地。厚重的历史文化底蕴，蓬勃的现代化发展态势，是莘莘学子求学成才的理想之地。襄樊学院理工学院一校两区,分为城市校区和隆中校区,学校环境优雅，恬静宜人，拥有完善的教学、实验、办公、生活等基础设施，办学条件优良。我院依托母体学校的雄厚师资力量和优质办学资源，为确保教学质量增强学生专业素养',244607);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北艺术职业学院',' ','湖北',0,8,0,0,0,' ','暂空',' ','http://www.artschool.com.cn','一、学院历史沿革湖北艺术职业学院创建于1958年，原为湖北省戏曲学校，1966年因“文革”停课，1970年停办；1977年11月恢复重建；1988年，湖北省戏曲学校更名为湖北省艺术学校；1992年，与湖北省文化艺术干部学校合并；1993年，被湖北省人民政府和文化部确认为“省部级重点中专”；2000年，与湖北省电影职工中等专业学校合并，组建成新的“湖北省艺术学校”；2001年，被国家教育部确认为国家级重点中等职业学校；1999年，湖北省艺术学校与华中师范大学合作办学，成立华中师范大学艺术职业学院；',61325);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉交通职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.whjzy.net','武汉交通职业学院是一所经湖北省人民政府批准、直属湖北省教育厅领导和管理的全日制普通高等学校。学院具有50多年的办学历史，是省级最佳文明单位。学院现有在校生13000人，学院座落于武汉市洪山区黄家湖大学城，占地面积1000亩，校舍面积32万平方米，图书馆藏书70万册。学院学科齐全，门类众多，共七大类，十个教学机构，38个专业。下设船舶与轮机工程系、汽车工程系、交通与物流管理系、电子与计算机系、机电工程系、人文社会科学系、财经系、继续教育学院、国际学院、实践教学中心。学院毕业生就业覆盖面广，就业率达',434696);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('咸宁职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.xnvtc.edu.cn','咸宁职业技术学院是2003年4月经湖北省人民政府批准，由原咸宁教育学院、咸宁应用科技学校、湖北省咸宁财贸工商管理学校和咸宁财税会计学校合并而成。办学历史始于成立咸宁专区的1965年。原咸宁教育学院的前身华中农学院咸宁分院创办于1976年，1984年被省人民政府确定为“湖北省中职师资培养培训基地”，1999年被省人民政府确定为“湖北省首批高等职业教育试点院校”。在40多年的办学过程中，学院培养了近10万名各级各类专门人才，其中高职高专毕业生23800余名。2003年，学院被教育部和信息产业部等六部',54711);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长江工程职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.cj-edu.com.cn/','长江工程职业技术学院（长江工院）是一所经湖北省人民政府批准组建，国家教育部备案的公办普通高等学校。学院的前身是始建于1959年，隶属于水利部长江水利委员会的长江工程大学，首任校长是被毛泽东主席称为“长江王”的林一山同志（时任长江流域规划办公室主任）。至今，学院具有近50年的办学历史。2004年6月，根据国务院（国办发［2000］11号）文件精神，学院由水利部划转湖北省管理，是湖北省人民政府直属的25所高校之一。长江工院分为三个校区，主校区位于九省通衢的湖北省武汉市江夏经济开发区大学城，占地540',151483);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江工业大学之江学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://zs.zjc.zjut.edu.cn','浙江工业大学之江学院是1999年经浙江省人民政府批准、浙江工业大学举办、2004年教育部确认的独立学院，是中国独立学院协作会常务理事单位、全国高等学校教学研究会独立学院专门委员会副主任委员单位。学院办学的各个环节与浙江工业大学紧密结合，是浙江工业大学的重要组成部分。学生毕业后，符合毕业条件的，颁发“浙江工业大学之江学院”毕业文凭；符合学士学位授予条件的，授予“浙江工业大学之江学院”学士学位。1、工大办学，特色鲜明。之江学院遵循“依托母体、相对独立、办出特色”的办学方针，以浙江工业大学雄厚的师资与',1950989);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江师范大学行知学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://xz.zjnu.edu.cn/','浙江师范大学行知学院是1999年8月经省政府批准，并于2004年11月被教育部确认的一所综合性全日制本科独立学院。2010年被授予“全国先进独立学院”称号。2013年，在全国独立学院综合排名第9，全国高水平独立学院排名第7，被评为全国校园环境满意度50强高校。学院位于浙江师范大学西校区，坐落于国家历史文化名城金华市，地处浙江中部，北依沪杭，南联闽粤，交通便捷。校园环境清幽，绿树成荫，芳草似锦，与国家级风景名胜双龙洞交相辉映、相得益彰，是求知成才的理想之地。学院现有商学、法学、文学、艺体、理学、工',613377);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波大学科学技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','oldname": "宁波大学科学技术学院','宁波大学科学技术学院位于美丽的沿海开放港口、副省级城市—宁波市，于1999年经浙江省人民政府批准建立，是全省第一批进行国有民办高校体制改革试点的学校。在2004年首批接受教育部办学条件和教学工作专项检查中，获最高等级评价。学院以“怀虚涵育，修己立人”为院训，以“创造差异、错位发展、彰显特色、成就品牌”为办学宗旨,搭建“平台+模块”课程结构体系，实行“多维课堂联动”人才培养模式，培养知识结构强、动手能力强、适应能力强、综合素质高的“三强一高”“双栖型”人才。学院根据现代科学技术发展趋势和地方经济社',558567);
