insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江理工大学科技与艺术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://kyzstu.benke.chaoxing.com/','浙江理工大学科技与艺术学院是一所以纺织、服装、艺术设计为特色，理、工、文、经、管、法等多学科协调发展，以高素质应用型人才培养为目标，由浙江理工大学参与举办的一所普通全日制本科独立学院。学院于2000年开始面向浙江省招收全日制本科生，2004年，圆满通过教育部对独立学院的办学条件和教学工作的专项检查和确认，并在“中国校友会2006中国独立学院排行榜”上排名第42名。2006年秋季开始，学院在杭州市文一西路960号（西城校区）办学。西城校区占地面积563亩，现有总建筑面积近18万平方米。多媒体教室、',202923);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江海洋大学东海科学技术学院',' ','浙江',0,2,0,0,0,' ','暂空',' ','http://dk.zjou.edu.cn/','浙江海洋学院东海科学技术学院成立于2000年1月，是经浙江省人民政府批准，以浙江海洋学院为依托创建的一所按民办机制运作的全日制普通本科院校。2004年11月正式被国家教育部确认。学院位于“千岛新城”的舟山市，原浙江海洋学院西校区，占地面积328亩，总建筑面积12万多平方米。校园环境幽优雅，基本设施先进，办学条件齐备，是读书治学的理想园地。东海科技学院根据我省经济社会发展的需要，以市场为导向设置专业。目前有工学、理学、管理学、文学、医（药）学等五个学科门类，设有机械设计制造及其自动化、船舶与海洋工',357704);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江中医药大学滨江学院',' ','浙江',0,3,0,0,0,' ','暂空',' ','http://bjxy.zjtcm.net/','院校代码：116浙江中医药大学滨江学院是浙江中医药大学利用现有的优质教育资源、按照新机制新模式举办的本科层次的独立学院。2000年1月，经浙江省人民政府浙政发[2000]4号文批复，学院正式创立，并于同年秋季起开始招生，列入浙江省高等学校第三批招生计划。2004年11月，学院经重新报批，获得国家教育部高函[2004]21号批文确认。学校现有校园占地面积210亩，教学行政用房建筑面积78215平方米。立足于浙江省经济建设和社会发展的需要，以市场需求为导向，学院设置了医、管、理、工、文等五个学科门类',288832);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('杭州师范大学钱江学院',' ','浙江',0,4,0,0,0,' ','暂空',' ','http://qjzs.hznu.edu.cn/','杭州师范大学钱江学院成立于1999年，是杭州师范大学所属的经教育部批准设立的一所综合性全日制本科独立学院。学院位于杭州市西湖区文一路222号，她遥望西湖、书香浓郁、环境优美、交通便利。学院现设有汉语言文学、英语、经济学、电子商务、会计学、旅游管理、市场营销、法学、社会工作、社会体育、舞蹈学、音乐表演、播音与主持艺术、广播电视编导、艺术设计、动画、数学与应用数学、信息与计算机科学、应用化学、环境科学、应用心理学、高分子材料与工程、机械设计制造及其自动化、电子信息工程、计算机科学与技术、护理学、工业',322402);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖州师范学院求真学院',' ','浙江',0,4,0,0,0,' ','暂空',' ','http://qzxy.hutc.zj.cn/','湖州师范学院求真学院是1999年8月经浙江省人民政府批准建立，2004年11月经教育部确认的一所全日制普通本科独立学院。2011年，基本完成了省教育厅要求的“剥离规范”工作，并确定湖州市城市建设投资集团公司为合作伙伴，促进了学院的规范发展。学院现有独立校园644亩，校舍面积近22万平方米，仪器设备总值4790万元；设有基础部和经管、社会发展、教育、体育、文学、外语、艺术、理学、信息与工程、生命科学、医学等11个系；设有国际经济与贸易、社会体育、汉语言文学、新闻学等35个本科专业；现有教职工481',265516);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('绍兴文理学院元培学院',' ','浙江',0,4,0,0,0,' ','暂空',' ','oldname": "绍兴文理学院元培学院','绍兴文理学院元培学院是一所经国家教育部确认的全日制本科独立学院。学院以我国近代教育家、思想家蔡元培先生的名字命名，秉承先生“思想自由、兼容并包”的精神，积极探索具有独立学院特色的教学管理之路。学院以“崇尚科学，追求真知”为理念；以培养适应地方经济社会发展，具有“一种意识，两种能力”--较强的创新意识、实践能力和社会适应能力的高级应用型人才为目标；努力打造规模结构合理、质量效益优良、综合实力较强、具有鲜明地方特色的一流本科独立学院。学院自创办以来，一批批元培人磨砺砥志、艰苦创业、蕴积涵育、革故鼎新',212858);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('温州大学瓯江学院',' ','浙江',0,4,0,0,0,' ','暂空',' ','http://www.ojc.zj.cn/','温州大学瓯江学院，2000年浙江省人民政府批准设立，2004年国家教育部批准为独立学院。下设三个分院和一个公共教学部，涵盖经济学、法学、教育学、文学、艺术学、理学、工学和管理学等八大学科门类，43个本科专业，全日制在校生7400余人。学院是国家级创业教育人才培养模式创新实验区、浙江省人才培养模式创新实验区、浙江省创业教育研究基地、温州市文化创意产业园、温州市小企业创业基地、温州市信息安全中心、温州市国家大学科技园分园。学院实行董事会领导的院长负责制，温州大学校长任董事长。校园坐落于温州市高教园区',315041);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江工商大学杭州商学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://hsy.zjgsu.edu.cn','浙江工商大学杭州商学院（原浙江工商大学国际经贸学院）是首批经浙江省人民政府批准的独立学院，成立于1999年，2004年11月根据《教育部关于对浙江省普通高等学校举办的独立学院予以确认的通知》被教育部正式确认为浙江工商大学杭州商学院。学院自1999年开始招收本科生，现有全日制普通本科在校学生5600余人。设有工商管理、市场营销、人力资源管理、经济学、国际经济与贸易、金融学、统计学、会计学、财务管理、旅游管理、资源环境与城乡规划管理、法学、电子信息工程、通信工程、信息管理与信息系统、电子商务、计算机',462424);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('嘉兴学院南湖学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://nhxy.zjxu.edu.cn/','嘉兴学院南湖学院是经浙江省人民政府批准、国家教育部确认举办的独立学院。学校校址位于闻名遐迩的革命圣地南湖之畔——浙江省嘉兴市。嘉兴毗邻上海、杭州、苏州，经济繁荣、交通发达、人文荟萃，素有“鱼米之乡、丝绸之府、文化之邦”的美称。学院现有独立校园200余亩，校园环境优雅。现有在校全日制本科学生8300余人，专任教师392人，其中具有高级职称者100余人；另有实验教师30余人、外聘教师218人。现有30个本科专业，涵盖经济学、法学、文学、理学、工学、医学、管理学等7大学科门类，并设置相应系部。学院拥有',385486);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国计量大学现代科技学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://xdkj.cjlu.edu.cn/','中国计量学院现代科技学院于1999年7月经浙江省教育厅同意、原国家质量技术监督局批准设立，是以新的办学机制和办学模式试办的独立学院，实行董事会领导下的院长负责制，按教育成本收费，按民办机制运作。2004年11月28日，经教育部正式批准为独立学院。学院位于下沙高教园区，毗邻本部，与中国计量学院仅一路之隔。学院设有机电工程系、计测工程系、信息工程系、管理系、人文与法学系等5个系和1个基础部。拥有工、管、法、文、理、经六大学科门类23个本科专业，在校生6800余人。学院按照“依托母体，办出特色，以人为',818173);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨现代公共关系职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.hljpr0451.com/','哈尔滨现代公共关系职业学院是经黑龙江省人民政府批准，国家教育部备案的普通高等学校。也是全国公共关系行业内唯一的一所普通高校。学院系中国公共关系协会、中国国际公共关系协会人才培训基地。国家劳动与社会保障部公关员指定培训单位。学院坐落于素有东方小巴黎之称的北国冰城哈尔滨，校区占地面积240亩，建筑面积6万多平方米，专职教师134人，副高职以上教师占教师总人数的30%以上。学院办学特色鲜明，教学仪器设备先进，在教育教学改革、师资队伍建设、管理模式、毕业生就业安置等方面大胆创新，成绩显著。真正实现了学生',19704);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江工程学院昆仑旅游学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.kllyxy.com/','黑龙江工程学院昆仑旅游学院,是经黑龙江省人民政府批准、教育部备案、国家计划内统一招生的独立本科院校（教育部代码13304）。学院以“就业就是硬道理，求学就业零距离”为办学理念，培养复合型、应用型人才；以瑞士“洛桑”模式为特色，名师办大学、开拓创新为举措，办特色院校。雄厚的师资力量：“专家治校、教授办学”。目前学院拥有一支由高级旅游管理专业人才组成的教师队伍。在孙忠家教授（从事中国古代史研究、硕士生导师）、邹尊兴教授（从事中国古典文学研究、硕士生导师）、韩凤来教授（从事社会主义经济理论研究、硕士生',42721);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东胜利职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdslvc.com/web/','山东胜利职业学院成立于2003年6月，其前身是1973年建校的胜利石油学校。学院隶属胜利石油管理局，实行山东省教育厅与胜利石油管理局双重领导、以胜利石油管理局为主的管理体制。学院占地744.8亩，建筑面积20.7万平方米，固定资产1.77亿元，图书馆藏书73.9万册。学院现设有油气勘查与信息工程系、石油工程系、储运与建筑工程系、机电工程系、电气与自动控制工程系、管理工程系等6个教学系和建设工程分院、交通工程分院、医疗卫生分院3所分院，以及11个机关职能部门和13个科级单位。其中，三所分院均为独立',66132);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东经贸职业学院',' ','山东',0,6,0,0,0,' ','暂空',' ','http://www.sdecu.com/','公办全日制高校：山东经贸职业学院是山东省人民政府批准、国家教育部备案的具有高等学历教育招生资格的省属公办全日制普通高等学校。2014年，被确定为全国供销合作社示范性高等职业院校。2016年，被确定为山东省首批技能型人才培养特色名校。优越的地理环境：学院位于世界风筝都潍坊市区，校园环境优美，绿树成荫，绿化面积达7万平方米，是省级“花园式单位”。一路之隔，就是风景优美的鸢都湖公园和国家4A级旅游景区白浪绿洲湿地公园，毗邻火车站、汽车站，交通便利。先进的办学思想：学院秉承“质量立校，特色兴校，人才强校',126904);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东工业职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdivc.net.cn/','山东工业职业学院是经山东省人民政府批准、国家教育部备案的一所国办全日制普通高等学校，隶属于山东钢铁集团有限公司。学院的前身是创建于1959年的张店有色金属工业学校，后先后更名为山东省有色金属学校、山东省冶金工业学校、山东省工业学校;2002年山东冶金职工大学从济南迁入淄博。2003年5月，在国家级重点中专山东省工业学校和山东冶金职工大学的基础上升格组建为山东工业职业学院。学院现为山东省文明单位、山东省名校工程首批立项建设单位。学院坐落于淄博市高新技术开发区，东邻齐国故都，西接“大染坊”故址，南望',138080);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东化工职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.qledu.net/','山东化工职业学院是经山东省人民政府批准、国家教育部备案、中国石化集团齐鲁石化公司组建的省属公办全日制综合性普通高等学校，受山东省教育厅领导。学院位于世界足球发源地齐国故都山东淄博临淄，交通便利，文化底蕴丰厚。齐国故都昭示了“春秋五霸之首、战国七雄之冠”的辉煌，稷下学宫开创了“百家争鸣”的先河，田齐王陵见证了“东方金字塔”的壮观。学院环境优美，是齐鲁石化公司文明单位。学院始建于1978年，经过30多年的建设和发展，积累了丰富的办学经验，办学实力明显增强，已为公司和社会培养了近3万名各类优秀人才，近',47367);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛黄海学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.huanghaicollege.com/','青岛黄海职业学院建于1996年，坐落于风景如画、气候宜人的青岛西海岸，是经山东省人民政府批准、教育部备案、面向全国招生的全日制普通高校。学院占地1200余亩，建筑面积30万平方米。学院拥有中外教授、高级工程师和年轻骨干教师1400余人，全日制在校生近3万余人。办学十余以来，已向社会输送了5万余名毕业生。经过十余年的发展，学院教育教学设施不断完善，功能齐全的教室、图书馆、阅览室、计算机中心、学术交流中心等均按照高标准配备，宿舍楼、餐厅、超市、自助银行、医务室等生活服务设施一应俱全。学院师资雄厚，有',474760);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛求实职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.qdqs.com/','青岛求实职业技术学院创建于1992年，是经山东省人民政府批准、国家教育部备案的普通高等院校，是一所高起点、多层次、多学科、综合性高等院校。学院坐落在经济发达的国际化海滨城市——青岛，毗邻胶州湾，风景秀丽、环境优美。学院总占地1200余亩，下设信息工程学院、商学院、外国语学院、基础教育学院、机电工程学院、建筑工程学院、汽车工程学院、航空服务学院、旅游学院、艺术学院、城市管理学院、青岛学院(试点本科)、影视学院、体育学院、报关学院、动漫学院、酒店管理学院、海洋学院、飞行学院等24个学院。开设了空中乘',84814);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('济南职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.jnvc.cn/','济南职业学院是2004年由山东省人民政府批准成立的从事高等职业教育的全日制普通高等学校。是山东省示范高职院校，国家确定建设的国家级骨干高职院校。学院被评为山东省德育工作优秀高校，省级文明校园，全省高校思想政治教育工作先进单位和省级文明单位，在全省高职高专人才培养工作水平评估中获得优秀等次。2010年，学院被省教育厅确定为山东省示范性高职院校，济南是中国重要的工业城市之一，在汽车、机械、钢铁、化工、纺织、轻工、食品、建材等产业方面极具优势，机械制造、机动车制造、信息电子、化工医药等行业在全国具有举',311918);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东协和学院',' ','山东',1,3,0,0,0,' ','暂空',' ','http://www.sdxiehe.com/home/','山东协和学院是国家教育部批准的普通本科高校，也是山东财政支持的本科“特色名校”建设单位。创建于1993年，坐落于山东省省会——“泉城”济南。校园占地1480亩，建筑面积41.7万平方米。学校设机电工程学院、建筑工程学院、计算机学院、经济管理学院、护理学院、医学院、外国语学院、教育科学学院、继续教育学院和基础部(思政部)等10个二级院(部)。开设普通本科专业17个，普通专科专业39个，形成了工学、管理学、医学、文学、教育学协调发展的学科专业结构。一、地理环境优越，拥有得天独厚的人文教育资源山东，古',393465);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏大学新华学院',' ','宁夏',0,0,0,0,0,' ','暂空',' ','http://xinhua.nxu.edu.cn','宁夏大学新华学院成立于2002年3月，是国家教育部首批确认的独立学院，由宁夏大学按照新体制、新模式举办的一所全日制本科普通高等学校。2007年12月，宁夏回族自治区教育厅与宁夏大学共建新华学院。学院座落于西部塞上历史文化名城银川市，毗邻宁夏大学。学院占地面积约500亩，教学、行政用房建筑面积近10万平方米，建有综合体育场两处，文体活动中心一个，教学用计算机教室、多媒体教室、语音室等20多个，图书阅览中心两个。学院依托宁夏大学雄厚的办学资源，根据地方经济和社会发展需求，现设有文学、工学、理学、管理',238571);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南大学滇池学院',' ','云南',0,0,0,0,0,' ','暂空',' ','http://www.ynudcc.cn/','【品牌】云南大学滇池学院是由云南省唯一的全国重点大学及“211工程”大学云南大学申办并经教育部批准的独立学院。自2001年创办以来，凭借云南大学先进的办学理念、雄厚的综合实力和学校灵活的办学机制，已经形成经、法、教育、文、理、工、管、艺术多学科协调发展，以培养高素质应用型人才为主的普通本科高等学校。学校现有10个二级分院、2个公共教学部，44个本科专业，全日制在校学生18000余人，分别来自全国27个省（市、区），是云南省规模最大的独立学院。建校十余年来，为社会培养了25000余名优秀毕业生。建',970123);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南大学旅游文化学院',' ','云南',0,0,0,0,0,' ','暂空',' ','http://www.lywhxy.com/','云南大学旅游文化学院是云南大学在不断发展过程中，于2002年在拥有世界文化遗产、世界自然遗产、世界记忆遗产三项桂冠的丽江市创办的一所全日制新型本科学院，2004年被国家教育部首批确立为独立学院。经过短短的六年时间，旅游文化学院已经发展成为占地800亩，建筑面积超过20万平方米，可容纳10000名学生生活和住宿，综合实力侪身中国西部前列的独立学院。一流环境和谐氛围学院所在地丽江市位于云南省西北部，金沙江中游。丽江历史上曾是滇西北政治经济文化中心,是汉唐时代通往西藏和印度、尼泊尔等地的“丝绸之路”和',333553);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明理工大学津桥学院',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.oxbridge.edu.cn/2013/index.html','昆明理工大学津桥学院成立于2001年，是由昆明理工大学申办、云南省国有大型企业云南省城市建设投资集团有限公司投资，经云南省教育厅审核、国家教育部批准的全日制本科（国有）独立学院。学院坐落于四季如春、彩云之南的昆明市，这里冬无严寒、夏无酷暑、湖光山色、四季宜人的气候特别适合学生学习与生活。学院地处昆明高新技术产业开发区海源北路。学院以“环境生态化、景观园林化、校园信息化”为设计思想，书院型的欧美建筑风格营造出幽雅的学习生活氛围。学院拥有藏书丰富的图书馆、设施齐全的学生活动中心、电教中心、网络中心、',362243);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南师范大学商学院',' ','云南',0,0,0,0,0,' ','暂空',' ','http://www.ynnubs.com ','云南师范大学商学院是经教育部审批，由云南师范大学利用社会投资按新的机制和新的模式举办的独立学院，属普通高等教育本科层次。六年来，商学院在各级领导和主管部门的关心、支持下，坚持“全新机制、相对独立、办出特色、创建品牌”的发展思路，充分发挥独立学院体制新、机制活的优势，整合优质资源，创新培养模式，办学规模逐年扩大，办学质量稳步提升，赢得了学生、家长及社会各界的广泛好评。截止2006年底，在校生达9000余人，已成为云南省规模最大的独立学院，综合办学实力居全国300多所独立学院前列。2006年被中国教',1082217);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南师范大学文理学院',' ','云南',0,4,0,0,0,' ','暂空',' ','http://www.ysdwl.cn/','云南师范大学文理学院地处祖国西南美丽的春城——昆明，是教育部批准的本科独立学院。这里是“一带一路”南线门户，是“2015魅力中国-全国最具吸引力的十大城市”之一。全年平均气温22.3度，空气质量优秀，是学子求学的理想之地。作为全国优秀独立学院之一，传承“西南联大”传统，现代高水平大学是学校追求的目标。文理学院以其高水准的教研水平、对学生的个性关怀与全力支持而闻名遐迩。我们坚信，云南师范大学文理学院的任何一门课程，都会给您带来充实且高质量的学习体验，我们更会致力于增加每一位学生的生命价值与意义，他',596738);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南艺术学院文华学院',' ','云南',0,8,0,0,0,' ','暂空',' ','http://whxy.ynart.edu.cn/','云南艺术学院是全国七所综合性本科艺术院校之一，是硕士研究生授予单位。云南艺术学院文华学院是云南艺术学院与社会力量合作，利用非国家财政性经费举办的实施本科学历教育的高等学校。文华学院于2002年1月24日经云南省教育厅批准成立，2004年1月18日经国家教育部教发函〔2004〕11号文件正式确认为独立学院。文华学院坐落在四季如春、风景秀丽的春城昆明，位于昆明市西郊（距院本部约4公里），有独立的校园和基本办学设施，现占地面积200亩，建筑面积6万平方米，教学楼、办公楼、实验室、图书馆、运动场、学生公',126063);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽电气工程职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.aepu.com.cn','“国家示范性高等职业院校建设计划”骨干高职院校安徽电气工程职业技术学院是经安徽省人民政府批准，国家教育部备案的省属公办全日制普通高等学校。学院隶属安徽省电力公司，业务上接受安徽省教育厅指导。是一所以工科为主，工、管、财学科相结合，特色鲜明的电力高等职业院校。学院座落在省会合肥市，南校区位于包河大道，北校区位于黄山路高校密集区，占地面积240余亩，校舍建筑面积10万平方米。拥有良好的教学设施和完善的生活体育设施。校园景观及环境优美，具有文明和谐、积极向上的良好育人氛围。悠久的办学历史学院是由安徽电',69441);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽冶金科技职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://zhao.ahyky.com','安徽冶金科技职业学院是经安徽省人民政府批准、教育部备案、独立设置的一所全日制普通高校，由马钢集团公司投资兴办。马钢是全国特大型钢铁联合企业，拥有世界上最先进的冷热轧薄板、彩涂板、大H型钢生产线和亚洲最大的车轮轮箍专业生产厂，是我国最大的国际上市公司之一，也是我国精品建材基地和具有较强国际竞争力的现代化大型企业集团。其雄厚的经济实力和科技实力，不仅可为学院提供优秀的教师和良好的教学、科研条件，而且可为学生提供良好的实习、实训基地。学院坐落在长三角地区风景秀丽的全国文明城市——马鞍山市。学院占地30',38327);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽城市管理职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.cmoc.cn/','安徽城市管理职业学院是经省政府批准设置、教育部备案的公办全日制普通高等学校，原为1981年成立的安徽省直机关业余大学，2003年转制成全日制普通高等职业学院，行政隶属中共安徽省直机关工作委员会，业务受安徽省教育厅领导。学院位于合肥市新站区高教园区淮海大道1号。学院现设有城市建设系、现代服务系、城市设计系、财务金融系、工商管理系、国际商务系、信息工程系和公共教学部。高等职业教育专业学制三年，主要包括建筑智能化工程技术、建设工程监理、工程造价、国际邮轮乘务管理、老年服务与管理、物业管理、会计、财务管',172126);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽机电职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahcme.edu.cn/','安徽机电职业技术学院是首批国家骨干高等职业院校立项建设单位，是安徽省教育厅直属的一所以工科为主的公办高等职业院校，为全国机械行业骨干职业院校、全国职业教育先进单位、国家技能型紧缺人才培养基地、首批省级示范性高职院校立项建设单位和省级中职骨干教师培训基地。学院立足安徽、面向全国，培养现代制造业高素质技能型专门人才。学院秉承以“修德、练技、立业、报国”为校训的优良传统，坚持“校企合作、工学结合”的人才培养模式，逐步形成了“校企一体，产学互动，能力为本”的人才培养特色。学院始建于1935年的安徽芜湖私',184599);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽工商职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','oldname": "安徽工商职业学院','安徽工商职业学院是一所具有55年办学历史的公办全日制高等院校，隶属于安徽省教育厅。学院座落于安徽省会合肥，建筑面积19万平方米，校内实训基地建筑面积2.2万平方米，图书藏量64万册，电子图书3.5TB，在校生10000余人。2009年11月在安徽省教育厅人才培养工作评估中取得了优秀等次；2010年，省级示范高职院校获准立项；2013年安徽省教育系统首届文明单位立项建设；2011年、2013年连续被评为第九届、第十届安徽省文明单位。学院是安徽省职业与成人教育学会会长单位、安徽省教育会计学会会长单位',119571);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽中澳科技职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.acac.cn/','安徽中澳科技职业学院（AnhuiZhong-AoInstituteofTechnology）是隶属于安徽省科学技术厅的公办高等学校。1998年，安徽省常务副省长张平率政府代表团访问澳大利亚，与西澳州政府在珀斯（Perth）签订了科技与教育合作协议，由安徽省科学技术厅负责实施该项目。经双方协商，安徽省科技培训中心与西澳州中央政府理工学院（TAFE）联合成立安徽中澳科技继续教育学院，引入澳大利亚职业教育模式，开展科技教育培训。2000年作为安徽大学的专科教学点开始普通高等教育招生。2003年6月，经',62072);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阜阳科技职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.fky.net.cn/','阜阳科技职业学院是第九届全国人大代表、全国劳动模范、全国优秀乡镇企业家张贺林同志筹资创办，2003年经安徽省人民政府批准、教育部备案的全日制普通高等学院。学院坐落在安徽省阜阳市清河西路99号，二环路与三环路之间，东靠市政府、西临颍州西湖，面对阜阳师范学院，交通方便，环境幽雅，是培养人才的好地方。学院占地面积500亩，各种用房10万平方米，附有小学、中学、中专、医院等机构，各类在校生近万人。学院机构健全，班子和谐，制度完善，教学规范，管理有序，收费合理，服务热情，校园安全稳定。阜阳科技职业学院师资',31209);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('亳州职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.bzvtc.com/','亳州职业技术学院坐落在中华药都——安徽亳州，是一所有近30年办学历史的普通高等职业院校。学院占地560亩，总建筑面积23.2万平方米，在校生7000余人，教师382人，其中具有副高以上职称81人。学院设有药学院、医护系、管理学系、电子与电气工程系、信息工程系、建筑工程系、基础教学部和成人教育部8个二级教学单位，共开设42个涵盖药学类、护理类、电气信息类、工商管理类、土建类等大类专业。其中，中药学、连锁经营管理、药品生产技术、药品质量与安全为中央财政支持建设专业;中药学、连锁经营管理、药品生产技术',108225);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽国防科技职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahgf.com.cn/','安徽国防科技职业学院始建于1974年，前身是安徽省大江机械工业学校，1997年更名为安徽省国防科技工业学校，2003年6月经安徽省人民政府批准、教育部备案，独立升格为公办全日制普通高等职业院校,隶属于安徽省国防科学技术工业办公室。学院是安徽省人才培养工作优秀院校，是全省24所拥有自主招生权的院校之一，建有中央职业教育数控技术实训基地、国防科技工业职业教育实训基地、安徽省国防科技工业高技能人才培养培训基地。学院坐落于国家级园林城市和合肥经济圈骨干城市——安徽省六安市，与六安市人民政府隔路相望，紧邻',153808);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安庆职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.aqvtc.cn/','安庆职业技术学院坐落在国家园林城市、国家历史文化名城、国家森林城市、中国优秀旅游城市——安徽省安庆市，地处国家级安庆经济技术开发区，与安庆市政务新区、商务新区、文化体育中心、秦潭湖生态公园相毗邻，区位优越，环境优美。校园总体规划由上海世博会中国馆设计者何镜堂院士主持设计，校名由国学大师、著名书法家启功先生题写。我院创办于2003年6月,2009年以优秀等次通过教育部人才培养工作评估，2010年立项建设安徽省示范性高职院校，2014年12月以优秀等次通过安徽省示范性高职院校项目验收暨人才培养工作个',92208);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽艺术职业学院',' ','安徽',0,8,0,0,0,' ','暂空',' ','http://www.artah.cn','安徽艺术职业学院前身为安徽艺术学校，创办于1956年，1958年升格为安徽艺术专科学校，1959年皖南大学艺术系并入后成立安徽艺术学院，1963年为贯彻“八字方针”，安徽艺术学院恢复为安徽艺术学校。学校1988年开办安徽大学艺术专科班，1996年与安徽大学联合开办安徽大学艺术学院，并于1998年通过本科论证开始招收本科生，2003年6月经安徽省人民政府批准成立安徽艺术职业学院。安徽艺术职业学院是事业单位、副厅级建制，行政上隶属安徽省文化厅领导和管理，业务上接受安徽省教育厅指导，是安徽省唯一一所国',49494);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('烟台工程职业技术学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.ytetc.edu.cn/','烟台工程职业技术学院是由烟台市人民政府主办的国办全日制普通高等专科院校。学院坐落在美丽富饶的沿海开放城市——山东烟台，前身始建于1957年，1990年成立烟台市高级技工学校，是全国首批试办的两所高级技工学校之一;2002年成立烟台市技术学院;2005年烟台市轻工技校和烟台市纺织中专整体并入;2006年成立烟台工程职业技术学院;2012年，加挂烟台市技师学院校牌。学院是“国家级高技能人才培训基地(2012年)”、“国家高技能人才培训基地”、“山东省技师培训基地”、“山东省技师工作站”、“山东省首批',125210);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东凯文科技职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdkevin.cn/','山东凯文科技职业学院是经山东省人民政府批准、教育部备案的以信息和工程类专业为特色的全日制普通高等学院。学院前身是山东工业大学报山东省教委批准成立的山东工大科技专修学院。2000年7月，原山东大学、山东医科大学、山东工业大学合并组建新山东大学后，学院更名为山东山大科技专修学院。2005年，学院被山东省人民政府批准为独立设置的全日制普通高校,具有独立颁发学历文凭资格，纳入国家统一招生计划，面向全国招生。学院位于济南市东部大学城，占地660多亩，在校生近万人。目前设有机械学院、电子信息学院、计算机学院',70700);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('烟台大学文经学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://wenjing.ytu.edu.cn','烟台大学文经学院成立于2003年，为全日制本科层次普通综合类高校，是国家教育部首批确认的独立学院。目前在校生一万二千余人。2005年1月，学院以优异成绩通过教育部独立学院办学条件和教学工作专项检查。2006年4月，在教育部对《普通高等学校学生管理规定》(21号令)落实情况的检查中，得到专家的一致好评。2012年6月，通过山东省独立学院综合检查、山东省学士学位授予考察评审等，专家组对学院建设发展给予了肯定和好评。学院位于烟台市莱山区，东临黄海，西依青山，环境优雅，风景秀丽，气候宜人，是育人、治学的',1538380);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('聊城大学东昌学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.lcudc.cn/','一、学院简介聊城大学东昌学院是由聊城大学申请举办、聊城市政府及山东泉林集团投资建设、国家教育部批准设立的独立学院。学院位于山东省聊城市经济技术开发区，建校于2002年，占地500余亩，总资产5.2亿元，在校生7000余人。学院目前设有中文、经济管理、外国语、教育、数学与信息工程、影视传媒、化学生物、机电工程、音乐表演、美术设计、基础部等11个系(部)，31个本科专业和16个专科专业，是一所以工科为主，涵盖文、理、工、经、管、艺等学科门类，以应用专业为主的教学应用型综合性本科院校。学院依托聊城大学',320688);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛理工大学琴岛学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.qdc.edu.cn','院校代码：13378青岛理工大学琴岛学院是由青岛理工大学按新机制、新模式举办的一所全日制普通本科高校。学院设施齐全，风景优美，占地1060亩，校舍建筑面积52万平方米，图书资料111万余册，电子图书30万余种，教学仪器设备总值1.6亿元，拥有全国领先的校园云计算平台，实现校园无线网络全覆盖。学院先后获得全国先进独立学院、山东省大学生创业教育示范院校、青岛市思想政治工作先进高校、青岛市花园式单位等荣誉称号。学院现有全日制本专科在校生16000多人，拥有本科专业32个、专科专业21个，设土木工程系、',2462524);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东师范大学历山学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.lishanu.edu.cn','山东师范大学历山学院是2005年6月经国家教育部和山东省人民政府批准设立的普通全日制本科高校。学校坐落于青州市云驼风景区，总占地1000余亩，建筑面积25万余平方米。校园依山傍水，空气清新，环境优美，左邻潍坊护理职业学院，右邻潍坊工程职业学院，形成了国内独特的山城大学城氛围。学校图书馆藏书50余万册，千兆专线光缆共享山东师范大学的优质教育资源。校内运动设施齐全，建有400米塑胶标准体育场，塑胶篮球、排球、羽毛球、网球、乒乓球场，正在建设中的体育馆、游泳馆将在2017年7月投入使用。学校建有三星级',270952);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国石油大学胜利学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.pusc.cn/','中国石油大学胜利学院是教育部批准设立的全日制本科普通高等学校，是由教育部直属高校中国石油大学(华东)、国家特大型企业胜利石油管理局和山东省东营市人民政府在优化整合原胜利油田师范专科学校优良办学资源的基础上，按新机制、新模式合作举办的规范化独立学院。学院于2003年3月成立，时称石油大学胜利学院，2005年3月更名为中国石油大学胜利学院。2013年11月，中国石油大学(华东)、胜利石油管理局、东营市人民政府三方签署共建协议，中国石油大学胜利学院掀开新的发展篇章。学院位于黄河三角洲中心城市，有着石油',1059904);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东外国语职业学院',' ','山东',0,5,0,0,0,' ','暂空',' ','http://www.sdflc.com/','山东外国语职业学院是2005年经山东省人民政府批准、教育部备案的一所民办高等职业院校。学院位于享有“中国魅力城市”之称的山东省日照市，座落在人文、生态环境优雅的日照大学科技园，依山傍海，风景秀丽。学院占地面积886亩，规划总建筑面积约31万平方米。学院现有外语系、经济管理系、计算机系、人文艺术系4个系，构建以应用外语类专业为主体、以经济管理类和计算机信息类专业为两翼、以人文艺术类和涉外服务类专业为支撑的专业建设格局。现开设应用英语、应用韩语、应用日语、应用俄语、应用德语、应用西班牙语、商务日语、',118551);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潍坊工商职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.wfgsxy.com/','潍坊工商职业学院是山东省政府批准、教育部备案的全日制普通高校。占地1250亩,建筑面积16.6万平方米，在校生10000多人，教职工600余人，其中教授、副教授、高级专业技术人员128余人，“双师型”教师120多人，具有硕士学位和在职攻读硕士学位的教师160余人、博士1人，长期外聘专家学者30多人。学院以专科层次的职业技术教育为主，拥有大专教育、远程教育、教师教育、社会培训等多种办学模式，致力打造多种模式的、全新规模的职教品牌超市；组织机构健全，有继续教育学院、社区学院、12个处室、10个系部，',78144);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('德州职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.dzvtc.cn/','德州职业技术学院是经山东省人民政府批准成立、教育部备案，由德州市人民政府兴办的全市唯一国办普通高职院校。学院实行一个学校两块牌子，即“德州职业技术学院”和“德州市技师学院”，以从事高等职业教育为主，同时兼顾预备技师、高级技工和技师的培养、培训任务，是融高等职业教育和高级技工教育为一体，培养高素养、高技能“双高”人才的新型高职院校。学院占地面积1120亩，规划建筑面积40万平方米。行政办公楼、教学楼、图书信息楼、学生公寓25万平方米建筑已投入使用。学院注重实习实训设施建设，大力培养学生的实际操作技',99328);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('枣庄科技职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.zzkjxy.com/','枣庄科技职业学院是经山东省人民政府批准、教育部备案的全日制普通高等职业院校。学院地处的滕州市，东枕沂蒙山，西掬微山湖，南俯苏北，北依泰山，素有“三国五邑之地，文化昌明之邦”的美誉，因科圣墨子和百工之首鲁班故里、国家级红荷湿地公园而驰名中外。学院占地1000亩，教职工786人，其中教授、副教授、高级讲师203人，研究生75人。全日制在校生15000人，成教学员3000余人。建有校内实验实训室78个，校外实习实训基地165处，信息中心、实训实习车间、图书馆、多媒体教室、计算机房、语音室等教学设施完善',72725);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北科技学院',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.hbkjxy.cn/','风雨砥砺彰显典范河北科技学院创建于1991年，是经河北省人民政府批准、教育部备案,具有独立颁发国家高等教育学历证书资格的全日制普通本科高校。学院坐落在历史文化古城保定市，地理位置优越，交通便利，素有“京畿重地”、“首都南大门”之称。学院下设机电工程系（院）、汽车工程系（院）、建筑工程系（院）、经济管理系（院）、信息与艺术系（院）、继续教育处（院）等六个二级单位，涵盖45个本、专科专业，是一所以工科为主，兼工、经、管、文多学科协调发展的综合性大学。学院面积617.2亩，在校生8000余人来自全国二',258697);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('保定电力职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.bddy.cn','保定电力职业技术学院，始建于1957年，隶属于中国华北电网有限公司。多年来，学院秉承“追求卓越，自强不息”的精神，践行“德技双修，勤绩立世”的校训，形成了“严谨勤奋，团结进取”的校风、“厚德博学，善导敬业”的教风、“勤学善思，精技致用”的学风。学院设有电气工程系、动力工程系、信息工程与管理系和基础教学部，目前在校生5000人。办学底蕴丰厚：学院是河北省职业教育先进单位、河北省职业教育教学改革先进单位、河北省文明单位、河北省园林式单位、全国电力职业技术教育标兵学校、国家电力公司双文明单位、全国精神',207263);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北机电职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hbjd.com.cn/','河北机电职业技术学院建校于1956年，先后在承德、石家庄、保定、邯郸等地办学，1972年搬迁邢台市，郭沫若曾两次为学校亲笔题写校名。2003年4月，河北省人民政府批准学校独立升格为普通高等院校，改名为“河北机电职业技术学院”。建校至今，为社会培养各类专业技术与管理人才5万余名。学院隶属于河北省教育厅，是一所以工科为主、文管兼备、门类齐全的国办全日制普通高等院校，是国家机电行业骨干院校、河北省装备制造业职业教育集团牵头单位、全国机械行业骨干职业院校。2010年被批准为“河北省示范性高等职业院校”。',180215);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('渤海石油职业学院',' ','河北',0,1,0,0,0,' ','暂空',' ','oldname": "渤海石油职业学院','渤海石油职业学院是中国石油天然气集团公司与河北省共建、面向全国招生的公办全日制普通高等院校。学院建于1976年华北石油会战初期，2004年12月经教育部批准更名为渤海石油职业学院。2006年8月，中国石油天然气集团公司在学院成立了“中国石油华北技师学院”，承担集团公司技师、高级技师和高技能人才的培养、培训任务。学院位于北京南特大型国有企业华北油田所在地——河北省任丘市，处于环渤海经济开放带，毗邻驰名中外的“华北明珠”白洋淀。纵贯南北的交通大动脉京九铁路和京开公路（国道106线）从本市通过，交通极',218880);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('廊坊职业技术学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.lfzj.cn/','廊坊职业技术学院是经河北省人民政府批准、教育部备案的公立全日制普通高等学校，实施专科层次高等职业教育。学院坐落于素有“京津走廊”之称的河北省廊坊市。现有东方大学城主校区和市区三个分校区，主校区位于环渤海经济圈的腹地——京津塘高速公路沿线的廊坊经济技术开发区。学院占地面积600亩，总建筑面积19万平方米，固定资产总值2.3亿。建有各类实验实训室70余个，校内实训基地4个，其中包括现代农业和数控技术两个省级职业教育实训基地，教学仪器设备总值4612万元。拥有校外实习实训基地150多个。图书馆馆藏资源',282295);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('唐山科技职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.tskjzy.cn/','唐山科技职业技术学院是经河北省人民政府批准，教育部备案的省属国办全日制高等职业学院，隶属于唐山钢铁集团有限责任公司，大学专科层次。唐山是具有百年历史的北方工业重镇，现代工业的摇篮。在这里诞生过第一座现代化煤井、第一条标准轨距铁路、第一辆蒸汽机车、第一袋水泥。有着深厚的科技、工业及文化底蕴。唐山人敢做敢为，不甘平庸，外向张扬的城市性格创造了一个个奇迹，造就了一代代英豪，这里人杰地灵。唐山科技职业技术学院就座落其中。学院分为主校区、成教校区、中专校区和实训校区，总占地面积363亩。走进主校区，绿树成',83027);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄邮电职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.sjzpc.edu.cn','石家庄邮电职业技术学院始建于1956年，现隶属中国邮政集团公司，为全国邮电行业知名院校。学院同时是中国邮政集团公司培训中心、远程教育中心和科技支撑基地。50多年来，学院依托行业办学体制优势，始终致力于邮电高等教育研究和高素质、高技能特色专门人才培养，积累了具有行业特色的优质教育资源，构建了邮电特色专业体系，形成了校企紧密融合的办学机制和特色优势。2004年在教育部人才培养工作水平评估中被评为优秀院校。学院坐落在石家庄市东南高教区，建有东、西两个校区，校园占地面积300余亩，拥有校舍建筑面积17.',419760);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北公安警察职业学院',' ','河北',0,11,0,0,0,' ','暂空',' ','http://www.hebsjy.com','河北公安警察职业学院是一所专科层次的普通高等院校，隶属于河北省公安厅。作为河北公安教育的最高学府和河北公安人才培养的重要基地，河北公安警察职业学院坚持社会主义办学方向，坚持“依法治警，依法治校”的方针，坚持规模、质量、效益协调发展的工作思路，坚持严格的警务化管理模式，形成了自己的办学风格。学院的发展走过了25年的风雨历程，其前身为创建于1978年的河北省公安学校。1982年，学校搬迁至省会石家庄；1991年，河北省公安学校正式更名为河北省人民警察学校；1998年，河北省人民警察学校与河北科技大学',251568);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄工商职业学院',' ','河北',0,6,0,0,0,' ','暂空',' ','http://sjzgsxy.com/','石家庄工商职业学院是河北省政府批准设立、教育部备案的一所全日制普通高等职业院校。学院的前身是1983年10月共青团河北省委创办的河北刊授学院，1995年6月更名为河北青年进修学院，2003年5月改建为石家庄工商职业学院。学院位于省会石家庄市西南高教区的民心河畔。这里垂柳碧波，风景优美；高校云集，人文气息浓郁；毗邻石家庄站，交通便捷。学院占地27万平方米，建筑面积8.3万平方米，现代化的教学楼、图书馆、公寓、食堂、超市、浴室、医务室等各种设施齐全，具备良好的学习、生活条件。学院下设五个二级学院：管',230387);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄理工职业学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.sjzlg.com','石家庄理工职业学院成立于2003年，国家部标代码13400，是一所教育部批准，计划内招生，独立颁发国家承认的普通高等专科学历毕业证书的高等职业院校。原名石家庄联合技术职业学院，2008年4月11日国家教育部批准正式更名为石家庄理工职业学院（《教发[2008]13号》），是河北省最大和实力最强的民办高校。学院职能部门设有办公室、人事处、教务处、学生处、招生办公室、就业指导中心、后勤处、图书馆。教学单位下设建筑工程系、机电工程系、信息技术系、管理系、护理学院、成教部、中专部（招生代码5377；557',74232);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄科技信息职业学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.kjxinxiedu.com/','学院简介石家庄科技信息职业学院，是经河北省政府批准、国家教育部备案的大学专科层次的省属普通高校，按国家计划面向全国统一招生，学生毕业颁发普通大学专科毕业证书。学院是以科技信息类专业为主，以工、经、文、医、管等学科协调发展的综合性普通高校，设有信息科学、经济管理、外语、医药工程、机电及自动化，基础部、体育部，实验中心、现代信息中心、现代教育中心等五系两部三中心。开设计算机网络技术、计算机多媒体技术、计算机信息管理、软件技术、软件技术（游戏开发方向）、移动通信技术、通信技术、金融保险、财务信息管理、',117238);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北大学工商学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.hicc.cn','河北大学工商学院创建于2001年，是经河北省人民政府批准，教育部首批确认，具有独立法人资格，执行教育部《独立学院设置与管理办法》，实施本科学历教育的高等学校。目前学院已发展成为一所集文、理、工、经、法、管等多学科为一体的综合性学院，成为省内创办最早、在校生人数最多、教学设施最完善、管理最规范的独立学院。河北大学工商学院建院以来，得到了各级政府和社会各界的亲切关怀和大力支持。全国人大常委会副委员长李铁映、国务委员原教育部部长陈至立、原全国人大常委会副委员长彭珮云、教育部部长周济、教育部副部长吴启迪',1365672);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北科技大学理工学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://hbklg.hebust.edu.cn/','河北科技大学理工学院成立于2001年，是依托省属骨干大学——河北科技大学举办，经国家教育部和河北省人民政府批准建立的全日制本科独立学院。学院位于河北省省会石家庄市，交通便利，共享京津冀协同发展的良好环境和优质资源。学院现有全日制本科在校生15000余人，设有专业55个，是一所具有良好办学条件和集工学、理学、文学、法学、艺术、医学、经济学、管理学等多学科为一体的综合性独立学院，校园环境优美，教学、实验及生活设施先进齐全，是莘莘学子求学成才的理想殿堂。学院始终坚持“一切为了学生，经过四年的培养与塑造',1376592);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北师范大学汇华学院',' ','河北',0,4,0,0,0,' ','暂空',' ','http://huihua.hebtu.edu.cn','河北师范大学汇华学院成立于2001年6月，是国家教育部和河北省政府批准设立，由河北师范大学按照新机制、新模式举办的全日制本科层次的独立学院。汇华学院坐落在石家庄市西南高教区，毗邻河北师范大学西校区，规划占地800多亩，学院教学设备先进、齐全，环境优雅，是一所园林式校园。河北师大充分发挥其雄厚教师、教学、管理资源的优势，保障汇华学院办学。建院7年来，学院秉承师大优良传统，注重教学质量，注重打造人才，实行严格的教学管理，培养学生全面发展。学院坚持“以学生为本”的办学理念，致力建设让人民满意的大学。广',957031);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北经贸大学经济管理学院',' ','河北',0,6,0,0,0,' ','暂空',' ','http://jgxy.heuet.edu.cn/','河北经贸大学经济管理学院是由河北经贸大学举办，按照新机制、新模式运行的本科层次全日制普通高等学校。学院于2001年7月经河北省政府(冀教发[2001]81号文件)批准成立，并报教育部备案，当时学院名称为“河北经贸大学财通学院”。2003年5月，根据河北省教育厅冀教发[2003]57号文件，河北经贸大学财通学院更名为河北经贸大学经济管理学院。次年，根据冀教发[2004]107号文件，我院成为教育部首批确认的独立学院之一。我院位于石家庄市西南高教区，地理位置优越、交通便利，拥有良好的人文环境和教学氛',1096640);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北医科大学临床学院',' ','河北',0,3,0,0,0,' ','暂空',' ','http://lcxy.hebmu.edu.cn/','河北医科大学临床学院是河北省人民政府2001年7月批准成立的13所独立学院之一，由河北医科大学主办，于2004年首批通过了教育部的确认,为全日制本科层次教育。学院位于石家庄市东南高教区，交通便利，校园环境优美典雅，建有现代化的教学大楼、实验中心、图书馆、学生公寓楼、食堂、标准运动场等基础设施，为学生提供了良好的学习环境和生活条件。临床学院依托河北医科大学雄厚的教育教学资源，以市场为导向，以教学为中心，积极探索适应社会需求的、灵活高效的办学体制。经过近几年来的建设，学院已发展成为涵盖多门学科的综合',1550076);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华北电力大学科技学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hdky.edu.cn/','华北电力大学科技学院成立于2002年6月，是国家教育部和河北省人民政府批准设立，由国家“211工程”重点建设高校华北电力大学与河北省电力公司按新机制、新模式合作举办的全日制本科层次的独立学院。2004年经过国家教育部专家组评估为合格办学单位。华北电力大学科技学院坐落于历史文化古城保定。保定市位于河北省中部，南距河北省省会石家庄125公里，北距北京141公里，东距天津155公里，地处“京、津、石三角”中部；总面积达2万多平方公里(市区面积312.3平方公里)，人口达一千多万(市区人口八十多万人)，',1479086);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西服装职业技术学院',' ','江西',0,8,0,0,0,' ','暂空',' ','http://www.fuzhuang.com.cn/','江西服装职业技术学院是经江西省人民政府批准，国家教育部备案，具有独立颁发普通高等教育高职（专科）学历文凭资格，纳入普通高等教育计划内招生、目前国内专业设置最齐全的服装类民办普通高等职业技术院校。学院坐落在英雄城南昌市南郊，占地1300余亩。建筑面积近50万平方米、独具特色的欧式建筑风格展示了服装无国界、中国服装走向世界的优美意境和雄浑气魄。校园内亭榭楼台错落，绿树芳草遍地，香樟、翠竹、玫瑰、桂花等花木芳草争奇斗艳，诗词歌赋、服饰文化陈列馆、雕塑、绘画、服饰创作与表演等校园风物，让学校成为彰显中外',36586);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西服装学院',' ','江西',1,8,0,0,0,' ','暂空',' ','http://www.jift.edu.cn/','江西服装学院是经国家教育部批准设立的全日制普通本科高校,是一所面向时尚行业，以培养创意设计、工程技术、市场推广和经营管理高级专门人才为主的专业性院校。学院秉承“特色立校、质量强校”的办学理念，形成了以服装教育为主体、艺术设计、纺织、经贸管理协调发展的学科体系。学院坐落在江西省南昌市南郊，千亩校园，绿树成荫,生态宜人，人文景致，相得益彰，是读书修性的好地方。40余万平方米的校舍建筑，欧式风格，雄浑大气。霓裳宫、服饰文化陈列馆等承载着厚重的文化底蕴。学院现设有时装设计分院、服装设计与管理分院、服装工',117150);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西科技职业学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.jxkeda.com/','江西科技职业学院是一所经江西省人民政府批准设立、国家教育部备案（教育部代码13419），具有独立颁发国家高等学历证书资格的,纳入国家统招计划的全日制综合性普通高校。学院地处“八一军旗升起的地方”、“英雄城”??江西省南昌市，校园景色秀丽、环境优美，是学生读书成才的好地方。学院设有计算机信息工程系、机电工程系、经济管理系、外语外贸系、建筑工程系、艺术系等六大系，设有50多个市场热门的特色专业。学院现有来自全国30个省、市、自治区的各民族在校生8000余名，迄今已为国家培养和输送了三万余名高等专业人',94652);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌职业学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.nczyxy.com/Default.html','南昌职业学院是经江西省人民政府批准、国家教育部备案，具有独立颁发国家高等教育学历资格的全日制综合性普通高等学校，是江西省唯一以省会中心城市命名的高职院校，得到了省委省政府和南昌市委市政府的高度重视和大力支持，设有经济管理分院、机械工程分院、信息技术分院、人文外语分院、艺术设计分院、音乐舞蹈分院和体育卫生分院。学院坐落在“物华天宝、人杰地灵”的历史文化名城—南昌市，南昌先后获得了全国卫生先进城市、中国优秀旅游城市、世界十大动感都会、中国十大最美丽城市等20余个荣誉称号，拥有中国最高的“南昌之星”摩',250483);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌工学院',' ','江西',1,9,0,0,0,' ','暂空',' ','http://zsb.ncgxy.com/','南昌工学院坐落于八一军旗升起的英雄城——南昌。学院由原省委书记傅雨田为首的一批老同志创办于1988年，是经国家教育部批准的一所全日制普通本科高校，也是全国最大和华东地区唯一的全国高校民族预科教育基地。南昌工学院作为一所融普通本专科教育、少数民族预科教育、新疆和西藏中职教育以及民族特项培训为一体的本科高校。近年来，为贯彻落实“促进高校办出特色，建立高校分类体系，实行分类管理”、“建立现代职业教育体系”的要求，深度契合国家经济转型升级对人才需求的驱动及我国教育人才结构性调整，学校以二次创业的精神和魄',306433);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西外语外贸职业学院',' ','江西',0,6,0,0,0,' ','暂空',' ','http://www.jxcfs.com/','江西外语外贸职业学院是经江西省人民政府批准，江西省唯一一所公办的外语外贸类高等职业学院，隶属于江西省对外贸易经济合作厅。学院始创于1964年，曾在联合国开发计划署(UNDP)的援助下，设立了江西省国际经济贸易人才培训中心，为中国南方区域培养中高级外语和外经贸人才，并与上海外贸学院、对外经济贸易大学等高校合作办学。四十多年来，学院秉承“严谨、求知、开拓、创优”校训，以“招得进，育得好，送得出”为办学宗旨，以“让学生成才，让家长放心，让社会满意”为工作总体目标，以“立足江西，面向全国，走向世界”为发',155480);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西工业贸易职业技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.jxgmxy.com','江西工业贸易职业技术学院是教育部备案的一所集文、理、经、工、管和艺术等学科门类齐全的综合性公办普通高等院校。学院创建于1964年，占地面积663亩，现有全日制在校生7500余人。学院地处江西省会城市??英雄城南昌，江南名楼??滕王阁与学院仅一江之隔，学院南邻南昌市政府新址红谷滩，西靠风景秀丽的梅岭风景名胜区。学院新校区坐落在南昌市前湖高校园区，占地540亩，一期工程投资1.8亿元，今年即可满足5000名高职新生进校。学院师资力量雄厚，教学设备先进，学术气氛浓厚，科研成果突出。现有专职教师260余',67241);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宜春职业技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.ycvc.jx.cn','宜春职业技术学院是经江西省政府批准，国家教育部备案，由宜春宜春市政府主管的公办专科层次的全日制普通高校。学院确立了“以服务为中心，以就业为导向，培养既有一定的理论知识又有较强动手能力的实用型人才”的培养目标，树立了“以德治校、质量立校、人才强校、特色兴校”的办学指导思想。坚持“高质量、综合性、多层次、开放式”的办学方向。突出强调学生要有较高的道德素质以及对知识的综合应用能力和实践操作能力，实行“多证制”教育，构建以能力为本位，以“多证”为框架，以综合素质教育和职业技能培训为主线的人才培养模式。学',115184);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西应用工程职业学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jxatei.net/','江西应用工程职业学院系一所具有光荣历史和革命传统的公办普通高等学校。它的前身是安源路矿工人补习学校。这所补习学校是由中国工人运动的早期领导人，无产阶级革命家李立三同志遵照毛泽东同志的指示于1922年1月创办的，是中国共产党创办的第一所职业学校。当时，李立三、刘少奇、陈潭秋等老一辈无产阶级革命家亲自担任教员编著教材，传授工人文化，宣传马列主义和革命道理，为中国革命输送了大批人才，做出了不可磨灭的贡献。后因战争而停办，1949年冬，恢复建校。1999年列入江西省首批高职招生院校，后经江西省人民政府批',64809);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西生物科技职业学院',' ','江西',0,2,0,0,0,' ','暂空',' ','http://zsw.jxswkj.com/','江西生物科技职业学院是经江西省人民政府批准，国家教育部备案的一所全日制公办的普通高等院校，是江西省唯一一所以生物科技为特色的高等职业学院。近半个世纪以来，学院先后为社会培养和输送了一万余名优秀的高质量实用型人才，他们中有不少人已成为生物科技领域的管理者和技术骨干，在社会产生了广泛的影响，也为学院赢得了良好的社会声誉。学院地处南昌市青云谱区，占地近500余亩，环境优美、交通便利。校舍建筑面积11万余平方米。拥有教学楼、实训楼、图书馆、大礼堂、学生公寓、游泳池、标准体育运动场等教学设施。图书馆藏书多',77702);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西建设职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jxjsxy.com','我院为公办全日制普通高等专科院校，是国家教育部、建设部确定的示范性高等职业学院，也是省内唯一一所建设类（土木建筑领域）大专院校。学院位于南昌市红谷滩中心区，与江南名楼滕王阁隔江相望。唐代诗人王勃在《滕王阁序》中的千古名句“落霞与孤鹜齐飞，秋水共长天一色”所描绘的正是我院地处之处的极致景色，校园环境幽雅怡人。学院创办于1958年，办学历史悠久，师资力量雄厚。现有专任教师200余人，副教授以上职称教师50余人；“双师型”教师70余人，其中有：国家注册建筑师、注册结构工程师、注册监理工程师、注册造价师',165819);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('抚州职业技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.fzjsxy.com/','抚州职业技术学院是一所集理工、经贸、生物、艺术为一体，以培养高级应用型技术管理人才为主的公办全日制综合性普通高等专科学校。学院新校区坐落在江西省抚州市迎宾大道新东城教育园区内。距省会南昌市100公里，北接浙赣线铁路、东依鹰厦铁路、西临向莆铁路；东临一级公路、昌厦一级公路、京福高速公路环绕校园。悠悠抚河水曾哺育了灿若星河的抚州名士；居庙堂而安天下的王安石、吟四梦屈西厢的汤显祖、一代大擘文化词丽的曾巩、心学鼻祖陆九渊、无产阶级革命家李井泉、国学泰斗游国恩、物理学家饶毓泰、书法巨匠舒同、曼妙琴弦盛中国',50883);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌大学科学技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.ndkj.com.cn/','南昌大学科学技术学院是由南昌大学申报举办，于2001年8月1日经江西省发展计划委员会、江西省教育厅批准成立的一所本科综合性独立学院。学院座落于英雄城南昌高校云集、人才济济的城东，占地面积600亩，其中：青苑校区54亩，青山湖南校区550亩。学院设有南昌大学优势本科专业43个，面向全国26个省（市）招生，现有全日制本科层次学生11921人。2003年，根据国家教育部《关于规范并加强普通高校以新的机制和模式试办独立学院管理的若干意见》（教发[2003]8号）的文件精神，学院把握机遇，规范办学，于当年',435650);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌大学共青学院',' ','江西',0,0,0,0,0,' ','暂空',' ','oldname": "南昌大学共青学院','南昌大学共青学院创办于1985年，由南昌大学（原江西大学）和共青开发区联合创办，现在是南昌大学的独立学院，1985年原中共中央总书记胡耀邦同志亲笔为学院题写了院名。学院座落在风景如画的鄱阳湖畔，位于国家级生态示范区——江西省共青开放开发区，学院占地面积1680余亩，校园环境优雅，教学设施齐全。学院重视硬件建设和内涵发展走多层次、多规格办学道路，力争在近几年内把学院建设成为省内享有盛誉、国内颇具影响的新型学院。南昌大学共青学院秉承“办人民满意的教育”的办学理念，以“团结、勤奋、求实、创新”为校训，',461418);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华东交通大学理工学院',' ','江西',1,1,0,0,0,' ','暂空',' ','http://www.ecjtuit.com.cn/','华东交通大学理工学院位于“物华天宝，人杰地灵”之美誉的江南历史文化名城——江西省南昌市，地处南昌经济技术开发区，座落在风景秀丽的黄家湖畔，是经江西省教育厅、省发展计划委员会首批批准成立，并经国家教育部（教发[2003]543号文）确认的一所以普通本科学历教育为主的独立学院。学院下设土木建筑分院、机电工程分院、电气与信息工程分院、经济管理分院、人文法学分院、科学技术分院等六个分院和基础学科部等，开设有39个本（专）科专业，在校生万余人。优良的办学条件、秀美的校园坏境。学院现有占地面积1500余亩，',524245);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东华理工大学长江学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://cjc.ecit.cn/','东华理工大学长江学院是依托东华理工大学(原华东地质学院－中央部委院校)优质的本科教育资源和53年的办学经验，经教育部批准，于2002年成立的普通本科独立学院。2004年，学院顺利通过了教育部对独立学院办学条件的专项检查，并获得专家的一致好评。学院地处南昌经济技术开发区，占地面积890亩，院内湖光山色、景观优美、人文气息浓厚、结构布局合理、育人功能突出，是现代化、园林式的新型大学校园。学院引进国际先进的办学理念，通过引进外籍教师，为学生创造良好的英语学习环境。按照“理论基础适度、专业特色鲜明、应用',263096);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌航空大学科技学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.nckjxy.cn/','南昌航空大学科技学院是一所面向全国招生、以工科为主、文理经管专业相结合的普通高等院校。隶属于南昌航空大学。2001年7月经江西省教育厅、江西省计划发展委员会批准试办，2003年12月经国家教育部确认为“独立学院”。学院占地面积700余亩，校舍建筑面积45万平方米，现设24个本科专业，涵盖工学、管理学、文学、经济学等学科门类，现有在校学生5335人。建院以来，学院按照国家教育部《关于规范并加强普通高校以新的机制和模式试办独立学院管理的若干意见》（教发[2003]8号），一方面加大了教学仪器设备等硬',273833);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西理工大学应用科学学院',' ','江西',0,1,0,0,0,' ','暂空',' ','oldname": "江西理工大学应用科学学院','江西理工大学应用科学学院坐落于千里赣江第一城——江西省赣州市，地处风景秀丽的章江之滨。这里是客家文化的摇篮，是中国革命的红色故都，是全国著名的园林化城市。学院2001年8月1日经江西省人民政府批准设立，系江西省首批批准设立的八所独立学院之一，也是首批（2003年12月）经国家教育部确认的249所独立学院之一；学院原名为南方冶金学院西点学院，2002年更名为南方冶金学院应用科学学院，2004年更名为江西理工大学应用科学学院；学院隶属于江西理工大学，是由江西理工大学按新机制、新模式创建的一所全日制普',295813);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('景德镇陶瓷大学科技艺术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jci-ky.cn/','历史沿革景德镇陶瓷学院科技艺术学院是景德镇陶瓷学院申办的，经教育部批准的，按新机制运行的公办全日制本科独立学院。学院于2001年8月根据江西教育厅、江西省发展计划委员会（赣教计字[2001]195号）的批复开始筹建，2003年12月国家教育部(教发函[2003]543号)予以确认,2001年年底成立并于2002年开始招生。名城名校世界闻名的瓷都——中国景德镇，属江西省直辖市，是国务院首批公布的全国24个历史文化名城之一和国家甲类对外开放城市。位于江西东北部，毗邻黄山、庐山、婺源等国际著名旅游景区',147286);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西农业大学南昌商学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.ncsxy.com','学院性质江西农业大学南昌商学院创建于2001年7月，由江西农业大学和江西亨通房地产开发有限公司合作举办，是经教育部首批批准为实施普通高等学校全日制本科教育的独立学院。地理环境学院占地490亩，地处昌北大学区中心，交通便利，出入安全。校园内有山有水，喷泉辉映，学生公寓环湖而建，绿树掩映，碧草如茵。花园式的校园给学生提供了优美而舒适的生活、学习环境。办学规模学院以江西农大六十多年的办学经验和优势学科为依托，已形成经、管、文、工、农、法等学科为一体的教学体系，现设有经济系、财会系、管理系、计算机系、外',262679);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西师范大学科学技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.gpu2001.com/','科技学院创办于2001年5月，是国家教育部和江西省人民政府批准设立的全日制普通高校独立学院。是一所融文、法、理、工、商、管、体育和艺术教育于一体的按新机制新模式运行的综合性学院。学院按民办机制运作，依靠多年积累的教育教学资源优势，荟萃学校名师和社会名流，突出办学、治学和育人特色，树立科学发展观，确立“以人为本，质量立校”的理念，走“以市场为导向，凝特色求发展”之路；坚持按大学发展规律办学，按人才成长规律教学，按科学管理规律治学。学院校址位于南昌市内的中心地带北京西路437号，校园占地400余亩。',340128);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('赣南师范大学科技学院',' ','江西',0,4,0,0,0,' ','暂空',' ','http://www.gnnu.cn/','赣南师范学院位于江西南部、京九线上的国家历史文化名城——赣州市。学校创办于1958年6月，是目前赣粤闽湘四省边际区域唯一的一所具有硕士学位授予权的省属高等师范院校。办学50多年来，学校已为国家输送了各类合格毕业生6万余人。2007年，学校以“优秀”成绩通过教育部本科教学工作水平评估，教育部评估专家组称赞学校“是一所办学特色鲜明、发展前景良好的本科师范院校，在教学质量和办学实力方面处于同类地方高等师范院校的前列”。学校学科齐全，涵盖法学、经济学、历史学、文学、理学、工学、教育学、管理学等8个学科门',144637);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西财经大学现代经济管理学院',' ','江西',0,6,0,0,0,' ','暂空',' ','http://xjg.jxufe.cn/ ','江西财经大学现代经济管理学院是一所以经济、管理类学科为主,法、工、文等学科协调发展的本科院校。学院于2001年8月由江西省教育厅和江西省发展计划委员会批准设立，并于2003年12月经教育部予以确认的独立学院。学院位于江西省南昌市昌北，占地面积460余亩，教学、生活设施齐全，环境优美。学院依托江西财经大学雄厚的教育资源和优势学科，坚持以教学为中心，以学生成才为本，走“质量立院”之路。学院教师主体来自江西财经大学，现有教师330余人，其中副高职称以上教师占52％，硕士以上学历的教师占43％。学院于2',489665);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江生物科技职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.hljswkj.org.cn/','黑龙江生物科技职业学院是公办普通高等院校，创建于1948年，坐落在哈尔滨市，在校生7200余人。设有农学系、生物制药系、水产技术与环境工程系、食品生物系、畜牧兽医系、经济管理系、信息工程系、建筑工程系共8个系，35个专业。现有专任教师321人，教授28人，副教授128人，“双师素质”教师225人。有省优秀中青年专家1人，全国优秀科技工作者1人，省级教学名师4人，全国农业职业教育教学名师2人，省级、委级、院级学术带头人35人。有1个中央财政支持的职教实训基地、1个人力资源社会保障部专项资金支持的特',207177);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江商业职业学院',' ','黑龙江',0,6,0,0,0,' ','暂空',' ','http://www.hljszy.cn','黑龙江商业职业学院是经省政府批准、国家教育部备案、独立设置的一所公办全日制普通高等院校，坐落在秀美灵和的镜泊湖畔—牡丹江市。学院始建于1958年，前身为黑龙江省蚕业学校，1972年定名为黑龙江省牡丹江商业学校，1995年更名为黑龙江财贸学校，2000年被国家教育部首批命名为国家级重点中专学校。2003年3月，经黑龙省人民政府批准，在黑龙江省财贸学校的基础上组建黑龙江商业职业学院，实施普通专科层次高等职业技术教育，同时保留中等职业教育功能。2009年，学院凭借多年发展建设取得的优异成绩，顺利通过了',71330);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江公安警官职业学院',' ','黑龙江',0,11,0,0,0,' ','暂空',' ','oldname": "黑龙江公安警官职业学院','黑龙江公安警官职业学院的前身是黑龙江政法干部学校和黑龙江省人民警察学校。1946年建校，培训公安和政法干警。1978年开始中专学历教育，其中1984年—1989年举办了6年专科班（痕迹检验和刑事侦查专业）。1998年—2003年与黑龙江大学合作办学，成立黑龙江大学警官学院。2003年3月经黑龙江省人民政府批准成立黑龙江公安警官职业学院，是培养公安专业人才的高等院校，同时是黑龙江省中级警官的培训基地。学院坐落在风景秀丽的松花江北岸哈尔滨市利民开发区警官路，占地面积960亩，建筑面积14.8万平方米',29216);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江信息技术职业学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.hljitpc.com/','黑龙江信息技术职业学院是经黑龙江省政府批准、国家教育部备案的正规普通高等院校，隶属于黑龙江省工业和信息化委员会，是黑龙江省唯一一所培养信息类专业技术人才的公办全日制高等院校，被黑龙江省教育厅确定为黑龙江省骨干高等职业院校。学院是北京中关村国际软件人才培养基地之一、黑龙江省国际软件人才教育基地、黑龙江省服务外包职业教育集团理事长单位。学院占地面积20余万平方米，建有教学楼、实验楼、图书馆、综合楼、办公楼、学生公寓、餐饮中心等基础设施，藏书40万册。学院高度重视实践教学条件的改善，注重校内实训基地建',71818);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨江南职业技术学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.13451.cn/Index.html','哈尔滨江南职业技术学院是经省政府批准，国家教育部备案，具有独立颁发高职(专科)学历证书的全日制普通高校。学生在校期间可参加高等教育自学考试专本衔接教育，学习本科课程各科成绩合格可获得高等教育自学考试本科毕业证书和学位证书。学院位于哈尔滨市，坐落在国家著名旅游区金上京阿城新华开发区江南大学城，占地面积达10万平方米。学院设有图书馆、阅览室、计算机电教中心、多媒体教室、形体训练室、航空服务模拟训练舱、模拟会计实习室、旅游管理实训中心、食品检测实验室、播音主持演播室、广播电视人才培养仿真实训基地、电子',55746);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江农垦科技职业学院',' ','黑龙江',0,2,0,0,0,' ','暂空',' ','http://www.nkkjxy.com/','黑龙江农垦科技职业学院始建于1965年，校园占地50万平方米，建筑面积15万平方米，固定资产3.5亿元。坐落于哈尔滨宾西国家级经济技术开发区，毗邻国家AAAA级旅游风景区二龙山。黑龙江省骨干高职院校建设单位，黑龙江省级文明单位标兵。几代人风雨沧桑，行健自强。溯源寻根，薪火长传北大荒。从黑龙江农垦红兴隆管理局友谊农场四分场到宾西经济技术开发区；从东北农垦总局第一农业技术学校，到黑龙江农垦科技职业学院，学院三易其址，五次更名，实现了由中职到高职，由偏远地区到哈尔滨市周边办学的历史性跨越，演绎了一个又',106209);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阳光学院',' ','福建',1,1,0,0,0,' ','暂空',' ','http://ygxy.fzu.edu.cn/','阳光学院成立于2001年，由国家“211工程”重点建设大学福州大学和阳光控股共同举办，是福建省首批设立的独立学院。2015年经国家教育部批准，成为福建省首批转设为独立设置的民办普通本科高校。近年来，学院荣获“全国五四红旗团委”、“福建省五四红旗团委”、“福建省先进基层党组织”、“福建省平安校园”、“福建青年五四奖章集体”等多项荣誉。近四年来，毕业生初次就业率平均为95%。历经十五年的发展，阳光学院已经发展成为一所涵盖理、工、经、管、文、法、艺等学科的31个本科专业，拥有在校本科生8000多人的多',438010);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门大学嘉庚学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.jgxy.xmu.edu.cn','厦门大学嘉庚学院创办于2003年，是经国家教育部批准，由教育部直属重点大学厦门大学和厦门嘉庚教育发展有限公司共同举办的，按新机制和新模式运作的独立学院。2010年7月，学校获评“全国先进独立学院”。2011、2012年，学校获评“中国最具品牌价值独立学院”榜首。2013年，学校荣登“回响中国”最具领导力独立学院榜首。2014年，学校再次登上“回响中国”最具知名度独立学院榜首。2003年10月21日，时任国务委员陈至立、福建省委副书记黄瑞霖为厦门大学嘉庚学院揭牌，掀开了嘉庚学院向优质大学奋进的历史',2664709);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州大学至诚学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.fdzcxy.com','福州大学至诚学院创办于2003年7月，是经国家教育部批准，由国家“211工程”重点建设高校——福州大学和福州大学资产经营公司利用非国家财政性经费共同举办，实施全日制本科学历教育的高等学校。学院位于福州大学老校区（福州大学怡山校区，福州市杨桥西路50号)，坐落于风景秀丽的闽江之畔。办学六年来，学院在各级领导、有关部门及社会各界的关心和支持下，充分利用母体学校的优质办学资源，以及社会办学力量，秉承艰苦奋斗、严谨治学、勇于拼搏的福州大学精神，遵循“明德博学，臻于至善”院训，围绕“打造至诚品牌，创建一流',727002);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('集美大学诚毅学院',' ','福建',0,12,0,0,0,' ','暂空',' ','http://chengyi.jmu.edu.cn/','集美大学诚毅学院是经国家教育部批准设立的独立学院。其举办者集美大学，是福建省重点建设的八所高校之一，是一所以教学为主、以本科教育为主、以培养具有创新精神和实践能力的应用型人才为主，努力服务于地方经济建设与社会发展的省属多科性大学。历任福建省省长担任校董会主席。集美大学的前身院校渊源于著名爱国华侨领袖陈嘉庚先生创办的集美学村各校，至今已有89年的办学历史。集美大学设有20个学院（含诚毅学院），有19个硕士点，49个本科专业，6个专科专业，专业覆盖经济学、法学、教育学、文学、理学、工学、农学和管理学',1237505);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建师范大学协和学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://cuc.fjnu.edu.cn/','福建师范大学协和学院是由福建师范大学与有关企事业联合申办，2003年5月经福建省人民政府批准创办，2003年12月通过教育部首批确认的独立学院。学院实施全日制本科教育，独立颁发国家承认学历的普通高等学校毕业文凭。2005年在教育部对全国独立学院办学条件和教学情况的专项检查中评价良好，名列全国百所较好独立学院。百载春秋，薪火相传。学院沿用了福建师范大学前身校之一的福建协和大学的校名，继承和发扬老协和大学的优良办学传统，砥砺出“厚德励进，博学自强”的校训精神，依托福建师范大学百年学府的优质教育资源，',399689);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南检察职业学院',' ','河南',0,11,0,0,0,' ','暂空',' ','http://www.hnfjc.cn/','河南检察职业学院是经国家教育部备案批准的全国检察机关唯一一所面向社会招生的全日制普通高等院校，与国家检察官学院河南分院一个机构两个牌子。学院隶属于河南省人民检察院，目前开展有普招大专、成人大专、与中国政法大学联办成人本科班、研究生班、与国外合办专科、本科、硕士连读等多层次学历教育，目前全日制大专在校学生近4000人，是培养优秀检察官和国家法治人才的摇篮。学院位于河南省郑州市新郑龙湖大学园区，占地1018亩，规划设计总建筑面积18万平方米，预计投资2.9亿元，拟建教学楼、办公楼、培训楼、综合服务楼',120185);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('商丘工学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.sstvc.com','商丘工学院是国家教育部批准的一所普通本科高校。学校现占地76.67公顷，校舍建筑面积30.823万㎡，校内实验室、实训车间120个，校外实习、实训基地128个，图书馆馆藏纸质图书106.7万余册。学校设有机械工程学院、土木工程学院、医学院、教育与现代艺术学院、管理学院、信息与电子工程学院、继续教育学院、思想政治理论课教学部、体育教学部、基础教学部、公共艺术教学部等11个教学院(部)。开设土木工程、机械设计制造及其自动化、计算机科学与技术、会计学、电子信息工程、建筑工程技术、环境设计、新闻采编与制',667052);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南大学民生学院',' ','河南',0,6,0,0,0,' ','暂空',' ','http://minsheng.henu.edu.cn','2003年，伴随着中国高等教育改革发展的前进步伐，经国家教育部和河南省人民政府批准，在历史文化名城、八朝古都开封，河南大学民生学院应运而生。学院新校区与河南大学金明校区紧邻，她扎根在河南大学这片有着百年文化积淀的沃土上，茁壮成长。2009年，河南大学与河南日报报业集团签订合作协议，共同建设民生学院，掀开了学院发展的新篇章。这种强强联合的办学模式，得到了时任中央领导李长春与教育部领导的肯定与支持。建院以来，学院秉承河南大学“明德新民，止于至善”的校训，逐渐形成了“团结、勤奋、严谨、朴实”的优良院风',2003000);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南师范大学新联学院',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.xlxy.edu.cn','河南师范大学新联学院是经教育部批准的普通全日制本科独立学院，2003年正式招生。现有郑州和新乡两个校区，占地961亩，总规划建筑面积64万平方米，在校学生18300余人。新联学院现设有英语、汉语言文学、数学与应用数学、物理学、生物技术、学前教育、心理学、机械设计制造及其自动化、工业设计、材料成型及控制工程、电子信息工程、土木工程、城市地下空间工程、财务管理、工商管理、旅游管理、广播电视编导等38个本科专业，是一所涵盖了经济学、法学、教育学、文学、理学、工学、管理学、艺术学等八个学科门类的应用技术',1153067);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('信阳学院',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.hrxy.edu.cn/','信阳学院的前身为原信阳师范学院华锐学院，创办于2003年，2016年经教育部批准转设为全日制民办普通本科高校。学校位于大别山革命老区、“全国十佳宜居城市”信阳市，西邻“江淮明珠”南湾湖，南望“云中公园”鸡公山。校园环境优美，景色宜人，被誉为“河南最美大学校园”;学校名师荟萃、治学严谨、学风浓郁、设施齐全，是莘莘学子学习深造的理想学府。学校现有外国语学院、文学院、社会科学学院、理工学院、数学与信息学院、艺术学院、土木工程学院、商学院等8个学院和翻译研究所、应用心理学研究所、汽车技术研究所、应用生物',763288);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安阳学院',' ','河南',0,4,0,0,0,' ','暂空',' ','http://shm.aynu.edu.cn/','安阳学院(原安阳师范学院人文管理学院)成立于2003年4月，是经国家教育部批准设立的一所实施普通本科学历教育为主的高等学校，由河南省春来教育集团投资。2015年10月我校顺利通过全国高等学校设置评议委员会专家组转设验收。2016年1月26日至2月24日，教育部公示拟批准设置高等学校名单，我校拟转设为独立设置本科院校——安阳学院。2016年4月，教育部下发了《关于同意安阳师范学院人文管理学院转设为安阳学院的函》(教发函【2016】92号)，2016年5月16日，安阳学院正式揭牌。地域环境学校坐落于',248667);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新乡医学院三全学院',' ','河南',0,3,0,0,0,' ','暂空',' ','http://www.sqmc.edu.cn/','新乡医学院三全学院是河南省唯一的医学类独立学院。学院秉承“全面适应社会需求，全面实施素质教育，全面培育医学英才”的办学指导思想，突出办学特色，已逐步成为一所以医学为主体，文学、理学、工学、管理学等多学科协调发展，特色鲜明、优势突出的新型高等院校。学院位于河南省新乡市，占地1500亩，建筑优雅，环境整洁优美，被河南省、新乡市授予“园林单位”称号。目前在校生16400余人。学院现代化学生公寓设施完备，校区网络全覆盖，为在校生提供优质的管理服务。学院施行“双院制”管理，设有基础医学院、护理学院、检验与',1368915);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南科技学院新科学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://xkxy.hist.edu.cn/','河南科技学院新科学院是教育部批准的按新机制、新模式举办的独立学院，属于本科层次学历教育的全日制普通高等学校，具有学士学位授予权。学院创建于2003年，地处中原名城新乡市，现有校舍建筑面积26万平方米。教学设施齐全，建有生物、化学综合实验室、微机实验室、语音室、形体训练室等各类实验室99个，校内外实习、实训基地48处。图书馆文献总量120万册。学院师资力量雄厚，现有专任教师699人，其中具有高级职称的教师195人，具有硕士及其以上学位的教师435人。全国优秀教师、省市级学术技术带头人、省青年骨干教',291509);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州工商学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.wfxy.edu.cn','郑州工商学院是2016年经教育部批准设立的全日制民办普通本科高校。学院前身为河南理工大学万方科技学院，创建于2002年，是河南省第一所本科层次的独立学院。学院位于郑州市郑东新区，地处中原腹地，植根中原文化，共享省会城市丰富的经济、文化、教育资源。校园环境优雅、设备先进、功能齐全。现有建筑工程学院、机械与电信工程学院、经济管理学院、会计学院、文法学院、艺术学院、继续教育学院等七个学院。学院建有工程技术实训中心、土工类实训室、宝玉石类实训室，工科实训室、经管类实训室、空乘实训中心、演播厅等校内学生实',1972547);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中原工学院信息商务学院',' ','河南',0,6,0,0,0,' ','暂空',' ','http://www.zcib.cn/','中原工学院信息商务学院（以下简称“信息商务学院”）成立于2003年4月，是首批经国家教育部批准的具有全日制普通本科学历教育办学资格的独立学院。学院由中原工学院和嘉宏控股集团共同举办，实行董事会领导下的院长负责制管理模式，具有独立法人资格。经过十多年的发展，学院的办学条件日益改善，办学规模不断扩大，教学质量稳步提高，师资队伍建设不断加强，学科专业建设成效显著，取得了骄人的办学成绩和良好的社会效益，赢得了上级领导和社会各界的一致好评，并先后荣获“全国先进独立学院”、“中国民办高等教育优秀院校”、“河',1242271);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北师范大学知行学院',' ','甘肃',0,4,0,0,0,' ','暂空',' ','http://zxxy.nwnu.edu.cn/','西北师范大学知行学院成立于1999年,2004年被国家教育部确认为甘肃首批独立学院，属本科层次的普通高等院校。学校面向全国招生，招生计划纳入国家普通高等教育统招计划。自2008年起招收的学生，学习期满成绩合格的，颁发西北师范大学知行学院的毕业证书，符合学士学位授予条件的，颁发西北师范大学知行学院的学士学位证书。2009年学院有30多名应届本科毕业生考取了兰州大学、中南财经政法大学、陕西师范大学、西北师范大学等高校的硕士研究生。西北师大知行学院，春风化雨英姿勃发。西北师范大学知行学院坐落于兰州市区',324662);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州交通大学博文学院',' ','甘肃',0,1,0,0,0,' ','暂空',' ','http://www.bowenedu.cn','兰州交通大学博文学院是2002年由兰州交通大学申办、经甘肃省教育厅批准成立的兰州交通大学的二级学院，2004年被国家教育部确认为甘肃省首批独立学院，属本科层次的普通高等学校。学院招生纳入国家普通高校统招计划，面向全国20多个省、市、自治区招生。自2008年起招收的学生，学习期满成绩合格的，颁发兰州交通大学博文学院的毕业证书，符合学士学位授予条件的，颁发兰州交通大学博文学院学士学位证书。学院继承兰州交通大学优良的办学传统，博采众长，十分注重吸收省内外著名大学办学经验，弘扬“博衍明德，文倡格物”校风',547527);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州理工大学技术工程学院',' ','甘肃',0,1,0,0,0,' ','暂空',' ','http://www.lutcte.cn','兰州理工大学技术工程学院是由兰州理工大学与深圳市华育昌国际科教开发有限公司联合举办的全日制普通本科院校。2004年2月被国家教育部首批确认为“独立学院”。2010年7月被中国独立学院协作会授予“全国先进独立学院”称号。学院办学起点较高，办学机制科学，办学质量保证，办学特色鲜明，办学实力雄厚。学院目前有在校生9000余人，设有10个系，5个教学部，1个工程训练中心，31个本科专业，建立了170余家校外实验实习实训基地，形成了以机械、电气、土木、材料、化工等工学学科专业为主，兼备理学、文学、管理学专',530276);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武威职业学院',' ','甘肃',0,0,0,0,0,' ','暂空',' ','http://www.wwoc.cn','“国家示范性高等职业院校建设计划”骨干高职院校武威职业学院是2003年经甘肃省人民政府批准成立的一所全日制公办普通高职专科院校。与电大武威分校、武威师范、武威市财贸学校、武威市旅游职业中专合并办学。学院以高等职业教育为主体，形成了本科、专科、中专、短期培训等多层次多规格的办学格局。学院地处素有“银武威”美誉的甘肃省武威市，占地面积300亩，校舍建筑面积7.6万平方米，固定资产1.2亿元，其中教学仪器设备总值1600万元；馆藏图书17.11万册，新增电子图书66万册；拥有机械加工中心、汽车维修中心',126960);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃交通职业技术学院',' ','甘肃',0,1,0,0,0,' ','暂空',' ','http://www.gsjtxy.edu.cn','甘肃交通职业技术学院是经甘肃省人民政府批准、国家教育部备案的我省唯一一所主要培养交通运输类应用型人才的高等职业院校，其前身是成立于1956年的甘肃省交通学校。2003年4月，甘肃省交通学校、甘肃省交通干部学校合并成立甘肃交通职业技术学院。2009年4月由甘肃省交通运输厅划转至甘肃省教育厅。学院先后被上级有关部门授予国家实施“技能型紧缺人才培养培训基地”、“甘肃省专业技术人员继续教育基地”、“省级文明单位”、“省级文明单位标兵”等称号。道路桥梁工程技术专业、计算机网络技术专业被确定为省级特色专业;',381808);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西警察学院',' ','广西',0,11,0,0,0,' ','暂空',' ','http://www.gagx.com.cn','学院前身是1950年1月成立的广西省公安学校，后经历了广西公安干部学校、广西政法公安学校、广西政法干部学校、广西人民警察学校、广西公安管理干部学院等发展阶段，2003年改制为广西警官高等专科学校;2015年，经教育部同意和自治区人民政府批准，升格为广西警察学院。学院由自治区公安厅主管，自治区教育厅进行业务指导，是广西唯一培养公安专门人才的全日制普通本科院校。学院现有仙葫、长湖和北海等三个校区,占地面积42.75万平方米(641.188亩)，校舍建筑面积27.83万平方米。其中，教学科研行政用房面',133950);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西现代职业技术学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.gxxd.net.cn/','广西现代职业技术学院创建于1978年，办学实力雄厚，是一所集工、理、经、管、文为一体的全日制综合性国家公办普通高等院校。学院占地520亩，在校生5200人。设有6个系，42个专业，建有学前教育、冶金技术、建筑装饰、机械制造与自动化等7个全区一流的实训基地和50多个基础实验室、校外实训基地，实验实训设备总价值2亿多元。目前，教职工356人，教师中具有博士、硕士研究生学历152人，教授8人，副教授48人，“双师型”教师183人。学院以“办人民满意的职业教育”为宗旨，坚持“以生为本，育人为先，质量第一',34644);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北海职业学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.bhzyxy.net','学院性质——北海职业学院是北海市人民政府举办的唯一的公办普通高等院校，面向全国招收高中起点三年全日制大专生，于2003年8月8日经广西壮族自治区人民政府批准、国家教育部备案成立。学院前身是北海市师范学校，已有30多年的办学历史。办学定位——学院坚持“立德树人，产教融合，特色兴校，服务社会”的办学理念，以服务临港产业为核心，努力建成专业结构优化、海洋文化特色鲜明、规模适度、适应经济社会发展需求、区域领先的高等职业院校。学院校训——汇流纳川修德砺能荣誉积淀——先后获得“国家级语言文字规范化示范校”，',56883);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北海艺术设计学院',' ','广西',0,8,0,0,0,' ','暂空',' ','http://www.sszss.com','北海艺术设计学院是一所经教育部批准的专业性极强的艺术设计类二本院校。学院位于美丽的南海之滨北海市。学院具有世界一流的先进办学理念和极高的专业水准。学院经历了17年的办学历程，通过整合优质资源，逐渐形成了一支强大的优秀教师团队。学院有着全国大多数高校无与伦比的办学优势：一是发挥哲学教育的特色，能够把高深的概念以及复杂的问题简单化;二是采取北海和北京两地办学模式，与清华大学美术学院、中央美院、北京服装学院等高校学术往来密切，充分借助北京政治、经济、文化、信息和高校师资优质资源，在拓宽学生视野的同时，',53830);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林山水职业学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.guolianweb.com/index.html','桂林山水职业学院是经广西壮族自治区人民政府批准，教育部备案的全日制普通高等专科学校，招收3年或5年一贯制大专生。校园坐落在山水甲天下的桂林市新区，距市政府和市民广场一公里，周围有中央公园、桂林大剧院、桂林市博物馆、桂林市图书馆等。校园既携漓江水的神韵，又带秀丽之城的灵气，是读书求学的理想之地。学院创办以来，培养了众多的能发展实用型人才，2011年1月顺利通过了教育部对学院人才培养工作评估，成为一所教育部验收合格的高校。学院的人才培养目标是：以服务为宗旨，以就业为导向，以质量为核心，培养“下得去、',42909);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.sxu.edu.cn','山西职业技术学院是一所隶属于山西省教育厅的综合性全日制普通高等职业院校。2008年，被国家教育部评为全国高职高专人才培养工作办学水平“优秀等级”院校。2010年，被国家教育部、财政部确定为“国家示范性骨干高职院校建设单位”。学院下设材料与建筑工程学院、信息工程学院、物流商务学院和机械工程学院四个二级学院，分布在四个校区。2013年，学院在太原东山占地1050亩新校区建设项目已经省发改委批准立项。学院设有十一系三部，面向“建材、信息、物流、建筑、装备制造、旅游”等行业开办有37个专业；注册有山西职',363637);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西煤炭职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','oldname": "山西煤炭职业技术学院','“国家示范性高等职业院校建设计划”骨干高职院校山西煤炭职业技术学院是经山西省人民政府批准，独立设置的全日制普通高等职业学院，位于山西省会太原市。学院前身是1952年建校的山西采矿学校，2000年起试办高职专业，2003年1月升格为高等职业技术学院，隶属于山西省煤炭工业局，业务上接受山西省教育厅领导。学院占地47.3万平米，建筑面积15.5万平米。开设有煤矿开采技术、矿井通风与安全、矿山机电、矿山测量、煤炭深加工与利用等20个专业，在校生6997名，成人教育专科函授生94名。现有专兼职教师466名',144964);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西金融职业学院',' ','山西',0,6,0,0,0,' ','暂空',' ','http://www.sxjrzyxy.com','山西金融职业学院位居“晋商”文化源远流长的古城太原，是经山西省人民政府批准、由国家教育部备案、山西省教育厅主管的一所金融类全日制普通高校，是一所以培养金融及财经行业高等应用型人才为主要目标的高等职业院校。学院以“立足金融、立足应用、立足一线”为办学特色，设有金融系、会计系、信息技术系、管理系和实训考证中心等七个教学机构，开设有金融管理与实务、国际金融、投资与理财、会计电算化、计算机应用技术、计算机信息管理、市场营销、应用英语等13个专业。其中“金融管理与实务专业”为省级教改试点专业。2004年，',199307);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原城市职业技术学院',' ','山西',0,0,0,0,0,' ','暂空',' ','http://www.cntcvc.com/','太原城市职业技术学院是经山西省人民政府批准、教育部正式备案的公办综合高等职业院校。学院位于太原市胜利桥西，环境优雅，交通便利。学院占地近200亩，拥有先进的教学、实训、生活、文体等设施设备，其中建筑技术实训基地为国家级实训基地，建材实验室成为我省高职院校唯一国家级建筑材料检测点，电子商务与机电一体化专业实训基地为国家财政重点支持的实训基地。原山西省委书记王茂林在我院设有“王茂林捐赠图书藏书馆”。学院有城建系、工程经济系、信息工程系、工商管理系、机电工程系、财会系、艺术设计系以及基础教学部等系部，',150624);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西大学商务学院',' ','山西',0,0,0,0,0,' ','暂空',' ','http://www.sdsy.sxu.edu.cn/','山西大学商务学院成立于2001年3月，是国家教育部和山西省人民政府批准设立的独立学院。山西大学商务学院秉承百年老校——山西大学的优良办学传统，是一所以全新机制和模式运行的全日制普通高等学校。学院建在具有2500年悠久历史的文化名城——山西省省会太原，位于太榆路武宿机场南侧。校园占地面积1000亩，校舍建筑30.19万平方米。校园环境优雅，办学设施完备；教学管理严谨有序，校园文化充满活力。学院坚持先进的办学理念，从现代科学技术和地方经济社会的发展需求出发，以现代商务为学院的办学特色，以现代信息技术',1101062);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原理工大学现代科技学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.xdkj.tyut.edu.cn/','太原理工大学现代科技学院是由太原理工大学和中国煤炭博物馆共同举办的独立学院，是经国家教育部和山西省人民政府批准成立的实施本科学历教育的高等学校。太原理工大学是一所具有百余年办学历史和深厚文化底蕴的高等学府，是国家“211工程”重点建设的大学，具有丰富的办学经验和雄厚的教学资源，是国家培养高层次专业人才和进行多学科、高水平学科研究的重要基地。现代科技学院充分发挥其智力、人才资源优势，不断加强独立学院教师队伍和管理队伍的建设，建立并不断完善独立学院教学水平的检测和评估体系，有效保证了办学质量。中国煤',393821);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西农业大学信息学院',' ','山西',0,2,0,0,0,' ','暂空',' ','http://www2013.cisau.com.cn/bkzs.asp','山西农业大学信息学院成立于2002年，是经教育部、山西省人民政府批准设立的全日制普通本科高校，是一所以信息科技为特色，经、文、理、工、农、管、艺术等多学科协调发展的应用技术大学。学院办学以来多次获得“全国十大独立学院”、“全国十大品牌独立学院”、“中国品牌影响力独立学院”、“中国最具办学特色独立学院”、“山西省重点工程项目”、“山西省教学体制改革试点单位”、“全国最具就业竞争力示范（院）校”等荣誉，综合实力位居全国前列。中国校友会网2016年中国农林独立学院排行榜第3名，2016年《中国科教评价',285993);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西师范大学现代文理学院',' ','山西',0,4,0,0,0,' ','暂空',' ','http://www.sxnu.edu.cn','山西师范大学现代文理学院是2002年6月经山西省人民政府批准、国家教育部备案，以新机制、新模式举办的实施全日制本科学历教育的高等学校，也是山西省唯一的一所师范类本科独立院校。学院位于山西省临汾市解放东路85号。学院充分利用山西师范大学优质的教育教学资源和合作方充足的资产以及有效的运行机制，保证了教育教学质量稳步提升;坚持以质量为中心，积极探索教育教学模式的改革与创新，全面实施素质教育，取得了良好的办学效果;办学14年来已向社会输送各类人才16600余人，成为支持地方经济建设和社会发展的人才培养基',305272);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中北大学信息商务学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://xxsw.nuc.edu.cn/','中北大学信息商务学院是经中华人民共和国教育部和山西省人民政府正式批准成立的独立学院，由中北大学举办，创建于2003年。中北大学信息商务学院是一所多科性的大学，现有本科专业28个，涵盖工、文、理、经、管、法、教七大学科门类，教学任务主要由中北大学教师承担。学院面向全国24个省、市、自治区招生，目前全日制在校生8000多名。学院北校区位于山西省太原市学院路3号，与中北大学校园毗邻，南校区位于山西省太原教育园区东外环路8号路15号。学院占地683亩，建筑面积20多万平方米，校园环境优雅，空气清新，学院',355363);
