insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆大学科学技术学院',' ','新疆',0,0,0,0,0,' ','暂空',' ','http://www.kj.xju.edu.cn','新疆大学科学技术学院成立于2002年8月。2004年1月，通过国家教育部关于办学资质的审核批准，成为我国首批本科院校举办的独立学院。2005年1月，又顺利地通过了国家教育部的综合办学检查。学院现有在校本科生3346人，设有15个专业（18个专业方向）。新疆大学是目前新疆办学规模最大、办学历史最悠久、学科门类最齐全、科研水平最高的综合性大学，是新疆惟一一所全国重点大学，是国家面向21世纪重点建设的“211工程”百所大学之一，是教育部与自治区“区部共建”高校。新疆大学作为全国重点本科院校举办独立学院',160354);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆农业大学科学技术学院',' ','新疆',0,2,0,0,0,' ','暂空',' ','http://www.xjstc.net.cn/','新疆农业大学科学技术学院成立于2002年7月，是经国家教育部批准的实施本科层次普通高等教育的独立学院。学院位于乌鲁木齐市农大路110号，校园环境优雅，基础设施齐全，办学条件先进，师资力量雄厚，是新疆首批获得教育部批准举办的独立学院。学院采用全新的教育理念，新型管理体制和运行机制，实行全员聘任和岗位负责制。根据精干高效的原则，注重能力，按需设岗，独立开展教学、学生管理及学院建设发展等各项工作，积极构建“学院自主、社会参与、市场介入、政府调控”的良性互动发展环境。学院设有语言文学与教育学部、经济与商',123102);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆医科大学厚博学院',' ','新疆',0,3,0,0,0,' ','暂空',' ','http://www.xjmu.edu.cn','新疆医科大学是一所集医疗、卫生、药学教学和科研为一体的综合性医科大学，目前内设17所学院（中医学院、基础医学院、第一临床医学院、第二临床医学院、第三临床医学院、第四临床医学院、第五临床医学院、成人教育学院、厚博学院、公共卫生学院、药学院、成教学院、护理学院、医学工程技术学院、国际医学教育学院、研究生院、语言学院），2个部（人文社会科学部、体育教学部），4个系（中医系、口腔系、针灸骨伤系、中药系），5所附属医疗机构。大学本部现有1个七年制临床医学专业，17个本科专业和专业方向，28个专科专业和专业',230056);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆财经大学商务学院',' ','新疆',0,6,0,0,0,' ','暂空',' ','http://202.201.208.9/school/swxy/','新疆财经大学商务学院是我国高等教育改革发展中涌现出的一种新的办学形式，是新疆财经大学以优质的教育资源、良好的教育品牌、严谨的管理模式与良好的社会资金相结合，按新机制、新模式举办的本科层次的独立学院，是经自治区教育厅、国家教育部先后审查确认的财经类本科院校。新疆财经大学商务学院，充分利用新疆财经大学现有高等教育优质资源，依托其雄厚的师资力量、优良的基础设施、先进的管理手段、经济科学的整体优势，坚持“因材施教、强化基础、重视实践、培养能力、提高素质”的办学指导思想，努力培养理论深厚，基础扎实，具有创',170526);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆建设职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.xjjszy.net','一、学院概况新疆建设职业技术学院是经自治区人民政府批准成立，并在国家教育部备案的一所培养现代建设类高级技术应用型专门人才的省属国办全日制高等职业院校。学生毕业后颁发国家教育部电子注册的毕业证书。学院位于新疆维吾尔自治区首府乌鲁木齐。学院是一所建设专业类的高等职业院校，是由新疆建筑职大、新疆建工学校、新疆城建学校、新疆建材学校合并组建而成，是国家建设部和教育部批准的建筑类技能型紧缺人才培养基地，是自治区建筑工程实训基地和自治区抗震安居人才培训基地，建有自治区第六和第十八国家职业技能鉴定站。在三十多',246150);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆兵团警官高等专科学校',' ','新疆',0,11,0,0,0,' ','暂空',' ','http://www.xjbtjx.com/index.asp','新疆兵团警官高等专科学校系国家教育部批准成立的全日制普通高等警察类专科院校，是一所培养监狱、劳教、公安人民警察的学校，也是以培养各类实用型政法人才为目标的政法类学校。学校位于新疆维吾尔自治区首府乌鲁木齐市北郊的五家渠市，距首府30公里，南靠青格达旅游风景区，环境幽静、空气清新、交通便利。学校占地面积197亩，教学楼、办公楼、图书馆、培训楼等建筑错落有致;多媒体教室、刑侦实验室、模拟法庭、警体训练馆、田径运动场、心理咨询室、机动车辆驾驶等教学设施齐全;学校建有千兆校园网，设有专用的电子阅览室，学生',81192);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南质量工程职业学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://yx.gaokao.haedu.cn/school/6268','河南质量工程职业学院是经河南省人民政府、国家教育部批准成立的，目前国内唯一一所以培养技术监督、质量检测类高级应用型人才为主的公办高等职业院校，位于中原工业名城——平顶山市，现有南北两个校区和一座大型校办工厂，占地58.5万㎡，建筑面积27.7万㎡。学院设有食品与化工系、经济管理系、信息工程系、机电工程系、建筑工程系、法政系、五年制大专部、基础部、社会科学部、成人教育部等6系4部和食品检验、农产品检验、商品质量检测技术、机电产品质量检验、工程监理等27个专业。学院现有普通类在校生11000人，电大',59622);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州信息科技职业学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.techcollege.cn','郑州信息科技职业学院（部标代码13565，录取代码6270）是2003年经河南省政府批准成立的一所专科层次高等职业院校。为充分利用现有教育资源，经省政府同意，从2006年起与河南广播电视大学合作办学，以郑州信息科技职业学院名义安排招生计划，利用河南广播电视大学教育资源培养学生。目前学院位于郑州市郑东新区龙子湖高校园区，占地面积465亩，建筑面积14万平方米，在校普通专科生4000余人，现有财经文法系、机械电子工程系、计算机科学与技术系、艺术系等四个系、二十多个专业。现代化网络多媒体教学手段的广泛',143059);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西铁路工程职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.sxri.net/','“国家示范性高等职业院校建设计划”骨干高职院校陕西铁路工程职业技术学院的前身是铁道部渭南铁路工程学校，创办于1973年，2002年由铁道部划归陕西省人民政府，隶属陕西省教育厅管理。2003年经陕西省人民政府批准、教育部备案，改制升格为专科层次的高等职业技术学院，现已成为一所以交通工程类和土木工程类专业为特色、培养铁路工程、基础设施工程建设急需的应用性专门人才的高等职业技术学院,有“铁路建设工程师”摇篮的美誉。学院位于陕西省渭南市站北街，东望著名的西岳华山，西临秦始皇兵马俑博物馆，高速公路、陇海铁',248492);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宝鸡职业技术学院',' ','陕西',0,4,0,0,0,' ','暂空',' ','http://www.bjvtc.com/','宝鸡职业技术学院是2003年4月经陕西省人民政府批准，宝鸡市七所中专学校合并组建而成的一所公办全日制专科层次普通高等院校。追溯办学历史，迄今已逾百年，优良办学传统闻名遐迩，英才辈出享誉三秦大地。学院秉承百年办学传统，优化教育资源，现已形成了以教育、工学、医学、农学、管理等五大学科门类为主，融高、中等职业教育、职业技能培训、成人教育于一体的办学格局,是目前西北地区规模最大，人数最多，发展最快的高职院校。学院师资力量雄厚、专业门类齐全、教学成果丰硕。现有教职工812人，其中专兼职教师703人，博士、',130584);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西航空职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.sxhkxy.com/','陕西航空职业技术学院是经陕西省人民政府批准、教育部备案的一所具有高等学历教育招生资格的全日制公办普通高等院校，隶属陕西省教育厅和中国航空工业集团公司。学院重视职业教育理论研究，不断深化教育教学改革，以质量立校，形成了办学特色鲜明、专业结构合理、培养目标明确、就业体系完善的现代高等职业教育体系，2008年9月，教育部专家组对学院人才培养工作水平进行了评估，获得“优秀”的评估结论。建校20多年来，学院为中国航空工业发展和地方经济建设培养了万余名各类高素质技能型专门人才。学院地处素有西北“小江南”之称',458440);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西电子信息职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.sxitu.com','陕西电子信息职业技术学院创建于1992年，是经陕西省人民政府批准，在国家教育部备案，招生纳入国家统招计划，面向全国招生的一所以电子信息和数控技术为特色的全日制普通高校。学院下设计算机技术系、通信工程系、经济管理系、数字控制系和中专部，共开设37个专业。学院环境幽雅，交通便利，教学设施齐全，拥有先进的教学设施及实验设备。学院师资力量雄厚，管理学科规范，在教学中重应用，强技能，不断提高学生的综合素质，教学成效显著。2005年以来连续有学生分别考入西安交通大学、西安建筑科技大学、西安科技大学、郑州大学',75838);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('金陵科技学院',' ','江苏',1,1,0,0,0,' ','暂空',' ','http://www.jit.edu.cn','金陵科技学院是经教育部批准的培养应用型人才的全日制普通本科院校。学校位于江苏省省会南京，拥有江宁、幕府两个校区，占地面积1600亩，校园建筑面积50余万平方米，馆藏图书120万册。专任教师中，教授、副教授等高级职称教师占30％，教师中有一批全国优秀教师、政府特殊津贴获得者、省级有突出贡献的中青年专家、教学名师和优秀教师、江苏省“333高层次人才培养工程”、“青蓝工程”等优秀人才。学校现有全日制在校学生18000人。学校设有商学院、人文学院、信息技术学院、机电工程学院、建筑工程学院、艺术学院、园艺',1410696);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南软件职业技术学院',' ','海南',0,1,0,0,0,' ','暂空',' ','http://www.hncst.edu.cn/','海南软件职业技术学院是2003年3月经海南省人民政府批准设立，教育部备案，由原海南琼海师范学校升格而成的主要培养信息、软件行业的高素质技术技能人才的公办全日制高等职业院校，至今已有91年的办学历史。学院是海南唯一一所连续两次顺利通过教育部人才培养工作评估的公办高职院校，并获得了专家组的高度评价。学院坐落在红色娘子军的故乡、博鳌亚洲论坛永久会址所在地、中国优秀旅游城市、全国精神文明建设示范城市、海南省东部中心城市——海南省琼海市市中心。学院占地326亩，建筑面积近16万平方米，固定资产总值1981',325406);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南政法职业学院',' ','海南',0,11,0,0,0,' ','暂空',' ','http://www.hnplc.com/','海南政法职业学院是经国家批准的海南省唯一一所专业设置涵盖公安、司法、法律类专业的公办高等政法类职业学院。2009年初，学院顺利通过教育部人才培养水平评估；2008年至2013年连续六年被评为“海南省高等学校毕业生就业工作优秀单位”；2009年，学院被中央政法委列为政法干警招录培养体制改革试点单位，为全国唯一综合培养公安、司法人才试点招生高职院校；2012年列入海南省单独招生高职院校。学院位于海南省海口市新大洲大道，交通便利。有在校学生4500名，设有公安司法系、应用法律系、法务技术系、公共安全技',127892);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南外国语职业学院',' ','海南',0,5,0,0,0,' ','暂空',' ','http://www.hncfs.edu.cn/','海南外国语职业学院HainanCollegeofForeignStudies海南外国语职业学院是经教育部批准设立的全国唯一公办全日制外语类高校，是海南省外语语种最多、外语专业最齐的高校，并于2009年顺利通过了教育部人才培养工作评估，2012年成为全省第二所省市共建高校。[优越的学习条件]学院创建于1947年，68年来已经为社会培养了大批优秀中小学英语师资和其他各类应用型人才。学院座落于全国著名的“文化之乡”、“华侨之乡”、“名人之乡”、“中国最新航天发射中心”——文昌市，距海南省会海口56公里',149650);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北工程大学科信学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://kexin.hebeu.edu.cn','河北工程大学科信学院，是依托省属骨干大学——河北工程大学举办，2001年7月经河北省人民政府批准成立的一所全日制本科层次的独立学院。2003年12月教育部予以确认。2005年，学院以优异的成绩通过了教育部“独立学院办学条件和教学状态专项检查”，2012年取得学士学位授予权。学院座落在国家历史文化名城、中国成语典故之都——河北省邯郸市，校园与河北工程大学主校区毗邻，占地600余亩，设施先进，环境优美，办学条件优良。学院依托河北工程大学的品牌专业资源，根据国家和区域经济社会发展需求，科学合理的设置有',787117);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国矿业大学徐海学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://xhc.cumt.edu.cn','中国矿业大学是教育部直属的全国重点高校、国家“211工程”和“985优势学科创新平台项目”建设高校，同时也是教育部与江苏省人民政府、国家安全生产监督管理总局共建高校，学校设有研究生院。通过长期发展和建设，学校已经形成了以工科为主、以矿业为特色，理工文管等多学科协调发展的学科专业体系和多科性大学的基本格局。目前，学校设有22个学院，59个本科专业；有35个一级学科硕士点，9个专业学位授权点，16个一级学科博士点，14个博士后科研流动站；有1个一级学科国家重点学科，8个国家重点学科，1个国家重点（培',992204);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁科技大学信息技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','oldname": "辽宁科技大学信息技术学院','辽宁科技大学信息技术学院是经教育部批准的、按照新机制、新模式举办的全日制本科普通高等学校（独立学院），是一所以工科为主，兼有文、经、管三大学科门类的多科性院校，属国家计划内统一招生。学院位于国家级旅游、卫生城市——辽宁省鞍山市，地处风景秀丽的千山山麓，群山环抱，风景秀丽，环境幽雅，交通便捷。学院主要面向辽宁、吉林、黑龙江、内蒙古、河北、河南、山东、山西、湖北、湖南、天津、安徽、江苏、浙江、陕西、四川等16个省（市）招生。目前已开设电子信息工程、自动化、计算机科学与技术、软件工程、网络工程、电子商',82884);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁石油化工大学顺华能源学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://shnyxy.lnpu.edu.cn/','地理位置辽宁石油化工大学顺华能源学院（原辽宁石油化工大学顺华学院）位于辽宁省抚顺市。抚顺是国家老工业基地之一，素有\"煤都\"之称,经过半个多世纪的建设,已从单一的煤炭工业城市,发展成在全国占有重要位置的能源、原材料基地和以石化工业为主导、门类比较齐全的现代化工业城市,为共和国的工业化作出了历史性的贡献。抚顺也是一座具有悠久历史的文化名城——中国满清王朝的发祥地，也是雷锋的第二故乡。学校隶属及特色辽宁石油化工大学顺华能源学院是经国家教育部批准设立，由新中国第一所石油工业学校——辽宁石油化工大学和新中',101557);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北工业大学城市学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://cc.hebut.edu.cn','河北工业大学城市学院2001年经河北省人民政府批准成立，2004年教育部重新审核予以确认，在天津和廊坊分别设有校区。p城市学院办学所依托的母体学校——河北工业大学始建于1903年，是国内历史最悠久的工科大学之一。现设有13个学院、3个教学管理学院和3个教学部（中心）。拥有2个国家重点学科、4个省强势特色学科、15个省级重点学科，设有61个本科专业、13个专科专业，3个一级学科博士点、17个博士点、10个一级学科硕士点、56个硕士点及15个工程硕士专业学位授权领域、17个高校教师在职攻读硕士学位专',1195430);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川外国语大学重庆南方翻译学院',' ','重庆',0,5,0,0,0,' ','暂空',' ','http://www.tcsisu.com','四川外语学院重庆南方翻译学院是2001年经重庆市教委批准，由四川外语学院和重庆南方集团有限公司联合创建的全日制本科高校，是全国首批按新机制、新模式举办的经教育部确认的独立学院。学院具备招收本科、专科及高职专科的多层次学历教育资格，根据教育部有关规定，对学习期满且成绩合格的学生，颁发教育部认可的四川外语学院重庆南方翻译学院全日制普通本科（专科）毕业证书。按照国家有关规定，2008年秋季以后入学的独立学院学生，凡符合学士学位授予条件的，均以独立学院名称颁发学士学位证书。2008年秋季以前入学的学生，',436841);
