insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆工商大学融智学院',' ','重庆',0,6,0,0,0,' ','暂空',' ','http://www.cqrz.edu.cn/','重庆工商大学融智学院（以下简称学院）由重庆工商大学举办。学院成立于2001年4月,2003年12月经教育部（教发函〔2003〕541号）批准为全日制本科层次.重庆工商大学融智学院（以下简称“学院”）由重庆工商大学举办。学院成立于2001年4月,2003年12月经教育部（教发函〔2003〕541号）批准为全日制本科层次的独立学院。2011年7月，经重庆市学位委员会审核，学院新增列为学士学位授予单位。学院先后荣获“重庆市创先争优先进基层党组织”、“重庆市依法治校示范校”、“重庆市平安校园”等称号。办',1209465);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆工商大学派斯学院',' ','重庆',0,6,0,0,0,' ','暂空',' ','http://zjc.paisi.edu.cn/zsxx/','重庆工商大学派斯学院创建于1999年4月，2003年12月教育部确认为“独立学院”（教发函［2003］541号文件），是一所以全日制普通本科学历教育为主、兼有全日制普通专科学历教育、按新模式、新机制建设和运行的普通本科高等学校。学院代码：13591；办学许可证号：教民5003821000003。学院座落于中国历史文化名城——重庆市合川区城区内，校园古朴典雅、古树参天、环境怡人，具有深厚的历史文化底蕴。学院前身是1937年春成立的四川省国立第二中学和1950年在此基础上成立的合川师范学校。1937',898820);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('燕山大学里仁学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://stc.ysu.edu.cn','燕山大学里仁学院成立于2001年7月，是经河北省人民政府批准、教育部确认的独立学院，由全国重点大学――燕山大学按照新机制、新模式举办的一所全日制普通本科高等学校。学院座落于著名海滨城市――河北省秦皇岛市，位于燕山大学西校区，占地1020余亩，建筑面积38万平方米，校园环境幽雅，基础设施先进，师资力量雄厚，办学条件优良。学院依托燕山大学学科基础，根据地方经济和社会发展需求，现设有6个系，41个专业，涉及工、管、经、文、法等多个学科门类。学院面向全国招生，现有在校生11600余人。学院拥有一支德才兼',1091636);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄铁道大学四方学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.stdusfc.cn','石家庄铁道学院四方学院是经国家教育部批准，2001年开始招生，采用新的办学模式成立的全日制普通高等教育独立学院。作为铁道学院的独立学院，四方学院依托铁道学院，充分利用铁道学院的办学资源与设施，同时独立建设了一流的教学、实验设施，具有一流的教学生活环境，拥有一批教学严谨、勇于奉献的教师和管理队伍，这些都为同学们顺利完成学业提供了有力保障。学院专业以工为主，设有工、文、经、管等多学科20多个本科专业，面向全国招生，主要培养适应社会发展需要的高等技术应用型人才，毕业颁发普通高等院校毕业证书、学士学位证',1016582);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄经济学院华信学院',' ','河北',0,6,0,0,0,' ','暂空',' ','http://www.sjzuehx.cn/','石家庄经济学院华信学院成立于2001年，是经教育部和河北省人民政府批准设立，由石家庄经济学院按新机制、新模式创办的独立设置的全日制普通本科高等学校，办学类型为独立学院。2005年通过了教育部专家组对学院的合格评估。学院位于石家庄市西南高教区，占地面积368亩，建筑面积12.3万平方米，现有在校本科生近8000余人。学院校园环境优雅，办学条件齐备、交通便利，具备良好的学习和生活环境。学院继承和发扬石家庄经济学院“勤奋、求实、团结、创新”的优良校风，倡导以学生为主体、以教师为主导，注重多样性、开放性',845580);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北农业大学现代科技学院',' ','河北',0,2,0,0,0,' ','暂空',' ','http://xianke.hebau.edu.cn/','河北农业大学现代科技学院成立于2001年7月，是经教育部批准设立、以新机制、新模式举办的全日制本科层次的独立学院。按教育部文件规定，学业期满成绩合格，符合毕业条件者，颁发由国家统一印制的河北农业大学现代科技学院本科毕业证书，符合学士学位授予条件者，授予河北农业大学现代科技学院学士学位。学院根据国家经济建设需要进行专业设置，旨在为社会培养具有高技能的应用型人才。目前学院共设有46个本科专业，学科专业涵盖“农、工、理、管、经、文、法”七大门类，形成了相互交叉、文理渗透的学科体系，学院在校生12000',743504);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原科技大学华科学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.kdhk.cn/','太原科技大学华科学院是一所以机械类、材料类、电子信息类等专业为特色，以高素质应用型人才培养为目标，由太原科技大学和山西省导通科技公司联合创办的一所独立学院。学院位于太原市晋祠路，紧邻太原市风景名胜晋阳湖，在太原晋阳新区的规划范围。学院于2002年6月经山西省人民政府同意组建，2003年开始招收全日制本科生。2004年3月，教育部确认为独立学院。2005年1月，通过了教育部对独立学院的办学条件和教学条件的专项检查。自2003级学生起，对符合毕业条件的学生颁发毕业证书，并经审核，对符合条件的学生授予',244900);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西医科大学晋祠学院',' ','山西',0,3,0,0,0,' ','暂空',' ','http://www.sxmu-jcc.com/','山西医科大学晋祠学院始建于2002年，是经教育部、山西省人民政府批准，由山西医科大学主办，社会力量参与，以新机制、新模式成立的山西省唯一的一所以医学专业为主的全日制普通本科独立学院。学院坐落在具有2500年悠久历史的文化名城太原，校园占地面积330亩，绿化面积达40%以上，园林式的校园风景秀丽、绿荫环抱、碧草如茵、清静幽雅,是读书治学的理想之地。学院立足山西，面向全国部分省、市、自治区招生,重点培养学生创新、实践能力，使学生成为“复合型、应用型、技术型”人才。建院以来,为社会培养医药卫生专业人才',195928);
