insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春理工大学光电信息学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.csoei.com','长春理工大学光电信息学院是由长春理工大学与长春中山实业集团有限公司于2001年合作创办，2004年经国家教育部确认，成为实行新型办学模式的独立学院。学院以长春理工大学的优势学科为基础，以校本部雄厚的师资力量、丰富的教育教学经验、优质的教育资源和良好的办学声誉为依托，为全面建设小康社会培养具有创新精神与实践能力的应用型人才。学院座落在长春市高新技术产业开发区大学城内，校园占地面积20万平方米，建筑面积18万平方米，教学设备投资5000多万元。校园规划别致、环境幽雅，基本设施先进，办学条件精良。建院',405582);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林动画学院',' ','吉林',1,8,0,0,0,' ','暂空',' ','http://www.jldh.com.cn/','在美丽的北国春城长春，镶嵌着一颗璀璨的艺术明珠，这就是培养艺术人才的摇篮——吉林动画学院。学院前身吉林艺术学院动画学院,是2000年6月由吉林省教育实业集团股份有限公司投资，与吉林艺术学院合作创办的；2004年2月被教育部确认为普通高等学校独立学院；2008年9月，经国家教育部综合评估，又被正式批准为独立设置的本科层次的民办普通高等院校，更名为吉林动画学院，成为全国320多家独立学院中首批、吉林省第一所由独立学院转设为本科层次的民办普通高校。经过八年的建设，学院现已发展成为一所以动漫专业为主体，',310030);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西财经大学华商学院',' ','山西',0,6,0,0,0,' ','暂空',' ','http://www.schsxy.com/','山西财经大学华商学院是经国家教育部批准设立的独立学院。学院紧邻山西财经大学主校区，地理位置优越，交通便利，环境优美。现设有经济学、国际经济与贸易、贸易经济、金融学、保险、财政学、法学、英语、信息管理与信息系统、计算机科学与技术、工商管理、市场营销、会计学、财务管理、旅游管理、行政管理、人力资源管理、物流管理、社会体育共19个普通本科专业。设有经济贸易系、财政金融系、管理系、财务会计系、文法系、基础教学一部和基础教学二部共7个系部。目前在校学生7500余人。学院依托山西财经大学独特的品牌优势和优质',315300);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁中医药大学杏林学院',' ','辽宁',0,3,0,0,0,' ','暂空',' ','http://www.lncmxl.edu.cn/','XinglinCollegeofLiaoningUniversityofTraditionalChineseMedicine辽宁中医药大学杏林学院是由辽宁中医药大学于2001年7月创办，以培养中医药类应用型人才为主的省属全日制普通本科高等学校(独立学院)。杏林学院在辽宁中医药大学的领导下，严格执行党的教育方针，以“不求最大、但求最强”的办学理念，继承发扬“勤奋务实”的优良校风，坚持“两优、两重、一适应”办学思路，突出中医药办学特色，因材施教，办学效果显著。杏林学院现为“中国独立学院协作会”副会长',544625);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽财经大学商学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://acsxy.aufe.edu.cn/','安徽财经大学商学院是2003年创办并经教育部确认的本科层次全日制独立学院，坐落在淮河流域中心城市、以珍珠城美誉遐迩的安徽省蚌埠市，毗邻山水相连、景色秀丽的国家4A级风景区龙子湖畔，环境优美，交通便捷。学院现设有会计学系、经济与金融系、工商管理系、财务与审计系、国际商务系、人文与艺术系等6个系级教学机构，1个实验室管理中心；开设有会计学、国际经济与贸易、金融学、财务管理、市场营销、人力资源管理、工商管理、物流管理、保险学、广告学、英语（商务英语方向）、审计学、统计学、国际商务、资产评估、金融工程、',554909);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽大学江淮学院',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://www.ahujhc.cn','安徽大学江淮学院是经教育部批准，由安徽大学主办，纳入第三批本科录取的全日制普通高等教育本科院校。学院办学的指导思想是：充分发挥安徽大学的办学优势和民营机制的办学活力，坚持“夯实基础、科学管理、确保优质教育”的指导方针，逐步把学院建设成特色鲜明的应用型本科院校。学院坐落于国家四大科教基地之一的合肥，占地面积约720亩，分为龙河校区（合肥市肥西路3号）和史河校区（合肥市史河路8号），合肥十景之一“五里飞虹”横跨学院两个校区。其中，拥有50多年文化底蕴的龙河校区为主校区，校园幽静典雅、景色怡人，自然景',309343);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽信息工程学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahpumec.edu.cn/','安徽信息工程学院是2016年4月经教育部批准的独立设置的全日制普通本科高等学校。2012年，在安徽省人民政府的大力支持下，亚太地区最大的语音上市公司科大讯飞有限公司与省属重点高校安徽工程大学合作举办机电学院。2016年经教育部批准为独立设置的全日制普通本科高等学校，并转设更名为安徽信息工程学院。学院位于享有“全国文明城市”、“国家园林城市”等系列美誉之称的安徽省次中心城市——芜湖市。目前拥有文津、新芜两个校区，总占地面积约1300余亩，在校生近9000人。学院以“产业工程师、创业企业家的摇篮”为',237717);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽工业大学工商学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://icc.ahut.edu.cn/','安徽工业大学工商学院是由安徽工业大学申办，经教育部批准设立的独立学院，是全日制普通本科高校。学院坐落在安徽省马鞍山市，交通便捷，气候宜人。校园环境幽雅，基础设施先进，办学条件齐备。学院建院以来，秉承其申办学校安徽工业大学数十年办学所形成的“团结、求实、勤奋、创新”的优良校风，依托安徽工业大学的整体优势，不断改革创新，着力提升“教学为中心”的理念；牢固确立教学质量为生命线的意识；逐步形成了“夯实基础、强化实践、注重创新、提高素质”的人才培养模式。学院致力于将学生培养成为德、智、体美全面发展、基础扎',180335);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽农业大学经济技术学院',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://jjjs.ahau.edu.cn/','安徽农业大学经济技术学院（13616）是经教育部批准，由安徽农业大学申办，与社会优质资源合作举办的，以培养应用型、复合型高级技术人才为目标，以安徽农业大学优质的办学资源、严格的教育教学管理为基础，采用新的办学机制和模式运行的全日制本科普通高等院校（独立学院）。学院座落在被誉为全国四大科教城市之一的安徽省省会合肥市，交通便利，环境幽雅，绿树成荫，是治学和成才的理想园地。经济技术学院充分发挥安徽农业大学的综合办学优势，雄厚的师资队伍，丰富的教学管理经验，贯彻“以学生为中心，理论联系实际”的办学理念，',196810);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽师范大学皖江学院',' ','安徽',0,4,0,0,0,' ','暂空',' ','http://wjcollege.ahnu.edu.cn/','安徽师范大学皖江学院是2003年经国家教育部批准成立,由安徽师范大学按新机制、新模式主办的独立学院（二级学院），办学层次为全日制普通本科。学院实行董事会领导下的院长负责制。学院招生属普通高校计划内招生，学院严格执行安徽省物价局核定的收费标准收取各项费用。安徽师范大学是安徽省建校最早的高等学府,也是全国建立较早的综合性大学之一。学校现有三个校区，校园总面积3300多亩，建筑面积约88万平方米。在近80年的办学历程中，刘文典、周建人、郁达夫、苏雪林、陈望道、朱湘、朱光潜等一大批著名学者先后在校执教，',236562);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽医科大学临床医学院',' ','安徽',0,3,0,0,0,' ','暂空',' ','http://cc.ahmu.edu.cn/welcome/','安徽医科大学临床医学院是安徽医科大学与合作者共同投资兴建、经教育部确认审批、按准办学成本收费、民办机制运行的独立学院，院址位于合肥经济技术开发区翡翠路15号。安徽医科大学临床医学院秉承安徽医科大学80余年来办学理念，以向社会提供合格高素质医学人才为目标。创新管理和运行机制，结合高校专业结构调整，改造传统人才培养模式，用最新的教学内容、课程体系设置打造医学精英教育平台。教学计划体现重视学生实践操作能力、团队协作精神和高度的社会责任感的培养。学院的教学生活设施齐全，育人环境优良。安徽医科大学临床医学',353457);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('阜阳师范学院信息工程学院',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://210.45.32.7/xiweb/cie/','阜阳师范学院信息工程学院是2003年经安徽省政府批准试办，2004年经教育部确认的本科层次的独立学院。学院位于阜阳师范学院清河校区，西清河纵贯其中，杨柳低垂，松杉林立，花香鸟语，环境宜人。学院设有信息科学、中文、外语、经济管理和艺术5个系共13个专业，在浙江、江苏、山东、湖北、江西、河南及安徽等7省招生，目前在校生4588人。学院占地420亩，教学生活用房10万平方米，各类图书70万册。学院不断更新教学设施，改善教学条件，逐步建立了国内一流的微机室、语音室和各类实验室。学院大力加强师资队伍建设，',138271);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淮北师范大学信息学院',' ','安徽',0,4,0,0,0,' ','暂空',' ','http://www.hbcnc.edu.cn/','淮北煤炭师范学院信息学院是2004年4月经国家教育部审批确认（教育部教发函[2004]7号）的一所以本科教育为主的综合性全日制普通高等学校。学院坐落在集能源、工业、商贸和园林为一体的新型城市安徽省淮北市。校园依山而建，风景秀丽，充满了浓厚的人文气息，幽雅的校园环境和现代化的生活设施为学生提供一个良好的学习和生活空间。学院依托淮北煤炭师范学院的综合办学优势，根据国家和地区经济社会发展的需要设置专业。学院现设有经济学、汉语言文学（师范）、英语、数学与应用数学（师范）、应用化学、电子信息科学与技术、应',148134);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳科技学院',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.syist.edu.cn/','沈阳科技学院，前身为沈阳化工大学科亚学院，始建于1999年，2016年经教育部批准升格为独立设置的普通本科高校。学校是一所培养化工、电子、信息、机械、材料、经管等方面高素质、应用型、技术技能型人才的全日制普通本科学校。现有在校生6100余人。经过多年的发展建设，形成以工学为主，理学、管理学、经济学、教育学五大学科协调发展的办学格局。学校以“科教兴国、服务社会”为己任，为地方经济建设和社会发展培养了万名合格人才。l优越的地理位置和办学条件学校坐落于沈阳市新行政、文化核心区域---浑南区国家大学科技',345990);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林师范大学博达学院',' ','吉林',0,4,0,0,0,' ','暂空',' ','http://www.bdxy.com.cn/','吉林师范大学博达学院（BodaCollegeofJilinNormalUniversity），是经教育部批准的具有普通高等本科学历教育资格的独立学院，是由具有50年丰富办学历史的吉林师范大学与实力雄厚的四平市铁东区热轧钢厂合办的一所育人特色鲜明的教学型大学。学院坐落于松辽平原美丽的英雄城—吉林省四平市。博达学院于1999年底开始筹建，2000年4月被吉林省教育委员会正式批准为吉林师范大学的二级学院，当年秋季首批新生入校。2004年3月被教育部批准确认为独立学院。现有在校生5800余人。在8年不断',450728);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春大学旅游学院',' ','吉林',0,0,0,0,0,' ','暂空',' ','http://www.cctourcollege.com/','长春大学旅游学院,创建于2000年,2004年经教育部确认为独立学院.学习期满成绩合格的,颁发由国家规定的普通高等学校独立学院毕业证书,符合学士学位授予条件的,颁发长春大学旅游学院学士学位证书.学院系中国旅游协会理事单位和中国旅游教育分会常务理事单位,吉林省旅游局和长春市旅游局行业岗位定点培训单位,吉林省人力资源和社会保障厅职业技能鉴定中心授权的国家职业技能鉴定基地.春华秋实,旅院人发奋图强,筚路蓝缕,玉汝于成.办学十四年来,谱写了一个又一个的精彩华章,积淀了独特的人文底蕴,培育了“天道德业体,',204525);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东科技大学泰山科技学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://tskjxy.sdust.edu.cn/','山东科技大学泰山科技学院是经教育部批准的本科层次的独立学院。学院坐落于著名的旅游胜地泰山脚下，校园环境幽雅，基本设施先进，办学条件齐备，是一所充满生机活力的花园式大学，是莘莘学子求学的理想之地。山东科技大学负责泰山科技学院的教学和管理，任课教师和管理干部全部由山东科技大学派出。山东科技大学现有在校生2.75万余名，其中博士、硕士研究生3700余名。有教职工2900余人，其中专任教师1900余人，教授257名，副教授411名。有中国科学院院士3名，中国工程院院士1名，俄罗斯自然科学院外籍院士5名，',426601);
