insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都师范大学科德学院',' ','北京',0,8,0,0,0,' ','暂空',' ','http://www.kdcnu.com/index.html  ','首都师范大学科德学院自2004年创办迄今，已走过十个春秋。今日科德，以其特有的风姿屹立于北京，秉承“科学致用德业立身”的校训，走“精品化、艺术化、国际化”的特色之路，已成为应用型艺术人才的培养摇篮。首都师范大学科德学院（学校代码13629）是经教育部批准实施本科层次学历教育的新型全日制普通高等学校（独立学院）。学校设有艺术设计学院、传媒学院、演艺学院、国际文化学院，在校生近5000人。共开设19个专业及专业方向，面向全国31个省、市、自治区招生。学校成立十年来，共向社会输送了8300多名毕业生，',1464003);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京工商大学嘉华学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.canvard.edu.cn/','北京工商大学嘉华学院创办于2004年，是经教育部批准成立的全日制本科普通高等学校，国家计划内招生，颁发国家承认的毕业证书和学士学位证书。学院依托国际化高端平台，借势北京优质教育资源，培养具有国际视野、专业素养和职业道德的商科人才，力求发展成为一所高水平的国际化商学院。学院设有财务与管理系、金融与贸易系、语言与传播系、国际部、基础部与德育中心6个教学单位，开设了会计学、国际商务、金融学、投资学、文化产业管理、视觉传达设计、审计学、金融工程8个本科专业，面向全国29个省市自治区招生，目前在校生500',1320987);
