insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉学院',' ','湖北',0,6,0,0,0,' ','暂空',' ','http://www.whxy.net/','中南财经政法大学武汉学院（简称“武汉学院”）是经教育部（教发函〔2003〕584号文件）批准成立的一所独立学院，实施全日制普通本、专科学历教育。举办方中南财经政法大学是教育部直属的一所以经济学、法学、管理学为主干学科的普通高等学校，是国家“211工程”重点建设高校之一。武汉学院地处湖北省武汉市洪山区雄楚大街636号，占地面积399.5亩，建筑面积16.3万余平方米。学院教学设施配套、生活设施齐全，现可容纳12000余名大学生在校学习生活，学院建有标准田径场、足球场等体育运动设施，有1.8万平方米',1382189);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙理工大学城南学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.csust.edu.cn/pub/zsw/','长沙理工大学城南学院由原长沙交通学院公路学院和长沙电力学院科技学院于2003年合并组建，是经湖南省人民政府批准成立、教育部确认，由长沙理工大学按新机制、新模式与社会力量合作举办的本科层次的独立学院。学院坐落在历史文化名城长沙市中心，在长沙理工大学原校址——金盆岭校区办学，占地面积近1000亩，临滔滔湘江水，眺葱葱桔子洲；校园芳草流青，秀木苍翠，理风文乐，激浊扬清；是湖南省文明高校和园林式单位。长沙理工大学城南学院注重自身师资队伍建设，同时共享长沙理工大学的师资条件，现有专任教师625人，其中高级',680796);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海外国语大学贤达经济人文学院',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.xdsisu.edu.cn','上海外国语大学贤达经济人文学院是上海市第一批经教育部批准成立的独立学院之一，由上海外国语大学主办，上海贤达投资有限公司合作举办。上海外国语大学负责具体指导学院的日常教学和管理，上海贤达投资公司负责投资和校园建设。上外贤达学院依托上海外国语大学在外语教研与管理方面的优势，培养以外语为专长，德、智、体全面发展、具有创新精神和实践能力、适应社会发展需要的高素质复合型专业人才为目标，力求进一步满足国家特别是区域经济建设和社会发展对应用型、外向型专门人才的需要，以及广大青年希望接受更高层次、更优质量教育的',1086887);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('温州商学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.wucc.cn/','温州大学城市学院是经国家教育部批准设立的独立学院，其前身是创办于1999年的原温州大学经济学院、信息科学与工程学院。经10年的建设和发展，学院已成为一所基础坚实、实力雄厚、特色鲜明的高等院校。天体物理学家、中科院院士孙义燧受聘担任学院名誉院长。学院位于温州高教园区，南接大罗山，北邻三垟湿地，占地近500亩，校园环境优雅，基本设施先进，办学条件优越。学院依托温州大学办学优势，根据现代科学技术发展趋势和地方经济社会发展需求设置专业。学院目前设有经济分院、国际贸易分院、管理分院、会计分院、电子信息分院',214391);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西大学行健文理学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://xingjian.gxu.edu.cn/','广西大学行健文理学院(院校代码：13638)是经国家教育部批准，由广西大学和广西希达教育开发有限公司按照新机制和新模式合作创办的全日制本科普通高校(独立学院)。学院位于广西首府南宁市，与广西大学毗邻，校园环境优美，设施完善，办学条件优良。2008年5月起成为中国独立学院协作会常务理事单位之一。2010年3月起连续两届成为中国独立学院协作会广西分会会长单位。学院依托国家“211工程”重点建设高校、教育部与广西共建高校广西大学丰富的教学资源、深厚的文化底蕴以及雄厚的学科基础和强大的综合办学优势办学，',477547);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西民族大学相思湖学院',' ','广西',0,9,0,0,0,' ','暂空',' ','http://xshxy.gxun.edu.cn','广西民族大学相思湖学院创建于2002年4月，原名为广西民族学院相思湖学院，2004年2月，经教育部批准，成为全日制本科普通高校(独立学院)，2006年3月，教育部同意广西民族学院相思湖学院更名为广西民族大学相思湖学院。现任董事长为广西民族大学党委常委、副校长伍先华编审。学院位于南宁市鹏飞北路1号，占地面积300亩，已建成的校园建筑面积13.84万平方米。教学基础设施完备，拥有多媒体教室76间、多媒体数字语言实验室15间、计算机公共实验室4间，专业实验室54间，校外实习实训基地68个，教学仪器设备',311946);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西师范大学漓江学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.gxljcollege.cn/','广西师范大学漓江学院(LijiangCollege，GuangxiNormalUniversity;简称：LJC)是经教育部和自治区人民政府批准，由广西师范大学联合社会力量举办的全日制普通本科高等学校。学校于2001年5月23日在广西师范大学育才校区创建，2003年10月被确认为全国首批独立学院后租借桂林陆军学院地方生学院实施办学，2007年10月整体搬迁至新落成的独立校园。学校地处历史文化名城、国际旅游名城桂林市，坐落在漓江之滨的雁山大学园区内，东向与广西师范大学雁山校区隔路相望，与桂林旅游学',383562);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西师范学院师园学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.gxsy.edu.cn','一、学院概况广西师范学院师园学院是经国家教育部批准设立，由广西师范学院和社会力量联合举办的一所普通全日制本科院校(独立学院)，具有学士学位授予权，纳入国家统一招生计划。学院坐落于广西壮族自治区首府南宁市，周边有风景秀美、环境优雅的良凤江国家森林公园和大王滩水库。校园规划占地面积22万平方米，规划总建筑面积为20万平方米。教育教学和生活设施完备，在校本科生9137人。学院被评为广西壮族自治区卫生学校、广西壮族自治区南宁市爱国卫生先进单位。二、学科专业建设学院依托广西师范学院的办学优势，以师范教育为',187268);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林电子科技大学信息科技学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://i.guet.edu.cn/','桂林电子科技大学信息科技学院(以下简称学校)创办于2001年，是经教育部批准设立的实施普通本科学历教育的高等院校。举办方桂林电子科技大学是全国四所电子科技大学之一，是工业和信息化部与广西壮族自治区共建高校，广西壮族自治区重点建设高校。我校坐落在历史文化名城、国际旅游胜地桂林，毗邻桂林电子科技大学花江校区。依托桂林电子科技大学的学科、专业优势以及优质的教育教学和管理资源，已逐步发展成为具有较高办学水平的普通本科院校。现有31个本科专业，面向全国25个省(市、自治区)招收全日制普通本科生，在校生近9',373366);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林理工大学博文管理学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.bwgl.cn/','桂林理工大学博文管理学院是经教育部批准、由桂林理工大学利用高校优质办学资源与社会力量合作创办、独立设置的全日制本科普通高校。学院依托桂林理工大学优势学科和良好的教育资源，秉承桂林理工大学的优良办学传统，教学管理科学严谨，校园文化充满活力。学院坚持“育人为本、质量立校、特色强校”的办学理念，坚持以教学为中心，以市场为导向，培养具有现代管理理念、创新创业精神和较强实践能力的高级应用型人才。学院现有建筑工程系、经济与管理系、信息科学系、设计系、外语系等五个系，2013年学院设置土木工程、建筑学、测绘工',286124);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京大学金陵学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.jlxy.nju.edu.cn/','长江之滨，龙王山下，在国家级南京高新技术产业开发区内，南京大学金陵学院正在崛起。这里铭刻“诚朴雄伟、励学敦行”的大学校训，这里坚守“嚼得菜根，做得大事”的精神家园。在这里，花香与书馨是不变的风景，特色与创新是永恒的追求。学校创办于1998年，坐落在南京大学浦口校区，占地1011亩，校舍31万平米，师生员工12000人。学校的举办方——百年名校南京大学，是直属教育部的重点综合性大学、国家首批“211工程”、“985工程”和“2011计划”建设高校，过去一个多世纪在人才培养、科学研究、社会服务和文化',760291);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵阳中医学院时珍学院',' ','贵州',0,3,0,0,0,' ','暂空',' ','http://58.42.245.173/web/shizheng/xueshengchu/Index.asp','贵阳中医学院时珍学院成立于2001年6月，是经国家教育部和贵州省人民政府批准设立的新机制本科独立学院，国标代码为：13647，面向全国招生。学院充分依托母体学院——贵阳中医学院的优质教学资源，着重培养具有创新实践能力的高素质中医药高级人才。在2005年教育部专项检查中，学院成为贵州省唯一获得良好评价的独立学院。学院坐落于贵阳市城郊，占地160余亩，规划占地300余亩。校园群山环抱、绿树成荫、环境幽静，是学子读书深造的绝佳学府。学院图书馆、实验室等教学设施齐全，拥有价值几千万元的教学仪器设备，宽带',213873);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州大学科技学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://cst.gzu.edu.cn','贵州大学科技学院是国家“211工程”重点建设大学——贵州大学与贵州捷星慧旅航空空乘咨询服务有限公司合作举办的综合性全日制本科独立学院。2001年5月经省人民政府批准成立，同年开始招生。学院现有文学、法学与公共管理学、工学、商学4个专业教学部和马列主义教学部、体育教研室2个公共教学单位，有汉语言文学、新闻学、英语(含外事管理、翻译方向)、法学、行政管理、公共事业管理(含民族文化产业管理方向)、工程管理、计算机科学与技术、电子信息科学与技术、电子信息工程(含民航机务维修方向)、通信工程(含3G通信方',622374);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州大学明德学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://mdc.gzu.edu.cn/','贵州大学明德学院是2001年6月经贵州省人民政府批准、贵州大学举办的综合性本科独立学院。明德学院位于贵阳市茶店，占地155.32亩，教学行政用房建筑面积90532平方米，图书馆建筑面积7050平方米，藏书24万余册，教学仪器总值1000余万元。校内教学大楼、实验室、图书馆、计算机中心、语音室、大学生活动中心、体育场、游泳池、学生公寓、食堂等教学与生活设施配套齐全。目前在校生3815人。学院依托母体贵州大学的学科基础，充分发挥机制灵活的特色，在专业设置上以地方社会、经济发展急需人才的应用型专业为主',489350);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州师范大学求是学院',' ','贵州',0,4,0,0,0,' ','暂空',' ','http://www.gznuqsxy.cn','学院创办于2001年6月，是国家教育部批准设立的、办学规模超过万人的全日制普通高校，位于拥有“避暑之都”和“森林之城”美誉的贵州省省会城市——贵阳市白云区，毗邻贵阳国家高新技术产业开发区、贵阳市综合保税区、中关村贵阳科技园核心区，具有得天独厚的地理优势。学院占地面积500余亩，拥有现代化教育教学设施;先后获全国先进独立学院、全国100强独立学院、贵州省最佳独立学院、区域高水平独立学院、贵州省“五好”基层党组织、贵州省“春晖行动”先进工作集体、贵州省优秀志愿服务组织、贵州省五四红旗团委等荣誉称号，',308263);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('遵义医学院医学与科技学院',' ','贵州',0,3,0,0,0,' ','暂空',' ','http://mts.zmc.edu.cn/','遵义医学院医学与科技学院坐落在历史文化名城遵义市，2001年5月经教育部批准创建，原名遵义医学院科技学院，当年9月正式招生。2004年底经教育部批准，更名为遵义医学院医学与科技学院。学院实行董事会领导下的院长负责制，是具备独立法人资格的全日制本科院校。学院秉承“明德笃学、践履求真”的院训，充分利用遵义医学院优质教育科研资源，以社会需求为导向，以教育教学质量为根本，致力于培养专业基础扎实、实践能力强的素质高、技术精的实用型人才。学院占地面积500余亩。教学楼、多媒体教室、实验室、图书馆、体育场馆等',231029);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京理工大学紫金学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://zj2.njust.edu.cn/','南京理工大学是国防科学技术工业委员会直属的全国首批重点大学、“211工程”重点建设院校。经过50余年的建设已发展成为以工为主，理、工、文、经、管、法、教等多学科综合配套、协调发展的理工科大学。南京理工大学紫金学院是由原国家兵器工业总公司和原江苏省教委于1999年批准成立的南京理工大学公有民办二级学院。2004年，经教育部批准，学院改制为独立学院。学院的办学性质为民办普通本科教育，由南京理工大学与社会力量联合举办。南京理工大学负责指导学院各专业教学计划的制定和实施，负责选派、推荐和审定各专业核心课',551255);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京航空航天大学金城学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://jc.nuaa.edu.cn/','南京航空航天大学金城学院是南京航空航天大学联合社会力量于1999年经原中国航空工业总公司和江苏省教育委员会批准成立的公有民办二级学院，2004年经教育部批准，学院改制为相对独立的二级学院（简称独立学院）。学院所依托的南京航空航天大学是一所具有航空、航天、民航特色，以工为主，工、管、理、经、文、法、艺等多学科协调发展，国家“211工程”建设和建有研究生院的全国重点大学。南京航空航天大学对金城学院的教学质量负责，对学院的发展规划、学科专业建设、师资队伍建设、学生教育管理进行指导和帮助。学院座落于南京',711970);
