insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州大学松田学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://sontan.net/enrollmentOnline.do','广州大学松田学院始建于2000年9月，由广州大学和增城市松田实业有限公司合作创建。2004年4月，经教育部批准为独立学院，实现了由专科向本科教育的跨越。2005年1月，学院顺利通过了教育部专项检查。2011年，学院及15个申报专业一次性通过了广东省学位委员会组织的学士学位授予权评估。学院为全日制普通本科院校，面向全国统一招生，学制四年，学生成绩合格者由学院颁发“广州大学松田学院”具印的、教育部统一电子注册的本科毕业证书，符合学院学士学位授予规定者，授予相应学士学位。学院座落在美丽富饶的荔枝之乡—',938418);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津外国语大学滨海外事学院',' ','天津',0,5,0,0,0,' ','暂空',' ','http://bhws.tjfsu.edu.cn/','天津外国语大学滨海外事学院简介天津外国语大学滨海外事学院于2004年经教育部批准设立，是天津外国语大学按照新的机制和模式举办的全日制普通本科高校，实行董事会领导下的院长负责制。学院坐落于天津市滨海新区，地处大港生态高教园内，文化气息浓厚，环境优美，交通便利。学院是一所以外国语言文学为优势学科，文学、经济学、管理学、法学等多学科协调发展、以培养应用型、复合型和涉外型人才为特色的高等院校。学院秉承天津外国语大学“中外求索德业竞进”的校训，坚持“以人为本”的办学理念，按照“以特色发展带动内涵发展促进和',422063);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津体育学院运动与文化艺术学院',' ','天津',0,7,0,0,0,' ','暂空',' ','http://www.tjtwy.cn/','天津体育学院运动与文化艺术学院是由天津体育学院与北京现代音乐研修学院按照新型办学模式和运行机制举办的全日制本科层次独立学院，是以培养我国文化产业急需的艺术及管理人才为主要办学定位的高等艺术学府。天津体育学院成立于1958年，是我国较早建立的专业体育学院之一，新世纪以来又不断拓展办学的专业领域，在国内率先培养体育和艺术相融合的复合型人才，并在国内较早获批举办独立学院。北京现代音乐研修学院具有16年的艺术教育办学积累，形成了面向社会发展和市场需求的教育理念和办学体制，拥有优秀师资队伍和先进的教学设施',107997);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津商业大学宝德学院',' ','天津',0,6,0,0,0,' ','暂空',' ','http://www.boustead.edu.cn/','天津商业大学宝德学院是2004年经国家教育部批准，由天津商业大学和天津摩根教育管理咨询有限公司合作举办的本科层次的独立学院，学制四年。学生学习期满成绩合格的颁发本科毕业证书，符合学士学位授予条件的颁发学士学位证书。学院是以管理学、经济学和艺术学为主，兼有工学、文学的多科性独立学院。目前，设有国际工商管理系、涉外经济贸易系、外国语言文学系、计算机与信息技术系和艺术设计系，共有工商管理、会计学、财务管理、市场营销、物流管理、人力资源管理、国际经济与贸易、金融学、旅游管理、劳动与社会保障、英语、日语、',527460);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津医科大学临床医学院',' ','天津',0,3,0,0,0,' ','暂空',' ','http://www.tijmu.edu.cn/lcyxy/','天津医科大学临床医学院是2004年经国家教育部确认的全国首批以新的机制和模式举办的独立学院（教发函[2004]22号文件《教育部关于对天津市普通高校举办的独立学院予以确认的通知》）。学院依托天津医科大学优质教育资源，建院以来始终坚持“以质量求生存、以特色谋发展”的办学思路,紧紧围绕培养德智体美全面发展的应用型高级专门人才的目标，努力为社会培养高素质医学专门人才。天津医科大学临床医学院坐落在天津市滨海新区大港高校园区，学院占地面积22.3万平方米，建筑面积12.6万平方米，设有图书馆、教学楼、实验',905366);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北师范大学人文学院',' ','吉林',0,0,0,0,0,' ','暂空',' ','http://www.chsnenu.edu.cn','东北师范大学人文学院是经教育部批准，由东北师范大学按新的机制和模式申办并实施普通高等本科教育的独立学院。学院按国家下达的招生计划，参加全国统一招生。符合毕业条件者，颁发教育部统一印制的东北师范大学人文学院本科毕业证书，实行电子注册。符合学士学位授予条件的，授予学士学位。学院坐落在景色宜人的北国春城即吉林省长春市净月经济开发区。分东西两个校区，与东北师范大学净月校区相邻，可以共享优质的教育资源与浓厚的学习氛围，是青年学子接受高等教育的理想学校。学院的最大优势是办学起点高，办学实力强，师资力量雄厚。',757895);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南开大学滨海学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://binhai.nankai.edu.cn','南开大学滨海学院概况南开大学滨海学院是2004年经教育部批准设立，由南开大学和天津市滨海新区(原大港区政府)按照新的模式和机制合作创办的全日制普通本科高校，坐落于天津市滨海新区大港高教生态园内。学院为股份制合作办学实体，具有独立的校园和基本办学设施，实施独立的教学组织与管理，具有独立法人资格，实行董事会领导下的院长负责制。学院拥有千亩校园，万人规模，总建筑面积22万余平方米，在校生10000余人。学院面向全国招生，学生毕业后颁发国家统一印制的南开大学滨海学院毕业证书，符合学位授予条件的授予南开大',2305388);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('电子科技大学成都学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.cduestc.cn/','电子科技大学成都学院是国家教育部批准成立的独立学院(教发函[2004]21号)，是由电子科技大学与成都国腾实业集团合作创办，是采用新模式新机制举办的以本科层次为主的普通高等学校。学院创建于2001年，坐落在享有“天府之国”美誉的成都，位于国家级高新技术产业开发区——成都市高新西区，现有本、专科学生17000余名，占地1100亩。学院现设有系(分院)10个，本、专科专业40余个，是国家国际软件人才培训基地、国家软件产业基地人才培训中心。指导思想坚持教育以育人为本，以学生为主体;办学以人才为本，以教',2410106);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉理工大学华夏学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.1957.cn','武汉理工大学华夏学院是经国家教育部批准，由教育部直属重点大学，国家“211工程”重点建设高校武汉理工大学与实力雄厚，管理规范的上市公司武汉人福高科技产业股份有限公司联合创办，采用新机制运行的独立学院。学院以普通全日制本科教育为主，纳入国家普通高等学校招生计划，面向全国招生。武汉理工大学华夏学院现已搬迁。新校区位于武汉东湖新技术开发区关山大道489号，目前用地面积为360亩。新校区与中南民族大学、中南财经政法大学、武汉科技学院等院校毗邻，与武汉大学、华中科技大学、武汉理工大学等多所高校的科技园一路',1005166);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都理工大学工程技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.cdutetc.cn','成都理工大学工程技术学院是由中国核工业西南物理研究院与成都理工大学在“产、学、研”全面合作的基础上强强联合、共同创办。2000年经四川省教育厅批准为全日制普通高等学校（二级学院），2004年2月经国家教育部确认为独立学院（公办全国有），属全国有事业法人单位，并在2004年获“中国一流高等独立学院”荣誉称号。2004年教育部专家组在对全国独立学院的办学条件、教学质量的全面考察评估中，我院被教育部评为本科独立院校教学水平评估优秀高校。2006年4月，我院作为四川独立学院代表接受了教育部专家组对学生管',841698);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都信息工程大学银杏酒店管理学院',' ','四川',0,6,0,0,0,' ','暂空',' ','http://www.chcedu.cn','成都信息工程学院是我国西南地区普通高校中唯一一所拥有50年历史的独立设置的信息工程学院。成都信息工程学院银杏酒店管理学院是中华人民共和国教育部按照“优先支持办学质量高，办学条件好的普通高校试办独立学院”规定审核批准，具有成都信息工程学院良好的软硬件基础与闻名川内外的成都银杏企业的产业优势，专业从事培养服务型企业和组织经营管理人才的成都信息工程学院的本科独立学院。',298467);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青海大学昆仑学院',' ','青海',0,0,0,0,0,' ','暂空',' ','http://klc.qhu.edu.cn','青海大学昆仑学院简介（2014年4月）青海大学昆仑学院是2004年经国家教育部批准设立，由青海大学按新机制和模式举办的一所独立学院，也是青海省唯一一所普通本科独立学院。学院位于青海省西宁市生物科技产业园，占地面积17.3万m2，建筑面积7.8万m2。图书馆拥有各类图书和电子图书27.8万余册。有专任教师236名，其中教授、副教授133名，具有博士、硕士学位教师104名。学院秉承青海大学优良的办学传统和教育理念，坚持“以人为本、德育为先、能力为重、全面发展”的办学理念，坚持“依托青大、规范办学、注',400861);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京理工大学珠海学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://zhaosheng.zhbit.com/','北京理工大学珠海学院是经中华人民共和国教育部批准，于2004年5月8日正式成立的普通高等学校。依赤花山脉纵横延伸330万平方米的珠海学院座落在美丽的珠海市唐家湾，京珠高速公路、广珠城际铁路和广东西部沿海高速公路从学校的东北两侧通过，交通便利。校园内20万平方米的人工湖波光涟漪，自然绿化和人工绿化环境达校区总面积80%以上，松林、相思林、荔枝林、竹林成片分布，浅潭碧水点缀其中，宁静、典雅，是读书求学的理想场所。学校设有信息科学技术学院、计算机科学技术学院、机械与车辆工程学院、化工与材料学院、管理与',1929888);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安交通大学城市学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xjtucc.cn/','西安交通大学城市学院是由西安交通大学和西安交大博通资讯股份有限公司共同举办并经国家教育部2004年5月批准设立的全日制本科层次的独立学院。党委书记、院长是西安交通大学博士生导师陈光德教授。学院依托西安交通大学百年名校雄厚的师资力量、规范而严格的教学管理优势，根据现代科学技术发展趋势和地方经济社会发展需求设置专业。任课教师和管理干部主要从西安交通大学选聘，确保教学质量和办学方向。学院现设有电气与信息工程、计算机、经济、管理、机械工程、外语、艺术设计、护理等8个系、4个基础课教研室、25个本科专业。',1054725);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北大学现代学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xdxd.cn/','西北大学创建于1902年，其前身陕西大学堂是由清朝光绪皇帝亲自谕批设立的中国最早的几所现代大学之一。在此基础上，几经沧桑离合发展成早期的西北大学。抗战年间，西北大学又合并了自北京西迁的原北平大学的一部分，组成了西北联合大学。其时，与由清华、北大、南开等组成的西南联合大学齐名。此后，又相继从西北大学分化并形成了西北地区的农林、政法、医学、师范、民族、工业、财贸、经济、银行、会计等多所专门大学，而西北大学本身仍继续发展，至今已成为文、史、哲、经、管、法、理、工、外语等学科门类齐全、教学与科研并重、校',1483245);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安建筑科技大学华清学院',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xauat-hqc.com','西安建筑科技大学华清学院是经国家教育部批准，由西安建筑科技大学联合社会力量以新机制、新模式于2004年创办的全日制本科院校。学院坐落在历史文化名城西安，位于南二环东端，西安高新技术东开发区幸福林带南段，南眺驰名中外的唐代大雁塔，北临举世闻名的明代长安城墙，东依景色怡人的浐灞生态园。学院占地面积六百余亩，距西安建筑科技大学雁塔校区三公里，交通便利，环境优雅、风格别具。学院建有高标准的教学楼、公寓楼、图书馆、餐饮服务中心、大学生活动中心、计算中心、室内外运动场、多媒体教室及实训基地。其中，各类实验室',1313757);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安财经学院行知学院',' ','陕西',1,6,0,0,0,' ','暂空',' ','http://www.xcxz.com.cn','青岛理工大学是一所以工为主，理工结合，土木建筑、机械制造、环境能源学科特色鲜明，理、工、经、管、文、法、艺多学科协调发展，科学教育与人文教育相结合的多科性大学。学校前身是1952年12月创建的“山东省青岛建筑工程学校”。1953年6月由山东省划归中央人民政府重工业部领导。1960年6月升格为“山东冶金学院”，开始招收本科生。此后，学校隶属关系几经更迭，办学层次几起几落。1978年更名为“山东冶金工业学院”，恢复本科招生，隶属冶金工业部。1985年9月更名为“青岛建筑工程学院”。1993年被国务院',1176832);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西科技大学镐京学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.kdhj-edu.net/','陕西科技大学镐京学院是全国重点大学——陕西科技大学按照新机制、新模式与陕西斯卡雷•博德制衣有限公司合作举办的本科层次的独立学院。学院成立于2004年，是陕西省首批成立的独立学院。学生毕业时获得国家承认的独立学院本科毕业证书，可授予独立学院具名的中华人民共和国学士学位证书。西咸零距离，镐京学院得天独厚镐京学院占地600余亩，位于西安沣渭新区；古都西安，世代传承、雍容儒雅，满腹经纶，博学智慧，大气恢弘，高校林立，历史文化底蕴深厚，现代科技日新月异。学院距西安市中心10公里，飞机场20公里，交通便捷高',774582);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安工业大学北方信息工程学院',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://zs.bxait.cn','西安工业大学北方信息工程学院是由西安工业大学借助自己的行业、学科、地缘、管理等优势，面向陕西支柱产业国防、制造业需求，联合北方光电科技集团有限公司（原西安北方光电有限公司）和中国兵器工业西北管理局，经教育部2004年5月批准设立的普通高等学校独立学院（教发函[2004]72号）。学院继承和发扬了西安工业大学优秀的办学理念和文化管理思想，初步确立了：“坚持面向社会，服务于中小型企业，培养具有创新精神和实践能力、特色鲜明、能够适应市场竞争的本科应用型人才”的办学定位，经过不断摸索与实践，渐次形成了“',562295);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('延安大学西安创新学院',' ','陕西',0,0,0,0,0,' ','暂空',' ','http://www.xacxxy.com/','延安大学是由毛泽东同志亲自命名、中国共产党创办的第一所综合大学，是教育部和陕西省人民政府共建院校，陕西省省属重点大学。延安大学西安创新学院是2004年5月经教育部批准成立的一所全日制本科独立学院。学院以延安大学的优势学科为基础，以延安大学人才资源和良好的办学声誉为依托，致力于培养适应经济社会发展需要的高素质应用型人才。学院按国家计划统一招生，并按国家规定颁发经教育部电子注册的本科毕业证书，符合学位授予条件者，颁发相关学科学士学位证书。延安大学西安创新学院坐落在古城西安大学城，占地384亩，校园内',894759);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林大学珠海学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://www.jluzh.com/','吉林大学珠海校区创建于2002年元月，占地5,000亩，现由吉林大学珠海学院、吉林大学研究生院珠海分院和吉林大学南方研究院组成。吉林大学南方研究院是吉林大学在珠三角地区建立的高层次人才培养基地和科学研究基地，自2007年开始招收硕士和博士研究生。吉林大学珠海学院是根据教育部教发[2003]8号文件《关于规范并加强普通高校以新的机制和模式试办独立学院管理的若干意见》，由吉林大学申请，经教育部批准在吉林大学珠海校区建立的独立学院。学院学科涵盖经济学、文学、理学、工学、医学、管理学等六大学科门类，目前',2231847);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安电子科技大学长安学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xdca.com.cn/','西安电子科技大学长安学院是经国家教育部（教发函[2004]156号）批准成立，实行新型办学模式的全日制普通本科独立学院，学院具有独立法人资格。学院位于西安高新开发区新型工业园区，东邻西安大学城，南依西安电子科技大学新校区，教学设施齐全，校园环境优美，交通和通信非常便利。学院的教学管理、师资队伍、学生管理、行政管理等工作全部由西安电子科技大学负责组织与实施，教师队伍70%以上具有副教授以上高级职称，教学管理按照教育部本科教学质量评估标准的要求进行，确保教学质量、教学水平完全符合普通本科IT行业的专',578880);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华中师范大学武汉传媒学院',' ','湖北',0,8,0,0,0,' ','暂空',' ','http://www.whmc.edu.cn/\n','华中师范大学武汉传媒学院是经国家教育部批准设立的全日制普通本科高等学校，是中南地区唯一的传媒类本科高校。学校发扬华中师范大学百年名校的优良教育传统，凝炼践行人本、博雅、创新的教育理念，切实加强自身建设，已发展成为具有鲜明传媒文化与科技教育特色的多学科协调发展的综合性高等学校。学校座落于武汉市江夏区藏龙岛科技园区，东临凤凰台，西傍汤逊湖，首期占地320亩，二期规划占地500亩，现有校舍建筑面积20余万平方米，建有与现代传媒教育相适应的系列演播厅、观片室、录音棚、非线性编辑中心、动画与数字技术实验中',323271);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国传媒大学南广学院',' ','江苏',0,8,0,0,0,' ','暂空',' ','http://www.cucn.edu.cn/','中国传媒大学南广学院是中国传媒大学为拓展品牌优势，充分利用优质教育资源，满足国家对应用型信息传播人才日益增长的需求，与南京美亚教育投资有限公司合作，经教育部批准于2004年创办的四年制、本科层次的独立学院。国内最具规模的跨国影视娱乐传媒集团之一——华夏视听环球传媒集团总裁蒲树林担任南京美亚教育投资有限公司董事长及南广学院董事会董事长。中国传媒大学名誉校长刘继南兼任南广学院名誉校长。学校坐落在风景秀丽的国家地质公园——江苏南京方山风景区脚下，规划用地1660亩，一期校园规划建筑面积36万平方米，已',1693406);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江邮电职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.zptc.cn/','浙江邮电职业技术学院是一所省属公办全日制普通高等职业院校，省级“文明单位”，前身为创建于1958年的浙江省邮电学校，是一所具有五十年办学历史的职业院校。学院座落于历史文化名城鲁迅故乡——绍兴，学院杭州校区为学生综合实训基地和成人教育培训中心。学院是首批省级高技能人才培训基地（电子信息项目），也是全省高校中唯一拥有邮电通信特有工种职业技能鉴定站的学府，在通信领域发挥深远的影响，毕业生和培训学员遍布全省各大通信运营企业。学院坚持“以素质为核心，以技能为本位”的办学理念，注重学生综合职业能力的培养。浙',38235);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北司法警官职业学院',' ','河北',0,11,0,0,0,' ','暂空',' ','http://www.jjgxy.com.cn/','河北司法警官职业学院是经河北省人民政府批准、教育部备案的国办全日制警察、法律类普通高等学校，由原河北司法学校、河北警官学校和河北政法函授大学合并组建，隶属于河北省司法厅，主要为政法系统和社会各界培养司法警察和各类实用型法律职业人才。学院本部坐落在国家历史文化名城--邯郸市，省会设有石家庄校区（原河北司法学校校址），校园面积1000余亩，建筑面积93740多平方米，固定资产总额8415万元。学院环境幽雅，教学、生活设施完善，设备齐全，有独具特色的射击馆、摔跤馆、刑事侦查实验室、模拟法庭、多媒体教室',139150);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西工商学院',' ','山西',1,6,0,0,0,' ','暂空',' ','http://www.sxtbu.net','山西工商学院是国家教育部批准建立的全日制普通本科高等院校，迄今已经有28年的办学历史。学院现占地722亩，在校生12317人，设有商学院、会计学院、金融学院等10个二级学院和4个教学部，开设工商企业管理、财务管理、国际金融等34个专科专业和19个本科专业，建有9个实验实训中心、86个多专业仿真模拟实训室、3个多功能演播厅,校内生产性实训基地2个，校外实习实训基地65个,图书馆藏书100万册。学院现有专任教师665人，其中具有高级专业技术职称的教师238人，具有硕士以上学位的教师414人。学院在全',158855);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西体育职业学院',' ','山西',0,7,0,0,0,' ','暂空',' ','http://www.sxptc.com','山西体育职业学院是山西省一所体育类大专院校，校址在太原市高新技术开发区以南，占地面积300亩。学院前身为山西省体育运动学校，省部级重点中专。学校始建于1956年，“文革”期间停办，1977年恢复办学，2000年迁入现校址，2004年4月升格为山西体育职业学院。目前，我院作为培养竞技体育人才和社会体育人才的基地，形成集教学、训练为一体的具有鲜明体育特色的，具有附中、附小、中专、大专、本科一条龙完整的办学体系。学院以培养竞技体育人才和社会体育职业人才为目标，办学指导思想定位于：以市场为导向，以就业为',37547);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西警官职业学院',' ','山西',0,11,0,0,0,' ','暂空',' ',' http://www.sxpolice.cn ','山西警官职业学院是一所实行警务化管理的政法类高等职业学院，其前身是创建与1964年的山西省公安技校，是山西省培养人民警察及社会各方面实用型法律人才的重要基地。目前，主要开设的法律类高职专业有：刑事执行、法律事务、司法警务等。此外，学院还保留有中专层次的司法警察、警务特训与服务、计算机及用、法律等专业。从1964年建校至今，学院先后经历了技校、中专、大专学历教育和在职民警培训等几个发展阶段，为山西省政法战线输送了大批专门人才，成为山西省的知名院校。经过40余年的不懈努力，学校面貌发生了令人瞩目的巨',102646);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西国际商务职业学院',' ','山西',0,6,0,0,0,' ','暂空',' ','http://zjb.sxibs.com/','我院紧密围绕当前我国高等职业教育的山西国际商务职业学院建校于中国改革开放之初的1985年，是经山西省人民政府批准、国家教育部备案的一所国立公办全日制普通高校，隶属于山西省商务厅，副厅级建制，面向全国招生。学院先后获得“山西省和谐文明单位”“太原市园林绿化先进单位”“山西省劳动竞赛集体一等功单位”等荣誉。学院经过近三十年的发展，正在向着建设特色鲜明的国际贸易人才、商务英语人才、工商管理人才、会展营销人才、信息技术人才的强院迈进。学院设报关与国际货运、国际商务、国际贸易实务、会计(涉外会计方向)、商',96172);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潞安职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://sxlazy.cen114.com ','潞安职业技术学院座落在环境优美、风光旖旎、古迹众多的山西省长治市，全国煤炭工业著名企业——潞安矿业集团公司机关所在地。长太高速直贯南北，北接太旧高速，南连晋焦高速；长邯高速横穿东西，铁路交通四通八达，离长治飞机场仅三十公里，交通十分便利。学院的前身为潞安矿务局职工大学，创建于1975年。学院以全日制普通高等职业教育为主，以培养中高等技术应用人才为目标，在潞安集团的大力支持下，现已发展成集潞安职业技术学院、太原理工大学函授站、山西省委党校、东北财经大学和中国矿业大学北京校区在职研究生培训点为一体的',59541);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原旅游职业学院',' ','山西',0,5,0,0,0,' ','暂空',' ','http://www.tylyzyxy.com/','太原旅游职业学院是教育部备案的公办全日制普通高校，是山西省创办最早、规模最大的一所旅游专业院校，是国家旅游局旅游院校协会13所创始会员学校之一。近年来，学院荣获“山西省劳动模范单位”、“山西省职教先进单位”、“山西省文明和谐单位”、“全国三八红旗集体”等多项荣誉。学院占地404.1亩，总建筑面积12余万平方米，校园环境优美，设施设备一流，学院内的旅游专业实训中心为国家级实训示范基地，内有国内领先的三维电子导游仿真实训室、数字化旅游规划实训室、中外语同声传译间等先进实训设备及涉及饭店、旅行社、景区',93831);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西旅游职业学院',' ','山西',0,5,0,0,0,' ','暂空',' ','http://www.sxtvi.edu.cn','山西旅游职业学院是经山西省人民政府批准、教育部备案的一所专科层次的全日制公办高等院校，也是山西省第一所独立设置的旅游高等职业学院。省发展和改革委员会为主管部门，省教育厅为教学业务管理部门，省旅游局为旅游业务指导部门。学院位于省城太原高校云集的学府区，有着浓厚的文化氛围。校园景色优美，环境宜人，是读书学习、提高技能、修身养性的理想之地，被太原市人民政府命名为园林化学校。学院现有教职工306人，其中教授3人、副高职称68人，中级职称99人，教师队伍中具有博士、硕士研究生学位的占教师总数62.9%。学',139260);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西管理职业学院',' ','山西',0,6,0,0,0,' ','暂空',' ','http://www.sxglzyxy.com.cn/','山西管理职业学院是经山西省人民政府批准、国家教育部备案,于二○○三年十一月由山西省行政管理学校改建而成的一所全日制普通高等专科学校。学院由山西省人事厅主管、主办，位于临汾市尧都区河西，占地面积280亩，建筑面积3.5万平方米，交通便利，环境幽雅，花园式校园绿树成荫、花团锦簇，具有现代化的教学设施、设备，雄厚的资师力量和完善的管理体系。学院建有气势宏伟的教学大楼、现代化的图书馆和宽敞明亮的学生公寓、餐厅、礼堂等，设有微机房、语音训练中心、多媒体教室、校园闭路电教系统、校园网络、专业模拟室，是省教育',39266);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('乌兰察布职业学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.wlcbzyxy.com.cn','乌兰察布职业学院是经自治区人民政府批准、国家教育部正式备案的公办全日制普通高等院校。学院位于晋、冀、蒙三省交汇处—集宁。区位优势位居自治区第一。新校区处在自治区级工业园区——察哈尔工业园区，总占地面积882亩，总建筑面积170869平方米；教学行政用房总面积87489平方米，实习实训及辅助用房23950平方米，学生宿舍66388平方米，体育场馆8031平方米，多媒体教室座位数4600个，语音室座位数108个。以“节能、环保、生态、人文”为理念，构筑了自治区最具魅力的高职院校，获国家第三届百年建筑',77798);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津冶金职业技术学院',' ','天津',0,1,0,0,0,' ','暂空',' ','http://www.tjmvti.cn','天津冶金职业技术学院是经天津市政府批准，国家教育部备案的独立设置的市属全日制普通高等院校。学院地处北辰区（原天津大学冶金分校校址），占地242亩，建筑面积12.97万平方米。教学设备资产值达5194万元。校园环境幽雅，教学设施齐备先进，住宿条件干净舒适，体育场馆设施齐全，现有各类在校生7000余人。学院设冶金工程系、电气工程系、机械工程系、电子信息工程系、经济管理工程系、基础课部、体育部等七个教学系（部）。我院治学严谨、校风优良，以培养面向生产、建设、服务和管理第一线需要的高技能人才为教育目标，',111020);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津石油职业技术学院',' ','天津',0,1,0,0,0,' ','暂空',' ','http://www.tjsyxy.com','天津石油职业技术学院是经天津市人民政府批准、国家教育部备案的全日制公办普通高等职业技术学院。学院毗邻景色宜人的团泊新城。著名诗人郭小川曾在这里写下了著名的诗篇《团泊洼的秋天》。学院占地1447亩，建筑面积约25万平方米，固定资产原值13138万元。学院教学、生活设施完备，设备先进齐全，图书馆藏书43.5万册，建有多个电子阅览室。学院设有石油工程技术、钻井技术、油气开采技术、石油化工技术等17个高职专业，目前在校生规模4500余人。学院师资力量雄厚，教师队伍年龄、学历、职称结构合理，职业教育经验丰',226858);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津城市职业学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjcsxy.cn/','天津城市职业学院前身是天津市河北区职工大学，始创于1958年8月，2002年学校转制成为全日制高职学院，成为全国首家多功能、社区性高职学院。学校曾被原国家教委评为\"成人高等教育先进学校\"、\"全国成人高等教育评估优秀学校\"。被天津市政府授予\"天津市职业教育先进单位\"光荣称号，教育部职业教育专家组来我院考察，对我院的办学特色和职业教育改革给予充分肯定。50年的时间学校历尽沧桑而逐步走入辉煌，从一所享誉全国的成人高校转制为综合性高职院校，由单一的成人教育发展为全国首家集职业教育、继续教育、社区教育为一',177356);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京科技职业学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.5aaa.com','北京科技职业学院是经北京市人民政府批准、国家教育部备案，具有独立颁发国家承认学历文凭资格的全日制综合性民办普通高等职业院校。学校创建于1997年，建校十二年来，北京科技职业学院秉承校党委书记周继庭同志所倡导的“育人报国”宗旨，实现了超常规、跨越式发展，取得了学校建设的巨大成就。形成了占地面积达2875亩的林木叠翠、鸟语花香、依山傍水、亭塔相间的昌平沙河校区和八达岭校区。现有教育教学用房总建筑面积70余万平方米，各类校内教学实验室、实训室92个，校外实训、实习基地106个，计算机终端6987个；教',622585);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都医学院',' ','四川',1,3,0,0,0,' ','暂空',' ','http://www.cmc.edu.cn/','成都医学院创建于1947年解放战争的炮火硝烟之中,初期为豫皖苏军区卫生学校,1950年改编为18军卫生学校，1962年改编为西藏军区卫生学校，1974年扩编为成都军区军医学校（现址），1993年更名为中国人民解放军成都医学高等专科学校。1999年5月归并第三军医大学，更名为第三军医大学成都军医学院。2004年8月,经国务院、中央军委批准，学院整体移交四川省并更名为成都医学院，为省属高等医学本科院校。学院以本科教育为主，面向全国招生，为社会培养各类高素质、实用型医药卫生人才。学院地处成都市北郊，毗',1823936);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林医药学院',' ','吉林',1,3,0,0,0,' ','暂空',' ','http://www.jlmpc.cn/','吉林医药学院原为中国人民解放军第四军医大学吉林军医学院。学校创建于1952年，曾是一所具有光辉历史和优良传统的军队医学院校。2004年8月，经国务院、中央军委批准，学校由军队整体移交吉林省，纳入国家公办普通高校序列，面向全国招生，是吉林省唯一独立设置的西医本科院校。学校坐落在中国魅力城市、北国雾凇之都—吉林省吉林市，是国家绿化模范单位。学校占地面积52.6万平方米，建筑面积26.6万平方米，固定资产总值7.9亿元。学校拥有一流的教学设施，有2.4万平方米的图书馆及5万平方米的实验大楼，所有教室均',1047508);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东文艺职业学院',' ','广东',0,8,0,0,0,' ','暂空',' ','http://www.gdla.edu.cn','广东文艺职业学院是一所有着光荣历史传统和深厚文化积淀的综合性高等职业艺术学院。始建于解放初期的“华南人民文学艺术学院”是我院的最初校名。当时，南方各省刚解放不久，各方面的干部都很缺乏。在叶剑英同志的亲自主持下，中共中央华南分局决定创立中南党校、南方军政大学、华南人民文学艺术学院三所学校，以尽快培养党、政、军和文化等各方面干部。并任命华南分局文教厅副厅长、著名作家欧阳山担任华南人民文学艺术学院院长，校址设在广州市光孝寺内。我省著名的文艺家杜埃、关山月、黄新波，陈残云、华嘉等同志都在我院任教。招收三',161263);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州体育职业技术学院',' ','广东',0,7,0,0,0,' ','暂空',' ','http://www.gztzy.edu.cn','广州体育职业技术学院是经省政府批准、国家教育部备案、广州市人民政府举办的一所以培养优秀体育人才为宗旨的普通高等学校，其前身广州市伟伦体育运动学校是国家高水平后备人才训练基地和国家级重点中专学校，同时，也是国家游泳队、击剑队、举重队、跳水队、羽毛球队训练基地。学院培养了大批优秀的体育竞技人才，如奥运冠军杨景辉、杨维、张洁雯，世界冠军关渭贞、林燕芬、吴迪西、江剑芬、余锦豪、林志刚、刘秀华、邓伶、董兆致、吴汉雄等世界体育明星，为国家、省输送1000多名优秀的竞技体育后备人才，培养了2200多名中、小学',114519);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州工程技术职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gzvtc.cn/','广州工程技术职业学院是经广东省人民政府批准、国家教育部备案的广州市属普通高校，创办于1964年。学院以先进的教育理念、独特的办学思路、优秀的师资队伍、优雅的校园环境、鲜明的高职特色，致力于培养具有现代竞争意识和综合职业能力、素质全面，能适应二十一世纪经济发展需要的实用型技术人才。作为一所有着四十多年历史的公办高校，近年来，在不断的改革、超越和发展中打下了稳固的基础，积累了丰富的经验，逐步形成了知识、品格、才艺、技能四元并重的办学风格。以独特的思路和新颖的做法应对竞争，谋求突破，实现了跨越式发展，',266437);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中山火炬职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.zstp.cn/','“国家示范性高等职业院校建设计划”骨干高职院校中山火炬职业技术学院是经广东省人民政府批准、国家教育部核准备案、中山市人民政府管理的公办全日制普通高等院校。拥有校舍10.9万平方米．有专职教师145人，其中教授12人、副教授37人、博士8人，硕士42人，建有实习、实训基地80余个，完善的办学设施为学院的教学、生产实习和技能培训提供了可靠的保证。中山火炬职业技术学院专业设置符合市场需求，目前设置有16个专业：包装技术与设计、印刷技术、印刷图文信息处理、产品造型设计、视觉传达艺术设计、计算机应用技术、',165492);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江门职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.jmpt.cn','江门职业技术学院是2004年3月17日经广东省人民政府批准、国家教育部正式备案的公办全日制普通高等学校。学院是一所集文、理、工、管、艺术类专业于一体的多学科综合性院校，设有工业制造与技术系、材料技术系、信息技术系、经济管理系、教育与教育技术系、外语系、艺术与设计系和中专教育部等8个系部，涉及模具设计与制造技术、汽车检测与维修技术、机电一体化技术、计算机应用技术、电脑艺术设计、商业动漫设计、电脑游戏开发、服装艺术与制作、中英文秘书、英语教育、物流管理、旅游管理、舞蹈表演、幼儿教育等49个专业方向。',298722);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('茂名职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://websites.mmvtc.cn:808/zsw/','茂名职业技术学院是2004年经广东省教育厅批准成立的茂名市第一所全日制公办高等职业技术院校，前身是创建于1986年的国家重点中专学校“茂名市建设中等专业学校”。学院秉持“修德强技求实创新”校训，确立“知识+技能+特长”三位一体人才培养目标，树立“职业决定未来，技术创造辉煌”和“用脑指挥手”的高职教育观，注重培养学生的综合素质和职业能力、创新精神和创业能力，以培养生产、服务、管理第一线的高级技术技能人才为己任，大力推进“教、学、做”一体化教学模式改革，先后为社会培养了3万余名毕业生。学院现有全日制',234266);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('珠海城市职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','oldname": "珠海城市职业技术学院','珠海城市职业技术学院是我市唯一一所由珠海市人民政府主办的全日制普通高等院校。2004年4月经省人民政府批准成立，并报国家教育部备案。学院现有教职员工300余人。在专任教师中，硕士研究生以上学历的占33.5%，副高以上职称的占28.5%。学院现有全日制高职高专学生3000余人，本、专科成人学历教育学生7000余人。目前，学院已形成全日制高职教育为主体、成人教育与职业培训并存的办学格局。设有工、文、管、经、艺术、教育类29个专业，根据珠海经济与社会发展的需要，学院还将开设食品营养与安全、应用化工、投',323693);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州涉外经济职业技术学院',' ','广东',0,6,0,0,0,' ','暂空',' ','http://www.gziec.net/','广州涉外经济职业技术学院是2004年经广东省人民政府批准、国家教育部备案设立的民办普通高职院校。学校地处广州市白云区，毗邻广州南湖游乐园，坐落于风景秀丽的白云山麓，空气清新，地理位置优越，交通极其便利，是陶冶情操、求学成才的理想之地。学校占地面积1043亩，建筑面积17多万平方米，图书馆藏书77万多册，校内实训室70个，多媒体课室、语音室近120间，校外实训基地160多个，建有先进的适应网络资讯时代的网络中心，并在学生宿舍配有独立的高速上网的网络端口（到桌面的速度可达2-4M），学生宿舍设施完善',385509);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州南洋理工职业学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.nyjy.cn','广州南洋理工职业学院始创于1984年，是经广东省人民政府批准成立、教育部备案的省属民办普通高等学校。学院着力打造“技能培养特色鲜明，就业竞争力优势突出”的办学特色，毕业生就业率一直保持在98%以上。校园占地面积508亩，建有设备先进的实验室（场）108个，机房和多媒体教室100多间，馆藏图书80多万册，数字化校园，空调宿舍、智能水电系统，学习、生活、体育、文娱设施一应齐全。学院现设有11个教学系部，即机电工程系、汽车工程系、电子信息工程系、管理系、财经系、外语系、建筑工程系、艺术设计系、服装系以',434927);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州科技职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://zs.gzkjxy.net/','广州科技职业技术学院创办于1999年，在2004年经广东省人民政府批准、国家教育部备案，广东省教育厅主管的一所民办普通高等职业院校。学院占地面积944亩，在校生10600多人。学院坐落于广州市白云区钟落潭“广州第二大学城”内，紧靠京珠高速和广从快速干线，交通十分便利。钟落潭高校园区属广州市绿化生态用地范围，全部建筑采用低密度建设的生态发展模式;相当部分是技术类院校，与广州大学城形成互补态势，成为广州市高等职业技术重要人才培养基地。广州科技职业技术学院简称“广科院”，是在广州医学院南大学院基础上，',447469);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('惠州经济职业技术学院',' ','广东',0,6,0,0,0,' ','暂空',' ','http://www.hzcollege.com/','惠州经济职业技术学院是经广东省人民政府批准、国家教育部备案、具有独立颁发国家承认大专学历文凭的全日制普通高等院校。学院位于石化名城、数码之都、旅游胜地的惠州市。学院占地面积668亩，建筑面积12万平方米，校园风景秀丽、环境幽雅，拥有宽敞明亮的教学大楼、标准的学生公寓、先进的教学仪器设备和体育运动设施。校园内绿树成荫，鸟语花香，是莘莘学子潜心读书的好地方。学院以专科教育为主，目前开设有数控技术、汽车技术服务与营销、计算机应用、动漫设计与制作、广告设计与制作、应用电子技术、市场营销、物流管理、旅游管',175293);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东莞南博职业技术学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://www.dgnbzx.cn/','东莞南博职业技术学院是经广东省政府批准设立、国家教育部备案的全日制普通高等院校，是东莞市第一所多科性、培养高技能型人才的职业技术学院。学院地处现代制造业名城?东莞市，校园座落在南城区，南靠水濂湖公园，北临西平水库，东依西湖乐园，依山傍水，风景优美。学院占地面积450亩，建筑面积近25万平方米，总投资3亿元。学院拥有各类图书35万册，建成计算机网络、计算机维修、数控机床、机械加工、电工电子等一流实验室12个，校外实习、实训基地28个，艺术系拥有大型练功厅和琴房37间，教学仪器设备达1800万元。学',187022);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆现代职业技术学院',' ','新疆',0,3,0,0,0,' ','暂空',' ','oldname": "新疆现代职业技术学院','新疆现代职业技术学院是一所经自治区人民政府批准，国家教育部备案成立的具有独立颁发国家学历文凭资格的综合性全日制高等院校。教育部编制高等院校代码13726。学院坐落于风景秀丽的乌鲁木齐市。1995年建校以来开展各层次学历和非学历教育、短期培训等教学活动。学院占地面积83250平方米，建筑面积41774平方米，院校新建校园用地面积3000余亩（其中2000余亩进行荒山绿化），建筑总面积148100平方米，总投资16250万人民币（2008年完工）。学院教学设备仪器价值900余万元，拥有各类藏书13万',71689);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆天山职业技术学院',' ','新疆',0,0,0,0,0,' ','暂空',' ','oldname": "新疆天山职业技术学院','新疆天山职业技术学院是自治区人民政府批准、国家教育部备案、以培养高级应用型人才为目标，具有颁发国家学历文凭资格的民办国家普通高校(教育部编制高等院校代码为13727)。是国家教育部远程教育新疆基地、全国网络教育系统理事单位、全国就业力示范院校。设有国家英语应用能力考点，英语四、六级考点，以及职业技能鉴定中心，国家劳动部计算机高新技术职业资格考试站等职业资格考试机构。曾获自治区教育厅授予的“先进办学单位”、“教育系统先进集体”、国家民政部授予的“全国先进单位”称号。中国教育报、新疆日报、新疆电视台',68060);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京培黎职业学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.bjpldx.edu.cn/','北京培黎职业学院是经北京市人民政府批准，国家教育部备案，在原北京培黎职业大学基础上设立，具有独立颁发国家承认学历的市属全日制民办普通高校，开展高等职业教育(专科)。学院设置7个系，23个高职教育专业，在校生4000余人。2008年4月12日，尊敬的温家宝总理在日理万机中为我院亲书校训“手脑并用，创造分析”和给同学们复信，给予全院师生极大的教育、鼓舞和鞭策。学院的前身是创建于1983年5月的北京培黎职业大学，系我国改革开放中最早创立的民办大学之一。其历史源于上世纪抗战时期路易·艾黎创办的培黎学校。',187690);
