insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江煤炭职业技术学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.hmzy.cn/','黑龙江煤炭职业技术学院是2004年经黑龙江省人民政府批准，国家教育部备案的省属专科层次的普通高校。始建于1949年11月，前身是双鸭山矿务局工业学校，具有悠久的办学历史，座落在一个兼有大煤田、大粮仓、大森林、大湿地，集煤炭、电力、化工、钢铁等产业为一体的风光秀美的新兴工业城市——双鸭山市。占地面积27.3万平方米，建筑面积18.1万平方米。学院以培养“思想品德好、理论基础扎实、操作能力强”的应用型人才为目标,以“质量立校、特色办校、立足煤炭、服务社会”为办学方向，以“学生第一、实践第一、就业第一',39318);
