insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆城市职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqcvc.com.cn/','重庆城市职业学院是经过重庆市人民政府批准、国家教育部备案，由重庆市总工会主管，面向全国招生的全日制公办普通高等院校。重庆市人民政府市长王鸿举为学院亲题了校名。学院分别座落在重庆市渝中区和重庆职业教育基地—永川区。这里基础设施完善，有气势辉煌、设计合理、能齐全的教学楼，有藏书丰富、实现自动化管理的图书馆，有现代化得网络与计算机中心和语音室，有设备先进的实习实训中心，有为学生搭建自主创业平台的创业交流中心，有供正式比赛使用的运动场，有舒适方便的学生公寓，有满足师生生活需要的各项服务设施，整个校区依山',102257);
