insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西邮电职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','oldname": "陕西邮电职业技术学院','陕西邮电职业技术学院是隶属于中国电信集团公司领导的全日制通信类高等职业技术院校，创办于1958年8月，原名陕西省邮电学校。1999年被评为“国家级重点中专学校”；2004年4月经陕西省人民政府批准，教育部备案，升格为“陕西邮电职业技术学院”。学院行政主管为陕西电信实业公司，教学业务受陕西省教育厅指导。目前，学院占地面积为180亩，建筑面积为69079m2，总资产已达1.45亿元。学院设有通信系、计算机系、管理系、基础部、职业教育研究室、体育部、图书馆等教学机构。现开设通信技术、移动通信技术、电子',133993);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安海棠职业学院',' ','陕西',0,0,0,0,0,' ','暂空',' ','http://www.xahtxy.cn/','西安市灞桥区水安路30号学院邮箱：xianhaitang@126.com招生咨询热线：029-82602000设备设施完善学院地处古都西安闻名全国的白鹿原大学城，是西安海棠医药科技集团创办的一所以中医美容类专业为主的高职院校。学院绿树成荫，鲜花绽放，三季有花，四季常青，绿化面积达70%以上，体现了人与自然的和谐之美。清一色的现代建筑，宏伟壮观，为学生学习、生活提供了良好的条件，是青年学子求知成才的理想学府。学院占地面积500多亩，总建筑面积16万余平方米。图书馆拥有图书百万万册，期刊400余种。',417302);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安汽车科技职业学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.atc168.com/zhaosheng/index.asp','西安汽车科技学院创建于1987年，是经国家教育部备案、具有国家计划内统招资质的全日制普通高校。学院秉承“为中国汽车行业培养优秀的应用型高技能人才”的办学宗旨，以汽车检测与维修技术、汽车电子技术、汽车运用技术、汽车服务与营销4大重点专业为中心，到目前已经建成涵盖汽车制造、汽车改装、汽车检测与维修、汽车服务与营销、汽车金融与保险、汽车业工商管理、汽车电子商务、交通运输等领域的专业设置体系。学院自成立以来，在全国，尤其是中西部地区职业汽车教育领域享有良好声誉。学院位于文化氛围浓郁的古城西安,校舍建筑面',197610);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安东方亚太职业技术学院',' ','陕西',0,0,0,0,0,' ','暂空',' ','http://www.yt-edu.cn/','延安大学西安创新学院是2004年5月经教育部批准成立的一所全日制本科独立学院。学院按国家计划统一招生，并按国家规定颁发经教育部电子注册的普通高校本科毕业证书，符合学位授予条件者，颁发延安大学西安创新学院相关学科的学士学位证书。董事会下设延安大学西安创新学院、东方亚太“非常高职”，东方亚太是以培养高技能的技师，打造高收入的职业技能人才为目标，学院与企业联手，以城市新兴行业高级技师为导向，采用行家治校，文凭技能双师型培养模式，形成了以实践教学为主，强化学生的动手能力，立足市场应用型人才需求，双轨道双',43935);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('通辽职业学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.tlzyxy.com/','通辽职业学院是一所公办全日制普通高等院校。学院于2009年10月顺利通过国家高等职业院校人才培养工作评估。2015年被评为《自治区示范性高职院校》。学院坐落于通辽经济技术开发区，占地面积803亩，建筑面积14.6万平方米，馆藏图书40万册;建有校内各类实验实训场所100个，校外实训基地129家;固定资产3.8亿元，教学科研仪器设备总值近6273万元;校园网接入总带宽100M，教学用计算机1317台，多媒体教室153个;设有国家职业技能鉴定所。学院下设护理系、药学系、机电工程系、化工系、财经管理系',143456);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('科尔沁艺术职业学院',' ','内蒙古',0,8,0,0,0,' ','暂空',' ','http://www.keqysxy.com.cn ','科尔沁艺术职业学院经内蒙古自治区人民政府批准，教育部备案，以原通辽市艺术学校为基础组建，坐落于科尔沁草原腹地——通辽市。学院是自治区唯一一所独立设置的艺术类公办高等职业院校。具有40年的办学历史，文化底蕴丰厚，以举办专科层次高等职业教育为主，兼顾中等职业教育职能。学院占地508亩，建筑面积6.9万平方米，设有音乐系、民族音乐表演系、舞蹈系、美术系、传媒系、公共教学部和一个附属中专学校。学院现有在职教职工248人，教授8人、副教授34人，硕士研究生19人。自治区突出贡献中青年专家1人，国家、自治区',25572);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('白城医学高等专科学校',' ','吉林',0,3,0,0,0,' ','暂空',' ','http://www.bcyz.cn','吉林省白城医学高等专科学校是教育部批准成立的全日制普通高等学校。学校现有教职工746人，其中正高级职称13人，副高级职称118人，中级职称244人，师资队伍素质精良、结构合理。在校学生4000多人，形成了普通专科、成人本科及研究生课程班教育并存的多层次办学格局。学校占地45.5万平方米，总建筑面积10多万平方米，拥有基础、临床专业三大实验中心，具有标准化的微机室、语音室、多媒体教学、双向视频教学系统等现代教学设施，配有集远程网络教学、多媒体教学、双向视频教学系统为一体的现代教育技术中心，电化教学',168573);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑河学院',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.hhhxy.cn/','黑河学院是黑龙江省属公立综合性普通本科高等学校，坐落于素有祖国北疆明珠之称的黑龙江省黑河市，与俄罗斯远东第三大城市——布拉戈维申斯克市隔江相望，是中俄4300多公里边境线上我国境内第一所全日制普通高等本科院校。学校办学历史悠久，前身为成立于1958年的黑河师范专科学校，历经齐齐哈尔师范学院黑河分校、齐齐哈尔大学黑河分校几个发展阶段，2004年经国家教育部批准成立黑河学院，2010年被确定为“黑龙江省特色应用型本科高校培育单位”，2013年被省教育厅批准建立黑龙江省对俄经贸人才培养基地。学校开设3',690310);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西电力职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','oldname": "山西电力职业技术学院','山西电力职业技术学院是在原太原电力学校基础上，经省政府批准成立，由教育部备案的一所具有颁发普通高等教育学历证书资格的高职院校。学院太原校区位于省城太原风景秀丽的晋阳湖畔，占地面积170亩;临汾校区坐落在历史名城--尧帝古都临汾，占地134.6亩。目前在校学生5000余人。办学思路明确。学院坚持“立足电力、服务社会，以服务为宗旨、以就业为导向”的指导思想，以全日制高等职业技术教育为主体，实行高等职业教育与成人高等教育相结合，全日制教育与函授教育并存的办学格局，主要为地方和电力行业培养生产、建设、管',205759);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('台州科技职业学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.tzvcst.net/','台州科技职业学院是教育部备案通过、浙江省人民政府批准正式建立、台州市人民政府主办的公办专科层次的全日制普通高校，办学历史悠久。学院座落在风景秀丽的蜜桔之乡——黄岩，永宁江碧水环绕，马鞍山隔江相望，生态环境良好，对外交通便捷。规划总用地760.9亩，建筑面积22.7万m2，现已征地524.9亩，建筑面积已建9.5万多m2，在建10.7万m2。学院现有教职工222人，专兼任教师156人，副高以上职称教师占33%。硕士学位的教师占28.2%，“双师型”教师占54%。学院拥有藏书16.9万余册，各类报刊',49692);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海电影艺术职业学院',' ','上海',0,8,0,0,0,' ','暂空',' ','http://www.shfilmart.com','上海电影艺术职业学院是经国家教育部、文化部和上海市人民政府批准的全日制普通高等艺术院校，座落于上海市浦东新区张江高科技园区内，距地铁二号线张江高科站800米，规划占地400亩，面向全国招生，目前在校生达2000余人。作为中华人民共和国文化部高等艺术教育的共建试点单位，学院聚集了影视、动画、设计、舞蹈、音乐艺术领域的众多资深教授和业内专家，并坚持以学生知识、能力、素质的全面提高为办学宗旨，改革和创新高等艺术教育，强调艺术与高科技的高度融合，全面推行产学研结合，强化实践环节，以技能应用为主，培养实用',85055);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('无锡城市职业技术学院',' ','江苏',0,6,0,0,0,' ','暂空',' ','http://http:www.wxcu.edu.cn','无锡城市职业技术学院是经江苏省人民政府批准设立并报教育部备案，由无锡市人民政府举办的全日制公办普通高职(专科)学院。学校设有师范学院、贸易金融学院、会计学院、旅游学院、物联网工程学院、传媒与艺术设计学院、建筑与环境工程学院、国际合作学院、干部培训与继续教育学院、基础课部、体育部等教学单位，开设与区域经济发展和产业结构紧密契合的高职(专科)专业38个，构建了旅游、建筑工程技术、建筑装饰工程技术、物联网工程技术、金融、营销与贸易、艺术设计、会计等八个专业群，初步实现了院级-市级-省级-国家级重点专业',144314);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('无锡工艺职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.wxgyxy.cn','隶属于江苏省教育厅的全日制公办普通高等学校教育部高职高专人才培养工作水平评估优秀学校江苏省首届职业教育创新大赛“优秀学校”奖江苏省高校党建工作创新奖2006年“最佳党日活动”优胜奖无锡市安全文明校园江苏省安全文明校园连续多年毕业生就业率在98%以上无锡工艺职业技术学院座落于美丽的太湖西岸、著名的教授之乡——陶都宜兴。学院是经江苏省人民政府批准隶属于江苏省教育厅的全日制普通高等学校，是宜兴市目前唯一一所独立建制的普通高等院校。学院前身系创建于1958年的陶都工业大学，拥有50年办学历史。学院有着浓',149342);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('金山职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jinshan-cn.com','金山职业技术学院是江苏省人民政府批准，教育部备案的全日制民办普通高等学校，学生毕业后颁发教育部电子注册的普通高等学校毕业文凭。校园优美，设施齐备学院所在地江苏扬中经济发达、风景秀美，是首批“国家级生态示范区”、“中国工程电气岛”、“省新型工业化产业示范基地”，2015年4月在全国县域经济创新力评比中高居榜首。学院设有6系，21个专业，一期占地400亩，校内实验实训室67个，校外实训基地120多个。校园布局合理、配套齐全，学院先后被扬中市评为“文明单位”、“绿化文明单位”，被省教育厅评为“全国大学',37399);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏财经职业技术学院',' ','江苏',0,6,0,0,0,' ','暂空',' ','http://www.jscjxy.cn/','江苏财经职业技术学院是经江苏省人民政府批准的省属公办全日制普通高等学校，具有50年的办学历史，地处苏北中心城市、开国总理周恩来的故乡——淮安。目前，学院占地面积812亩，建筑面积24.76万平方米，固定资产逾6亿元，教学仪器设备总值6700万元，图书馆馆藏纸质图书67.4万册、电子图书9000GB。现有全日制在校生8308人，教职工470人，专任教师377人，正高职称18人，副高职称119人，具有博士、硕士学位教师167人，“双师”素质专业教师比例达80%。在半个世纪的办学历程中，学院为社会输送',172602);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('扬州工业职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.ypi.edu.cn','扬州工业职业技术学院是省属公办普通高校，江苏省示范性高职院校，学校坐落在荣获“联合国人居奖”的历史文化名城——扬州市。新校区位于扬州市扬子津科教园，与扬州大学新校区隔路相望，区位优势明显，交通方便快捷。学校占地55.6万平方米，建筑面积30.3万平方米，是一所园林式、数字化、生态型的现代化校园，环境优美，景色宜人。学校设有化学工程学院、建筑工程学院、电气与信息工程学院、机械工程学院、经济管理学院、装饰与艺术设计学院、国际交流学院、继续教育学院以及社科部、基础部、体育部等11个院(部)。面向全国1',134344);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南热带作物职业学院',' ','云南',0,2,0,0,0,' ','暂空',' ','http://2db.ynry.cn/','云南热带作物职业学院（前身云南省亚热带作物技术学校，创建于1960年3月）是一所具有独立办学资格的省属公办普通高等职业学院。学院在风景秀丽、气候宜人的世界茶源、普洱茶都、中国茶城——普洱市(原思茅市)和神奇美丽的西双版纳两地办学。学院依托农垦、立足云南、面向全国、辐射东南亚，主要培养热带农业生产与管理、产品加工、经济管理、市场营销、园林园艺技术、旅游服务、信息技术等社会需要的高技能应用型人才。学院院部位于普洱市思茅区（西双版纳州景洪市设校区）。学院占地10.2万M2，校舍面积5.5万M2，办学设',22682);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南国防工业职业技术学院',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.ynvtc.cn','云南国防工业职业技术学院座落于美丽的春城昆明，学院具有近50年的办学历史，2004年7月1日，经云南省人民政府批准，国家教育部备案，学院整合更名为：“云南国防工业职业技术学院”,是一所以光、机、电为特色，培养适应制造、信息等产业一线需要的高级应用型技能人才的全日制普通高等职业技术学院。学院位于昆明市北市区龙泉路，沿路聚集了十数所大中专院校和科研机构，具有良好的学习环境及浓厚的人文和学术氛围；学院现有在校学生5000余人，教职工300余人，占地面积300余亩，建有各类实验室68个，建有微机管理的图',121551);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南机电职业技术学院',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.ynmec.com/','“国家示范性高等职业院校建设计划”骨干高职院校云南机电职业技术学院于2004年7月经云南省人民政府批准,由云南省机械工业学校和云南省机械技工学校两校合并组建,是一所以工科为主、文理科兼容的综合性全日制高等职业技术学院(专科层次)。学院至今已有近三十年的职业学校办学历史,曾先后被教育部评选为全国中等职业教育示范专业学校、国家级重点中等专业学校、国家示范性数控技术职业教育实训基地等。学院位于云南省昆明市盘龙区茨坝，学院占地面积441亩，校舍建筑面积130058平方米；固定资产总值18594.12万元',70245);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南林业职业技术学院',' ','云南',0,2,0,0,0,' ','暂空',' ','http://www.ynftc.cn/','云南林业职业技术学院是经云南省人民政府批准、教育部备案、国家公办，具有高等学历教育资格的公办全日制普通高等职业技术学院。学院办学历史悠久，始建于1955年。学院下设七个专业系部，承担着24个专业、4400多名在校生的教学和管理工作，是一所专门培养实用型、技能型技术人才的高等职业学院。学院办学条件优越，被评为昆明市“园林单位”。校址位于昆明市盘龙区金殿国家级风景名胜区，距世博园北门500米。校园布局有序、绿草如茵、环境优雅、交通便捷、适宜办学。学院占地面积为574.6亩，固定资产总值达5000多万',97222);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南城市建设职业学院',' ','云南',0,0,0,0,0,' ','暂空',' ','http://www.yncjxy.com/','云南北美职业学院是经云南省政府批准，国家教育部备案，纳入全国高考计划内统一招生，具有独立颁发国家承认学历文凭资格的民办全日制普通高等院校。学院目前有两个校区，一个培训中心，民大校区位于昆明市教场北路190号云南民族大学分部，周围高校林立，学习氛围浓厚，交通便捷，信息灵通。西校区位于昆明市高新开发区海屯路272号，校园环境优美宁静，鸟语花香，是学习和成长的良好环境。培训中心位于位于昆明市人民西路岷山车场旁，校园独立，交通便捷。方便我们的学生参加实作实训；与市场紧密结合，方便我们的学生就业和创业。学',52235);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('马鞍山师范高等专科学校',' ','安徽',0,4,0,0,0,' ','暂空',' ','http://www.massz.cn/','江东之畔，翠螺峰旁，坐落着底蕴深厚、人文荟萃、文化浓郁的马鞍山师范高等专科学校。学校始建于1958年，2004年5月经教育部批准升格为马鞍山师范高等专科学校。校园占地面积320亩，地处国家级经济技术开发区，毗邻政务中心，与马钢总部隔路相望，北通市中心，南接当涂县，西邻采石矶，东望新城区。学校曾荣获全国先进师范院校，全国语言文字工作先进单位，全国艺术教育工作先进单位，安徽省第五、第六、第七、第八、第九届文明单位，创建全国文明城市先进单位，两届全国大学生艺术展演优秀组织单位，全国未成年人思想道德建设',107966);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州外语外贸职业技术学院',' ','福建',0,6,0,0,0,' ','暂空',' ','http://www.fzfu.com','福州外语外贸职业技术学院是经福建省人民政府批准设立、国家教育部备案，具有独立颁发国家承认学历的民办全日制普通高等职业技术学院，是一所以外语经贸为特色，涵盖外语、经管、商贸、电子信息技术和通讯等专业大类的高职院校。现为福州市职业教育先进单位、福建省职业教育先进单位，教育部高职高专人才培养工作水平评估良好学校。学院座落在素有“海滨邹鲁”之称的历史文化名城---福州。榕城优美的风景，闽都厚重的人文底蕴和气候宜人、经济繁荣、交通便利的城市环境，为莘莘学子提供了优越的学习生活环境。学院校区位于仓山区城门镇',46778);
