insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('闽北职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','oldname": "闽北职业技术学院','闽北职业技术学院于2004年2月26日经福建省人民政府批准正式成立，是一所由南平市人民政府主办的,业务由省教育厅管理的专科层次的全日制公办普通高等职业学校。学院前身为建阳师范学校，创建于1929年。2005年5月，南平市委、市政府决定闽北职业技术学院由建阳迁往南平市江南新区职教园区。新校区规划用地500亩，总建筑面积10万平方米，其中一期工程建筑面积6万平方米，于2009年6月投入使用。学校布局合理，功能齐全，依山傍水，环境优美，是读书、治学、生活的理想场所。学院现设有食品与生物工程系、管理系、',28506);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州黎明职业技术学院',' ','福建',0,6,0,0,0,' ','暂空',' ','http://www.fzlmxy.cn','福州黎明职业技术学院是由“全国乡镇企业十大百强”、“福建省最佳形象企业”、“福建省标兵企业”——福建省黎明企业集团公司投资兴办，经福建省人民政府和国家教育部批准设立的一所全日制民办普通高等学校，学制2-3年，2004年开始面向省内外招生。学院秉承“明德至善、博学多能”的校训，倡导“创业办学、光而不耀、校企相长、服务海西”的办学理念和“以学生为本”、“教育就是服务”的育人理念，以“爱的教育”为主线，加强内涵建设，凝练办学特色，人才培养工作水平不断提升，2008年通过了教育部评估，是福建省第四所通过',188397);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门演艺职业学院',' ','福建',0,8,0,0,0,' ','暂空',' ','http://www.xmyanyi.com','厦门演艺职业学院是福建省教育厅批准、国家教育部备案的一所综合艺术学院。学院坐落于闻名遐迩的艺术之岛——厦门鼓浪屿。厦门鼓浪屿是一个钟灵毓秀、充满了艺术灵感的圣地，这里风光旖旎、四季如春、艺术气息浓厚，素有“钢琴之岛”的美称。厦门演艺职业学院的2万平方米的教学用房及3千多平方米的学生宿舍散落于岛上各方，与岛上的万国建筑相映成趣，这不仅为鼓浪屿的艺术内涵增色，同时也为新型艺术学子营造了极好的艺术氛围和学习环境。为适应学院在“十一五”期间更大的发展，目前学院正在筹划新校舍的选址和设计方案，一所崭新的学',58434);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门华天涉外职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.xmht.com ','厦门华天涉外职业技术学院，筹建于2002年，2004年2月经福建省人民政府批准正式设立，国家教育部备案，纳入全国高考统招计划（国家统招代码13768），具有独立颁发国家承认学历证书资格的全日制综合性普通高等院校。同时，于2005年10月经福建省高等教育自学考委员会批准为国家高等教育自学考试助学单位（福建省高等教育自学考试委员会闽教考办[2005]153号）因此，学院招收国家计划内高考统招生，同时招收国家高等教育自考生和小自考生。学院现设机电系、计算机信息系、济贸易系、外语系、现代服务管理系和影视',133853);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州科技职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.fzstc.com','福州科技职业技术学院于2001年筹建。福建省人民政府于2004年2月批准正式建校并报教育部备案。学院为专科层次的全日制民办普通高等院校，学制三年，面向全国招生，文理兼收。学院已创良好品牌，列入全国高考统一招生，颁发国家教育部电子注册、国家承认的大专文凭。学院拥有一批教学经验丰富的专家、教授作为学术带头人，还有一批既有坚实理论基础又有丰富实践经验的高级工程师以及优秀中青年教师在院任教，其中正副教授、高级工程师、高级会计师占40%。学院高度重视教学质量，尤其将计算机教学贯穿培养人才的全过程，并强化英',12653);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州经贸职业技术学院',' ','福建',0,6,0,0,0,' ','暂空',' ','oldname": "泉州经贸职业技术学院','泉州经贸职业技术学院是经福建省人民政府批准，由泉州市人民政府举办，教学业务由福建省教育厅管理的公办全日制普通高等职业技术学院。学院地处我国首批历史文化名城、古代海上丝绸之路起点、国际花园城市、著名侨乡—泉州市，坐落在鲤城区南环路，背倚著名风景区紫帽山，前临泉州母亲河晋江水，风光秀丽，交通便捷。学院现占地350亩，建筑面积7.2万平方米。已拥有标准体育场、大中型多媒体教室、计算机中心、网络中心、科技实训楼、各类实验室、教学模拟教室、图书馆等设施，且与校外多家集团企业、公司建立了实训基地，为满足学生',65163);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建对外经济贸易职业技术学院',' ','福建',0,6,0,0,0,' ','暂空',' ','http://www.fibec.cn/','福建对外经济贸易职业技术学院是一所全日制公办大学，坐落于美丽的侨乡－－福州马尾国家经济开发区亭江镇，前身为创办于1979年的福建对外贸易学校。2004年，经省政府批准，独立升格为福建对外经济贸易职业技术学院。至今已培养一万多名各类外贸人才，为福建省对外贸易事业做出了积极贡献，被贾庆林同志誉为“外贸摇篮”。2008年，学院以“优秀”等级通过教育部人才培养水平评估工作；2008年，连续第三次荣获福建省“文明学校”称号；2009年，跨入“省级示范性高职院校”行列。目前，学院占地面积458亩（另有预留地',89877);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湄洲湾职业技术学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.fjmzw.com/','湄洲湾职业技术学院是一所经福建省人民政府批准成立、国家教育部备案的专科层次全日制工科类公办高等院校。位于海峡西岸经济区中心地带的湄洲湾畔，这里依山伴海，风光秀丽，历史文化积淀深厚，享有“文献名邦”、“海滨邹鲁”之美誉。学院位于海峡西岸经济区中心地带的莆田：占地面积535亩；建筑面积16万平方米。专任教师283人，其中教授、副教授等高中级职称教师83人，博士、硕士研究生59人，“双师型”教师58人。教学仪器设备总值2500万元，拥有中央财政支持的电工电子与自动化实训基地、实验实训大楼6幢，实训中心',35115);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌师范高等专科学校',' ','江西',0,4,0,0,0,' ','暂空',' ','http://nctc.com.cn/','南昌师范高等专科学校座落在南昌市美丽的青山湖畔，前身为南昌师范学校，是全省唯一面向基础教育培养大专层次师资的学校，也是我省儿童心理研究、残疾人教育研究、小学教育研究、学前教育研究基地。该校创办于1908年，是一所具有百年办学历史的名校，灵山秀水哺育了一批优秀人才。培养了国画大师傅抱石、近代数学家曾炯、国家一级编剧毛秉权，国家一级作家孙海浪、国家一级作曲家郝士达，省书法家协会副主席赵定群等大批艺术家教育家。也培养了诸如查俊如、崔琳、李芸、姜红等党政优秀干部。还培养了一大批优秀歌唱家节目主持人，如杨',212100);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西中医药高等专科学校',' ','江西',0,3,0,0,0,' ','暂空',' ','http://www.jxtcms.net/','江西中医药高等专科学校创建于1986年，是全国八所中医药高等专科学校之一，也是国家中医药管理局重点建设的新型特色高等院校。学校坐落于“十一世纪改革家”王安石、“东方莎士比亚”汤显祖的故乡、旰江医学、建昌帮发源地、素有“文化之邦”、“才子之乡”、“名中医之乡”美称的江西省抚州市。学校共有两个校区，总占地面积500余亩，老校区位于抚州市赣东大道1111号，校内教学楼、实验楼、临床大楼、图书馆、行政楼、专家楼、体育中心等设施齐全，环境优美绿树成荫，池榭亭阁风景别致；新校区位于江西金巢经济技术开发区，园',283716);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西先锋软件职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.aheadedu.com/','1、江西先锋软件职业技术学院简介江西先锋软件职业技术学院是经江西省人民政府批准、国家教育部备案并纳入国家统一招生计划的普通高等院校。由中国软件百强企业、国家规划布局内重点软件企业--先锋软件集团联合国家外专局中国国际人才交流基金会等单位共同创办。学院座落于风景秀丽的南昌梅岭国家森林公园山麓的南昌软件基地内，依山傍水、风景秀丽，气候宜人；学院占地面积近1000亩，建筑面积近20万平方米，现有全日制在学校生3800余人；教学层次包括二年制高职、三年制高职、五年一贯制高职和自考专升本等，同时提供汽车驾',59640);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('淄博师范高等专科学校',' ','山东',0,4,0,0,0,' ','暂空',' ','http://www.zbnc.edu.cn','淄博师范高等专科学校是经教育部批准、以培养培训专科层次小学及幼儿教师为主的公办全日制普通高校。学校秉承“立德树人”的校训，大力实施“质量立校、人才强校、特色兴校”战略，培养了一大批基础知识扎实、基本能力强的小学幼儿教师和适应经济社会发展需要的高技能应用型人才，为淄博市乃至山东省的基础教育和社会发展作出了重要贡献。学校位于淄博市淄川经济开发区，坐落在奂山脚下，西临滨博高速公路，南靠胶王路，与济南经十东路贯穿一线，西距济南80公里，区位优势明显，交通便捷通达。学校现有校区占地1280亩，校舍规模达到',277631);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东中医药高等专科学校',' ','山东',0,3,0,0,0,' ','暂空',' ','http://www.stcmchina.com','山东中医药高等专科学校是一所省属全日制普通高等专科学校。学校始建于1958年，前身为山东省中医药学校，2004年经国家教育部和山东省政府批准，升格为高等专科学校。学校坐落于胶东半岛烟台，有两个校区，分别在著名旅游风景区烟台养马岛和素有“梨乡”美誉的莱阳，学校共占地736亩，校舍建筑面积15万平方米，固定资产4.8亿元。学校坚持以邓小平理论和“三个代表”重要思想为指导，深入贯彻落实科学发展观，立足于基层医药卫生事业，坚持以素质教育为基础，以能力培养为本位，以基础理论、基本知识和基本技能为重点的办学',259334);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沧州医学高等专科学校',' ','河北',0,3,0,0,0,' ','暂空',' ','http://www.czmc.cn/  ','四十八年励精图治，四十八年春华秋实。在48年的办学历程中，沧州医专不断发展壮大，奋力开拓前行，从建校初期十几间简陋的校舍到颇具规模的现代化学校，形成了“治学严、要求高、重实践、求质量”的优良传统和“厚德、笃学、求是、拓新”的良好校风，为社会培养了20000余名卫生技术人才。特别进入新世纪以来，学校焕发出新的生机和活力，开始了宏伟的第二次创业。短短几年内，学校抢抓机遇、拼搏进取，完成了新校区建设和改建升格的历史性重任，实现了跨越式发展。理念创特色、保质量、求发展校训厚德、笃学、求是、拓新校风严谨治',299328);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('漯河医学高等专科学校',' ','河南',0,3,0,0,0,' ','暂空',' ','http://www.lhmc.edu.cn','漯河医学高等专科学校是一所全日制公办普通高等专科学校。学校座落于中国食品名城、国家级森林城市——河南省漯河市，校园沙澧河环绕，环境幽雅，景色宜人。目前，学校占地1539亩，建有两个校区，建筑面积40万平方米，现有教职工3512人(含直属附属医院)，专任教师729人，副高以上职称教师537人，硕士以上学位教师543人。学校全日制本专科在校生1.3万余名，其中与新乡医学院联合培养3届临床医学专业本科在校生148名。校本部教学仪器设备总值1.02亿元，图书馆藏书100余万册。设置以医学学科为主体的两个',929066);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南阳医学高等专科学校',' ','河南',0,3,0,0,0,' ','暂空',' ','http://www.nymc.cn','南阳医专的前身是创办于1951年的南阳卫校。2004年经国家教育部批准升格为南阳医学高等专科学校。2006年南阳市委市政府对南阳医学教育资源进行整合，将南阳中医药学校连同张仲景国医大学(理工学院国医国药系)并入南阳医学高等专科学校，形成了中西医结合、优势互补的办学格局。目前，学校占地1316亩。校舍建筑面积47.8万平方米，教学科研仪器设备总值1.05亿元。馆藏图书150余万册，中外文期刊1520种。学校内设20个党政群团机构，14个教学系(部、院)，6个教辅机构。开设有临床医学、护理、医学影像',517396);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('商丘医学高等专科学校',' ','河南',0,3,0,0,0,' ','暂空',' ','http://www.sqyx.edu.cn','商丘医学高等专科学校坐落在中国历史文化名城、全国优秀旅游城市——商丘古城湖畔，是一所全日制公办普通医学高等专科学校。学校前身是始建于1921年的“归德圣保罗医院护士学校”，是河南省开办医学专业最早的学校之一。基本情况：目前，学校占地86.67万平方米，校舍建筑面积34.57万平方米，其中教学科研及辅助用房面积19.17万平方米；图书馆面积1.3万平方米，馆藏纸质图书71.25万册，电子图书2460GB，中外文纸质期刊905种；全校资产总值37911.59万元，其中教学、科研仪器设备资产总值599',882268);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州电子信息职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zyfb.com/','郑州电子信息职业技术学院是省政府、国家教育部列入2013年升本计划的普通高等院校，是全国先进职业院校、国家重点专业院校。学院党委是省委组织部批准直属省高工委领导的党委。学院两校区1000余亩花园般的校园，30余栋教学科研大楼，三个高标准运动场，万余名在校生。620多名专兼职教师中教授、副教授、高讲、高工等中高级职称的教师占60%以上，再加上解放军信息工程大学、河南大学、郑州大学等高校在我校任教的专家教授组成了强大的师资队伍。学院近亿元的各种先进教学设备，各系各专业都有先进的实验实训室，近百家国家',462013);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('信阳职业技术学院',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.xyvtc.edu.cn','信阳职业技术学院是河南省政府批准，教育部备案，由原信阳教育学院、信阳师范学校和信阳卫生学校合并组建的一所专科层次普通高等院校，以师范教育、医学教育和职业技术教育为办学特色，培养适应社会主义市场经济发展需求的中小学师资、医技人员和高等技术应用型人才。学院历史悠久，社会声誉较高，是国家级文明单位和省级花园式学校。学院位于信阳市城区中心。校园占地354亩，建筑面积23万平方米，馆藏图书36万册，中外文期刊1162种。拥有完善的教育教学和后勤服务设施，建有专业学科实验室90多个，并有附属小学、附属医院。',93667);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('嵩山少林武术职业学院',' ','河南',0,7,0,0,0,' ','暂空',' ','http://www.shaolinkungfu.edu.cn','嵩山少林武术职业学院是2004年经河南省人民政府批准成立、国家教育部备案的培养应用型、复合型、外向型高技能性人才的民办普通高等院校。学院坐落在全国著名武术之乡、中国优秀旅游城市河南郑州登封市西郊，毗邻207国道，依山傍水，环境优美，交通便利。新校区占地860亩，教学楼、图书馆、宿舍楼、电子阅览室、多媒体教室、语音室、计算机中心、驾驶培训中心、训练馆、餐厅、洗浴中心、健身房、宾馆等学习、生活设施齐全。学院自创办之日起就从高起点入手，在现代职业技术教育理论指导下，以服务为宗旨、以就业为导向，坚持“学',52814);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州工业安全职业学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zazy.cn','郑州工业安全职业学院是经河南省人民政府批准、教育部注册备案、省教育厅和省工业和信息化厅负责管理的一所专科层次的公办普通高等职业院校，主要培养适应生产、建设、管理一线的应用型高级技能人才。学院位于河南省郑州南大学城，东邻107国道，南靠321省道，地理位置优越，交通方便。学院占地七百多亩，办学条件优良，教学、生活设施齐全。学院建有各类实验室40个、校外实习基地16个,并建有校内实训中心、计算机中心、网络中心、多媒体教室、语音室等现代化教学设施，拥有运动场、足球场、篮球场、排球场等体育设施。学院有一',90259);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('永城职业学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.yczyxy.com','永城职业学院坐落在被誉为“汉兴之地、面粉之乡、能源之都、生态之城”的河南省永城市，是一所国家公办的全日制普通高等专科学校。学校的前身是始建于1956年的永城师范学校，建校以来已为社会培养了各类人才2万余名。学院先后被授予“河南省文明学校”、“河南省普通大中专毕业生就业工作先进集体”、“河南省大中专学生志愿者暑期‘三下乡’社会实践活动先进单位”、“河南省‘五爱教育’先进单位”、“商丘市文明单位标兵”等荣誉称号。学院占地面积510亩，建筑面积20.67万平方米，馆藏纸质图书33.7万册，电子图书20',39842);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南经贸职业学院',' ','河南',0,6,0,0,0,' ','暂空',' ','http://www.hnjmxy.cn/','河南经贸职业学院坐落在风景秀丽、文化底蕴丰厚的中原历史名城、河南省省会——郑州市。她是由河南省政府批准建立、在教育部备案、隶属于河南省教育厅的一所高等职业院校，占地843余亩，建筑面积20余万平方米。现设有财经金融、经济贸易、工商管理、会计、外语旅游、信息管理、工艺美术、电子工程、技术科学等9个系和社科、体育2个教学部，在校生12000余人。开设计算机应用技术、计算机网络技术、计算机多媒体技术、计算机信息管理、软件技术、应用电子技术、财务管理、会计（注册会计师）、涉外会计、会计电算化、经济信息管',233534);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南交通职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','oldname": "河南交通职业技术学院','河南交通职业技术学院是经河南省政府批准、教育部备案的河南省唯一的一所国家公办交通类全日制普通高等专科学校，其前身是创办于1953年的国家级重点中等专业学校河南省交通学校,隶属于河南省交通厅,具有50多年的办学历史，积淀了丰富的教学经验和文化底蕴。汽车运用技术等骨干专业被国家七部门定为国家级紧缺专业人才培养基地。目前全日制高职大专生7608人，成人学历教育专、本科及研究生在校生2700多人。学院办学条件十分优越学院位于省会郑州市，拥有得天独厚的交通地理优势和十分丰富的交通教育资源，南北两个校区环境',174925);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南农业职业学院',' ','河南',0,2,0,0,0,' ','暂空',' ','http://www.hnac.com.cn','“国家示范性高等职业院校建设计划”骨干高职院校河南农业职业学院是经河南省人民政府批准、国家教育部备案的全日制公办普通高等院校。学院是中国职业技术教育学会农业职业教育专业委员会副主任单位、河南省农业职业教育集团理事长单位、河南省农业职业教育研究会会长单位、河南省职业教育与成人教育研究会副会长单位。学院地处郑汴之间的中牟县城，交通便利。随着郑东新区开发和郑汴一体化建设，学院的区位优势日益突出。学院占地面积1108亩，建筑面积26.8万平方米，图书馆藏书45万册，教学用各类实验室80个，其中园艺、种子',85300);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州旅游职业学院',' ','河南',0,6,0,0,0,' ','暂空',' ','http://www.zztrc.edu.cn/','郑州旅游职业学院是河南省唯一一所以培养旅游人才为主的公办高等职业院校，是河南省高等职业教育品牌示范学校、“河南省高等学校数字化校园示范工程”院校、河南省旅游职业教育集团理事长单位。学院占地1251亩，建筑面积57万平方米(其中新校区1036亩，总投资10亿元)，拥有教学设备仪器价值7000余万元，建有150多个校内外实习实训与就业基地，被河南省政府确定为重点建设的示范性职业教育园区。现设8系3部1院，开设37个专业，有全日制在校生近万名。学院师资力量雄厚，汇集了魏小安、张辉、姚延波、龚绍方、李胜',534444);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zzyedu.cn','郑州职业技术学院是经河南省人民政府批准、国家教育部备案的一所普通高等院校。设有国家职业技能鉴定所，是国家高技能人才培训基地、国家紧缺人才数控技术应用培训基地，河南省文明学校，河南省勤工俭学先进集体、省级示范性职业学校、河南省最具特色的十佳职业院校之一，首批河南省诚信规范招生示范院校。三十多年来为国家培养了数以万计的高素质技能型人才，他们在各自的岗位上为祖国的现代建设发挥聪明才智，做出积极贡献。学院自1976年3月创建以来，紧密结合国民经济发展需求，根据市场设专业，围绕需要育人才。现有机械工程系、',224969);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江汉艺术职业学院',' ','湖北',0,8,0,0,0,' ','暂空',' ','http://www.hbjhart.com/','江汉艺术职业学院是经湖北省人民政府批准、教育部备案的省属全日制公办普通高校。学院地处江汉平原腹地的中国明星市、戏剧泰斗曹禺先生的故里——湖北省潜江市(省直辖市)，这里北依汉水，南望长江，沪蓉高速、318国道、襄岳公路交汇贯通，交通十分便捷。学院于1958年创建。校园规划科学，布局合理，环境优美。教学楼、音乐楼、美术楼、演播厅、餐厅礼堂楼、综合大楼等现代化大型建筑鳞次栉比。400米标准运动场、学生公寓、图书馆等配套设施一应俱全。校园内绿草如茵，繁花似锦，假山池沼，翠林修竹，掩映成趣，是全国绿委表彰',53739);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉工业职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://zsxx.e21.cn/sqlimg/school_html_jt/2006_08_23/article_30640.htm','武汉工业职业技术学院是经湖北省人民政府批准成立、国家教育部备案的全日制普通高等学校。学院始建于1975年，三十多年来，秉承“德以辅仁，学以致用”的治学精神，遵循“以人为本，学以致用，服务社会”的办学理念，致力于培养高素质技能型专门人才，学院立足行业、企业，为国家培养和输送了大量生产、建设、服务和管理第一线的高素质技能型人才，形成了鲜明的冶金建设行业特色，得到行业和社会的高度认可。学院现设有建筑工程系、机械工程系、电子与电气工程系、管理工程系、经济与贸易系艺术系、航空服务学院、国际学院、继续教育学',188500);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉民政职业学院',' ','湖北',0,11,0,0,0,' ','暂空',' ','http://www.whmzxy.cn/','武汉民政职业学院是经湖北省人民政府批准成立、教育部备案、具有高等学历教育招生资格的公立全日制普通高校，是全国仅有的两所专门社会工作类高校之一。学院地处武汉东湖科技文化区内，毗邻“武汉?中国光谷”。坐拥伏虎山，俯瞰南湖，树木葱郁，建筑格局错落有致，被评为园林式学校。学院占地500余亩，建筑面积10余万平方米，独立图书馆藏书20余万册，计算机中心拥有400余台最新电脑，配有电子阅览室、标准化语音室、校园宽带网、多媒体教室等，实现了教学手段的现代化。学生公寓属最新设计和全新建筑，电话、宽带网一应俱全，',105080);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鄂东职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://edzy.zsxx.e21.edu.cn/','鄂东职业技术学院是经教育部和湖北省政府批准设置的一所全日制普通高等学院（公办学校），学院位于全国著名的教育之乡、武汉市卫星城——湖北省黄冈市，北依大京九，南临长江，西接武汉，东望九江，交通便利，环境优美。建校以来，学校赢得了各级政府和社会的充分肯定。1991年被原国家教委、计委、劳动部、人事部、财政部授予“全国职业技术教育先进单位”，2002年学院被教育部、财政部授予“国家重点建设示范学校”，同时，数控专业被确定为国家级重点专业，2003年学院被教育部授予“全国职业指导工作先进学校”，2004年',45615);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北财税职业学院',' ','湖北',0,6,0,0,0,' ','暂空',' ','http://www.hbcszyxy.cn/v2015cms/index.html','湖北财税职业学院座落在武汉市武昌区陆家街301号，东靠107国道，南接京珠、沪蓉高速和武汉中环，北临武昌火车站，西依长江。学院是经湖北省人民政府批准成立、国家教育部备案、具有高等学历办学资格、全国税务系统唯一一所面向全国招生，以财税为主，涉及工商、外语、信息等学科的公办全日制普通高等职业学院，行政上隶属于湖北省地方税务局，教育教学业务接受湖北省教育厅指导，是国家教育部确认的“全国计算机技术应用等级考试培训基地”和“全国国际商务英语培训中心和认证考试点”、国家税务总局确认的湖北省税务干部教育培训中',96071);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黄冈科技职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbhgkj.com','黄冈科技职业学院是经湖北省人民政府批准设置、教育部备案的全日制普通高等学校。学院位于全国著名的教育、文化之乡——湖北省黄冈市。这里，人文荟萃，文化底蕴深厚，苏轼在这里写下了彪炳千秋的一词二赋；活字印刷的毕升、医药家李时珍、大文豪闻一多、地质学家李四光等都出生、成长在这块神奇的土地上。万里长江长虹卧波，京九大动脉贯穿全市、沪蓉、沪渝、京珠高速公路交汇于此，交通十分便利。学院东邻风景秀美的遗爱湖公园，南面长江，地理位置得天独厚。学院建设布局合理，总建筑面积近12万平方米，生活、教学设施齐全。学院下设',34233);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北国土资源职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbgt.com.cn','湖北国土资源职业学院是一所国土资源行业主办的普通高校（高职高专），以培养资源勘查类、地质工程与技术类、矿业工程类、测绘类、土建施工类、工程管理类人才为主，兼顾培养机械设计与制造类、自动化类、电子信息类、计算机类、财务会计类、经济贸易类、市场营销类、艺术类人才，具有鲜明的行业办学特色。学院位于历史文化名城荆州市中心城区，环境优美，交通便利，先后被国土资源部和湖北省教育厅授予“部级文明单位”、“校园管理先进单位”、“园林式学校”等荣誉。学院设立了资源工程系、测绘工程系、信息工程系、机电工程系、工商管',59695);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北生态工程职业技术学院',' ','湖北',0,2,0,0,0,' ','暂空',' ','http://www.hb-green.com','湖北生态工程职业技术学院是经湖北省人民政府批准，中华人民共和国教育部备案的公办全日制普通高等学校。学院创建于1952年，地处“九省通衢”的华中重镇武汉市，北瞰浩淼汤逊湖，紧邻国家级重点高新技术工业园区—武汉?中国光谷。学院占地405亩，建筑面积13.8万平方米，学院坚持“以人为本，学以致用，服务社会”的办学理念，践行“心修自然,强技养德”的校训。经过近60年的建设与发展，学院已形成以林业生态、园林景观、环境艺术、生态旅游为特色，理、工、文学科相互结合，办学特色鲜明的普通高等院校。学院拥有一支师德',103973);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南中医药高等专科学校',' ','湖南',0,3,0,0,0,' ','暂空',' ','http://www.hntcmc.net','湖南中医药高等专科学校是2004年经国家教育部批准升格，由湖南省卫生计生委、湖南省教育厅管理的公立全日制普通高等专科学校，地处中华民族始祖炎帝神农氏陵寝地、中南地区重要交通枢纽--株洲市，正在建设的新校区距长沙仅30分钟车程，是湖南省中医药实用型技能性人才的主要培养基地。学校始建于1959年，已有50多年办学历史。现占地900余亩(含云龙职教城新校区)，总建筑面积20.8万平方米。校园环境优美，香樟成行，配套了以中医药为主体的环境设施，校园绿化美化全部采用药用植物，具有浓厚的中医药文化氛围。标准',168982);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南民族职业学院',' ','湖南',0,9,0,0,0,' ','暂空',' ','http://www.hnvc.net.cn/Article/Index.html','湖南民族职业学院于2003年7月由原岳阳师范学校升格为全日制普通高等院校。该院曾经是国家中等师范学校名校和全国先进师范学校，是教育部首批确定的全国20所小教大专班实验师范之一，也是教育部直接定点为西藏自治区培养专科层次小学教师的全国2所学校之一，2006年被评为全国教育援藏先进单位。升格转型后，学院的办学思路是做强教师教育，做优职业教育，做特民族教育，做活成人教育。学院设置了人文系、信息技术系、外语系、艺术系、教育系、成教部7个系部，4500名在校学生分别来自全国18个省、市、自治区的26个民族',111275);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湘西民族职业技术学院',' ','湖南',0,9,0,0,0,' ','暂空',' ','http://www.xxmzy.com','湘西民族职业技术学院是经湖南省人民政府批准，教育部备案，由原湘西自治州农校、吉首经济贸易学校和湘西民族水利电力工业学校合并组建的一所公办全日制普通高等学校，为副厅级机构，由州人民政府主办主管，教育业务归省教育厅管理。迄今有52年办学历史，为社会培养了4.2多万名毕业生。学院系全国劳动力转移培训十大示范基地之一和全国青年创业培训基地。学院占地面积1074.2亩，分南校区、北校区、水电校区和新校区，建筑面积16.2万平方米，校园依山傍水，环境优美，是读书、治学的理想园地。学院坚持“以服务为宗旨，以就',27615);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('衡阳财经工业职业技术学院',' ','湖南',0,6,0,0,0,' ','暂空',' ','http://www.hycgy.com','衡阳财经工业职业技术学院是2004年5月经湖南省人民政府批准、教育部备案、衡阳市人民政府主管的一所全日制公办普通高等职业院校。学院现分为院本部（座落在衡阳市珠晖区狮山路20号）和实训基地（座落在衡阳市雁峰区芝麻塘1号）两个校区，共有全日制在校学生6952人，成教学生2163人。学院占地面积700亩，建筑面积16万平方米；固定资产价值2亿多元，教学仪器设备总值2800多万元，教学用计算机1184台，拥有标准的教学楼、电教中心、学生公寓、学生食堂，建有先进的校园网络系统、双向闭路电视系统、现代远程教',164074);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('益阳职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.yyvtc.cn/','湖南省益阳职业技术学院（代码：4743）是经湖南省人民政府批准、并报教育部备案的国家全日制普通公办高等院校。座落在世界羽毛求冠军的摇篮、文化名人“三周”和著名教育家张国基先生的故乡、美丽的山水银城——益阳市。近年来，学院始终坚持立足立场、质量为本、特色立校的办学理念，不断创新办学模式和人才培养模式，各项工作均取得了比较理想的成绩，先后获“全国德育工作先进单位”、“湖南省职业教育与成才教育先进单位”、“湖南省毕业生就业工作先进单位”、“首届湖南社会信赖学校”等一系列殊荣。学院环境幽雅，教学设施先进',40478);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('肇庆医学高等专科学校',' ','广东',0,3,0,0,0,' ','暂空',' ','http://www.zqmc.net/?page_id=4963','经国家教育部和广东省人民政府批准，国家级重点中等职业学校－－广东省肇庆卫生学校于2004年5月升格为肇庆医学高等专科学校。升格后的肇庆医专属公办全日制普通高等学校，坐落于国家优秀旅游城市、历史名城－广东省肇庆市城区。学校经过几十年的建设发展，校园绿树成荫，环境优雅，教学设施完善，师资力量雄厚，是一所集教学、医疗、科研和培训于一体的高等专科学校。从普通中专??国家重点中专??到医学高等专科，经历了４６年风雨历程的肇庆医学高等专科学校，在广东省人民政府、教育厅、卫生厅的关怀下，在肇庆市委、市政府的全',516730);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('琼台师范学院',' ','海南',1,4,0,0,0,' ','暂空',' ','http://www.qttc.edu.cn','琼台师范高等专科学校是经教育部批准成立的公办全日制普通高等学校，其前身琼台书院始建于清康熙四十四年（1705年），迄今已有三百余年历史。琼台书院是清代琼州唯一府立书院，是海南探花、进士和举人的摇篮。三百年来，琼台勇立历史潮头，传播先进文化，推进社会进步，为南国边陲地区经济建设和社会发展培育了众多英才，铸就了誉满海内外的“百年师范”。学校是海南省唯一承担小学幼儿园师资培养培训的师范专科学校。2010年起开始承担“国培计划——海南省农村小学骨干教师置换脱产培训”工作，2011年被指定为海南省幼儿师资',447198);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川商务职业学院',' ','四川',0,6,0,0,0,' ','暂空',' ','http://www.scsw.net.cn','四川商务职业学院是经省人民政府批准成立、经国家教育部备案、直属省商务厅的公办全日制普通高等学校，是一所集内外贸于一体的财经类商科高等院校，是四川省重要的现代商务高素质高技能人才培养培训基地。悠久的职教历史学院前身为成都女子蚕桑传习所，创建于1907年(清光绪33年)，先后更名为四川省立女子实业讲习所、四川省立高等职业学校、四川省立成都女子职业学校、四川省成都商业学校等。1993年更名为四川省商业学校。2004年经四川省人民政府批准，四川省商业学校(国家级重点中专)与四川省对外经济贸易学校(省部级',299879);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川司法警官职业学院',' ','四川',0,11,0,0,0,' ','暂空',' ','http://www.sjpopc.net','悠悠旌城，崇德尚阳;勃勃警院，杏坛之光。四川司法警官职业学院是一所公办政法类全日制普通高等学校，隶属于四川省司法厅。经过三十余年的建设与发展，现已成为四川省司法行政系统学历教育基地、政治教育基地、科研基地和干警培训基地。发展区位显要服务功能拓展学院现坐落于成都半小时经济圈、国家高等职业教育综合改革试验区、国家园林城市——四川省德阳市，地理位置优越，交通便利，环境优美。2009年学院被中央政法委、司法部等十五部委列为全国政法干警招录培养体制改革试点院校，2011年被德阳市人民政府立项为“德阳国家高',224406);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广安职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.gavtc.cn/','广安职业技术学院是经四川省人民政府批准、广安市人民政府主办的全日制公办普通高等学校，是教育部、四川省重点支持建设院校和四川省第一所“部、省、市共建”高职院校，2013年被省教厅、财政厅列为“四川省省级示范性高职院校”建设单位。学院现有在校学生8900余人。2014年开始面向全省单独招生。悠久的办学历史学院前身为1906年创建的四川省岳秀女学，有着百余年的办学历史，著名革命英雄传奇小说《红岩》中“双枪老太婆”的原型人物陈联诗、邓惠中均毕业于此，文化底蕴深厚。百年历史，百年辉煌，新的广安职业技术学院',153352);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川信息职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.scitc.com.cn/','四川信息职业技术学院隶属于四川省经济和信息化委员会，是经四川省人民政府批准、教育部备案的全省唯一一所电子信息类公办高等职业院校，面向全国招生，现已建立起大专教育、成人(继续)教育、中专教育等多层次办学体系，是培养生产、建设、管理、服务一线高技能人才的摇篮。学院地处中国历史上唯一的女皇武则天诞生之地——广元。学院始建于1976年，时称广元无线电技工学校，1994年成立四川省电子工业学校，2004年成立四川信息职业技术学院，2009年11月通过人才培养工作评估。2013年7月1日，省教育厅、财政厅联',135882);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川工业科技学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.scimvc.cn/','四川警安职业学院是经四川省政府批准、教育部备案的面向全国招生的全日制综合性普通高校。现设有普通高职高专、成人大专、自学考试本、专科班。学院位于四川省历史文化名城绵竹新市经济开发区，毗邻风景秀丽的３０００亩剑南春森林公园。经过十多年的艰苦创业，励精图治，学院目前拥有４００余亩的独立生态校园，2０余万平方米的欧式建筑，固定资产１.３亿元。2006年国家计划内统招在校生将达到6000余人。校内鸟语花香、树木苍翠、绿草盈盈、泉水潺潺，园林式的氧吧校园，是理想的学习修身之地。学院设有经济管理系、旅游管理系',236097);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黔西南民族职业技术学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.qxnzy.net/','2004年4月，学院经省人民政府批复同意由原州农业、水电、农机化、财贸和卫生5所普通中等学校合并组建。2008年11月，贵州省黔西南民族行政管理学校并入学院。2011年6月，经省人社厅批准，州人民政府以学院为依托，成立黔西南技师学院。学院占地面积563亩，现分两个校区办学。下设生物工程系、机械与电子工程系、水利电力工程系、商务系、医药系、护理系、社会管理系及成人教育与培训部8个教学机构。现有在职职工430人，专任教师322名，正高职称8名，副高职称130名，讲师研究生以上学历41人。全日制在校学',63383);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州轻工职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://www.gzqy.cn/','■全国第四届黄炎培优秀学校■全国职业教育师资培养培训重点建设基地■贵州省省级示范性高等职业技术院校■贵州省高职高专思想政治教育评估优秀院校■贵州省外派劳务培训基地■微软IT学院贵州省唯一授权点■贵州省2015年唯一一所入围全国就业50强高校贵州轻工职业技术学院2004年2月经贵州省人民政府批准成立，隶属于贵州省教育厅，占地1000亩，规划建筑面积39万平方米，校园坐落在贵州花溪大学城，是一所以举办高职教育为主，同时举办中等职业教育、职前职后培训，集工程技术、信息技术、工艺美术、经济管理、旅游服务',122074);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西警官职业学院',' ','陕西',0,11,0,0,0,' ','暂空',' ','http://www.sxjgxy.edu.cn/www/index.asp','陕西警官职业学院是经陕西省人民政府批准、教育部备案、由陕西省教育厅主管的全日制公办高等职业院校，主要实施高等职业学历教育，同时承担在职干警培训业务。学院坚持以创建应用型学院为目标，立足为公安、司法行政机关服务，培养政治素质良好、专业基础扎实、实战能力强、勇于奉献，能胜任基层公安、司法行政等政法工作的应用型、职业型人才。学院设有五系五部一馆一中心等12个教学及辅助机构，包括警察管理一系、警察管理二系、法律系、警察技术系、经济管理系；科研部、思想政治理论教学研究部（基础部）、警体部、培训与继续教育部',804727);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('忻州职业技术学院',' ','山西',0,0,0,0,0,' ','暂空',' ','http://www.xzvtc.com/','忻州职业技术学院是2004年6月，经山西省人民政府批准、国家教育部备案、整合原忻州商校、忻州卫校、忻州农机校和忻州艺校四所中等专业学校，成立的高等职业院校。是忻州市“十一五”规划重点建设项目之一。新校区坐落于忻州市经济技术开发区，占地485亩。一期工程建筑总面积约13万平方米，在建工程约2万多平方米，包括教学大楼、实习实训中心、学生公寓楼、地下餐厅、中心广场等，建设规模可满足近万名学生的学习和生活需要。学院现设护理系、医学系、财经系、机电系、计算机应用系、艺术系、应用美术系、管理系和基础部，有专',76659);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北女子职业技术学院',' ','河北',0,5,0,0,0,' ','暂空',' ','http://www.hebnzxy.com/','河北女子职业技术学院(原河北省妇女干部学校)是河北省唯一的国办全日制女子高等学府。始建于1948年，“文革”期间停办，1984年恢复重建，2004年经河北省政府批准、国家教育部备案升格为大学专科层次的普通高等院校。多年来我院坚持特色办学，以教学为核心，以就业为导向，严谨治校、务实进取，为社会培养了大批优秀人才。学院现校址坐落于省会西南高教区新石中路，新校区坐落在石家庄市西南高教区友谊南大街，总建筑面积60410平方米，分为教学区、办公区、生活区、实习区、运动区。其中教学用房（教学楼、实验楼、图书',131747);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古交通职业技术学院',' ','内蒙古',0,1,0,0,0,' ','暂空',' ','http://www.nmjtzy.com/','内蒙古交通职业技术学院(内蒙古民族交通技师学院)是一所公办全日制高等职业院校和自治区首所技师学院。经过多年发展建设，学院综合办学实力稳步增强，人才培养水平逐年提升，现在是全国交通类职业教育示范院校、国家高技能人才培训基地、技能型紧缺人才培训基地和职业技能鉴定站、人社部一体化课程教学改革试点院校、教育部首批中德职业教育合作项目院校、内蒙古首批建设的7所示范性高等职业院校之一、内蒙古“草原英才工程”高层次人才创新创业基地、内蒙古职业资格认证培训、技师培训和专业技术人员继续教育基地，先后获得“全国职业',120173);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西经贸职业技术学院',' ','广西',0,6,0,0,0,' ','暂空',' ','http://www.gxjmxy.com/','广西经贸职业技术学院是经广西壮族自治区人民政府批准成立，公办的全日制普通高等职业院校(学历层次大专，机构级别为副厅级事业单位）。学院青山校区坐落在风景秀丽青秀山脚下，交通十分便利。正在筹建中的五合校区位于南宁市五合大学城，占地815亩，地势开阔，环境优雅，是教学、生活及健身的理想场所。学院以财经、商贸类专业为主体，以旅游、电子信息类等相关专业为辅。开设有贸易与管理系、财会金融系、信息工程系、旅游与外语系、艺术设计与建筑管理系、基础部、思想政治理论课教学部等七个教学系、部，有物流管理、会计电算化、',102845);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西工商职业技术学院',' ','广西',0,6,0,0,0,' ','暂空',' ','http://www.gxgsxy.com','广西工商职业技术学院是经自治区人民政府批准成立、国家教育部备案的一所国家公办全日制普通高等学校。学院座落在首府南宁，傍临景色秀丽的邕江。学院办学始于1953年，历经63年的传承发展，现已发展成为以财经管理大类专业为主体的高等职业技术学院。学院现有三个校区。其中校本部位于南宁市鹏飞路15号，占地面积15.11万平方米;东校区位于南宁市中尧路15号，占地面积4.86万平方米;西校区位于南宁市新村路8号，占地面积2.15万平方米。三个校区共计占地面积22.12万平方米，校舍总建筑面积17.39万平方米',75716);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西演艺职业学院',' ','广西',0,8,0,0,0,' ','暂空',' ','http://www.gxart.cn ','广西演艺职业学院是经广西壮族自治区人民政府批准、教育部电子注册、国家承认学历，以艺术教育为主，兼有商学类、文理类教育的一所全日制普通高等院校。学院在广西艺术学校的基础上组建而成，拥有一支治学严谨、经验丰富的教师队伍和一支年富力强、敬业爱岗的管理团队。现设有三个系，20多个专业方向，正向着艺术类、商学类、文理类、电子信息类教育等多学科、综合化方向发展。',31435);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西外国语学院',' ','广西',1,0,0,0,0,' ','暂空',' ','http://www.gxufl.com/','广西外国语学院坐落在中国—东盟博览会永久会址，北部湾经济区中心城市，广西壮族自治区首府——南宁，是一所经教育部批准成立的全日制普通本科学校(二本)，前身是广西东方外语职业学院，成立于2004年;2011年4月，经教育部下文批准同意将广西东方外语职业学院升格为本科高校并更名为广西外国语学院。学校占地面积2315多亩，校舍面积33多万平方米，全日制在校生14000多人，其中国外留学生282人，建有25000多平方米的先进数字化图书馆，87个专业实验室，65个实践教学基地，其中国外实践教学基地25个，',208504);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西电力职业技术学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.gxdlxy.com','广西电力职业技术学院是由广西壮族自治区人民政府举办，经国家教育部批准设立，直属广西壮族自治区教育厅管理的一所全日制公立普通高等学校。其前身为南宁电力学校、广西电力技工学校、广西电力培训中心三位一体，始建于1979年，原隶属国家电力工业部，由广西电力工业局直接管理。2001年起划转广西壮族自治区政府管理，直属自治区教育厅。2004年，经广西壮族自治区人民政府批准升格为高等职业学校，是广西特色高校建设项目立项建设高校和自治区示范性高等职业院校重点培育单位。办学三十多年来，在上级领导和社会各界的热情关',84057);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京理工大学泰州科技学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.nustti.edu.cn/','南京理工大学泰州科技学院坐落于苏中美丽的凤凰城—江苏省泰州市主城区，是在国防科工委、江苏省委、省政府大力支持下，经教育部批准、由南京理工大学与泰州市政府合作举办的全日制本科普通高校，是一所具备现代办学理念和科学育人模式的以工为主，理、工、经、管、文等多学科综合配套、协调发展的独立学院。国家首批“211工程”建设院校——南京理工大学全面负责学院的教学与管理，泰州市政府负责投资建设，毕业生颁发国家统一印制的南京理工大学泰州科技学院本科毕业证书，符合南京理工大学学位授予条件的，授予南京理工大学学士学位',468768);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京师范大学泰州学院',' ','江苏',0,4,0,0,0,' ','暂空',' ','http://zs.nnutc.edu.cn/','南京师范大学泰州学院是一所于2004年6月经国家教育部批准成立的普通高校本科层次独立学院，由南京师范大学和泰州高教投资发展有限公司共同组建。学院实行董事会领导下的院长负责制。博士生导师陈双林教授担任院长、法定代表人，郭宁生同志担任党委书记。经过11年的砥砺奋进，学院正在由规模扩张型向内涵提升型转变，取得了可喜的办学成果，学院成为泰州历史上第一座万人本科大学，先后荣获“全国先进独立学校”和“中国民办高等教育优秀院校”等称号，《人民日报》《光明日报》《中国教育报》多次报道我院办学业绩。学院办学规范，',743933);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东莞理工学院城市学院',' ','广东',0,1,0,0,0,' ','暂空',' ','oldname": "东莞理工学院城市学院','东莞理工学院城市学院是2004年6月经国家教育部批准成立的独立学院。学院的举办者东莞理工学院是广东东莞的第一所普通本科院校，是省市共建的大学，是教育部第一批“卓越工程师培养计划”实施高校（全国61所，广东3所）,2010年8月获批成为广东省立项建设的新增硕士学位授予单位。东莞理工学院对城市学院的教学组织和管理负责，委托管理干部担任学院领导，遴选优秀专业教师负责教学工作，指导学科专业建设，打造专业品牌。举办学校深厚的文化底蕴、优良的教学资源，为城市学院高起点运作、跨越式发展创造了条件。学院投资方广',361675);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽财贸职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.aftvc.com/','安徽财贸职业学院是安徽省人民政府批准设置、教育部备案的公办全日制普通高等学校，2008年在安徽省高等职业院校人才培养工作评估中获优秀等次，2010年被确定为安徽省示范性高等职业院校建设单位，2011年被确定为全国供销合作社示范性高等职业院校建设单位。学院创建于1964年，始终秉承“明德厚学、知行合一”的校训，遵循“专家治学、名师兴校、科研强校、特色扬校”的办学方针，坚持“以就业为导向、以创新为动力、以质量为根本、以特色为生命”的办学理念，把握“立足行业、面向社会、开放办学”的定位，积极为供销合作',350646);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽国际商务职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.ahiib.com/','安徽国际商务职业学院是公办全日制普通高等院校，行政上隶属于安徽省商务厅，业务上接受安徽省教育厅指导，具有高等职业技术专科学历教育招生资格。学校位于安徽省省会合肥市，总面积552亩，其中新校区占地502亩。教职工300余人，副教授以上职称66人，硕士以上学位教师133人，省级专业带头人10人，省级教学名师6人，省级优秀教师5人，具备双师素质的教师149人。学校现有在校生1万余人。设有三院四系二部——国际贸易学院、商贸流通学院、继续教育学院、商务外语系、信息服务系、财会金融系、公共管理系、思政教研部',151575);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽林业职业技术学院',' ','安徽',0,2,0,0,0,' ','暂空',' ','http://www.ahlyxy.cn','安徽林业职业技术学院是公办全日制普通高等院校,其前身是安徽省合肥林业学校，创建于1952年，是新中国成立后我国建立的第一批大中专院校之一，曾经是原国家林业部直属六所中专学校之一，有着近60年的办学历史。2004年6月，经安徽省人民政府批准独立升格为安徽林业职业技术学院。学院坐落在合肥市大蜀山风景区，依山面湖，与国家级合肥高新技术产业开发区相邻，交通便利，环境优美，人文汇萃，为省级花园式单位，是我省唯一一所培养以林业学科为主体多学科协调发展的高等职业技术学院。学院现占地面积约150亩，校舍建筑面积',64905);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽审计职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.ahsjxy.cn/','学院简介地理位置学院坐落于安徽省合肥市经济开发区。合肥是国家四大科教城市之一，自然景色锦绣多姿，文化古迹众多，交通便利。办学性质学院是全国唯一一所公办全日制审计高等职业学院，隶属于安徽省审计厅，业务上接受安徽省教育厅指导。办学规模学院是安徽省直文明单位，现拥有包河和方兴两个校区，校园总面积262350平方米;建筑面积107390平方米，其中，学生宿舍面积44720平方米，实习实训场地面积14919平方米;教学仪器设备总值2775万元;图书馆馆藏图书108.98万册(其中电子图书81万册，纸质图书',106715);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽新闻出版职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahcbxy.cn/','安徽新闻出版职业技术学院是经安徽省人民政府批准成立的公办全日制普通高等职业技术学院，隶属于安徽省新闻出版局，是中德两国政府合作创建的新闻出版印刷包装行业产学研一体化的职业教育培训基地，被国家新闻出版总署确定为“数字出版高端人才培养基地”和“全国印刷复制高技能人才培训基地”。学院坐落在合肥经济技术开发区，占地11.5万平方米，校舍建筑面积11.72万平方米，建有教学楼、办公楼、学生公寓楼、学生食堂、实验实训中心、图书馆、校办实习工厂等校舍和体育运动设施，以及新闻采编、数字媒体设计、电子编辑、在线出',41156);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽邮电职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahptc.cn/','安徽邮电职业技术学院是2004年经安徽省人民政府批准、教育部备案的一所专科层次的公办全日制高等职业学院，为目前安徽省内惟一的通信类高等院校，也是华东地区首家独立设置的高等通信职业学院。学院前身是有着60多年历史、被誉为“安徽邮电黄埔军校”的安徽省邮电学校，坐落在安徽省省会合肥市文教单位聚集的蜀山区潜山路382号，校园环境优美，建筑典雅，花木繁茂。周边交通便捷，有3、8、22、快速公交4、48、124等多条公交线路直达，长江路高架和未来轨道交通途经此地。学院现有教职员工300余名，专职教师120余',60425);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽工业职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahip.cn','安徽工业职业技术学院是经安徽省人民政府批准成立的一所以高等职业教育为主，集高职教育、成人教育、技能培训等为一体，涉及工学、经济学、管理学等多门类学科的公办全日制普通高等职业院校。学院地处安徽省铜陵市。于1956年建市的铜陵位于长江中下游南岸，是中华民族青铜文明的发祥地之一，黄山、九华山近在咫尺，是典型的江南山水园林城市。经过五十年奋发图强的建设尤其是近二十年来改革开放春风的沐浴，铜陵市的人均国内生产总值、财政收入、进出口贸易额、居民收入等几项指标已稳居安徽省前矛，已成为皖江地区一颗璀璨的明珠。学',58688);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江国际海运职业技术学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.zimc.cn/','浙江国际海运职业技术学院坐落在风景秀丽的“海天佛国、渔都港城”——舟山，毗邻国际大都市上海，是一所以航海类专业为重点，港口物流、船舶工程、海洋旅游专业并进发展的普通高等职业技术学院。浙江国际海运职业技术学院目前占地499亩，建筑面积9万多平方米，全日制学生6200余人。学院教学、图书、科研、生活设施齐全，拥有先进的船舶操纵模拟器等实验设备和海上训练基地等实训场所。学院开展国家海事局批准的各类船员培训项目，其中驾驶台资源管理、机舱资源管理、电子海图等培训项目走在了全国同行的前列。学院是香港船东会与',52692);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江体育职业技术学院',' ','浙江',0,7,0,0,0,' ','暂空',' ','http://www.zjcs.net.cn/login.php','浙江体育职业技术学院是一所由浙江省体育局主管，接受浙江省教育厅指导，以竞技体育专业为特色，以培养一流竞技体育人才为根本任务的省属全日制高职院校。经浙江省人民政府批准，学院于2004年9月在合并浙江职工体育运动技术学院、浙江省体育运动学校和浙江省体育局萧山体育运动训练基地的基础上筹建。2006年1月，浙江省体育训练一大队、浙江省体育训练二大队和浙江省田径运动管理中心实质性并入筹建中的浙江体育职业技术学院。同年7月，浙江省人民政府批准浙江体育职业技术学院建校。作为一所以培养一流竞技体育人才为根本任务',47168);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('济南工程职业技术学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.jngcxy.com/','济南工程职业技术学院是2004年7月经山东省人民政府批准，面向全国17个省市招生的公办全日制普通高等院校，隶属济南市政府。学院位于济南市经十东路6088号，占地面积50万平方米，总建筑面积24万多平方米，绿化面积10多万平方米，拥有满足各专业教学需要的实验实训室、语音室、多媒体教室及计算机中心、图书信息楼等现代化教学设施，建立了数字化校园，宽带网络覆盖全院。现有全日制高职在校生九千余人。学院师资力量雄厚，拥有一支结构合理、规模适度，以中青年教师为主体的骨干教师队伍，同时聘请一批既有理论水平又有实',239927);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东电子职业技术学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdcet.cn/','◎山东省技能型人才培养特色名校全国职业技术教育先进单位国家计算机应用及软件技术技能型人才培养学校国家电子信息产业高技能人才培训基地教育部—中兴通讯ICT行业创新基地齐鲁软件园软件人才培养基地山东省学生资助先进单位新浪教育盛典——2016年度山东最具就业竞争力高职院校通过ISO9000质量管理体系认证……◎公办省属全日制普通高等学校，国标代码13856山东电子职业技术学院创建于1978年，是山东省成立最早、规模最大、专业最全、师资最优、就业最好、综合实力最强的电子信息类高等职业示范性院校。经过39',108817);
