insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泰山职业技术学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.mtotc.com.cn/','泰山职业技术学院是经山东省人民政府批准、教育部备案、泰安市人民政府举办的全日制普通高等院校。学院坐落于世界自然与文化双遗产、五岳之首--泰山脚下、天烛峰风景区内，地处环山路和泰明路相交地段，依山傍城，交通便利。校园为园林式设计格局，建筑群错落有致，环境幽雅，景色秀丽。学院占地面积982亩，校舍建筑面积156687平方米，建有高标准教学楼、实验实训楼、图书馆、体育场、大学生生活中心等设施。固定资产35167万元，教学仪器设备总值3322万元。教学用计算机1403台，多媒体教室和语音室座位3895个',101194);
