insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南工商职业学院',' ','海南',0,0,0,0,0,' ','暂空',' ','http://www.jntbc.edu.cn','海南工商职业学院是教育部备案的全日制普通高等职业院校，坐落在海南明代先贤唐胄创办的养优书院原址——海南四大文化名村之一的海口攀丹村。学院前身为2004年创办的海南万和信息职业技术学院。2010年4月，教育部批准海南万和信息职业技术学院更名为“海南工商职业学院”。2012年5月，唐胄后人唐捷先生承继先辈“耿介、廉洁、孝顺”品质风徽，全资受让海南工商职业学院并出任法人代表和董事长。学院秉持“养优”之校训、“自强弘毅”之校风、“立德尚教”之教风、“向学笃行”之学风，坚持“师生为本，人才强校，质量立校，',67565);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南信息统计职业学院',' ','河南',0,0,0,0,0,' ','暂空',' ','http://www.hnisvc.com','河南信息统计职业学院是中国大陆地区唯一的一所以信息、统计为主要特色的财经类职业学院。学院前身河南省计划统计学校，创建于1951年，已有60多年的统计教育办学历史，其中举办高职教育已有12年。学院位于河南省郑州市，地理位置优越，交通便利，校园环境优美。在长期的办学过程中，学院坚持立足河南，辐射全国的办学方向，为社会输送了上万计的以会计、统计专业为主的财经类应用型人才，毕业生在会计、统计及其他经济管理行业大多成为骨干力量。学院师资力量雄厚，有享受国务院特殊津贴的专家1人，省职业教育专家2人。有一支由',43400);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京航空航天大学北海学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.bhbhxy.com/','北京航空航天大学（简称：北航）是全国首批重点大学、“985工程”和“211工程”重点建设院校，拥有博士学位授权点63个，硕士学位授权点144个，院士14人，长江学者26人，博士生导师471人。自1952年建校以来，北航发展成为以工为主，理、工、文、经、管、法、哲、教等多学科综合配套、协调发展的多科性、开放式、研究型大学。北京航空航天大学北海学院（简称：北海学院）是北航在广西北海市建立的本科独立学院，于2005年经教育部批准开始招生。北海学院不仅是一所全日制普通高等院校，更是我国优质高等教育资源支',251187);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国地质大学长城学院',' ','河北',0,12,0,0,0,' ','暂空',' ','http://www.cuggw.edu.cn','中国地质大学长城学院是经国家教育部批准成立，由中国地质大学（北京）与保定贺阳教育投资有限公司合作，按新机制、新模式举办的独立学院。学校办学层次为全日制普通本科，纳入国家普通高等学校统一招生计划，面向全国招生，目前在校生14000多人。学校地处历史文化名城—素有北京南大门之称的河北省保定市，保定作为首都功能疏解的集中承载地和京津产业转移的重要承载地，与京津形成京津冀城市群的核心，区位优势得天独厚。学校占地面积1400余亩，建筑面积45.6万平方米，具有齐全的教学基础设施、文化体育设施和学生综合服务',1992711);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海师范大学天华学院',' ','上海',0,0,0,0,0,' ','暂空',' ','http://www.sthu.cn','上海师范大学天华学院于2005年4月经国家教育部批准建校，由上海师范大学、上海天华教育文化投资有限公司合作举办,是一所本科层次的独立学院，学制四年，独立招生，独立颁发毕业证书。上海师范大学负责培养目标、专业教学计划的制定和教学组织工作。学院首期总投资已达到4亿元人民币,占地面积570亩,教学设施先进,运动场地宽敞,校园环境优美,四周天然河道环绕,绿树掩荫,是现代大学生理想的学习场所。生活区由上海天贤教育后勤服务有限公司负责投资和管理,学生公寓内空调和热水器等设施齐全。专业设置学院设有工学、管理学',1042805);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西北工业大学明德学院',' ','陕西',1,0,0,0,0,' ','暂空',' ','http://www.npumd.cn','西北工业大学明德学院是国家教育部批准设立的全日制本科层次的独立学院。学院地处西安市长安区，秦岭北麓、沣河东侧，毗邻西安高新技术产业开发区、西北工业大学长安校区，占地610亩，校舍建筑面积21.6万平方米。学院师资力量雄厚，教学仪器设备先进，办学基础设施优良。园林化、信息化、人文化的校园环境建设，标志性的图书馆大楼、实践教学中心，良好的体育运动场所以及学生公寓，优质的后勤服务等，为师生员工营造了舒适、优雅的学习、工作和生活环境。学院依托西北工业大学雄厚的综合办学优势，根据国家和地区经济建设与社会发',1097189);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津师范大学津沽学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.jinguxy.cn/','天津师范大学津沽学院（以下简称津沽学院）是经国家教育部批准、由天津师范大学和福建省海滨教育投资有限公司按照新型办学模式和运行机制举办的全日制本科层次独立学院。津沽学院于2005年开始招生，现已有8个系21个本科专业，在校学生5200余名。学院坐落于天津师范大学主校区内，占地面积400余亩，有独立的办学条件和基础设施。优越的地理环境为津沽学院实现与母体校的资源共享提供了得天独厚的办学优势。津沽学院坚持“以人为本，从严治校，以就业为导向，以质量求生存，以特色谋发展”的办学理念，努力为社会培养高素质应',800202);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津理工大学中环信息学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjzhic.com/','天津理工大学中环信息学院系国家教育部2005年4月批准成立的全日制本科层次普通高校，由天津理工大学和天津中环电子信息集团有限公司合作兴办。学院坐落于渤海之滨天津市，校园环境优雅，文化气息浓厚，办学条件优良。先进的教学设施，丰富的图书馆藏，仿真的实验环境，完备的运动场地，是莘莘学子读书求学的理想场所。学院重视学科专业建设，始终把提高教育质量放在首位，依托天津理工大学在本科教学方面的优势，设置了自动化工程、机械工程、电子信息工程、计算机工程和经济与管理5个系，开设自动化、电气工程及其自动化、机械工程',651860);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京科技大学天津学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://tj.ustb.edu.cn/','北京科技大学天津学院是2005年经教育部批准，由北京科技大学和广东珠江投资集团有限公司合作举办的本科层次的全日制独立学院。学院依托北京科技大学优质教育资源，实施“应用型”理论教学和以“职业能力培养为主线”的实践教学，培养适应经济和社会发展需要的理论基础扎实、实践技能强、综合素质高并具有创新精神的应用型本科人才。合作方广东珠江投资有限公司及其企业联盟涉及房地产、基础设施、物流、教育、通讯、交通、能源等多个行业，投资版图已拓展到北京、天津、上海、深圳、西安、成都、长沙等各主要中心城市。学院位于天津市',1188408);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京中医药大学东方学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.df-college.com','北京中医药大学东方学院(国标码：13899以下简称东方学院)系北京中医药大学(教育部直属重点大学、国家唯一的中医药“211”工程建设高校)申办，2005年教育部批准建立的全日制普通本科学院。学院现有廊坊、渤海两个校区，占地1300亩，建筑面积32万平方米，已建成中医学、中西医临床医学、针灸推拿学、护理学、医学检验技术、中药学、中药制药、中草药栽培与鉴定、工商管理、市场营销、公共事业管理等11个本科专业。2014年经河北省教育厅批准，新增“护理”、“医学检验技术”等8个专科专业。2007年上级党委',1250148);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁财贸学院',' ','辽宁',1,6,0,0,0,' ','暂空',' ','http://www.lncmxy.com/','辽宁财贸学院位于美丽的海滨旅游疗养胜地—兴城海滨风景区。这里，面对一望无际的渤海，背靠郁郁葱葱的首山，西临六百年宁远古城，遥望佛岛袅袅青烟。坐拥城、泉、山、海、岛，自然景观、人文景观合于一身，历史文化底蕴丰厚，气候宜人,距高速公路、高速铁路仅5公里，交通便利，是理想的办学之地。辽宁财贸学院是2008年9月经教育部批准全国首批由独立学院转设的民办普通高校，前身为沈阳师范大学渤海学院。辽宁财贸学院自成立以来，全面贯彻党和国家的教育方针，坚持社会主义办学方向，坚持办学的公益性原则，坚持办学为地方经济建',615254);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京邮电大学世纪学院',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.ccbupt.cn/','北京邮电大学世纪学院培养信息科技时代英才北京邮电大学世纪学院是经教育部批准，由北京邮电大学与北京学涵教育科技有限公司按照新的机制和新的办学模式合作举办的全日制本科普通高校，是教育部直属高校在京举办的第一所独立学院。学院位于延庆县康庄镇，现有校园占地500亩，建有充足的教学及辅助用房，独立的图书馆大楼及实验楼。学院现设有通信与信息工程系、电子与自动化系、计算机科学与技术系、经济管理系、艺术与传媒学院、外语系、基础教学部、国际学院8个教学单位，现设有通信工程、计算机科学与技术、电子科学与技术、机械工',1266365);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中山大学新华学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.xhzsb.net/','中山大学新华学院(院校代码:13902)是中山大学依据教育部教发〔2003〕8号文的精神，与广东东宝集团公司按新机制新模式申办、经教育部批准设立的涵盖文、理、医、工、经、管、法、艺等学科的多科性独立学院(教发函〔2005〕68号)。学院现有两个校区。广州校区位于广州市天河区龙洞华美路与广汕一路交界处，规划占地面积385亩，教学设施齐备，交通十分方便，与中山大学校园网实现互联，共享中山大学丰富的信息资源。东莞校区位于与广州市黄浦区一江之隔的东莞市麻涌镇，规划面积1800亩，其中教学用地1200亩，',1124669);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川大学锦城学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.scujcc.com.cn/','四川大学锦城学院是2005年由四川大学申办、经教育部批准成立的全日制普通本科高等学校，是在川大百年老校的基础上建立的高起点、高水平、实现跨越式发展的多学科、综合性的应用型、创业型大学。学校遵循“传承知识、培养人才、引领社会、服务大众”的办学宗旨，秉承“做人与做事结合、传统与现代结合、通识与专业结合、严格与宽松结合、秩序与自由结合、传承与创新结合”的办学理念，以培养“高素质、复合型、经世致用”的应用型人才为目标，致力于办特色鲜明、优势学科专业突出、社会认可度高的一流应用型本科高等学校。高起点200',2535804);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京工业大学耿丹学院',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.gengdan.cn/zs/zsw/','北京工业大学耿丹学院经教育部批准成立于2005年7月，是北京工业大学与北京耿丹教育发展中心合作举办的独立学院，是按新机制、新模式运行的全日制普通本科院校。学院以中国早期教育家、革命家、留英博士——耿丹烈士的名字命名。学院位于北京市顺义区，交通便利。作为北京市“十一五”时期重点建设的卫星新城，这里拥有现代汽车生产基地、空港物流基地、中国北方微电子生产基地、北京奥运会水上运动场馆、中国国际展览中心和2009年第七届中国花卉博览会的主展馆；这里得天独厚的区域优势、多元化的产业结构、优质的外部环境为学院',768904);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京工业大学浦江学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://zs.njpji.cn/','浦江学院是1998年经江苏省首批批准成立的公有民办二级学院，2005年经教育部批准改办为独立学院，学院的教育、教学、学籍管理等纳入南京工业大学统一管理。南京工业大学的三位院士为学院的名誉院长，成立了由工业大学党委书记、校长和各个专业学院专家组成的教学委员会。任课教师、管理干部从南京工业大学的教师干部中选聘，且大部分为教授、副教授。学院依托南京工业大学的学科基础，设置了计算机科学与技术、电子信息工程、建筑学、国际经济与贸易等专业近40个。目前，学院有在校生8000余人，已毕业学生就业率高，受到用人',673559);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京师范大学中北学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.nnuzc.cn/zhaosheng/','南京师范大学中北学院是经国家教育部批准，由南京师范大学和南京师范大学教育发展基金会共同举办的独立学院。学院坐落于南京紫金山东麓的仙林大学城内，与南京师范大学仙林校区比肩而立。中北学院自1999年建院以来，秉承百年老校——南京师范大学优良的办学传统和先进的教育理念，依托南京师范大学的优质教学资源，独立组织教学，是南京师范大学本科生培养的有机组成部分。学院的学生与南京师范大学其他院（部）的学生共享学校的图书资料、实验设施、体育场馆、学生公寓等教育教学资源。中北学院现有文、理、工、管理、艺术等多个学科',471692);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南工商学院',' ','云南',1,1,0,0,0,' ','暂空',' ','http://www.yngsxy.net/','云南工商学院坐落于四季如春、风景秀丽的春城昆明，是一所具有独立颁发本科学历文凭资格、具有学士学位授予权、具有“专升本”招生资质的普通高校。也是一所师资力量雄厚，学科特色鲜明，教育质量和科研水平较高的应用型大学。学院主校区位于嵩明职教基地，占地1103亩，图书馆藏书150万册，教学、科研仪器设备、教学行政用房等资产总值近15亿元。学院下设12个二级学院，开设有50多个本、专科专业。目前已形成了以工学（建筑工程、计算机工程、机械工程等）和商学（经济学、会计学、管理学等）为优势特色，文理并重、多学科协',269723);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津工艺美术职业学院',' ','天津',0,8,0,0,0,' ','暂空',' ','http://www.gmtj.com/','天津工艺美术职业学院是经天津市政府批准、国家教育部备案成立的天津市唯一一所国办艺术类高职学院。学院历史悠久，具有坚实的办学基础和雄厚的师资力量，形成独特艺术设计教育的办学格局。学院拥有一支稳定的教师队伍，教授、副教授、讲师等高中级职称人数占教师总人数的80％。学院占地110亩，图书馆藏书10万余册，已成为培养现代工艺美术设计家的摇篮，为社会各界输送大量的具有较高专业艺术素质和专科层次的高素质、高技能应用型艺术设计人才。学院面向本市和全国招生，学制三年。学生学业期满成绩合格，颁发国家教育部统一印制',82891);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州现代信息工程职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gzmodern.cn','学院是2005年经广东省人民政府批准、教育部正式备案成立的全日制普通民办高职院校，是高职人才培养评估合格院校。学院拥有中央财政支持实训基地、省级高职教育实训基地、广州市信息工程专业继续教育基地、省级重点建设专业。学院位于广州市天河区，临近科学城，占地面积773亩，交通便利，环境优雅，是学习、生活的好地方。学院设有8系2部及继续教育学院，开设34个专业和专业方向,建有校内实训实习场所88个，先后与省内外200多家大中型企业进行了深度合作并建立了校外实习基地，成为教育部支持的西门子校企合作项目国内首',206700);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('晋中职业技术学院',' ','山西',0,0,0,0,0,' ','暂空',' ','http://www.jzzy.sx.cn/','晋中职业技术学院于2004年12月经山西省人民政府晋政函[2004]201号批复批准成立，是由原晋中农业学校、晋中财贸学校、晋中供销学校、晋中煤炭中专四所中专和并升格组建而成的公立高职院校。学院占地416亩，建筑总面积117457.10平方米，教学用房面积64986.45平方米，实习实训用房面积86187.60平方米。学校以原晋中农业学校为依托，新建建筑面积93633平方米，投资1.58亿元，建筑项目包括：教学办公楼、图书馆、实训楼、风雨操场、学术报告厅、阶梯教室、学生餐厅、学生公寓、综合服务楼',62728);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西华澳商贸职业学院',' ','山西',0,6,0,0,0,' ','暂空',' ','http://www.huaao.sx.cn','山西华澳商贸职业学院是经山西省政府批准、国家教育部备案的全日制高等专科院校。学院与澳大利亚教育部直属公立技术专科院校SWANTAFE学院及亚历山大理工学院合作，是中澳教育部承认的高等双语院校。学院坐落于山西首府太原市的教育园区，风景秀丽、环境幽雅、文化气息浓厚，是理想的育人环境和学习场所。学院教学设备齐全，师资力量雄厚，现有专兼结合、中外结合双师型教师142人，其中，副教授以上职称42人，英语外教4人，韩语外教1人，多位教师留学于美、英、德、加、澳、新西兰、日、韩等国。学院具有熟悉国际通用的教材',46522);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('乌海职业技术学院',' ','内蒙古',0,1,0,0,0,' ','暂空',' ','http://www.whvtc.net/structure/indx.htm','乌海职业技术学院创办于2004年，是经内蒙古自治区人民政府批准成立、国家教育部备案的全日制公办普通高等职业院校，受内蒙古自治区教育厅和乌海市人民政府双重领导。学院与内蒙古工业大学乌海学院是一个实体，两块牌子。学院是自治区首批示范性高等职业院校立项建设单位，并高质量的通过了教育部第二轮高职高专人才培养工作评估。学院占地730亩，分教学区、行政区、实验实训区、文体活动区、生活区等5个功能区域。完成建筑面积12.57万平方米，绿化面积16.33万平方米，运动场地面积8.94万平方米；教学行政用房近6万',74373);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春信息技术职业学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','oldname": "长春信息技术职业学院','长春信息技术职业学院是经吉林省人民政府批准、全国统招的普通高等学校，具有独立颁发专科学历证书资格。学院以专业化办学为发展方向，立足计算机软件及相关专业培养实用型人才。几年来学院积累了丰富的培养高级IT职业人才的经验，形成了一整套独具特色的教学模式、教学方法和管理模式。学院师资力量雄厚，有一支既有项目开发经验又有教学能力的双师型教师队伍，学院有教师180人，双师型教师95人。学院4个专业学科的学术带头人均由行业专家或教授担任。去年学院招收二年制高职学生906人，五年制高职学生50人。信息产业是国家',103981);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('松原职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.sypt.cn','松原职业技术学院座落在美丽的江滨城市——松原，是教育部备案，松原市唯一一所公办高等职业技术学院，由松原市政府和吉林油田市企共建，是国内职业教育的新型办学模式院校。经过几年的努力，适应经济社会发展对职业技术人才的需求，面向国内外就业市场，以优惠的发展政策、优势的专业支撑、优秀的师资、优良的设施、优质的就业服务，洋溢蓬勃现代气息、凝结浓郁人文情怀、汇聚和谐与奋斗精神的松原职业技术学院正崛起在松花江边、圣水湖畔。07年，学院以超常的速度，主校区当年异地建设，当年投入使用，成为学院发展史上新的里程碑。学',50914);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('七台河职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.qthzyxy.com/','七台河职业学院是经黑龙江省人民政府批准、国家教育部备案，列入普通高等学校招生计划的全日制公办高等职业学院。学院坐落在素有“焦煤之都”和培养“世界冠军摇篮”之称的山水园林城市——七台河市，以培养生产、建设、管理、服务等一线需要的高等职业技术应用性人才为主，同时广泛开展成人专科、本科教育和各类职业培训。学院现有各类在校生4600余人。良好的办学条件。学院占地面积16万平方米，建筑面积7万平方米，固定资产1.8亿元。图书馆藏书22.5万册。学院建有功能齐全、设备先进的学术报告厅、多媒体教室、多媒体语音',29309);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东理工职业学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdpi.edu.cn/','广东理工职业学院是经广东省人民政府批准、教育部备案的公办普通高等院校，学院隶属于广东省教育厅、面向全省招生、培养高级专业技能人才。广东理工职业学院属第三批A线录取院校。招生对象为参加普通高考学生。广东理工职业学院校址在广州市下塘西路3号，学院设广州和南海两个校区，分别在广州市下塘西路3号和佛山市南海区桂城南新三路2号。学院拥有一支高素质的专业教师队伍，有完备的教学生活设施，学院建有主干千兆的校园网、各类藏书达30多万册的数字图书馆，有完善的多媒体课室、多功能电教室、计算机网络课室、语音室、汽车实',327892);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西城市职业学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.gxccedu.com/','【十年树木，百年树人】广西城市职业学院2010年被评为“自治区文明单位”，是一所经国家教育部备案，具有独立颁发国家承认学历文凭资格的综合性全日制普通高校，现有在校生10000余人。学院坚持\"质量立校、人才强校、文化塑校、特色兴校\"的办学理念，形成了\"科学精神与人文精神\"相结合的校园文化，培养\"勤奋、严谨、求实、创新\"的学风，努力建成现代化职业教育基地。【幽雅的学习环境】学院座落于祖国南疆的左江河畔,占地880多亩，毗邻南宁国际机场，交通便利，环境幽雅。2011年，校内最大的综合实训基地——逐羊景',62452);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南工艺美术职业学院',' ','湖南',0,8,0,0,0,' ','暂空',' ','http://www.hnmeida.com.cn','“国家示范性高等职业院校建设计划”骨干高职院校湖南工艺美术职业学院位于“三周”（周扬、周谷城、周立波）故里、文化古城的益阳，铁路、公路，国道、高速，纵横畅达。邻秀峰公园、会龙公园，波光碧影，环境宜人。拥有标准学生公寓、全塑胶田径场、全新体育馆、网球场、展厅、教学楼……设有4个系20个专业（含专业方向），是全国艺术设计类专业设置最全的高校之一……拥有实力雄厚的教学团队：高级职称（正、副教授、高级工艺美术师）以上的专业教师58人，并聘请了中央美院、清华大学美术学院、广州美术学院、同济大学及日本、美国',84396);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南九嶷职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.hnxxjsxy.com/bagecms/index.php','湖南九嶷职业技术学院是经湖南省人民政府批准，教育部备案的全日制普通高等院校，始建于1980年，2008年与湖南潇湘技师学院合并，两院资源整合，办学实力更加雄厚，专业建设更具特色，是湖南省第一所有专科、技师、高级工等多层次、多形式、多功能的高技能人才教育培训基地。湖南九嶷职业技术学院是在原“九嶷山学院”的基础上发展起来的。1980年原国家学部委员、北京农业大学第一任校长、著名农林学家、教育家乐天宇教授自筹资金在其家乡宁远县创办“九嶷山学院”，系新中国成立后第一所民办高校，乐天宇教授任董事长、院长。',27549);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南理工职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.hnlgzy.net','湖南理工职业技术学院是经湖南省人民政府批准、国家教育部备案的、面向全国招生的公办全日制普通高等专科学院。学院位于伟人故乡—湘潭，学院分南、北两院。教学楼、科技楼、报告厅、实训楼、图书馆、体育馆等教学、实验生活、文娱设施一应俱全，校园环境幽雅，布局合理，是读书治学的理想场地。学院拥有一支既有理论造诣又有实践经验的师资队伍，现有专兼职教师220多人，具有中高职称的教师占总人数的80%，“双师型”教师占教师总人数的50%。近年来，学院教师在教学与科研方面取得了丰硕的成果，在省内外同类院校中享有很高的声',78582);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南软件职业学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnsoftedu.com','湖南软件职业学院是经湖南省人民政府批准成立、国家教育部备案，纳入国家计划统一招生的全日制普通高等院校。学院于2001年建校，现有长沙、湘潭两个校区。长沙校区占地398亩，建筑面积7万多平米，坐落在天心区新开铺路，毗邻省政府新址。湘潭校区占地1000亩，坐落在“长株潭”一体化的核心地段九华经济开发区。新校区依山傍水，环境优美。总建筑面积40万平方米，一期工程10万平方米于2008年8月建设完成，有近4000名学生搬进了新校区学习和生活。二期工程已经启动，计划3年内竣工。总投资近10亿元，现代化教学',59309);
