insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江民族职业学院',' ','黑龙江',0,9,0,0,0,' ','暂空',' ','http://www.mvcollege.com','黑龙江民族职业学院是国家民委与黑龙江省人民政府共建高校，坐落在北国冰城哈尔滨，其前身最早可追溯到1928年张学良创办的东北蒙旗师范学校。办学80多年来，学院忠实履行人才培养、科学研究、社会服务、文化传承和促进民族团结进步的办学职能，为国家和社会培养了一大批优秀人才和民族团结的种子，为科教兴国、兴边富民、振兴东北老工业基地和推动黑龙江边疆文化大省建设方面作出了重要贡献。近年来，学院凭借着国家加快发展现代职业教育和高度重视民族教育的强劲东风，扬帆起航，破冰前行，走出了一条扩容与提质并重的快速发展之路',81930);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南工业贸易职业学院',' ','河南',0,6,0,0,0,' ','暂空',' ','http://www.hngm.cn/','河南工业贸易职业学院是在河南省经济贸易学校的基础上，经河南省人民政府批准、国家教育部备案的一所独立设置的专科层次高等职业院校。学院于1950年创建，具有近60年办学历史。学院座落在郑州市区，现有南、北两个校区，占地面积513亩，总建筑面积12.49万平方米。北校区（优胜校区）位于郑州市优胜北路4号，居郑州市中心位置；南校区（龙湖中心校区）位于郑州市龙湖经济技术开发区。交通便利，环境优美，校园内绿树常青，绿草如茵，亭台小景点缀其中，构成优雅的育人环境。学院现有教职工310人，其中专任教师192人，',118293);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南汽车工程职业学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.zzptc.com','学院是湖南省首批8所卓越高等职业技术学院立项建设单位之一。坐落于株洲市红旗北路，毗邻沪昆高速和长株高速，交通便利，环境优美，占地784亩，校舍建筑面积近20万平方米，建有7个实训中心、89个实训室和102个校外实训基地，馆藏纸质图书29万册、电子图书32万册，总资产3.53亿元。学院设有七个教学院系──汽车电子系、汽车运用系、汽车商务系、机电工程系、信息工程系、经济贸易学院、基础教育学院。开办汽车电子技术、汽车技术服务与营销、汽车检测与维修、数控技术、会计、电子商务等涉及七个专业大类的25个专业',39682);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙电力职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','oldname": "长沙电力职业技术学院','长沙电力职业技术学院是经湖南省政府批准，教育部备案的公办全日制普通高等专科院校。学院隶属于国家电网湖南省电力公司，教育业务由湖南省教育厅管理，是湖南省唯一的电力类高等职业院校。目前，与湖南省电力公司中心培训部实行“两块牌子、一套人马”的管理模式，既承担着培养电力行业高素质技能型专门人才的职责，又肩负着湖南电力职工培训和技能鉴定的任务。学院创建于1956年，2005年升格为高等职业技术学院，座落于国家历史文化名城——长沙，位于长沙经济技术开发区，校园占地面积460余亩。建校50年多年来，学院全面贯',104108);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南水利水电职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnslsdxy.com/','湖南水利水电职业技术学院是经省人民政府批准、国家教育部备案的水利水电类全日制公办普通高等院校，隶属于湖南省水利厅。学院建校三十多年来，办学成果得到了各级领导和社会各界的充分肯定。2008年，学院在教育部的人才培养工作水平评估中获评“优秀”，并在2013年以全部指标合格的优异成绩通过教育部第二轮人才培养工作评估;2012年1月，水利部确定我院为水利高等职业教育示范院校、全国水利定点培训机构;2013年7月，湖南省教育厅确定我院为湖南省示范性(骨干)高职院校、湖南省教育信息化试点学校，近年来先后获得',60287);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南现代物流职业技术学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.56edu.com','湖南现代物流职业技术学院是一所以现代物流为主导专业的公办全日制高等职业院校，地处省会长沙，是湖南省人民政府和中国物流与采购联合会共建院校、教育部高职高专人才培养工作水平评估“优秀”院校、湖南省示范性高等职业学院和国家物流职业教育人才培养基地，与国防科技大学共同承担建设“物联网感知技术与应用湖南省工程研究中心”。办学理念：对接产业，工学结合，提升质量，打造品牌办学特色：用现代物流理念培养现代物流人才专业建设：学院现有物流管理系、物流工程系、物流信息系、物流经贸系、公共管理系等5个系部，开设物流管理',98779);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南高速铁路职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.htcce.com','湖南高速铁路职业技术学院是一所由衡阳市人民政府主办主管的全日制公办普通高等学校。1951年由铁道部创办，首任校长是后来担任了铁道部部长、铁道兵副司令的郭维城将军。学院是全国第一所以“高铁”命名、中南地区唯一以铁路工程建设为主体专业的高职院校，新校园占地面积800亩，全日制学生1万余人，60多年来，培养了5万余名毕业生。学院是全国职业教育先进单位，全国职业技术学校职业指导工作先进学校，教育部人才培养工作水平评估优秀学校，湖南省示范性(骨干)高等职业院校，火车头奖杯获得单位，中国地方铁路协会培训基地',428297);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南交通工程职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.htcrh.com/','湖南交通工程职业技术学院是2005年3月经湖南省政府批准，教育部备案的一所综合性、多学科、全日制公办高等院校。学院北依南岳首峰回雁峰山麓，东临湘江河畔，校园风景秀丽，为湖南省“园林式单位”和“百佳文明卫生单位”，是广大莘莘学子求学的理想场所。学院的前身是创办于1951年的原铁道部属国家级重点中等专业学校—衡阳铁路工程学校，办学历史悠久，成就辉煌，被誉为铁路的“黄埔军校”。历史上曾两度升为“衡阳铁道工程学院”和“衡阳铁道学院”，为全国铁路“火车头奖杯”获得者。学院占地247亩，建筑面积12万平方米',15785);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南铁路科技职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hntky.com','湖南铁路科技职业技术学院是经湖南省人民政府批准、教育部备案设立的公办全日制高等学校，由株洲市人民政府举办，湖南省教育厅业务管理。学院前身是株洲铁路机械学校，成立于1956年，由原铁道部批准设立，广州铁路局主管，期间于1958年更名为株洲铁道学院，举办本科教育，后因国家院系调整恢复举办中专，2005年开始举办高等职业教育，主要为铁路运输行业培养专门人才。办学以来，培养各类大中专毕业生8万多人，分布在包括香港在内的全国各地铁路和地铁线上，成为我国铁路运输行业的中坚力量。学院是湖南省示范性(骨干)高职',277930);
