insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃畜牧工程职业技术学院',' ','甘肃',0,2,0,0,0,' ','暂空',' ','http://xmgcedu.cn/','甘肃畜牧工程职业技术学院是由甘肃省畜牧学校和甘肃省农业机械化学校两所国家级重点中专于2004年6月经甘肃省人民政府批准，合并组建的专科层次的普通高等学校，隶属甘肃省教育厅。原甘肃省畜牧学校始建于1952年9月，其前身为“西安畜牧学校”隶属西北军政委员会畜牧部管理。1954年，学校移交甘肃省，1958年学校由西安市搬迁至黄羊镇，同时更名为“甘肃省畜牧学校”，隶属甘肃省农牧厅。1960年经甘肃省委批准成立“甘肃省畜牧兽医专科学校”，1961甘肃省畜牧兽医专科学校撤销，1970年学校下放给原武威地区，',121301);
