insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连软件职业学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.dlrj.edu.cn','大连软件学院是经辽宁省政府批准、国家教育部备案、国家统一计划招生的全日制普通高等院校。学院成立于1993年，是全球软件和服务外包新领军城市的高等人才培养基地，是“大连IT教育联盟成员单位”。学院连续两次获得“辽宁省优秀高校”和“全国文明诚信单位”、“大连市特色党组织优秀单位”等多项殊荣。学院下设软件工程系、网络工程系、媒体艺术系、商务旅游系、管理系、实训中心、外语教学部、基础教学部等教学单位，开设软件技术、计算机网络技术、计算机多媒体技术、计算机信息管理、电子商务、商务英语、旅游管理、酒店管理、',71983);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连翻译职业学院',' ','辽宁',0,5,0,0,0,' ','暂空',' ','oldname": "大连翻译职业学院','在黄海与渤海的相交之处，镶嵌着一颗璀璨的“北方明珠”，这就是与青山相依、同大海相连的滨城——大连。这里山川秀美，气候宜人，先后被评为“全球环境500佳城市”、“2004cctv最具经济活力城市”。在这个美丽的城市里，有一座经辽宁省人民政府批准（辽政[2005]86号）、国家教育部备案（教发函[2005]71号）的民办普通高职院校。学院位于辽宁省大连市。建校14年来，学院形成了以外语专业为主体，商务、旅游、经管类等多专业协调发展的专业格局，开设有应用英语、应用日语、应用韩语、应用法语、应用俄语、电',42421);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁商贸职业学院',' ','辽宁',0,6,0,0,0,' ','暂空',' ','http://www.lnsmzy.edu.cn/','辽宁商贸职业学院成立于2005年4月，前身是始建于1984年的辽宁农业管理干部学院，是公办省属普通高等职业学院。学院位于中国东北中心城市沈阳，毗邻世界文化遗产清福陵，周边地区教育科研单位集中，校园环境幽雅，学术气氛浓郁，是求知治学的理想之地。学院拥有一支以专业带头人为主导，以优秀中青年教师为骨干，以双师型教师为特色的师资队伍。教师队伍中，副教授以上职称56人，注册会计师、律师、经济师、土地评估师等双师型教师占教师总数的48%以上。学院设有商务贸易系、财务管理系、商务外语系、技术经济系、信息技术系',194266);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连枫叶职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.dlxgjy.com/','大连枫叶职业技术学院是辽宁省政府批准、国家教育部备案，具有高等学历教育的普通高等职业技术学院。大连港务专修学院是辽宁省教育厅批准的唯一一所港务类高等教育机构，是辽宁省优秀学校、大连市诚信学校。2010年两院成功融合，在以高职教育为主的发展思路指引下，正向集职业教育、学历教育、继续教育、高教自考于一体的多元化方向发展，为不同层次的学生提供最合适的学习方案。学生就读大专的同时可套读本科，学制内完成专、本科学习，成绩合格，可取得国家承认的专、本科毕业证书。学院坐落于风光秀丽的大连市大黑石旅游度假区。占',111911);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州百年职业学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.scc.edu.cn/','苏州港大思培科技职业学院由世界名校香港大学的直属学院-香港大学SPACE学院(专业教育)联合苏州科技学院与高博教育管理(苏州)有限公司共同创办。学院是经江苏省人民政府批准建立、教育部备案的江苏省首家内地与香港合作办学的全日制普通高等专科学校。学院地处国内首个高等教育国际化示范区——独墅湖科教创新区，区内环境怡人，设施齐全，有23所高等学府和知名研究生院坐落于此，学术氛围浓郁。学院毕业生除可以直接报考香港的大学外，学院的课程得到近60多所海外正规大学的承认，直接与境外高校的学位课程衔接，学生已修读',129348);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州市职业大学',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://zhaosheng.jssvc.edu.cn/','苏州市职业大学是经江苏省人民政府批准，由苏州市人民政府主办的全日制普通高等专科学校，前身为创办于1911年的苏州工业专科学校。自1981年成立以来，学校秉承“勤、勇、忠、信”的校训，践行“质量立校、人才强校、机制活校、特色兴校”的办学理念，赢得了良好的社会声誉，成为区域品牌院校。目前，正致力于建成市属普通本科院校—“苏州学院”。学校现有石湖、干将路、潭山和吏舍弄四个校区，占地面积1262亩，校舍建筑面积45.6万平方米，馆藏纸质图书132万册，教学科研仪器设备总值1.48亿元。设有10个学院（部',501001);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆山登云科技职业学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.dyc.edu.cn/','1.学院全称：昆山登云科技职业学院(国标代码：13963，江苏招生代码：1973)2.校址：江苏省苏州市昆山市马鞍山西路1058号3.办学性质：民办4.办学层次：高职(专科)5.办学类型：全日制普通高职教育6.批准成立的时间：2005年5月7.批准单位：江苏省人民政府昆山登云科技职业学院是经江苏省人民政府批准(苏政复〔2005〕25号)、教育部备案，由台湾政行校企各界贤达人士共同投资兴建的一所全日制民办专科层次的普通高等学校。学院坐落于全力建设苏南国家自主创新示范区、海峡两岸电子商务经济合作实验',46226);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京视觉艺术职业学院',' ','江苏',0,8,0,0,0,' ','暂空',' ','http://www.niva.cn','南京视觉艺术职业学院创立于2000年，占地面积800亩，是一所按国际标准创办的综合性艺术大学，设有摄影系、传媒系、设计系、音乐系、高尔夫运动与管理系和国际学院等五系一院，建有摄影棚、摄影博物馆、录音棚、演播厅、影视观摩厅、苹果设计机房、美术馆、音乐厅、形体房、化妆室、琴房、灯光网球场、高尔夫练习场等一流的教育教学设施。学院先后与韩国中部大学和美国橘郡海岸学院合作办学，学分互认，师资共享。学院以办国际化、品牌化的一流高级艺术院校为目标，在教学方法、内部设施、管理模式方面均与国际接轨。十余年来，秉承',67778);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东药品食品职业学院',' ','山东',0,3,0,0,0,' ','暂空',' ','http://www.sddfvc.cn/','山东药品食品职业学院始建于1958年，学院实行省教育厅和省食品药品监督管理局双重领导，以山东省食品药品监督管理局为主的管理体制，是一所专科层次的全日制国办普通高校。学院总占地907亩，校舍面积14.6万平方米；固定资产2.1亿元，教学仪器设备总值2829.7万元，图书33.4万册；有63个校内实验、实训场所，与企业合作共建4个实践教学基地、24个校外实习基地。我院现有教职工304人，高职在校生5449人。学院设有山东省药监系统培训中心、山东省执业药师培训中心培训点、山东省医药行业第二职业技能鉴定',144967);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆工商职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqtbi.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校重庆工商职业学院是由重庆市人民政府举办，教育部批准设立的公办全日制普通高等学院，面向全国招生。重庆工商职业学院位于重庆直辖市主城区环境优美、生机盎然的国家级高新技术产业开发区二郎科技城。学院二个校区占地面积1300亩，建筑面积133074平方米。学院建有现代化的教学大楼、综合大楼、实验大楼、高标准演播室、学术报告厅、学生活动中心、学生公寓、多功能体育场、篮球场、羽毛球场、汽车驾驶培训学校等；学院图书馆面积6899平方米，各类图书、刊物、杂志等共计35',239294);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆应用技术职业学院',' ','重庆',0,6,0,0,0,' ','暂空',' ','http://www.cqyyzy.com/','重庆民生职业技术学院是经重庆市人民政府批准、国家教育部审定备案，颁发国家学历文凭、教育部电子注册、国家统招的普通高等院校。学院代码13968。学院前身是1946年创办的由冯玉祥将军任董事长，著名教育家陶行知先生任校长，李公仆任副校长，张澜、沈钧儒等任董事的社会大学。1985年复校后更名为“重庆社会大学”。2005年，经重庆市人民政府批准、教育部审定备案更名为“重庆民生职业技术学院”。学院现座落于重庆市合川区大学园区，占地500亩，交通便捷。学院拥有功能齐全、设备完善的综合教学楼、实验实训中心、图',67993);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建生物工程职业技术学院',' ','福建',0,3,0,0,0,' ','暂空',' ','http://www.fjvcb.cn/home/','福建生物工程职业技术学院是经省委、省政府批准并报国家教育部同意设立，是全省唯一一所医药、生物类全日制国家公办高等大专院校，现隶属于福建省食品药品监督管理局，业务由国家教育行政主管部门指导。学院地处福建省福州市区的闽江之畔、洪山桥旁，环境优美，交通便捷，学习、生活设施配套齐全。学生毕业时颁发国家统一的普通高等学校毕业证书。学院现占地150多亩（新校区将征地308亩），设有7个系11个专业，设置有省级精品专业和精品课程。各类在校生近5000人。学院拥有一支作风优良、业务过硬、比例均衡、结构合理的教师',62161);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建艺术职业学院',' ','福建',0,8,0,0,0,' ','暂空',' ','http://www.fjyszyxy.com/','福建艺术职业学院是福建省文化厅主管、福建省惟一一所专门培养从事音乐、美术、舞蹈、戏剧等文化艺术专业人才，具有大中专学历教育的省级公立高等艺术院校，并在全省各地设有十五个分校（班）。福建艺术职业学院前身为福建艺术专科学校，始创于1958年7月，1960年，经福建省人民委员会（福建省人民政府前身）批准，升格为本科学历教育的福建艺术学院；1962年，因全国性暂时困难，先后被调整为福建艺术学校、福建戏曲学校；文革期间停办；1974年复办福建艺术学校；2005年5月31日，经福建省人民政府批准，正式组建福',69071);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建幼儿师范高等专科学校',' ','福建',0,4,0,0,0,' ','暂空',' ','http://www.fj61.net/Default.aspx','福建幼儿师范高等专科学校，系直属福建省教育厅的公办师范类高等专科院校，是全国学校艺术教育工作先进单位和福建省文明学校。经福建省人民政府批准，学校于2005年5月由福建省福州幼儿师范学校和福建省福州艺术师范学校合并组建福建儿童发展职业学校，2012年3月获教育部批准设立“福建幼儿师范高等专科学校”。学校起源于福州协和幼稚师范学校和“闽师之源”全闽师范学堂，具有百年师范教育办学历史，是福建省学前教育和小学艺术教育等师范教育领域的开拓者，曾获教育部“为基础教育培养合格师资，方向明确，成绩显著”的表彰。',71175);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门城市职业学院',' ','福建',0,5,0,0,0,' ','暂空',' ','http://www.xmcu.cn/','厦门城市职业学院是经福建省委省政府批准设立的,由厦门市政府举办，经国家教育部备案，在厦门市广播电视大学、厦门教育学院和厦门职工大学三校教育资源整合的基础上建立的一所高职高专院校。学院集高职高专教育、远程开放教育、成人教育、学历非学历教育及劳动技能培训为一体，规模大、专业广，功能齐全。学院环境优美，教学、生活设施先进，是一所数字化、人文化、现代化的绿色校园。学院主校区位于厦门岛内东海岸的前埔南路，南邻风光旖旎的环岛路，北接宽阔的莲前大道，东与小金门岛隔海相望，东北面是厦门国际会展中心，校园依山傍海',224551);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三明职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.smvtc.com','三明职业技术学院是经福建省人民政府批准设立，国家教育部备案的一所公立全日制普通高等专科学校，是在优化整合原三明业余大学、三明财经学校、三明卫生学校、三明轻纺工业学校等教育资源基础上组建的综合性高职院校，具有几十年的优良办学传统和扎实的办学基础。校园坐落在国家级优秀旅游城市、福建省工业基地——三明市市区，有铁路、高速公路通达省内外，交通便利。校区距市中心一公里路程，位于闽江支流的沙溪河畔，背依仙人谷国家森林公园，前与麒麟山公园相连，依山傍水，终年鸟语花香。校园、公园融为一体，人与自然和谐相处，是莘',58466);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁德职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.ndgzy.com','一、学院概况宁德职业技术学院是经福建省人民政府批准、教育部备案，闽东唯一的一所具有高等教育招生资格的公办高等职业技术院校。院址设在福安市区，占地面积达503亩。学院设农业科学、人文科学、计算机、机电工程、财经管理五个系，2009年招生专业12个，面向本省，辐射周边省、市、自治区，服务区域经济发展，以全日制高职(大专)学历教育为主，致力培养适应教育、生产、经营、管理、服务等第一线需要的高素质技能型人才。二、设施先进、师资雄厚学院校风优良，特色鲜明，是一所教学、生产、科研、示范推广、经营服务相结合，',37552);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州软件职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.fzrjxy.com','福州软件职业技术学院是经福建省人民政府批准，国家教育部备案，在国家计划内统一招生并可独立颁发国家承认专科学历的民办全日制普通高等职业技术学院。学院是一所以软件、动漫、艺术设计类专业为特色，涵盖外语、经管、商贸等专业的高职院校。学院被福州市教育局授予“平安校园”称号，被福州市教工委评为“优秀基层党组织”。学院座落于福州的形象窗口和政治、经济、文化中心，福州核心城区鼓楼区。校区位于风景秀丽，人才集聚，科技发达，软件企业众多，素有“东南硅谷”之称的福州软件园内。校区内生活设施配套齐全，教学设施现代先进',38833);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门兴才职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.xmxc.com/','简介厦门兴才职业技术学院创始于1983年，是经福建省人民政府批准、国家教育部备案，纳入全国高考统一招生计划，具有独立颁发国家承认学历的全日制普通高等院校，是福建省首批正式成立党委的民办高校之一。厦门是我国最早成立的四个经济特区之一，是海峡西岸经济区最重要的中心城市，沐浴改革春风，经过三十多年的发展，经济发达，文化繁荣，环境优美，人文素质高，先后获得“全国文明城市”、“国际花园城市”、“联合国人居奖”等荣誉称号，是理想的求学乐园和创业乐土。学院位于厦门市集美大学城区、高速铁路厦门北站旁。集美大学城',165131);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京医科大学康达学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://kdc.njmu.edu.cn/','南京医科大学康达学院是1999年经江苏省教育厅批准成立的公有民办二级学院，2005年经教育部批准重新设立为独立学院。办学九年来，康达学院锐意改革，勇于创新，显示出“办学理念先进、教学特色鲜明、综合实力雄厚、社会声誉良好”的勃勃生机，赢得了学生、家长、社会的普遍认可和广泛赞誉。学院现有临床医学、预防医学、医学影像、药学、护理学、公共事业管理6个专业，分设临床医学、全科医学、急诊医学、整形与美容医学、医学影像学、预防医学、临床药学、护理学、护理学（英语班）、医疗保险、健康教育与媒体传播、卫生信息管理',392914);
