insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京信息工程大学滨江学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.bjxy.cn','南京信息工程大学滨江学院，是经教育部批准由南京信息工程大学举办的一所独立学院，办学层次为全日制普通本科。学院设有9个系，36个专业及方向，目前在校生近12000人。学院坐落于南京国家高新技术产业开发区内，校园环境优雅，学习、生活、服务设施齐备。南京信息工程大学前身是有“中国气象人才摇篮”美誉的南京气象学院，始建于1960年，1978年被确定为全国重点大学，2004年更名为南京信息工程大学，是中国气象局和江苏省人民政府共建的全国重点高校，是江苏省重点建设大学，教育部本科教学工作水平评估优秀学校，具',708288);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州大学文正学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.sdwz.cn','苏州大学文正学院位于古城苏州西南的吴中区，东临越湖路，西傍吴山，北靠石湖和上方山国家森林公园，在校生8500多人。学院依山傍水，景色优美，环境优雅，是莘莘学子学习深造的理想场所。苏州大学文正学院作为一所公有民办二级学院是由原江苏省教育委员会于1998年12月批准成立的。2005年，按照教育部教发[2003]第8号文件精神和江苏省教育厅（苏教发[2005]40号）文件精神，学院改办为独立学院。现根据国家教育部的有关规定，学生学业期满，成绩合格，颁发苏州大学文正学院普通高等教育全日制本科毕业证书，符',650899);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州大学应用技术学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://tec.suda.edu.cn/','苏州大学应用技术学院（AppliedTechnologyCollegeofSoochowUniversity）位于中国第一水乡—周庄古镇旅游区2公里处，距苏州大学独墅湖校区20公里。毗邻苏州工业园区、昆山和吴江高新技术开发区。校园环境优美，体现了“小桥、流水、书院”的建筑风格，校园空气清新，设施一流，是莘莘学子理想的求学场所。学院成立于1997年11月，2005年改制为由国家“211工程”重点建设高校、江苏省省属重点综合性大学——苏州大学举办的本科层次的独立学院。学院现有机电工程、电子信息、服装',392009);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州科技大学天平学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://tpxy.usts.edu.cn/','苏州科技学院天平学院是经教育部批准的、本科层次的全日制普通高等院校，学制四年。学院地处古城苏州的西部——国家级高新技术产业开发区内，是江苏省内成立较早的独立学院之一。苏州科技学院由原苏州城市建设环境保护学院和苏州铁道师范学院于2001年9月合并组建而成，是一所以工为主，工、理、文、管、艺协调发展的多科性大学。苏州科技学院天平学院由苏州科技学院的前身之一--苏州城市建设环境保护学院申办，经原江苏省教育委员会(苏教计[1999]215号)1999年9月批准成立的公有民办二级学院。2005年学院按照教',417054);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏大学京江学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://jjxy.ujs.edu.cn/recruitment.asp','江苏大学京江学院(招生代码1821)是1999年成立的公有民办二级学院，2005年经教育部批准为独立学院。学院办学规模定为10000人，全部为四年制本科层次。京江学院景色秀丽，环境幽雅，共享江苏大学的优质教育资源。学院现有40个专业，涵盖理、工、文、法、经、管、医等7大学科门类,各类建筑面积30余万平方米，教学计算机800多台,多媒体教室7000座位,语音实验室550座位。学院自成立以来已累计培养七届毕业生共8000余人，目前在校生达9300人。京江学院牢固确立“依托母体、立足江苏、面向全国、服',731315);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('扬州大学广陵学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://glxy.yzu.edu.cn/','扬州大学广陵学院是1998年12月经原江苏省教委批准，由全国首批博士、硕士学位授予单位、江苏省重点综合性大学——扬州大学按新机制、新模式设置的本科层次的公有民办二级学院，2005年经教育部批准变更为独立学院。11年来，学院实践“以人为本，机制创新，质效双求，办出特色”的办学理念，坚持以培养应用型、复合型、创新型的适应社会需求的高素质人才为目标。全院现有普通全日制本科生近9000人。广陵学院位于风景秀丽、人文荟萃的历史文化名城——扬州，地处城南扬子津高教园区，占地面积500余亩，建筑面积近23万平',260647);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京邮电大学通达学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.tdxy.com.cn','南京邮电大学通达学院是经国家教育部批准，由南京邮电大学1999年创办的全日制本科独立学院。学院座落在南京市中心城区新模范马路66号和南京仙林大学城文苑路9号。通达学院拥有先进的教学、实验设备，计算中心、电教中心、语音实验中心、程控交换技术中心、移动通信技术中心、光纤通信技术中心和图像处理与图像通信、语音信号处理、计算机通信、IP网、实验通信网等专业实验室为学生提供了良好的教学实验环境。学院坚持以培养应用型创新人才为目标，以信息产业（IT）相关高新技术为立足点，依托南京邮电大学雄厚的教学资源、学科',514745);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京财经大学红山学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://hs.njue.edu.cn','南京财经大学红山学院是1999年经江苏省教育厅批准建立的民办本科学院，2005年经国家教育部批准，成为国家普通高等教育独立学院。学院依托南京财经大学的学科、专业优势，坚持育人为本、提高质量、强化特色、改革创新、服务社会的办学理念，为地方经济建设和社会发展培养经济学类、管理学类、法学类、文学类等应用型、技术技能型人才。学生学业期满，符合毕业和学士学位授予条件，按国家教育部和江苏省有关规定颁发南京财经大学红山学院毕业证书和学位证书。毕业生就业按国家政策执行。学院现有普通全日制本科生8000多人，设有',978067);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('常州大学怀德学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://hdc.cczu.edu.cn/','江苏工业学院怀德学院是经教育部批准的独立学院。学院位于江苏省常州市(江苏工业学院白云校区)，交通便捷，环境优美，学风淳朴，是一所花园式学校。学院办学实力雄厚，形成了以“责任”为特征的主流校园文化，为广大青年学子健康成长提供了深厚的人文底蕴和良好的育人氛围。二、办学特色怀德学院坚持社会主义办学方向，以社会经济发展和市场需求为导向，立足江苏，人才培养主要面向长三角经济发达地区。坚持服务育人的理念，以教育质量为核心，以通识教育和专业教育为主线，坚持本科学历教育和综合实践能力培养相结合，培养素质高、竞争',451787);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南通大学杏林学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://xlxy.ntu.edu.cn/','南通大学杏林学院座落于风景秀丽素有“江海门户”之称的沿海开放城市、著名的教育之乡——南通市，以南通大学钟秀校区作为办学校园，占地面积500余亩。校园环境幽雅，育人氛围浓郁，管理制度严谨，生活设施齐全，是理想的学习、生活场所。杏林学院依托南通大学实力雄厚的师资队伍，共享母体优质的教育教学资源，现有在校学生8000余人，设有39个专业(含方向)。南通大学杏林学院办学八年多来，形成了自己的办学特色：先进的办学理念：坚持“以人为本”的办学理念，确立培养具有合格的理论基础、较好的应用能力训练、明显的技能优',418706);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京审计学院金审学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://jsxy.nau.edu.cn/','南京审计学院金审学院是南京审计学院2002年12月经江苏省教育厅批准建立的一所普通本科层次的公有民办二级学院，2005年5月经教育部批准转制为独立学院。学院位于南京市浦口大学园区，校园面积500亩，建筑面积84636平方米。学院教学实验设施先进，后勤服务系统完善。学院招生范围以江苏省为主，面向全国招生，目前根据社会经济发展的需要，设立了审计学、会计学、金融学、国际经济与贸易、计算机科学与技术、财务管理、工商管理、信息管理与信息系统等八个特色专业，尤其是以国家审计和内部审计为主定位的审计学专业依托',873516);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛农业大学海都学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.hdxy.org/','青岛农业大学海都学院是2005年经教育部批准由青岛农业大学（原莱阳农学院）举办的独立学院。学院设在青岛农业大学原莱阳校区，占地1000亩，图书馆藏书50万册，校舍建筑面积18.65万平方米，固定资产总值3.26亿元，教学仪器设备值2470万元。在2006年山东省教育厅组织的高校办学条件核查中，生均仪器设备值、生均图书、生师比等指标均列全省12所独立学院前两位。2006年在由中国教育科学研究院组织的全国独立学院实力综合测评中荣获“中国最具竞争力独立学院”称号。2007年被中国高等教育学会授予“中国',327262);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏城市职业学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.jscvc.cn/','江苏城市职业学院院校代码：1401江苏城市职业学院座落于风景秀丽的六朝古都南京市区内，学院坚持社会主义办学方向，坚持“以市场需求为导向，以能力培养为中心”的办学指导思想，树立“以人为本”的办学理念，教学严谨，管理规范，设施齐全，是莘莘学子深造的理想场所。合法的办学资质：学院是经江苏省人民政府批准成立、教育部备案的全日制公办普通高等学校。学生入学后即在教育部电子注册学籍，凡学完规定课程，经考核成绩合格者，获颁国家承认的学历证书。科学的培养方案：学院按照全日制高职(专科)培养规格、教学要求，制定科学',171837);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('济南大学泉城学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.ujnpl.com','济南大学泉城学院是国家教育部和山东省人民政府正式批准成立，实施本科层次学历教育的综合性高等院校。自2005年建校以来，学院依托济南大学雄厚的办学实力，坚持“以学生为本，以育人为主，以品牌立院”的办学理念，积极培养具有创新精神的高素质应用型人才。济南大学是山东省重点建设的综合性大学，具有学士、硕士学位授予权和硕士研究生免试推荐权，是博士学位授予权立项建设单位。学校现设学院26个，硕士学位授权一级学科20个、二级学科144个，硕士专业学位培养类别3个、工程硕士专业学位授权领域13个。全日制在校本科生',951021);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南农业大学华豫学院',' ','河南',1,0,0,0,0,' ','暂空',' ','http://www.hnhyedu.net/','河南农业大学华豫学院是经国家教育部批准的普通本科高校。学院地处国务院命名的历史文化名城、中国优秀旅游城市——商丘市。这里是陇海和京九铁路、连霍和济广高速公路及105和310国道的交汇处，地理位置优越，交通便利，气候宜人；这里历史悠久，人杰地灵，英才辈出，历代文人墨客荟萃。一流的教学设施学院依托河南农业大学办学。河南农业大学是有近百年历史的重点高校，学科门类齐全，综合实力强，优质的办学资源和丰富的办学经验，为学院的建设和发展提供了强有力的支撑。学院文化学术氛围浓厚，拥有现代化的教学管理系统和先进的',126791);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('商丘学院',' ','河南',1,0,0,0,0,' ','暂空',' ','http://www.hnhyedu.net/','商丘学院是经国家教育部批准的全日制普通本科院校。学校坐落于商丘市区东南部著名的应天书院旧址旁，邻近已有四千多年历史的国家4A级旅游景区商丘古城。商丘地处陇海和京九铁路、连霍和济广高速公路及105和310国道的交汇处，东临皖苏，西扼中原，北接齐鲁，南襟江淮，地理位置优越，气候宜人，历史厚重，人杰地灵，历代文人墨客荟萃。商丘学院如一颗璀璨明珠，闪耀在广袤的豫东平原上。优美的校园环境学校目前占地面积为2017亩，全日制在校生2万人，现总资产超13亿元，建筑面积达52万平方米。校园环境优雅大气，教学区、',205971);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川文化传媒职业学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.svccc.net','四川文化传媒职业学院是经四川省人民政府批准、国家教育部备案的全日制综合性普通高等院校。具备颁发国家承认学历文凭的资格，2009年在国家统招计划内面向全国招生。国标代码：14004，招生代码：5188。学院坐落于成都市西郊崇州市区，与温江大学城连为一体，既无闹市之喧嚣，又无山居之闭塞，具有浓郁的学习氛围和深厚的文化底蕴。初唐四杰之一王勃在此留下“海内存知己，天涯若比邻”的千古佳句；南宋文豪陆游更是抒写了壮志凌云的《剑南诗集》。崇州距成都市区20余公里，有成温邛高速和光华快速通道连接，交通便利。学院',94268);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川华新现代职业学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.schxmvc.com.cn/','●办学性质四川华新现代职业学院是经四川省人民政府批准、教育部备案的全日制国家计划内统一招生的普通高等职业学院，学制三年，颁发经教育部电子注册备案的普通高校专科毕业证书。学院现设有经济管理系、工程管理系、旅游系、应用技术系、信息工程系和艺术系等6个系，共有29个专业及方向，覆盖了现今高就业率的优势行业。学院通过大力开展校地合作、校企合作、订单培养，着力打造从事房地产、建筑工程、汽车、物流、旅游、酒店管理、IT、商贸、经济、服装设计、动漫、艺术设计等行业的生产经营管理人才。●狠抓教育教学软硬件建设，',98494);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川管理职业学院',' ','四川',0,6,0,0,0,' ','暂空',' ','http://www.scmpi.cn','四川管理职业学院是经四川省人民政府批准成立并报国家教育部审核备案的全日制公办普通高等学校，创建于1983年，原名四川行政财贸管理干部学院，已有26年的办学历史。学院始终坚持以人为本的教育教学理念，紧密结合市场的需求，以服务为宗旨，以就业为导向，努力探索有利于学生成才和就业的办学规律，始终把提高教学质量，培养合格人才放在第一位，积累了比较丰富、成熟的办学经验，26年来为四川省的经济建设和社会发展培养了大批高级管理人才和高层次应用型人才。学院设有公共管理系、工商管理系、计算机工程与管理系、城镇经营管',147221);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川艺术职业学院',' ','四川',0,8,0,0,0,' ','暂空',' ','http://www.scapi.cn','四川艺术职业学院是经四川省人民政府批准，国家教育部备案的公办全日制高等艺术学院。其前身为享誉国内的国家级重点中专四川省舞蹈学校和四川省川剧学校(四川省艺术学校)。学院现有中高级专任教师400余人,开设艺术类专业20余个,在校生4500余人。四川艺术职业学院的办学定位主要为社会培养高层次专业文化艺术类人才。学院拥有一流的艺术教育师资队伍,长期的教学实践积累了丰富的教学经验,取得了辉煌的教学成果,尤其在重点学科舞蹈专业和戏剧表演专业的演艺人才培养方面,名列国内舞台艺术教育前茅。长期以来,学院为国家培',82650);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆三峡医药高等专科学校',' ','重庆',0,3,0,0,0,' ','暂空',' ','http://www.sxyyc.net','重庆三峡医药高等专科学校是重庆市人民政府举办、重庆市教育委员会主管的全日制公办医药卫生类普通高等院校。学校座落在千年古城、美丽的长江之滨—重庆市万州区，始创于1951年，经过60多年的锤炼，2006年由原万州卫生学校和万县中医药学校合并升格为高等专科学校，是重庆市市级示范性高等专科学校。学校占地839亩，校舍建筑面积42万平方米，固定资产7亿元，教学仪器设备总值9762万元，各类馆藏纸质图书64万册，建有主干为千兆的校园网络基础平台和设备先进的电子阅览室。学校设有临床医学、中医、护理、医学技术、',715698);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆医药高等专科学校',' ','重庆',0,3,0,0,0,' ','暂空',' ','http://www.cqyygz.com','重庆医药高等专科学校是国家教育部批准的公办全日制普通高等专科学校，位于重庆市文化名区——沙坪坝区大学城。学校始创于1948年的中国人民解放军华东野战军第一后方医院卫生学校，经过近70年的办学锤炼，积累了丰富的办学经验，办学水平和人才培养质量得到社会的广泛认可，培养了十余万名优秀的医药卫生技术技能型专门人才。2015年4月，中共中央政治局常委、全国人大常委会委员长张德江在重庆市委书记孙政才、市长黄奇帆、市人大常委会主任张轩陪同下亲临我校视察，充分肯定了我校的办学水平。学校是重庆市示范性高等职业院校',1465314);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川中医药高等专科学校',' ','四川',0,3,0,0,0,' ','暂空',' ','http://www.scctcm.cn','四川中医药高等专科学校是2006年2月经教育部批准，在原绵阳医科学校基础上建立起来的一所公办全日制普通高等专科学校。学校是四川省唯一一所医学类普通高等专科学校。学校实行省、市共建，以绵阳市管理为主的管理体制。学校办学历史悠久，有近50年办学经历，先后毕业学生3万余人，招生遍及全国10多个省市，大多数毕业生已成为市内外医疗卫生单位的技术和管理骨干。学校现有占地面积380.2亩，规划校园面积540亩，二期工程即将动工。现有校舍建筑面积12.6万M2。现拥有教学大楼3幢、实验大楼2幢、学生公寓6幢、图',389199);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('遵义医药高等专科学校',' ','贵州',0,3,0,0,0,' ','暂空',' ','http://www.zunyiyizhuan.com/','遵义医药高等专科学校是经教育部批准设置的一所全日制公办普通高等专科学校。坐落国家历史名城遵义。学校具有近60年的办学历史。2014年学校整体搬迁至遵义大学城新校区，新校区占地1100余亩，现第一期工程已投入10亿元，基础设施和教学设施完善，是一所现代化和花园式的大学。学校现有在职职工407人（不含附属医院），博士、硕士研究生100人。学校建有中药标本陈列馆和人体生命科学馆，有一所直属附属医院和精神卫生中心医院、两所非直属附属医院和一所国家科研Ⅱ级中药生药学实验室、有全省医学高职类学校唯一的法医司',477783);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('曲靖医学高等专科学校',' ','云南',0,3,0,0,0,' ','暂空',' ','http://www.qjyz.org/','曲靖医学高等专科学校于2006年2月经国家教育部批准成立，是公办的普通医学高等专科学校。学校位于南中国第一大河——珠江源头，云南省曲靖市西山经济技术开发区。学校办学历史可追溯到1958年，其前身为云南省曲靖卫生学校。原曲靖卫生学校毕业生遍布全省大部分医疗机构，很多人成为医疗卫生单位的骨干。学校占地面积367亩，建筑面积9.86万平方米。在校教职员工174人，其中：专任教师134人。硕士研究生10人，副高级职称教师占教师总数42%。有功能齐全的实验室，有中西医实习医院，各实习医院具有完善的实习管理',208941);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('楚雄医药高等专科学校',' ','云南',0,3,0,0,0,' ','暂空',' ','http://www.cxmtc.net','楚雄医药高等专科学校是楚雄彝族自治州人民政府主办、省州共管的高等专科学校。学校坚持“以人为本，重质量、突特色、创品牌”的办学理念。实施“药学、检验特色骨干专业支撑学校发展”战略，秉承“励志笃行，大医精诚”的校训，弘扬“远志勤业，厚德济世”的校风，坚持“博学善导，敬业求真”的教风，倡导“虚心好学，善思勤勉”的学风。建校60年来为云南省培养各类医疗卫生技术人才近2万人。学校现有教职工251人，其中专任教师199人，教授9人，副教授55人，讲师65人，双师型教师75人，省级教学名师1人，校级教学名师4',288728);
