insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南财经大学天府学院',' ','四川',0,6,0,0,0,' ','暂空',' ','http://www.tfswufe.edu.cn','西南财经大学天府学院是经教育部(教发函[2006]81号)批准,将西南财经大学三大校区(光华校区、柳林校区、涪江校区)之一的涪江校区进行整体置换，并完整继承了原西南财经大学电子商务学院（二本招生）的全部师资和管理队伍、办学理念、图书资料、教学仪器设备、校园建筑及生活设施等而设立的独立学校。2010年及2012年，我校均荣获了四川省教育厅两年一评的“普通高等学校毕业生就业工作优秀本科院校”称号，并在2010年二十一世纪中国教育年度总评榜中，荣获“21世纪中国教育改革创新示范院校”，2011年荣获“',2059119);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津大学仁爱学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjrac.edu.cn','天津大学仁爱学院是经国家教育部批准，由天津大学和天津仁爱集团合作创办的具有独立法人资格、独立的校园校舍、独立招生和独立财务的本科层次全日制普通高等学校。天津大学仁爱学院充分发挥天津大学的优质教育资源，创新办学机制，实行开放式办学、多样化培养，办学水平和人才培养质量位居同类院校前列。2010年学校被评为全国先进独立学院。2013年7月我校成为首批第一所通过教育部全国独立学院规范验收的学校，受到教育部专家组的高度评价。天津大学仁爱学院立足天津、融入滨海，面向环渤海区域经济建设，服务全国，主动适应社会',1248165);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川大学锦江学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.scujjedu.cn','四川大学锦江学院是经国家教育部批准，由百年名校、国家“985工程”和“211工程”首批重点建设的教育部直属全国重点大学—四川大学举办的独立学院;是一所按照新机制、新模式运行的全日制普通本科高等学校。学校设13个学院，37个本科专业、8个专科专业，学科涵盖文、理、工、经、管、艺等门类，面向全国招生，在校学生16000人。学校恪守“尊德性而道问学，致广大而尽精微”的校训，践行“学生为本，通专并重，知行合一，教学相长”的办学理念，培养具有国际视野的高素质应用型人才。学校地处四川省彭山县，位于成都天府新',1557755);
