insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安理工大学高科学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xthtc.com/','西安理工大学高科学院是2006年经教育部批准成立的全日制普通本科独立学院。学院位于陕西省西安市大学城，建筑面积10万平方米；目前拥有17个实验室，教学仪器设备总值2000万元；现开设英语、信息管理与信息系统、机械设计制造及其自动化、电子信息工程、计算机科学与技术、包装工程、财务管理、电气工程及其自动化、工程管理、工业设计和国际经济与贸易11个本科专业，面向全国14个省（市）招生。学院以西安理工大学的优势学科为基础，以优质的人才资源和良好的办学声誉为依托，聘请了一大批知名的教授担任各学科带头人，教',360654);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安科技大学高新学院',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.gaoxinedu.com','西安科技大学高新学院是经教育部批准，由西安科技大学主办的全日制普通本科高校。学院秉承西安科技大学五十余年厚重的历史文化积淀，充分发挥其优质教育教学资源和学科优势，注重实践教学，突出“产学研”相结合的办学特色，务实进取，开拓创新，取得了丰硕的办学成果。学院先后被评为“西安市文明校园”、“西安市绿化园林式校园”、陕西省民政厅“5A级社会组织”单位；学院产学研基地建设项目于2009-2015连续七年被列为“西安市重点建设项目”、2015-2016年被列为“省级重点建设项目”；学院创业产业基地被纳入陕西',1234679);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南科技大学城市学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://cc.swust.edu.cn/','西南科技大学城市学院是经教育部批准设立，由西南科技大学举办的全日制普通高等学校，在本科第三批次、专科第二批次招生。学院由西南科技大学原副校长李众立教授出任院长。学院现有18个本专科专业，专业以工为主，涵盖工、管、文等学科领域。学院以建设具有鲜明办学特色的新型应用型大学为目标，培养“本科学历+学位+专业技能素质”的创新型工程科技人才。学院坐落在著名的中国科技城、四川省第二大城市绵阳市的富乐山风景区南麓，距市中心3公里，交通便利。毗邻中国工程物理研究院，并与我国西部航空城仅一墙之隔，具备浓厚的科学技',434888);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建农林大学金山学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://jsxy.fafu.edu.cn','福建农林大学金山学院是2006年经教育部批准成立的全日制本科层次独立学院。2011年起学院在福建省本二批次招生，2012年增列为学士学位授予单位。学院是2013年大学生创新创业训练计划国家级学校。学院位于福建省福州市区，坐落于风景秀丽的闽江与乌龙江之间，占地720亩，现有校舍建筑面积24万平方米，拥有现代化的多媒体教学楼、实验楼、图书馆、体育馆等，教学设施先进，生活条件完备。学院无缝对接福建农林大学校本部，可充分共享其优质教育资源和优美的校园环境，共沐福建农林大学79年办学历史积淀的校园文化，现',398870);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古科技职业学院',' ','内蒙古',0,1,0,0,0,' ','暂空',' ','http://www.imtvc.cn/','内蒙古科技职业学院成立于1991年，是经内蒙古自治区人民政府批准、国家教育部备案，纳入国家统一招生计划，具有独立颁发国家承认学历证书资格的的全日制普通高校。学院是内蒙古自治区“先进办学单位”、“就业先进单位”、“学生资助工作先进单位”、“教育部示范性实训基地”，设有国家职业技能鉴定站、普通话水平测试站、数字视频DV策划师命题考试机构、全国计算机应用技术证书考试(NIT)内蒙古考试培训中心、国家大学毕业生创业培训基地。学院目前设有医学技术系、教育系、汽车工程系、建筑工程系、经济管理系、艺术传媒系、',40602);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古北方职业技术学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.nmbfxy.com','内蒙古北方职业技术学院座落于自治区首府呼和浩特市大学城，始建于1984年，是经自治区人民政府批准，国家教育部备案，国家计划统一招生的全日制综合性普通高等学院。学院具有鲜明护理、医药专业特色，是同类院校中唯一获得中央财政支持的实践教学基地院校，政策上享有单独考试招生的优势。先后被教育厅评为“自治区示范性高职院校”、优秀办学单位”、“依法治校示范校”、“就业示范100强”、“百名优秀学生母校”等荣誉称号。学院占地521亩，建筑面积12万平方米，教学仪器设备总值3000余万元，各类图书资料30余万册。',52643);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('赤峰职业技术学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.cfzyjsxy.com','赤峰职业技术学院（原内蒙古职业技术学院）于1995年筹建；1996年学院成立。2006年4月经内蒙古自治区人民政府内政字（2006）119号文件批准、国家教育部教发[2006]11号文件备案，更名为赤峰职业技术学院。学院系国民经济教育序列、国家统一招生的一所全日制普通高等学历教育院校。学院目前占地面积100亩，总建筑面积30000平方米，基础建设和教学实验设施设备总值1亿余元，现有专兼职教师200余人，各级各类在籍学生2776人。目前，为扩大办学规模，学院多方面筹措资金，计划新征地500亩，进行',32858);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古经贸外语职业学院',' ','内蒙古',0,6,0,0,0,' ','暂空',' ','http://www.nmgjwy.com ','内蒙古经贸外语职业学院是经内蒙古自治区人民政府批准、国家教育部备案，是具有颁发国家承认学历资格的全日制普通高等院校。学院座落于依山傍水、绿树成荫的大青山脚下，占地面积875亩，建筑面积8万多平方米，有完善的办学条件和良好的教学设施。学院总规模可达到6000余人，现有在校生2600余人，教职工107人，固定资产总值过亿元，教学仪器设备总值700多万元，图书馆藏书6万余册，长年聘请5名外籍专职教师授课。学院立足区内，面向全国，服务于区域社会经济发展。学院肩负着培养面向生产、建设、服务和管理第一线需要',42812);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林铁道职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.jtpt.cn','吉林铁道职业技术学院坐落在美丽的北国江城——吉林省吉林市，是一所具有60多年职业教育历史的省属高职院校。设东西两个校区，东校区位于松江东路189号，西校区位于中兴街43号。学院曾隶属于铁路行业，其前身是创建于1948年的吉林铁路经济学校和创建于1958年的吉林铁路运输职工大学，2001年6月两校合并，2005年8月移交吉林省政府，由省教育厅管理，2006年2月，经省政府批准改建为吉林铁道职业技术学院，是吉林省唯一一所具有铁道类专业的高等职业院校。多年来与铁路企业建立起的长期稳固合作关系和广阔的人',358841);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江交通职业技术学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.hlcp.com.cn/zs/','黑龙江交通职业技术学院是经黑龙江省政府批准、教育部备案的一所公办高等职业技术学院，学院位于美丽的鹤城齐齐哈尔市，迄今已有60年的办学历史。学院2005年由铁路移交地方政府管理后，继续坚持立足铁路、面向龙江、辐射全国的办学宗旨。学院现有铁道运输管理、铁道机车车辆、土木工程、电信工程、机电工程5个系。普通高职专科共开设20个专业，开设的专业主要面向铁路、交通、信息技术、装备制造等领域。学院现有教职工385名，从事教学科研的人员260名，其中教授、副教授86人。已形成了一支热心教育事业、业务水平较高、',158514);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨应用职业技术学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.hyyzy.com/','哈尔滨应用职业技术学院是黑龙江省人民政府批准，哈尔滨市人民政府主管，国家教育部备案、独立设置、国家计划内统一招生，具有独立颁发国家承认学历文凭资格的全日制普通高等院校。现已成为一所以工科类学科为特色，以工、文、理、经、管多学科协调发展，培养经济社会发展急需的一专多能高技能型人才的综合性国家普通高校。学院占地面积24.8万平方米，建筑面积13万平方米。校园内绿地、甬道、花坛、雕塑与错落有致的十几栋大楼交相辉映，在机场路旁形成一道独特的风景线，学院实行多媒体教学，图书馆、阶梯教室、400米标准运动场',44018);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京机电职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jseea.cn/schoolinformation/1338366542990.html','南京机电职业技术学院是经省政府批准、教育部备案的一所国有公办高职院校，坐落于南京麒麟科技创新园内。学院设有机械工程系、电子工程系、信息工程系、自动化工程系、人文社科系、继续教育学院。全院教职工近400人,在校生近9000名。学院开设了22个专业，涵盖了机械、电子、计算机、艺术设计、通信、物联网、会计等多个领域。其中电气自动化、数控技术两个专业被列为教育部、财政部“提升专业服务产业能力”重点建设专业;数控技术、数控设备应用与维护、机械设计与制造、机械制造及自动化、模具设计与制造五个专业构成的“机械',78418);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('芜湖信息技术职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.whit.ah.cn/','芜湖信息技术职业学院是经安徽省人民政府批准、教育部备案、独立设置的国有公办全日制普通高等院校。其前身为始建于1978年的芜湖教育学院。已具有三十年办学历史积淀的芜湖信息技术职业学院，经过近几年的快速发展如今已成为集学历教育、职业培训、职业技能鉴定与社会服务为一体的普通高等职业技术学院。学院是芜湖市文明标兵单位；劳动与社会保障部和信息产业部高技能人才培训基地；国家职业技能鉴定考试中心；国家四、六级英语考试考点；全国计算机等级考试考点；商务部国际商务培训考点；对日软件工程师培训基地；安徽省中职骨干教',48216);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门软件职业技术学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.xmist.edu.cn/','厦门软件学院是在福建省教育厅和厦门市政府倡议下创办，经福建省人民政府批准、教育部备案、面向全国高考统招的全日制普通高等院校（国标代码14059）。学院座落在高校云集、文化底蕴深厚、交通便捷的集美大学城，占地面积800余亩，规划建筑面积46万平方米，总投资逾12亿元，可容纳20000名学生学习、生活。学院校园环境幽雅、配套设施齐全、办学特色鲜明、教学设备先进、教风学风淳正、师资力量雄厚。学院大力推进教学改革，将课程设置与岗位需求对接、授课形式同职场实景置换，因材施教，增强学生就业竞争力，使学生毕业',160426);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福建体育职业技术学院',' ','福建',0,7,0,0,0,' ','暂空',' ','http://www.fjipe.cn/new/','福建体育职业技术学院于2005年经福建省人民政府批准正式成立，隶属于福建省体育局。是福建省属专科层次的普通高等职业院校，以全日制高等专科教育为主，同时开展多种形式的非学历教育。学院2003年经福建省教育厅批准筹建，开始招收运动训练专业五年制专科学生，2006年开始正式招收全日制普通高等职业两年制专科学生，是省内唯一一所集培养高水平竞技体育专业人才和社会体育管理服务人才为一体的高职体育院校。福建体育职业技术学院是在整合省体工大队、省体工二大队、省射击中心、省游泳中心、省体育学校、省体育科学研究所等',40620);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三峡电力职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.tgcep.cn/','三峡电力职业学院是经湖北省人民政府批准，中华人民共和国教育部备案的全日制普通高等专科院校。校园坐落在世界水电之都——湖北省宜昌市市中心的东山之巅，占地面积429亩，建筑总面积15万余平方米，校园风景宜人，一年四季鸟语花香，既携秀山丽水的神韵，又带水电之城的灵气，是读书求学的理想之地。学院确立“以市场为导向、以学生为中心、以能力为本位、以特色促发展”的办学理念，设有动力工程系、电力工程系、建筑工程系、机电工程系、经济管理系、环境艺术系等6个教学单位，开办了水利水电工程、发电厂及电力系统、电厂热能动',63397);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州电力职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zzdl.com','郑州电力职业技术学院是经河南省人民政府批准成立，国家教育部备案的一所全日制普通高等院校,郑州电力中等专业学校与郑州电力职业技术学院属两校一体、资源共享、共同发展。学校位于历史古都郑州市商都大街东段310国道旁，占地500余亩，建筑面积16万平方米，在校生13000余人。学院高楼林立，绿树成荫、楼台亭榭、苍松翠柏，环境清静优雅，是莘莘学子深造成才的理想学府。多年来，学院在院党委、院委会的领导下，团结奋进，积极探索，不断改善办学条件，完善教学体制，贯彻“重视理论学习、强调实践操作”的教学方针，注重素',72466);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东科贸职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gdkm.edu.cn','广东科贸职业学院前身是广东省农业管理干部学院（代码：14063），成立于1985年，是一所经广东省人民政府批准成立，国家教育部备案的公办普通高等学校。省人大常委会主任欧广源任我院名誉院长。学院现有广州天河校区和广州白云校区，普高招生专业30个，在校生人数5000多人，今年招生面向广东、广西、海南、福建、江西、湖南、四川、重庆八个省市。学院有深厚的历史和文化底蕴，师资力量雄厚，治学严谨，校风优良。推行“双证书”制度，注重学生实操技能的培养，有较完善的教学基础设施和实训条件，能为学生提供80多种职业',123986);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州科技贸易职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gzkmu.com','广州科技贸易职业学院由广州市科学技术协会于1984年创办，是经广东省政府批准、国家教育部备案的广州市属公办全日制普通高等职业院校。著名科学家、中国工程院院士、广州市科学技术协会名誉主席钟南山担任名誉院长。2011年学院顺利通过了人才培养工作评估，现正在创建省级示范性高职院校。学院现有番禺校区（毗邻广州大学城）、越秀花果山校区，校区地理位置优越，校园环境优美。设有商贸学院、管理学院、机电系、信息工程系、艺术设计系、服装系、公共课教学部、思政部。全日制在校生6800余人。教职员工453人，其中专任教',171697);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中山职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','oldname": "中山职业技术学院','中山职业技术学院是一所省市共管、以市为主，面向社会，面向市场，培养高素质技能型人才的公办全日制普通高等学校。它筹建于2005年，2006年6月正式挂牌成立。学院占地面积600余亩，建筑面积18万平方米，前后投入资金近7亿。现代化校园，环境幽雅、文风浓郁、设施齐全，拥有各类图书共计43.7万册。学院把握先进高等职业教育的前进方向，坚持“育人为本工学结合创新发展服务社会”的办学理念，结合区域产业结构特点和自身优势资源设立了机械工程系、电子信息工程系、计算机工程系、经济管理系、艺术设计系等五个系，建成',111182);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('柳州城市职业学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.lcvc.cn','柳州城市职业学院是一所由柳州市人民政府主办的公办全日制高等职业学校，学院以全日制高等职业教育为主导，集学历教育、成人教育、远程教育、国际留学生教育和技能培训于一体，培养城市化进程中的现代服务业高端技能型人才。目前，学院设有文化与旅游系、经济贸易系、机电与汽车工程系、建筑工程与艺术设计系、信息工程系、中专部、公共教学部等七个教学系部;设有由成人教育处、培训处、远程教育中心组成的开放学院;开设有学前教育、汽车检测与维修技术、汽车营销与服务、会计、物流管理、小学教育、建筑装饰工程技术、空中乘务等39个',53969);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('百色职业学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.bszyxy.com/','百色职业学院创建于2006年3月，由百色市人民政府举办、建设和管理，业务上受自治区教育厅指导。学院是国务院扶贫办认定的广西唯一一所劳动力转移培训示范基地，是自治区大石山区特困村屯贫困家庭子女职业教育工程培训基地，是自治区人力资源与社会保障厅劳动力培训品牌基地，是百色市安全生产培训基地，是百色市残疾人培训示范基地、是百色市微型企业孵化园，是共青团百色市委青年就业创业培训基地。有国家人社部颁发认定的国家级职业技能鉴定所一个。学院成立以来，先后被授予“全国农村成人教育先进学校”、“全区高校先进党组织”',25768);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆青年职业技术学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqyu.edu.cn/web/guest/home','重庆青年职业技术学院(国标代码：14069)是由重庆市人民政府举办，教育部备案，重庆市教育委员会主管，面向全国招生的全日制公办普通高等学校。学院创建于1954年，秉承“明德笃学、求真强技”的校训，坚持“以人为本，敢为人先”的发展理念，坚持“产教融合、校企合作、工学结合、知行合一”的办学思路，以立德树人为根本，以内涵建设为主线，以服务发展为宗旨，以促进就业为导向，着力培养真才实学、朝气蓬勃、忠诚勤奋、敢于担当的高素质技术技能型人才。区位优势明显。学院坐落在风景秀美的北碚区国家大学科技园旁，紧邻国家',97686);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川科技职业学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.scstc.cn','四川科技职业学院是经国家教育部备案，工学、理学、管理学、经济学、文学、艺术、农学、医学等多学科综合发展的全日制、综合性普通高等院校，面向全国统一招生，在校生规模达16000余人。学校秉承“修德、技精、勤思、协作”的校训，围绕“授人以渔，能力为本”的教育理念，践行“用明天的主流技术培养今天的人才”的宗旨，在“倾力打造东方加州理工学院”办学愿景的指引下，致力于培养具有能适应生产、建设、服务和管理需要的高端技术技能型人才。学校地处成都市国家高新技术开发区西区团结“大学城”，享有得天独厚的地理优势，科技',141016);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南电气职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnjd.net.cn/','湖南电气职业技术学院是一所有近70年办学历史的公办高等职业技术教育专科学校。学院位于伟大领袖毛泽东的故居、长株潭城市群两型社会配套改革试验区——湘潭市。学院规划用地面积400亩，毗邻国有大型企业湘电集团和湘钢集团，校园环境幽雅，空气清新。学院于2006年4月由湘潭电机厂职工大学和湘潭电机厂技工学校合并组建，其前身可追溯至1941年湘潭电机厂举办员工训练（学校）班。2006年以来，学院事业规模不断扩大，办学条件明显改善，综合实力日益增强，成为国家高技能人才培训基地、国家职业技能鉴定所、湖南省青年职',36374);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南外国语职业学院',' ','湖南',0,5,0,0,0,' ','暂空',' ','http://www.hnflc.cn','湖南外国语职业学院是湖南省人民政府批准、国家教育部备案的一所全日制普通高等院校，也是中南地区唯一一所独立设置的以外语类专业为主的全日制外国语高职学院。学院现为中国国际教育交流协会常务理事单位、中国西部教育顾问单位。学院分长沙和浏阳两个校区。目前在校学生7000余人，占地面积39.7万平方米，建筑面积17万平方米，固定资产总值超过1.46亿元。学院交通便利，环境优美，教学和生活设施完善，是广大学子理想的求学深造之地。学院高度重视师资队伍建设，把师资队伍建设作为基础性、战略性工作和“强校工程”来抓，',95342);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京经济管理职业学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.biem.edu.cn/','北京经济管理职业学院（原北京市经济管理干部学院）（以下简称学院）是经北京市政府批准、国家教育部备案的普通高等学校，1979年12月创建。学院以举办全日制高职教育为主，同时承担着国家重点企业和北京市国资委系统企业经营管理人员培训、国际合作办学、留学生教育、党校函授教育及研究生教育等任务。1988年，成立北京国际语言培训中心；1991年6月，成立北京市工业党校（现为北京市国资委党校）；2003年3月，成立北京经理学院；2003年8月，经国务院国资委批准，河北远东职业技术学院整体划转学院，设立学院南校',219758);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京劳动保障职业学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.bvclss.cn','北京劳动保障职业学院隶属于北京市人力资源和社会保障局，于1984年开始筹建，1986年正式招生，其前身是北京市计划劳动管理干部学院，1999年开始普通高等职业教育招生。建院二十多年来，学院大力发展高等职业教育，依托行业优势，以就业为导向，以服务为宗旨，为首都经济建设和社会保障事业发展培养了大量高素质技能型专门人才。学院现设有劳动经济管理系、安全工程系、工商管理系、机电工程系、实训中心和基础部等6个教学系部。开设有劳动与社会保障、人力资源管理（人力资源服务）、城市管理与监察（城市设施安全技术）、城',256167);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁装备制造职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://zy.ltcem.com/','辽宁装备制造职业技术学院,坐落在沈阳东部辉山风景区,占地538亩,建筑面积15万平方米，其中有29000平方米实习厂房、实训工作室和制造业博览中心，配置教学设备和仪器价值3850万元。学院现有机械工程、自动控制、信息工程、经济管理、人文社科和艺术设计共6个系，在校学生6290人。辽宁装备制造职业技术学院是辽宁省政府整合原辽宁电大高职院和其他社会资源，于2006年快速高效创办起来的。学院定位是服务于国家振兴战略，紧紧围绕现代装备制造业特别是柔性制造等先进制造技术的发展，培养生产、建设、管理、服务一',123217);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽河石油职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','oldname": "辽河石油职业技术学院','辽河石油职业技术学院是经辽宁省人民政府批准，教育部备案的一所以油气开采技术、钻井技术、石油与天然气地质勘探技术、油田化学应用技术、油气储运技术为主体的理工科高等职业技术学院。学院占地面积66万平方米，分两个校区，即辽河石油职业技术学院校本部和渤海校区，其中，教学用建筑面积9.4万平方米，图书馆藏书26.78万册。学院拥有校园网、远程教育网络系统、电视教学系统，建有多媒体网络教室、语音教室、远程双向视频教室等现代化教学设施，建有满足教学实践需要的标准实验室、校外实习基地及校内实习基地。学院还设有国',64228);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东商务职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdbi.com.cn/','山东商务职业学院位于开放前沿的海滨城市——烟台，是经山东省人民政府批准设立、国家教育部备案的省属公办全日制普通高校。学院坐落于烟台市国家级高新区的中心地带，背靠高新产业，北临大海，风景秀丽，环境宜人，教育文化氛围浓郁。校园占地1397亩，校舍建筑面积36.7万平方米，总投资达7亿元。目前，学院面向全国20余个省市招生，在校生14000人。优异的育人质量和雄厚的办学实力，使我院连续多年超额完成招生计划。近三年学院录取新生全部为第一志愿，成为全省招生情况最好的几所专科院校之一。学院始终坚持与时俱进的',219765);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东城市建设职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdcjxy.com/','山东城市建设职业学院位于泉城济南，其前身为创建于1980年的山东省城市建设学校。学院由山东省教育厅、住建厅共管，住建厅主管，以“培育鲁班传人，建设美丽城市”为己任，集全日制普通高职、五年制高职、成人教育为一体的公办高等职业院校。2013年12月，被省教育厅评定为“山东省技能型人才培养特色名校”。鲜明的办学特色：建校30年来，学院始终坚持以培养“城市建设”人才为特色，致力于建筑类专业的教学与研究，底蕴丰厚。完善的专业设置：学院开设建设管理类、建筑设计类、土建施工类、环保类等四大类31个专业，建有1',439893);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('烟台汽车工程职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.ytqcvc.cn/','烟台汽车工程职业学院是一所经山东省政府批准、国家教育部备案的公办高等职业院校。学院座落在美丽的滨海城市-烟台，具有坚实的办学基础和雄厚的师资力量。学院占地1000亩，建筑面积300000平方米；现有教职工510人，其中专任教师385人，副教授以上职称的教师91人，硕士研究生学位的教师145人；设有汽车构造实验室、汽车发动机实验室、金相物理实验室、数控机床实验室、机械加工中心、焊切技术车间等三十多个实验室和实训车间，并设有多媒体教室、微机室和音像备课中心，拥有国内领先的汽车发动机实验设备和大型数控',86004);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东司法警官职业学院',' ','山东',0,11,0,0,0,' ','暂空',' ','http://www.sdsfjy.com/','山东司法警官职业学院（原山东省法律学校）于2006年4月25日经省人民政府批准设立[鲁政字（2006）117号]，隶属于山东省司法厅,经过二十多年的建设和发展，学校办学取得了可喜成绩，先后被省政府授予“全省民族团结先进集体”称号，被省文明委授予“省级文明单位”称号，被司法部荣记集体一等功，被国家教育部批准为“国家级重点学校”。学院位于济南市风景秀丽的燕子山下，教学生活设施完善，拥有教学楼、办公楼、图书馆、礼堂、餐厅、浴室、学生和教工宿舍等教学生活设施，拥有教学用计算机420余台，并全部上网，实现',223353);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵阳护理职业学院',' ','贵州',0,3,0,0,0,' ','暂空',' ','http://www.gynvc.edu.cn/','贵阳市护理职业学院建校于1939年月10月，已有66年的办学历史和经验，现隶属贵阳市卫生局领导。学校位于贵阳市中心，占地面积47808平方米，建筑面积30075平方米，是一所全日制、多学科的中等职业学校。学校于1981年、1994年、2000年、2004年连续四次被评为国家级重点中等职业学校；1999年学校14个专业通过贵州省中等医学教育专业认证，2002年护理专业被卫生部、教育部批准为首批国家级重点建设示范专业；2004年，学校被列为全国护理专业技能型紧缺人才培养培训工程项目学校。学校师资力量',231533);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西藏职业技术学院',' ','西藏',0,0,0,0,0,' ','暂空',' ','http://www.xzgzy.cn','“国家示范性高等职业院校”西藏职业技术学院位于拉萨市，是于2005年7月经自治区人民政府批准，在西藏自治区农牧学校和西藏自治区综合中专学校的基础上合并组建而成。2006年9月正式挂牌成立，隶属自治区教育厅。学院现有西、北两个校区，一个教学实训基地，占地面积36.56万平方米。各类教学仪器设备总值2598万元，馆藏纸质图书27.78万册。学院现有农林系、畜牧兽医系、机电系、电子信息系、建筑系、旅游系、财经系、公共教学部、成人教育部和中央农业广播电视学校西藏分校等“七系两部一校”10个教学部门；设有',33173);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川文化产业职业学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.scvcci.cn/','四川文化产业职业学院是经教育部备案、四川省人民政府批准、中共四川省委宣传部主管的公办全日制普通高等专科学校，全国第一所独立设置的培养高端技能型专门人才的文化产业普通高校，四川省第一批教育信息化试点单位，四川省卓越新闻传播人才教育培养计划高校，四川省省级示范性高等职业院校建设计划重点培育院校。学校实行四川文化产业职业学院、四川省干部函授学院、四川省宣传文化干部培训中心和四川文化产业发展研究中心“两院两中心”统一运作的组织管理模式。学校主校区位于成都市天府新城华阳，距成都中心城区12公里，校园面积5',339285);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津财经大学珠江学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://zhujiang.tjufe.edu.cn/','天津财经大学珠江学院成立于2006年，是经教育部批准、由天津财经大学与广东珠江投资有限公司按照独立学院模式和机制合作建立的一所本科层次的普通高等院校。天津财经大学珠江学院的举办方实力雄厚，举办方之一的天津财经大学系新中国最早建立的五所财经大学之一，拥有50多年的办学史，积淀深厚，在财经教育领域享有较高的知名度；另一举办方广东珠江投资有限公司则系全国知名的大型房地产开发集团公司。天津财经大学珠江学院坐落于风景优雅，环境清新的天津市宝坻区京津新城，紧邻津蓟高速公路，交通便利。校区占地面积900亩，基',1185961);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('温州科技职业学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.wzvcst.cn/','温州科技职业学院是一所由温州市人民政府举办，国家教育部备案的公办普通高等职业学院。办学理念先进，办学定位明确学院坚持发挥“农科教一体化，产学研相结合”的优势，确立“以服务为宗旨、以就业为导向，走产学研结合发展道路，立足温州、服务浙江、面向全国，培养高端技能型人才，建设农类特色鲜明、全面发展的高职院校和农业科研强院”的办学定位。师资力量雄厚，教学成效显著学院现设有农业与生物技术系、园林系、动物科学系、经贸管理系、信息技术系、公共教学部、成教学院等五系一部一院，已开设农林牧渔、财经、电子信息、制造等',83621);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江汽车职业技术学院',' ','浙江',0,1,0,0,0,' ','暂空',' ','http://www.geelyedu.com/sy.html','浙江汽车职业技术学院是由吉利集团出资举办、浙江省人民政府批准、教育部备案，我省目前惟一的一所汽车类专科层次的全日制普通高等职业技术学院，坐落在国家级历史文化名城——浙江省临海市。学院与吉利临海生产基地紧密相依，与吉利汽车研究院咫尺毗邻。学院依托吉利集团的汽车产业优势，实行工学结合、校企合作的人才培养模式，是一所“前校后厂”、初具德国“双元制”职业教育模式特色的高等职业学院。学院现开设了“商务英语”、“计算机辅助设计与制造（汽车造型方向）”、“汽车制造与装配技术”、“汽车检测与维修技术”、“汽车电',41871);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江横店影视职业学院',' ','浙江',0,8,0,0,0,' ','暂空',' ','http://zjhyxy.zhiye.chaoxing.com/','浙江横店影视职业学院(筹)是经浙江省人民政府(浙政函［2006］54号文)批准由全国特大型民营企业----横店集团投资筹建的一所以影视科技和影视工程教育为主，以影视艺术和影视产业管理为辅的多科性高职院校。横店集团是以电气电子、医药化工和影视娱乐为主导产业的国家特大型民营企业。自1996年以来，横店集团已投资约30多亿元精心培育影视产业，并探索出一条企业自主投资建设，以市场为导向的影视产业运作体系。经过十年的发展和积累，横店已经拥有亚洲规模最大的13座拍摄基地和两个现代化摄影棚。截止2006年底，',119081);
