insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('滇西科技师范学院',' ','云南',0,4,0,0,0,' ','暂空',' ','http://www.lcnc.edu.cn/','临沧师范高等专科学校是在临沧教育学院的基础上经教育部正式批准改制的一所公办全日制普通高等师范专科学校。学校代码为：14092。临沧师范高等专科学校建在临沧市，临沧市位于云南省西南部，因濒临澜沧江而得名。南与缅甸交界，国境线长290.29公里，有3个国家级、省级口岸、17条边贸通道，是通往南亚、东南亚的重要门户。临沧四季如春，有“亚洲恒温城”之美称。临沧是“世界著名的滇红之乡”;“亚洲独具特色的水电基地”;中国佤文化荟萃之地;“云南重要的蔗糖和酒业生产基地”；“昆明通往缅甸仰光的陆上捷径”。临沧交',57790);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('运城幼儿师范高等专科学校',' ','山西',0,4,0,0,0,' ','暂空',' ','http://www.sxycys.com','运城幼儿师范高等专科学校是经国家教育部批准成立的一所培养学前教育教师的高等学校，其前身是1978年建立的运城市幼儿师范学校。学校坐落于华夏之根、诚信之邦、大运之城的中国十大魅力城市之一的运城市，地处空港新区南二路，占地面积540亩，建筑面积11.3万平方米。学校现设有学前教育、语言文学、艺术、美术4个系和基础教学部，开设学前教育、语文教育、英语教育、音乐教育、美术教育、艺术设计、舞蹈表演、体育教育8个专业。图书馆藏书17万册，教学设备仪器价值1500万元，在校生4000余人。学校环境优雅，教学设',78538);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江幼儿师范高等专科学校',' ','黑龙江',0,4,0,0,0,' ','暂空',' ','http://zsjy.hljys.cn/zs/','黑龙江幼儿师范高等专科学校是国家教育部于2007年3月16日正式批准成立的一所公办普通高等院校。学校的历史可以追溯至1906年(清光绪三十二年)清政府在宁古塔(现宁安市)两等小学附设的师范学堂，至今已有110年的办学历史。1990年以后，牡丹江师范学校、宁安师范学校、牡丹江幼儿师范学校三所学校合并，建立黑龙江幼儿师范高等专科学校，发展成为独具特色的高等学府，是全国最早组建的三所幼儿师专之一。学校占地面积17.6万平方米，建筑面积7.8万平方米，固定资产8364万元，其中教学仪器设备2566万元，',93127);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安庆医药高等专科学校',' ','安徽',0,3,0,0,0,' ','暂空',' ','http://www.aqyyz.cn/','安庆医药高等专科学校前身是安庆卫生学校，办学历史可以追溯到1943年诞生于抗日烽火中的“安徽省省立立煌高级助产护士职业学校”，是安徽省最早开办的公立医学教育学校。2007年3月，经国家教育部和安徽省人民政府批准，独立升格为安庆医药高等专科学校，填补了皖西南医药高等教育空白。2012年，顺利通过教育部“高等职业院校人才培养工作评估”。学校自2005年至今，连续3届获得“全国文明单位”称号。学校目前设有临床医学系、药学系、护理系、公共基础部等4个教学系部和9个党政管理部门及教辅机构，拥有1所直属附属',146070);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('益阳医学高等专科学校',' ','湖南',0,3,0,0,0,' ','暂空',' ','http://www.hnyyyz.com','益阳医学高等专科学校的前身是创办于1950年的益阳卫生学校，2007年3月经教育部和省人民政府批准升格为普通医学高等专科学校，现有教职员工520人，在校学生8000人。学校位于益阳市风景秀丽的梓山湖畔，东望长沙、北靠洞庭，新校区占地501.6亩，建筑面积20万平方米。学校遵循“以学生为本，以加强教师队伍建设为重点，以教学为中心，以深化教学改革和提高科研水平为核心，以教育教学质量为生命”的办学宗旨，以培养具有创新精神和实践能力，下得去、用得上、留得住的高素质医疗卫生技术应用性人才为目标，建有较为现',136166);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥师范学院',' ','安徽',1,0,0,0,0,' ','暂空',' ','http://www.hfnu.edu.cn','合肥师范学院坐落在包公故里、全国四大科教基地城市之一的安徽省省会合肥市，是一所省属全日制普通本科院校。学院校园占地603亩，校舍建筑面积28.5万平方米。教学科研仪器设备总值3654万元。建有计算机、语音、教师技能训练3个公共教学实验中心和10个综合实验室。馆藏纸质图书83.63万册,电子图书41万册，中外文期刊1419种，拥有6个中外文数据库。学院长期以本科学历教育为主，形成了以人文学科、理学为主干，经、管、文、理、工等多学科协调发展的学科专业体系。设有中文、政法与管理、教育、经济、外语、美术',968581);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北第二师范学院',' ','湖北',1,4,0,0,0,' ','暂空',' ','http://www.hue.edu.cn','湖北第二师范学院坐落在九省通衢的历史文化名城武汉市，是一所以教师教育为特色，文、理、教育、经济、工学协调发展的省属公办普通本科院校，是湖北教师教育的重要基地。学校始于1931年创立的湖北省立教育学院。2007年3月，经教育部和湖北省人民政府批准，改建为湖北第二师范学院。2009年1月，与湖北省经济管理干部学院合并，组建新的“湖北第二师范学院”。学校面向全国招生，为湖北乃至全国培养、培训各类高素质人才。学校下辖武汉语言文化职业学院。校园如画学校现有三个校区（光谷主校区、卓刀泉校区和汤逊湖校区），占',1387685);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东政法学院',' ','山东',1,11,0,0,0,' ','暂空',' ','http://www.sdupsl.edu.cn','山东政法学院是由山东省人民政府领导、山东省教育厅主管的国办普通本科高等院校，学校位于美丽的山东省会城市——济南市。学校从最初的山东政法干校（1955年）发展至今已有50多年的历史。半个世纪以来，她历经沧桑、艰苦创业，秉承求真务实、特色发展的办学传统，在几代山政人的辛勤耕耘和不懈努力下，学校的各项事业都有了长足的发展，现已发展为一所以法学教育为特色涵盖法学、经济学、管理学、文学等多学科的省属普通高等院校，为国家培养了一大批优秀的法律类、经济类、管理类、外语类等高级专门人才。学校占地853亩，校舍建',1759328);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原工业学院',' ','山西',1,1,0,0,0,' ','暂空',' ','http://www.tit.edu.cn','太原工业学院创建于1954年，坐落于具有2500年历史的古城—山西省太原市。建校55年来，学校坚持“知行合一、行胜于言”的育人理念，共培养各类人才近10万名，现已发展成为以工为主，涵盖工、理、经、管、文、法六大学科门类的全日制普通高等院校。学院下设机械工程系、电子工程系、自动化系、化学与化工系、计算机工程系、环境与安全工程系、材料工程系、理学系、管理工程系、外语系、设计艺术系、经济与法学系等12个系以及继续教育部、体育与素质拓展部、思想政治理论教学部。共有本科专业29个。各类注册学生12000多',1514187);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津广播影视职业学院',' ','天津',0,8,0,0,0,' ','暂空',' ','http://www.tjgbys.com ','天津广播影视职业学院是经天津市人民政府批准并报国家教育部备案成立的天津第一所广播影视类学院，隶属天津广播电视电影集团,是独立设置的全日制公办高等职业学院。学院采用新的办学机制和模式，为全国广播影视等文化产业领域和信息传播领域培养急需的应用型、技能型高级人才。学院位于天津侯台湿地风景区，建筑面积12万平方米，集教学、科研与影视节目制作于一体，第一期工程主体建筑2.5万平方米已于2008年10月竣工。学院设立四系一部，即新闻采编系、主持与表演系、影视动漫系、影视技术系、基础课部。学院以天津广播电视电',82412);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('冀中职业学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.jzhxy.com','冀中职业学院是经河北省人民政府批准，国家教育部备案的国办全日制普通高等职业学院。冀中职业学院是一所古老而年轻的普通高等学校，拥有千年文化底蕴，百年师范教育历史。民国初年直隶女子师范在此兴办，1947年定州解放，改建为冀中区第一师范学校，新中国成立以后，改名为河北省立师范学校，2002年3月18日升格为保定师专定州分校。为适应社会主义市场经济对高技能人才的需求，遵循国家教育改革发展的要求，我院抓住机遇，深化改革，实施由师范教育向职业教育转型。2006年7月27日，经河北省人民政府批准、国家教育部备',40009);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西老区职业技术学院',' ','山西',0,0,0,0,0,' ','暂空',' ','http://www.sxlqzy.cn','学院简介：山西老区职业技术学院是经山西省人民政府批准、教育部备案、具有19年办学史的民办全日制高等院校。学院坐落于太原市和平北路东流街，交通便利、环境优雅。占地面积471亩，分南北两个校区，拥有设备完善的教学大楼、实验大楼、阶梯教室、图书馆、学生公寓及附属医院。图书馆藏有各类中外文期刊，专业信息资源丰富多样，电子阅览室常年开放，先进的计算机管理控制系统和设施完善的实验室服务于教学、科研、学生管理、文化宣传等工作；真正实现信息化教学，为提高学生的动手能力和综合素质创造了优良的条件。优越的地理位置：',62036);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁地质工程职业学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.lndzxy.com','辽宁地质工程职业学院是经辽宁省人民政府批准，国家教育部备案，以国土资源类专业人才培养为主的全日制普通高校，隶属辽宁省有色地质局，是我国唯一以地质命名的高等职业院校。学院坐落于全国最大的边境城市──丹东，占地面积502亩，在校生4000人。学院坚持以服务国土资源行业和区域经济社会发展需要为平台，遵循“以市场为导向，以改革为动力，以质量求生存，以特色求发展”的办学理念，以培养“下得去、留得住、用得上”高素质技术技能型人才为目标，形成了面向国土资源行业岗位群，以资源类专业为主体，以地矿方向机电和建筑类',83898);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('白城职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.bcvit.cn','白城职业技术学院是一所全日制公办高职院校。学院坚持以服务为宗旨，以就业为导向，以质量求生存，以改革为动力，以和谐促发展的办学方针；坚持依托市场，校企合作，工学结合，逐步实行弹性学制的办学模式，走产学研一体化的办学路子。为青年学子提供广阔的发展空间，为企业培养高素质的专业技术人才。优越的办学条件：学院办学条件优越，校园分南北两个校区，占地面积17万平方米，建筑面积5.27万平方米。除具有图书馆、多媒体教室、语音室等现代化教学设施外；更有能够满足教学需要的机电一体化数控编程实验室、电工电子电拖实验室',40629);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨科学技术职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.hrbkjzy.org.cn/','哈尔滨科学技术职业学院是由哈尔滨市政府创办、省政府批准、国家教育部备案的综合性公办高等职业学院。学院办学历史悠久，近年发展迅速，被评为黑龙江省文明单位标兵、黑龙江省就业工作先进单位、黑龙江省思想政治教育工作先进单位、哈尔滨市第十三届劳模单位、哈尔滨市教育系统先进集体标兵及哈尔滨市绿色学校。获得首批国家语言文字规范化示范校、国家语言文字工作先进集体等荣誉称号。学院座落于金源文化肇兴地——哈尔滨市阿城区。学院占地面积近25万平方米，校舍面积15万平方米，有设计先进，规模宏伟，能够充分体现现代建筑风格',87304);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江粮食职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.hljgvc.org.cn/','黑龙江粮食职业学院始建于1959年，是经黑龙江省政府批准、教育部备案、国家公办的教育厅直属普通高等职业技术学院，面向全国招收国家计划内统招生。学院座落在有东方小巴黎之称的美丽冰城哈尔滨市市中心，毗邻地铁一号线和兴路站，学院地理位置优越，交通条件便利。学院师资力量雄厚，师资结构合理，现有教授、副教授132人。我院是一所集粮食工程、粮油储检、经济管理、艺术设计、航空服务等多学科多领域的综合性职业院校。学院设有六个系一个部：粮油工程系、经济管理系、设计艺术系、信息工程系、航空管理系、物流与供应链管理系',33219);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('漳州城市职业学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.zcvc.cn/','漳州城市职业学院坐落在福建省沿海金三角地带的鱼米花果之乡、历史文化名城漳州，是一所公办全日制普通高等学校。漳州城市职业学院的前身为漳州教育学院，2007年经福建省人民政府批准更名（闽政文[2007]41号），教育部备案（教发函[2007]66号）。她是闽南地区一所百年名校，是一座革故鼎新、励志奋进的文化殿堂。自1905年，一百年来，她弘扬文化，铸造学风，延聘名师，培育英才，在风雨征途中精进日新；一百年来，她薪火传承，自强不息，为发展教育事业，推动地方经济建设和社会进步励精图治，不辱使命；一百年来',56934);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门南洋职业学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.xnanyang.com/','厦门南洋学院是经福建省人民政府批准正式设立，国家教育部备案，具有独立颁发国家承认文凭资格的全日制综合性普通高等院校，由海内外十五位热心祖国教育事业的中青年学者（教授、博士）、企业家联合发起创办。学校拥有良好的办学条件和一流的师资力量，现设有7个学院88个专业，在校学生近万人，是福建省发展最快、我国经济特区中规模最大的民办大学。有教学科研大楼、学生活动中心、大型学生综合餐厅以及10栋学生公寓生活楼，并设有篮球场10个、羽毛球场8个、排球场4个和高标准的多媒体、计算机教师、语言训练室等。现有从厦门大',198634);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门东海职业技术学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.xmdh.com','厦门东海职业技术学院是经福建省人民政府批准、国家教育部备案，纳入高考统招计划，具有独立颁发国家承认的高等教育大专学历文凭资格的全日制高等职业技术院校。【办学区位优势】学院座落在“国际花园城市”——厦门。厦门是我国第一批经济特区之一，经济发达，文化繁荣，风景秀美，重视教育，有“教育城”的美誉，多次获得国家卫生城市、国家园林城市、全国科教兴市先进城市、中国优秀旅游城市、国际花园城市、联合国人居奖和全国文明城市等荣誉称号。【办学理念领先】学院提出“立足厦门，面向海西，合理定位，科学发展”的服务海西行动',133923);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('漳州理工职业学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.zzlg.org/','漳州吉马印刷职业技术学院系福建吉马集团漳州吉马彩印有限公司创办的民办高等院校，2007年经福建省人民政府批准、教育部备案正式设立，（国际代码14115）。学院位于漳州市蓝田工业开发区东部，与著名的云洞岩旅游区毗邻。学院占地214亩，第一期规划至2010年，总投资3亿元，全日制在校生将达5000名以上，校舍建筑面积9万平方米。现有校舍面积5万平方米，专任教师200多名，图书符合国家办学标准，实验实训设备优良。校外实训基地厂房3万平方米，现代数字化印刷机械设备价值8000多万元。学院属专科层次的普通',52181);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武夷山职业学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.wyszyxy.com','武夷山职业学院是经福建省人民政府批准、教育部备案，由福建省金悦集团投资兴办的一所大专层次的民办全日制普通高等职业学院。学院位于世界双世遗产地、中国优秀旅游城市、中国茶文化艺术之乡——武夷山高教园区，占地面积500亩，建筑面积将达18万平方米。校园分教学区、教工生活区、学生生活区、文体活动区和后勤服务区。学院充分利用自然环境，将现代、简洁、流畅的建筑风格有机融入，配以园林、绿化、雕塑等景观，营造出人与自然和谐相处的校园环境特色。学院距武夷山市区3公里，到武夷山机场与火车站各约2公里，距风景区5公里',39712);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('漳州卫生职业学院',' ','福建',0,3,0,0,0,' ','暂空',' ','http://www.zzwzy.com','漳州卫生职业学院是一所全日制公办高等职业学院，其前身是创办于1934年的国家级重点中专学校—福建省漳州卫生学校。历经70多年的改革发展，学校在办学质量和办学规模上都得到长足的进步。改革开放以来，特别是近十年来，学校抓住机遇，深化改革，与时俱进，持续发展，2004年12月8日福建省人民政府批准同意升格为漳州医学护理职业学院（闽政文[2004]347号），2007年2月3日福建省人民政府批复同意更名为漳州卫生职业学院（闽政文[2007]19号），2007年4月通过国家教育部备案，是福建省紧缺人才（护',117844);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('菏泽家政职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.hzjzxy.com/','山东菏泽家政职业学院是在山东省菏泽卫生学校的基础上2007年3月经山东省人民政府批准,教育部备案的专科层次的国办全日制普通高职院校。学院坐落在苏鲁豫皖四省交界的单县城,交通便利,办学环境优雅.学院占地1000余亩,建筑总面积15.1万平方米,固定资产总值3亿元.其中教学科研仪器设备总值3000万元；图书馆藏书21万册,学院具有完善的教学设施设备,建有教学楼、实验楼、图书馆、办公楼、标准化学生公寓、多功能餐厅等基础设施；拥有现代化的计算机实验室、语言实验室、多媒体教室、家庭保洁实验室、家居布置艺术',61851);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北科技职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','oldname": "湖北科技职业学院','湖北科技职业学院是经湖北省人民政府批准、教育部备案，具有独立颁发学历文凭资格的省属全日制普通高等学校。学院位于武汉中国光谷软件园内，毗邻华中科技大学、中南财经政法大学、中南民族大学等高校。二十多年以来，学院坚持以高职高专为主体，同时与湖北大学、湖北工业大学、武汉工程大学等高校合作举办普通本科，积累了丰富的办学经验，现有在校普通本专科学生7000余人。学院占地面积360余亩，建筑面积近20万平方米，另有实训基地建设扩充规划用地200余亩，具备良好的办学条件。学院拥有一支政治、业务素质强，专业理论水',92372);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北青年职业学院',' ','湖北',0,11,0,0,0,' ','暂空',' ','http://www.hbqnxy.com','一、深刻厚重的办学历史湖北青年职业学院是我省唯一从事青年干部培养、研究青年教育发展的省属公办全日制普通大专院校。办学五十六年，积累了丰富的青年教育、管理经验，为青年工作战线培养了大批青年管理人才，同时也培养了大批生产、建设、服务、一线急需的应用型青年技术人才。顺应时代的发展，2003年我院在省委省政府关怀下，在教育厅的支持下，开始独立举办高等职业教育，目前在校高职学生2400人。2006年9月经湖北省政府批准、教育部备案正式成立湖北青年职业学院，学院发展揭开新的一页。二、创新发展的办学格局自20',37180);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南都市职业学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.hnupc.com','湖南都市职业学院是经湖南省人民政府批准、国家教育部备案的全日制职业普通高等院校。学院坐落在长沙黄花国际空港工业园，毗邻黄花国际机场，与15家世界五百强企业落户的长沙国家级经济技术开发区为邻，环境优雅、风景秀丽、交通便捷。到火车站乘114路公交车可在校门口下车，距飞机场仅3公里。学院以理工科为主，也开设一些文科特色专业的综合性院校。学院占地面积、建筑面积教学与其他用房以及设备设施、师资等，均符合国家教育部标准。馆藏图书20万余册，各类报刊杂志520余种。拥有校内印刷工厂、模具工厂、数控模具、钳工技',61081);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南电子科技职业学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://www.8379888.com','湖南电子科技职业学院是经湖南省人民政府批准，教育部备案的具有独立颁发高等教育学历文凭资格的全日制普通高等院校。学院坐落于国家历史文化名城长沙，地处湖湘文化独厚的河西大学城内，依巍巍麓山，临浩浩湘水，与麓谷国家级高新技术开发区一脉相衔，科技产业发达、生态气息和文化氛围浓郁。校园气势恢弘，环境幽雅，是求学修业的理想之处。学院下设电子通信工程系、计算机科学技术系、数控与模具工程系、汽车工程系、动漫设计系、服装艺术设计系、旅游管理系、经济管理系、国际金融管理系、外语系、基础课教学部和继续教育部，共开设二',46861);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州珠江职业技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','oldname": "广州珠江职业技术学院','广州珠江职业技术学院是2006年经广东省人民政府批准设立，国家教育部备案的一所综合性全日制民办普通高等院校，隶属于广东教育厅，学校已于2016年上半年通过了高等职业院校人才培养工作评估。承担国家计划内招生任务，面向全国招生，专业设置涵盖电子信息、财经商贸、装备制造、土木建筑、公共管理与服务、旅游、文化艺术、教育体育等几大类，现有教育学院、中医药学院、机电与汽车学院、建筑与艺术学院、珠宝与服装学院、信息技术学院、马克思主义学院、财经学院、管理学院等教学单位。有学前教育、中药学、药品经营与管理、汽车',89917);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州松田职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.sontanedu.cn','广州松田职业学院前身为广州大学举办的独立学院--广州大学松田学院专科部，具有10年办学历史和严谨的办学传统，是一所经广东省人民政府批准、教育部备案的全日制普通高等职业学院。学院由增城市松田实业有限公司投资，目前占地面积540亩，建筑面积20多万平方米。正在筹建中的新校园占地面积1300亩，已列入广东省重点建设项目。广州松田职业学院坐落于广州东部现代化生态新城区--增城市，地处珠江三角洲都市圈内，是广州、东莞、深圳等珠三角城市群和广深经济带的重要节点。毗邻港澳，南与东莞隔江相望，东临惠州，北界从化',107847);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西工程职业学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://gxgcedu.com','广西工程职业学院是2007年经广西壮族自治区人民政府批准设立、教育部核准备案，面向全国招生的一所全日制普通高等职业学校。学院设有南宁、平果两个校区。规划用地1000亩，校舍建筑面积18万多平方米。南宁校区坐落在中国-东盟博览会永久会址、广西壮族自治区首府南宁市;平果校区坐落在广西平果县大学城，离南宁市40分钟动车。校园碧草蓝天、花团锦簇，亭台点缀、雕塑点睛，文化雨露、润物无声，是学习、工作、生活的理想场所。广西工程职业学院目前设有建筑工程系、建筑与艺术设计系、机电工程系、经济管理系、公共基础课教',57510);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆财经职业学院',' ','重庆',0,6,0,0,0,' ','暂空',' ','http://www.cqcfe.com/','重庆财经职业学院是重庆市人民政府举办，国家教育部备案，重庆市商业委员会、重庆市教育委员会主管，财政全额拨款，以财经类专业为主，具有独立颁发普通高等教育专科学历资格的全日制公办普通高等职业院校。学院创建于1956年，始名江津专区财贸干部学校，开展干部教育与培训；1974年开办全日制中专教育；1983年更名为重庆第二财贸学校；2007年转制升格为普通高等职业院校，更名为重庆财经职业学院，举办高等职业教育。学院位于渝西区域性中心城市——永川城区、重庆职教城中心，东临永川植物园，南贴成渝高速公路和成渝高',666695);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵阳职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://www.gyvtc.edu.cn/default.html','贵阳职业技术学院是经贵州省人民政府批准，国家教育部备案、贵阳市人民政府举办的一所全日制、综合性、开放型的高等职业技术学院。学校性质：公办学校代码：14129办学层次：高等职业教育办学类型：普通高等教育学习形式：普通全日制学院有近60年的办学历史，实行省市共管以市为主的管理体制。学院占地共1000余亩，建筑面积26万平方米，主校区在贵阳市观山湖区。贵阳职业技术学院现有5个特色分院（轨道交通分院、城乡规划建设分院、装备制造分院、磷煤化工分院、食品药品生产与检测分院）、10个系（城市轨道交通系、机电技',629971);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南锡业职业技术学院',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.ytvtc.com/HJCMS/index.htm','云南锡业职业技术学院是２００７年经云南省人民政府批准、国家教育部备案的一所具有专科学历教育资格，以工科为主、培养有色金属行业高技能人才的综合性全日制国有公办普通高等职业技术学院。学院招生纳入国家统一计划，学生毕业后颁发国民教育大专文凭，国家承认学历。学院座落在美丽的锡都个旧，占地面积６２６．４亩，校舍建筑面积５万多平方米，运动场地６千多平方米，实验室和实训车间１７个，实验、实训设备仪器６００多台套，科研教学仪器设备６３０万元，固定资产５７６０万元，学院教学及生活设施完善。学院有一支忠诚和热爱党的',32932);
