insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽涉外经济职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.ahaec-edu.cn/','安徽涉外经济职业学院（原安徽农业大学涉外经济职业学院）是经安徽省政府批准并经国家教育部备案的一所高等学院，学院办学条件优越、师资力量雄厚、专业结构合理、管理队伍规范、“涉外”特色明显。学院坐落在科教名城省会合肥的大学城内，西临政务文化新区，南接滨湖新城。这里风景秀丽、环境优美、人文厚重，交通便捷，高校资源丰富，具有较强的区域优势。学院建筑面积近20万平方米，校园内花团锦簇、广场喷泉、园路蜿蜒、绿树成荫，是一个花园式学校。学院拥有多功能厅、学术报告厅、多媒体教室、微机室和专业实验室，另有各专业校外',106345);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽绿海商务职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.lhub.cn/','“中国民办教育百强学校”“2009安徽最具特色教育机构”安徽省民办高校中首家成立工会的学校安徽省唯一参加上海世博会服务的高职院校安徽绿海商务职业学院位于安徽省省会合肥经济技术开发区大学城内，是经安徽省人民政府批准、教育部备案的,具有高等学历教育资格的普通高等职业院校。学院创办于2007年，坚持贯彻：“以服务为宗旨、以就业为导向，走产学研结合发展道路”的职业教育方针，遵循“以质量求生存，以和谐求发展；坚持以人为本、坚持以德为先、坚持人为为人、坚持以教导学、坚持以管促理”的办学思路。学院建有实验实训',33759);
