insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州城建职业学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://zs.gzccc.edu.cn/','广州城建职业学院是经广东省政府批准、国家教育部备案的全日制普通高等职业院校,起源于1960年广州市建筑工程局主办的“广州业余建筑工程学院”，2007年在广州大学城建学院的基础上独立设置。2013年，学校以优异成绩通过广东省高职院校人才培养工作评估，获评“民办高职内涵建设优秀院校”、“全国民办高校创新创业教育示范学校”、“广东省大学生创新创业教育示范校”、“广东省依法治校示范校”、“广东省节能型示范高校”。学校位于有“广州市后花园”之称的从化区，办学条件优越，基础设施完备，校园环境优美，占地面积约',426439);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('蚌埠经济技术职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.bjy.ah.cn/','蚌埠经济技术职业学院是一所由海外留学归国人员创办，2007年经安徽省人民政府批准、国家教育部备案，参加国家计划内统一招生，具有学历教育资格的全日制普通高等学校。学院目前设财经系、旅游系、信息技术与计算机系、传媒艺术系和基础部等五个教学系部，开设会计、市场营销、金融与证券、计算机应用技术、计算机系统维护、酒店管理、旅游管理、旅游英语、营养与配餐、图形图像制作、广告设计与制作、装潢艺术设计共十二个专业。蚌埠经济技术职业学院的办学指导思想是，以经济建设和社会发展需求为目标，培养生产、建设、管理、服务第',37811);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆职业大学',' ','新疆',0,0,0,0,0,' ','暂空',' ','http://www.xjvu.edu.cn','新疆职业大学（国标代码14138）院校介绍新疆职业大学，1962年12月由新疆维吾尔自治区人民政府创办。历经四十余年，学校已发展成为一所隶属于自治区人民政府，由自治区教育厅主管的具有高等学历教育招生资格的综合性普通高等学校。学校在乌鲁木齐市有三个校区，迎宾校区位于北京北路1075号，巴州路校区位于巴州路14号，友好校区位于友好南路413号。学校设有商学院、机械电子技术学院、计算机信息技术学院、外国语学院、传媒与设计学院、继续教育学院、旅游与酒店管理系、中国语言系、烹饪与餐饮管理系、公共教学部（体',105887);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京社会管理职业学院',' ','北京',0,11,0,0,0,' ','暂空',' ','http://www.bcsa.edu.cn','北京社会管理职业学院是经北京市人民政府批准成立的全日制公办普通高等职业院校，其前身为1983年成立的民政部管理干部学院(国家民政部直属的唯一高等学府)，有着近三十年高等教育经验和深厚人文积淀，培养的学子遍布民政行业各条战线，被誉为“民政人才的摇篮”。学院地处北京东燕郊经济开发区，与通州新区相望，周边与五所高等院校毗邻，30分钟即可到达市中心，既能尽享首都生活之方便，又远离了城市喧嚣，是莘莘学子求学成才的理想园地。占地310亩，绿化面积达50%以上，是花园式校园。建筑面积10万余平米，各类教学实训',101146);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄人民医学高等专科学校',' ','河北',0,3,0,0,0,' ','暂空',' ','http://www.sjzrmyz.com/','石家庄人民医学高等专科学校是一所经国家教育部批准的全日制普通医学高等专业学校。多年来，学校认真贯彻执行国家教育方针，本着“依法治校、从严执教、培育英才”的教育指导思想，遵循“团结、勤奋、求实、创新”的校训，以全新的办学思路、鲜明的办学特色，高目标、高要求、高质量服务于祖国医疗卫生事业。2006年被教育部评为国家级重点中专；2007年由美国全球奖学金联盟委托河北省教育厅联办的“国际护理教育基地”在学校揭牌，成为河北省首家国际护理教育基地；2008年被教育部、河北省人民政府正式批准筹建石家庄人民医学',237584);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州高博软件技术职业学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.gist-edu.cn/','苏州高博软件技术职业学院是经江苏省人民政府批准、教育部备案的全日制普通高等院校。2007年3月，由微软（中国）有限公司、苏州高新区人民政府、高博教育管理（苏州）有限公司三方合作创建，旨在为江苏省、长三角乃至全国培养现代服务业和战略性新兴产业所需的高素质、应用型、国际化人才。办学几年来，在高新区政府以及各级领导的大力支持与帮助下，学院招生规模不断扩大，办学实力不断增强，办学质量不断提升，办学特色逐步彰显，教学科研和社会服务等各项事业取得积极进展：一、学院发展现状：朝阳专业、名企合作、精英化培养五年',107787);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西枫林涉外经贸职业学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.fenglin.org/','江西枫林涉外经贸职业学院是江西省政府批准，国家教育部备案的普通高等专科学校，由江西省教育厅主管。学院创办于2003年，前身为江西枫林理工专修学院，由香港海达国际投资公司投资、加拿大多元文化协会资助，旨在培养新型的具有国际化视野、国际竞争力的职业技术人才。学院占地面积1343亩，地处江西九江云山经济开发区，位于旅游胜地庐山西海旁，距中国最大淡水湖、世界候鸟保护区鄱阳湖仅70公里，西望神雾山之麓，北依梯云诸岭为向，东接水库沐风临湖，南有修河在前，枕山襟水，山屏水障，藏经聚气，选址正符“尤跨胜地，占奥',30771);
