insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('周口科技职业学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zkkjxy.net/','周口科技职业学院简介周口科技职业学院是由周口市海燕职专晋升的一所高等职业院校，创办于1981年，是周口市第一家民办技术学校。从租用别人几间平房的服装班开始，发展到现在两个校区，常年在校生7000多人，由于学院采取长短结合的培养模式，年培养培训学员1万两千多人的办学规模。学校开设应用电子技术、数控设备应用与维修、服装设计与制作、汽车检测与维修、机电一体化技术、物流管理等社会急需专业。多年来，在上级党委、政府和教育主管部门的亲切关怀和正确领导下，学校全体师生勇于实践，锐意创新，逐步探索出了一条-\"注',72843);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西理工职业技术学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.gxlgxy.com/','广西理工职业技术学院是一所经自治区人民政府批准、国家教育部备案，具有独立颁发国家承认学历文凭资格的全日制普通高校，附属有广西理工职业技术学校(全国中职示范校、国家级重点中专)和广西建筑材料工业技工学校。★良好的办学条件校园占地面积1239亩，有南宁、崇左两个校区，现有在校生30782人，其中大专生8324人，中职生22458人。教职工1334人，专任教师1193人，具有教授、副教授职称的教师318人，双师型教师955人。校园建筑面积39.77万平方米，教学设备达12041万元，建有功能齐全，信息',69236);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('梧州职业学院',' ','广西',0,0,0,0,0,' ','暂空',' ','http://www.wzzyedu.com/','梧州，东邻粤港澳，南接玉林，西连南宁，北邻桂林，素有“百年商埠”和“世界人造宝石之都”之美誉，是国务院全力打造的西江亿吨黄金水道中心城市、“泛珠三角经济圈”和“泛北部湾经济圈”交汇节点区域性枢纽城市。座落于这个快速发展城市中的梧州职业学院，和梧州的经济社会发展同步，充满着朝气和希望。梧州职业学院是一所公办全日制普通高等院校，是广西高校安全文明校园、广西卫生系统优秀学校。学院位于广西梧州市区，毗邻玫瑰湖湿地生态公园，占地约600亩，规划用地1200亩，建设面积近50万平方米。学院融山水园林为一体，',49474);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南科技职业学院',' ','海南',0,1,0,0,0,' ','暂空',' ','http://www.hnkjedu.cn/','海南科技职业学院校址是原中央美院的海口校区，2007年由南昌航天科技集团支助、创办。本学院由海南省人民政府批准，国家教育部备案成立的一所普通高等职业院校。学院距离新省委大院2.5公里；学院占地面积420亩，另有水域面积160亩，计划2011年新增用地500亩。计划2009年9月在校生6000名，2010年在校生11000名。我院实行专家治学。现有博士生导师3名，硕士生导师2名，享受国务院津贴专家3名，博士4名，11个分院院长均为高校有教学与管理经验的教授或博士，院长杨秀英副教授有十年高校办学经验',104707);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆科创职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqie.cn/','重庆科创职业学院是具有独立颁发国家学历文凭资格的全日制普通高校。学院建校于1993年，前身为重庆信息工程专修学院，是由香港科创集团捐资兴办，以培养电子、机械、信息技术及现代管理人才为主的高等院校，现有在籍学生25000余人。学院主校区紧依省级旅游风景区——《十面埋伏》国内拍摄地永川茶山竹海,占地1234亩，建筑面积近40万m2。学院建有先进的多媒体教室、学术报告厅、科技大厦、机电数控中心、电子和计算机实训大楼、工商实训大楼、图书馆、足球场、别墅式高档学生公寓等，教学和生活设施完善、功能齐全。学院',166259);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川城市职业学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.scuvc.com/','四川城市职业学院位于四川省省会—成都市，是一所经四川省人民政府批准、教育部备案的全日制综合性普通高等学校，于2008年4月在具有十多年本科办学历史的四川师范大学信息技术学院、四川师范大学外事学院办学基础上组建而成。学院地处成都市东三环，紧邻成都铁路东客站、地铁2号线洪河站，区位优势十分明显。学院自成立以来，恪守“修德砺能，务实拓新”的校训，弘扬“团结奋进，追求卓越”的学院精神，遵循“社会需求引领城市发展，高职教育开启幸福人生”的办学理念，强化“教会学生做人、教会学生做事”的人才培养理念，坚持“以',281283);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川现代职业学院',' ','四川',0,6,0,0,0,' ','暂空',' ','http://www.scmvc.cn/','四川现代职业学院是四川省人民政府2008年批准，国家教育部备案、四川省教育厅主管的一所民办普通高等职业专科院校。学院位于四川省省会---成都市所辖的具有科研教育新城与工业新城之称的西南航空港经济技术开发区。毗邻四川大学、西南民族大学、成都信息工程学院新校区，具有浓厚的大学氛围，校内环境优美、湖光树影、绿草茵茵与现代化建筑交相辉映。校园占地320亩，现有全日制普通在校学生5000余人，自学考试应用型本、专科专业在册学生2000余人。现拥有一大批把握前沿知识、掌握行业技术的高学历、高层次的师资队伍，',97354);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西经贸职业学院',' ','山西',0,6,0,0,0,' ','暂空',' ','www.sxieb.com','山西经贸职业学院（山西经济管理干部学院）始建于1984年，是一所经山西省人民政府批准设立、国家教育部备案的集教学、科研、培训、人才评荐、咨询、服务为一体的高等院校，学院由山西省人民政府领导，山西省经济委员会主管。学院占地总面积468亩，分南北两个校区，北校区位于太原市晋祠路与南内环西街的交汇处，毗邻滨河公园；南校区地处山西省著名的晋祠风景旅游区，两校区相距10公里，交通便利，环境幽雅。学院根据山西建设新型能源和工业基地对人才的需求，现设有经贸类、管理类、工业技术类、计算机科学类、人文艺术类和旅游',115455);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('佳木斯职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.jmsjs.com/','佳木斯职业学院是经黑龙江省政府批准、教育部备案的一所全日制公办普通高职院校。学院坐落在生态魅力城市佳木斯市，学院占地面积46万平方米，建筑面积6.5万平方米。教职工260人，其中专任教师195人，具有副高级以上专业技术职称的教师94人。学院目前开设报关与国际货运、数控技术、汽车检测与维修技术、焊接技术及自动化等20个专业，在校生3000余人。学院拥有数控机床、机械加工、焊接技术、模具制造、汽车维修、网络信息等各类现代化大型实习实训基地。有数控机床模拟、机械加工演示、汽车模拟驾驶、汽车发动机、汽车',54317);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海体育职业学院',' ','上海',0,7,0,0,0,' ','暂空',' ','http://www.ssi.edu.cn/mainAction.do','历史沿革上海体育职业学院是培养优秀运动员勇攀体育高峰的高等学府，创建于50年代初期，最初命名为华东体训班，1955年易名为上海体育学院竞技指导科，1960年扩编为上海体院运动系，文化大革命期间称作上海体育工作大队，1981年改名为上海体育学院分院，1984年更名为上海体育职业学院。2002年10月学院合并组建新的上海体育学院。基本情况上海体育职业学院是一所培养优秀运动员勇攀竞技体育高峰的高等学府，创建于50年代初期华东体育训练班，几易其名后于1984年被市政府批准成立上海体育运动技术学院，200',33899);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京旅游职业学院',' ','江苏',0,6,0,0,0,' ','暂空',' ','http://www.jltu.net/','南京旅游职业学院伴随着我国旅游业的起步，于1978年在全国最早设立，被誉为“中国旅游人才的摇篮”与“中国酒店业的黄埔军校”。学院坚持精致化、专业化、人文化、国际化的办学方向，积极学习借鉴国外旅游高等职业教育先进的办学理念，注重内涵建设，注重情商培养，注重作风养成，努力为我国的旅游业培养广受认可的合格人才。学院毕业生就业率始终保持在98%以上，在全省各高校中名列前茅，毕业生遍布国内五星级酒店、百强旅行社、AAAAA级景区、航空公司、机场、高铁、银行、电信、房地产等高端服务行业，就业实现了“四高”，',105002);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南建筑职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.hnjs.com.cn/','河南建筑职业技术学院创办于1958年，前身为河南省建筑职工大学。1982年，经河南省人民政府批准、原国家教委备案，成为独立设置的全日制公办成人高等专科学校。1998年与河南省建筑工程学校合署办学。2002年，经省教育厅批准举办普通高等职业教育。2008年3月，经河南省人民政府批准并报教育部备案，改制为河南建筑职业技术学院，是我省独立设置的普通高等职业技术学院中唯一的建筑类院校，现隶属于河南省建设厅。建校以来，学院始终坚持社会主义办学方向，全面贯彻党的教育方针，以服务河南省经济社会发展为己任，立足',226677);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南国防工业职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnkgzy.com/','湖南科技工业职业技术学院坐落在长株潭两型社会试验区，是经湖南省人民政府批准、国家教育部备案的全日制普通高等职业技术学院。学院由中国兵器行业与大型军工企业——江南机器集团主管主办，是中国国防科技工业培养军工特有专业高级技能人才一所主要的高职学院。2008年4月，学院被中国人力资源与社会保障部（人社部函[2008]65号）确立为全国首批“国家高技能人才培养示范基地”。2008年6月，学院被中国兵器集团确立为中国兵器系统高技能人才培养分中心。办学40多年来，学院坚持“面向地方、面向农村、面向行业”的办',60366);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆建筑工程职业学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cctc.cq.cn/','重庆建筑工程职业学院隶属于重庆市城乡建设委员会，是重庆市唯一一所全日制公办建设类高职院校。学院1956年建校，办学历史悠久，秉承厚德、笃学、求实、创新的校训，坚持以市场为导向，以行业为依托，以特色为抓手，以能力为本位，形成鲜明的行业特色和优势，是一所集普通高等职业教育、成人高等教育、建筑职业技能培训、建筑工程质量检测为一体的高职院校。',760239);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄科技工程职业学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.sjzkg.edu.cn/','石家庄科技工程职业学院隶属于石家庄市政府，创建于1924年，始称“直隶第八师范学校”。1999年开始培养专科生，2001年经省教育厅批准，改名为“石家庄师范专科学校正定分校”；2004年经省教育厅批准，更名为“石家庄学院正定分院”；2007年经省政府批准，教育部备案，改建为“石家庄科技工程职业学院”，实施专科层次的高等职业技术教育。学院坐落于国家级历史文化名城—正定，距离省会石家庄仅10余公里，交通便利，京广铁路、107国道穿境而过，石太高速、石黄高速与京石高速、京昆高速在这里汇合，石家庄正定国',67239);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('朔州职业技术学院',' ','山西',0,0,0,0,0,' ','暂空',' ','http://www.szvtc.com/','朔州职业技术学院是经山西省人民政府批准，教育部备案的全日制普通高等院校。学院位于朔州市朔城区西门外恢河河畔。占地面积550亩，现有建筑面积8万多平方米。有高大、气派、稳重，具有明显地域标识的九层教学大楼和楼前广场，宽阔开放功能齐全的体育运动场，引发深邃思考的科研实验中学，正在建设中的学生餐厅和文化活动中心、图书馆、校内公园和文化长廊……，是一块萌发梦幻、蕴动激情、充满无限生机的热土。学院认真贯彻党的教育方针，全面落实科学发展观，确立“以服务为宗旨，以就业为导向，以奉献求支持,以创新求发展”的办学',43063);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('包头铁道职业技术学院',' ','内蒙古',0,1,0,0,0,' ','暂空',' ','http://www.bttzy.com/web/guest/home','包头铁道职业技术学院是2008年元月，经自治区人民政府批准，国家教育部备案的公办全日制高等职业技术院校。学院以高等职业教育为主，同时兼有中等职业教育职能。学院现有各类在校学生7000余名，教职工420人，其中75%以上的教师具有中、高级职称，双师型教师占专业教师总数的56%，目前有30余名教师取得和正在进修硕士、博士学位。学院建筑面积11万平方米，拥有各类实验室43个，室内、外实习演练场所12处，教学仪器6700余台（件）。建有音像、语音、现代化、多媒体教室和各类专业教室。校园内安装了双向可控闭',212205);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁铁道职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.lntzy.com/','校史溯源辽宁铁道职业技术学院前身锦州铁路运输学校始建于1952年，当时名为锦州铁路技术学校，属中专性质，招收初中毕业生，1953年改名为锦州铁路学校，设行车及货运工作、商务管理两个专业，隶属于铁道部。1955年改名为锦州铁路运输学校，以培养车务和商务技术管理人员为主。1957年将车务和商务合并为一个铁道经营专业。1958年10月学校升格为锦州铁道学院，设大专部和中专部。大专部开设有铁道运输、机械、电信、建筑、体育等专业。中专部仍开设铁道经营专业。1961年大专部停止招生，恢复中专建制。1969年',359946);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长白山职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','oldname": "长白山职业技术学院','长白山职业技术学院是经吉林省政府批准，国家教育部备案的一所具有高等教育招生资格的公立高职院校。学院座落在白山市区中心，占地338亩，建筑面积12万平方米，教职工513人，现有在校生4,000人，2012年在校生将增至6,000人。学院是以高等职业教育为主，兼顾中等职业教育，同时开展社会培训和职业技能认证教育的多层次、多门类教学型高职院校，设有药学、工业工程、财经、教育、旅游和康复保健六个系及一个培训中心，开设中药制药技术、机电设备维修与管理、机电一体化技术、应用韩语、国际商务、会计、护理、旅游管',41445);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('徽商职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.huishangedu.cn/','学院概况公办学校，历史久，师资强学院是经安徽省人民政府批准、教育部备案的公办全日制普通高等职业院校，隶属于安徽省国有资产监督管理委员会。学院前身——安徽省物资学校，创办于1965年，已有半个世纪的办学历史。于2015年10月，迎来建校50周年华诞。学院面向全省招生，现有在校生6500余人。学院有多名省级教学名师、省级专业带头人、省级教坛新秀、省级教学团队、省级优秀教育管理者等，具有副高以上职称的教师占教师总人数的20%，具有硕士学位的教师占53.4%，“双师素质”教师比例达60%以上。学院是全国',115414);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('马鞍山职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.mastc.edu.cn/','作为马鞍山市属唯一公办高等职业院校，学院前身为创办于1984年的马鞍山联合大学，2000年更名安徽工业大学职业技术学院，2008年经国家教育部和安徽省人民政府批准更名为马鞍山职业技术学院。地理区位优势学院毗邻南京市45公里，位于拥抱长江、山水相间、生态宜居的全国文明城市安徽省马鞍山市。该市拥有1个国家级经济技术开发区、1个国家级新材料基地、1个省级高新技术产业开发区、6个省级经济开发区、1个省级服务外包产业示范园区、1个省级台湾工业园，主要经济指标总量和人均值一直位居全省前列，是安徽省挺进全国最',166208);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东传媒职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdcmc.net','山东传媒职业学院是经山东省人民政府批准成立、国家教育部备案的全日制普通高等专科学校。隶属于山东省新闻出版广电局，与省教育厅共管。是省内唯一一所广播影视及大众传媒类的普通高校，国有公办。学院地处山东省济南市经十东路，占地面积956亩。教学及生活设施齐全，拥有能够进行现场直播的演播中心和省内唯一的有线电视专业实验室；有先进的校园计算机网、有线电视网和以国家有线电视网为依托的交互式远程教室等公共服务设施，设有省级普通话水平测试站、广播电视系统特有工种职业技能鉴定站，为学生学习和实训提供了有力保障。学院',246334);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('临沂职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.lyzyxy.com/','临沂职业学院是经山东省人民政府批准、国家教育部备案，纳入国家统一招生计划的全日制高等专科院校，是一所专门培养高等职业技能人才的院校。学院环境优雅，景色宜人，文化氛围浓郁。学院教育理念先进，师资力量雄厚，拥有一支以国家级省级学科带头人、教学能手为骨干的教学科研队伍，有教职工531人，教授、副教授116人，有大批“双师型”教师，分别具有注册建筑师、注册会计师、高级训练师、律师等资格。学院占地面积411亩，校舍建筑面积12万多平方米，图书馆藏书20余万册，电子图书30多万册，仪器设备总值5200多万元',90501);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('枣庄职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdzzvc.cn/','枣庄职业学院（枣庄技术学院）是经国家教育部和山东省人民政府批准设立的全日制普通专科（高职）学院。学院坐落于枣庄市新城祁连山路中段，环境优美，交通便利。规划面积850亩，一期建成521亩，全院510名在职教职工中，具有硕士学位及在读硕士学位人数达102人，具有高级职称的161人，“双师型”教师占到专业教师的60%以上。现有各类在校学生7600余人。枣庄职业学院是在枣庄技术学院的基础上组建而成的。枣庄技术学院前身是枣庄市高级技工学校，2002年升格为枣庄技术学院，2003年5月在全市职业教育资源整合',108329);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('毕节职业技术学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.gzbjzy.cn/','学院简介:毕节职业技术学院是2008年2月经贵州省人民政府批准(黔府函【2008】31号文件)、国家教育部备案成立的全日制公办普通高等职业技术学院（学院代码：14198）。以实施高等职业教育为主，兼举中等职业教育和各类技能培训。学院坐落在毕节市环东大道风景旖丽的倒天河畔，与毕节学院比邻交辉，现占地163亩，规划占地400余亩，有“电子信息工程”、“工矿建筑”、“财经”、“农业工程”四个系，开设有“电子信息工程技术”、“计算机网络技术”、“电子商务”、会计电算化”、“煤矿开采技术”、“建筑工程技术',64967);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古大学创业学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.imuchuangye.cn/','一、基本情况内蒙古大学创业学院是内蒙古大学与社会投资方合作，于2005年开始筹建、2008年经教育部正式批准设立并在当年开始招生的一所本科层次的独立学院。学院位于内蒙古首府呼和浩特市，毗邻南湖湿地公园和内蒙古大学南校区，环境优美、交通便利，地理位置优越。学院面向全国招生，新生报到率每年维持在96%以上，目前有全日制本科生6700余名。二、办学理念学院秉承内蒙古大学“求真务实”的校训，弘扬“崇尚真知、追求卓越”的内大精神，贯彻“以质量求生存，以创新求发展，以贡献求支持，以共赢求合作”的发展理念，致',153708);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国矿业大学银川学院',' ','宁夏',0,0,0,0,0,' ','暂空',' ','http://www.cumtyc.com.cn','中国矿业大学银川学院是经教育部批准，由中国矿业大学举办的普通本科独立学院。中国矿业大学是教育部直属的全国重点大学，是国家“211工程”、“985工程”、“优势学科创新平台项目”重点建设的高校之一。有50个博士点、110个硕士点、12个博士后科研流动站。中国矿业大学在百年的办学历史中，积淀了丰富的人才培养和高校管理经验，形成了一批高水准的教师和管理队伍，现有专业教师1600人。其中：中国工程院院士6人，全国高校教学名师2人，教授270人，副教授385人，博士生导师197人，硕士生导师662人。名师',406785);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京第二外国语学院中瑞酒店管理学院',' ','北京',0,5,0,0,0,' ','暂空',' ','http://www.bhi.edu.cn','北京第二外国语学院中瑞酒店管理学院是2008年经教育部批准，实施本科层次学历教育的全日制普通高等学校。作为中国唯一获得瑞士洛桑酒店管理学院学术认证的大学，学院紧密围绕旅游酒店行业对人才的需求办学，以培养应用型高级专门人才为目标，以酒店管理专业为特色，不断探索应用型人才培养模式。秉承“国际化、应用型、学习洛桑”和“育人教书”的办学理念，践行“学以致用”的校训，学院紧密联系业界，服务业界，着力将学生们培养成为具有良好职业道德、职业素养、职业技能，在酒店留得住、受欢迎、能发展的高级管理人才。学院全面引',813876);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京交通大学海滨学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.bjtuhbxy.cn/','北京交通大学海滨学院由北京交通大学与华城（黄骅）科教有限公司共同举办、经国家教育部教发函【2008】147号文批准正式设立的全日制本科层次独立学院。列入国家统一招生计划。北京交通大学直属教育部，是全国首批博士、硕士学位授予单位和首批进入国家“211工程”建设的高校之一。全校有专任教师1636人，其中有中国科学院院士3名，中国工程院院士6名；教授277人，副教授588人，博士生导师215人；教师中具有博士学位的占52.1％，有硕士以上学位的占85.9％。北京交通大学海滨学院位于环渤海湾经济圈内的河',1598021);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河海大学文天学院',' ','安徽',1,1,0,0,0,' ','暂空',' ','http://www.hhuwtian.edu.cn/','河海大学文天学院是经教育部批准设立，由河海大学与江苏大业投资有限公司、无锡市大业房屋建设开发有限公司合作举办的全日制普通本科独立学院，也是教育部直属全国重点高校在安徽省举办的唯一一所独立学院。河海大学是教育部直属，以水利为特色，工科为主、理工结合，文、经、管、法等多学科协调发展的全国重点大学，是国务院首批授权可以授予学士、硕士、博士学位和自行审定教授、博士生导师的高校，是国家“211工程”重点建设以及全国56所设有研究生院的高校之一，以优秀成绩通过了教育部本科教学评估。其师资、学科、管理、品牌等',349833);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古师范大学鸿德学院',' ','内蒙古',0,4,0,0,0,' ','暂空',' ','http://www.honder.com/','内蒙古自治区仅有的两所独立学院之一内蒙古师范大学鸿德学院是由国家教育部正式批准（教发函[2008]149号）设立的本科层次的独立学院。2006年3月，经教育部批准筹建，2008年5月7日，经教育部批准正式成立。学生修满学分，颁发教育部统一印制的国家承认学历的内蒙古师范大学鸿德学院本科毕业证书；符合学士学位授予条件的，颁发内蒙古师范大学鸿德学院学位证书。连续四年本科毕业生就业率居自治区首位建校以来，学院始终坚持“一手抓发展，一手抓内涵建设”、“按本科大学标准办本科”的指导思想，狠抓学院基本建设与日',347860);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('同济大学浙江学院',' ','浙江',0,0,0,0,0,' ','暂空',' ','http://www.tjzj.edu.cn/','同济大学浙江学院于2008年5月经国家教育部正式批准成立，由同济大学、宏达控股集团有限公司、嘉兴市教育发展投资有限公司合作举办，是具有独立法人资格举办全日制本科学历教育的独立学院。学院坐落于交通便利、经济发达、人文荟萃、环境优美的浙江省嘉兴市南湖之畔，占地830余亩，现有校舍建筑面积12万平方米，包括教学楼群、实验室、图书馆、体育运动场、学生食堂、学生宿舍等现代化的教学和生活设施，为广大师生提供了完善的学习与生活保障。至2010年，校舍总建筑面积将达到38万平方米，各类设施进一步完善，为学院的不',780111);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海财经大学浙江学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','oldname": "上海财经大学浙江学院','上海财经大学浙江学院于2008年5月经教育部批准成立，由上海财经大学和浙中教育集团合作举办，是一所按新机制和新模式运作的具有独立法人资格、举办全日制本科学历教育的独立学院。学院位于金华市环城南路99号，规划占地面积约67公顷，已征用面积约56公顷，规划建筑面积28万平方米。拥有现代化的教学和生活设施，为广大师生提供完善的学习与生活保障。学院坚持“质量兴院、特色强院”的办学理念，采用先进的教育方法、教育手段，配置优良的教育资源，拥有一支结构合理、稳定的高素质教师队伍，开设有会计学、财务管理、金融学',597380);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北劳动关系职业学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.hbgy.edu.cn/','河北劳动关系职业学院简介河北劳动关系职业学院是经省政府批准、国家教育部备案设立的全日制公办普通高等职业专科院校，隶属于河北省总工会。学院现有普通高等职业教育、成人高等教育、中专、工会干部培训、短期技能培训等多个教育层次，设置管理学、法学、工学三个学科类别，30多个专业，在校生3000余人。在长期的教育实践中，积累了丰富的教学管理经验，创立了独树一帜的“一专二能三结合”人才培养模式，即所学主体专业要“专”，掌握外语和计算机要“能”，专业、能力以及职业技能要“结合”。这一创新的教育模式改变了我国传统',48399);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连航运职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','oldname": "大连航运职业技术学院','大连航运职业技术学院(代码14209)经辽宁省人民政府批准成立，并经教育部获准备案，是一所以培养高素质、技术型、应用型航运人才为主的民办全日制普通高等学校。办学层次为专科，学制三年，面向全国高考统招，招收高中毕业生。学院地处大连旅顺经济开发区大学城，紧邻老铁山国家级自然保护区、世界和平公园、旅顺新港、烟（台）大（连）火车轮渡码头，依山傍海，风景秀丽。校园占地面积27万平方米，校舍面积12万平方米。学院设有航海、轮机、电气以及金工等实验室，面向航运业、港口业，设置航海技术、轮机工程技术、电气自动化',138076);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽现代信息工程职业学院',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://www.ahmodern.cn/','安徽现代信息工程职业学院是经安徽省人民政府批准(皖政秘[2008]21号《安徽省人民政府关于同意设置安徽现代信息工程职业学院的批复》)，国家教育部备案成立（国家教育部教发函[2008]165号《教育部关于成立公布备案的高等职业学校名单的通知》）的一所全日制民办普通高等职业院校。学院地处国家历史文化名城——寿县新城区，与国家重要能源城——淮南市毗邻。合阜路、寿六路、寿霍路、寿淮路在此交汇；距合淮阜高速公路入口处仅5公里，淮南火车站20公里，正在建设中的合肥新桥国际机场70公里；学院与省城合肥及六安',32149);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西经济职业学院',' ','广西',0,6,0,0,0,' ','暂空',' ','http://www.gxevc.com','广西经济职业学院是经广西壮族自治区人民政府批准设立，2008年6月经国家教育部备案，由广西凯宁置业投资有限公司投资创办的一所全日制普通高等职业学校。学院位于上升为国家战略的北部湾经济区中心的南宁市，坐落于“南宁—东盟经济开发区”（武鸣华侨投资区），一期建设306亩，已建成4万多平方米校舍和各种配套设施，包括电教室、多功能语音室、多媒体教室、艾菲奖营销广告案例中心、篮排球和羽毛球场等教学实训设施；图书馆藏书数万册，校园互联网宽带光缆接入教室及宿舍，可满足学生的多种兴趣、爱好和学习需要。学院立足广西',73203);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南经贸外事职业学院',' ','云南',0,12,0,0,0,' ','暂空',' ','http://www.ynjw.net/','云南经贸外事职业学院是经云南省人民政府批准，国家教育部备案，纳入国家计划内统一招生，具有独立颁发国家承认学历文凭的全日制普通高等学校。学院座落在昆明主城区美丽的西山山麓、滇池之滨的海口，风景如画，气候宜人，环境优美。学院占地450多亩，规划用地650亩，建筑面积20余万平米，拥有六个在省内独有的产、工、学结合的实训基地。学院设有经济管理系、工程系、计科系、机电系、人文系、护理系、设计艺术学院，开设有经济管理类、工程类、机电类、电子信息类、文化教育类、医药类、艺术设计类七大类别39个专业。学院拥有',85616);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄科技职业学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.sjzkjxy.net/','石家庄科技职业学院，是经河北省人民政府批准、国家教育部备案的全日制普通高等院校。按国家计划面向全国统一招生（国标代码：14213），面向全国录取普通高考学生入学。培养“医药化工”“机械电子”“信息网络”“经济管理”“应用英语”等高级技术人才，独立为毕业学生颁发大学普通专科（本科）学历证书。同时，学院还设有国际文化交流与合作、国家学历文凭考试教育和各种形式的教育培训。学院坚持“服务社会，一切为了学生”的办学宗旨，坚持“开放式办学，封闭式管理，一体化服务”的特色；贯彻“教学与管理并重，理论与技能相长',54213);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京体育职业学院',' ','北京',0,7,0,0,0,' ','暂空',' ','http://www.bjtzhy.org/','北京体育职业学院是北京市人民政府批准建立、由北京市体育局于2008年6月创建的一所高等体育职业院校。属于全额拨款的事业单位，该院是在原北京市职工体育运动技术学院和北京市第二、第五体育运动学校基础上整合而成的。现有专业：竞技体育专业、运动训练专业（竞技体能训练方向）、社会体育专业（康体健身方向）、体育保健专业、体育服务与管理专业。在校生源：北京市市级优秀运动队的本市户籍学生、外省市已进入北京市集体户口的高级中等学校毕业的在训运动员，北京市普通高中毕业生。其主要职能是：负责学院与兄弟院校联办的各类学',35286);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('乌兰察布医学高等专科学校',' ','内蒙古',0,3,0,0,0,' ','暂空',' ','http://www.wlcbyz.com/','我校是内蒙古自治区唯一的一所医学高等专科学校。位于自治区中部的乌兰察布市集宁新区，交通便利，环境怡人。从2009年9月起，按照自治区教育厅核定的国家专科招生计划独立招收各类专科学生。学校目前已发展成为以全日制普通专科教育为主体，五年制高职教育和成人高等教育为补充，兼顾中职教育和职业培训的多学科、多专业、多层次的医学高等专科学校。学校由校本部和附属医院两部分组成。校本部占地680亩，一期建筑面积8.19万平方米。各类实验室75个，教学科研仪器设备总值2442万元。馆藏图书26万余册。包括多媒体教室',115389);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西幼儿师范高等专科学校',' ','广西',0,4,0,0,0,' ','暂空',' ','http://www.gxyesf.com/','广西幼儿师范高等专科学校的前身，是1938年我国著名幼儿教育家张雪门创办的北平香山慈幼院桂林分院广西幼稚师范学校。2009年3月，学校经教育部批准升格为广西幼儿师范高等专科学校，现为广西唯一一所独立建制的幼儿师范高等学校，直属广西壮族自治区教育厅管理。广西壮族自治区幼儿教师培训中心、广西特殊教育师资培训中心挂靠我校。学校为国家教育体制改革试点技术支撑单位、中央财政支持重点专业建设单位、教育部首批教育信息化试点单位、全国学校艺术教育工作先进单位、全国校园文化先进单位、广西高校师范教育能力提升计划基',157056);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川幼儿师范高等专科学校',' ','四川',0,4,0,0,0,' ','暂空',' ','http://www.scyesz.edu.cn','四川幼儿师范高等专科学校是2009年3月经教育部批准，在原四川省江油师范学校基础上建立起来的一所公办全日制普通高等专科学校，是四川唯一一所以培养幼儿、小学教育师资为主的普通高等学校。学校现设有学前教育、音乐教育、美术教育、语文教育、英语教育5个专业。学校以“多元融合、尚知笃行”为办学理念，在管理上坚持“依法办学、民主管理”；在用人上坚持“人尽其才、才尽其用、任人唯贤、用人之长”；在教育上坚持“发现学生的优势，尊重学生的优势，发挥学生的优势，发展学生的优势”；在教学上坚持“以质量为中心”，强化教学',231420);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安医学高等专科学校',' ','陕西',0,3,0,0,0,' ','暂空',' ','http://www.xagdyz.com/','西安医学高等专科学校是国家教育部批准设立，陕西省教育厅主管的一所全日制普通高等专科学校。西安医学高等专科学校位于风景秀丽的西安市秦渡镇。交通便利，办学环境优雅。学校占地面积512亩，建筑面积13.5万平方米。学校建有医学各专业实验室65个，实验设备先进，物品齐全；校图书馆藏书总数11.3万册，同时还有电子阅览室，电子图书达20余万册。学校建有一个标准游泳池、旱冰场、篮球场、排球场等各种体育运动设施。学校实行校、系（部）两级管理，设有护理系、药学系、医学检验系、医学影像系、口腔医学系五个系及一个基',583826);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州师范学院',' ','贵州',1,4,0,0,0,' ','暂空',' ','http://www.gznc.edu.cn/','贵州师范学院是教育部批准的贵州省属公办普通本科院校，地处美丽的避暑之都贵阳市，学校距市中心仅十五分钟车程。校园占地面积1042亩(含代管林地350亩)，紧邻环城林带，嘉木簇拥，空气清新;依山就势，错落有致。建筑面积近30余万平方米，校园绿化覆盖率达42.3%。学校现有教职工1059人，其中有专任教师728人，含教授54人、副教授256人，占专任教师总数的42.58%。专任教师中有博士、硕士学位的共571人，硕士博士学历比例达81.18%。学校享受政府特殊津贴专家9人、省管专家1人、省级教学名师6',736560);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北东方学院',' ','河北',0,0,0,0,0,' ','暂空',' ','oldname": "河北东方学院','一、学院简介廊坊东方职业技术学院是经河北省人民政府批准，国家教育部备案，具有独立颁发国家承认学历资格的全日制普通高校。目前，学院设置八系二部：文物系、医学系、民航服务与管理系、艺术表演系、艺术设计系、信息工程系、经济管理系、旅游管理系和基础部、中专部及城铁学院，涵盖文物、医学、现代服务、艺术、IT、建筑等近30个专业，面向全国招生。学院位于“连京津之廊，环渤海之坊”的廊坊东方大学城内，地处京津走廊核心区，距北京天安门40分钟车程。校园一期占地300亩，拥有设施先进的苹果机房、缝纫机房、电子电工实',142925);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('现代管理大学',' ','北京',1,0,0,0,0,' ','暂空',' ','http://www.mau.edu.cn/','现代管理大学创办于1986年，系国家事业法人单位，是经北京市教委批准成立的一所民办高等教育机构（办学许可证：111010020000340），学校根据社会经济发展的需求，不断调整专业设置，逐渐形成了以经济、管理类专业为龙头，经、管、文、法、工等5大类学科协调发展。学校拥有占地面积400余亩的独立校区，建筑面积10万多平方米，建有工科、文科实训室，计算机网络中心等20多个特色教室，140个普通教室，有6栋学生公寓，3个学生活动中心广场，400米塑胶标准运动场等，教育教学设施一应俱全。学校设有工商管',123220);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('运城职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.ycptu.edu.cn','运城职业技术学院是经山西省政府批准教育部备案的一所国家统招高校（学院代码：14226）。附设宏源工业技术学校为学院中专部（招生代码：31431）。学院规划宏伟，聘请清华、同济等著名大学建筑大师规划设计，采用国际最新技术，高起点、高规格进行建设，现代化科学环保节能的教学大楼、学生公寓楼、综合生活服务大楼、超大型体育场等一应俱全，占地3000亩、投资10亿元、发展规模2-—3万人，堪称全省一流。是省政府2002年批准立项的运城市“十一·五”重点建设项目，是重点建设的高职教育基地和高等职业教育试验示范',92194);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连装备制造职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.dlemc.net/','大连装备制造职业技术学院是经辽宁省人民政府批准、国家教育部备案、以工科为主的普通高等院校，是全国装备制造业高等人才培养基地，是辽宁省职业教育创新型实训基地。学院地处素有中国“大樱桃之乡”的大连金州新区，东临迅速崛起的大连先进装备制造业园区，西靠风光秀丽的大连金渤海岸现代服务业发展区，与魏和公园、樱韵广场近在咫尺，空气清新、交通便利，白杨参天、小桥流水、曲径通幽，花香四溢、果香扑鼻、鸟鸣鱼跃，如诗如画、美不胜收。学院以国家计划内高职教育为主，同时举办国家成人高等教育、国家高等教育自学考试和短期培训',133722);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连汽车职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.dlqcxy.net/','大连汽车职业技术学院是辽宁省人民政府批准，国家教育部备案的省属全日制民办普通高等职业院校（大专学历），系国内首批成立的专门培养汽车后市场服务的各类高级专业人才的高等职业技术学院，招生计划纳入国家高校统招计划。学院坐落在美丽的海滨城市——大连，依托于被国家评为“改革开放30年最具影响力的优秀汽车服务集团”的大连天巳汽车服务集团，集团对学院的固定资产投入累计已达11亿5千多万元。学院占地面积26万平方米，教学行政用房6万多平方米，图书馆藏书10万册。拥有现代化的多媒体教室、理实一体化教室、汽车仿真教',107709);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽矿业职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.anhky.com/','安徽矿业职业技术学院坐落于安徽省淮北市市中心，北枕相山，南临濉水，是经安徽省人民政府同意设置、教育部批准建立的全日制普通高等职业院校。安徽矿业职业技术学院的前身是安徽淮北矿业集团公司党校、干校、职工教育中心，创建于1978年，现有机电工程系、能源工程系、电子与信息工程系、公共课教学部、培训部、成教处、实训中心等教学单位。安徽矿业职业技术学院拥有东西两个校区，占地312亩，校舍建筑面积18万平方米，拥有教学楼、学术报告厅、电教馆、餐饮洗浴大楼、标准化学生公寓和价值3000多万元的实习实训设备。学院',37028);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥信息技术职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.hfitu.cn/','合肥信息技术职业学院是经安徽省人民政府批准、国家教育部备案，具有独立颁发学历文凭资格的全日制普通高等院校。学院面向全国招收国家计划内统招生，学院代码为14230。学院座落于合肥国家级经济技术开发区的大学城，毗邻安徽大学新区和合肥工业大学新区，投资2亿元人民币，占地面积304.96亩，规划总建筑面积15万平方米，现有校舍建筑面积6.7万平方米，其中教学和行政用房4.3万平方米，教学仪器设备总值947.3万元，图书馆藏书10.5万册，另有电子图书56万册，是一所教室、实验室、图书馆、学生公寓、食堂、',50080);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州轻工职业学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.qzqgxy.com/','泉州轻工职业学院位于著名侨乡、中国品牌之都——泉州晋江市，是经福建省人民政府批准成立、国家教育部备案、纳入全国高考统一招生、颁发国家承认学历的全日制普通高等学校。学院由五大知名企业集团（金龙集团、恒安集团、安踏集团、浔兴集团、艾派集团）共同出资创建，校园占地600亩，一期建筑面积10万平方米。校园环境优雅，拥有先进的教学设备和体育活动与生活设施，营造良好的育人环境。学院院长郭亨群教授是享受国务院政府特殊津贴的专家、华侨大学原副校长、博士生导师。学院实施人才强校战略，从北京大学、厦门大学等全国重点',70939);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('漯河食品职业学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.lsgx.com.cn/','漯河食品职业学院是河南省人民政府正式批准、国家教育部备案的一所全日制普通高等职业学院，是漯河市委、市政府为落实省委书记徐光春同志重要指示精神而设立的，主要为漯河中国食品名城和河南食品产业培养急需的高素质技能型人才。省委书记徐光春同志去年3月份来漯河调研全省食品产业发展情况时明确指出：漯河要用新观念、新思路、新举措全力打造食品加工基地、食品交易基地、食品消费基地、食品研发基地、食品教育基地，成为名副其实的中国食品名城。漯河市委、市政府从建设中国食品名城的高度，向省委、省政府提出依托漯河市食品工业学',59992);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州城市职业学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.brenda.edu.cn/','郑州布瑞达理工职业学院是经河南省人民政府批准，教育部备案成立的一所民办普通高等职业院校。实施全日制专科层次学历教育，学制为三年。学生通过国家高考统招录取，修完学业，成绩合格者可颁发教育部统一电子注册的专科毕业证书。学院以工科为主，文、理、管兼顾。致力于培养德智体美全面发展的国际型、复合型、适应国内外生产、建设、管理、服务第一线需要的现代技术应用人才，使他们具有系统而扎实的专业理论和相关知识基础，具有较熟练、较前沿的专业技能和通用技能，具有传承拓展能力和自主创新精神，能适应产业现代化、智能化和知识',77228);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三亚理工职业学院',' ','海南',0,1,0,0,0,' ','暂空',' ','http://www.ucsanya.com','三亚市位于北纬18°9｀，是海南建设国际旅游岛的核心城市，她是中国唯一地处热带，全球空气质量排名第二的旅游城市。三亚不仅具备现代国际度假五大要素——阳光、海水、沙滩、绿色植被、洁净空气，而且还拥有邮轮港、国际机场、免税店、河流、温泉、岩洞、田园、热带动植物、民族风情等各具特色的旅游资源，已成为世界关注与向往的旅游游目的地。三亚理工职业学院位于三亚市迎宾大道学院路，毗邻古人类文化遗址——国家文物保护单位落笔洞及热带风情万科森林公园，与创造了中国民办高等教育奇迹的三亚学院为邻。校园依山近海，空气清新',127952);
