insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆商务职业学院',' ','重庆',0,6,0,0,0,' ','暂空',' ','http://www.cqswxy.cn','重庆商务职业学院始建于1962年，是经重庆市人民政府批准，教育部备案的全日制普通高校，隶属于重庆市商业委员会，近50年的办学历史源于商务，专注于商务，积淀了深厚的商务文化底蕴，为重庆乃至西部的商贸、金融、餐饮、IT等行业培养了一大批实用技能人才。办学理念扬儒商文化，晓经商之道，铸通商本领，育崇商精英。地理位置学院坐落在闻名遐迩的具有革命历史传统的歌乐山上，紧邻重庆大学城、西永微电子工业园和西部现代物流园，交通便利，环境优美。校园温馨和谐、书香浸润，传统与现代交融，是教书育人理想场所。在青山葱翠怀',207390);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西轻工职业技术学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.sxqgzy.cn/','山西轻工职业技术学院始建于1958年，前身是山西省轻工业学校，是经省政府批准、教育部备案的专科层次的公办高等职业院校。学院设轻工化工工程系、机电信息工程系、工商管理系、艺术设计系等七个系、部，设有19个高职专业。其中“精细化学品生产技术”“材料工程技术”“食品营养与检测”等专业是山西省高职高专紧缺人才培养专业；“装饰艺术设计”为山西省高职高专教改试点专业。学院拥有一支高素质的教师队伍。有教育部高职高专轻化类专业教学指导委员会主任1人，山西省高职高专人才培养委员会副主任1人，山西省高职高专语文课程',67216);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('鄂尔多斯职业学院',' ','内蒙古',0,1,0,0,0,' ','暂空',' ','http://www.ordosvc.cn','鄂尔多斯职业学院(学校代码：14248。前身原伊盟师范学校、教育学院、广播电视大学)是2007年4月经自治区人民政府批准，2009年4月在教育部备案，由鄂尔多斯市人民政府兴办的一所公办全日制普通高等职业院校。校园建设学院坐落于资源富集、环境优美的国家4A级旅游城市——鄂尔多斯市康巴什新区,占地1015亩，校区规划建筑面积26万平方米，一期建筑面积14.2万平方米。学院校园环境优美，是鄂尔多斯市花园式学校，自治区普通高校学生工作和就业工作先进单位。师资队伍建设学院现有教职工245人，其中专任教师2',51173);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西新闻出版职业技术学院',' ','江西',0,0,0,0,0,' ','暂空',' ','http://www.jxcb.com/','江西新闻出版职业技术学院是一所经江西省人民政府批准设立的公办性质的普通高等专科学校，隶属于江西省新闻出版局（版权局）。学院位于江西省省会南昌，新校区座落在赣江环绕的蒋巷国家生态示范区，占地190余亩，建筑面积8.6万平方米，校内有优良的教学生活设施和高标准的实训基地及文体场馆，馆藏图书20万余册。学院前身为江西省新闻出版学校，1985年创建，2004年批准筹建学院，2008年5月批准学院正式设立。积20多年的办学历史，师资力量雄厚，其中硕士以上学历30多人。依托新闻出版行业，开设了适应印刷、采编',30253);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('驻马店职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.zmdvtc.cn/','驻马店职业技术学院位于驻马店市置地大道西段，占地316亩。校舍建筑面积11.79万平方米，教学楼、实验楼、图书馆、运动场等基础设施齐全。学院建由有实验室22个，实验分室47个；仪器设备总值1287万元，购置图书15.5万册，各类期刊432种。学校建有校园网和9个校外实习实训基地。学院现有教职工260人，专任教师143人，其中具有高级职称的教师57人，占专业教师总数的39.8%，讲师64人，占专任教师总数的44.7%，双师型教师10人。这支教师队伍不仅学历结构、职称结构科学合理，而且具有良好的教学',52889);
