insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州华夏职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gzhxtc.cn/','广州华夏职业学院是一所经广东省人民政府批准成立、教育部备案，面向全国招收国家任务生的全日制普通高等院校。学院坐落在有\"广州后花园\"之称的从化市明珠工业园内，学院占地面积41.2万平方米，总建筑面积17.5万平方米，固定资产6.8亿元。拥有汽车实训车间、ERP综合实训室、多媒体语音室等各类专业实习实训室47个，设有多媒体教室128间，教学用计算机1185台，图书馆藏纸质图书43.21万册，电子图书总数1000GB，每学年新增纸质图书10万册左右。实训建筑面积达25322平方米；有先进的网络设备、设',159213);
