insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古工业职业学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.nmgongye.cn','内蒙古工业职业学院是经内蒙古自治区人民政府批准，教育部备案，纳入国家统一招生计划，具有独立颁发国家承认学历证书资格的全日制普通高等职业院校。学院成立于1997年，经过十几年的发展，现已成为集高等学历教育、成人继续教育、中职学历教育、短期职业培训为一体的综合型教育机构。学院始终坚持社会主义办学方向，全面贯彻国家教育方针;始终坚持以服务为宗旨，以就业为导向，走工学结合、校企联合的改革发展道路，培养德、智、体全面发展的生产、建设、管理、服务等领域第一线的高技能应用型专门人才，积极为自治区经济建设和社会',29856);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('呼伦贝尔职业技术学院',' ','内蒙古',0,0,0,0,0,' ','暂空',' ','http://www.hlbrzy.com/','呼伦贝尔职业技术学院是经内蒙古自治区人民政府批准，教育部备案，涉及能源化工、机电、医药卫生、财经、旅游、艺术、电子信息各类于一体的综合性院校，也是呼伦贝尔市唯一一所市属公办高等职业院校。学院坐落在被誉为“中国最佳民族风情魅力城市”，“草原明珠”的内蒙古自治区呼伦贝尔市中心城(海拉尔)新区。学院占地面积62.2万平方米，建筑面积20.3万平方米，固定资产总值10亿元。拥有现代化的体育馆、体育场和图书馆等教育教学配套设施。学院现有全日制在校生5400人，教职工583人，其中专任教师380人，高级职称',35869);
