insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁冶金职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.lnyj.net','辽宁冶金职业技术学院是经辽宁省人民政府批准、国家教育部备案的省属全日制普通高等职业学校。作为一所以冶金、机械制造、自动化控制、电子信息、物流为主干专业的高等职业学校，六十多年的办学历史使学院具备了厚重的人文底蕴，培养了大批社会欢迎、企业急需的高素质技术技能人才，被广泛誉为培养企业技术技能型人才的“黄埔”。学院地处辽东半岛东南部山水秀丽的山城本溪市区内，环境优美、空气清新。本溪北接沈阳、抚顺，南连丹东，西距辽阳，东与吉林通化毗邻,陆路交通便利。学院占地面积21.36万平方米，建筑面积12.39万平',44929);
