insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆旅游职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqvit.com','重庆旅游职业学院是重庆市人民政府设立、教育部备案的一所公办高等旅游院校，被确定为中央财政专项经费支持建设单位、重庆市“旅游人才发展重点工程”建设单位、重庆市旅游职业教育现代学徒制人才培养模式试点单位、重庆市大学生创新创业训练计划建设高校。学院一直得到重庆市委、市政府的高度重视。建校以来，党和国家领导人张德江、贺国强等，以及国家发改委、国家民委、国家扶贫办等有关部门的领导、重庆市相关领导，都曾到校视察指导工作。学院地处渝东南地区和武陵山片区中心城市黔江，坐落在黔江新城科教园区。校园占地506亩，图',63902);
