insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('榆林职业技术学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.yulinvtc.com.cn','榆林职业技术学院是经陕西省人民政府同意，陕西省教育厅批准筹建的，拟由榆林市人民政府举办，旨在为陕北能源化工基地建设服务的高等职业技术院校榆林职业技术学院位于榆林经济开发区裕华路以南，东环路以西，北星路以东区域，距市区10km。项目总占地面积718.63亩，规划净用地面积640.68亩。南北长约680米，东西宽约780米，呈不规则的“口”字形。区位榆林市位于陕西省北部，地处陕甘蒙宁晋五省（区）交界接壤地带。榆林经济开发区位于榆林市区南部，是以能源化工项目建设为主体的经济开发区。榆林职业技术学院地处',51488);
