insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛远洋船员职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.coscoqmc.com.cn','青岛远洋船员职业学院基本情况青岛远洋船员职业学院隶属于中国远洋运输(集团)总公司，教育行政管理隶属山东省，是一所面向全国招生的高等职业院校。学院成立于1976年，坐落于青岛市市南区，前身为青岛海运学校。1980年，经交通部批准改为成人高校，更名为“青岛远洋船员进修学院”。1983年经交通部批准，更名为“青岛远洋船员学院”。2010年经教育部批准改建为普通高等职业院校，更名为“青岛远洋船员职业学院”。建院39年来，为中国远洋航运事业培养各类人才50000余人。学院占地面积54.4万余平方米，建筑面',87004);
