insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川长江职业学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.sccvc.com/','四川长江职业学院是四川省人民政府批准、教育部同意设置的全日制专科层次普通高等院校，面向全国招收参加全国统一高考的普通高中毕业生。毕业生颁发教育部统一印制、电子注册的普通高校毕业证书。优越的办学条件学院位于成都市东三环路十陵立交桥外侧300米，地处十陵历史文化风景区与龙泉驿国家级经济技术开发区，距成都市中心天府广场约8公里。东枕规划中的城市“绿肺”——青龙湖公园，南邻西部最大交通枢纽——成都东站，毗邻四川师范大学、成都理工大学等高校，区位优越、交通方便。既具有得天独厚的自然、人文环境，又具有先进活',87027);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南方科技大学',' ','广东',1,0,0,0,0,' ','暂空',' ','http://zs.sustc.edu.cn/','南方科技大学是由中国广东省领导和管理、深圳市举全市之力创建的一所公办创新型大学，目标是迅速建成国际化高水平研究型大学，建成中国重大科学技术研究与拔尖创新人才培养的重要基地。郭雨蓉为现任党委书记，陈十一为现任校长。学校位于深圳市南山区，校园占地面积194.38万平方米，规划总建筑面积为70多万平方米，分两期建设。目前正开展校园一期续建及二期项目的规划设计工作，即将启动建设，预计2018年初步建成。学校被确定为国家高等教育综合改革试验校。2012年4月，教育部同意建校，并赋予学校探索具有中国特色的现',346822);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京电影学院现代创意媒体学院',' ','山东',0,8,0,0,0,' ','暂空',' ','http://www.bfamcmc.edu.cn','北京电影学院现代创意媒体学院是北京电影学院唯一的独立学院，是国家教育部正式批准设立的一所本科层次全日制学历教育的高等院校。2011年正式开始面向全国招收艺术类考生。北京电影学院现代创意媒体学院是为满足不同社会群体不断增长的多层次、多样化的教育需求，在北京电影学院雄厚的办学实力基础上拓展新的学科专业和发展空间，着力培养国家急需的既熟悉影视艺术又懂得现代科技的数字影视制作、新媒体艺术、网络艺术和动漫游戏设计制作等影视复合型人才的现代新型特色学院。北京电影学院现代创意媒体学院依托北京电影学院独具特色的',100612);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄幼儿师范高等专科学校',' ','河北',0,4,0,0,0,' ','暂空',' ','http://www.sjzysgz.com/','石家庄幼儿师范高等专科学校是教育部批准设置的公办师范类全日制普通高等学校，是河北省乃至全国最具规模和实力的现代化的幼儿师范高等专科学校之一。石家庄幼儿师范高等专科学校的前身是河北石家庄幼儿师范学校，由原河北石家庄幼儿师范学校和原河北石家庄外国语师范学校合并组建而成。原河北石家庄幼儿师范学校创建于1984年5月，是河北省人民政府批准建立的全省第一所国办全日制中等幼儿师范学校；原河北石家庄外国语师范学校的前身是石家庄市师范学校，创建于1948年5月，是石家庄市小学师资的摇篮。2006年10月27日，',71320);
