insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州东华职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gzdhxy.com/','广州东华职业学院国际代码：14362。按国家招生管理规定录取并取得我院正式学籍的学生，在校期间完成教学计划规定的理论和实践教学环节，符合学院毕(结)业条件者，颁发国家承认学历的毕业证书，并报广东省教育厅电子注册。颁发学历毕业证书的学院名称为广州东华职业学院，毕业证书的种类：全日制普通高等学校毕(结)业证书。严格执行教育部规定的“学校负责，招办监督”的录取体制，切实遵守教育部、省招生办公室的有关政策和规定，以考生高考成绩为基本依据、以考生志愿为次序，以“公平、公正、公开”为原则，综合衡量、择优录取',54108);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东创新科技职业学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdcxxy.net/ ','广东创新科技职业学院创建于2011年2月，是经广东省人民政府批准、教育部备案、广东省教育厅主管的一所全日制民办普通高等职业院校，目前在校生规模达到7000多人。学院下设计算机与通信系、机电工程系、艺术设计系、建筑工程系、经济管理系、会计系、外语系等7个系，开设25个专业。学院坚持“高起点、高标准、严管理、创新路、铸特色”的办学方针，实施教授专家治学、名师技师执教，大力推进教育创新，深化教学改革，实施校企合作、工学结合，突出强化技能训练，提高学生的实践技能水平。今年学院首届毕业生以其适应性强、专业',88143);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆安全技术职业学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqvist.net','重庆安全技术职业学院是2011年3月经重庆市人民政府批准、教育部备案的一所全日制公办普通高等院校，占地175亩（在建用地900亩），校舍建筑面积70000平方米，教学仪器设备总值1800万元，有与专业相适应的实训实验室53间。学院有较为稳定的校外实习基地10个，适用图书5.2万册，教学楼、实验实训室、学生宿舍、学术报告厅、图书馆、篮球场、网球场、运动场、学生食堂等设施齐全，教学、生活和活动场所齐备。学校招生专业包括安全技术管理、建筑工程技术（安全技术方向）、应用化工技术（安全技术方向）、机电一体',30515);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆公共运输职业学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqgyzy.com/','重庆公共运输职业学院是经重庆市人民政府批准设立（渝府【2010】17号）、国家教育部备案的国有大型企业举办的全日制普通高等职业学院，重庆市教育委员会为学院主管部门。学院由重庆市属重点国有大型企业——重庆城市交通开发投资（集团）有限公司（简称重庆交通开投集团）出资举办。学院座落在重庆双福工业园区，现占地面积185426㎡，二期发展用地200000㎡，现有校舍建筑面积约10万㎡。学院下设有轨道交通系、交通运输管理系、车辆工程系、机电工程系、公共艺术系、基础教学部、计算机与网络中心、城市轨道交通实训中',144434);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆艺术工程职业学院',' ','重庆',0,8,0,0,0,' ','暂空',' ','http://www.cqysxy.com/','重庆艺术工程职业学院是经重庆市人民政府批准成立，教育部备案全日制普通高等职业院校(教育部代码：14367)，承担国家统招任务，面向全国招生；具有独立颁发国家承认的学历文凭资格。学校由重庆港鑫建筑装饰设计工程有限公司(装饰行业全国百强企业)创办，依托建筑及装饰行业优势，专注打造以“艺术设计”、“建筑工程”和“影视传媒”类为核心的特色专业群。主办单位和所属行业丰富的工程案例、广博的社会资源、持续的发展前景，为学院人才培养、学生就业等提供了良好的条件和市场。学校现有重庆南岸茶园和铜梁学府大道两个校区，',733217);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆轻工职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqivc.com','重庆轻工职业学院位于重庆市九龙坡区含谷。学院设食品工程系、管理系、建筑工程系、设计系、化学工程系、体育系、信息技术系、机电系。以食品类、包装印刷类、酒店、财务管理等专业为特色，带动学院现有19个专业健康发展。学校基于“办有文化根基的高职院校”的办学理念，遵循“着眼于学生整个职业生涯，培养学生可持续发展能力”的人才培养理念，坚持“学生的成长需求至上”的总价值观，为学生在校期间收获最大的成长价值做出贡献。培养学生既有脚踏实地的工作能力，又有长远发展的特殊眼光。大学期间，养成独立思考、自主学习的能力，',66112);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆电信职业学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqtcedu.com/','重庆电信职业学院是经重庆市人民政府批准、教育部备案、面向全国招生的全日制普通高等院校（教育部代码：14369）。学校现有两个校区，其中卫星湖校区地处重庆市永川区风景秀丽的卫星湖文化旅游区；龙水湖校区位于毗邻有“西湖”美誉之称的龙水湖和国家5A级风景区——大足石刻，重庆市双桥国家级经济技术开发区，两校区依山傍水，风景如画，是广大青年学子理想的学习和生活场所。学校校园共占地1300余亩，校舍面积20余万平方米，数字化图书馆、标准化塑胶跑道田径场、标准化学生公寓、标准化学生食堂等一应俱全，现代教育技术',223093);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆经贸职业学院',' ','重庆',0,6,0,0,0,' ','暂空',' ','http://www.cqvcet.com','重庆经贸职业学院是经重庆市人民政府批准，国家教育部备案，重庆市教委主管，面向全国招生的全日制普通高等职业院校。学院位于黔江新城职教园区，毗邻美丽的黔江峡谷公园，占地约506亩，校园环境优美，建筑特色鲜明，是读书学习的理想场所。学院秉承“为社会培养技能型人才”的办学宗旨，形成了“创新与自信使人生更有价值”的校训，确立了“能者治教，严谨治学”的办学理念。学院现有经济贸易系、建筑工程系、机械与电子工程系、国际商务系4个教学系；设有会计、审计实务、海关管理、市场营销、国际贸易实务、报关与国际货运、机电一',39510);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州盛华职业学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.forerunnercollege.com/','《贵州盛华职业学院简介》（学校代码：14371）全球十大华人经济领袖、2011年台湾首富王雪红陈文琦夫妇秉承其父王永庆先生的教育扶贫理念，投巨资在中国西部贵州省公益举办一所全日制普通高等职业学校--贵州盛华职业学院。学校已获得贵州省人民政府和国家教育部的批准，定于2011年9月开学。这是一所在国内首创的、高标准的、公益性举办的高等职业学院：学校公益办学不以营利为目的，学校章程明确规定举办者永远不分红和不要求经济回报。学校致力于帮助优秀贫困学生\"零成本\"完成学业、并找到一份工作。原则上不少于1/3',20560);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明卫生职业学院',' ','云南',0,3,0,0,0,' ','暂空',' ','http://www.kmhpc.net/','昆明卫生职业学院简介明德博学慎思济世昆明卫生职业学院隶属云南骨伤产业，是一所经云南省人民政府批准，报国家教育部备案的全日制普通高等职业院校。学院招生纳入国家当年普通高校招生计划，具有独立颁发国家承认的学历文凭资格，是目前云南唯一一所医学类的高等职业院校。学院坐落在美丽的滇池南岸，西隔呈贡大学城，交通便利，环境优美，区位优势明显，是一所现代化、园林式、生态型的和谐大学校园。学院占地400余亩，建筑总面积20余万平方米，目前已建成教学楼、行政楼、图书馆、教研楼群、学生公寓、学生餐厅、标准化足球场、篮',74180);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南现代职业技术学院',' ','云南',0,1,0,0,0,' ','暂空',' ','http://www.ynxd.net.cn/','云南现代职业技术学院是经云南省人民政府批准、国家教育部备案的全日制普通高等职业学院，属云南省36所高职高专院校之一。学院具有独立颁发普通高等院校学历文凭资格，纳入国家统一招生计划，面向全省招生。学院坐落于云南省楚雄市东南新区的楚雄州职教园区内，设有机电建筑工程系、学前教育与艺术系、商贸旅游系、医药卫生系、食品工艺系5个系13个专业。园区有教职工468人，专兼职教师343人，副高职称教师126人，具有硕士学位教师62人，“双师型”教师138人。学院设施完善、教学环境优雅，为学生学习、生活提供了良好',22577);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃有色冶金职业技术学院',' ','甘肃',0,1,0,0,0,' ','暂空',' ','http://www.gsysyj.org/','甘肃有色冶金职业技术学院创建于2010年7月，是经甘肃省人民政府批准成立、国家教育部备案，由金昌市人民政府和金川集团公司共同出资兴建的全日制高等职业技术学院。学院地处享有“祖国镍都”美誉的甘肃省金昌市，占地面积703亩，总规划建筑面积23万平方米，计划总投资8.7亿元(其中金川集团公司支持6000万元)。一、二期总投资近5亿元，已建成综合办公楼、教学楼、学生公寓、后勤综合楼、实训楼、体育场等约13万平方米。学院有教职员工150名(其中专、兼职教师110名,博士后1名、硕士40名，副高以上职称21',53323);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('白银矿冶职业技术学院',' ','甘肃',0,12,0,0,0,' ','暂空',' ','http://www.bymu.cn/','白银矿冶职业技术学院（原白银有色金属职工大学）是由甘肃省政府批准、国家教育部备案的普通高等职业院校，已有30多年的办学历史。2001年取得高等学历教育招生资格。现已形成以资源开发、有色金属采矿、选矿、冶炼、化工等专业为特色，以机电一体化、数控技术等专业为优势的全日制普通高等职业院校。学院老校区占地面积202亩（新校区占地476亩，一期3万m2正在加紧建设中），现有教职工206人，其中专职教师170人，具有高级职称的52人，中级职称的79人，专职教师中具有研究生学历的8人，大学本科以上110人。已',10523);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁夏防沙治沙职业技术学院',' ','宁夏',0,1,0,0,0,' ','暂空',' ','http://www.nxfszs.cn/','宁夏防沙治沙职业技术学院是经自治区人民政府批准设立、国家教育部备案的集高等职业教育、中职教育和职业技能培训鉴定为一体的公办全日制高等学校。学校以生态环境建设为重点、现代农业技术、葡萄酒酿造技术为特色，是全国唯一一所产、学、研相结合的防沙治沙专业院校。学校规划用地4000亩，校园环境优美，人文气息浓厚。学校现有各级各类在校生5000多人，教职工140余人。学校设置防沙治沙工程系、环境与资源工程系、现代农业工程系、食品与生物工程系（葡萄酒工程技术中心）、风景园林系、沙产业文化与艺术系、中专部、公共学',14328);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽黄梅戏艺术职业学院',' ','安徽',0,8,0,0,0,' ','暂空',' ','http://www.ahmxx.cn/','安徽黄梅戏艺术职业学院由安徽黄梅戏学校升格而来，前身是1956年创办的安庆市黄梅戏剧团演员训练班，1958年8月1日成立安庆市艺术学校，1963年改名安徽安庆黄梅戏学校，1969年因文革停办，1976年重办训练班，并于当年在原址复校，1979年正式改名为安徽黄梅戏学校，2012年采用现校名。学校坐落在中国历史文化名城安庆市，是全国唯一以培养黄梅戏表演艺术人才为主的综合性高等艺术学校。目前设有黄梅戏表演、音乐、美术、群众文化艺术、舞蹈、影视表演、杂技七大艺术类专业。其中，黄梅戏表演是全国唯一的特色',14355);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('潍坊工程职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.wfec.cn','◇办学历史悠久，社会声誉优良潍坊工程职业学院是由原潍坊教育学院改建而成的国办普通高等专科学校，学院历史悠久，展位图起源于1881年英国基督教会创办的培真书院，学院几经变迁，1978年经教育部批准设立潍坊教育学院，开始举办专科层次的高等教育，1998年经批准成为山东省首批举办高职教育的院校，2010年经省政府批准转制改建为潍坊工程职业学院。目前，学院有青州市南、北两个校区和潍坊校区，总占地面积1500余亩，有13个教学院系，开设38个高职专业。在校生15000余人。学院主要承担普通高等职业教育、普',54873);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州商贸旅游职业学院',' ','河南',0,6,0,0,0,' ','暂空',' ','http://www.zzvcct.com/','郑州商贸旅游职业学院是经河南省人民政府批准、教育部备案，集商贸和旅游于一体普通民办高等院校，我院的前身是创建于1996年的河南旅游专修学院。学院位于中国八大古都之一的商贸城市——河南省省会郑州，交通便利，地理位置优越。中原历史悠久、人文景观丰富、商贸气息浓厚，为我院培养商贸旅游复合型、应用型人才奠定了坚实基础;也为我院学子就业提供了广阔发展空间。学院占地面积170余亩，建筑面积65000余平方米，图书馆、计算机室、语音室、多媒体教室及各专业的实验实训室一应俱全。校园环境优雅，绿树成荫，是莘莘学子',20827);
